		['newyearEvent_time_easy'] = {
			['episode'] = {
				['3'] = [[„Ho ho ho, so machen wir das! Beeil dich, an deinen Arbeitsplatz!“]],
				['2'] = [[„Dieses Geld ist für den Ausbau unseres Betriebs bestimmt, aber die Einheimischen in dieser Welt wollen viel mehr als die durchschnittlichen Arbeitskosten. Wenn ich ihnen so viel zahle, wie sie verlangen, sind wir schnell bankrott, und die Kinder haben keine Lust mehr auf Urlaub.“]],
				['1'] = [[Ich kam an einem kleinen Gebäude mit einem Schild vorbei, auf dem „TLtoys Fabrik“ stand.<new_line>„Endlich bist du da, mein lieber Freund. Ich brauche sofort Hilfe. Es gibt keine Arbeiter in der Fabrik! Ich brauche dich wirklich für ein paar Schichten, sonst bekommen die Kinder kein Spielzeug …“]],
				['7'] = [[„Genau, wir müssen auch Spielzeug für die jungen Pfadfinder herstellen. Ich habe Teile für den Bau von Spielzeugsoldaten gefunden. Allerdings sind die Materialien ein wenig radioaktiv. Willst du sie reinigen, oder hast du keine Angst vor einer kleinen Krankheit?“]],
				['6'] = [[„Sooo, was haben wir denn hier? Wunderschön! Sieht aus, als wärst du schon dein ganzes Leben lang ein Handwerker. Du versteckst nicht zufällig spitze Ohren unter deinem Haar, oder?“]],
				['5'] = [[„Hier sind die Materialien. Wenn ich zurückkomme, sollten die Ergebnisse deiner Arbeit den Tisch verschönern. Wenn du bereit bist, zögere nicht – fang an.“]],
				['4'] = [[„Du hast viel Arbeit vor dir! Zuerst musst du Geschenke für die jungen Mitglieder des schönen Geschlechts machen. Wähle selbst aus, was du für sie machen willst.“]],
				['9'] = [[„Ho ho ho, wie schön! Die Kinder werden sich bestimmt freuen. Danke für diese gute Tat!“]],
				['8'] = [[„Hier sind die Materialien. Wenn du müde bist, mach eine Pause – aber nur eine kurze. Und mach dich sofort an die Arbeit!“]]
			},
			['choice'] = {
				['3'] = [[„Ich habe keine Zeit.“ (Gehen)]],
				['2'] = [[„Du hast einen Haufen Geld. Gibt es wirklich niemanden, der einen Lohn will?“]],
				['1'] = [[„Okay, ich arbeite für eine Weile.“]],
				['7'] = [[Bereit machen]],
				['6'] = [[„IIch will Puppen bauen“ (8 Stunden)]],
				['5'] = [[„IIch will Kuchen backen“ (8 Stunden)]],
				['4'] = [[Zur Werkbank gehen]],
				['10'] = [[An die Arbeit machen (8 Stunden)]],
				['9'] = [[„Ich werde sie sauber machen“ (14 Stunden)]],
				['8'] = [[„Bringen wir es schneller zu Ende“ (6 Stunden) (+Strahlung)]]
			}
		},
		['witch_delivery'] = {
