			['episode'] = {
				['1'] = [[Als ich ankam, wurde ich von einer Frau mittleren Alters begrüßt. Sie sah überhaupt nicht nach einer Hexe aus. „Nun, was hast du mir mitgebracht? Was ist in der Kiste? Leg es auf den Tisch. Keine falsche Scham.“]],
				['2'] = [[Nachdem ich ihr erzählt hatte, dass ich aus der Darüberliegenden Welt komme, lud die Hexe mich ein, mich an den Tisch zu setzen, gab mir zu essen und fragte mich über Neuigkeiten von jenseits des Portals aus. Dann gab sie mir die Tränke für die Taverne und ging wieder ihren Dingen nach. Es wird auch langsam Zeit, dass ich mich auf den Rückweg mache.]]
			}
		},
		['newyearEvent_energy_hard'] = {
			['episode'] = {
				['10'] = [[„Altmodische Methoden des schmutzigen Kapital… Kapitalismus. Man sagt, so macht er es. Deshalb will auch niemand für ihn arbeiten.“]],
				['15'] = [[„Na gut … Du hast gewonnen … Morgen werde ich … Ich werde in die Fabrik gehen.“<new_line>Mit diesen Worten ging der Bauer ins Land der Träume, und ich ging wieder als Sieger hervor.]],
				['14'] = [[„Äh … Du sprichst die Wahrheit, Bärtiger! Komm schon, Prost auf gute Taten!“<new_line>Der Bauer wischte sich eine männliche Träne weg und füllte beide Gläser.]],
				['3'] = [[„Es ist, wie es ist. Aber vielleicht hilft der Gegenstand, den du bekommen hast. Es ist ein kleines Geschenk, aber du musst derjenige sein, der es überreicht. Wenn du die Person, die du besuchen wirst, überzeugen kannst, für mich zu arbeiten, wird das die Fabrik retten.“]],
				['2'] = [[„Ja, ich habe ein Problem. Wegen des Arbeitskräftemangels stehen der Fabrik schwere Zeiten bevor. Ich kann niemanden finden, der für das Wohl der Wunder in eurer Welt arbeiten will. Die Eltern lassen ihre Kinder nicht mitmachen, und die Eltern selbst wollen es auch nicht. Sie sagen, sie hätten schon genug zu tun und die Apokalypse stehe vor der Tür.“]],
				['1'] = [[„Ho ho … Hallo, lieber Freund … Es gibt einen bestimmten Gegenstand, den ich brauche. Du hast ihn nicht zufällig, oder?“]],
				['11'] = [[„Wunder? Es heißt, er weiß nicht mal, wie man zaubert … Er will nur, dass die Leute umsonst arbeiten.“<new_line>Der Bauer füllte beide Gläser wieder auf.]],
				['7'] = [[Der Bauer füllte beide Gläser.<new_line>„Trink.“]],
				['6'] = [[Ein vertrautes Gesicht erschien in der Tür.<new_line>„Endlich bist du da! Ich habe auf diesen Moment gewartet! Du hast mich gedemütigt! Du hast mich schwach gemacht! Aber jetzt werde ich mich rächen. Ich habe mich lange darauf vorbereitet, und jetzt kann ich dir die Stirn bieten!“<new_line>Der Bauer nahm dir das Geschenk aus der Hand, holte schnell den Inhalt heraus und legte es auf seinen Tisch.]],
				['5'] = [[Wir erreichten einen einsamen Bauernhof am Rande des Dorfes. Ich erinnerte mich vage an diesen Ort, aber beim letzten Mal war der Hofes in einem viel besseren Zustand. Geschäfti wünschte mir Glück und ging.]],
				['4'] = [[In den traurigen Augen des Geschäftsnachtsmanns blitzte Freude auf.<new_line>„Ho ho ho! So wird's gemacht! Komm, ich bringe dich hin.“]],
				['12'] = [[„Diese Schwindler … diese Schwind…ler … Wir brauchen sie hier nicht!“<new_line>Der Bauer knallte sein Glas auf den Tisch.]],
				['13'] = [[„Was für ein Mensch? Ein guter Mensch? Also … ich würde gerne denken, dass ich ein guter Mensch bin.“]],
				['9'] = [[„Ach, deswegen. Ich habe gehört, dass der Geschäftsnachtsmann seinen Angestellten ein schlechtes Angebot macht, so dass niemand für ihn arbeiten will.“<new_line>Der Bauer füllte wieder beide Gläser.]],
				['8'] = [[„Also, was willst du von mir, Bärtiger?“]]
			},
			['choice'] = {
				['3'] = [[„Ergibt Sinn.“]],
				['2'] = [[„Nein, aber ich werde versuchen, es zu finden.“ (Weggehen)]],
				['1'] = [[„Hier, nimm das. Ist etwas nicht in Ordnung?“]],
				['7'] = [[Hinsetzen]],
				['6'] = [[An die Tür klopfen]],
				['5'] = [[„Los geht‘s!“]],
				['4'] = [[„Okay, ich versuch‘s.“]],
				['11'] = [[„Na also, geht doch! Du … du kannst gut sein … Es kommt nicht darauf an, wer etwas anbietet, sondern WAS sie anbieten!“]],
				['10'] = [[„Keine Einwände … Aber denkst du … denkst du, du bist ein guter Mensch?“]],
				['9'] = [[„Aber er vollbringt Wunder in der Welt …“]],
				['8'] = [[„Du bist Bauer, aber es ist Winter. Wir wollen dir einen Job anbieten.“]]
			}
		},
		['halloween_boss_2022_5'] = {
			['choice'] = {
				['1'] = [[Umdrehen]],
				['2'] = [[Weiter]]
			},
			['episode'] = {
				['3'] = [[„Kannst du nicht lesen, Sterblicher? Wenn der Name von jemandem auf der Seite steht, dann war's das – es gibt keine Hoffnung für ihn. Vergeude keine Zeit – Igor erntet nur Organe von frischen Leichen. Such weiter.“]],
				['2'] = [[Ich rannte auf die Person zu, aber ich kam zu spät – er war bereits verblutet und an seinen Wunden gestorben.<new_line>Ich hörte ein Kichern hinter mir.]],
				['1'] = [[Als ich ankam, stieß ich auf ein Rudel Raubtiere, das sich um jemanden scharte. Ich hatte keine Wahl – sie hatten mich bereits bemerkt.]]
			}
		},
		['boatman'] = {
			['episode'] = {
				['3'] = [[„Nun, aus dem Lager ... dem Fischerdorf. Es liegt westlich von Petrozavodsk, ziemlich weit weg. Ich segele normalerweise nicht so weit, aber ich musste Wassili abholen. Wir sind beide begeisterte Funker, daher haben wir eine Weile über Funk gesprochen. Woher kommst du?“]],
				['2'] = [[Der Schiffer drehte sich überrascht um.<new_line>„Nein, Wassili ist mein Freund. Ich bin hier, um ihn in unsere Siedlung zurückzuholen, aber er antwortet nicht.“]],
				['1'] = [[Ein Mann mit einer Gasmaske stand neben dem Boot.]],
				['7'] = [[Der Schiffer hat Wassilis Ausrüstung in eine Holzkiste gepackt und mich um meine Karte gebeten.<new_line>„Hier ist mein Dorf. Ich werde dich, wie versprochen, nach Belomorsk bringen. Ich weiß nicht, warum du in diese höllische Stadt willst, aber das ist deine Sache.“]],
				['6'] = [[„Hier, nimm diese Brechstange. Sie ist alt, aber bei der Tür wird sie dir helfen.<new_line>Geh und ich hole ein paar Gewehre, die uns bei diesem Job helfen. Triff mich an der Verrückten-Baracke.“]],
				['5'] = [[„Wir haben einen Plan, aber keine Kugeln. Hör zu, in der Stadt gibt es einen Baumarkt. Er ist versperrt, aber ich wette, dass es dort Schwefel und Salpeter gibt. Das kann ich verwenden, um Schießpulver herzustellen ...“]],
				['4'] = [[Als der Schiffer sah, dass ich allein zurückkehrte, verdüsterte sich sein Gesicht. Ich gab ihm das Tagebuch und erzählte, was passiert war.<new_line>„Diese Verbrecher ... Sie haben sogar das Radio mitgenommen. Wir müssen uns an diesen Bastarden rächen und zurückbringen, was sie gestohlen haben.“]],
				['10'] = [[„Gut, wir haben's geschafft. Viel Glück bei deiner Suche. Ich werde im Fischerdorf auf dich warten.“]],
				['9'] = [[„Von Kandalaksha nach Belomorsk gibt es keine Straße. Zu viel Strahlung. Es gibt nur einen Weg südlich von hier: Über das Wasser.<new_line>Hör zu, denkst du, du könntest nach Wassili Ausschau halten? Bring ihn her und wir schaffen es gemeinsam über das verstrahlte Land.“]],
				['8'] = [[Ich habe dem Schiffer nichts anderes zu sagen.]]
			},
			['choice'] = {
				['3'] = [[„Warte eine Sekunde ... bist du aus dem Lager?“]],
				['2'] = [[„Einverstanden, abgemacht“]],
				['1'] = [[„Bist du Wassili? Ich habe dein Funksignal gehört“]],
				['7'] = [[„Ich hab hier noch was zu erledigen“]],
				['6'] = [[„Bis zum nächsten Mal“]],
				['5'] = [[„Ich kann gehen“]],
				['4'] = [[„Sieht so aus, als gäbe es eine Menge von denen. Wir brauchen einen Plan“]],
				['10'] = [[„Bis zum nächsten Mal.“]],
				['9'] = [[„Ich komme aus Murmansk, aber ich bin auf dem Weg nach Belomorsk“]],
				['8'] = [[„Ja, gehen wir“]]
			}
		},
		['celebrate_new_year'] = {
			['choice'] = {
				['1'] = [[Ded Moroz!]],
				['2'] = [[Ded Moroz!]]
			},
			['episode'] = {
				['7'] = [[„Die Winterkönigin ist noch im Eis. Ich taue sie auf, wenn sie ihr Vertrauen in die Leute wiederfindet“, sagt Moroz zu mir. „Was dich betrifft, ich bin dir ewig dankbar, dass du geholfen hast, mich zu retten. Hoffentlich sehen wir uns nächstes Jahr wieder. Nimm einstweilen dieses Abschiedsgeschenk. Frohes neues Jahr!“]],
				['6'] = [[Wir feiern die ganze Nacht. Ded Moroz wünscht allen ein frohes neues Jahr und gibt ihnen Geschenke. Später kommen er und Sneguroschka zu mir, um mir zu danken.]],
				['5'] = [[„Ich höre euch, ich höre euch!“, sagt eine laute Stimme aus einer nahen Baumgruppe, und als wäre nichts gewesen, tritt Ded Moroz hinter ihnen hervor. Er trägt seinen roten Mantel, hat einen neuen Stab und einen Sack voller Geschenke! Sneguroschka läuft zu ihm und fällt ihm um den Hals, und er lacht nur leise und klopft ihr auf den Rücken.]],
				['4'] = [[Das klang schon besser. Die Leute lächeln. „Einmal noch!“, ruft Sneguroschka enthusiastisch.]],
				['3'] = [[Alle rufen mit, allerdings ohne Überzeugung oder Koordination. „Noch mal!“, ruft Sneguroschka.]],
				['2'] = [[Sneguroschka versammelt alle im Kreis um den Baum, den ich geschmückt habe. „Und jetzt rufen wir Ded Moroz!“, sagt sie laut.]],
				['1'] = [[Zu meiner Verblüffung finde ich beim Baum eine festlich gedeckte Tafel vor, um die schon viele Leute sitzen. „Siehst du? Es ist alles bereit, und alle, denen du dieses Jahr geholfen hast, sind zu Besuch gekommen“, sagt Sneguroschka und nimmt meine Hand. „Komm, es ist nicht mehr viel zu tun.“]]
			}
		},
		['leprechaun_debts'] = {
			['episode'] = {
				['1'] = [[Ich hatte kaum Zeit, mich zu erklären, bevor die Leprechauns ihre Gesichter in zornige Fratzen zogen und mich angriffen.]]
			}
		},
		['new_year_search_medium'] = {
			['episode'] = {
				['3'] = [[„Endlich. Sogar meine Rentenschecks kommen schneller als du. Gut, als Dankeschön verrate ich dir ein Zauberergeheimnis.<new_line>Du kannst jedes Stück Schrott in ein hochwertiges Artefakt verwandeln, wenn du ‚verflucht‘ auf das Preisschild schreibst. Verfluchte Knochen, Beeren, Münzen – was du willst.“]],
				['2'] = [[„Okay, ich brauche noch eine Sache. Ich mache eine Schaufensterdekoration daraus. Bei einer solchen Reliquie werden die Dorfbewohner wirklich ausrasten.<new_line>Was guckst du denn so? Wenn du so alt bist wie ich, ist nicht mehr alles Sonnenschein und Regenbogen. Ich muss nebenbei noch in einem Zauberladen arbeiten.“]],
				['1'] = [[„Hör auf zu tun, was du gerade tust. Ich habe eine Aufgabe für dich.<new_line>Hol mir etwas Schrott. Mit ein bisschen Magie kann ich ihn in das mächtigste Artefakt auf dem Markt verwandeln.“]]
			}
		},
		['halloween_fight_easy'] = {
			['episode'] = {
				['1'] = [[Das markerschütternde Heulen der unnatürlichen Bestien, die in diesem Gebiet wüten, ist meilenweit zu hören. Sie stellen sicherlich eine Bedrohung für die Menschen hier dar. Ich werde gut belohnt werden, wenn ich sie besiege.]],
				['2'] = [[Ich bin sehr nah an den Hort der Bestien herangekommen. Jetzt kann ich angreifen.]]
			}
		},
		['halloween_search_medium_1'] = {
			['episode'] = {
				['1'] = [[„Hallo, mein Junge. Ich braue heute einen sehr schwierigen Trank. Vielleicht hast du ein paar der Zutaten?“]]
			}
		},
		['lair5'] = {
			['episode'] = {
				['1'] = [[Ich hätte wetten können, dass die Vampire auf mich warteten. Sie hatten mich bereits umzingelt, noch bevor ich in die Höhle vorgedrungen war.]]
			}
		},
		['emba_time_easy2'] = {
			['episode'] = {
				['3'] = [[Hervorragend, der Brunnen wurde gereinigt. Allerdings muss das ganze gefährliche Grünzeug jetzt aufgeschichtet und verbrannt werden. Andernfalls werden sich die Biomutanten weiterhin hier versammeln, um zu fressen und sich zu einem gefährlichen Rudel zusammenzuschließen.]],
				['2'] = [[Ich habe alles um den Brunnen herum aufgeräumt, aber ich bemerkte, dass alles im Inneren blühte. Außerdem schwamm Schimmel auf dem Wasser in der Tiefe.]],
				['1'] = [[In der Mitte der verschlungenen Pflanzen befand sich ein alter Brunnen. Es scheint, dass sich das Myzel an ihm als Wasserquelle festgesetzt hat und schnell wächst.<new_line>Orte wie dieser ziehen immer Biomutanten an, also muss diese potenzielle Gefahrenquelle zerstört werden.]],
				['4'] = [[Endlich ist alles erledigt. Wenigstens gibt es jetzt einen Brennpunkt weniger in diesem Bezirk.]]
			},
			['choice'] = {
				['3'] = [[Sammle alles auf einen Haufen und zünde es an]],
				['2'] = [[Mach den Brunnen frei]],
				['1'] = [[Breche alle Äste und Wurzeln ab]]
			}
		},
		['emba_isaev_krasnoyarsk'] = {
			['episode'] = {
				['3'] = [[„Jetzt, wo der Prototyp in den Händen der Ölmänner ist, macht es für uns keinen Sinn, das Projekt zu beenden.<new_line>Wir müssen mit allen Mitteln versuchen, die Begrünungstechnologie zu sichern. Ich muss Sie erneut bitten, die Dokumente zu finden. Sie sind sowohl für unseren Plan als auch für die gesamte Studie notwendig.“]],
				['2'] = [[„Natürlich ist ein militärischer Konflikt nicht die Lösung. Aber wir haben einen Plan. Ich werde jetzt noch nicht auf die Einzelheiten eingehen.“]],
				['1'] = [[„Da sind Sie ja endlich. Wir stecken in großen Schwierigkeiten. Das Ölsyndikat hat unseren Prototyp des Begrünungsmechanismus gestohlen. Wir müssen sie daran hindern, ihn zu benutzen, sonst wird das ganze Ödland zu einer grünen Hölle.“]],
				['7'] = [[„Sie müssen sich an den Umständen orientieren. Versuchen Sie zumindest, die Blackbox von dort zu holen.<new_line>Sie und der Biologe haben ein hervorragendes mobiles Labor für uns eingerichtet, von dem aus wir ein Signal senden können, das sich auf die Chiffren dieser Blackbox stützt. Also, Genosse, bereit für eine letzte Mission?“]],
				['6'] = [[„Und alle seine Mutationen sind eine Reaktion auf äußere Reize. Der fatale Fehler des E.M.B.A.-Projekts bestand darin, das Relikt-Myzel an einen Supercomputer anzuschließen.<new_line>Es reagierte radikal auf diese Veränderung. Offenbar ist die Entstehung von Biomutanten und autonomen Individuen wie dem Gesandten darauf zurückzuführen, dass das Myzel Zugang zu den Logikschaltkreisen des leistungsfähigsten Computers erhielt.“]],
				['5'] = [[„Ursprünglich dachten wir, dass das Myzel spontan mutiert und das Hauptproblem in dieser Zufälligkeit liegt.<new_line>Aber aus den Informationen, die Sie erhalten haben, geht hervor, dass es tatsächlich anders funktioniert. Das Myzel ist am stabilsten, wenn es in Ruhe gelassen wird.<new_line>Dieses Relikt-Myzel existierte Tausende von Jahren in einer stabilen Form in isolierten Höhlen.“]],
				['4'] = [[„Hallo, Kollege. Wichtige Neuigkeiten! Laut den Unterlagen gibt es in der Bio-Station ein Relikt-Myzel, das mit einem Supercomputer verbunden ist. Dort befindet sich das Entscheidungszentrum.“]],
				['9'] = [[„Wahnsinn! Unglaublich! Unvorstellbar!“]],
				['8'] = [[„Also, viel Glück.“<new_line>(hält ein Stück Papier mit dem Code hin)]],
				['17'] = [[„Wir waren gezwungen, alle Entwicklungen einzumotten und die Entwicklung unserer Organisation neu auszurichten. Diese ganze Geschichte mit dem Relikt-Myzel …<new_line>Sie haben es selbst gesehen – die Leute sind ehrgeizig, gierig und können sich oft nicht entscheiden. Nicht einmal für einen guten Zweck.“]],
				['16'] = [[„Und dann trennen sich unsere Wege.“]],
				['15'] = [[„Hallo, lieber Genosse. Es wurde alles bestätigt: Der Prototyp wurde tatsächlich zerstört.“]],
				['14'] = [[„Später, mein Freund, später.“]],
				['23'] = [[„Zugegeben, wir haben es nicht übers Herz gebracht, es komplett zu zerstören. Wir haben es in die Höhle gestellt, wo es die letzten Jahrtausende war. Kein Computer. Zurück in seiner natürlichen Umgebung.<new_line>Der Biologe und einige meiner Kollegen werden noch eine Weile hier bleiben, um die gesamte Technologie aus den Bio-Stationen zu entfernen. Einige werden wir zerstören, andere werden wir mitnehmen.“]],
				['22'] = [[„Nein, denn ich glaube, dass die Menschen aus all diesen Katastrophen eine Lehre ziehen könnten. In der Zwischenzeit werden wir Archivare – im wahrsten Sinne des Wortes.<new_line>Wir werden Informationen speichern und die schlimmsten Fehler auf dem Weg der Menschen zum Wissen verhindern.“]],
				['21'] = [[„Wir haben die Blackbox entschlüsselt. Das Signal wurde erzeugt und bereits an den Myzel-Prototyp gesendet. Das Gerät wird sich selbst zerstören, und den Ölmännern wird nichts bleiben.<new_line>Wir brauchen etwas Zeit, um das zu überprüfen, und wir werden diesen Ort verlassen. Kommen Sie bald wieder, um sich zu verabschieden.“]],
				['20'] = [[„Das ist schwer vorherzusagen. Alles, was wir aus den Dokumenten wissen, ist, dass es eine sehr große Wissenschaftsstadt ist. Und in ihrem Zentrum, unter der Bio-Station, gibt es dieses verborgene Relikt-Myzel, das mit einem Supercomputer verbunden ist.<new_line>Wie Mutationen und der Computer seine lebenswichtige Aktivität beeinflussen könnten, ist unvorhersehbar.“]],
				['19'] = [[„Die Ölmänner wollen den erbeuteten Begrünungsmechanismus für kommerzielle Zwecke nutzen.<new_line>Sie wollen nicht verstehen, dass das Relikt-Myzel sofort die Oberhand gewinnen und eine weitere Katastrophe auslösen wird.<new_line>Das bedeutet, dass unser Ziel darin besteht, die Wissenschaftsstadt zu infiltrieren und ein Selbstzerstörungssignal an den Prototyp des Myzels zu senden, den wir in unserem Mechanismus verwendet haben.“]],
				['18'] = [[„Ach, mein Freund, ach. Nicht zu diesem Preis. Aber Sie haben einen unschätzbaren Beitrag zur Verteidigung dieser wackeligen Welt geleistet. Jetzt hat sie vielleicht eine kleine Chance, zu überleben. Viel Glück, lieber Freund, und leben Sie wohl!“]],
				['12'] = [[„Ja! Es passt alles zusammen. Nichts würde dieses Ding daran hindern, Sporen über die ganze Welt zu verbreiten und alle Städte zu bedecken. Zumal es sich von hoher Strahlung zu ernähren scheint.<new_line>Aber alles, was es von uns will, ist Frieden. Offenbar kann es nicht zwischen den Menschen unterscheiden und sieht die Menschheit als einen einzigen Organismus. Es sieht jedes menschliche Wesen als die gesamte Menschheit an.“]],
				['13'] = [[„Genau. Ich würde unserem Myzel-Freund hier zustimmen. Der Ehrgeiz, gemischt mit den besten Absichten der Menschheit, hat ihm alle Werkzeuge in die Hand gegeben. Wessen Verantwortung ist größer?<new_line>Die Antwort scheint nicht zu unseren Gunsten zu auszufallen. Deshalb ist der Gesandte uns so ähnlich geworden, ganz zu schweigen vom Schicksal von Gerin und Kurganow …“]],
				['10'] = [[„Dieses Relikt-Myzel hat den vollen Anschein eines Verstandes. Nicht einen einzelnen, wie bei uns, sondern einen verteilten. Das ist wahrscheinlich der Grund, warum es so leicht mit dem Supercomputer kommunizieren konnte.“]],
				['11'] = [[„Das Relikt-Myzel wurde aus den Eingeweiden der Erde gezogen und einer intensiven Selektion unterzogen. All diese Vorgänge sind nichts anderes als ein Versuch dieses Lebewesens, sich wieder vor dem Menschen zu schützen.<new_line>Die Symbiose mit dem Computer gab ihm nicht nur nie dagewesene Möglichkeiten dazu, sondern wurde sozusagen auch durch unsere Arbeitsmethoden angeregt.“]]
			},
			['choice'] = {
				['2'] = [[„Was wird aus den Entwicklungen? Wir wollten die Welt retten.“]],
				['1'] = [[„Wie können wir sie aufhalten? Sie haben eine ganze Armee.“]],
				['7'] = [[„Was ist los, Doktor?“]],
				['5'] = [[„Aber was ist mit den Mutationen?“]],
				['4'] = [[„Was ist das Relikt-Myzel?“]],
				['9'] = [[„Also, all diese Bio-Stationen …“]],
				['17'] = [[„Was passiert mit dem Relikt-Myzel?“]],
				['16'] = [[„Was ist mit der Begrünung des Ödlands?“]],
				['15'] = [[„Ich bin fertig“.]],
				['14'] = [[„Was erwartet mich in der Bio-Station?“]],
				['19'] = [[„Auf Wiedersehen, Dr. Issajew.“]],
				['11'] = [[„Was ist mit dem Prototyp der Ölmänner?“]],
				['18'] = [[„Also keine Begrünung des Ödlands …“]],
				['12'] = [[„Was werdet ihr dann als nächstes tun?“]],
				['22'] = [[„Klingt pessimistisch.“]],
				['21'] = [[„Und es hat uns als Bedrohung wahrgenommen?“]],
				['20'] = [[„Man muss also nur zur Bio-Station gelangen und in den Raum mit dem Supercomputer einbrechen?“]]
			}
		},
		['embacity_radio2'] = {
			['episode'] = {
				['3'] = [[Der Schimmel hat das Gebäude praktisch zerstört und Beton und Eisen in eine Brutstätte für Biomutanten verwandelt. Ich werde einen Schutzanzug brauchen, um mit ihnen fertig zu werden.]],
				['2'] = [[Der Laden war mit Schimmelpilzen bedeckt, und aus den grünen Trieben der Wurzeln sprossen große scharlachrote Knospen. Während ich sie betrachtete, hätte ich fast die Biomutanten übersehen. Ich muss nachsehen, ob mein Schutzanzug in Ordnung ist.]],
				['1'] = [[Der Laden ist stark überwuchert, aber nicht in schlechtem Zustand. Es gibt nicht viele Mutantenspuren, aber irgendetwas ist auf jeden Fall drin. Bevor ich reingehe, muss ich überprüfen, ob meine Verteidigung in Ordnung ist.]],
				['4'] = [[In den übrigen Schaufenstern sind die Umrisse von umherstreifenden Biomutanten gelegentlich zu erkennen. Es ist keine gute Idee, mich ohne einen Schutzanzug zu nähern.]]
			}
		},
		['embacity_institute'] = {
			['episode'] = {
				['3'] = [[Eine große Horde von Biomutanten wühlt zwischen den Wurzeln herum, und die riesigen Ausläufer zerstören fast das Gerüst des Gebäudes. Ohne speziellen Schutz kann ich nicht in den Kampf ziehen.]],
				['2'] = [[Die Kuppel des Instituts war mit kräftigen Stämmen bedeckt, die mit roten Blüten beladen waren. Vielleicht waren sie es, die die mutierten Kreaturen anlockten. Ich muss überprüfen, ob mein Schutzanzug in Ordnung ist.]],
				['1'] = [[Eine massive Masse von Wurzeln und Schimmelpilzen hat das gesamte Gebäude verunstaltet. Eine kleine Armee von Mutanten hat sich hier versammelt, und ich muss einen speziellen Schutz tragen, um sie zu bekämpfen.]],
				['4'] = [[Das Institut ist schwer beschädigt, und in den Fenstern sind mehrere mutierte humanoide Kreaturen zu sehen. Es ist keine gute Idee, sich ohne einen Schutzanzug mit ihnen anzulegen.]]
			}
		},
		['darkness_boatman'] = {
			['choice'] = {
				['3'] = [[Das Skelett durchsuchen]],
				['2'] = [[Später zurückkommen]],
				['1'] = [[Nach unten gehen]],
				['7'] = [[Den Kontrollraum durchsuchen]],
				['6'] = [[Zum Kontrollraum gehen]],
				['4'] = [[Weitergehen]],
				['9'] = [[Laufen!!]],
				['8'] = [[Das Bedienfeld untersuchen]],
				['17'] = [[Sich das Tagebuch schnappen]],
				['16'] = [[Die Notiz genau ansehen]],
				['15'] = [[„Hallo! Kopierst du?“]],
				['14'] = [[Den Notizblock untersuchen]],
				['18'] = [[Den Bunker verlassen]],
				['19'] = [[Nach oben schauen]],
				['12'] = [[Gegen die Kreatur kämpfen]],
				['13'] = [[Den Generator wieder auffüllen]],
				['10'] = [[WEGRENNEN]],
				['20'] = [[Kopierst du? Kopierst du?!]]
			},
			['name'] = [[Militärbunker]],
			['episode'] = {
				['17'] = [[Das Bedienfeld war mit einem tragbaren Generator betrieben worden, der schon lange kein Benzin mehr hatte.<new_line>Ich entdeckte auf dem Boden eine Pistole. Als ich sie ergreifen wollte, hörte ich über mir ein Rascheln.]],
				['19'] = [[Hier gibt es nichts mehr. Zeit, von diesem Ort zu verschwinden.]],
				['14'] = [[„...p-sch-sch... Wasja, antworte. Kopierst du ... p-sch-sch...“<new_line><new_line>Der uralte Generator begann zu rumpeln und schaltet dann ab.<new_line>Das Bedienfeld wurde dunkel.]],
				['3'] = [[Das Skelett hatte gebrochene Rippen und das rechte Bein fehlte. Wer - oder was - konnte sowas getan haben? Ich muss vorsichtig sein.]],
				['2'] = [[Ich kletterte nach unten und fand mich in einer Ventilationskammer wieder. Sofort entdeckte ich ein Skelett in einer zerlumpten Offiziersuniform.]],
				['1'] = [[Hinter dem Hauptquartier entdeckte ich einen Ventilationsschacht.<new_line>Das sieht wie ein Militärbunker aus. Ich wette, da drin finde ich etwas Nützliches.]],
				['18'] = [[Eine Männerstimme hallte aus den Lautsprechern:<new_line>'p-sh-sh... Wassili, kopierst du?... p-sch-sch... Wasja, ich bin in der Nähe von Kandalaksha... p-sch-sch... Diese verdammten Verrückten haben die Stadt überrannt ... p-sch-sch...']],
				['7'] = [[Ein Tagebuch lag auf dem Bedienfeld, aber es war zu dunkel, um darin zu lesen.]],
				['6'] = [[Im Kontrollraum entdeckte ich ein massives Funkbedienfeld. Leider hatte es keinen Strom.]],
				['11'] = [[Ich war in einem Lagerraum voller Benzinfässer gelandet.<new_line><new_line>Jetzt hatte ich eine Chance, gegen das Biest zu kämpfen.]],
				['4'] = [[Als ich durch den Korridor ging, bemerkte ich eine Schleusentür mit der Aufschrift „Kontrollraum“.]],
				['12'] = [[Umhüllt von Flammen kämpfte die Kreatur ein letztes Mal und bewegte sich dann nicht mehr.<new_line>Ich griff mir einen Gaskanister und kehrte müde in den Kontrollraum zurück.]],
				['13'] = [[Der Generator erwachte spuckend zum Leben. Das Bediendfeld schaltete sich ein und schien Frequenzen zu durchlaufen, wohl auf der Suche nach aktiven Funksignalen.<new_line>Nach ein paar Minuten ertönte etwas, das wie eine menschliche Stimme klang.]],
				['9'] = [[Ich konnte nicht mehr normal denken, sprintete in einen anderen Raum und verriegelte die Tür hinter mir.<new_line>Der Hundertfüßer begann, sich gegen die rostige Vertäfelung zu werfen. Die Tür würde nicht mehr lange halten.]],
				['8'] = [[Über mir entdeckt ich einen riesigen Hundertfüßer, dessen Körper sich praktisch über die gesamte Decke erstreckte.<new_line>Und anscheinend hatte das Taschenlampenlicht dieses massige Biest geweckt.]]
			}
		},
		['base_restaurant'] = {
			['episode'] = {
				['1'] = [[Die Räuber haben direkt in diesem Restaurant ihr Lager aufgeschlagen. Wahrscheinlich bewahren sie hier all ihr Diebesgut auf. Aber es wird nicht leicht sich hineinzuschleichen: Auf dem Dach stehen Wachen.]]
			}
		},
		['emba_fight_hard'] = {
			['episode'] = {
				['3'] = [[„Wenn ich nicht alles durchdacht hätte, würde ich jetzt tot und ohne mein zweites Auge herumliegen.<new_line>Übrigens, wo wir gerade von Vorsicht sprechen. Ein Rudel könnte sich an Objekten wie diesen aufhalten … Ohne die lateinischen Namen zu verwenden, hast du vielleicht schon von dem Nest und der Pustel gehört?<new_line>Ich rate dir, dich zuerst im Kampf mit ihnen auseinanderzusetzen.“]],
				['2'] = [[„Das ist auch für jemanden, der kein Profi ist, ganz einfach. Progress braucht lebende Biomutanten und ich habe eine Falle aufgestellt. Du musst sie in die Falle treiben, und darin solltest du gut sein.<new_line>Wenn wir beide an Ort und Stelle sind, werden wir den Rest des Rudels erledigen.“]],
				['1'] = [[„Hey, warte mal. Du bist nicht schlecht mit Waffen. Ich habe einen Job für dich.“]],
				['7'] = [[„Das hoffe ich wirklich. Ich werde den Ort des Hinterhalts von der anderen Seite aus umrunden, und du solltest dich in etwa fünf bis zehn Minuten auf die Gruppe der Biomutanten zubewegen.“]],
				['6'] = [[„Du bist auch schlau, was für ein Fund! In Ordnung, Professor, ich werde den Ort des Hinterhalts von der anderen Seite aus umrunden, und du solltest dich in etwa fünf bis zehn Minuten auf die Gruppe der Biomutanten zubewegen.“]],
				['5'] = [[„Mit der ist es einfacher. Es ist so etwas wie ein organisches Reservoir mit einem Vorrat an Biomasse. Es hilft den Mutanten, Sporen zu absorbieren.<new_line>Übrigens: Wenn du in ihre Nähe kommst, wenn sie explodiert, brauchst du keine Angst zu haben. Die Berührung ihres Schleims ist allerdings nicht das beste Gefühl.<new_line>Aber wenn du mit ihm bedeckt bist, wird er die Wirkung der Sporen trotzdem absorbieren. Das ist etwas Gutes für dich, denn es saugt sie ein oder so ähnlich.“]],
				['4'] = [[„Sie sind wirklich interessante Organismen. Es ist eigentlich nicht ganz klar, ob sie lebendig sind oder nicht. Ein Nest ist so etwas wie eine Myzelkolonie. Gelegentlich kommen Grasfresser aus ihnen heraus.<new_line>Im Moment versuche ich herauszufinden, ob sie dort nur in der Stase warten oder ob sie sich regenerieren. Vielleicht werden sie auch von der Kolonie selbst geboren.“]],
				['11'] = [[„Kein schlechter Job. Es war schwieriger, als ich dachte, aber es ist schön, mit dir zu jagen. Ich werde mal nachsehen, wer da drüben in der Falle sitzt. Wir sehen uns.“]],
				['10'] = [[Ein Teil des fliehenden Rudels war irgendwo verschwunden — anscheinend hatte die Falle funktioniert. Mehrere weitere Biomutanten stürmten vor und Schüsse ertönten aus dieser Richtung, als sich die Jägerin dem Kampf anschloss.<new_line>Ich kann das Rudel weiter verfolgen und sie jetzt von hinten angreifen. Ich könnte sie auch flankieren und sehen, ob es dort eine bessere Position gibt.]],
				['9'] = [[Das war nur ein Teil des Rudels. Ich trieb die, die übrig geblieben waren und sich dem Kampf nicht angeschlossen hatten, weiter zu dem Ort, auf den die Jägerin gezeigt hatte.]],
				['8'] = [[Ich habe genug gewartet, ich kann mit dem Angriff auf das Rudel der Biomutanten beginnen.]]
			},
			['choice'] = {
				['3'] = [[„Ich verstehe, ich bin bereit.“]],
				['2'] = [[„Ich sehe, du hast alles durchdacht.“]],
				['1'] = [[„Wie wäre es mit ein paar Details?“]],
				['7'] = [[Gehe vorwärts]],
				['6'] = [[Warte und greife an]],
				['5'] = [[„Richtig, und was ist mit der Pustel?“]],
				['4'] = [[„Was ist das?“]],
				['9'] = [[Flankiere sie]],
				['8'] = [[Greife von hinten an]]
			}
		},
		['lair_beemother'] = {
			['episode'] = {
				['1'] = [[Scheinbar nutzt die Mutterbiene den Bienenstock, um ihren mutierten Nachwuchs großzuziehen. Und sie wird ihn wohl mit ihrem Leben verteidigen.]]
			}
		},
		['quest_mad_man'] = {
			['choice'] = {
				['1'] = [[Versuchen, zu sprechen]],
				['2'] = [[Näher herangehen]]
			},
			['episode'] = {
				['3'] = [[Ich fand einen Überlebenden im Haus! Er war in Lumpen gehüllt und abgemagert. Seine Augen blickten wild umher und als er mich sah, schrie er kurz auf und versteckte sich hinter einem kaputten Tisch.]],
				['2'] = [[Anscheinend hat ihm nicht gefallen, was ich getan habe. Er hat wie verrückt geschrien und ist mit einem Speer auf mich losgegangen!]],
				['1'] = [[Ich habe einen Überlebenden im Haus gefunden! Er war in Lumpen gehüllt und abgemagert. Seine Augen blickten wild umher und als er mich sah, schrie er kurz auf und versteckte sich hinter einem kaputten Tisch.]]
			}
		},
		['santa_wizard'] = {
			['choice'] = {
				['3'] = [[„Ausfallsicherungen im Falle einer Überlastung?“]],
				['2'] = [[„Bist du sicher?“]],
				['1'] = [[„Wenn wir ihn angreifen, verlieren wir unsere Magie.“]]
			},
			['episode'] = {
				['5'] = [[„Ganz genau! Wenn wir diese zuerst zerstören, dann wird die Waffe einen direkten Angriff definitiv nicht überleben. Es ist gut, wenigstens manchmal mit jemand Kompetentem außer mir zu arbeiten. Cyber, führe uns zu den Energie-Schleusen.“]],
				['4'] = [[„Natürlich ist er sicher! Er ist ein Zauberer, verdammt noch mal!<new_line>Und noch etwas, Choomba, zu deiner Beruhigung. Ich habe erfahren, dass der Krampus Energie-Schleusen hat. Jetzt weiß ich, was sie sind.“]],
				['3'] = [[„Das stimmt. Aber es ist auch wahr, dass ein direkter Angriff das ist, was der Feind am wenigsten erwartet. Wenn wir alle auf einmal zuschlagen, wird diese Superwaffe die absorbierte Energie nicht bewältigen können und einfach explodieren.“]],
				['2'] = [[„Diese Waffe ist ein dummer magischer Staubsauger! Je mehr ich darüber lerne, desto weniger verstehe ich, was ich tun soll.“]],
				['1'] = [[„Was ist hier passiert? Konntet ihr die Dinge nicht ohne mich regeln, hm?“]]
			}
		},
		['bandit_camp_quest_pt2'] = {
			['episode'] = {
				['6'] = [[„Ganz ruhig. Wir wurden ein bisschen aufgehalten, aber wir gehen jetzt. Wir sind froh, dass du das friedlich abgewickelt hast. Nimm dieses kleine Geschenk als Zeichen unseres Danks.“<new_line>Die Räuber packten ihre Sachen fertig und flohen aus dem Lager.]],
				['5'] = [[„Ich habe meine Meinung geändert“, sagte der Anführer der Räuber grinsend.]],
				['4'] = [[Sollte ich vielleicht mal nachsehen, was im Räuber-Lager so vor sich geht?]],
				['3'] = [[Ich war mehr als zufrieden, dass ich mich um diese Schurken kümmern konnte.]],
				['2'] = [[Nach ein paar Tagen kehrte ich zum Lager zurück. Die Räuber waren nirgendwohin gegangen, ebenso wenig wie ihre Besitztümer.]],
				['1'] = [[Nach ein paar Tagen kehrte ich zum Lager der Räuber zurück. Sie hatten Wort gehalten und nur eine Notiz hinterlassen.<new_line>„Danke, dass wir das friedlich abwickeln konnten.“]]
			},
			['choice'] = {
