				['5'] = [[Sich die Notiz schnappen und verschwinden]],
				['4'] = [[Jetzt los]],
				['3'] = [[Auf den vereinbarten Zeitpunkt warten]],
				['2'] = [[Angreifen]]
			}
		},
		['halloween_boss_2022_4'] = {
			['choice'] = {
				['1'] = [[„Hast du irgendwelche Anhaltspunkte?“]]
			},
			['episode'] = {
				['1'] = [[Der Bauer traf mich bei seinem leeren Kornspeicher:<new_line>„Alles ist weg, ich habe keine Ernte mehr! Und letztes Jahr habe ich auf dem Jahrmarkt noch einen Preis gewonnen – für den größten Kürbis!“]],
				['2'] = [[„Hier gibt es nichts. Du kannst auch in der Mühle und in der Scheune nachsehen. Das haut einfach nicht hin! Es findet ein Fest statt, und ich habe keinen einzigen Kürbis ...“]]
			}
		},
		['halloween_boss_2022_2'] = {
			['choice'] = {
				['1'] = [[„Woher kommen die?“]],
				['2'] = [[„Und wie kommen die Zombies durch?“]]
			},
			['episode'] = {
				['3'] = [[„Nun, dieser Drecksack hat den Wachen etwas ins Getränk geschüttet, sie sind eingeschlafen, und er ist auf den Friedhof gelangt. Und jetzt können wir ihn nicht erwischen – er versteckt sich hinter einem giftigen Nebel und einer Menge toter Menschen. Los, verpass ihm eine Tracht Prügel!“]],
				['2'] = [[„Vom alten Friedhof, natürlich. Wir haben dort sofort eine Wache aufgestellt – wir wissen ja, was passiert, wenn der Pestarzt auf Gräber stößt ...“]],
				['1'] = [[„Hallo, Jäger! Bisher sind wir stark genug, um einzelne Zombies abzuwehren, aber es tauchen immer mehr von ihnen auf.“]]
			}
		},
		['take_stash_quest'] = {
			['episode'] = {
				['3'] = [[Wer auch immer dieses Versteck verlassen hatte, war in Eile gewesen und hat nicht tief gegraben. Ich konnte mir die Kiste schnell holen.]],
				['2'] = [[Als ich den Flecken erreichte, sah ich ein frisches Loch und Fußspuren auf dem Boden.]],
				['1'] = [[Ich habe das Versteck leicht gefunden.]],
				['7'] = [[Ich habe die Kiste. Hier kann ich nichts mehr tun.]],
				['6'] = [[Es war nicht einfach, aber ich habe es geschafft, die Räuber zu schlagen.]],
				['5'] = [[Die Fußspuren führten mich tief in die Wälder und direkt zu einer Leiche. Der Körper lag auf dem Boden und hielt eine schmutzig-fleckige Kiste fest in den Armen.]],
				['4'] = [[Die Fußspuren führten mich durch die Wälder direkt ins Lager der Räuber. Sie hatten das Versteck eindeutig beraubt. Ich sollte zum Stützpunkt zurückkehren und es melden. Obwohl ich wette, dass sie mir mehr zahlen, wenn ich die Ware selbst von den Banditen zurückhole.]],
				['11'] = [[Mein Manöver funktionierte. Der Bär ist abgelenkt und kümmert sich nicht mehr um mich.]],
				['10'] = [[„Okay, und wer ist jetzt der König der Wälder?“]],
				['9'] = [[Irgendwie schaffte ich es, die Kiste aus den Händen des toten Mannes zu winden. Doch plötzlich hörte ich ein lautes Knacken hinter mir. Ich drehte mich um und sah einen Bären. Der Bär schaute mich an, als hätte er sein Mittagessen gefunden.]],
				['8'] = [[Als ich den Körper untersuchte, hörte ich ein lautes Knacken hinter mir. Ich drehte mich um und sah einen Bären.]]
			},
			['choice'] = {
				['3'] = [[Die Kiste nehmen und verschwinden]],
				['2'] = [[Das Versteck mit den Händen ausheben]],
				['1'] = [[Das Versteck mit der Schaufel ausheben]],
				['6'] = [[Die Leiche durchsuchen]],
				['5'] = [[Den Fußspuren auf dem Boden folgen]],
				['4'] = [[Sicherstellen, dass du dich vorbereitet hast, bevor du etwas annimmst]],
