		['hunt_quest'] = {
			['episode'] = {
				['3'] = [[Die Frau schaute mich mit verzweifelten Augen an.<new_line>„Gibt es irgendeine Chance ... dass du ein bisschen Nahrung übrig hast?“]],
				['1'] = [[Eine Frau, die ein kleines Kind in den Armen hielt, näherte sich mir. Sie sagte nichts, sondern stellte sich einfach neben mich und starrte auf ihre Füße.]],
				['7'] = [[Die Frau nickte dankbar.<new_line>„Vielen Dank. Damit sollte ich die nächsten paar Tage überstehen ...“<new_line>]],
				['6'] = [[Der Chef studierte mein Gesicht intensiv.<new_line>„Hast du es bekommen?“]],
				['5'] = [[Der Kunde runzelte die Stirn.<new_line>„Nein, Kumpel, so hatten wir nicht gewettet. Das ist nicht annähernd genug Fleisch. Du bekommst garantiert kein Geld von mir.“]],
				['4'] = [[Die Frau starrte mich ehrfurchtsvoll an.<new_line>„Vielen Dank! Du bist so freundlich!“]],
				['11'] = [[„Wir hatten einen Deal. Gib mir das Fleisch oder verschwinde!“]],
