				['9'] = [[Der Kunde nickte mir zufrieden zu.<new_line>„Ist also alles glatt gelaufen?“]],
				['8'] = [[Das Gesicht der Frau wurde grau.<new_line>„Entschuldige, dass ich dich gestört habe.“<new_line>]]
			},
			['choice'] = {
				['3'] = [[„Ich kann dir ein bisschen Fleisch abgeben“]],
				['2'] = [[„Brauchst du etwas?“]],
