		['emba_navigator_fight'] = {
			['episode'] = {
				['3'] = [[„Fassen wir uns kurz: Um das Labor zu verbessern, brauchst du Sporoblüten und Genofrüchte. Sie wachsen in überwucherten Wissenschaftsstädten.<new_line>Dort brauchst du einen AIDA-Anzug und Unkrautvernichter, die du von Wissenschaftlern im Tausch gegen Disketten bei den Verbrauchsgütern bekommen kannst.<new_line>Um den Biologen Waffen und alle möglichen coolen Sachen herstellen zu lassen, brauchst du Biomasse. Diese kann aus den Biomutanten gewonnen werden. Das war’s dann auch schon. Grüße, bis zum nächsten Mal!“]],
				['2'] = [[„Gut gemacht, ich habe dich beobachtet. Also, geh zum Biologen, du musst ihm bei seinen Forschungen helfen. Bring die Blackbox zu ihm. Und wenn du schon dabei ist, mach sich in seinem Labor nützlich.<new_line>Vergiss nicht, die Biomasse aus den Myzelstücken zu gewinnen. Du kannst das mit einem Messer machen, aber ich schlage einen Extraktor vor. Disketten kannst du bei den Wissenschaftlern bei den Verbrauchsgütern gegen einen Extraktor eintauschen.<new_line>Was noch …? Hast du dich alles über die Wissenschaftsstadt gemerkt, oder soll ich es wiederholen?“]],
				['1'] = [[Eine Gruppe von Biomutanten läuft die Straße entlang. Sie sind auf der Suche nach etwas Fressbarem.]]
			},
			['choice'] = {
				['1'] = [[„Kannst du das wiederholen?“]],
				['2'] = [[„Ich habe mir alles gemerkt. Bis zum nächsten Mal.“]]
			}
		},
		['halloween_def_hard'] = {
			['episode'] = {
				['14'] = [[Der Zauberer gähnte laut.<new_line>„Puh. Ich habe gute Arbeit geleistet. Andere sollten sich davon eine Scheibe abschneiden ... Leider besagt der Zauberer-Kodex, dass jede Person, die mir angeblich hilft, einen Zettel erhalten muss. Wenn sie den lesen, können sie mich in der Stunde der Not herbeirufen, blah blah blah ...<new_line>Aber denk dran, wenn es nicht wirklich eine Stunde der Not ist, wirst du es bereuen. Ende der Geschichte.“]],
				['3'] = [[„Mach dich auf den Weg. Steh nicht einfach nur rum.“]],
				['2'] = [[„Steh nicht einfach so da! Soll ich etwa allein die Horden von Monstern besiegen und das Dorf verteidigen? Jemand könnte ruhig mal einen Finger rühren, um mir zu helfen.“]],
				['1'] = [[„Bist du hier, um zu helfen oder nur um zuzusehen?“]],
				['7'] = [[Der Zauberer winkte träge mit seinem Stab.<new_line>„Abrakarabraboom. Erledigt. Ich hoffe, ich habe diese ganze Kraft nicht umsonst an dich verschwendet.“<new_line><new_line>Das Heulen der Bestien scheint jetzt sehr nah zu sein.]],
				['9'] = [[„Du hast versucht, mir zu helfen, aber es hat nicht viel gebracht. Vielleicht kannst du dir jetzt einen etwas klügeren Plan ausdenken? Wähle den Zauber, den du diesmal willst.“]],
				['5'] = [[Der Zauberer winkte träge mit seinem Stab.<new_line>„Abrakarabraboom. Erledigt. Ich hoffe, ich habe diese ganze Kraft nicht umsonst an dich verschwendet.“<new_line><new_line>Das Heulen der Bestien scheint jetzt sehr nah zu sein.]],
				['4'] = [[„Ich habe tagelang gekämpft, ohne mich auszuruhen, und jetzt soll ich mir einen Plan ausdenken?! Gott, du bist nutzlos. Also gut, ich glaube, die Monster nähern sich wieder. Wie wäre es, wenn ich dich mit einem Zauber belege, damit du dich nützlich machen kannst. Sag mir, welchen Spruch du willst.“]],
				['12'] = [[„Abrakabra ... Das ist genug. Es wird reichen. Die Bestien nähern sich. Mach dich bereit.“]],
				['13'] = [[„Abrakabra ... Das ist genug. Es wird reichen. Die Bestien nähern sich. Mach dich bereit.“]],
				['10'] = [[Der Zauberer stupste mich mit seinem Stab an und sagte:<new_line>„Abrakadabra. Erledigt.“<new_line><new_line>Die Bestien nähern sich wieder,.]],
				['11'] = [[„Du warst nur zu 5 % nützlich. Warum sollte ich Magie an dich verschwenden? Kannst du dich vielleicht ein bisschen mehr anstrengen? Überlege dir gut, welchen Zauber ich dir auferlegen soll, damit du wirklich von Nutzen bist.“]]
			},
			['choice'] = {
				['3'] = [[„Was ist der Plan? Was machen wir?"]],
				['2'] = [[„Ich komme nur vorbei.“]],
				['1'] = [[„Ich bin hier, um zu helfen.“]],
				['7'] = [[„Ich will so schnell rennen wie ein Wolf.“]],
				['6'] = [[„Ich will so gut sehen können wie ein Adler.“]],
				['5'] = [[„Ich will so stark wie Herkules werden.“]],
				['4'] = [[„Ich will fliegen wie ein Vogel.“]],
				['9'] = [[„Vielen Dank.“ (Gehen)]],
				['8'] = [[Angreifen]]
			}
		},
