		['catch_thief_quest'] = {
			['episode'] = {
				['19'] = [[Am Morgen erzählte ich dem Leiter des Lagerhauses alles. Er schüttelte enttäuscht den Kopf.<new_line>„Tut mir leid, Kumpel, du hast den Dieb nicht erwischt, dann gibt es auch keine Belohnung.“]],
				['41'] = [[„Naja, danke. Jetzt ist alles in Ordnung. Diese Diebe haben mich ausgesaugt! Hier, das steht dir zu.“]],
				['40'] = [[Der Vorarbeiter des Lagerhauses kann sehr überzeugend sein. Der Dieb ist aufgeflogen und hat seine Komplizen verpfiffen. Der Chef sagte zu mir:<new_line>„Also, das sind ja Neuigkeiten. Jetzt werde ich die Basis um Hilfe bitten müssen. Oder könntest du dich mit dieser Bande befassen? Ich werde dich für deine Bemühungen bezahlen.“]],
				['39'] = [[Der Vorarbeiter des Lagerhauses kann sehr überzeugend sein. Der Dieb hat geknackt und uns gesagt, wo er die Beute versteckt hat. Der Chef hat mich gefragt:<new_line>„Könntest du das, was dieser Schnorrer weggeschleppt hat, wieder zurückholen? Ich werde dich für deine Mühe bezahlen.“]],
				['38'] = [[Der Verbrecher wollte nichts zugeben. Vielleicht ist er nur ein flüchtiger Dieb, und es war jemand anderes, der das Lagerhaus überfallen hat.]],
				['35'] = [[Es ist ein ziemlicher Hindernisparcours, und jetzt wird der Dieb nirgendwo hingehen. Aber dann hörte ich ein Rascheln in der Nähe. Jemand kommt, es ist Zeit, sich zu verstecken!]],
				['1'] = [[Laut Leiter des Lagerhauses hat es sich jemand zur Gewohnheit gemacht, alles, was nicht niet- und nagelfest ist, mitzunehmen. Wir müssen diesen Dieb fangen.]],
				['34'] = [[Ich sah, wie der Dieb eine Leiter über den Zaun warf und begann, hinunterzuklettern. Aber auf den letzten Stufen fiel er mit einem Knirschen hinunter. Er hat sich das Bein verstaucht, wenn nicht schlimmer. Aber wenigstens haut er nicht ab!]],
				['7'] = [[Es ist Nacht. Ich sollte mir überlegen, von welchem Punkt ich beobachte.]],
				['6'] = [[Die Leitung der Stolperfalle ist bereit und gut versteckt.]],
				['5'] = [[Ich habe die Leiter mit einer Sprengfalle versehen, aber es sieht sicher aus. Jetzt höre ich ein Rascheln in der Nähe. Jemand kommt und es ist Zeit, sich zu verstecken!]],
				['4'] = [[Ich habe in der Nähe des Zauns Fußspuren gesehen. Sie führten in Richtung Wald und es gibt auch eine Schleifspur.<new_line>Im Gebüsch habe ich eine handgefertigte Leiter gefunden.]],
				['10'] = [[Der Leiter des Lagerhauses dankte mir und fügte hinzu:<new_line><new_line>„Ich habe den Eindruck, dass er nicht allein gearbeitet hat. Aber jetzt werden wir das wohl nicht mehr erfahren.“]],
				['22'] = [[Der Leiter des Lagerhauses war mit den Ergebnissen zufrieden.<new_line><new_line>„Ich wusste, dass ich dir vertrauen kann. Nimm deine Belohnung, du hast sie verdient.“]],
				['29'] = [[Der Anführer gab mir meinen Anteil an den gestohlenen Waren.]],
				['8'] = [[Ich hörte ein Krachen aus dem Wald. Offensichtlich hatte meine tödliche Falle funktioniert.<new_line>Ich sollte dem Leiter des Lagerhauses die Neuigkeiten mitteilen.]],
				['17'] = [[Nachdem er den Job beendet hatte, drehte sich der Dieb um und begann auf der Leiter nach oben zu steigen. Glücklicherweise brachen die von mir gelockerten Sprossen unter seinem Gewicht und er stürzte zu Boden.]],
				['16'] = [[Ich hörte ein Rascheln im Gebüsch und sah den dunklen Schatten eines Mannes, der etwas in den Händen hielt. Allerdings konnte ich nicht sehen, was es war.]],
				['15'] = [[Der Leiter des Lagerhauses warf einen Blick auf den Dieb.<new_line><new_line>„Er ist am Leben? Gut, dann können wir ihn jetzt befragen.“]],
				['14'] = [[Der Dieb wirft die Leiter über den Zaun auf unsere Seite. Als er herunterkletterte, sprang ich aus meinem Versteck. Er stolperte und die Leiter brach unter seinem Gewicht, sodass er zu Boden stürzte.]],
				['24'] = [[Der Schurke kannte die Wälder eindeutig besser als ich. Nach ein paar Minuten war er außer Sicht.]],
				['25'] = [[Nun, ich habe den Dieb eingeholt. Aber er führte mich direkt zu seinen Kumpanen, die echt nicht glücklich über mich waren.]],
				['28'] = [[Die Räuber hegten einen Groll gegen mich, aber als ich ihnen den Schlüssel zum Lagerhaus zeigte, änderte sich ihre Stimmung. Wir räumten das Lagerhaus schnell aus.]],
				['23'] = [[Der Dieb erblickte mich. Er ließ etwas fallen, was ihn am Wegrennen hinderte und flüchtete in die Wälder.]],
				['30'] = [[Ich schlug die Räuber, aber jetzt muss ich mir überlegen, was ich als nächstes mache.]],
				['31'] = [[Nach einiger Zeit stürzte der Leiter ins Lagerhaus, aufgeregt.<new_line><new_line>„Was geht hier vor sich? Wie sind die Räuber ins Lagerhaus gelangt?“]],
				['32'] = [[„Das war ein cleverer Trick. Gute Arbeit! Ich hab‘s zuerst nicht verstanden. Du hast eine ordentliche Belohnung verdient.“]],
				['18'] = [[Ich schaffte es nicht, den Dieb zu fangen, und er verschwand in die Nacht.]],
				['12'] = [[Ich hörte ein dumpfes Geräusch, als würde irgendwer die Leiter auf der anderen Zaunseite aufstellen. Wenn er herüberklettert, lauere ich im Hinterhalt.]],
				['13'] = [[Als ich den Dieb fesselte, bemerkte ich, dass er sich auf den Eisenstangen gepfählt hatte und jetzt wie verrückt blutete.]],
				['21'] = [[Nachdem ich sie besiegt hatte, schaute ich mich im Lager um. Ich war nicht stark genug, um alles zurückzubringen, was sie gestohlen hatten, aber ich schnappte mir, was ich konnte.]],
				['20'] = [[Der Wolf führte mich zu einem ganzen Räuberlager.]]
			},
			['choice'] = {
				['40'] = [[Den schlauen Plan erklären]],
				['48'] = [[Hol die Beute]],
				['2'] = [[Sich im Gebiet umsehen]],
				['1'] = [[Bis zum Einbruch der Nacht auf den Dieb warten]],
				['47'] = [[Kehre zurück in Deckung]],
				['7'] = [[Die untersten Sprossen der Leiter zerstören]],
				['6'] = [[Auf dem Weg zur Leiter eine Granaten-Stolperfalle auslegen]],
				['46'] = [[Hebe eine Grube aus]],
