		['boatman_camp_chat'] = {
			['episode'] = {
				['3'] = [[„Du gibst einfach keine Ruhe! Ich habe dir schon gesagt, dass es gefährlich ist und du noch nicht bereit bist! Du solltest ein bisschen länger hier bleiben und deine Fertigkeiten verbessern. Hilf den Einheimischen und hol dir ein paar Aufträge in der Taverne. Verkaufe ein paar Waren, sprich mit dem Vorarbeiter. Dann, wenn du bereit bist, werde ich dir alles erzählen.“]],
				['2'] = [[„Suppe?“ Der Schiffer lächelte. „Kein Grund, so ein Fass aufzumachen. Ich hörte von einem Wissenschaftsinstitut, in dem sie vielleicht an diesem Virus gearbeitet haben. Aber dort ist es gefährlich. Strahlung, Mutanten ... Menschen. Du solltest den Fuß nicht in diese Gegenden setzen.“]],
				['1'] = [[Der Schiffer kochte gerade Fischsuppe in einem großen Trog. Als er mich sah, nickte er:<new_line>„Du hast es also geschafft. Hast du in Belomorsk gefunden, wonach du gesucht hast?“]]
			},
			['choice'] = {
				['3'] = [[„Du hast mich überzeugt. Bis zum nächsten Mal.“]],
				['2'] = [[„Wo ist dieses Institut?“]],
				['1'] = [[„Nein, ich will wissen, was passiert ist. Wer den Krieg begonnen hat, was hinter der Epidemie steckt ...“]]
			}
		},
		['infected_shop'] = {
			['episode'] = {
				['1'] = [[Nach dem Äußeren des Gebäudes zu urteilen, hatten es sich die Mutanten-Insekten in dem alten Laden bequem gemacht. Vielleicht gab es drinnen etwas Wertvolles, aber es war ein gefährlicher Job.]]
			}
		},
		['comrade_in_trouble_bar'] = {
			['episode'] = {
				['3'] = [[Der Oberwächter nickte, als er meinen Bericht hörte:<new_line>„Gute Arbeit! Du bist eine große Hilfe. Hier ist ein kleines Extra für deine Mühen.“]],
				['2'] = [[Der Oberwächter nickte, als er meinen Bericht hörte.<new_line>„Nachricht erhalten, wir werden ihm Unterstützung schicken. Danke für deine Hilfe!“]],
				['1'] = [[Der Oberwächter nickte, als er meinen Bericht hörte.<new_line>„Danke für die Nachricht, du hast deine Arbeit erledigt.“]]
			}
		},
		['pumpkin_seed'] = {
			['episode'] = {
				['1'] = [[Ich pflanzte den Samen in den Boden und goss ihn. Buchstäblich innerhalb von Sekunden erschien ein Spross, der ein paar Momente später zu einer dicken Ranke wurde. Es dauerte nicht lange, bis ein kleiner Kürbis entstanden war. Zu meiner großen Überraschung wuchsen diesem kleinen Kürbis Augen, Zähne und kleine Pfoten. Nach einer weiteren Minute löste sich der Kürbis von der Ranke und packte meinen Stiefel. Ich bin mir nicht ganz sicher, was hier los ist, aber ist wohl an der Zeit, diesem Kürbis zu zeigen, wer hier der Boss ist.]],
				['2'] = [[„Es scheint, dass endlich ein würdiger Gegner in dieser Welt erschienen ist. Unser Kampf wird legendär! Auf Wiedersehen, Mensch“, brüllte der Kürbisriese. Er ließ mich verdutzt zurück und marschierte in die Ferne, wobei er bei jedem Schritt die Erde erschütterte. Für mich wird es wohl Zeit, in die Taverne zurückzukehren.]]
			}
		},
		['halloween_energy_easy'] = {
			['episode'] = {
				['3'] = [[„Super! Jetzt müssen wir die Samen pflanzen. Fang an, wenn du bereit bist.“]],
				['2'] = [[„Vielen Dank! Zuerst müssen wir einen Garten ausheben. Fangt an, wenn ihr so weit seid.“]],
				['1'] = [[„Hallo, Wanderer! Kannst du uns helfen? Wir vermissen unseren Kommandanten sehr. Es ist schwer hier ohne ihn, deshalb wollen wir einen neuen anbauen. Wir haben schon Samen und einen Garten gefunden, aber wir sind zu ungeschickt für die Arbeit.“]],
				['7'] = [[„Oh, Sonnengesichtiger Kaiser, kehre zu uns zurück. Vergib uns, dass wir dich im Stich gelassen haben. Wir brauchen dich. Kehre zurück und wir werden dir treu sein!“<new_line><new_line>Das Ritual dauerte mehrere Minuten. Danach zerstreuten sich die Kürbisköpfe mit niedergeschlagenen Blicken.<new_line>]],
				['6'] = [[„Hier geht es ums Geschäft, und das Geschäft hat Vorrang vor allem anderen. OK, nimm es und geh an die Arbeit.“]],
				['5'] = [[„Sieh nur zu, dass du ihn wieder verschließt. Dieser Dünger ist für die Kürbisköpfe Gold wert. Ich habe tagelang daran gearbeitet.“]],
				['4'] = [[„Gut gemacht. Jetzt müssen wir nur noch die Pflanzen düngen. Kannst du etwas Dünger beim Bauern abholen? Er sollte zu Hause sein.“]],
				['9'] = [[„Zuerst müssen wir einen Garten ausheben. Fang an, wenn du bereit bist.“]],
				['8'] = [[„Nein ... Vielleicht haben wir etwas falsch gemacht. Wir werden es noch einmal versuchen. Vielen Dank für deine Hilfe.“]],
				['10'] = [[„Jetzt müssen wir die Samen pflanzen. Fang an, wenn du bereit bist.“]],
				['11'] = [[„Alles ist fast fertig. Wir müssen die Pflanzen nur noch düngen.“]]
			},
			['choice'] = {
				['6'] = [[„Konntest du den Kommandanten zurückholen?“]],
				['5'] = [[„Warum hilfst du ihnen überhaupt?“]],
				['4'] = [[„OK.“ (Zum Bauern gehen)]],
				['3'] = [[Anfangen]],
				['2'] = [[„Keine Zeit.“ (Gehen)]],
				['1'] = [[„In der Tat ungeschickt. Sicher, ich werde helfen.“]]
			}
		},
		['halloween_energy_medium'] = {
			['episode'] = {
				['9'] = [[Ich hob eine weitere Kiste auf, doch dann stieß mich etwas hart von hinten an. Die Kiste fiel mit einem lauten Krachen um.<new_line>„Oh je, was hast du getan?! Was hast du getan?“<new_line>]],
				['14'] = [[Ich bin durch das Portal gesprungen und wieder in der Hexenhütte gelandet. Ich glaube, sie ist wütend auf mich. Ich sollte sofort von hier verschwinden.]],
				['3'] = [[„Okay, es ist fertig, Junge. Das ist ein Portal, das es uns erspart, meine Sachen zu Fuß zu suchen. Spring rein. Ich bin gleich bei dir.“]],
				['2'] = [[„Na, bist du nicht der beste kleine Junge?“<new_line><new_line>Die alte Hexe nahm den Deckel von einem großen Kessel und warf eilig einige Kräuter hinein.]],
				['1'] = [[„Oh, du bist gerade noch rechtzeitig gekommen, Junge! Hilf mir doch mal, ja? Ich brauche dringend jemanden, der mir beim Umzug hilft. Alleine schaffe ich das nicht.“]],
				['8'] = [[Ich warf eine weitere Kiste in das Portal<new_line>„OK, jetzt können wir gehen.“<new_line>]],
				['7'] = [[„OK, jetzt die.“<new_line>Die Hexe zeigte mit dem Finger auf eine andere Kiste.<new_line>“]],
				['6'] = [[Ich warf die Kiste in das Portal. Die alte Hexe schloss sich dem Vorgang mit unnatürlicher Behändigkeit an<new_line>„Jetzt die da.“]],
				['5'] = [[Wir landeten in einem gut erhaltenen Lagerhaus. Die alte Hexe flüsterte: <new_line>„Heb die Kiste auf und wirf sie in die Pforte. Sei einfach still.“<new_line>]],
				['4'] = [[„Ha, wir haben wohl Angst, was? Es gibt keinen Grund, Angst zu haben. Ich bin eine nette Hexe. Ich will euch nichts Böses.“<new_line>Die alte Hexe sprang flink in den Kessel und verschwand.]],
				['12'] = [[„Vielen Dank, mein Junge. Du bist wirklich ein guter Junge.“]],
				['13'] = [[„Pst ... Wir bringen meine Sachen zurück, Junge. Ich habe es dir gesagt. Wenn du Lärm machst, werde ich dich bestrafen müssen.“]],
				['10'] = [[Die Tore des Lagerhauses schwangen auf und ein Wachmann erschien in der Tür:<new_line>„He, was haben wir denn hier?! Diebe? Ich werde euch zeigen, was mit Dieben passiert!“<new_line>]],
				['11'] = [[Während ich mit der Wache kämpfte, schleppte die Hexe weiter eifrig Kisten.<new_line><new_line>„Okay, wir können jetzt gehen.“<new_line>]]
			},
			['choice'] = {
				['7'] = [[„Ich habe nichts gemacht! Du hast mich geschubst!“]],
				['6'] = [[Die Kiste nehmen und sie in das Portal werfen]],
				['5'] = [[„Nein, spring du zuerst.“]],
				['4'] = [[In den Kessel springen]],
				['3'] = [[Warten]],
				['2'] = [[„Okay, ich werde helfen.“]],
				['1'] = [[„Irgendetwas ist hier faul. Was machen wir in einem Lagerhaus?“]],
				['8'] = [[In das Portal springen]]
			}
		},
		['comrade_in_trouble_rad'] = {
			['episode'] = {
				['1'] = [[Der Scout liegt auf dem Boden. Es mag seine eigene Dummheit gewesen sein, aber das war eine schreckliche Art zu gehen.]]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['3'] = [[„Ich habe einen speziellen Trank gebraut, den sie wohl zu sehr mochten. Weißt du, ich freue mich über Gäste, aber nur, wenn sie eingeladen sind. Ich habe eine interessante Falle aus Pilzen, mit der wir sie fangen können. Soll ich sie aufstellen oder werden wir auch so mit denen fertig?“]],
				['2'] = [[Während die Hexe die Monster ablenkte, rannte ich direkt auf die Hütte zu. Kaum war ich drinnen, stand das Haus auf den Beinen.<new_line><new_line>„Schon besser! Hilfst du einer alten Dame raus? Gemeinsam werden wir sie schneller vertreiben.“]],
				['1'] = [[Eine Menschenmenge hatte sich um die Hütte versammelt wie Fans bei einem Rockkonzert.<new_line>Die alte Hexe steckte ihren Kopf aus dem Fenster:<new_line>„Komm schnell her, Junge! Ich werde sie ablenken!“]],
				['7'] = [[„Eins ...“]],
				['6'] = [[„Es scheint alles bereit zu sein. Wir springen auf drei. Fertig?“]],
				['5'] = [[„Kluger Junge! Es scheint alles bereit zu sein. Wir springen auf drei. Fertig?“]],
				['4'] = [[„Deine Wahl, Junge. Wie wäre es mit einem Trank vor dem Kampf? Übrigens, ich hatte noch keine Gelegenheit, ihn auszuprobieren.“]],
				['9'] = [[„Drei!“]],
				['8'] = [[„Zwei ...“]],
				['10'] = [[„Oh, vielen Dank, mein Junge!“]],
				['11'] = [[„Mein Rücken hat sich verkrampft. Ich bin nicht mehr so jung. Nichts für ungut, nicht wahr, mein Junge? Das hast du gut gemacht! Nimm deine Belohnung und mach dich auf den Weg.“]]
			},
			['choice'] = {
				['3'] = [[„Stell die Falle auf.“]],
				['2'] = [["Wie sind diese Biester hierher gekommen?"]],
				['1'] = [[Zur Hütte laufen]],
				['7'] = [[....]],
				['6'] = [[„Nein, danke.“]],
				['5'] = [[Den Trank trinken]],
				['4'] = [[„Wir werden mit denen fertig.“]],
				['9'] = [[„Wieso bist du nicht gesprungen?“]],
				['8'] = [[Springen]]
			}
		},
		['snowmen_fortress'] = {
			['episode'] = {
				['1'] = [[Die Burg der Schneemänner ist ein ansehnliches Bauwerk aus Eisblöcken. Die Wächter auf den Türmen werfen mir unter ihren Eimerhüten misstrauische Blicke zu und kneten sich schon mal Schneebälle zurecht.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['1'] = [[Der Knäuel blieb stehen. Es war klar, wo ich das Monster finden konnte. Eine pechschwarze Sturmwolke thronte über einem Gebäude in der Nähe und entlud ihre Blitze in die verbogenen Blitzableiter auf dem Dach.]],
				['2'] = [[Da war keine Tür – oder besser gesagt: Sie lag neben dem Eingang. Innen stach mir Ozon in die Nase. Und überall verliefen Kabel. Ich folgte ihnen und drang tiefer in das Gebäude ein. Die Kabel führten mich in einen Raum in der Mitte. Dort lag das Monster regungslos auf einem Metalltisch. Ein seltsamer, buckliger Mann huschte geschäftig um das Monster herum. Als er mich erblickte, schrie er erschreckt auf und betätigte einen Schalter an der Wand. Elektrizität entlud sich und das Monster erwachte zu Bewusstsein.]]
			}
		},
		['comrade_in_trouble_loot'] = {
			['episode'] = {
				['1'] = [[In dem leeren Lager liegt eine schwere Kiste auf dem Boden. Der Späher hat mich gebeten, sie in die Siedlung zu bringen.]]
			}
		},
		['santa_cave'] = {
			['choice'] = {
				['1'] = [[„Was? Ich kann dir nicht folgen.“]],
				['2'] = [[„Bitte sehr.“]]
			},
			['episode'] = {
				['3'] = [[Der Geschäftsnachtsmann zeigt mit dem Finger auf den halb geschmückten Baum, richtet ihn dann auf mich und wirft die Hände hoch. Der neu eingetroffene Mann kratzt sich mit seiner Keule am Kopf.]],
				['2'] = [[„Ho ho ho, das wird eine harte Zeit werden. Aber weißt du, wenn die Natur jemandem gewisse Fähigkeiten nimmt, gibt sie ihm … Ich meine, dass er ein fantastischer Fährtenleser ist! Lass es mich versuchen.“]],
				['1'] = [[„Hmmm? Oooh!“]],
				['4'] = [[„Hm … Äh, aah, buh, oooh!“<new_line><new_line>Anscheinend hat Höhlennachtsmann etwas verstanden. Er winkt mit der Hand, dass wir ihm folgen sollen.]]
			}
		},
		['halloween_search_easy'] = {
			['episode'] = {
				['3'] = [[„Wie soll ich sonst herausfinden, was er bewirkt? Wenn du es nicht ausprobierst, kannst du die Belohnung vergessen.“]],
				['2'] = [[„In Ordnung, eine Prise hiervon ... Ein Spritzer von dem ... Doppelt, doppelt, Müh und Gemoppel ... Feuer brennt und Kessel blubbert! Ich glaube, es ist fertig. Jetzt probier mal einen Schluck von meinem Trank.“]],
				['1'] = [[„Sei gegrüßt, Junge. Hast du zufällig ein paar Dinge, die ich brauche? Ich werde dich für deine Hilfe großzügig belohnen.“]],
				['4'] = [[„Du bist ein kluger Kopf, Junge. Es zu versuchen, war die richtige Entscheidung.“]]
			},
			['choice'] = {
				['3'] = [[„Das war nicht der Deal.“]],
				['2'] = [[„Das geht nicht, Oma.“ (Gehen)]],
				['1'] = [[„Hier, nimm das.“]],
				['4'] = [[„Also gut, gib mir den Trank.“]]
			}
		},
		['emba_time_medium2'] = {
			['episode'] = {
				['10'] = [[„Hast du das alles aufgezeichnet? Gut. Diese beiden Parasiten haben mehrere Leichenfresser zu ihrem Rudel gebracht.<new_line>Sie sind wirklich clever …“]],
				['14'] = [[„Verdammt, er ist weg! Macht nichts, eines Tages … Eines Tages. Und danke für deine Hilfe, du bist jetzt frei. Ich muss jetzt über einiges nachdenken.“]],
				['3'] = [[„Ausgezeichnet, lass uns gehen. Siehst du das Rudel da drüben? Die am stärksten überwucherten von ihnen sind die Grasfresser. Ich bezweifle, dass sie empfindungsfähig sind, denn ihre Körper sind zu sehr vom Myzel zerfressen. Schau, sie haben sich von der Gruppe abgespalten.<new_line>Folge ihnen. Wenn sie angreifen, denk daran, dass sie beim Sturz eine Sporenwolke freisetzen, die alle in der Nähe angreift. Halte also im Kampf Abstand zu ihnen.“]],
				['2'] = [[„Ich beobachte die Bewegungen der Biomutanten. Ich versuche, die Logik in ihren Wanderungen zu finden. Ich will einfach verstehen, ob es unter ihnen empfindungsfähige Arten gibt.<new_line>Ich brauche einen Partner, damit ich nichts verpasse.“]],
				['1'] = [[„Hey, komm her. Ich habe gesehen, wie du Biomutanten jagst. Ich denke, ich könnte dich auf eine Mission mitnehmen.“]],
				['11'] = [[„Ja, die Leichenfresser sind die interessantesten von ihnen. Das Myzel hat einfache Tiere verändert, sie stärker und intelligenter gemacht und vielen von ihnen ungewöhnliche Fähigkeiten verliehen.<new_line>Jetzt … Lass uns einen hierher locken. Ich will sowieso ihre Ernährung studieren. Zum Beispiel das Gemüse, das sie essen. Geh und such welche, ich werde hier warten.“]],
				['7'] = [[„Und jetzt lasst uns diese Parasiten beobachten.<new_line>Ihr Verhalten ist ziemlich komplex und wir müssen so vorsichtig wie möglich sein. Ich werde es mit diesen beiden selbst aufnehmen und du solltest versuchen, den anderen mit dem Sturmgewehr nicht zu verlieren.“]],
				['6'] = [[„Sie sind die intelligentesten von allen. Sie sind nicht so stark von dem Myzel infiziert. Sie haben sich wahrscheinlich am längsten gegen seinen Einfluss gewehrt. Trotzdem sind sie immer noch Monster.<new_line>Außerdem sind sie die gerissensten und gefährlichsten von ihnen. Sie haben nicht vergessen, wie man menschliche Waffen benutzt. Wenigstens stecken sie dich nicht an, denn sie spucken keine Sporen aus.“]],
				['5'] = [[„Ich folgte den Grasfressern und hielt fest, was sie taten, wenn sie anhielten. Am Ende kehrten sie zum Hauptrudel zurück.<new_line><new_line>„Hmm, sie sind wieder da. Waren sie auf Erkundungstour? Oder waren sie auf der Suche nach Nahrung? Wie auch immer, ich werde sie später analysieren. Und das sind Parasiten, siehst du?“]],
				['4'] = [[Die Grasfresser bewegen sich erst einmal langsam. Ich kann ihnen die ganze Zeit folgen oder mich um meine eigenen Angelegenheiten kümmern, ohne Gefahr zu laufen, sie aus den Augen zu verlieren.]],
				['12'] = [[„Super. Gut, dann lassen wir das jetzt hier, gehen weg und schauen, was der Aufpasser macht. Ich habe dort eine Stolperdrahtfalle angebracht.“]],
				['13'] = [[Der Leichenfresser näherte sich langsam, krächzte und entließ eine Sporenwolke aus seinem Maul in Richtung des Gemüses und kam erst danach auf es zu.<new_line>Er stolperte über das Kabel, es gab einen Knall und eine Wolke aus weißem Rauch. Es scheint jedoch, dass der Rauch keine Wirkung auf den Aufpasser hatte, nachdem er sich mit der Sporenwolke vermischt hatte.<new_line>Als sich der Rauch und die Sporen verzogen hatten, war der Leichenfresser nirgends mehr zu sehen.]],
				['9'] = [[Ich habe mich darauf konzentriert, den dritten Parasiten zu beobachten. Er hat es nicht eilig, irgendwo hinzugehen, also habe ich Zeit, mich vorzubereiten.]],
				['8'] = [[Die Jägerin lachte skeptisch.<new_line>„Ich hoffe, dein Vertrauen macht dich nicht eines Tages zu ihrem Abendessen. Wir werden die Parasiten aufspüren.<new_line>Ihr Verhalten ist ziemlich komplex und wir müssen so aufmerksam wie möglich bleiben. Ich werde diese beiden auf mich nehmen und du solltest versuchen, den anderen mit dem Sturmgewehr nicht zu verlieren.“]]
			},
			['choice'] = {
				['3'] = [[Verfolge die Grasfresser]],
				['2'] = [[Folge den Grasfressern]],
				['1'] = [[„Was ist die Aufgabe?“]],
				['7'] = [[Hol die Beobachtungsunterlagen heraus]],
				['6'] = [[„Verstanden.“]],
				['5'] = [[„Ja, ich weiß eine Menge über sie.“]],
				['4'] = [[„Was weißt du über sie?“]],
				['9'] = [[Entferne dich und beobachte]],
				['8'] = [[„Sollen wir jetzt die Leichenfresser beobachten?“]]
			}
		},
		['emba_craft_easy3'] = {
			['episode'] = {
				['3'] = [[Es hat unter der Erde gerumpelt. Das bedeutet, dass da unten etwas zusammengebrochen ist. Es sah so aus, als ob die Stängel und ihre schwarzen Wurzeln von selbst zitterten und nicht wegen des Bebens des Bodens.<new_line><new_line>Nach ein paar Sekunden war alles still. Das ganze Grün wird verwelken und sterben, jetzt, wo es von seinen Wurzeln abgeschnitten ist. Bereits jetzt schon scheinen sich die Blätter und schimmeligen Wucherungen zusammengerollt zu haben. Tolle Arbeit.]],
				['2'] = [[Der Lüftungsschacht ist sehr eng und es ist unmöglich, sich hineinzuzwängen. Das bedeutet, dass es ein Geheimnis bleiben wird, woher diese Pflanzen kommen. Für den Moment kann ich einfach die Öffnung des Schachts an der Oberfläche zerstören.<new_line>Ich könnte etwas Entflammbares oder Explosives hineinwerfen, denn es gibt keine anderen Möglichkeiten.]],
				['1'] = [[Die Öffnung des Lüftungsschachts befindet sich inmitten eines Knäuels aus verschlungenen Pflanzen. Es ist ziemlich merkwürdig, dass sie an einem anderen Ort wachsen und erst hier an die Oberfläche gekommen sind.]]
			},
			['choice'] = {
				['5'] = [[Wirf den Sprengstoff]]
			}
		},
		['cold_footprints'] = {
			['choice'] = {
				['1'] = [[Was ist hier los?]]
			},
			['episode'] = {
				['5'] = [[„Du hast sie alle getötet, nicht wahr?“, sagt die Weißhaarige plötzlich zu mir und sieht mich kalt an. „Das ist schließlich das, was ihr am besten könnt. Aber keine Sorge, bald beginnt der ewige Winter, und das Leid hat ein Ende.“ Damit schlägt sie ihren Stab auf den Boden, der Spiegel zerspringt, und ich sehe nichts mehr.]],
				['4'] = [[Im Spiegel erscheint das Bild einer großen weißhaarigen Frau mit einem Stab in der Hand. Sie sitzt auf einem Thron aus Eis, neben ihr sitzt ein gefesselter alter Graubart. Das ist Ded Moroz!]],
				['3'] = [[Als ich das Zelt des Anführers betrete, fällt mir sofort ein riesiger Eisspiegel in der Mitte auf. Wie magisch angezogen gehe ich zu ihm und lege die Hand auf seine glatte Fläche.]],
				['2'] = [[Ich glaube schon, die Eisbriganten besiegt zu haben, da kommt noch einer aus dem großen Zelt: ein Riese mit einer gigantischen Axt. Er wird wohl der Anführer sein. Zeit für den nächsten Kampf.]],
				['1'] = [[Ein großes Zelt mit frostüberzogenen Tierhäuten, ein paar kleinere Zelte. So sieht das Lager der Eisbriganten aus. Sie haben mich schon bemerkt, also ist es Zeit, sie anzugreifen.]]
			}
		},
		['embacity_biolab_helecopter'] = {
			['episode'] = {
				['1'] = [[Mehrere Biomutanten tummeln sich um den Hubschrauber. Es ist seltsam – es gibt hier nicht viel von den Pollen und Sporoblüten, die sie lieben. Offenbar ist es das Stadtzentrum, das sie anlockt.]]
			}
		},
		['emba_commander_easy'] = {
			['episode'] = {
				['3'] = [[„Ausgezeichnet, ausgezeichnet. Wir müssen die freien Radikale verändern … und die Diffusion um die Biomutanten erhöhen …<new_line>Ah, du bist immer noch da! Danke für deine Hilfe. Komm wieder und wir probieren eine neue Formel aus.“]],
				['2'] = [[„Ausgezeichnet! Wir werden dich natürlich nicht als Kanonenfutter benutzen. Du musst nur in sicherer Entfernung stehen und Signale an die Geschütztürme senden, während wir unsere Beobachtungen machen.<new_line>Du bist erfahren, also wirst du die Steuerung schon hinbekommen. Los geht's!“]],
				['1'] = [[„Hallo, Kamerad! Siehst du diese Biomutanten? Wir haben sie mit einer speziellen Lösung bestäubt und sie sollten sich im Kampf anders verhalten.<new_line>Wir brauchen jemanden, der ihre Angriffe eindämmt, während wir unsere Beobachtungen machen.“]]
			},
			['choice'] = {
				['1'] = [[„Ich bin bereit zu helfen.“]],
				['2'] = [[„Ich bin nicht bereit dafür.“]]
			}
		},
		['stock_bandit'] = {
			['choice'] = {
				['7'] = [[Durch die Seitentür fliehen]],
				['6'] = [[Warten]],
				['5'] = [[Den Plünderer nach unten stoßen und die anderen bekämpfen]],
				['4'] = [[Unauffällig verhalten und warten]],
				['3'] = [[Die Kiste auf den Plünderer werfen]],
