			},
			['episode'] = {
				['7'] = [[Nachdem ich nach unten geklettert bin, mussten sich meine Augen erst an die Dunkelheit gewöhnen. Das Licht, dass durch den Gully hereinfällt, sollte ausreichen, um das Versteck zu finden.]],
				['6'] = [[Ich habe beschlossen, kein Risiko einzugehen und die Stolperfalle zu umgehen. Jetzt kann ich mich nach dem Versteck umsehen.]],
				['5'] = [[Nach kurzer Zeit habe ich eine Kiste in einem der Ausläufer der Kanalisation gefunden. Ich muss mir die Sachen nur noch schnappen und mich aus dem Staub machen.]],
				['4'] = [[Es war ziemlich einfach, die Stolperfalle zu entschärfen. Jetzt kann ich das Versteck suchen.]],
				['3'] = [[Nachdem ich nach unten geklettert bin, habe ich mich umgesehen und bemerkt, dass die Räuber eine Falle gelegt haben: ein Stolperdraht, der mit einer Granate verbunden ist.]],
				['2'] = [[Es war nicht leicht, aber ich habe es schließlich geschafft, den Gullydeckel zu öffnen. Zum Glück war die Leiter in die Kanalisation noch intakt, aber es ist ziemlich dunkel dort unten. Ich brauche eine Taschenlampe oder Fackel, um das Versteck zu finden.]],
				['1'] = [[Der Eingang zum Unterschlupf der Räuber hat sich als gewöhnlicher Gullydeckel herausgestellt, der in die Kanalisation der Stadt führt. Ich brauche ein Brecheisen, um ihn zu öffnen.]],
				['8'] = [[Ich ging ein paar Schritte weiter, bis ich mit dem Bein an irgendetwas hängenblieb. Ein paar Sekunden später wurde eine Explosion ausgelöst. Es war eine Falle! Trümmerteile haben sich an mehreren Stellen in meinen Körper gebohrt. Die Schmerzen waren fürchterlich.]]
			}
		},
		['new_year_boss_2'] = {
			['choice'] = {
				['3'] = [[„Bist du verrückt?“]],
				['2'] = [[„Müssen wir ein Loch graben?“]],
				['1'] = [[„Werden wir hier jemanden verprügeln?“]],
				['7'] = [[„Äh, sollen wir schlafen gehen?“]],
				['6'] = [[Das Loch mit Gras verdecken]],
				['5'] = [[„Das bedeutet …“]],
				['4'] = [[Ein Loch graben]],
				['9'] = [[„Sollen wir einfach warten?“]],
				['8'] = [[„Für einen Überfall hinlegen?“]],
				['17'] = [[Tot spielen]],
				['16'] = [[„Äh, okay …“ (Neben das Loch legen)]],
				['15'] = [[„Warum sollten die hier sein?“]],
				['14'] = [[„Okay, machen wir uns an die Arbeit.“]],
				['18'] = [[Ihm folgen]],
				['12'] = [[„Unsere Lochgrabmission?“]],
				['13'] = [[„Wie bitte?“]],
				['10'] = [[„Ein Herzinfarkt? Hä?“]],
				['11'] = [[„Du … kannst sprechen?“]]
			},
			['episode'] = {
				['3'] = [[„Iiih“<new_line>Der Höhlennachtsmann hat begonnen, fröhlich zu nicken. Offenbar muss er genau hier ein Loch graben.]],
				['2'] = [[Er schüttelt den Kopf und versucht, die oberste Erdschicht mit dem Griff seiner Keule wegzukratzen.]],
				['1'] = [[„Oh ooh! Ah buh.“<new_line>Höhlennachtsmann schlägt mit seiner Keule auf den Boden. Anscheinend will er uns etwas mitteilen …]],
				['7'] = [[Jetzt liegt der Höhlennachtsmann mit ausgebreiteten Armen und Beinen neben dem Loch.]],
				['6'] = [[„Ach, jetzt verstehe ich! Er will, dass wir das Loch mit etwas abdecken. Um es zu verbergen!“<new_line>Höhlennachtsmann lächelte und nickte.]],
				['5'] = [[Der Höhlennachtsmann untersuchte sorgfältig das Loch, das ich gegraben hatte.<new_line>„Uh wah, uhh wah!“<new_line><new_line>Während er uns ansah, riss er ein paar lange Grashalme ab und legte sie über das Loch.]],
				['4'] = [[„Du hast es verstanden, oder? Er will ein Loch graben, um eine Falle zu bauen! Die Natur ist eine erstaunliche Sache, Partner.“]],
				['9'] = [[„Uuh äh …“<new_line>Der Höhlennachtsmann steht auf, schwankt und fällt dann abrupt auf den Rücken.]],
				['8'] = [[Ohne aufzustehen, schüttelt der Höhlennachtsmann den Kopf<new_line><new_line>„Nein, nein. Es ist etwas Anderes … Versuch, so zu denken, als wärst du ein bisschen, ähm … Ich meine, versuch es noch einmal.“]],
				['17'] = [[„Niemand wird den Köder schlucken, wenn du nur daliegst! Um ein verwundetes Tier zu spielen, musst du ein verwundetes Tier werden.“]],
				['16'] = [[„Vertrau mir einfach. Es bleibt keine Zeit, dir beizubringen, wie du die große fraktale Ähnlichkeit der Einheit des Seins intuitiv erkennen kannst.“]],
				['15'] = [[„Gut, wir brauchen einen Lockvogel. Leg dich neben das Loch, um die Entführer anzulocken.“]],
				['14'] = [[„Wenn du mit Geschäftsnachtsmann sprichst, hört er jetzt dieselbe Art von Sprache, die du vorher von mir gehört hast …“<new_line><new_line>Geschäftsnachtsmann schaute uns kurz an, winkte mit der Hand und ging wieder weg.]],
				['20'] = [[Während des Kampfes schlich sich eines der Bandenmitglieder heimlich davon und machte sich aus dem Staub!]],
				['19'] = [[Ich traue meinen Augen nicht! Kaum hatte ich mich neben das Loch gelegt, hörte ich ein Klappern und das Dröhnen von Motoren.]],
				['12'] = [[„In der Tat. Ein Individuum kann sich nicht so viel Wissen aneignen, dass es seinen eigenen mentalen Rahmen überschreitet, wenn es sich in einer fremden Umgebung wiederfindet.<new_line>Aber zurück zu unserer Mission. Die Zeit drängt.“]],
				['13'] = [[„Uhhh, ahh, ho ho ho! Ah ih uga, ih.“]],
				['10'] = [[Der Höhlennachtsmann stand langsam auf, hob seine Keule und … schlug mir auf den Kopf!]],
				['11'] = [[Der Höhlennachtsmann hält mir eine Art Lappen an den Kopf.<new_line>„Tut mir leid, mein Freund. An einem bestimmten Punkt wurde klar, dass unsere Kommunikation in eine Sackgasse geraten war.“]]
			}
		},
		['halloween_fight_medium'] = {
			['episode'] = {
				['3'] = [[Ich habe den schnellsten der Gruppe ausgeschaltet, aber das sind noch lange nicht alle. Ich höre bereits neues Gebrüll. Die anderen Bestien nähern sich.]],
				['2'] = [[Ich machte die Horde mit lauten Rufen auf mich aufmerksam und begann zu rennen. Der Plan ging auf: Nur ein Teil der Bestien schaffte es, mir hinterherzulaufen. Ich muss sie so schnell wie möglich besiegen, bevor die anderen mich einholen.]],
				['1'] = [[Es sind zu viele Bestien hier versammelt. Ich kann nicht mit der ganzen Horde auf einmal fertig werden. Ich muss sie irgendwie aufteilen und sie in kleineren Gruppen besiegen. Teile und herrsche, wie man sagt.]],
				['4'] = [[„Ach, du schon wieder, Kleiner? Kannst du denn gar nichts alleine machen? Als ich in deinem Alter war, habe ich alles alleine gemacht. Also gut. Zeig mir, wer gemein zu dir ist.“]]
			},
			['choice'] = {
				['1'] = [[Schreien, um Aufmerksamkeit zu erregen]],
				['3'] = [[Den Zauberer beschwören]]
			}
		},
		['mirror_lady'] = {
			['episode'] = {
				['1'] = [[Das gesamte Haus war voller Spiegel. Genau wie der Tavernenwirt mich angewiesen hatte, entzündete ich eine Kerze vor einem der Spiegel und begann, Bloody Mary zu rufen. Zuerst kroch mir nur stinkendes Parfüm in die Nase, doch dann hörte ich Schritte und eine leise Stimme ... – So, so, wen haben wir denn hier?]],
				['2'] = [[Nach dem Kampf durchsuchte ich das Haus nach Spuren von Igor. Die Keramikpuppe auf der Kommode erschien mir irgendwie verdächtig. Ich hatte das Gefühl, dass sie mich anstarrte. Als ich einen Schritt näher trat, um einen genaueren Blick auf sie zu werfen, schlug sie mich ins Gesicht und trippelte flink auf ihren kleinen Füßen davon.]]
			}
		},
		['embacity_gastronome2'] = {
			['episode'] = {
				['3'] = [[Eine mit Wurzeln bedeckte Leiche lehnte an der Tür des Gebäudes. Ein Haufen Biomutanten scheint darauf zu warten, dass der Schimmel sie genug angreift. Ich kann nicht ohne speziellen Schutz in den Kampf ziehen.]],
				['2'] = [[Das Gebäude war noch nicht vollständig überwuchert, also gab es nicht viel zu essen für ein großes Rudel von Kreaturen. Ich muss überprüfen, ob meine Verteidigung in Ordnung ist, bevor ich reingehe.]],
				['1'] = [[Schimmel bedeckt das Gebäude, und hier und da wachsen rote Blumen. Sie scheinen eine Gruppe von Biomutanten angezogen zu haben. Ich darf meinen speziellen Schutz nicht vergessen.]]
			}
		},
		['secret_documents'] = {
			['episode'] = {
				['3'] = [[Der Weg ist frei, also kann ich das Labor jetzt durchsuchen.]],
				['2'] = [[„Also, was machen wir jetzt? Wir waren zuerst hier. Wenn du uns das Gleiche zahlst, überlassen wir dir diese Ruinen. Wenn nicht, können wir dir die Dinge auch auf eine viel fiesere Art und Weise erklären.“]],
				['1'] = [[Ich entdeckte eine Gruppe von Menschen am Eingang des Gebäudes. Ihr Anführer hörte mich an und schüttelte den Kopf.<new_line>„Du musst verstehen, dass wir nicht hierher gekommen sind, um spazieren zu gehen, sondern um seltene Informationen zu sammeln, die von wichtigen Leuten angeordnet wurden.“]],
				['7'] = [[Der Weg ist frei. Ich habe die Suche im Labor fortgesetzt und schließlich einen Tresor mit den EMBA-Markierungen gefunden.]],
				['6'] = [[Diese Leute waren nicht in der Stimmung zu reden. Ich rannte nach draußen, um ihren Anführer an unsere Abmachung zu erinnern, aber es war niemand mehr da.<new_line>Die Leute aus dem Labor sind mir bereits nach draußen gefolgt.]],
				['5'] = [[Diese Leute waren nicht in der Stimmung zu reden. Es ist nicht angenehm, in einem Labor eine Schießerei zu haben, also bin ich nach draußen gerannt.<new_line>Die Plünderer sind mir gefolgt.]],
				['4'] = [[Das Labor nahm nur einen sehr kleinen Teil des Gebäudes ein. Es wurde wahrscheinlich als Lagerraum für Archivdokumente genutzt.<new_line>Als ich eine andere Ecke des Gebäudes erkundete, hörte ich menschliche Stimmen.]],
				['11'] = [[Ich lockte die Insekten an die Oberfläche, aber sie wollten ihr Versteck nicht wirklich verlassen und krabbelten allmählich wieder hinein.]],
				['10'] = [[Das Labor nahm nur einen sehr kleinen Teil des Gebäudes ein. Es wurde wahrscheinlich als Lagerraum für Archivdokumente genutzt.<new_line>Bei der Erkundung einer anderen Ecke stieß ich auf eine Insektenhöhle.]],
				['9'] = [[Ich kam bei den Koordinaten des Gebäudes an, die mir die Archivare gegeben hatten. Es stellte sich heraus, dass die Tür versiegelt war. Es sieht so aus, als würde man hier Sprengstoff brauchen.]],
				['8'] = [[Ich habe die Geheimdokumente und muss sie zur Progress-Basis bringen und dem Biologen bei seiner Forschung helfen.]]
			},
			['choice'] = {
				['6'] = [[Locke die Insekten an die Oberfläche]],
				['5'] = [[Platziere den Sprengstoff]],
				['4'] = [[Kehre zur Progress-Basis zurück]],
				['3'] = [[Benutze ein Werkzeug zum Öffnen]],
				['2'] = [[Durchsuche das Labor]],
				['1'] = [[„Ich auch.“]]
			}
		},
		['blood_trail'] = {
			['choice'] = {
				['5'] = [[Ihm etwas sauberes Wasser hinterlassen und dich wieder auf den Weg machen.]],
				['4'] = [[Warten, bis er wieder bei Bewusstsein ist.]],
				['3'] = [[Medizin verwenden, um zu helfen.]],
				['2'] = [[Er ist kein Bürger mehr. Durchsuche ihn und nimm ihm seine Sachen ab.]],
				['1'] = [[Dem Pfad folgen]]
			},
			['episode'] = {
				['5'] = [[Nur eine Stunde später hat der Mann langsam das Bewusstsein wiedererlangt. Als ich ihm erklärt habe, was vorgefallen ist, hat er mir gedankt, indem er mir verriet, wo ich sein nah gelegenes Versteck finden kann.]],
				['4'] = [[Ich habe seine Wunden behandelt und verbunden, und die Blutpfütze so gut wie möglich aufgewischt. Jetzt hat er eine Chance zu überleben.]],
				['3'] = [[Im Inneren habe ich jemanden gefunden. Ein junger Mann lag in einer Pfütze seines eigenen Bluts. Anscheinend hat er versucht, seine Wunden zu verbinden, jedoch durch den starken Blutverlust das Bewusstsein verloren. Er braucht sofort Hilfe.]],
				['2'] = [[Genau wie ich befürchtet hatte, lauerten im Inneren einige Wölfe, die hungrig den Körper eines Fremden in Stücke rissen. Sie haben mich nicht bemerkt.]],
				['1'] = [[Auf der Türschwelle ist mir ein blutiger Fußabdruck aufgefallen, der ins Haus führt. Ich weiß nicht, was dort auf mich wartet.]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Als die Briganten ihre riesigen Eisblöcke auf Schlitten laden, kann ich mich näher heranpirschen. Durch das trübe Eis sehe ich die Umrisse von Leuten darin. Sie müssen so schnell wie möglich befreit werden.]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [[„Großartig, wir haben große Hoffnungen für dieses Vorhaben. Dein Beitrag wird uns helfen, wichtige Informationen über das EMBA-Projekt zu erhalten. Vielen Dank.“]]
			}
		},
		['embacity_biolab_6'] = {
			['episode'] = {
				['3'] = [[In der Halle sah ich einen Mann, der einen schweren Schutzanzug trug. Er ging um die Autoklaven in der Halle herum.<new_line>Als er mich bemerkte, bereitete sich der Mann auf einen Angriff vor.]],
				['2'] = [[„Das kann man so sagen. Nenne mich Gesandter. Wie ich sehe, hast du schon etwas in Erfahrung gebracht und schon viel geschafft. Aber du hättest dich nicht in meine Entwürfe einmischen sollen, ich habe so viel Zeit damit verbracht, an dem Brüter zu arbeiten. Und jetzt bist du an der Reihe zu sterben.“]],
				['1'] = [[„Ein neues Ärgernis … Was, dieser Idiot Gerin hat es nicht geschafft? Andererseits bin ich froh, wenn ihr euch selbst umbringt.“]]
			},
			['choice'] = {
				['1'] = [[„Ist es deine Stimme, von der Gerin gesprochen hat?“]]
			}
		},
		['emba_isaev_intro'] = {
			['episode'] = {
				['3'] = [[„Das hat mit der Forschung zu tun, die in Wissenschaftsstädten – typischen wissenschaftlichen Zentren – durchgeführt wurde. Hier in der Nähe gibt es eines. Diese Entwicklungen haben zu biologischen Bedrohungen geführt, und es sind neue Arten von Mutanten entstanden. Die Menschen geben uns, den Wissenschaftlern, die Schuld.“]],
				['2'] = [[„Wir können kein Labor auf offenem Feld einrichten, also wollen wir uns in dieser Siedlung niederlassen. Aber die Einwohner sind misstrauisch gegenüber Wissenschaftlern.“]],
				['1'] = [[„Ich glaube, Genosse, dass Sie unser potenzieller Agent sind. Ich bin Dr. Viktor Issajew. Freut mich, Ihre Bekanntschaft zu machen. Leider kann ich Ihnen nicht alle Details nennen, bis wir die Hauptprobleme gelöst haben.“]],
				['7'] = [[„Die Bedrohung ist beseitigt. Der Chef der Wache hat grünes Licht gegeben, dass das Labor in der Siedlung untergebracht werden kann. Wir haben etwas Zeit, und ich kann Ihre Fragen beantworten.“]],
				['6'] = [[„Ich habe den Ort auf der Karte markiert, der Expeditionsnavigator wird dort auf Sie warten. Er wird Sie auf den neuesten Stand bringen.“]],
				['5'] = [[„Wir bei Progress sind begeistert von Ihren Ambitionen und zukünftigen Beiträgen zur Expedition. Wir haben einen Plan, um das Problem zu lösen, und Sie können uns dabei helfen.“]],
				['4'] = [[„Nein, aber ich kann die einfachen Leute verstehen. Und jetzt sind wir hier, gerade um die Dinge in Ordnung zu bringen. Aber ohne Vorräte ist diese Mission zum Scheitern verurteilt.“]],
				['9'] = [[„Die erste Phase: Wiederherstellung der Biosphäre. Das Ziel unserer Expedition ist es, eine Terraforming-Technologie zu finden. Am Vorabend der globalen Katastrophe ist es Wissenschaftlern gelungen, sie zu entwickeln – das E.M.B.A.-Projekt.“]],
				['8'] = [[Der Doktor seufzte und nahm seine Brille ab, um sie zu putzen, als ob er sich auf ein langes Gespräch vorbereiten wollte:<new_line>„Wie Sie offensichtlich wissen, liegt die Welt in Trümmern. Progress hat eine Reihe von Maßnahmen vorbereitet, um die Krise zu überwinden.“]],
				['17'] = [[„Wir wissen nicht, woher sie kommen. Ich hoffe, Sie können uns helfen, das herauszufinden.“]],
				['16'] = [[„Korrekt. Dem Geheimdienst zufolge ist diese Wissenschaftsstadt völlig von Schimmelpilzen überwuchert und wird von feindlichen Lebensformen bewohnt. Sie sind vor kurzem auf einige von ihnen gestoßen.“]],
				['15'] = [[„Im Moment können wir keinen Kontakt zu ihnen aufnehmen, deshalb haben wir den Navigator von Vektor zur Hilfe geholt. Neben dem E.M.B.A.-Projekt interessiere ich mich auch für das Schicksal meiner Mitstreiter.“]],
				['14'] = [[„Vor nicht allzu langer Zeit schickte Progress eine Expedition in dieses Gebiet. Sie suchten nach einer anderen verlorenen Technologie. Aber sie haben zufällig eine dieser Wissenschaftsstädte entdeckt.“]],
				['19'] = [[„Zeigen Sie mal her. Hm, die werden uns bei unseren Forschungen sehr helfen. Bringen Sie sie sofort zum Biologen in der Siedlung und helfen Sie ihm bei seinen Forschungen.“]],
				['18'] = [[„Es handelt sich um eine Vereinigung von Fachleuten aus den modernsten wissenschaftlichen Bereichen. Wahrscheinlich ist sie die letzte ihrer Art, die es gibt … Deshalb werden wir manchmal ‚Archivare‘ genannt – wir versuchen, die Reste dessen, was überlebt hat, zu sammeln und zu bewahren.<new_line>Aber bald werden wir genügend Wissen und Ressourcen besitzen, um der Welt eine weitere Chance zu geben!“]],
				['12'] = [[„Er wird nicht leicht zu finden sein. Die Forschung wurde in Bio-Stationen auf dem Gebiet geschlossener Wissenschaftsstädte unter strengster Geheimhaltung durchgeführt. Daher kennen nicht einmal wir die Standorte aller Wissenschaftsstädte.“]],
				['13'] = [[„Nach der globalen Katastrophe ist das E.M.B.A.-Projekt in Verfall geraten, und unsere Expedition wird das wieder in Ordnung bringen.“]],
				['10'] = [[„Der gentechnisch veränderte Myzelstamm ist ein Schimmelpilz, der in der Lage ist, alle schädlichen Auswirkungen des Bodens zu absorbieren. Ein atomares Ödland könnte sich unter seinem Einfluss in ein fruchtbares Land verwandeln.“]],
				['11'] = [[„Nein, der Leiter des E.M.B.A.-Projekts war einer der besten Wissenschaftler unserer Zeit – Professor Mark Gerin. Dank seiner Arbeit war es möglich, den Durchbruch beim sogenannten Geo-Engineering zu schaffen.“]]
			},
			['choice'] = {
				['17'] = [[„Ja. Was sind das für Kreaturen?“]],
				['16'] = [[„Dann lohnt es sich, mit dieser Wissenschaftsstadt zu beginnen.“]],
				['15'] = [[„Und wo ist die erste Expedition?“]],
				['14'] = [[„Was ist eigentlich aus dem Projekt geworden?“]],
				['3'] = [[„Wie kann ich behilflich sein?“]],
				['10'] = [[„Erzählen Sie mir von eurem Plan.“]],
				['1'] = [[„Was ist passiert?“]],
				['11'] = [[„Was soll das bringen?“]],
				['7'] = [[„Ich habe im Bunker Dokumente gefunden.“]],
				['6'] = [[„Und was ist die Progress-Organisation?“]],
				['5'] = [[„Was wollt ihr jetzt machen?“]],
				['4'] = [[„Was ist das Hauptziel der Expedition?“]],
				['12'] = [[„Haben Sie das Projekt durchgeführt?“]],
				['13'] = [[„Also brauchen wir Gerin?“]],
				['9'] = [[„Haben sie recht?“]],
				['8'] = [[„Warum?“]]
			}
		},
		['emba_isaev_tver'] = {
			['episode'] = {
				['17'] = [[„Ja, das ist es! Eine experimentelle und methodische Gruppe von Bio-Stationen, die … Kampfexemplare züchten?! Das sind die Koordinaten des Ortes, an dem solche Experimente durchgeführt wurden.<new_line>Die erste Expedition ist in Gefahr, wir müssen dringend zu dieser Wissenschaftsstadt gehen! Unser nächster Standort wird Tula sein. Wir brechen auf, sobald Sie bereit sind.“]],
				['18'] = [[„Also gut, sind Sie bereit, die Expedition fortzusetzen?“]],
				['10'] = [[„Ich hoffe nicht. Theoretisch hätten Professor Gerin und andere Wissenschaftler des E.M.B.A.-Projekts während der globalen Katastrophe wie wir evakuiert werden müssen.<new_line>Aber wir haben den Kontakt zu ihnen verloren, wie auch zu allen anderen … und mit der Zeit wurden wir zu ‚Archivaren‘. Aber lassen Sie uns nicht über traurige Dinge reden.“]],
				['14'] = [[„Haben Sie schon ein AIDA-Kit zusammengestellt?“]],
				['3'] = [[„Gehen Sie in die nächstgelegene Wissenschaftsstadt, um nach der Blackbox zu suchen. Dort gibt es keine Strahlung, aber die Wahrscheinlichkeit, sich mit Myzelsporen zu infizieren, ist extrem hoch.<new_line>Bevor wir also weitergehen, besorgen Sie sich ein Sporenschutz-Kit – AIDA.“]],
				['2'] = [[„Na, super. Ihre Hauptaufgabe bei der bevorstehenden Expedition wird es sein, bei der Forschung zu helfen: Biomaterialien sammeln und nach Dokumenten und Blackboxen suchen.“]],
				['1'] = [[„Haben Sie die Dokumente schon dem Biologen gegeben und ihm bei seiner Forschung geholfen?“]],
				['11'] = [[„Die vollständige Entschlüsselung wird einige Zeit in Anspruch nehmen, aber wir haben bereits einen ersten Teil der Daten.<new_line>So, so, so ... Irgendetwas ist in allen Wissenschaftsstädten zur gleichen Zeit passiert. Die SOS-Signale der nächstgelegenen Bio-Stationen bestätigen das.“]],
				['7'] = [[„Beeilen Sie sich und bringen Sie die Blackbox zu dem Biologen, damit er seine Forschungen fortsetzen kann.“]],
				['6'] = [[„Nun, das ist großartig. Die Informationen über die E.M.B.A.-Projektforschung sind in einer Blackbox gespeichert. Sie befindet sich in der Bio-Station in der Mitte der Wissenschaftsstadt. Ihr Betreuer wird der Navigator sein, er wird bei der Suche helfen. Wenn Sie mich entschuldigen, ich muss jetzt wieder an die Arbeit gehen.“]],
				['5'] = [[„Haben Sie schon ein AIDA-Kit erhalten?“]],
				['4'] = [[„Unsere Mitarbeiter sind bereit, ein AIDA-Kit und andere Verbrauchsgüter gegen Disketten mit Daten einzutauschen.<new_line>Helfen Sie meinen Kollegen beim Studium des Myzels und speichern Sie die Daten auf Disketten. Wenn Sie genug gesammelt haben, kommen Sie in der Abteilung für Verbrauchsgüter vorbei und kaufen Sie sich einen AIDA-Spezialanzug.“]],
				['12'] = [[„Aggressives Wachstum des Myzels … Ausbreitung außerhalb der Bio-Station … unkontrollierte Sporenaktivierung … begleitet von einer Störung des zentralen Nervensystems. Ich denke, es war zu spät, um sie zu retten.<new_line>Aber wenn die erste Progress-Expedition weitergezogen ist, dann …“]],
				['13'] = [[„Der Biologe wird Ihnen dabei helfen. Aber Sie brauchen Biomasse, um den Anzug herzustellen.<new_line>Beim ersten Mal gebe ich sie Ihnen, aber von nun an müssen Sie sie mit einem Extraktor aus dem Myzel gewinnen. Sie können auch einen Extraktor vom Biologen bekommen.“]],
				['9'] = [[„Wir hatten keine Ahnung, dass das Myzel so etwas mit einer ganzen Stadt anstellen kann! Aber darüber brauchen Sie sich keine Sorgen zu machen, die Hauptsache ist, dass wir die Technologie wiederherstellen und dieser Welt eine Zukunft geben.“]],
				['8'] = [[„Willkommen zurück! Wie ich sehe, haben Sie Ihre Mission erfolgreich abgeschlossen, Genosse. Na, was haben Sie da?“]]
			},
			['choice'] = {
				['3'] = [[„Wo kann ich das bekommen?“]],
				['2'] = [[„Verstanden.“]],
				['1'] = [[„Noch nicht.“]],
				['7'] = [[„Ich bin noch nicht bereit.“]],
				['6'] = [[„Ich bin bereit.“]],
				['5'] = [[„Was habt ihr aus der Blackbox erfahren?“]],
				['4'] = [[Über Sichtungen berichten]],
				['11'] = [[„Haben sie etwas Neues entdeckt?“]],
				['10'] = [[„Was ist passiert?“]],
				['9'] = [[„Die Wissenschaftler in der Wissenschaftsstadt sind zu Mutanten geworden?“]],
				['8'] = [[„Ja.“]]
			}
		},
		['new_year_boss_4'] = {
			['choice'] = {
				['1'] = [[Zur Bühne gehen]],
				['2'] = [[„Wovon redet er?“]]
			},
			['episode'] = {
				['3'] = [[„Wir müssen ihn ausnüchtern. Jetzt werde ich … Genosse, bring ihm sauberes Wasser.“]],
				['2'] = [[Wir kamen gerade an, als das Lied zu Ende war, und Cybersanta fiel von der Bühne direkt in unsere Arme.<new_line>„Du bist gekommen … Samurai, wir haben eine Stadt zu verbrennen …“]],
				['1'] = [[Der Höhlennachtsmann führte uns den Weg entlang, aber an einem bestimmten Punkt gingen wir einfach in Richtung der donnernden Musik und fanden die Bühne.<new_line>Cybersanta springt auf ihr herum. Erstaunlicherweise ist unter der tobenden Menge auch jemand aus der Krampusbande.]]
			}
		},
		['new_year_boss_2_2'] = {
			['choice'] = {
				['7'] = [[„Ist der giftig?“]],
				['6'] = [[„Was hast du mit ihm gemacht?!“]],
				['5'] = [[Aus dem Rauch springen]],
				['4'] = [[„Lasst ihn in Ruhe!“]],
				['3'] = [[Den Alten fragen, was hier los ist]],
				['2'] = [[„Hey, Schluss damit!“]],
				['1'] = [[„Was ist …“]],
				['8'] = [[„Das ist alles das Werk des alten Mannes!“]]
			},
			['episode'] = {
				['3'] = [[„Hey, Alter! Hast du alle Geschenke eingesammelt? Gib sie hier ab!“<new_line>Der alte Mann mit der gruseligen Maske riss dem Mann eine Schachtel mit einer festlichen Schleife aus den Händen und warf sie ins Feuer.]],
				['2'] = [[Auf dem Dorfplatz werfen die Verbrecher den Weihnachtsschmuck auf einen Haufen neben einem Lagerfeuer. Ein paar Anwohner drängen sich an den Hauswänden. Angeführt werden sie von einem düsteren Mann mit ein paar Geschenken in den Händen.]],
				['1'] = [[Der Ausreißer führte mich in ein Dorf, das für die Feiertage geschmückt war. Vor meinen Augen wickelten Banditen Lichterketten von Masten ab, rissen Schmuck von Bäumen und verschwanden hinter Häusern.]],
				['7'] = [[Der Rauch verflüchtigte sich. Neben dem alten Mann, wo der Älteste gestanden hatte, stand nun eine Art Monster.<new_line>„Gauner, Krampusnachtsmanns Bande hat einen neuen Rekruten! Neuling, deine erste Aufgabe ist es, diesen Mistkerl zu beseitigen!“]],
				['6'] = [[Ein ganzer Haufen Weihnachtsschmuck flog in das Feuer, das einen Strom von beißendem Rauch ausstieß, der noch dicker als der erste war. Der alte Mann zog den armen Dorfältesten zu sich heran.]],
				['5'] = [[Plötzlich drehte sich der alte Mann zu mir um und fing an zu lachen:<new_line>„Ah, du willst mit dem Ältesten sprechen? Hey, Gauner, werft den ganzen Müll ins Feuer!“]],
				['4'] = [[Es war, als hätte mich niemand gehört. Das Geschenk landete im Feuer, blaue Flammen loderten auf, und blauer Rauch quoll hervor. Der alte Mann und seine Bande standen um das Feuer herum und atmeten den Rauch ein, wobei sich ihre Augen im Kopf zurückdrehten.]],
				['11'] = [[„Ach, du hast ihn nicht erkannt? Dieser alte Mann ist der Weihnachtsmann! Ich weiß nicht, wie er sich in so etwas verwandelt hat …<new_line>Ich kann mir diese Abscheulichkeit nicht einmal vorstellen. Vielleicht weiß der Geschäftsnachtsmann, was zu tun ist. Wir müssen ihn finden.“]],
				['10'] = [[„Darum geht es gar nicht. Die Art, wie sie ihn einatmen … während sie alles zerstören, was mit Weihnachten zu tun hat. Ist Ihre ganze Welt so korrumpiert?“]],
				['9'] = [[„Wie furchtbar! Wegen des schrecklichen Rauchs konnte ich Ihnen nicht zu Hilfe kommen.“]],
				['8'] = [[Als Antwort darauf brach der alte Mann erneut in Gelächter aus. Immer noch kichernd, rannte er zu seinem Motorrad und raste vom Platz.<new_line>Die Banditen, angeführt von dem entstellten Alten, kamen mir immer näher.]]
			}
		},
		['embacity_biolab_2'] = {
			['episode'] = {
				['1'] = [[Die verbliebenen Lampen erhellen die Kuppel nur schwach. In ihrer Mitte habe ich eine starke Tür gesehen. Sie ist versiegelt, und es gibt keine Möglichkeit, mit normalen Mitteln hindurchzukommen.]]
			}
		},
		['quest_cats'] = {
			['choice'] = {
				['1'] = [[Den Luchs angreifen]]
			},
			['episode'] = {
				['1'] = [[Ein paar Luchse haben sich an den Wurzeln eines alten Baums eingenistet. Von innen konnte ich ihre leisen Rufe hören.]]
			}
		},
		['sled_repair'] = {
			['choice'] = {
				['1'] = [[Schlitten untersuchen]]
			},
			['episode'] = {
				['1'] = [[Der Schlitten ist rasch gefunden, er steht verlassen an einer Landstraße und ist in fürchterlichem Zustand. Mal sehen, was ich tun kann.]]
			}
		},
		['emba_isaev_surgut'] = {
			['episode'] = {
				['10'] = [[„Sie verstehen das nicht! Diese Technologie ist instabil und gefährlich!“]],
				['3'] = [[„Leider wird in den Dokumenten nichts von einer Entwicklung erwähnt. Dies ist ein weiteres Datenverarbeitungslabor.<new_line>Aber hier ist das Erstaunliche. Wir bekommen Funksignale von dieser Bio-Station! Wir konnten sie noch nicht entschlüsseln, aber es könnte dort jemand sein, der noch bei Sinnen ist.“]],
				['2'] = [[„Wir sind bereits in Gesprächen mit den großen Fraktionen. Wir planen, die Technologie mit ihnen zu teilen, wenn sie uns helfen, ein Sicherheitssystem zu entwickeln.<new_line>Aber wenn wir das Sicherheitsproblem nicht lösen können, müssen wir das gesamte Programm abschalten und die Technologie loswerden. Sie sehen ja selbst, wie ernst die Lage ist. Wir können kein Risiko eingehen.“]],
				['1'] = [[„Wir arbeiten aktiv an Begrünungstechnologie. Und dank des Studiums von Gerins Arbeit sind wir ein gutes Stück vorangekommen!<new_line>Wir müssen noch ein System zum Schutz vor unbefugter Aktivierung entwickeln. Hier erhoffen wir uns Antworten darauf, wie wir dieses Problem lösen können.“]],
				['11'] = [[„Wie britische Wissenschaftler bewiesen haben, stinkt Geld nicht, Dok. Seien Sie nicht knauserig, ich will Sie nicht umbringen.“]],
				['7'] = [[„Wenn wir das Myzel nicht vollständig kontrollieren, können wir es nicht einmal teilweise nutzen. Wir können mit einem Fragment dieses Organismus arbeiten, aber früher oder später wird ein anderer Teil von ihm zu mutieren beginnen.<new_line>Und ein Gesandter wie dieser wird entstehen. Oder ein Brüter. Und es wird nicht mehr möglich sein, von irgendeiner Art von Kontrolle zu sprechen, nicht einmal über unseren Teil des Myzels.“]],
				['6'] = [[„Ja. Die Blackbox hat den Entstehungsprozess des Objekts, auf das Sie gestoßen sind, aufgezeichnet. Offenbar ist das gesamte Myzel ein einziger Organismus. Ein Telefonnetz, sozusagen.<new_line>Das Fehlen eines einzigen festen Wirts behindert es in keiner Weise. Aber für uns ist das ein kritisches Problem!“]],
				['5'] = [[„Was?! Es hat gesprochen?! Das kann nicht sein, das ist Unsinn … Wollen Sie damit sagen, dass es Gerin erwähnt hat?<new_line>Großer Gott, was ist das? Wir stellen die Produktentwicklung sofort ein, wir können diese Technologie nicht mehr weiterentwickeln!“]],
				['4'] = [[„Jetzt müssen wir nur noch herausfinden, was diese Wissenschaftsstadt verbirgt. Holen Sie bitte die Blackbox aus der Bio-Station.“<new_line>(hält ein Stück Papier mit dem Code hin)]],
				['12'] = [[„In Ordnung. Hier, nehmen Sie es und lassen Sie uns gehen.“]],
				['13'] = [[(flüstert) „Da sind Sie ja, mein Freund. Wir müssen sofort von hier verschwinden. Ich habe meine Kollegen in S.O.V.A. kontaktiert, wir fahren in ihre Stadt.<new_line>Wir brauchen wirklich Ihre Hilfe, bitte kommen Sie so schnell wie möglich dorthin.“]],
				['9'] = [[„Issajew, Sie sind ein kluger Mann, geben Sie mir den Koffer und machen Sie sich auf den Weg.“]],
				['8'] = [[„Wir werden den Abbruch vorbereiten, es wird noch ein paar Aufgaben für Sie geben. Kommen Sie in ein paar Stunden wieder.“]]
			},
			['choice'] = {
				['6'] = [[„Okay. Viel Glück, Doktor.“]],
				['5'] = [[Issajew nähern]],
				['4'] = [[„Was meinen Sie damit?“]],
				['3'] = [[„Warum? Habt ihr irgendwelche neuen Daten?“]],
				['1'] = [[„Wie werdet ihr die Technologie anwenden?“]]
			}
		},
		['emba_energy_easy2'] = {
			['episode'] = {
				['6'] = [[Mit großer Mühe kam ich aus dem Wasser und bekam wieder Luft. Ich habe genug von all diesen geheimnisvollen Bunkern und Forschungen.<new_line>Vor allem, weil ich die Kiste fallen gelassen habe und sie irgendwo unter Wasser auf dem Boden liegt.]],
				['5'] = [[Endlich habe ich es raus geschafft! Ich bin sicher, dass die Wissenschaftler, die ich kenne, einen Blick darauf werfen wollen. Ich werde es ihnen zeigen, wenn wir uns treffen.]],
				['4'] = [[Die Kiste ist schwer und es ist schwierig, mit ihr zu laufen, wenn mir das Wasser bis zum Hals steht. Und ich bin erst in der Mitte des Raumes.]],
				['3'] = [[Es stellte sich heraus, dass es eine halbtransparente Plastikkiste war. Darin kann man etwas erkennen, das wie diese seltsamen Pflanzen aussieht.<new_line>Hat sie wirklich schon jemand hier studiert?]],
				['2'] = [[Innen ist es leer und alles ist mit Wasser überflutet. Je weiter ich gehe, desto tiefer wird es. An einer Stelle müsste ich bis zum Hals untertauchen, um weiterzugehen.<new_line>Aber das ist der Raum, in dem etwas in den Regalen über dem Wasser steht!]],
				['1'] = [[Die Bunkertür ist kaputt. Wahrscheinlich war schon jemand hier.]]
			},
			['choice'] = {
				['3'] = [[Lass die Kiste fallen und verschwinde]],
				['2'] = [[Geh mit der Kiste zurück]],
				['1'] = [[Betritt das Wasser und gehe vorwärts]],
				['4'] = [[Geh weiter mit der Kiste]]
			}
		},
		['camp_supply_quest_pt2'] = {
			['episode'] = {
				['3'] = [[Der Anführer der Wächter klopfte mir auf die Schulter.<new_line>„Ich habe von den Männern gehört. Sie hatten Gutes über dich zu berichten. Weiter so. Hier ist deine Belohnung.“]],
				['2'] = [[Der Wächter schien besorgt zu sein.<new_line>„Ich habe nichts von den Männern gehört. Was ist dort passiert?“]],
				['1'] = [[Ich habe mich in der Basis mit dem Anführer der Wächter getroffen.<new_line>„Hallo, Bote.“]],
				['4'] = [[„Ach, ich dachte, es wäre etwas heruntergefallen ... Lass das Frachtgut hier und nimm deine Belohnung.“]]
			},
			['choice'] = {
				['3'] = [[Schweigendes nicken]],
				['2'] = [[„Ja, nun, hallo“]],
				['4'] = [[Den Angriff und die Opfer melden]]
			}
		},
		['camp_supply_quest'] = {
			['episode'] = {
				['14'] = [[Den Verbündeten gelingt es immer noch, ihr Lager zu verteidigen.]],
				['3'] = [[Ich habe die alten Gebäude entdeckt, in denen ich unsere Verbündeten treffen sollte. Plötzlich ertönte Gewehrfeuer. Sie wurden angegriffen!]],
				['2'] = [[Der Mann atmete erleichtert auf.<new_line>„Vielen Dank, du warst eine große Hilfe.“]],
				['1'] = [[Ich habe mich zum markierten Standort auf den Weg gemacht. Dort traf ich mich mit einem düsteren Typen.]],
				['10'] = [[Ich habe mich an den Räubern gerächt und das Lager verteidigt. Aber was war der Grund dafür?]],
				['7'] = [[In der Nähe des Lagers war es verdächtig ruhig.<new_line>Als ich näher kam, sah ich Räuber, die Wache hielten. Die Körper unserer gefallenen Verbündeten waren auf dem Boden verstreut.]],
				['6'] = [[Ich habe seine Wunde behandelt. Er wird es überstehen.]],
				['5'] = [[Der Mann nahm das Paket dankbar an.<new_line>Ich bemerkte einen verwundeten Mann in der Ecke.]],
				['4'] = [[Wir haben sie irgendwie vertrieben. Mein Verbündeter schaute mich überrascht an.<new_line>„Ich muss dir natürlich für deine Hilfe danken, aber ... wer bist du eigentlich?“]],
				['12'] = [[„Ich habe nichts“, sagte der Man mit einem traurigen Lächeln. „Gib mir das Frachtgut oder verschwinde!“]],
				['13'] = [[Der verletzte Mann liegt immer noch in der Ecke.]],
				['9'] = [[Im Lager gab es keine einzige lebende Seele mehr. Ich kam zu spät.]],
				['11'] = [[„Das Pack ... Schau! Alles wurde bereits übergeben.“]]
			},
			['choice'] = {
				['14'] = [[Hole Hilfe]],
				['1'] = [[„Hat jemand Vorräte bestellt?“]],
				['8'] = [[Rache suchen]],
				['5'] = [[Helfen]],
				['4'] = [[„Einige Freunden senden Ihnen Vorräte“]],
				['12'] = [[„Hier ist ein Paket für dich, aber du musst dafür bezahlen“]],
				['13'] = [[Das Paket übergeben]],
				['9'] = [[Warten, bis die Räuber verschwinden]],
				['11'] = [[Das Paket zurückbringen]]
			}
		},
		['mummy'] = {
			['choice'] = {
				['7'] = [[Die Truhe öffnen]],
				['6'] = [[Weiter beobachten]],
				['5'] = [[Nach rechts gehen]],
				['4'] = [[Nach links gehen]],
				['3'] = [[Geradeaus gehen]],
				['2'] = [[Den Korridor entlanggehen]],
				['1'] = [[Der Person folgen]],
				['8'] = [[Zurückgehen]]
			},
			['episode'] = {
				['3'] = [[Ich ging einige Minuten lang durch den Korridor und kam dann zu einer Weggabelung. Drei kleine Torbögen führten jeweils in eine identische Dunkelheit. Wie sollte ich weitergehen? Geradeaus, nach links oder nach rechts?]],
				['2'] = [[Die Gestalt warf die Ratte in einen Käfig und trug sie mit großem Zeremoniell in ein nahe gelegenes Haus. Ich trat einen Augenblick danach ein und erstarrte augenblicklich vor Überraschung. Auf der anderen Seite der Tür führte ein langer Korridor ins Ungewisse – aus Steinmauern, die von Fackeln beleuchtet wurden. Der unterirdische Gang war modrig und verstaubt, aber ich roch auch den angenehmen Duft von frischen Kräutern.]],
				['1'] = [[Als ich mich dem Ort näherte, lauschte ich aufmerksam. Zehn Meter vor mir stand jemand, in ein langes Gewand gekleidet, mit einer Schnabelmaske. Das verzweifelte Kreischen ließ mich erkennen, dass diese Person soeben eine Ratte gefangen hatte.]],
				['11'] = [[„Klasse! Bring sie her“, sagte die Truhe und riss ihr Truhenmaul mit den scharfen Zähnen weit auf. Ohne hineinzublicken warf ich den Rattenkadaver in die Truhe. Ihr Maul schlug zu und sie kaute laut. Dann spuckte sie mir einige Beutel mit Süßigkeiten in die Hände. „Und jetzt verzieh dich.“]],
				['7'] = [[Auf der gegenüberliegenden Seite des Torbogens führte eine Treppe zu einem kleinen Raum hinab, in dem Käfige mit Ratten aneinandergereiht standen. Dutzende der roten Augen starrten mich an. Nachdem ich erkannt hatte, dass ich hier nichts ausrichten konnte, wandte ich mich um und trat aus Versehen auf eine Steinplatte. Einen Augenblick später vernahm ich ein Klicken. Die Käfige hinter mir öffneten sich und die hungrigen Ratten quiekten wie verrückt.]],
				['6'] = [[Als ich mich ein wenig umsah, bemerkte mich die Mumie. Als sie den Kopf zu mir drehte, gab ihr Hals ein knirschendes Geräusch von sich, das mit durch Mark und Bein ging. Sie krächzte etwas, was sich wie ein altägyptischer Fluch anhörte.]],
				['5'] = [[Auf dem Steinsockel lag eine Mumie, in Mullbinden gehüllt. In einer Ecke stand der Arzt, den ich zuvor schon gesehen hatte, und murmelte etwas. Dabei rührte er ein zähflüssiges grünes Gebräu in einem rostigen Kessel um. Es duftete nach Brennnesseln und Spitzwegerich.]],
				['4'] = [[Der Bogen in der Mitte führte zu einem Labyrinth von Korridoren. Während ich sie durchschritt, orientierte ich mich an dem Duft der Kräuter. Meine Nase ließ mich nicht im Stich! Nach einigen Minuten kam ich zu einem großen Raum, in dessen Mitte ein Steinsockel war.]],
				['12'] = [[„Dann verzieh dich und komm bloß nicht wieder“, sagte die Truhe, ehe sie ihr Truhenmaul schloss.]],
				['9'] = [[Ich kam fast augenblicklich in einen kleinen Raum mit einer gewölbten Decke. In seiner Mitte stand eine einsame Truhe.]],
				['10'] = [[Als ich die Hand zu der Truhe ausstreckte, öffnete sich ihr Deckel einen Spaltbreit. Ich sah eine Reihe scharfer Zähne. „Hände weg“, zischte eine schroffe Stimme, die aus der Truhe kam. Schnell zog ich die Hand erschrocken zurück. „Das erstes Mal hier? Hast du eine Ratte dabei?“, fragte die Truhe.]],
				['8'] = [[Ich kehrte zu der Weggabelung zurück, um zu entscheiden, in welche Richtung ich als Nächstes gehen sollte.]]
			}
		},
		['new_year_craft_medium'] = {
			['choice'] = {
				['1'] = [[„Hände weg von der Keule!“]]
			},
			['episode'] = {
				['3'] = [[„Eh, seine Keule hat schon den Weg zu mir gefunden. Seltsame Zeiten, Genosse ... Aber der Höhlennachtsmann hat erwähnt, dass es nur ein Produkt gibt, das diesen Mann aufwärmen kann. Wirst du mir helfen?“]],
				['2'] = [[Dem Mann geht es besser, aber er zittert immer noch vor Kälte.<new_line>Höhlennachtsmann reibt seine Stöcke aneinander, aber es kommt nicht einmal ein Funke heraus.<new_line>„Ugh agh uuh?“]],
				['1'] = [[„Hallo, Genosse! Wir haben ein Opfer der dreckigen Tricks der Gauner gefunden.<new_line>Dieser arme Kerl ist fast erfroren. Wir haben nicht die Mittel, um ihn zu behandeln, kannst du uns helfen, welche zu finden?“]],
				['4'] = [[„Arghh, om-nom-nom!“<new_line>Der Höhlennachtsmann klopfte mir anerkennend auf die Schulter und begann, den Mann Löffel für Löffel mit der wärmenden Substanz zu füttern.<new_line><new_line>Vor unseren Augen begann es ihm besser zu gehen! Wer hätte gedacht, dass die Volksmedizin eine so gute Wirkung haben kann? Seht euch das an! Gute Arbeit!]]
			}
		},
		['snowmen'] = {
			['episode'] = {
				['1'] = [[Eine Bande Schneemänner belästigt einen armen Kerl und bewirft ihn mit Schneebällen. Zeit, ihrem Spaß ein Ende zu machen.]]
			}
		},
		['new_year_boss_6_2'] = {
			['choice'] = {
				['7'] = [[„Hurra!“]],
				['6'] = [[„Frohe Weihnachten!“]],
				['5'] = [[„Auf jeden Fall.“]],
				['4'] = [[„Aber nur ein Jahr lang!“]],
				['3'] = [[„Was meinst du damit?“]],
				['2'] = [[„Wir haben eine ganze Menge Weihnachtsmänner.“]],
				['1'] = [[„Wichtig ist, dass du zurückgekehrt bist.“]]
			},
			['episode'] = {
				['7'] = [[„Also gut, wir feiern – und dann zurück an die Arbeit! Ihr alle liebt es zu feiern, aber wie immer bin ich derjenige, der die Dinge erledigen muss. Wie auch immer, frohe Weihnachten!“]],
				['6'] = [[„Wir werden dir helfen, Partner. Ich kann nicht glauben, dass ich das sage, aber es reicht mit dem Geschäftlichen!<new_line>Wir verdienen unsere eigene kleine Party. Wir haben etwas zu feiern!“]],
				['5'] = [[„Du hast dich die ganze Zeit über gut um meine Aufgaben gekümmert. Auf jeden Fall besser als ich … Und Bär hat recht: Das Einzige, wofür ich jetzt gut bin, ist der Ruhestand.“]],
				['4'] = [[„Stimmt! Damit die Menschen Weihnachten feiern können, müssen wir eine Menge wieder aufbauen. Ich glaube nur nicht, dass der Weihnachtsmann im Moment in der Lage ist, das zu tun.“]],
				['3'] = [[„Alle haben immer Ausreden parat! Aber wer wird die Dinge in Ordnung bringen?! Kommt Weihnachten ganz von allein, ohne Weihnachtsmann?“]],
				['2'] = [[„So läuft das immer, Choomba.“]],
				['1'] = [[„Freunde, ich weiß nicht, was ich zu meiner Verteidigung sagen soll. Am Anfang wollte ich nur das Beste. Aber dann … Für das, was später geschah, habe ich keine Entschuldigung.“]],
				['8'] = [[„Ee eeee!“]]
			}
		},
		['pumpkin_king'] = {
			['choice'] = {
				['1'] = [[Weiter Kürbisse zermatschen]]
			},
			['episode'] = {
				['5'] = [[Ich zermatschte noch einige Kürbisse und schon bald erhellten zwei großen Flammen den Kreis. Diese Flammen brannten in den Augen des großen und majestätischen Kürbiskönigs und waren regelrecht hypnotisierend. Ich machte mich kampfbereit.]],
				['4'] = [[Nachdem ich die orange Flamme im letzten der Kürbisköpfe gelöscht hatte, wischte ich mir die Kürbisbrocken aus dem Gesicht. Die Kürbisbrut lag rings um mich herum – ihre Samen und ihr Saft bedeckten den Boden, tropften von meiner Waffe und meiner Kleidung.]],
				['3'] = [[Nachdem ich den Sieg davongetragen hatte, bildet ich mir ein, einen überraschten und wütenden Schrei zu vernehmen. Ich hatte es offenbar geschafft, Jacks Aufmerksamkeit zu erregen. Ich muss weitermachen.]],
				['2'] = [[Die Diener des Königs erlitten eine Niederlage, aber der König beobachtete mich noch immer höchstpersönlich. Ich fing wieder damit an, die Kürbisse zu zermatschen, und augenblicklich erschienen neue Schergen.]],
				['1'] = [[Ich habe mich genau an die Anweisungen gehalten – ich stand in der Mitte des Kreises und habe Kürbisse zermatscht. Einer nach dem anderen brach auf und ihre Innereien spritzten über den Boden. Nach einigen Sekunden hörte ich Geräusche und aus dem Augenwinkel sah ich, dass einige der Kürbislaternen um den Kreis herum in die Luft schwebten. Unter ihnen schälten sich Körper aus dem Nebel.]]
			}
		},
		['frankenstein2020'] = {
			['episode'] = {
				['1'] = [[So etwas hatte ich noch nie zuvor gesehen. Der Anblick des Monsters war schrecklich. Es stand vor mir und sah mich mit starrem Blick an. Sein Körper bestand aus menschlichen Teilen, die durch Elektrizität belebt wurden. Wir beide wussten, warum ich hier war. In diesem Moment schoss die Elektrode zu meiner rechten eine Ladung ab und das Monster kam auf mich zu.]],
				['2'] = [[Als das Monster fiel, entfuhr Igor ein Schrei. Er warf mir einen entgeisterten Blick zu, bevor er nach einem riesigen Folianten auf dem Schreibtisch griff und durchs Fenster floh.]]
			}
		},
		['emba_isaev_tula'] = {
			['episode'] = {
				['14'] = [[„Zuerst brauchen wir die geheimen Dokumente. Sie enthalten Informationen über die Forschung des E.M.B.A.-Projekts und den entschlüsselten Zugangscode zum Labor der örtlichen Wissenschaftsstadt.“]],
				['3'] = [[„Unsere Späher werden Ihnen helfen, die geheimen Dokumente zu finden. Gehen Sie in die Bar und folgen Sie ihren Anweisungen.<new_line>Haben Sie sie schon gefunden? Wenn ja, geben Sie die Dokumente dem Biologen und helfen Sie ihm bei seinen Forschungen.“]],
				['2'] = [[„Wir sind ständig auf der Suche nach Dokumenten, aber das ist eine langwierige und komplexe Aufgabe: Man weiß im Voraus nicht, welche Art von Nachforschungen oder Informationen zu den richtigen Archiven führen oder wo man sie suchen muss. <new_line>Daher bitte ich Sie jetzt, meinen Mitarbeitern bei ihren Aufträgen zu helfen. Die können Sie in der örtlichen Bar bekommen.“]],
				['1'] = [[„Da sind Sie ja, sehr gut. Bereit, mit der Arbeit zu beginnen?“]],
				['10'] = [[„Leider ist diese Blackbox sehr stark beschädigt, und wir müssen sie nun rekonstruieren. Aber es ist uns gelungen, Informationen über einige logistische Routen zu erlangen.<new_line>Nun müssen wir nach Kiew. Wir brechen auf, sobald Sie bereit sind.“]],
				['7'] = [[„Ein künstlich gezüchteter Biomutant direkt im Labor! Das ist verrückt.<new_line>Oh, und haben Sie die Leichen meiner Kollegen von der ersten Progress-Expedition dort gesehen?“]],
				['6'] = [[„Ja, diese Bio-Station hat automatische Gefechtstürme. Sie werden Ihnen im Kampf sehr nützlich sein, wenn Sie sie benutzen können. Der Navigator wird sich darum kümmern. Gehen Sie dorthin und beginnen Sie mit der Suche nach der Blackbox.“]],
				['5'] = [[„Eine Menge wissenschaftlicher Informationen. Das wird uns auf jeden Fall bei unserer Forschung helfen. Aus dem angewandten Teil konnten wir den Standort der Wissenschaftsstadt herausfinden und die Tatsache, dass es ein Kampfexemplar in der Bio-Station gibt.<new_line>Es wurde als ‚Objekt-Knochenbrecher‘ bezeichnet. Es gibt Hinweise darauf, dass es einen in das Nervensystem integrierten Myzelstamm hat.“]],
				['4'] = [[„Glückwunsch zum erfolgreichen Abschluss der Forschungsphase. Jetzt müssen wir uns auf die Suche nach der Blackbox in der Bio-Station der Wissenschaftsstadt machen.“]],
				['12'] = [[„Also gut, sind Sie bereit, die Expedition fortzusetzen?“]],
				['13'] = [[„Das hätte ich fast vergessen! Wir haben den Zugangscode zum Geheimfach des Labors entschlüsselt. Hier, ich habe ihn für Sie aufgeschrieben.“<new_line>(hält ein Stück Papier mit dem Code hin)]],
				['9'] = [[„Wir haben herausgefunden, dass die Einzigartigkeit des Myzels nicht nur auf seine Umwelteinflüsse zurückzuführen ist, sondern dass sich seine Gene erstaunlich leicht verändern lassen.<new_line>Sobald das Genom jedoch verändert ist, beginnen unkontrollierbare Mutationen, die sehr schwer vorherzusagen sind.“]],
				['8'] = [[„Dann besteht eine Chance, dass sie noch leben … Während der Rückverlegung werde ich versuchen, sie wieder zu kontaktieren. Jetzt kann ich Ihre Fragen beantworten.“]]
			},
			['choice'] = {
				['7'] = [[„Was habt ihr sonst noch herausgefunden?“]],
				['6'] = [[„Woher bekomme ich diese Dokumente?“]],
				['5'] = [[„Nein, es gab keine Leichen.“]],
				['4'] = [[Über Sichtungen berichten]],
				['3'] = [[„Gibt es Informationen darüber, wie man mit diesem ‚Objekt‘ umgeht?“]],
				['2'] = [[„Okay. Was habt ihr aus den Dokumenten erfahren?“]]
			}
		},
		['dracula2020'] = {
			['episode'] = {
				['1'] = [[Ich wurde Zeuge einer Schlacht der Titanen. Der Älteste und der Kürbisgott trafen im Kampf aufeinander und keiner von ihnen war bereit, nachzugeben. Ein Paar schwarzer Flügel lenkte mich allerdings von diesem unglaublichen Anblick ab. Graf Dracula höchstpersönlich war direkt vor mir gelandet. „Also du bist der wertlose Abschaum, der meine Pläne durchkreuzt hat. Mach dir keine Sorgen, ich werde mich ein für alle Mal um dich kümmern. Und danach werde ich mich dieses zu groß geratenen Kürbis annehmen“, drohte der Graf, sichtlich verärgert. Die Zeit für den finalen Showdown ist gekommen.]],
				['2'] = [[Dracula wurde in alle Winde zerstreut und die Riesen verschmolzen zu einer einzigen amorphen Masse von Tentakeln und Kürbisranken. Anscheinend hat der Älteste den Kampf verloren und beschlossen, sich zu ergeben. Ein riesiges Portal öffnete sich am Himmel und verschluckte beide, bevor es in sich selbst zusammenfiel. Das unglaubliche Schauspiel endete mit einem süßen, kleinen Kürbiskern, der mir in die Handfläche fiel. Ich denke, ich werde ihn dem Tavernenwirt geben.]]
			}
		},
		['halloween_boss_2022_3_2'] = {
			['choice'] = {
				['3'] = [[„Warum erzählst du mir das alles?“]],
				['2'] = [[Dem Mann folgen]],
				['1'] = [[„Das werden wir erst noch sehen!“]],
				['7'] = [[Auf dem Sofa Musik hören]],
				['6'] = [[Das Getränk probieren]],
				['5'] = [[Das Essen probieren]],
				['4'] = [[„Abgemacht.“]],
				['9'] = [[„Inwiefern?“]],
				['8'] = [[Zurück zu Graf Dracula gehen]]
			},
			['episode'] = {
				['3'] = [[„Du siehst, ich bin wirklich Graf Dracula, und ich habe mich in diesem wunderbaren Haus mit meinen schönen Bräuten niedergelassen. Und ich werde nicht leugnen, dass wir tatsächlich gelegentlich Reisende töten, die an unserem Haus vorbeikommen.“]],
				['2'] = [[Die Türen des Herrenhauses öffneten sich, und ein großer Mann kam auf uns zu:<new_line><new_line>„Halt, halt, halt! Verzeiht meiner Braut – sie ist jung und hat ein Gespür für Dramatik. Komm mit mir in meinen Garten, und ich erkläre dir alles.“]],
				['1'] = [[Wir traten durch das Tor eines luxuriösen Anwesens. „Hier wirst du dein Ende finden, meine Liebe. Du hast dir eine Audienz bei Graf Dracula verschafft. Zu deinem Pech auch noch als Snack.“]],
				['7'] = [[Der Kellner servierte mir ein Gericht und sagte:<new_line>„Perch au naturel—a true delicacy. Bon appétit!“]],
				['6'] = [[Der Garten ist mit Tischen voller Speisen und Getränke geschmückt. Auf einer Bühne spielt ein Streichquartett, umgeben von weichen Sofas.]],
				['5'] = [[„Wir veranstalten eine Soirée. Iss, trink, sei fröhlich. Und wenn du dich satt gesehen hast, kommst du zu mir und wir beenden ... nun, du verstehst, was ich meine.“]],
				['4'] = [[„Weißt du, wenn man seit Hunderten von Jahren lebt, werden die üblichen lärmenden Reden und das Säbelrasseln langsam langweilig. Warum benehmen wir uns nicht stattdessen wie Gentlemen?“]],
				['11'] = [[„Er war wirklich ein gütiger Mann, er tut mir sogar leid. Hm, was wollte ich noch sagen ... ? Ach ja, vergiss nicht, deine Dämonenstufe zu erhöhen! Sonst wirst du deinen nächsten Auftrag nicht bewältigen können.“]],
				['10'] = [[„Nun denn, mein lieber Gast, ich hoffe, du hast deinen Aufenthalt genossen. Also, fangen wir an ...“]],
				['9'] = [[Ich ließ mich auf dem Sofa nieder und döste fast ein, während ich dem Quartett zuhörte.]],
				['8'] = [[Der Kellner reichte mir ein Glas Wein:<new_line>„Genießen Sie es, es ist Falernian“]]
			}
		},
		['emba_time_medium'] = {
			['episode'] = {
				['7'] = [[„Schnitt! Das haben alle gut gemacht! Noch ein Dutzend solcher Szenen und wir werden etwas Großartiges haben. Ich habe auch jede Menge Ideen, um uns herum ist so viel los!<new_line>Das hast du wirklich gut gemacht. Ich werde dich zu den Dreharbeiten für die nächste Folge der Serie einladen. Du wirst Teil der Geschichte des Weltkinos werden, falls es die Welt noch gibt.“]],
				['6'] = [[„Du wirst mir dankbar sein, dass du dich auf diesen Film eingelassen hast! Sollen wir anfangen?“]],
				['5'] = [[„Schnitt! Jetzt kommt der wichtigste Teil. Die Kampfszene! Ich habe dir sogar einen Haufen Statisten besorgt, aber sei vorsichtig, mein Budget reicht nicht allzu weit.“]],
				['4'] = [[„Schnitt! Jetzt geh da rüber und filme die Straße. Siehst du, was sie da tun? Sie haben einen Kuhkadaver gefunden und fressen ihn. Komm, zeigen wir den Leuten die interessantesten Sachen. Action!“]],
				['3'] = [[„Hier, halte sie so und drücke hier. Schau, da drüben auf der Lichtung sind Biomutanten. Du musst filmen, was sie tun. Komm nur nicht zu nahe. Mach schon, ich brauche viel Material.“]],
				['2'] = [[„Nicht ‚irgendein Film‘, sondern ein großartiger. Schau dir die Zeiten an, in denen wir leben! Glaubst du, dass die Menschen nur Impfstoffe und Injektionsmittel brauchen? Du unterschätzt sie! Es ist wichtig, dass sie verstehen, was vor sich geht, und zwar nicht durch irgendeinen trockenen Artikel, sondern dank der Arbeit eines Künstlers. Ich meine mich selbst. Und du kannst mir dabei helfen.“]],
				['1'] = [[„Sei gegrüßt, Kamerad! Willst du bei der wissenschaftlichen Bildung der Nation mithelfen? Du wirst als Kameramann arbeiten müssen, während ich Panoramaszenen filme.“]]
			},
			['choice'] = {
				['5'] = [[„Alles klar, ich bin bereit.“]],
				['4'] = [[„Kämpfen?! Was für einen Film drehst du denn?“]],
				['3'] = [[Starte mit dem Filmen]],
				['2'] = [[„Okay, zeig mir, wo ich drücken muss.“]],
				['1'] = [[„Eine Art Film?“]]
			}
		},
		['intelligence'] = {
			['episode'] = {
				['5'] = [[Ich habe den Raum durchsucht und alle Blaupausen und Karten eingesammelt, die mit „EMBA“ gekennzeichnet sind. Ich muss mit ihnen zur Siedlung zurückkehren.]],
				['4'] = [[Doch so einfach ist es nicht, denn es gibt Biomutanten, die diesen Ort bewachen. Es sieht so aus, als hätte sich der letzte Befehl, den sie erhalten haben, in ihren Köpfen eingeprägt, so dass selbst ihre Mutation sie nicht dazu zwingen konnte, ihren Posten zu verlassen.]],
				['3'] = [[Ich bin an dem Standort. Wenn man den Karten glauben kann, die ich von Progress bekommen habe, sollte das Archiv mit den Dokumenten, die ich brauche, im nächsten Raum sein.]],
				['2'] = [[Ich könnte über den Übungsplatz gehen, da ich dort eine Menge Deckung für Schusswaffentraining installiert sehe. Eine andere Möglichkeit ist, durch das Lager für brennbare Materialien ins Innere zu gelangen. Ich werde mich durch beide Orte kämpfen müssen.]],
				['1'] = [[Die Lage ist nicht gut: Alle Menschen auf der Militärbasis haben sich in Biomutanten verwandelt. Es gibt aber auch gute Nachrichten — der Plan der Basis, den der Progress-Späher mir gezeigt hat, hat sich als richtig erwiesen. Es gibt zwei Möglichkeiten, in die Archivabteilung zu gelangen.]]
			},
			['choice'] = {
				['3'] = [[Öffne die Tür]],
				['2'] = [[Gehe durch das Lagerhaus]],
				['1'] = [[Gehe über den Übungsplatz]]
			}
		},
		['emba_time_hard'] = {
			['episode'] = {
				['3'] = [[„Du kannst Menschen und Filme nicht gut einschätzen. Sie werden vor seltsamen Erfindungen und unverständlichen Erklärungen davonlaufen.<new_line>In Zeiten, in denen alles zusammenbricht, hat ein Künstler die meiste Arbeit zu leisten. Ich muss die Kluft zwischen der Wissenschaft und den Menschen überbrücken.“]],
				['2'] = [[„Ich beschloss, mein Talent für die Entwicklung eines Produkts einzusetzen, das die Grenze zwischen Wissenschaft und hochkünstlerischem Kino überschreitet. Ich hatte eine Eingebung — das ist genau das, was die Menschen jetzt am meisten brauchen.“]],
				['1'] = [[„Kamerad, darf ich um eine Minute deiner Aufmerksamkeit bitten? Ich drehe gerade eine Serie über den Alltag der Progress-Wissenschaftler. Ich muss eine Menge Material sammeln, und mein fauler Kameramann ist krank. Ich brauche jemanden, der für ihn einspringt.“]],
				['7'] = [[„Das ist genug Ausrüstung für den schnauzbärtigen Kinotypen. Es ist besser, wenn ich dir erzähle, was mein Forschungsleiter herausgefunden hat! Im Grunde habe ich die Kappen für den Chromatographen verloren und wir haben uns auf den Weg gemacht, um …“<new_line><new_line>Ich machte mich bereit, eine weitere Geschichte über die Irrungen und Wirrungen eines Progress-Wissenschaftlers zu drehen.]],
				['6'] = [[„Hey, filmst du hier oder was?“]],
				['5'] = [[Ich habe genug Weitwinkelaufnahmen gemacht, also kann ich jetzt auch einige Objekte aus der Nähe filmen. Die Wissenschaftler haben mir erlaubt, die Zentrifuge und das biologische Vivarium in Aktion aus der Nähe zu filmen.]],
				['4'] = [[„Ich habe bereits eine Vereinbarung mit ein paar Wissenschaftlern getroffen. Ihr Geländewagen steht dort drüben. Drücke hier auf die Kamera und vergiss diesen Auslöser nicht. Sei nicht gierig mit dem Film, nur etwa ein Zehntel des Materials taugt sowieso höchstens etwas.“]],
				['11'] = [[„Vielen Dank! So, ich gehe jetzt ins Fotolabor. Komm später wieder, ich habe schon etwas zum Filmen für die nächste Serie gefunden.“]],
				['10'] = [[„Hast du die Chemikalien dabei? Ich muss jetzt die ganze Nacht im Fotolabor arbeiten. Ein paar von den Progress-Jungs könnten wenigstens mithelfen, ich gebe mir schließlich nur für sie Mühe.“]],
				['9'] = [[„Gut gemacht! Ich hätte gerne ein Filmteam, das jeden Tag eine solche Schicht macht. Es gibt jetzt ein endloses Meer von Material.<new_line>Warte, ich habe noch einen Job für dich. Mir geht das Entwicklungsmittel aus, aber ich habe gelernt, es selbst herzustellen.<new_line>Ich brauche aber noch ein paar Chemikalien. Kannst du sie für mich besorgen? Oder alle meine... Ich meine, deine Arbeit wird umsonst gewesen sein …“]],
				['8'] = [[Ich denke, ich habe genug Material gesammelt, um zehn Serien zu füllen und es ist an der Zeit, den Film dem Regisseur zu übergeben.]]
			},
			['choice'] = {
				['3'] = [[„Ich dachte, sie brauchen Wissenschaftler.“]],
				['2'] = [[„Ich kann helfen.“]],
				['1'] = [[„Was ist das für eine Serie?“]],
				['7'] = [[Drehe ein Interview]],
				['6'] = [[„Ja, was ist damit?“]],
				['5'] = [[Filme die Arbeitsmittel]],
				['4'] = [[Beginne, die Wissenschaftler zu filmen]],
				['9'] = [[Mach dich auf die Suche]],
				['8'] = [[Kehre zum Regisseur zurück]]
			}
		},
		['emba_search_medium'] = {
			['episode'] = {
				['7'] = [[Mehrere Biomutanten sind noch in der Höhle! Ich könnte sie angreifen und versuchen, den Trick zu wiederholen, sie mit Essen abzulenken und schnell mit den Beuteln wegzulaufen.]],
				['6'] = [[„Hurra, wir haben es geschafft! Danke, da fällt mir eine große Last von den Schultern. Gut, ich muss mich beeilen und recherchieren, bevor wieder etwas passiert. Pass auf dich auf!“]],
				['5'] = [[Es stellte sich heraus, dass auch in der Höhle niemand zu finden war. Die Situation entwickelt sich gut, ich sollte die Beutel nehmen und verschwinden.]],
				['4'] = [[Am Höhleneingang gibt es viele Spuren, aber ich kann die Biomutanten selbst nicht sehen, ich muss mich beeilen und die Beutel einsammeln.]],
				['3'] = [[„Ich habe von meinen Chefs ein paar Beutel mit Myzel für dich besorgt. Aber ich wurde durch ein anderes kleines Experiment abgelenkt und die Beutel wurden von Biomutanten gestohlen. Ich werde Ärger bekommen, wenn ich das nicht sofort in Ordnung bringe. Also, gehst du jetzt in die Höhle?“]],
				['2'] = [[„Ausgezeichnet. Komm mit mir, es ist ganz in der Nähe.<new_line>Sieh mal, hier werde ich das Futter platzieren und versuchen, sie weiter weg zu locken, während du zu dieser Höhle gehst, dort sollten die Beutel mit Myzel sein.“]],
				['1'] = [[„Hallo! Es ist etwas Unangenehmes passiert, kannst du mir helfen?<new_line>Die von den Biomutanten gestohlenen Beutel mit Myzel müssen zurückgegeben werden. Ich habe die Höhle gefunden, in die sie die Versuchsproben gebracht haben. Aber es sind zu viele Mutanten dort versammelt.<new_line>Ich schlage vor, sie weit weg von der Höhle zu locken. Du brauchst einen frischen Köder … hast du welchen?“]],
				['8'] = [[Das Problem ist gelöst, jetzt wird mich niemand mehr daran hindern, das Myzel zu nehmen.]]
			},
			['choice'] = {
				['6'] = [[Nutze Fisch als Ablenkung und nimm das Myzel]],
				['5'] = [[Nutze Fleisch als Ablenkung und nimm das Myzel]],
				['4'] = [[Sammle die Beutel und verschwinde]],
				['3'] = [[Betrete die Höhle]],
				['2'] = [[„Wie ist das überhaupt passiert?“]],
				['1'] = [[Gehe zur Höhle]]
			}
		},
		['emba_search_easy2'] = {
			['episode'] = {
				['3'] = [[Der Wissenschaftler sammelte die Pflanzen ein und kletterte mit ihnen in den Geländewagen. Nach einiger Zeit kehrte er zurück.<new_line><new_line>„Das ist vielversprechend, Kollege! Das Myzel begann schon in den ersten Sekunden nach dem Zusammenfügen aktiv mit dem fremden Gewebe zu interagieren.<new_line>Nun, ich werde meine Beobachtungen fortsetzen! Ich hoffe, wir arbeiten weiter zusammen!“]],
				['2'] = [[„Und, hast du das Herbarium gesammelt? Wir können es kaum erwarten, mit dem Experiment zu beginnen!“<new_line>]],
				['1'] = [[„Hallo, Kamerad! Willst du der Wissenschaft helfen? Wir untersuchen, wie das Myzel mit Pflanzen interagiert.<new_line>Kannst du uns Proben der typischen lokalen Flora liefern?“]]
			}
		},
		['emba_fight_easy2'] = {
			['episode'] = {
				['3'] = [[Die Gefahr ist gebannt. Ich habe hier einige Lebensmittel gefunden, die der Schimmel noch nicht erreicht hat.<new_line>Wenn die Bauern tatsächlich hierher zurückkehren, glaube ich nicht, dass sie etwas dagegen haben, wenn ich diese kleine Zahlung für ihre Sicherheit nehme.]],
				['2'] = [[Nirgendwo gibt es Menschen. Wenn sie überlebt haben und zurückkehren wollen, werden sie sofort in die Fänge der Biomutanten geraten.<new_line>Ich werde wahrscheinlich einige Leben retten, wenn ich die Biester jetzt vernichte.]],
				['1'] = [[Auch wenn die Farm nicht alt und ruiniert aussieht, ist sie doch völlig überwuchert von diesem neuen mutierten Grün. Und die Biomutanten sind genau hier, wandern umher und ernähren sich von dem wachsenden Schimmel.]],
				['4'] = [[Eine Luke öffnete sich im Boden neben dem Haus. Der Bauer kam darunter hervor und war wie erstarrt vor Angst.<new_line>Er schaute sich erschrocken um und kam auf mich zu. Er gab mir eine Tüte mit etwas, das wahrscheinlich Essen war, und schaffte es schließlich, so etwas wie ein Danke zu murmeln.]]
			}
		},
		['emba_fight_easy'] = {
			['episode'] = {
				['1'] = [[Ich sah eine Gruppe von Biomutanten, die sich um etwas scharten.]],
				['2'] = [[Die Biomutanten fressen den Schimmel, der auf den dunklen Wurzeln wächst, die aus dem Boden kommen. Diese gefährlichen Biester werden nun ihre Kräfte sammeln und alles angreifen, was in der Einöde lebt.]]
			},
			['choice'] = {
				['1'] = [[Nähere dich vorsichtig an]]
			}
		},
		['gift_delivery'] = {
			['choice'] = {
				['1'] = [[Geschenk platzieren]]
			},
			['episode'] = {
				['3'] = [[Zu meiner Überraschung finde ich einen kleinen Festbaum mit improvisiertem Schmuck. Jemand schläft hier in zahlreiche Decken gewickelt. Am besten lasse ich ein Geschenk unter dem Baum und gehe.]],
				['2'] = [[Ich erreiche den Ort und sehe nichts Ungewöhnliches. Gerade denke ich darüber nach, wo ich das Geschenk ablegen soll, als ich höre, wie hinter mir ein Gewehr durchgeladen wird und eine fremde raue Stimme sagt: „Karton auf den Boden legen und verschwinden. Nicht umdrehen.“]],
				['1'] = [[Es ist ein kleines Lager mit einem Unterstand aus schneebedeckten Zweigen. Das Feuer brennt, im Kessel brodelt Wasser. Der alte Mann ist wohl gerade erst gegangen. Wenn er wiederkommt und ein Geschenk findet, wird er wohl erschrecken.]]
			}
		},
		['santa_cyber'] = {
			['choice'] = {
				['3'] = [[„Und nun?“]],
				['2'] = [[„Wir brauchen Informationen über …“]],
				['1'] = [[„Hi, wir brauchen …“]]
			},
			['episode'] = {
				['5'] = [[„Was sollten wir denn sonst tun? Wir werden zum Konzert gehen und versuchen, noch einmal mit ihm zu sprechen. Genosse Geschäfti, ich glaube, du bleibst besser hier.“]],
				['4'] = [[„Uh aaah …“]],
				['3'] = [[„Ihr könnt mich mal! Ich werde hier abhauen. Wer mit mir auf der Bühne rocken will, wir sehen uns auf dem Konzert.“]],
				['2'] = [[„Pass auf, was du sagst, Cybersanta! Und ihr, Freunde, passt auf eure Taschen auf, damit er nichts stiehlt.“]],
				['1'] = [[„Yo, Choombas, was gibt's? Hey, wer hat diesen Geschäftsverramscher zur Party eingeladen?“]]
			}
		},
		['new_year_boss_5_4'] = {
			['choice'] = {
				['3'] = [[„Mein Portal?“]],
				['2'] = [[„Schlägst du vor, dass wir aufgeben?“]],
				['1'] = [[„Dann greifen wir an!“]],
				['4'] = [[„Schaff ich das alleine?“]]
			},
			['episode'] = {
				['6'] = [[„Als ob es eine andere Möglichkeit gäbe. Na gut, Junge, Kopf hoch. Ich glaube, du hast eine kleine Chance. Schmücke den Baum ein letztes Mal, während ich alles vorbereite.“]],
				['5'] = [[„Ja, das, auf dem du die ganze Zeit gestanden hast, du Genie. Wir haben keine Energie für mehr als das.<new_line>Also sobald du in Frostis Höhle bist, kannst du dich nur noch auf dich selbst verlassen.“]],
				['4'] = [[„Oh, na gut. Da ich sowieso mit euch hier festsitze … kann ich auch einen dummen Plan vorschlagen.<new_line>Ich könnte dein Portal direkt in Frostis Höhle umleiten.“]],
				['3'] = [[„Was bleibt uns denn sonst übrig? Wir haben unsere Konten bereits überzogen!“]],
				['2'] = [[„Wir haben fast unsere gesamte Energie für dieses Vakuum verbraucht! Direkt in Frostis Falle zu tappen, ist nicht nur töricht, es ist Selbstmord.“]],
				['1'] = [[„Ich kann eine Spur der Magie spüren, die uns gestohlen wurde … Frosti hat sich irgendwo in der Nähe verschanzt und wartet darauf, dass wir zurückschlagen.“]]
			}
		},
		['mad_man_base'] = {
			['episode'] = {
				['1'] = [[Der Zaun des baufälligen Hauses war blutverschmiert. Sieht wie das Werk von Verrückten aus. Sie lieben es, Müll zu sammeln, deshalb werde ich drinnen bestimmt etwas Interessantes finden. Aber ich glaube nicht, dass sie sich freiwillig von ihrem Schatz trennen werden.]]
			}
		},
		['emba_energy_medium'] = {
			['episode'] = {
				['3'] = [[„Ausgezeichnet! Bist du bereit? Grisha, öffne den Käfig! Action! Gut, los, los, los!“]],
				['2'] = [[„Es ist eine Dokumentation mit vielen Folgen. Unsere Zuschauer müssen mit eigenen Augen sehen, was die Biomutanten sind und warum sie gefährlich sind.<new_line>Ich habe ein paar erwischt. Ich brauche einen starken Schauspieler, der vor ihnen weglaufen kann, während ich filme. Aber wo kann ich heutzutage noch welche finden? Und du siehst eigentlich genau richtig aus für diese Rolle.“]],
				['1'] = [[„Hallo! Schau dir mal an, was hier los ist! Ich schaffe es gerade noch, es für die Ewigkeit auf Zelluloid zu bringen! Ich muss jetzt eine Verfolgungsszene filmen. Willst du mitmachen?“]],
				['11'] = [[„Oh je, du hättest dich im Namen der Kunst wirklich mehr anstrengen können. Ich muss jetzt ein paar neue Biomutanten fangen. Wie auch immer, trink das und reiß dich zusammen, ich habe noch etwas zu tun.“]],
				['7'] = [[Ich habe sie fast erreicht. Nur noch zehn Meter... Dann hörte ich den Knall von Schusswaffen und die Biomutanten fielen alle tot zu Boden.<new_line><new_line>„Du hättest das Filmteam nicht in Gefahr bringen dürfen, auch wenn deine Rückkehr wirklich beeindruckend aussah. Ich werde dich zu einer Vorführung einladen, sobald der Film fertig ist.“]],
				['6'] = [[Ich habe meinen Kurs geändert und renne jetzt auf die Filmcrew zu. Die Biomutanten, die mich verfolgen, werden bald zu ihrem Problem.]],
				['5'] = [[Ich habe mich von meinen Verfolgern gelöst, aber ich spüre, dass ich nicht mehr viel Kraft habe. Ich kann jetzt umkehren und zum Filmteam zurückkehren. Oder ich könnte versuchen, auf einen Baum zu klettern und mir dort oben etwas einfallen zu lassen.]],
				['4'] = [[Ich hatte schon angefangen zu rennen, als ich mich umdrehte und einen ganzen Schwarm wilder Biomutanten auf mich zustürmen sah. Ich muss jetzt Vollgas geben.]],
				['12'] = [[„Oh je, du hättest dich im Namen der Kunst wirklich mehr anstrengen können. Ich muss jetzt ein paar neue Biomutanten fangen.<new_line>Wie auch immer, trink das und reiß dich zusammen, ich habe noch etwas zu tun.“]],
				['10'] = [[Ich stolperte und fiel auf den Boden. Ich hörte das Knallen von Schüssen und das Heulen der Biomutanten.<new_line>Ich konnte den Direktor in sein Megaphon fluchen hören.]],
				['9'] = [[Mein Trick funktionierte und ich kehrte unbemerkt zum Direktor zurück.<new_line><new_line>„Kein schlechter Zug, wenn man bedenkt, dass du kein Profi bist. Es sollte auf dem Film gut aussehen. Ich werde dich zur Premiere einladen, sobald der Film fertig ist.“]],
				['8'] = [[Ich habe den Baum erreicht und bin hinaufgeklettert. Es sieht so aus, als hätten die Biomutanten mich aus den Augen verloren, aber sie irren in der Nähe herum.<new_line>Ich kann runterspringen und zum Filmteam laufen oder die Biomutanten mit etwas Essbarem ablenken und mich unbemerkt davonschleichen.]]
			},
			['choice'] = {
				['3'] = [[Sieh dich um]],
				['2'] = [[„Na gut, ich mache mit.“]],
				['1'] = [[„Was ist das für eine Verfolgungsszene, die du da drehst?“]],
				['7'] = [[Renne zum Baum]],
				['6'] = [[Kehre um]],
				['5'] = [[Falle hin]],
				['4'] = [[Lauf weg]],
				['11'] = [[Versuche aufzustehen]],
				['10'] = [[Lass das Essen fallen und verschwinde unbemerkt]],
				['9'] = [[Springe runter und renne weiter]],
				['8'] = [[Renne zur Filmcrew]]
			}
		},
		['search_killer1'] = {
			['episode'] = {
				['1'] = [[Ich habe eine Leiche in der Nähe des Portals gefunden und muss herausfinden, wohin der Mörder geflohen ist.]]
			}
		},
		['emba_energy_hard'] = {
			['episode'] = {
				['3'] = [[„Hat der Bauer dich geschickt? Ich habe es ihm schon gesagt — keine Lebensmittel, keine Waren. Du kannst selbst etwas Essen mitbringen, um mit mir zu handeln, wenn der Dreckskerl zu faul ist.“]],
				['2'] = [[„Weißt du, ich bin selbst eine Art Wissenschaftler. Aber ich brauche Materialien zum Studieren. Es gibt einen alten Mann, mit dem ich einen Deal gemacht habe. Du musst ihn nur besuchen und sie einsammeln.“]],
				['1'] = [[„Hey, ich erinnere mich an dich. Willst du ein Experiment machen? Ich glaube, wir haben schon ein paar Experimente zusammen gemacht.“]],
				['11'] = [[Es ist, als wäre meine Erinnerung an das, was dann geschah, völlig ausgelöscht worden. Ich wachte auf dem Hof auf, wo alles auf dem Kopf stand und ein verbogenes Messer im Zaun steckte.<new_line>Die Hütte des Bauern hatte Vorhänge an allen Fenstern und die Türen waren verschlossen. Es scheint, dass das Experiment fehlgeschlagen ist.<new_line>Aber ich fühle mich nicht so schlecht wie erwartet — vielleicht ist ja doch noch was dran an dem Myzelgebräu.]],
				['7'] = [[„Der alte Mann hat wirklich eine gute Mischung. Reine Ry … Re … agenten. Schreib deine Beobachtungen in dieses Tagebuch, wir bringen es später zu diesen g-gelben Jungs. Lass uns noch einen trinken!“]],
				['6'] = [[„Die Mischung des alten Mannes hat viele unerforschte Eigenschaften. Nun denn, die Wissenschaft ruft. Sollen wir das Experiment starten?“]],
				['5'] = [[„Das ist ein Myzelgebräu, Bruder. Die Wissenschaftler scheren sich einen Dreck darum, aber dieses Ding hat eine echte physikalische Wirkung! Es ist wirklich etwas Besonderes!“]],
				['4'] = [[Der alte Mann gab mir ein Bündel, das ich zu dem Bauern zurückbrachte.<new_line><new_line>Freudig entfaltete er das Bündel, nahm eine Flasche mit grüner Flüssigkeit heraus und begann, etwas in sein Tagebuch zu notieren:<new_line>„Na dann. Wir machen das Experiment Nummer … äh … Ich weiß nicht mehr, welche. Materialien … ein Liter der zu untersuchenden Substanz. Gut, bist du bereit?“]],
				['12'] = [[Was dann geschah, ist ein komplettes Gedächtnisloch. Ich wachte auf dem Hof auf, wo alles auf dem Kopf stand und ein verbogenes Messer im Zaun steckte.<new_line>Das Haus des Bauern hatte Vorhänge an allen Fenstern und die Türen waren verschlossen. Es scheint, dass das Experiment fehlgeschlagen ist.<new_line>Ich fühle mich grässlich. Wahrscheinlich habe ich mich bei der Dosis verrechnet — mit diesem Myzelgebräu ist nicht zu spaßen.]],
				['10'] = [[Nachdem er den letzten Schnaps getrunken hatte, stand der Bauer mühsam auf, machte zwei Schritte zur Tür und fiel auf den Boden, wo er sofort zu schnarchen begann. Ich nahm seine Unterlagen vom Tisch und bewegte mich wackelig in Richtung Ausgang. Ich bringe sie zu den Wissenschaftlern, damit sie seine Forschungen auswerten können.]],
				['9'] = [[„Siehst d-du, wenn du die richtige Pop … Prop … portion nimmst, kann ich z-zehn von diesen Meh … Muh … Mutanten schnippeln. Los, raus in den Garten, dann z-zeig ich‘s dir! Aber wir brauche n-noch …“]],
				['8'] = [[„Schau dir an, was dieses My-z … el macht. Und wir sind ernsthafte Leute. L-L-Lass uns das Ex … periment zu Ende bringen. Also gut, für die Wissenschaft!“]]
			},
			['choice'] = {
				['5'] = [[„Du bist echt ein Wissenschaftler …“]],
				['4'] = [[„Was ist das?“]],
				['3'] = [[„Gieß es rein.“]],
				['2'] = [[Gehe zu dem alten Mann]],
				['1'] = [[„Bist du in die Wissenschaft gegangen?“]]
			}
		},
		['embacity_biolab_reservoir_2'] = {
			['episode'] = {
				['3'] = [[Ein riesiger Tank nimmt einen großen Teil der Bio-Station ein. Er ist zur Hälfte mit grüner Flüssigkeit gefüllt.<new_line>Daran ist eine Pumpstation angeschlossen – ähnliche Maschinen stehen an den kleineren Zisternen am Stadtrand.]],
				['2'] = [[Ein riesiger Tank nimmt einen großen Teil der Bio-Station ein. Er ist jetzt leer.<new_line>Daran ist eine Pumpstation angeschlossen – ähnliche Maschinen stehen an den kleineren Zisternen am Stadtrand.]],
				['1'] = [[Ein riesiger Tank nimmt einen großen Teil der Bio-Station ein. Er ist bis zum Rand mit grüner Flüssigkeit gefüllt.<new_line>Daran ist eine Pumpstation angeschlossen – ähnliche Maschinen befinden sich an den kleineren Zisternen am Stadtrand.]]
			}
		},
		['wood_delivery'] = {
			['choice'] = {
				['1'] = [[Feuerholz platzieren]]
			},
			['episode'] = {
				['1'] = [[Ein paar Leute drängen sich um eine rostige Tonne, in der ein Feuer brennt. Sieht aus, als hätten sie bald kein Holz mehr. Ich könnte ihnen helfen.]],
				['2'] = [[Die Leute freuen sich sehr über das Holz. Sie machen mir einen Platz am Feuer frei und spendieren mir sogar was zu trinken.]]
