		['embacity_exogort'] = {
			['episode'] = {
				['3'] = [[Ich konnte ein zufriedenes Grummeln aus der Grube hören. Ich schaute nach unten und sah, dass das zahnbewehrte Maul geschlossen war. Offenbar war es für eine Weile mit dem Fressen fertig.]],
				['2'] = [[Die Zähne an den Wänden der Grube bewegten sich, und ein Rumpeln kam aus dem Boden. Einen Moment später flog ein Gegenstand aus dem Loch!]],
				['1'] = [[Der Weg ist durch eine Grube versperrt, die an das zahnbewehrte Maul eines monströsen Raubtiers erinnert. Ich verspüre einen unwiderstehlichen Drang, etwas in diesen bodenlosen Abgrund zu werfen.]]
			},
			['choice'] = {
				['1'] = [[Einen Kadaver in die Grube werfen]]
			}
		},
		['bandit_camp_quest'] = {
			['episode'] = {
				['5'] = [[Der Anführer der Räuber hob seine Hände. Ein Friedenszeichen.<new_line>„Wir wollen nicht kämpfen. Gib uns ein paar Tage und wir verschwinden.“]],
				['4'] = [[Das war nicht allzu schwierig. Ich muss mir irgendetwas Praktisches schnappen und von hier verschwinden.]],
				['3'] = [[Ein trockener Zweig zerbrach unter meinen Füßen und einer der Räuber ging ein paar Schritte in meine Richtung. Er versuchte, mich zwischen den Bäumen zu entdecken.<new_line>„Hallo, wer ist da? Was willst du?“]],
				['2'] = [[Ein trockener Zweig zerbrach unter meinen Füßen und alle Räuber drehten sich um und starrten auf den Busch. Kein Zweifel - sie haben mich bemerkt.]],
				['1'] = [[Ich habe in den Wäldern ein kleines Lager entdeckt. Diese Räuber trauen sich was, ein Lager so nahe an der Basis aufzuschlagen.]]
			},
			['choice'] = {
				['3'] = [[Das Lager durchsuchen und verschwinden]],
				['5'] = [[„Einverstanden, abgemacht“]],
				['1'] = [[Vorsichtig ans Lager heranschleichen]],
				['4'] = [[„Verschwindet von hier, solange ihr noch könnt“]]
			}
		},
		['emba_commander_easy2'] = {
			['episode'] = {
				['3'] = [[„Deine Arbeit ist so makellos, ich könnte ewig zusehen. Ich hoffe, meine Jungs haben alles gelernt. Danke, dass du dir die Zeit genommen hast!“]],
				['2'] = [[„Hier ist der Plan. Wir haben ein paar von diesen widerlichen Pilzen gefangen. Ich gebe dir die Ausrüstung, mit der wir sie jagen, und du zeigst uns, wie wir besser werden.<new_line>Ich habe die Jungs schon versammelt, sie sind bereit zuzuhören und zu lernen. Wenn etwas schiefgeht – keine Sorge, meine Männer haben die Mutanten im Visier. Bist du bereit?“]],
				['1'] = [[„Hallo, Wanderer! Ich habe schon ein paar Mal gesehen, wie du die Biomutanten zerlegt hast. Du solltest unseren Kämpfern zeigen, wie man mit diesen neuen Biestern umgeht.“]]
			},
			['choice'] = {
				['1'] = [[„Wie soll ich sie unterrichten?“]]
			}
		},
		['headless_horseman'] = {
			['episode'] = {
				['3'] = [[Die Straße vor dir ist ziemlich eben und gerade.]],
				['2'] = [[Da war er: In meinem Rückspiegel sah ich ein Motorrad, das in ein unheimliches grünes Glühen gehüllt war. Und vorne drauf saß ein Pferdeschädel. Nur einen Augenblick später flog sein leuchtender Helm neben mir. In einem spöttischen Ton mit einem metallischen Echo sprach er: „Mal sehen, was du kannst.“ – gleich darauf kehrte er zu seinem Körper zurück.]],
				['1'] = [[Schatten haben sich versammelt. Ich habe hinter mir das Geräusch eines Motors und das Echo eines wiehernden Pferds vernommen. Ich denke, dass mich der Reiter gefunden hat.]],
				['7'] = [[Als sich der Reiter herumdrehte, rammte ich ihn und drängte ihn von der Straße ab. Es überraschte mich, als ich den Geist in den Graben fliegen sah. Als ich an den Straßenrand kam, um nach ihm zu sehen, saß er ruckzuck wieder auf seinem Motorrad und brüllte: „Dafür wirst du mit deinem Leben bezahlen!“]],
				['6'] = [[Vor uns lag eine Haarnadelkurve.]],
				['5'] = [[Ich wurde langsamer, bis ich neben dem Reiter war. „Machst du dir schon in die Hose?“, fragte eine geisterhafte Stimme mit einem Kichern.]],
				['4'] = [[Ich gab Gas und ließ den Reiter hinter mir.]],
				['8'] = [[Mir wurde schnell klar, dass man in einer scharfen Kurve nicht unbedingt Gas geben sollte. Aber da hatte ich schon die Kontrolle verloren und landete im Graben. Als der Reiter meinen Unfall sah, lachte er auf und verschwand in der Nacht. Ich muss es offensichtlich noch einmal versuchen.]],
				['9'] = [[Ich bremste und nahm die Kurve vorsichtig. Der Reiter holte mühelos auf und fuhr sogar neben mir.]],
				['10'] = [[Bald hatte mich der Reiter überholt. „Ich bin enttäuscht“, sagte er, ehe er in die Dunkelheit davonraste.]],
				['11'] = [[Ich gab Gas, um vor dem Reiter zu bleiben, aber er warf eine Bombe unter meine Räder und ich verlor die Kontrolle. Ich musste anhalten und von der Straße weg. „Genug gerast. Zeit zu sterben“, brüllte er, als er mit seinem Motorrad direkt auf mich zukam.]]
			},
			['choice'] = {
				['6'] = [[Zurück auf die Straße]],
				['5'] = [[Den Reiter rammen]],
				['4'] = [[Langsamer werden]],
				['3'] = [[Gas geben]],
				['2'] = [[Die Straße im Auge behalten]],
				['1'] = [[Zurücksehen]]
			}
		},
		['newyearEvent_def_easy'] = {
			['episode'] = {
				['7'] = [[Ein Portal materialisierte sich neben mir, und der Zauberer trat heraus.<new_line>„Du schon wieder? Du nutzloser Idiot. Kannst du nicht mal dieses kleine Ding alleine bedienen? Gut, dann lass mich dir schnell helfen.“]],
				['6'] = [[Ein Portal materialisierte sich neben mir, und Cybersanta trat daraus hervor.<new_line>„Hallo, Choom! Was haben wir denn hier? Wieder ein Fall von Cyberpsychose? Prügeln wir ihnen die Scheiße aus dem Leib!“]],
				['5'] = [[Ein Portal materialisierte sich neben mir, und Bär trat heraus.<new_line>„Die Menschen brauchen Hilfe. Komm schon, Genosse! Für die Solidarität des Proletariats!“]],
				['4'] = [[Ein Portal materialisierte sich neben mir, und Höhli trat heraus.<new_line>„Eh aaah!“, schrie er und stürzte sich mit diesen Worten in den Kampf. Ich dachte mir, ich sollte mit ihm mithalten.]],
				['3'] = [[Hinter mir hörte ich das Geräusch eines Motors. Es handelte sich um ein kleines, altmodisches rotes Auto. Es hielt an, und Geschäfti stieg aus.<new_line>„Ho ho ho! Du schon wieder, mein lieber Freund? Komm schon, die Leute brauchen Hilfe. Wir haben keine Zeit zu verlieren.“]],
				['2'] = [[Nachdem ich mich konzentriert hatte, dachte ich mit aller Kraft an ein Weihnachtswunder. Nichts geschah. Vielleicht liegt es daran, dass ich selbst das Weihnachtswunder bin, denn ich bin auch eine Art Weihnachtsmann. Ich muss an etwas Bestimmtes denken.]],
				['1'] = [[Während ich mich den Feierlichkeiten nähere, sehe ich eine dicke Rauchwolke. Die Gauner ruinieren das Fest. Ich muss die Dorfbewohner beschützen, aber wenn ich es allein tue, bringe ich sie in zu große Gefahr. Ich brauche Hilfe.]]
			},
			['choice'] = {
				['5'] = [[Cybersanta]],
				['4'] = [[Bärnachtsmann]],
				['3'] = [[Höhlennachtsmann]],
				['2'] = [[Geschäftsnachtsmann]],
				['1'] = [[Um Hilfe rufen]]
			}
		},
		['easter_buyer'] = {
			['episode'] = {
				['3'] = [[„Ich sehe dich gerade zum ersten Mal! Wenn du nichts zu verkaufen hast, geh bitte.“]],
				['2'] = [[„Ich weiß nicht, wovon du da redest. Willst du etwas verkaufen?”]],
				['1'] = [[„Sei mir gegrüßt. Was brauchst du?“]],
				['4'] = [[„Schon gut, schon gut, ich habe nur gescherzt, warum bist du gleich so aufgebracht? Hier ist deine Belohnung.“]]
			},
			['choice'] = {
				['3'] = [[„Wir hatten einen Deal ...“]],
				['2'] = [[„Wir hatten einen Deal, soll ich dich daran erinnern?“ (du legst deine Hand an deine Waffe)]],
				['1'] = [[„Ich bin wegen der Belohnung hier.“]]
			}
		},
		['new_year_boss_6'] = {
			['choice'] = {
				['3'] = [[„Auf keinen Fall.“]],
				['2'] = [[„Das gleiche Angebot, das du dem Weihnachtsmann gemacht hast?“]],
				['1'] = [[Hinter dem Stapel hervortreten]],
				['7'] = [[„Krampus, du warst mal der Weihnachtsmann – komm schon!“]],
				['6'] = [[„Du lügst und ich werde dich umbringen!“]],
				['5'] = [[„Du bist ein Monster!“]],
				['4'] = [[„Krampusnachtsmann hat sie genommen?“]],
				['10'] = [[„Du hast dich verwandelt in …“]],
				['9'] = [[„Du bist gekommen!“]],
				['8'] = [[„Ja!“]]
			},
			['episode'] = {
				['10'] = [[„Wie kannst du es wagen? Du nutzloser alter Mann! Die Magie der Weihnachtsmänner gehört mir! Ich werde über den nuklearen Winter herrschen, und du wirst sterben! Ihr werdet alle sterben!“]],
				['14'] = [[„Wir unterhalten uns später, Partner! Nach der großen Entladung von Magie bricht hier alles zusammen! Schnappen wir uns Krampus und verschwinden wir!“]],
				['3'] = [[„Hey! Wie bist du an meinem … Du bist wohl doch nicht so dumm, wie ich dachte. Ich habe ein Angebot für dich.“]],
				['2'] = [[In einem Keller stapeln sich Ornamente und alle möglichen Schrauben und Werkzeuge.<new_line><new_line>Von der anderen Seite des Stapels höre ich Frostis Stimme.<new_line>„Nein, ich habe jetzt das Sagen! Dank dir fangen wir in einem muffigen Keller wieder von vorne an!“]],
				['1'] = [[„Okay, bist du bereit, dich zu teleportieren, oder muss ich hier draußen in der Kälte auf dich warten?“]],
				['11'] = [[„Iii! Aagaaah!“]],
				['7'] = [[„Ein Kampf bringt keinem von uns etwas! Lass uns stattdessen unsere Kräfte bündeln.<new_line>Ich werde mir deine Vorschläge anhören, wie wir menschlicher handeln können. Ich habe selbst ein paar Ideen! Also, sind wir uns einig?!“]],
				['6'] = [[„Ja, das war das Werk dieses alten Idioten. Damals nannte er sich noch ‚Weihnachtsmann‘ und wollte die Kraft dieser Ornamente für allen möglichen Unsinn verschwenden: Strahlenschutz, alternative Energiequelle. Pfft.<new_line>Aber er hatte das Glück, rechtzeitig auf ein Genie wie mich zu treffen und die richtige Richtung einzuschlagen: hin zur Energie.“]],
				['5'] = [[„Hört zu! Alles, was ich brauche, sind Ornamente von eurem Baum. Das sind die Schlüsselteile der Superwaffe. Was glaubst du, warum waren keine Ornamente am Baum, als du ihn gefunden hast?“]],
				['4'] = [[Krampusnachtsmann erschauderte, und Frosti stieß ein widerliches Lachen aus.<new_line>„Ha, ich hatte recht: Du bist nicht dumm. Dieser Dreckskerl war nicht in der Lage, mit dir fertig zu werden, selbst wenn er alle Trümpfe in der Hand hatte.<new_line>Nun stell dir mal vor, was für eine Macht du und ich erreichen können, wenn wir uns zusammentun!“]],
				['12'] = [[„Unsere Magie wurde freigesetzt, und ich musste verhindern, dass du es vermasselst! Ähm, ich meine … Danke, dass du unsere Magie wiederhergestellt hast oder so.“]],
				['13'] = [[„Hä? Was ist hier los?“]],
				['9'] = [[„Ich habe fast das ganze Weihnachtsfest mit meinen eigenen Händen zerstört. Mein Gehirn ist vergiftet, und die Dinge sind jetzt nicht mehr zu reparieren … aber ich werde die Hoffnung nicht sterben lassen!“<new_line><new_line>Krampusnachtsmann winkt mit seinem Stab und schlägt auf das Teil, das Frosti aus der zerbrochenen Waffe gezogen hat.]],
				['8'] = [[„Hmm, der erste Eindruck ist eben doch der beste. Du BIST dumm. Krampus, hör auf, an der magischen Batterie herumzufummeln und kümmere dich um diesen Schwachkopf!“]]
			}
		},
