		['emba_def_easy2'] = {
			['episode'] = {
				['5'] = [[„Und wo ist unser Kämpfer, ist er verwundet? Oh, das ist eine schlechte Nachricht. Tut mir leid, aber ich muss mich um ihn kümmern, wir dürfen auf keinen Fall einen Spezialisten verlieren!“]],
				['4'] = [[„Gut gemacht, mein Kamerad! Es ist eine Schande, dass nicht jedes unserer Forschungsteams Kämpfer wie dich dabei hat.“]],
				['3'] = [[„Wirklich? Das ist großartig. Da sind jetzt ein paar unserer Kollegen. Sie sind Soldaten einer Spezialeinheit … Aber auch die könnten etwas Feuerunterstützung gebrauchen.<new_line>Sie sind bereits in das Gebäude eingedrungen, aber auf dem Parkplatz hinter dem Haus wird noch gekämpft. Von wo aus kommst du rein?“]],
				['2'] = [[„Es ist wahrscheinlich, dass es hier ein Feldlabor gibt, in dem jemand all diese neuen biologischen Aktivitäten untersucht hat. Lange vor uns, verstehst du?<new_line>Aber wir können noch nicht hineingehen. Dort gibt es jede Menge Biomutanten.“]],
				['1'] = [[Irgendetwas beunruhigt die Wissenschaftler. Als Antwort auf meine Fragen führten sie mich zu einem einstöckigen Gebäude, das von einem Stacheldrahtzaun umgeben war.]]
			},
			['choice'] = {
				['3'] = [[Parkplatz hinter dem Gebäude]],
				['2'] = [[Ins Gebäude]],
				['1'] = [[„Und was ist das?“]]
			}
		},
		['herb_collect'] = {
			['choice'] = {
				['1'] = [[geben Kräuter]]
			},
			['episode'] = {
				['1'] = [[Sneguroschka sitzt von Waldtieren umgeben auf der Lichtung. Zahlreiche Vögel sind bei ihr, auch kleine Säugetiere, sogar zwei Rehe. Jedes Tier braucht irgendeine Art von Hilfe. Sneguroschka deutet auf einen Platz neben sich, dort soll ich die Kräuter lassen.]]
			}
		},
		['new_year_boss_4_4'] = {
			['choice'] = {
				['1'] = [[„Schluss damit! Wie können wir sie stoppen?“]]
			},
			['episode'] = {
				['5'] = [[„Ich habe viel von ihm gelernt, als ich jünger war. Wenn wir ihn hierher holen können, könnten wir etwas tun!<new_line>Nun, bereit, dein Voodoo zu machen und den Baum zu schmücken?“]],
				['4'] = [[„Die Waffe funktioniert mit Magie. Wenn wir sie hacken könnten, wäre das kein Problem.<new_line>Aber was wir mit diesem Weihnachtszauber machen sollen, keine Ahnung … Obwohl … Ich kenne einen Zauberer. Ja, genau!“]],
				['3'] = [[„Ich würde dir deine gesamte Branche sonstwohin stecken …“]],
				['2'] = [[„Wie schrecklich! Alles, was wir gebaut haben, wird benutzt, um die Macht des Krampus zu vergrößern!“]],
				['1'] = [[„Sie haben eine Waffe entwickelt, die den Weihnachtszauber aufsaugt. Jetzt muss der Krampus keine Geschenke mehr in Lagerfeuern verbrennen.<new_line>Er richtet einfach diese Waffe auf sie, und schwupps – saugt er den ganzen Weihnachtszauber auf! Er kann das im industriellen Maßstab tun. In globalem Maßstab!“]]
			}
		},
		['embacity_biolab_5'] = {
			['episode'] = {
				['3'] = [[„Eine Armee? Nein, dieser militärische Unsinn wird in der Vergangenheit bleiben. Du scheinst einfach nicht in der Lage zu sein, die Zukunft zu begreifen. Das ist schon in Ordnung, sei jetzt einfach geduldig, und ich werde dich in Ordnung bringen. Versuch, dich nicht zu wehren, wenn du das schaffst.“]],
				['2'] = [[„Ja … Genau wie ich dachte. Ihr habt alle nicht einmal versucht, die neue Realität zu begreifen. Ihr habt die Stimme einfach nicht gehört. Nicht einmal Kurganow, und er war bereit, viel zu tun.“]],
				['1'] = [[„Na, na, na, was haben wir denn da? Ein weiteres Forschungsobjekt ist eingetroffen, fantastisch. Schließe dich meinen Kollegen an, ich bin sicher, du bist genauso unmöglich dumm und kannst das Offensichtliche nicht sehen.“]],
				['4'] = [[Ich ging durch den Raum und fand eine unauffällige Tür mit einem Codeschloss. Ich schätze, dafür war der Code gedacht.]]
			},
			['choice'] = {
				['1'] = [[„Was haben Sie getan?!“]],
				['2'] = [[„Stellen Sie auch eine Armee auf?“]]
			}
		},
		['dracula'] = {
			['episode'] = {
				['7'] = [[Als wir eintraten, stand der Graf vor dem Thron. Er sah völlig unbeeindruckt aus. Ohne ein weiteres Wort hob der Jäger seine Armbrust und schoss direkt auf den Kopf des Vampirs. Mit atemberaubender Schnelligkeit fing Dracula den Pfeil mühelos einen Zentimeter vor seinem Gesicht ab. „Wie rüpelhaft“, sagte er lächelnd. „Ich sollte euch Manieren beibringen.“ Vollkommen geräuschlos stürzte sich der Vampir auf uns.]],
				['6'] = [[In der Burg trafen wir auf erheblichen Widerstand. Wir schafften es trotzdem, unsere Feinde Stockwerk für Stockwerk zurückzuschlagen. Das letzte Hindernis war ein Rudel Monster, das vor dem Thronsaal Wache stand.]],
				['5'] = [[Das Schloss war baufällig und wirkte eher wie eine schwelende Ruine als wie ein legendärer Palast. Die Explosionen, welche die Tore und alle Barrikaden zerfetzten, wirkten sich daher nicht nachteilig auf sein Erscheinungsbild aus. Die Armee verschwendete keine Zeit und stürmte in den aufgebrochenen Eingang, um die Monster zu bekämpfen. Der Jäger und ich folgten ihnen dicht auf den Fersen.]],
				['4'] = [[„Der Plan ist ganz einfach: Wir beide kämpfen uns zu den oberen Stockwerken durch und finden den Blutsauger. Die anderen kümmern sich einstweilen um die restlichen Monster im Schloss. Mir nach – wir fangen gleich an“, sagte der Jäger und wandte sich dem gewaltigen Schemen der Burg zu.]],
				['3'] = [[„Meister Helsing! Wir haben die Bombardierung gestoppt, wie du es befohlen hast. Wir haben alle Vorbereitungen abgeschlossen und können die Burg stürmen“, berichtete der unbekannte Jäger. „Hervorragend! Sprengt die Tore und Barrikaden in die Luft. Wir dringen ein und halten uns an den Plan“, antwortete mein Freund, der einen überraschend hohem Rang bekleidete.]],
				['2'] = [[Als ich aus dem Portal trat, stand ich mich mitten in einem Armeelager. Menschen, Jäger und andere Kreaturen rasten im Zickzack zwischen den Zelten hin und her, die mit Fackeln beleuchtetet wurden. Der Jäger, dem ich gefolgt war, stand gleich in der Nähe. Eine weitere Person in Jagdkleidung war zu ihm geeilt und sie unterhielten sich.]],
				['1'] = [[Der Jäger nickte und ging zum Ausgang des Portals. Er flüsterte einige Worte und schleuderte irgendein Pulver in den Nebel, der sich anschließend blutrot färbte und schneller zu wabern begann. Der Jäger war zufrieden. Er bedeutete mir mit einer Handbewegung, ihm zu folgen. Dann trat er in den Nebel.]],
				['8'] = [[„Tja, unsere Jagd ist zu Ende – für dieses Jahr. Ich hoffe, dass wir uns beim nächsten Mal wiedersehen“, sagte der Jäger. Hinter ihm wurde der Körper des Grafen in einen silbernen Sarg gelegt. „Als Zeichen meiner Dankbarkeit möchte ich dir meine Armbrust schenken. Ich hoffe, du kannst sie gut gebrauchen.“ Der Jäger gab mir seine Waffe und sagte: „Ich werde dich zurückschicken, aber du solltest vorsichtig sein: Die Portale werden sich bald schließen und bis zum nächsten Jahr verriegelt bleiben. Viel Glück in der Darüberliegenden Welt.“]]
			},
			['choice'] = {
				['5'] = [[Die Armbrust nehmen]],
				['4'] = [[Nach oben vorstoßen]],
				['3'] = [[Wie lautet der Plan?]],
				['2'] = [[Zuhören]],
				['1'] = [[Ihm folgen]]
			}
		},
		['catch_thief_extra'] = {
			['episode'] = {
				['3'] = [[Ich erlaubte den Räubern, die noch standen, die Leichen ihrer Kameraden wegzuschleppen. Nach einer solchen Prügelattacke werden sie nicht mehr so schnell zum Lagerhaus oder gar zur Siedlung zurückkehren.]],
				['2'] = [[Ich erreichte das Lager der Räuber. Sie waren dabei, die Beute zu sortieren und bemerkten mich zunächst nicht:<new_line>„Was? Welches Lagerhaus? Es war deines, jetzt ist es unseres, also beweg dich weiter, alter Mann!“]],
				['1'] = [[Der Dieb hat die Beute in einer abgelegenen Schlucht versteckt. Ich habe alles in eine Kiste gelegt, die ebenfalls dort lag. Ich muss sie abholen und zum Lagerhaus zurückkehren.]]
			},
			['choice'] = {
				['1'] = [[Lass die Kiste fallen und geh zurück, um die Belohnung zu holen.]],
