		['halloween_boss_2022_6'] = {
			['choice'] = {
				['3'] = [[Aufspringen und ihn ergreifen]],
				['2'] = [[Eine Granate nach ihm werfen]],
				['1'] = [[EinSeil über ihn werfen]],
				['4'] = [[Dem Esel folgen]]
			},
			['episode'] = {
				['5'] = [[Ich hörte ein Krachen und ein Brüllen aus einer kleinen Holzhütte, die ich vorher gar nicht bemerkt hatte. Die Sumpfbestie!<new_line>Aber dann erschien der Sensenmann neben der Hütte und drückte die Tür mit seiner Sense zu.<new_line>„Nun, Sterblicher, das ist das letzte Mal, dass ich dir helfe! Geh dem Esel nach, er rennt weg! Ich bleibe und halte die Tür auf.“]],
				['4'] = [[Der Esel hörte mich springen und wich zur Seite aus.<new_line>Er rannte los und schrie laut um Hilfe.]],
				['3'] = [[Er fing die Granate mit den Zähnen auf und warf sie direkt nach mir zurück. Dabei bemerkte ich, wie ihm etwas aus dem Maul fiel.<new_line>Der Esel rannte los und schrie laut um Hilfe.]],
				['2'] = [[Ich warf ihm das Seil um den Hals, aber der Esel schaffte es, es durchzubeißen! Dabei bemerkte ich, wie ihm etwas aus dem Maul fiel.<new_line>Der Esel rannte los und schrie laut um Hilfe.]],
				['1'] = [[Ich machte mich auf den Weg zu einer Hütte im Sumpf. Da war er – der Esel! Er wühlte in den Büschen herum und murmelte vor sich hin:<new_line>„Blaue Blumen, rote Dornen ...“<new_line>Ich kam nahe genug heran, um meinen Zug zu machen.]]
			}
		},
		['polar_star'] = {
			['episode'] = {
				['3'] = [[„Du bist stark, aber gegen die eisige Zauberei der Winterkönigin wirst du Schutz brauchen. Besorg dir eine Feder des Feuervogels und ruf mich wieder, wenn du bereit bist“, sagt der Bär und verschwindet. Die Kugel fällt sanft in den Schnee.]],
				['2'] = [[„Ich bin Umka, und ich weiß, warum du mich herbeigerufen hast. Ich werde dir helfen, aber die Winterkönigin ist sehr mächtig. Du musst beweisen, dass du stark genug für sie bist“, sagt der Bär. „Lass mich wissen, wenn du bereit bist.“]],
				['1'] = [[Als ich die Kugel zum Himmel hebe, füllt sie sich mit Sternenlicht und scheint noch heller. Dann gibt es einen blendenden Blitz. Als ich mich wieder an die Dunkelheit gewöhnt habe, steht ein großer Eisbär vor mir, der die Kugel auf der Stirn trägt.]]
			}
		},
		['ice_brigands'] = {
			['episode'] = {
				['1'] = [[Die Briganten haben mir aufgelauert, springen aus dem Hinterhalt und greifen mich an. In ihren bleichen Gesichtern ist keinerlei Menschlichkeit zu erkennen.]]
			}
		},
		['set_up_devices'] = {
			['episode'] = {
				['3'] = [[Ich beendete die Installation und versuchte, die Ausrüstung zu tarnen. Die Biomutanten kamen langsam näher. Die Ausrüstung beeinflusst sie wahrscheinlich auf irgendeine Weise, und es besteht die Gefahr, dass sie sie beschädigen könnten.<new_line>Die Archivare werden es zu schätzen wissen, wenn ich mich um ihre Ausrüstung kümmere. Andererseits habe ich meine Hauptaufgabe bereits erfüllt.]],
				['2'] = [[Die Archivare wollten die Ausrüstung genau hier platzieren. Und ich verstehe, warum — ich habe in der Ferne Biomutanten gesehen.]],
				['1'] = [[Ich bin in Position. Jetzt muss ich die Ausrüstung platzieren und sorgfältig tarnen.]]
			}
		},
		['traveler_salvation'] = {
			['episode'] = {
				['1'] = [[Ich finde ihn zusammengerollt unter einem Baum. Offenbar ist er völlig entkräftet eingeschlafen, aber es ist noch eine Spur Leben in ihm. Wäre ich später gekommen, er wäre erfroren.]]
			}
		},
		['scientist_rescue_bandit'] = {
			['episode'] = {
				['3'] = [[Der Räuber brachte uns eine kleine Metallbox. Der Wissenschaftler versteckte sie sofort in seinem Rucksack und bedankte sich bei mir.]],
				['2'] = [[Der Wissenschaftler eilte mit einem Freudenschrei in die Mitte des Lagers und kam mit einer kleinen Metallbox zurück. Er bedankte sich ausgiebig und versteckte die Schachtel in seinem Rucksack.]],
				['1'] = [[Wir nähern uns einem eher dürftig ausgestatteten Lager und einer der Räuber kommt uns entgegen:<new_line>„Ihr seid also wegen der Waffen gekommen? Hört mal, wir leben hier nicht im Luxus, also können wir sie nicht einfach zurückgeben. Erspart einem armen Mann ein wenig Geld für Brot, und ihr könnt in Frieden gehen.“]],
				['4'] = [[Der Wissenschaftler ist tot. Ihn zu beschützen war meine Hauptaufgabe, und ich habe versagt.]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['1'] = [[Der Totengräber war leicht zu finden - der schauerliche Kerl hat mich vor den Friedhofstoren empfangen. In seiner rechten Hand hielt er einen Spaten und in seiner linken ... ein verrottendes Bein. Er sah mich mit einem gruseligen Grinsen an, schmiss das Bein in einen nahe gelegenen Karren voller anderer Körperteile, warf sich die Schaufel über seine Schulter und rannte in die Tiefen des Friedhofs. Es ist Zeit, sich um ihn zu kümmern!]],
				['2'] = [[Der Totengräber wollte nicht reden. Ich fand eine seltsame Notiz in seinem Umhang. Sie sah aus wie eine Einkaufsliste mit Körperteilen – Arme, Beine und Köpfe – und war unten mit „Igor“ unterschrieben. Ich muss diese Notiz dem Tavernenwirt zeigen. Er wird wissen, was zu tun ist.]]
			}
		},
		['emba_intro_bunker'] = {
			['episode'] = {
				['1'] = [[Die alte Bunkertür ist angelehnt, und von unten kommt ein unangenehmes Knarren und Klappern. Da scheint etwas Gefährliches drin zu sein.]],
				['2'] = [[Ich habe die Biomutanten im unterirdischen Labor beseitigt und eine Datei mit dem Vermerk „Verschlusssache“ gefunden. Ich muss zum Navigator zurückkehren.]]
			},
			['choice'] = {
				['1'] = [[Geh zum Navigator zurück]]
			}
		},
		['place_stash_quest'] = {
			['episode'] = {
				['3'] = [[Ich habe ein verdächtiges Geräusch aus dem Gebüsch gehört. Ich habe getan, wofür ich angeheuert wurde, aber ich wette, sie werden mir mehr zahlen, wenn ich herausfinde, wer das Versteck in die Hände bekommen hat.]],
				['2'] = [[Ich vergrub die Kiste und bedeckte das Versteck mit totem Laub.]],
				['1'] = [[Ich fand den auf der Karte gezeigten Flecken. Das gibt definitiv ein gutes Versteck ab.]],
				['7'] = [[Die Granate landete direkt unter den Füßen der Plünderer. Sie explodierte mit solcher Wucht, dass sie keine Chance hatten. Der Hund rannte davon, erschreckt von dem Lärm.]],
				['6'] = [[Ich bin mit den Feinden fertig. Jetzt muss ich hier schnell verschwinden, bevor noch andere kommen.]],
				['5'] = [[Ich sah eine Person mit einem angeleinten Hund aus dem Gebüsch kommen.]],
				['4'] = [[Ein Ast knackte laut ich und entdeckte die Raubtiere.]],
				['12'] = [[Der junge Mann nickte, steckte die Flasche Wodka ein und verschwand.]],
				['9'] = [[Im Gebüsch begegnete mir eine Truppe unfreundlich aussehender Leute. Sie suchten eindeutig nach meinem Versteck.]],
				['10'] = [[Im Gebüsch traf ich den aufgeregten jungen Mann, den ich in der Taverne gesehen hatte.<new_line>„Es sind unruhige Zeiten, weißt du ... Sie baten mich, dich im Auge zu behalten.“<new_line>Er streckte seine Hände aus. In einer hatte er ein Päckchen Zigaretten, in der anderen eine Flasche Wodka.<new_line>„Hier. Amüsiere dich.“]],
				['11'] = [[Der junge Mann nickte, schob die Zigaretten in seine Tasche und verschwand.]]
			},
			['choice'] = {
				['3'] = [[Fliehen]],
				['2'] = [[Mit den Händen graben]],
				['1'] = [[Mit der Schaufel graben]],
				['7'] = [[Aus dem Versteck springen und angreifen]],
				['5'] = [[Das Gebüsch untersuchen]],
				['4'] = [[Verstecken]],
				['12'] = [[Die Zigaretten nehmen]],
				['8'] = [[Eine Granate aus dem Versteck werfen]],
				['11'] = [[Den Wodka nehmen]]
			}
		},
		['reinforcement_quest'] = {
			['episode'] = {
				['7'] = [[Wir gewannen, aber einige unserer Männer wurden verwundet. Sie werden sterben, wenn wir ihnen nicht schnell Hilfe bringen.]],
				['6'] = [[Großartig, wir haben gesiegt. Die Männer dankten mir für meine Hilfe.]],
				['5'] = [[Die Räuber schafften es ins Lager, aber die Männer waren schnell zur Hand und stellten die Räuber im Kraftstoffdepot.]],
				['4'] = [[Das waren nicht einfach Räuber; sie waren Mutanten. Aber die Männer machten einen guten Job und fingen sie auf ihrem Weg ins Lager ab.]],
				['3'] = [[Das waren nicht einfach Räuber; sie waren Mutanten. Und sie waren bereits ins Lager eingebrochen.]],
				['2'] = [[Die Männer waren schnell bei der Hand. Sie erwischten die Räuber auf deren Weg zur Basis.]],
				['1'] = [[Ich ging hin so schnell ich konnte. Die Dinge standen nicht gut: Die Räuber waren bereits ins Lager eingebrochen.]],
				['8'] = [[Ich leistete Erste Hilfe. Jetzt ist es an der Zeit, dass ich mich auf den Weg mache.]]
			},
			['choice'] = {
				['7'] = [[Das Erste-Hilfe-Set verwenden]],
				['6'] = [[Die Leichen durchsuchen und verschwinden]],
				['5'] = [[Helfen, die Räuber zu beseitigen]],
				['4'] = [[Helfen, die Mutanten-Höhle auszuräumen]],
				['3'] = [[Helfen, das Lager zu halten]],
				['2'] = [[Helfen, das Lager zu stürmen]],
				['9'] = [[Mach dich bereit]],
				['8'] = [[Verlassen und zur Basis zurückkehren]]
			}
		},
		['pharmacy_freak'] = {
			['choice'] = {
				['3'] = [[Auf die Leiche schießen]],
				['2'] = [[Die Apotheke durchsuchen]],
				['1'] = [[Die Person durchsuchen]],
				['4'] = [[Von der Apotheke wegrennen]]
			},
			['episode'] = {
				['5'] = [[Ich habe beschlossen, kein Risiko einzugehen und dem Mann vorsichtshalber in den Kopf zu schießen. Da sein Körper kurz zitterte, gehe ich davon aus, dass er noch am Leben war.]],
				['4'] = [[Als ich mich umdrehte und die Apotheke verlassen wollte, ist mir aufgefallen, dass der Mann am Boden verschwunden war. Noch bevor ich die Situation begreifen konnte, griff der Mann mich von hinten mit einem Messer an!]],
				['3'] = [[Wie erwartet war dort nichts mehr übrig – nicht mal Bandagen oder Aktivkohle.]],
				['2'] = [[Als ich den Arm ausstreckte, um die Person zu berühren, hat er plötzlich die Augen geöffnet und mich in die Hand gebissen! Dann ist er aufgesprungen und hat ein Messer gezogen.]],
				['1'] = [[Die Apotheke wurde komplett ausgeplündert, und auf dem Boden inmitten des ganzen Chaos lag jemand regungslos neben ein paar leeren Spritzen. Anscheinend gibt es hier nichts mehr zu holen.]]
			}
		},
		['halloween_fight_hard'] = {
			['episode'] = {
				['7'] = [[Ich habe das Schlachtfeld für den Fall eines Angriffs vorbereitet und ein paar einfache Fallen aufgestellt. Das ist wenigstens etwas. Als Nächstes machte ich ein paar Geräusche, und die restlichen Bestien kamen angerannt. Leider waren das die gefährlichsten von ihnen.]],
				['6'] = [[Ich habe die Gegend sorgfältig abgesucht und tatsächlich weitere Bestien in der Nähe entdeckt. Jetzt habe ich das Überraschungsmoment. Diese Monster sehen allerdings noch gefährlicher aus als die ersten.]],
				['5'] = [[Ich höre die Biester nicht mehr. Ich weiß nicht, wie sie es geschafft haben, mich in eine Falle zu locken. Ich muss nachsehen, ob sich noch mehr von ihnen hier verstecken.]],
				['4'] = [[Ich habe die Monster abgewehrt, aber das scheint nicht das letzte gewesen zu sein. Das Heulen der Bestien wurde um mich herum immer lauter.]],
				['3'] = [[Ein Ast knackte unter meinen Füßen, und die Bestien um mich herum begannen zu heulen. Es war eine Falle!]],
				['2'] = [[Als ich mich dem Geräusch vorsichtig näherte, begann es allmählich zu verstummen. Als ich die wahrscheinliche Quelle erreichte, war es völlig still geworden.]],
				['1'] = [[Das laute Getöse der Bestien ist in der Nähe zu hören. Es scheint hier eine Menge von ihnen zu geben. Ich sollte vorsichtig sein.]]
			},
			['choice'] = {
				['3'] = [[Aufmerksamkeit auf dich ziehen]],
				['2'] = [[Das Gebiet erkunden]],
				['1'] = [[Nah herangehen]]
			}
		},
		['construction_team_scout'] = {
			['episode'] = {
				['3'] = [[Je näher ich dem Ort komme, desto lauter wird der Lärm. Vorsichtig bewege ich mich auf das zu, was sich als das Dröhnen starker Motoren herausstellt. Dort sind also die Lastwagen. Eine Gruppe von bewaffneten Männern steht um sie herum. Ein paar weitere Männer tragen Holzkisten in ein nahe gelegenes Militärzelt.]],
				['2'] = [[Auf dem Boden sind schwarze Flecken von einer Explosion zu sehen, aber es gibt keine Anzeichen dafür, dass hier vor kurzem Menschen waren. Es ist mit Sicherheit zu spät, um herauszufinden, wer oder was die Explosion hier verursacht hat.]],
				['1'] = [[Nach den Spuren auf dem Boden zu urteilen, kommen die Tiere hierher, um Wasser zu holen. Das macht es nicht unbedingt gefährlich, aber es ist besser, es in Zukunft zu vermeiden.]]
			}
		},
		['cover_the_fighter'] = {
			['episode'] = {
				['3'] = [[Der Späher sitzt rauchend am Eingang eines Bunkers. Als er mich sieht, springt er auf:<new_line>„Verstärkung. Das ist eine tolle Nachricht, Kumpel! Ich habe das Schloss schon geknackt, wie wär's, wenn wir loslegen?“]],
				['2'] = [[Der Späher sitzt rauchend am Eingang eines verrosteten Hangars. Als er mich sieht, springt er auf:<new_line>„Verstärkung. Das ist eine tolle Nachricht, Kumpel! Ich habe das Schloss schon geknackt, wie wär's, wenn wir loslegen?“]],
				['1'] = [[Der Späher sitzt rauchend am Eingang einer langen Garage. Als er mich sieht, springt er auf:<new_line>„Verstärkung. Das ist eine tolle Nachricht, Kumpel! Ich habe das Schloss schon geknackt, wie wär's, wenn wir loslegen?“]],
				['11'] = [[„Kein Haufen Beute, aber wenigstens gehört alles uns. Da drüben ist noch eine Tür. Vielleicht finden wir dahinter etwas, das uns am Leben erhält. Aber so wie es sich anhört, wird es zuerst zu einem Kampf kommen. Sollen wir es riskieren oder nach Hause gehen?“]],
				['7'] = [[„Wow, sieh dir diesen Safe an! Was auch immer sich hinter so einem Schloss verbirgt, muss wertvoll sein. Schade, dass wir keine Zeit zum Verweilen haben. Wir haben die Hauptarbeit schon erledigt, aber wenn du auch das knacken kannst, zahlen unsere Jungs extra.“]],
				['6'] = [[Mein Begleiter senkt seine Waffe und untersucht schwer atmend die Räumlichkeiten:<new_line><new_line>„Puh, gut, dass ich auf dich gewartet habe, sonst wäre ich hier nicht lebend rausgekommen. Jetzt muss ich alles untersuchen, bevor die Monster zurückkommen. Danke für deine Hilfe, und viel Glück!“]],
				['5'] = [[Mein Begleiter senkt seine Waffe und untersucht schwer atmend die Räumlichkeiten:<new_line><new_line>„Puh, gut, dass ich auf dich gewartet habe, sonst wäre ich hier nicht lebend rausgekommen. Jetzt muss ich alles untersuchen, bevor die Monster zurückkommen.“]],
				['4'] = [[In der Dunkelheit hören wir ein Rascheln und Scharren. Wir holen unsere Taschenlampen gerade noch rechtzeitig heraus: Einige abscheuliche Kreaturen, die hier ihr Lager aufgeschlagen haben, bereiten sich auf einen Angriff vor.]],
				['12'] = [[Der Späher ist tot. Ich habe bei meinem Auftrag, ihn zu beschützen, versagt.]],
				['10'] = [[Es war niemand drinnen. Der Späher schaute sich im Raum um und kam zu mir zurück.]],
				['9'] = [[Der Späher ist damit beschäftigt, alle Schränke und dunklen Ecken auf dem Gelände zu untersuchen. Der Safe steht unangetastet da.]],
				['8'] = [[Nach einiger Anstrengung konnte ich den Safe öffnen. Darin fanden wir einige sehr gut erhaltene elektronische Geräte und Kisten mit Munition. Der Späher pfiff erstaunt und klopfte mir auf die Schulter.]]
			},
			['choice'] = {
				['3'] = [[Knack das Schloss]],
				['5'] = [[Geh durch die Tür]],
				['1'] = [[Betritt das Gebäude]],
				['4'] = [[Spreng das Schloss auf]]
			}
		},
		['new_year_boss_1'] = {
			['choice'] = {
				['3'] = [[„Wo ist der Weihnachtsmann?“]],
				['2'] = [[„Es gibt mehr als genügend Verdächtige.“]],
				['1'] = [[„Was ist hier passiert?“]],
				['7'] = [[„Was hat der Baum damit zu tun?“]],
				['6'] = [[„Was ist mit der Suche nach …“]],
				['5'] = [[„Ich? Wie?“]],
				['4'] = [[„Hey, fordere mich nicht heraus. Wer seid ihr Typen?“]],
				['9'] = [[„Also, muss ich gute Taten vollbringen …“]],
				['8'] = [[„Okay, hast du irgendwelchen Schmuck?“]]
			},
			['episode'] = {
				['10'] = [[„Ach, du hast recht! Das Verschwinden des Weihnachtsmanns hat eindeutig etwas mit dieser Bande zu tun, die von … Krampus? Also, ich kenne einen tollen Fährtenleser. Aber um ihn hierher zu bringen, musst du den Weihnachtsbaum wieder schmücken.“]],
				['3'] = [[In diesem Moment hörten wir das Aufheulen von Motoren, und Leute auf Motorrädern fuhren aus dem verbrannten Haus heraus.<new_line><new_line>Einer von ihnen rief uns zu.<new_line>„Was wollt‘n ihr? Wir haben hier jetzt das Sagen!“]],
				['2'] = [[„Mich schaudert es, wenn ich nur daran denke. Ich hoffe nur, der Weihnachtsmann konnte entkommen. Aber was für Banditen deiner Welt könnten so etwas tun?“]],
				['1'] = [[Wir sind an einem Holzhaus angekommen. Die Wände sind verkohlt, die bunten Fensterscheiben sind zerbrochen, und die verzierte Tür wurde zu Boden gestoßen.]],
				['11'] = [[„Hmm, Technik ist nicht meine Stärke … Ich weiß nur, dass der Weihnachtsbaum geschmückt sein muss, damit die Portale funktionieren.“]],
				['7'] = [[„Oh, dein Weihnachtsmann hat bei mir ein paar besondere Kostüme bestellt. Sie sind der Schlüssel zur ganzen Sache. Eins davon kostet nur … Nein, vergiss es, deine Welt ist eindeutig in Schwierigkeiten. Du kannst das Kostüm kostenlos haben. Das ist das Mindeste, was ich tun kann.“]],
				['6'] = [[„Unerhört. Was für Schurken! Aber eines ist klar: Weihnachten in eurer Welt ist in Gefahr. Ich hoffe, mein Weihnachtsmann-Kollege ist noch am Leben. Aber bis wir ihn finden, müsst ihr wohl seine Aufgaben übernehmen.“]],
				['5'] = [[„Ey, biste blöd oder was? Dir wurd gesagt, du sollst abhaun. Oder, nee! Der Krampus liebt dumme Menschen. Also werdet ihr Drecksäcke ihn besuchen! Los, schnappt sie euch!“]],
				['4'] = [[„Dein‘n Weihnachtsmann gibt's nich mehr. Verschwinde! Bevor wir auch noch den Bart von deinem Opa anzünden.“<new_line>Eine Flasche flog hinter dem Mann hervor und traf mich hart am Bein.]],
				['12'] = [[„Ha, der Zauber dieses Weihnachtsbaums funktioniert nur mit besonderem Schmuck, den ganz normale Menschen das ganze Jahr über mit Liebe und Wärme in ihrem Haus aufbewahren. Das kann man nicht in einer Fabrik herstellen … leider.“]],
				['13'] = [[„Und du wirst diesen Schmuck als Dankeschön erhalten. Beeil dich, du musst das Portal für den Fährtenleser öffnen. An die Arbeit!“]],
				['9'] = [[„In der Zwischenzeit werde ich hier meine Waren verkaufen. Der Weihnachtsmann ist zwar nicht zu sehen, aber das Fest steht trotzdem vor der Tür. Komm in meinem Laden vorbei. Vielleicht findest du etwas Nützliches.“]],
				['8'] = [[„Ho ho ho, ausgezeichnet. Tue Gutes und erhalte als Dankeschön Geschenke. So wirst du ein Meister-Weihnachtsmann.“]]
			}
		},
		['scientist_rescue_quest'] = {
			['episode'] = {
				['3'] = [[„Siehst mal, so sieht es aus. Eine Bande hat es bereits geschafft, ein Gerät von mir zu stehlen. Wir können direkt nach Hause gehen, aber du würdest der Siedlung wirklich helfen, wenn du auch das Gerät zurückholen könntest.“]],
				['2'] = [[„Gut, ich packe die Bodenproben in meinen Rucksack und steh dir dann zur Verfügung. Ist es in Ordnung, wenn ich meine Waffe an die Seite schnalle?“]],
				['1'] = [[Ich klopfte an die Tür des Wachhauses. Der Wissenschaftler öffnete den Riegel und richtete sofort ein Jagdgewehr auf mich.<new_line><new_line>Es bedurfte einiger Überzeugungsarbeit, um ihn davon zu überzeugen, dass ich aus der Siedlung kam.]],
				['7'] = [[Der Wissenschaftler wartet geduldig mit allem, was er in seinem Rucksack hat, außer dem Erste-Hilfe-Kasten.]],
				['6'] = [[„Es tut mir leid, aber Kampfmedizin ist etwas, das ich... vermeide. Ich habe sterilisierte Instrumente hier, alles ist im Grunde bereit. Und ich kann immer warten, bis wir unser medizinisches Zentrum erreichen. Es ist nur ein Kratzer, wirklich.“]],
				['5'] = [[„Ich bin natürlich bereit. Aber es gibt da eine Sache. Ein paar Räuber sind hier eingedrungen. Ich habe sie rausgeholt, aber ich habe mir eine kleine Wunde zugezogen, um es ganz offen zu sagen. Aber wirklich nichts, worüber man sich beschweren sollte.“]],
				['4'] = [[„Nicht so viele, denke ich. Ich zeige dir auf der Karte, wo ihr Lager ist, und dann liegt es an dir, ob du dorthin gehst oder direkt nach Hause fährst.“]],
				['9'] = [[Jemand hat dafür gesorgt, dass das Gerät einmal sehr sicher befestigt war. Außerdem sind die Befestigungen ein wenig rostig. Es wird anstrengend.]],
				['8'] = [[„Im Grunde bin ich bereit. Ich nehme alle Instrumente mit, aber ich hatte ein Problem mit einem auf dem Dach. Ich habe versucht, es abzuschrauben, aber das hat alles nur noch schlimmer gemacht.<new_line><new_line>Wenn du Zeit hast, könntest du versuchen, es abzuschrauben? Aber das Wichtigste ist, dass wir den Rest der Ausrüstung nach Hause bringen, also verlasse ich mich ganz auf deine Erfahrung.]]
			},
			['choice'] = {
				['6'] = [[Schraube es mit Werkzeug ab]],
				['5'] = [[Geh zur Siedlung]],
				['4'] = [[„Lass uns das unterwegs in Ordnung bringen.“]],
				['3'] = [[„Gibt es dort viele Räuber?“]],
				['2'] = [[„Alles ist bereit, los geht's.“]],
				['1'] = [[„Bereit für den Rückweg?“]]
			}
		},
		['emba_isaev_rostov'] = {
			['episode'] = {
				['3'] = [[„Ich muss Sie noch einmal bitten, unsere Erkundungsaufträge zu erfüllen, um die geheimen E.M.B.A.-Projektdokumente zu holen. Das ist in erster Linie notwendig, um den Zugangscode zum Labor zu entschlüsseln.“]],
				['2'] = [[„Ich nehme an, dass das dieselben Leute sind, die vor dem Krieg an dem Projekt gearbeitet haben. Den Aufzeichnungen zufolge wurde in die frühere Bio-Station von einem Mann eingebrochen, der den Zugangscode von Oberst Kurganow benutzte. Wir werden versuchen, mehr über ihn herauszufinden.“]],
				['1'] = [[„Die Blackbox, die Sie aus der letzten Wissenschaftsstadt mitgebracht haben, hat besser überlebt als die vorherige. Unsere Hypothesen werden bestätigt. An einigen der Bio-Stationen arbeitet noch jemand.“]],
				['11'] = [[„Das müssen wir noch herausfinden, aber wahrscheinlich ist es die Symbiose von Biologie und Kybernetik, die dahinter steckt. Vielleicht sind aber auch andere geheime Technologien im Spiel.“]],
				['7'] = [[„Er hat sie für Experimente benutzt?! Unfassbar! Er muss auch Gerin ausgebeutet haben, wir müssen ihn sofort finden.“]],
				['6'] = [[„Meine Güte, Sie haben sie gerettet! Meine Kollegen sind am Leben, das war mir schon klar. Ich habe unsere besten Ärzte zu ihnen geschickt. Was ist eigentlich mit ihnen passiert?“]],
				['5'] = [[„Das sehe ich auch so. Deshalb schlage ich vor, dass Sie sofort zum Navigator gehen. Sprechen Sie mit ihm und beginnen Sie mit der Suche nach der Blackbox. Wir haben den Zugangscode zum Geheimfach des Labors entschlüsselt. Hier, ich habe ihn für Sie aufgeschrieben.“<new_line>(hält ein Stück Papier mit dem Code hin)]],
				['4'] = [[„Nichts, was aus praktischer Sicht von Wert wäre. Aber wir haben herausgefunden, dass der Navigator etwas über Kurganow wissen muss – den Mann, dessen Zugangscode benutzt wurde, um in die vorherige Bio-Station zu gelangen.“]],
				['12'] = [[„Wir müssen Gerin retten. Den Aufzeichnungen zufolge könnte er ziemlich weit von hier entfernt sein. Auf dem Weg dorthin werden wir eine andere Wissenschaftsstadt besuchen. Dort gibt es ein bestimmtes Objekt, über das Kurganow Informationen gesammelt hat. Ich glaube, es gibt einen Grund dafür, und den können wir nicht ignorieren. Wir brechen auf, sobald Sie bereit sind.“]],
				['10'] = [[„Wir entdeckten, dass Wissenschaftler unter anderem moderne Technologien einsetzten, um das Myzel zu stabilisieren.<new_line>Computermaschinen mit bestimmten Algorithmen halfen offenbar, die unkontrollierbaren Veränderungen des Virus einzudämmen. Jetzt arbeiten wir aktiv in diese Richtung.“]],
				['9'] = [[„Aber er ist nicht im Krankenhaus unter den Verwundeten. Das bedeutet, dass er in Ausübung seiner Pflicht gestorben ist. Das ist eine sehr deprimierende Nachricht. Wir haben einen guten Mann und einen sehr wertvollen Mitarbeiter verloren. Aber wir müssen weiterarbeiten, damit sein Tod nicht umsonst war.“]],
				['8'] = [[„Auf jeden Fall. Ich glaube, der General hat Gerin gezwungen, die militärische Entwicklung fortzusetzen. Wenn wir Gerin retten, wird unsere Aufgabe viel leichter sein. Ich habe keine Berichte vom Navigator erhalten, haben Sie eine Ahnung, wo das Problem liegt?“]]
			},
			['choice'] = {
				['7'] = [[„Wir müssen ihn befragen!“]],
				['6'] = [[„Und wie hat Kurganow das Expeditionsteam kontrolliert?“]],
				['5'] = [[Die Explosion erwähnen]],
				['4'] = [[„Glauben Sie, es ist alles Kurganows Schuld?“]],
				['3'] = [[„Sie wurden von Kurganow kontrolliert.“]],
				['2'] = [[„Was soll ich jetzt tun?“]],
				['1'] = [[„Was?! Wer könnte das sein?“]]
			}
		},
		['forester'] = {
			['episode'] = {
				['1'] = [[Der Förster grüßte mich freundlich und lud mich an seinen Tisch ein. Als ich mit dem Essen und er mit seinen Geschichten fertig war, machten wir uns in die Wälder auf. Ein Rudel Wölfe hatte uns schon bald umzingelt. Hinter mir konnte ich das markerschütternde Knacken von Knochen und das Reißen von Kleidung hören. Als ich mich umdrehte, fand ich anstelle des Försters einen grauen Werwolf wieder. „Igor lässt grüßen!“, knurrte er, bevor er sich auf mich stürzte.]]
			}
		},
		['forest_camp'] = {
			['choice'] = {
				['3'] = [[Das linke Zelt betreten]],
				['2'] = [[Tarnkleidung anziehen und dich in die Zelte schleichen]],
				['1'] = [[Näher herangehen]],
				['7'] = [[Das rechte Zelt betreten]],
				['6'] = [[Schnappen, was du tragen kannst, und dich aus dem Staub machen]],
				['5'] = [[Das Hauptzelt betreten]],
				['4'] = [[Das Lager durchsuchen]],
				['9'] = [[Alles mitnehmen und dich aus dem Staub machen]],
				['8'] = [[Dem Räuber die Kehle aufschlitzen]]
			},
			['episode'] = {
				['7'] = [[Im Zelt lag ein schlafender Räuber, der lauthals schnarchte. Der leeren Flasche neben ihm nach zu urteilen, wird er auch nicht so schnell aufwachen.]],
				['6'] = [[Im Inneren herrscht Unordnung. Hoffentlich kann ich hier irgendwas Nützliches finden.]],
				['5'] = [[Da nun alle Räuber tot sind, kann ich in Ruhe ihr Lager durchsuchen.]],
				['4'] = [[Als ich mir den Weg ins Zelt freischnitt, fand ich einen überrascht dreinblickenden Räuber vor, der seine Pistole auf mich richtete.]],
				['3'] = [[Ich habe es geschafft, mich im Laub zu verstecken und mich an die Zelte heranzuschleichen. Ich könnte die Zeltwand mit meinem Messer aufschneiden und hineinsteigen.]],
				['2'] = [[Der Wachposten hat mich gesehen und augenblicklich sein Gewehr angelegt und geschossen! Die Kugel hat mich in die Brust getroffen, ich bin aber noch am Leben. Ich muss kämpfen.]],
				['1'] = [[Als ich durch den Wald lief, habe ich Rauch gerochen.<new_line>Ich habe schnell entdeckt, wo er herkam. Ein kleines Lager von Räubern mit Zelten und einem Feuer. Einer von ihnen hält Wache über das Gebiet, also wäre es riskant, näher heranzugehen.]],
				['8'] = [[Eine schnelle Bewegung und das Leben des Räubers war beendet. Er hat nicht einmal bemerkt, was geschehen ist. Jetzt gehören all seine Sachen mir.]]
			}
		},
		['newyearEvent_search_easy'] = {
			['episode'] = {
				['7'] = [[Die Suppe war unglaublich lecker. Als die Leute sahen, dass ich zufrieden war, begannen sie, sich selbst zu bedienen. Der Höhlennachtsmann wachte ruhig und mit einem zufriedenen Lächeln über das Essen.]],
				['6'] = [[Mir wurde sofort schlecht, und Höhli fing an, verständlich zu reden.<new_line>„Oh, es sieht so aus, als hättest du dich zu sehr von der grünen Energie isoliert, und deine Bauchspeicheldrüsenchakren verdauen hoch spirituelle Smoothies nicht besonders gut. Danke, dass du ein großes Opfer für die Menschen gebracht hast.“]],
				['5'] = [[Höhli steckte seinen Kopf in die Wanne und trank eine großzügige Menge des Inhalts.<new_line>„Lecker!“<new_line>Das ist alles schön und gut, aber sein Steinmagen könnte wahrscheinlich alles verdauen. Aber ein normaler Mensch wird das nicht unbedingt vertragen. Ich werde es wohl selbst probieren müssen.<new_line>]],
				['4'] = [[Höhli nickte anerkennend.]],
				['3'] = [[Nachdem ich eine Weile gelaufen war, stieß ich auf eine riesige Steinschale, die von allen Seiten durch eine magische Flamme erwärmt wurde. Die Leute standen drum herum. Höhli kochte intensiv und fügte ständig Zutaten hinzu. Schließlich war er fertig und verkündete:<new_line>„Lecker lecker!“]],
				['2'] = [[„Uuuuuuuuuu“<new_line>Höhli sprang vor Freude auf, riss mir die Kräuter aus der Hand und rannte davon.]],
				['1'] = [[In seiner üblichen Art versuchte Höhlennachtsmann mir zu erklären, dass er Kräuter für ein Weihnachtsrezept braucht. Ich erfuhr es vom Geschäftsnachtsmann, der ebenfalls versuchte, Höhli zu helfen, aber einen Schlag auf den Kopf bekam.<new_line>„Kau kau … Ah uuvaaah.“]]
			},
			['choice'] = {
				['6'] = [[„Vielleicht erst mal probieren? Sie werden sich vergiften.“]],
				['5'] = [[Die Suppe probieren]],
				['4'] = [[„Willst du das an die Leute verfüttern?“]],
				['3'] = [[Zu Höhli aufschließen]],
				['2'] = [[„Nee, ich habe sie nicht. Ich werde nachsehen.“ (Den Kopf schütteln und gehen)]],
				['1'] = [[„Hier.“ (Kräuter hinhalten)]]
			}
		},
		['new_year_boss_3'] = {
			['choice'] = {
				['5'] = [[Durch die Tür gehen]],
				['4'] = [[„Was sollen wir machen?“]],
				['3'] = [[„Ähhh …“]],
				['2'] = [[„Aber du hast doch gesagt, ich soll mich ‚um den Park kümmern‘.“]],
				['1'] = [[„Nein, es ist alles in Ordnung.“]]
			},
			['episode'] = {
				['7'] = [[„Das ist die dunkle Seite meiner Vergangenheit. Jetzt unterstütze ich den natürlichen Klassenkampf“]],
				['6'] = [[„Bärchen, das bedeutet, dass ich dich auch missverstanden habe. Schließlich hast du in deiner Welt eine Revolution nach der anderen angeführt!“]],
				['5'] = [[Bärnachtsmann fasste sich an den Kopf.<new_line>„Ich meinte, den Park zu schützen, falls der Krampus auftaucht! Wer beschlagnahmt schon Eigentum mit Gewalt?!“]],
				['4'] = [[„Was ist das dann hier? Oh nein … Bist du derjenige, der hier alles kaputt gemacht hat?!“]],
				['3'] = [[Als die letzten Schüsse verhallten, sah ich mich um und erblickte meine Weihnachtsmann-Kollegen in der Tür stehen.<new_line>„Sind wir zu spät, Genosse? Hat die Krampusbande schon den Park überfallen?“]],
				['2'] = [[Ich habe die Tür, die zum inneren Teil des Parks führt, eingeschlagen. Ich hoffe, das Geschenkelager ist dort.]],
				['1'] = [[Der Park ist nicht geöffnet. Das Tor ist verschlossen, und niemand antwortet, als ich laut klopfe. Ich werde das Tor aufbrechen müssen.]],
				['8'] = [[„Okay … Ein Problem nach dem anderen. Geschäftsnachtsmann hat einen Deal mit dem Manager gemacht – er wird die Geschenke teilen. Hoffentlich können wir diesen Vandalismus später unter den Teppich kehren.<new_line>Lass uns mit der Gang verhandeln gehen.“]]
			}
		},
		['halloween_def_easy'] = {
			['episode'] = {
				['3'] = [[„Oh! Hast du das gehört? Die Ungeheuer nähern sich. Machen wir ihnen Feuer unterm Hintern!“]],
				['2'] = [[„Ich danke dir. Ich werde das nicht vergessen. Wir müssen uns bereit machen. Ich kann entweder Fässer mit Treibstoff ausrollen, auf die du schießen kannst, oder ich kann Fallen aufstellen. Pass nur auf, dass du nicht in eine gerätst.“]],
				['1'] = [[„Freundlicher Fremder, bist du wirklich hier, um mir zu helfen? Diese Dämonen sind ein ziemliches Ärgernis. Sie haben meine gesamte Ernte verschlungen. Meine Nachbarn glauben nicht, dass es Dämonen sind. Sie sagen, ich sei ein alter Narr, der sich Dinge einbildet, und dass ich die Ernte selbst gegessen habe. Wirst du mir helfen? Es wird sich für dich lohnen.“]]
			},
			['choice'] = {
				['3'] = [[„Stelle die Fallen auf.“]],
				['2'] = [[„Ich habe keine Zeit. (Gehen)“]],
				['1'] = [[„Ich werde natürlich helfen“]],
				['4'] = [[„Benutze die Fässer.“]]
			}
		},
		['halloween_def_medium'] = {
			['episode'] = {
				['17'] = [[„Der Gegenangriff war erfolgreich, und die Verluste waren minimal. Allerdings plant der Feind nun, das verlorene Gebiet mit neuen Kräften zurückzuerobern. Wir brauchen eine kluge Strategie für den Kampf. Was sollen wir tun, Kommandant?“]],
				['16'] = [[„Hurraaa! Wir haben gewonnen!“]],
				['15'] = [[„Zu Befehl! Wir rücken auf Ihren Befehl aus.“]],
				['14'] = [[„Zu Befehl! Ungehorsam wird beendet!“]],
				['2'] = [[„Zu Befehl!“<new_line>Die Kürbisköpfe stellten sich in Reihen auf.]],
				['1'] = [[„Hallo! Danke, dass du unserer Aufforderung nachgekommen bist. Diese Biester haben uns den letzten Nerv geraubt. Früher konnten wir mit ihnen fertig werden, aber sie haben unseren Kommandanten bei einem kürzlichen Gefecht gefangen genommen. Ich fürchte, dass dies ohne ihn unsere letzte Schlacht sein könnte. Du siehst wie ein fähiger Mann aus. Kannst du unseren Kommandeur vertreten, zumindest für diese Schlacht?“]],
				['5'] = [[„Ja, Sir, Kommandant, Sir! Ich kann die Bestien hören. Wie lautet unsere Kampftaktik?“]],
				['12'] = [[„Danke für die Hilfe! Wir werden dich als unseren besten Kommandanten in Erinnerung behalten. Das heißt, nach unserem Lieblingskommandanten!“]],
				['13'] = [[„Wir erwarten einen groß angelegten feindlichen Angriff aus Richtung des Badehauses! Der Feind wird alle ihm zur Verfügung stehenden Kräfte einsetzen. Die Dorfbewohner sind verängstigt. Wenn der Feind sich nähert, könnten sie noch mehr Angst bekommen. Wir müssen einen Gegenangriff starten.“]],
				['19'] = [[„Ach, du schon wieder, Kleiner? Kannst du denn gar nichts alleine machen? Als ich in deinem Alter war, habe ich alles alleine gemacht. Also gut. Zeig mir, wer gemein zu dir ist.“]],
				['18'] = [[„Jawohl, Herr Kommandant! Der Feind nähert sich.“]]
			},
			['choice'] = {
				['3'] = [["Klar, stell dich in Reih und Glied auf!"]],
				['2'] = [[„Gib mir einen Statusbericht.“]],
				['9'] = [[„Geh du vor, ich gebe dir Deckung.“]],
				['7'] = [[„Ich gehe voraus, und du folgst mir!“]],
				['6'] = [[„Die Bewohner sind verängstigt. Wir holen zum Gegenschlag aus!“]],
				['5'] = [[„Schluss mit dem Ungehorsam!“]],
				['4'] = [[„Gib mir einen Statusbericht.“]],
				['12'] = [[„Ich werde einen Verbündeten um Hilfe bitten.“]],
				['8'] = [[„Ich gehe voraus, und du flankierst sie!“]],
				['10'] = [[„Ich werde sie ablenken, und du greifst von hinten an!“]],
				['11'] = [[„Wir brauchen keine Gerissenheit! Wir brauchen Mut!“]]
			}
		},
		['lair_ant'] = {
			['episode'] = {
				['1'] = [[Es war ein ekliger Anblick. Die Ameisen hatten ihr Heim offensichtlich aus wirkliche allem gebaut, was sie finden konnten: Abfall, Schmutz und verrottende Überreste. Aber selbst dieser unglaubliche Müllhaufen konnte etwas Wertvolles verbergen.]]
			}
		},
		['help_hunter1'] = {
			['episode'] = {
				['1'] = [[Eine Horde von Zombies hat den Jäger in die Enge getrieben. Allein hat er keine Chance, zu entkommen.]]
			}
		},
		['newyearEvent_commander_medium'] = {
			['episode'] = {
				['7'] = [[„Sie haben gute Arbeit geleistet, aber ich sehe eine weitere Gruppe von Gaunern am Horizont auftauchen. Ihr solltet ihnen noch eine kurze Lektion erteilen. Vielleicht lernen einige der Dorfbewohner es und setzen es im Kampf ein.“]],
				['6'] = [[Die anderen Milizionäre verstanden die Lektion und begannen ebenfalls aktiv zu üben. Nach einer Weile kam der Höhlennachtsmann auf mich zu.<new_line>„Die Gauner nähern sich. Die Dorfbewohner sollten gegen sie kämpfen, um deine Lektion zu festigen. Zeig ihnen, wie man kämpft.“]],
				['5'] = [[Zwei der vier Milizionäre verstanden meine Lektion perfekt und begannen zu üben. Die anderen beiden haben anscheinend nichts gelernt. Einer von ihnen fragte:<new_line>„Ah uuumaruuf?“]],
				['4'] = [[Die Miliz ist nicht sehr groß – nur vier Leute. Einer von ihnen kommt auf mich zu und grunzt fragend.<new_line>„Uuugruuuuum ah uuum?“]],
				['3'] = [[Meine intellektuellen Fähigkeiten haben nachgelassen, und Höhlis Rede beginnt zu mir durchzudringen.<new_line>„Den Göttern sei Dank, dass du mich verstehen kannst! Ich will den Dorfbewohnern beibringen, wie man sich gegen Feinde wehrt. Es ist mir sogar gelungen, eine Miliz zu gründen. Aber um ehrlich zu sein, bin ich nicht der beste Lehrer. Hilf mir.“]],
				['2'] = [[Als er mich sieht, springt Höhli vor Freude auf und schwingt seine Keule gegen meinen Kopf.<new_line>„Uuuuuuuuuuuuuh.“]],
				['1'] = [[Umringt von den Dorfbewohnern versucht der Höhlennachtsmann, ihnen auf seine übliche Art etwas zu erklären.<new_line>„Uuuurgh“, sagt er und zeigt mit der Hand auf seine Keule.<new_line>„Brrrrr!“, fährt er fort und deutet mit dem Finger auf den Wald.]],
				['8'] = [[Während des Kampfes begann sich mein Verstand allmählich wieder zu normalisieren.<new_line>Nach dem Kampf kam einer der Milizionäre auf mich zu.<new_line>„Dank dir können wir uns jetzt in der Stunde der Not selbst schützen. Das ist das größte Geschenk!“]]
			},
			['choice'] = {
				['10'] = [[Eine defensive Haltung einnehmen]],
				['3'] = [[„Nein, es gibt einen anderen Weg!“ (Wodka trinken)]],
				['2'] = [[Ihn deinen Kopf treffen lassen]],
				['1'] = [[„Was ist hier los?“]],
				['11'] = [[Zeigen, wie man einen Gegenangriff ausführt]],
				['7'] = [[Zeigen, wie man einen Speer benutzt]],
				['6'] = [[Zeigen, wie man eine Granate benutzt]],
				['5'] = [[Zeigen, wie man ein Gewehr benutzt]],
				['4'] = [[„Okay.“]],
				['12'] = [[Zeigen, wie man ausweicht]],
				['13'] = [[„Bin froh, dass ich helfen konnte.“ (Weggehen)]],
				['9'] = [[Den Schlachtruf zum Angriff geben]],
				['8'] = [[Zeigen, wie man eine abgesägte Schrotflinte benutzt]]
			}
		},
		['new_year_boss_5'] = {
			['choice'] = {
				['2'] = [[„Danke, aber wir haben immer noch eine weitere Energie-Schleuse vor uns.“]]
			},
			['episode'] = {
				['3'] = [[„Okay, los geht‘s!“]],
				['2'] = [[„Okay, und wer ist der zweite?“]],
				['1'] = [[„Diesmal machen wir es nicht auf deine Art, sondern auf die schlaue Art. Einige von uns werden zurückbleiben und die Umgebung bewachen, damit sich niemand davonschleicht und dem Krampus von unserem Sabotageakt erzählt.<new_line>Du kannst zwei Helfer für den Kampf auswählen. Wer werden sie sein?“]],
				['4'] = [[„Toller Kampf, Genosse! Als ich dir zusah, musste ich an mich selbst als Welpe denken …“]]
			}
		},
		['new_year_boss_5_3'] = {
			['choice'] = {
				['3'] = [[Aufstehen und Frosti hinterherjagen]],
				['2'] = [[Frosti hinterherjagen]],
				['1'] = [[„Du schon wieder?!“]],
				['4'] = [[„Was sollen wir jetzt machen?“]]
			},
			['episode'] = {
				['7'] = [[„Raus hier, bevor wir eingeschneit werden! Ich zahle den Preis dafür, dass ich mich mit euch eingelassen habe …“]],
				['6'] = [[Wieder einmal hat mich eine Schneewand umgeworfen!<new_line><new_line>„Das hat keinen Sinn. Frosti ist schlau. Und jetzt hat er den Teil der Superwaffe, der unsere Magie aufgesaugt hat.“]],
				['5'] = [[Ein Schneewirbel hat mich umgeworfen. Er schien aus dem Nichts zu kommen.]],
				['4'] = [[„Du wirst mich wiedersehen. In deinen Albträumen“<new_line><new_line>Frosti warf sich Krampus über die Schulter, zog eine Art glänzendes Teil aus den Fragmenten der Waffe und rannte die Treppe nach draußen hinauf.]],
				['3'] = [[„Dickköpfige Weihnachtsmänner! Dickköpfiger Krampus! Die Maschine – du hast sie zerstört! Aber egal, es ist noch nicht vorbei. Mal sehen, was du jetzt ohne deine Magie anstellst!“]],
				['2'] = [[„Oh, sieh mal einer an, wer zu Besuch kommt! Habt ihr Geschenke mitgebracht? Ha ha! Nein? Das macht nichts! Ich wollte sowieso mal sehen, wie meine Waffe Magie aus lebenden Kreaturen saugt!“]],
				['1'] = [[Wir haben eine Treppe aus Eis erreicht, die in den Untergrund führt. Der ganze Ort ist mit verkohltem Weihnachtsschmuck und zerrissenen Geschenkkartons übersät.]]
			}
		},
		['new_year_prologue'] = {
			['choice'] = {
				['1'] = [[Das Ornament nehmen]],
				['2'] = [[Auf die Plattform stellen]]
			},
			['episode'] = {
				['1'] = [[Mir fiel etwas ungewöhnlich Helles im Schnee auf. Ich bückte mich, um einen Blick darauf zu werfen – es stellte sich heraus, dass es ein Weihnachtsornament war.]],
				['2'] = [[Als ich meinen Kopf wieder hob, sah ich eine leuchtende Plattform direkt vor mir. Seltsam, vor einem Moment war sie noch nicht da.]]
			}
		},
		['emba_commander_medium2'] = {
			['episode'] = {
				['3'] = [[„Gut, kommen wir zur Sache. Die Operation ist kompliziert und Progress kann keinen erfahrenen Partner für mich finden.<new_line>Also musst du es sein — hast du schon mal nachts Biomutanten gejagt?“]],
				['2'] = [[„Warte. Ich arbeite gerade an einem kleinen Projekt und könnte etwas Hilfe gebrauchen. Ich brauche jemanden, der ein Sturmgewehr von einem Spaten unterscheiden kann.<new_line>Aber zuerst muss ich mich um etwas anderes kümmern. Ich brauche ein Seil, für den Fall, dass ich jemanden lebendig fange. Hast du eins? Es ist ein weiter Weg zum Geländewagen.“]],
				['1'] = [[„Warte. Ich arbeite an einem kleinen Projekt und könnte etwas Hilfe gebrauchen. Ich brauche jemanden, der ein Sturmgewehr von einem Spaten unterscheiden kann.<new_line>Aber zuerst habe ich etwas anderes zu tun. Mir geht die Tarnung aus. Kannst du mir die Basis für eine neue Portion bringen, während ich die Farbe verdünne?“]],
				['7'] = [[„Ich werde schnell handeln müssen und mich im Dunkeln ohne zusätzliche Ausrüstung aufhalten.<new_line>Deshalb möchte ich, dass du ein Nachtsichtgerät nimmst, eine Position mit einem Funkgerät in der Nähe einnimmst und mir die Situation auf dem Schlachtfeld meldest. Ich werde nach deinen Berichten handeln.“]],
				['6'] = [[„Hast du es etwa eilig, irgendwohin zu kommen? Jedenfalls will ich die Biomutanten im Dunkeln angreifen und sehen, wie sie sich ohne Licht zurechtfinden. Ein weiteres Experiment.“]],
				['5'] = [[„Ja. Wie wäre es also mit einem kleinen Abenteuer? Du hast doch keine Angst vor der Dunkelheit, oder?“]],
				['4'] = [[„Es gibt nicht viele, die das getan haben. Deshalb will ich lernen, wie sich diese Kreaturen in der Dunkelheit verhalten.<new_line>Vielleicht kann ich sogar jemanden fangen! Das wäre ein Durchbruch für mein Projekt.<new_line>Aber das ist eine Aufgabe für zwei Jägerinnen.“]],
				['11'] = [[„Du hast einen klaren Kopf bewahrt und ich kann mich auf dich verlassen. Es wäre natürlich gut gewesen, etwas Lebendiges zu fangen … na ja, nur so aus Interesse.<new_line>Nun, das macht nichts. Nächstes Mal. Danke für deine Hilfe.“]],
				['10'] = [[Wir machten eine Zeit lang unser eigenes Ding. Als es langsam dunkel wurde, stand die Jägerin auf, gab mir das Funkgerät und schlug vor, dass wir anfangen sollten.]],
				['9'] = [[„Wusstest du, dass Eidechsen und Warane Aasfresser sind und dass sie jedes Hindernis leicht überwinden? Wenigstens fliegen sie nicht.<new_line>Ich habe mich schon immer für Reptilien interessiert. Und diese hier haben all diese Fähigkeiten … Ich hoffe, ich fange diesmal eines.<new_line>Hmm, wie auch immer, genug geredet. Es ist Zeit, aufzubrechen. Hier ist das Funkgerät. Bist du bereit?“]],
				['8'] = [[„Ausgezeichnet. Während wir auf völlige Dunkelheit warten, kann ich dir etwas über die Biomutanten erzählen.<new_line>Wenn ich mir deine Waffe so ansehe – hast du sie in letzter Zeit gereinigt? Es würde mich nicht wundern, wenn sie bei jedem zweiten Schuss klemmt.“]]
			},
			['choice'] = {
				['3'] = [[„Ich werde keine Angst haben, was muss ich tun?“]],
				['2'] = [[„Für uns beide?“]],
				['1'] = [[„Was willst du eigentlich von mir?“]],
				['7'] = [[Finde mehr über die Biomutanten heraus]],
				['6'] = [[„Ich bin noch nicht bereit.“]],
				['5'] = [[„Ich kann es versuchen.“]],
				['4'] = [[„Was soll ich für dich tun?“]],
				['9'] = [[Starte die Operation]],
				['8'] = [[Kümmere dich um deine Waffen]]
			}
		},
		['newyearEvent_def_medium'] = {
			['episode'] = {
				['10'] = [[„Ich bin so gut, dass ich sogar noch ein wenig Magie übrig habe, um dich zu verzaubern. Wähle den Spruch, den du willst.“]],
				['3'] = [[Es sind so viele Leute auf dem Konzert – sie hocken wie die Sardinen auf dem Präsentierteller. Ich habe es irgendwie zum Perimeter geschafft, aber ich sehe meinen Partner nicht.]],
				['2'] = [[„Super! Dann geh zum Perimeter. Dein Partner wartet dort auf dich.“]],
				['1'] = [[„Was geht, Choomba? Ich werde ein riesiges Konzert geben, aber meine Fixer haben mir einen Tipp gegeben, dass ein paar ungebetene Gäste planen, es zu sprengen.<new_line>Wenn sie auftauchen, müssen sie genullt werden. Wirst du bei der Sicherheit helfen?“]],
				['11'] = [[Wie üblich belegte mich der Zaubernachtsmann mit einem Zauberspruch. Einige Minuten später tauchte der Feind am Horizont auf, und der Zauberer begann, eine komplizierte Beschwörungsformel zu rezitieren.<new_line>„Valar morghulis, ommm … alarmis.“<new_line><new_line>Als er fertig war, sah ich einen weißen Blitz, und wir landeten an einem anderen Ort mit unseren Feinden.]],
				['7'] = [[„Abrakadabra, fertig. Ich hoffe, du hast wenigstens etwas gelernt und wirst keine Magie für Unsinn verschwenden.“<new_line><new_line>In der Zwischenzeit erreichten die Schurken den Perimeter und begannen ihren Angriff.]],
				['6'] = [[„Warum steht dein Mund offen? Gleich fliegt ein Käfer rein. Wähle den Zauberspruch, den ich auf dich sprechen soll.“]],
				['5'] = [[„Du hast heute großes Glück, du Idiot. Und das hast du nur Cybersanta zu verdanken. Passt du nicht auf? Siehst du den Feind nicht kommen?“<new_line><new_line>Ich schaute in die Ferne, und der Feind kam tatsächlich näher.]],
				['4'] = [[Ich sah mich um, aber mein Partner war immer noch verschwunden. Als ich mich jedoch umdrehen wollte, um zu Cybersanta zurückzukehren, materialisierte sich der Zaubernachtsmann hinter mir.]],
				['12'] = [[„Choombas! Das Konzert war klasse! Danke, dass du diese Cyberpsychos genullt hast.“]],
				['13'] = [[„Wenn du dein Kultivierungsniveau anhebst, brauchst du nicht um Schutz zu bitten! Du musst an dir arbeiten, anstatt Konzerte zu spielen.“]],
				['9'] = [[„Du hast dich nicht genug angestrengt. Wenn du es wirklich versucht hättest, dann würdest du mich jetzt verzaubern und nicht umgekehrt. Na gut, ich zeige dir einen neuen Trick. Wenn du so hart an dir arbeitest wie ich, bekommst du vielleicht den Dreh raus.<new_line>Ich kann Feinde mit uns teleportieren, wohin ich will, damit wir sie leichter bekämpfen können. Aber du entscheidest, wohin.“]],
				['8'] = [[„Du hast also doch etwas gelernt. Es stimmt, was man sagt: Weihnachten ist eine Zeit der Wunder.“]]
			},
			['choice'] = {
				['3'] = [[„Bis du mein Partner oder sowas?“]],
				['2'] = [[Zum Perimeter gehen]],
				['1'] = [[„Kein Problem.“]],
				['11'] = [[Angreifen.]],
				['7'] = [[Zum Treibstofflager.]],
				['6'] = [[„Hab‘s versucht.“]],
				['5'] = [[„Ich will fliegen wie ein Vogel.“]],
				['4'] = [[„Verstehe.“]],
				['12'] = [[„Gern geschehen.“]],
				['10'] = [[Zum dichten Wald.]],
				['9'] = [[Zum undurchdringlichen Sumpf.]],
				['8'] = [[Zum sicheren Vorposten.]]
			}
		},
		['newyearEvent_energy_easy'] = {
			['episode'] = {
				['10'] = [[„Aaaahaharrraharagaaaaaaaaaaaaaaaaaaah!“]],
				['14'] = [[Meine geistige Klarheit kehrte allmählich zurück, und ich begann, Cybersantas Silhouette zu erkennen.<new_line>„Steh auf, Samurai. Sieht aus, als wärst du zu hart aufgeschlagen. Aber das ist schon in Ordnung: Das haben wir alle schon erlebt, nachdem wir eine so tolle Show hingelegt haben. Nächstes Mal wirst du es schaffen.“]],
				['3'] = [[„Nein, Choom, wir haben keine Zeit, nach einem zu suchen. Setz dich auf den Hocker und nimm die Sticks.“]],
				['2'] = [[„Ganz ruhig, ich brauche nur einen Schlagzeuger für meine Band. Irgendwie ist es schwer, in eurer Welt Musiker zu finden.“]],
				['1'] = [[„Was los, Choomba? Also, bereit, mir zu helfen, diese Welt in Atome zu zerschlagen“]],
				['11'] = [[Der Rest der Band verstummte unauffällig, und ich kam in den Groove und begann einen wütenden, schweren Rhythmus zu spielen. Als ich fertig war, brüllte die Menge mit noch nie dagewesener Energie, und Cybersanta gab eine Zugabe.<new_line><new_line>„Agraaaaaaaaaaaaaaaaaaaaaaaaaaaaah!“]],
				['7'] = [[Die Menge begann zu jubeln, und Cybersanta fuhr fort.<new_line>„Agravaaaaaaaaaaaaah haaaaaaarrrrrrrrrrr!“]],
				['6'] = [[Cybersanta fuhr mit seiner Metallhand an den Saiten entlang und fing manisch zu wimmern an.<new_line>„Aaaaah grrrakhah aaaaaaaaaaah, graaaym haaaaaaaaaaa!“]],
				['5'] = [[Cybersanta ging zum Mikrofon und verkündete:<new_line>„Heute möchte ich euch einen besonderen Gast auf unserer kleinen Feier vorstellen. Begrüßt den einzigartigen, beliebten WEIHNACHTSMANN aus eurer Heimatstadt!!!“<new_line><new_line>Die Menge tobte.]],
				['4'] = [[„Es kommt nicht darauf an zu wissen, wie man spielt. Was zählt, ist, wie cool du bist. Hau einfach auf die Pauke und gib alles!“<new_line><new_line>Cybersanta trat an das Mikrofon heran.]],
				['12'] = [[Ich schlug mit aller Kraft auf das Schlagzeug, und es begann auseinanderzufliegen. Nachdem das Publikum uns ein letztes Mal mit Applaus bedacht hatte, schloss sich der Vorhang vor der Bühne. Cybersanta kam zu mir herüber<new_line><new_line>„Siehst du, Choomba? Und du hast dir Sorgen gemacht!“]],
				['13'] = [[„Es ist ein Weihnachtswunder. Danke, dass du eine gute Tat vollbracht hast. Wir haben die Stimmung der Menschen gehoben, und jetzt wird es für sie nicht mehr so elend sein, einen weiteren Tag zu leben.“]],
				['9'] = [[„Uuhgravahahaaa!“]],
				['8'] = [[Die Menge brüllte noch lauter.<new_line><new_line>„Agravaaaaaaaaaaaaaaah ahmragaaaaahhhhhhha!“]]
			},
			['choice'] = {
				['10'] = [[Solo]],
				['14'] = [[Ohnmächtig werden]],
				['3'] = [[„Aber ich bin kein Musiker.“]],
				['2'] = [[„Soll ich eins finden?“]],
				['1'] = [[„Ich bin mir nicht sicher.“]],
				['11'] = [[Trommeln kaputtmachen]],
				['7'] = [[Schwung]],
				['6'] = [[Anfangen die Trommeln zu schlagen]],
				['5'] = [[Das Publikum begrüßen]],
				['4'] = [[Hinter das Schlagzeug setzen]],
				['12'] = [[„Was war das? Ich weiß nicht, wie man spielt.“]],
				['13'] = [[„Bin froh, dass ich helfen konnte.“ (Weggehen)]],
				['9'] = [[Blastbeat]],
				['8'] = [[Tippen]]
			}
		},
		['boatman_2'] = {
			['episode'] = {
				['3'] = [[Ich der Tasche des Toten war ein Feuerzeug sowie ein alter sowjetischer Reisepass. Es war Wassili, der Freund des Schiffers.]],
				['2'] = [[Als ich eintrat, traf mich ein fürchterlicher Gestank. Die Wände waren blutgetränkt und alles war herumgeworfen worden. In einer entfernten Ecke lag ein entstellter Leichnam und neben ihm auf dem Boden ein Notizbuch.]],
				['1'] = [[Hier ist etwas passiert. Die Tür ist aufgebrochen und der Raum ist voller Muschelgehäuse und getrocknetem Blut.]],
				['4'] = [[Wassili muss auf das Signal, zum Wasser zu kommen, gewartet haben, als die Verrückten sein Haus betrat. Nach den Spuren und dem Chaos zu urteilen, das sie hinterließen, töten sie ihn und stahlen die Funkantenne.<new_line>Ich muss das dem Schiffer erzählen.]]
			},
			['choice'] = {
				['1'] = [[Das Haus betreten]],
				['2'] = [[Den Notizblock nehmen]]
			}
		},
		['reinforcement_quest_pt2'] = {
			['episode'] = {
				['3'] = [[Der Chef der Wache konnte es von meinem Gesicht ablesen.<new_line>„Du hast getan, was du konntest.“<new_line>Er gab mir meine Belohnung.]],
				['2'] = [[Ich habe kein bisschen an dir gezweifelt. Hier, das schulde ich dir.“<new_line>Der Chef der Wache gab mir eine Belohnung für meine Hilfe.]],
				['1'] = [[„Sag schon, wie ist es gelaufen?“]]
			},
			['choice'] = {
				['1'] = [[„Wir haben sie zerschmettert“]],
				['2'] = [[„Wir haben die Feinde besiegt, aber ...“]]
			}
		},
		['newyearEvent_time_medium'] = {
			['episode'] = {
				['10'] = [[Ich muss die Fallen demontieren. Das wird etwa 8 Stunden dauern.]],
				['14'] = [[„Tja, ich bin dann mal weg. Deine Welt braucht Revolutionen! Wir sehen uns später!“]],
				['3'] = [[„Ruh dich so lange aus, wie du brauchst, und dann fangen wir an. Die Arbeit wird etwa 10 Stunden dauern.“<new_line>]],
				['2'] = [[„Es ist ganz einfach. Ich habe einen Plan bekommen. Wir müssen die Bauarbeiten nach diesem Plan durchführen!“<new_line>]],
				['1'] = [[„Hallo, Genosse! Es gibt kein schlechtes Wetter, um zu arbeiten, oder? Eigentlich vermisse ich anstrengende Arbeit! Kannst du mir helfen?“]],
				['11'] = [[„Ich würde das gerne irgendwie nutzen, aber wir haben keine andere Wahl. Die Einwohner sind entweder weggegangen oder wurden gefangen genommen. Es gibt einfach niemanden, der dieses Versteck bewohnt – außer den Gaunern natürlich. Räum das bitte auf.“]],
				['7'] = [[Geschäfti berührte mit den Fingern seinen Kopf, und einen Moment später geschah ein Wunder: Ein Portal erschien, und der ebenso wütende Höhlennachtsmann trat heraus.<new_line>„Ah uuvaaah?!“<new_line>Höhli schien zu fluchen und schlug Bär mit seiner Keule.<new_line>]],
				['6'] = [[„Ich habe nicht die Absicht, meine Arbeit mit Ausbeutern zu diskutieren!“]],
				['5'] = [[Bevor ich etwas sagen konnte, hörte ich das Geräusch eines Motors hinter mir. Es handelte sich um ein kleines, altmodisches rotes Auto. Es kam zum Stehen, und der Geschäftsnachtsmann stieg mit einem wütenden Blick aus.<new_line>„Was hast du getan, du verdammter Marxist? Du weißt doch, von wem du den Bauplan hast, oder?“]],
				['4'] = [[Bär arbeitete sehr schnell. Während ich mit einem Schuppen beschäftigt war, baute er alles drumherum. Wir beendeten den Bau, aber das Ergebnis unserer Arbeit sah seltsam aus.<new_line>Bär baute Eisblöcke, einen Haufen Fallen und eine Feuerstelle für Rituale.<new_line>„'Proletarier aller Länder, bleibt warm!', wie man so schön sagt.“]],
				['12'] = [[Jetzt muss ich die Befestigungen abbauen. Das wird etwa 12 Stunden dauern.]],
				['13'] = [[Als ich meine Arbeit beendet hatte, klopfte mir jemand auf die Schulter.<new_line>„Hallo, Genosse! Ich bin gekommen, um mich dafür zu entschuldigen, dass du diesen ganzen Ort auseinandernehmen musstest … aber ich bin nicht in der Lage, irgendetwas kaputt zu machen. Außerdem habe ich mich gerade erst von dem Streik des Höhlennachtsmannes erholt. Erlaube mir, mein Abendessen mit dir zu teilen.“]],
				['9'] = [[„Leider, mein lieber Freund, müssen wir das alles wieder abbauen. Die Gauner haben diesen Plan Bär gesteckt, also hast du dieses Versteck für sie gebaut.“]],
				['8'] = [[„Uuh aaaaavaaaaah …“<new_line>Nach Höhlis Treffer konnten er und Bär einander verstehen. Es sah so aus, als würde sich Bär angeregt über irgendetwas bei Höhli beschweren. In ihr bizarres Gespräch vertieft, verließen sie das Dorf.<new_line><new_line>Geschäfti und ich blieben allein zurück.]]
			},
			['choice'] = {
				['10'] = [[Energydrink trinken]],
				['3'] = [[„Klingt einfach und klar.“]],
				['2'] = [[„Ich habe keine Zeit.“ (Gehen)]],
				['1'] = [[„Was sollen wir machen?“]],
				['11'] = [[„Tschüß.“]],
				['7'] = [[„Können wir das nicht irgendwie nutzen?“]],
				['6'] = [[„Was soll ich jetzt tun?“]],
				['5'] = [[„Was in aller Welt hast du …“]],
				['4'] = [[An die Arbeit machen (10 Stunden)]],
				['12'] = [[An die Arbeit machen (14 Stunden)]],
				['13'] = [[An die Arbeit machen (16 Stunden)]],
				['9'] = [[Kuchen essen]],
				['8'] = [[An die Arbeit machen (12 Stunden)]]
			}
		},
		['supply_quest'] = {
			['episode'] = {
				['5'] = [[„Okay, hast du alles bekommen, was du brauchtest?“]],
				['4'] = [[Der Mann zeigte mir ein verächtliches Grinsen.<new_line>„Behalte diesen Müll und verschwinde.“]],
				['3'] = [[„Ein echter Pfennigfuchser, hmm! Du hast Glück, dass ich mich heute gut fühle.“]],
				['2'] = [[Der Mann musterte mich misstrauisch, schaute dann lange und genau in die Kiste.<new_line>„Dafür werde ich nicht den vollen Preis bezahlen.“]],
				['1'] = [[Der Mann warf einen Blick in die Kiste.<new_line>„Es ist ein Vergnügen, mit dir Geschäfte zu machen.“]]
			},
			['choice'] = {
				['3'] = [[Um die volle Summe bitten]],
