		['toy_story'] = {
			['name'] = [[Terem]],
			['episode'] = {
				['3'] = [[Eine Stunde später haben wir aufgeräumt und herausgefunden, dass das Terem von Eisbriganten angegriffen wurde. Sie haben zwar alles Mögliche zerschlagen, aber nichts mitgenommen. Als ich eintraf, hielten die Spielzeuge mich für einen der Briganten. Sneguroschka verspricht herauszufinden, wo die Briganten hergekommen sind.]],
				['2'] = [[„Halt!“, ruft Sneguroschka, als sie ins Terem rennt. Ich bin von lauter kaputten Spielzeugen umgeben, und der Nussknacker erstarrt in der Bewegung, den Säbel zum Angriff erhoben.]],
				['1'] = [[Die Tür zum reich geschmückten Terem ist eingeschlagen, und drinnen herrscht Chaos. Ich habe mich noch gar nicht gesammelt, da kommt schon eine merkwürdige Stimme hinter einem umgeworfenen Tisch hervor: „Da sind sie wieder! Schnell, Kanone laden! Zielen! Feuer!“ Dann gibt es einen Knall, und ein kleines Projektil zischt an meinem Ohr vorbei. Was ist hier los?]]
			}
		},
		['new_year_search_fight'] = {
			['choice'] = {
				['1'] = [[„Wie soll ich das geklaut haben?“]],
				['2'] = [[Den Weihnachtsschmuck rüberreichen]]
			},
			['episode'] = {
				['3'] = [[„Oooh, ja! Gescheeeeenkeee! Hat jemand ein Streichholz? Los geht's!“<new_line><new_line>Die Schurken vergaßen mich völlig und gingen.]],
				['2'] = [[„Leg dich nicht mit mir an! Wir bleiben hier, bis du uns unseren Anteil am Schmuck gibst. Und du auch!“]],
				['1'] = [[In einem der Räume bin ich auf eine Gruppe von Gaunern gestoßen. Ich glaube, die suchen auch etwas. Einer von ihnen zischte mir zu:<new_line>„Hey Mann, wo ist der ganze Weihnachtsschmuck? Hast du schon alles geklaut?!“]]
			}
		},
		['newyearEvent_craft_easy'] = {
			['episode'] = {
				['3'] = [[Ich hob das Geschenk auf und wir gingen zu einem der Häuser im Dorf. Geschäfti klopfte an die Tür.<new_line>„Ho ho ho!“]],
				['2'] = [[„Oh, wie interessant! Oh, wie schön … Warum gehen wir nicht los und liefern dieses Geschenk gemeinsam aus? Ich glaube, der Empfänger wird sich sehr freuen, den Handwerker zu sehen, der das gemacht hat.“]],
				['1'] = [[„Ho ho ho! Hallo, lieber Freund! Ich brauche deine Hilfe. Die Sache ist die, dass nicht nur die Kinder Geschenke verdienen, sondern auch einige Erwachsene. Aber meine Fabrik kann keine Geschenke herstellen, die für die Erwachsenen hier geeignet sind. Ich habe weder die Entwürfe noch die Materialien. Kannst du etwas für mich herstellen?“]],
				['7'] = [[Die Frau riss schnell das festliche Geschenkpapier ab und öffnete das Geschenk.<new_line>„Willst du mich auf den Arm nehmen? Danke, natürlich, aber ich glaube, du gehst besser, bevor mein Hund aufwacht.“]],
				['6'] = [[„Oh, das ist für mich? Was ist es?“]],
				['5'] = [[„Ich wollte dir den neuen Weihnachtsmann in eurer Gegend vorstellen. Er hat dir ein kleines Geschenk mitgebracht. Er wird hier für Magie und Wunder zuständig sein.“]],
				['4'] = [[Eine Frau öffnete die Tür.<new_line>„Wer ist da? Ach, es … Ich habe dir schon tausendmal gesagt, dass ich nicht in Ihre Fabrik komme!“]],
				['9'] = [[„Es gab einen kleinen Fehler bei dem Geschenk, aber ich lerne immer noch etwas über eure Welt, und ich vermisse meine treuen Elfen hier sehr. Beim nächsten Mal werde ich über die Fehler nachdenken, und wir werden ein besseres Geschenk machen. Aber du hast trotzdem eine sehr gute Tat vollbracht!“]],
				['8'] = [[Die Frau riss schnell das festliche Geschenkpapier ab und öffnete das Geschenk.<new_line>„Oh, ist das wirklich für mich? Wie schön. Velen Dank. Vielleicht habe ich mich in dir getäuscht. Ich werde über den Job nachdenken.“]]
			},
			['choice'] = {
				['6'] = [[Das Haus verlassen]],
				['5'] = [[Das Geschenk überreichen]],
				['4'] = [[„Nein, danke, ich muss los.“ (Weggehen)]],
				['3'] = [[„Okay, los geht‘s!“]],
				['2'] = [[„Ich werde sehen, was sich machen lässt.“ (Weggehen)]],
				['1'] = [[„Hier, nimm das.“]]
			}
		},
		['newyearEvent_fight_hard'] = {
			['choice'] = {
				['7'] = [[Das Holz anzünden]],
				['6'] = [[Den Sprengstoff anbringen]],
				['5'] = [[Feuerholz platzieren]],
				['4'] = [[Einen Plan ausarbeiten (8 Stunden)]],
				['3'] = [[Nach unten gehen]],
				['2'] = [[Die Festung erkunden (12 Stunden)]],
				['1'] = [[Den Schlüssel benutzen]],
				['8'] = [[Die Vorräte nehmen und abhauen]]
			},
			['episode'] = {
				['3'] = [[Als ich jeden Winkel der Festung erkundete, fand ich eine unauffällige Tür. Dort befand sich eine lange Treppe, die in den Untergrund führte.]],
				['2'] = [[Mit einem lang anhaltenden Rumpeln öffneten sich die Tore, und die Festung empfing mich mit eisiger Stille. Jetzt muss ich sie erkunden, um herauszufinden, wie ich sie am besten zerstören kann.]],
				['1'] = [[In den Weiten dem schneebedeckten Ödland befindet sich eine beachtliche Festung der Gauner. Ein riesiges, eisiges Schloss hängt an ihren Toren. Es scheint, dass die Bewohner dieser Festung gerade nicht zu Hause sind.<new_line>Es würde sich lohnen, diesen Ort zu zerstören, damit die Gauner nächstes Jahr nicht zurückkehren. Allerdings ist es nicht so einfach, hineinzukommen – man braucht einen Schlüssel.]],
				['7'] = [[Hinter den Mauern der Festung brummten die Motoren. Offenbar hat jemand herausgefunden, dass ich hier eingebrochen bin. Sieht aus, als hätte ich einen Kampf vor mir.]],
				['6'] = [[Diese Festung besteht aus Eis, also wäre die beste Art, sie zu zerstören, ein Feuer zu legen. Ich werde die oberen Etagen mit Brennholz füllen und bringe im Untergeschoss Sprengstoff an.]],
				['5'] = [[Nachdem ich die Wachen ausgeschaltet hatte, erkundete ich den Raum und fand eine weitere Geheimtür. Sie hat kein einziges Schlüsselloch und ist fest verschlossen.<new_line>Am besten sprenge ich die Tür in die Luft – und mit ihr alle Stockwerke darüber. Aber zuerst muss ich planen, wie ich das anstellen soll.]],
				['4'] = [[Im Untergeschoss bin ich dann doch auf die Bewohner der Festung gestoßen. Sie scheinen etwas Wertvolles zu bewachen.]],
				['9'] = [[Das trockene Holz brannte lichterloh. Das Feuer erreichte schnell den Sprengstoff, und die Festung ging in Flammen auf.<new_line>Nach einer Weile legte sich das Feuer, und ich konnte zum geheimen Eingang hinuntergehen. Wie ich erwartet hatte, war die Tür weggefegt worden – mein Weg zum Lager der Gauner war frei.]],
				['8'] = [[Endlich ist alles für die Zerstörung bereit. Jetzt muss ich nur noch das Holz verbrennen.]]
			}
		},
		['people_bonfire'] = {
			['choice'] = {
				['3'] = [[Zigaretten teilen]],
				['2'] = [[Tarnkleidung anlegen, dich heranschleichen und sie belauschen]],
				['1'] = [[Dich ihnen nähern]]
			},
			['episode'] = {
				['5'] = [[Dank meiner Tarnung konnte ich mich nahe an sie heranschleichen und sie belauschen. Sie sprachen über ihr Versteck, das sich in der Nähe befinden soll. Ich glaube, ich kann es finden.]],
				['4'] = [[Die Fremden zündeten sich Zigaretten an und erzählten mir ein paar unterhaltsame Geschichten von ihren Reisen, aus denen ich einiges lernen konnte. Ein paar Stunden später verabschiedete ich mich und machte mich wieder auf den Weg.]],
				['3'] = [[Wir tauschten Neuigkeiten aus, die wir gehört hatten, doch das Gespräch führte nirgendwohin, daher beschloss ich, mich wieder auf den Weg zu machen.]],
				['2'] = [[Die Fremden waren sichtlich überrascht, mich zu sehen, doch sie bemerkten, dass von mir keine Bedrohung ausgeht, und haben mich eingeladen, mich zu ihnen zu setzen.]],
				['1'] = [[Als ich durchs Ödland ging, ist mir aufsteigender Rauch aufgefallen. Kurz darauf kam ich an ein Lagerfeuer, an dem Menschen saßen und sich unterhielten.]]
			}
		},
		['newyearEvent_energy_medium'] = {
			['episode'] = {
				['3'] = [[„Ich bin mir zu 100 % sicher! Wettbewerb ist das höchste Gut. Unsere Zivilisation ruht auf Säulen. Und woher sollen wir wissen, wer die Säule ist und wer der Neuankömmling, wenn wir nicht unser Bestes tun, um miteinander zu konkurrieren? Entweder sie gewinnen und beweisen, dass sie Recht haben, oder wir tun es! Also, sind Sie bereit?“]],
				['2'] = [[„Gauner. Unsere Fabrik wirbt mit Weihnachten und Wundern, und sie sind dagegen. Sie sind also unsere direkten Konkurrenten, und wir müssen uns um sie kümmern. Bist du bereit, die Gerechtigkeit wiederherzustellen?“]],
				['1'] = [[„Ho ho hallo, mein lieber Freund! Unsere Fabrik wird von einer Reihe von Problemen geplagt, und ich habe endlich herausgefunden, woran es liegt. Unsere verdammten Konkurrenten mischen sich in unseren Betrieb ein! Kannst du mir helfen, ihre Fabriken ein wenig zu sabotieren?“]],
				['7'] = [[Als wir mit dem Verlegen der Minen fertig waren, gingen wir weiter und stießen auf einen Raum mit Weihnachtsschmuck.<new_line>„Oh, was für ein schönes Geschenk! Gut, dass wir hierher gekommen sind. Schmeiß alles in Säcke.“<new_line>Mit diesen Worten holte Geschäfti zwei rote Säcke heraus.]],
				['6'] = [[Nachdem wir die Fahrzeuge zertrümmert hatten, erkundeten wir den Eisblock weiter. Im nächsten Raum wurde der Treibstoff gelagert.<new_line>„Toll, hier können wir Minen legen! Nimm die Pyrotechnik.“<new_line>Geschäfti warf mir eine Schachtel mit Feuerwerkskörpern zu.]],
				['5'] = [[Es stellte sich heraus, dass es in dem Eisblock mehrere versteckte Eingänge gab, und wir betraten ihn durch einen. Wir schienen in einer Garage mit verschiedenen Fahrzeugen zu landen.<new_line>„Nimm einen Vorschlaghammer und mach hier alles kaputt!“]],
				['4'] = [[Wir mussten ein paar Meilen laufen, bis wir neben einem großen Eisblock landeten. Geschäfti sprach flüsternd zu mir.<new_line>„Folge mir. Oh, und hier ist eine Portalschriftrolle für dich. Wenn du merkst, dass du es nicht schaffst, lies die Schriftrolle, und sie wird dich hier rausbringen.“]],
				['11'] = [[Ich nahm die Schriftrolle heraus und begann sie zu lesen. Als Geschäfti mich sah, begann er schnell, dasselbe zu tun. Nachdem ich mit dem Lesen fertig war, sah ich einen hellblauen Blitz und fand mich einige Sekunden später in eines Ödland wieder.]],
				['10'] = [[Nachdem wir uns in sicherer Entfernung vom Eisblock entfernt hatten, nahm Geschäfti den Zünder heraus und drückte ihn. Es gab einen lauten Knall.<new_line>„Das war's! Wir haben einen guten Job gemacht und eine gute Tat vollbracht! Indem wir unsere Konkurrenten besiegen, werden wir die Fabrik zum Blühen bringen!“]],
				['9'] = [[Als wir mit dem Einsammeln der Ornamente fertig waren, warfen wir uns die Säcke über die Schultern und machten uns auf den Weg zum Ausgang. Es stellte sich jedoch heraus, dass uns eine Bande von Gaunern den Weg versperrte. Nachdem wir um die Ecke in Deckung gegangen waren, flüsterte Geschäfti:<new_line>„Wir können einen anderen Ausgang finden, aber das wird schwierig, denn die Säcke sind schwer. Oder sollen wir uns um diese Gruppe kümmern und direkt durchgehen?“]],
				['8'] = [[„Keine Sorgen, mein lieber Freund. Ich schenke den Menschen schon seit vielen Jahren Freundlichkeit und Wunder. Ich weiß sehr wohl, was richtig und was falsch ist. Nimm einen Sack und wirf den Schmuck hinein.“]]
			},
			['choice'] = {
				['3'] = [[„Das sind böse Jungs … Aber bist du sicher, dass sie hinter den Problemen in der Fabrik stecken?“]],
				['2'] = [[„Ich bin bereit.“]],
				['1'] = [[„Welche Konkurrenten? Wie sind sie hierher gekommen?“]],
				['7'] = [[Minen im Raum legen]],
				['6'] = [[Durch ein Portal fliehen]],
				['5'] = [[Alles mit einem Vorschlaghammer zerstören]],
				['4'] = [[Geschäftsnachtsmann folgen]],
				['10'] = [[Einen anderen Ausgang finden]],
				['9'] = [[„Ist Diebstahl falsch?“]],
				['8'] = [[Die Ornamente in den Sack tun]]
			}
		},
		['lair3'] = {
			['episode'] = {
				['1'] = [[Die Hexe zu finden, war nicht schwierig. Hoffentlich wird der Kampf gegen sie genauso einfach.]]
			}
		},
		['order_delivery_quest'] = {
			['episode'] = {
				['1'] = [[Ich fand den Lagerverwalter und er zeigte mir ohne viel Aufhebens das Frachtgut.]],
				['2'] = [[Ich fand den Lagerverwalter. Er sah recht unglücklich aus. Er zeigte mir das Frachtgut und fragte:<new_line>„Glaubst du, dass wir dir das ganze Zeug kostenlos geben?“]]
			},
			['choice'] = {
				['3'] = [[Bezahlung verweigern, dann verschwinden]],
				['1'] = [[Das Frachtgut schnappen und verschwinden]],
				['4'] = [[„Hier ist das Geld. Zufrieden?“]]
			}
		},
		['halloween_boss_2022_2_2'] = {
			['choice'] = {
				['7'] = [[„Na dann los.“]],
				['6'] = [[„Anti-Strahlungsmedizin.“]],
				['5'] = [[„Einen Schlaftrank.“]],
				['4'] = [[„Ein Fäulnisgift.“]],
				['3'] = [[„Ein Wahrheitsserum.“]],
				['2'] = [[„Eine Salbe für Stinkschnäbel“.]],
				['1'] = [[In Richtung der Blitze gehen]]
			},
			['episode'] = {
				['3'] = [[„Nun, wen haben wir denn da? Du bist wirklich ein seltsames Exemplar ... Lass uns ein wenig reden, bevor ich mich mit dir befasse.“]],
				['2'] = [[Der Pestarzt hat sich zwischen zwei offenen Gräbern niedergelassen und kippt etwas in einen brodelnden Kessel. Es ist an der Zeit, dem Ganzen ein Ende zu setzen.]],
				['1'] = [[In der hinteren Ecke des Friedhofs leuchten die Grabsteine mit grünen Blitzen auf – das muss der Ort sein, an dem der Pestarzt seine abscheulichen Experimente durchführt.]],
				['7'] = [[„Richtig. Sie bilden einen Schlaftrunk, der so stark ist, dass er als ‚Schluck des lebenden Todes‘ bekannt ist. Du bist schlauer, als ich dachte ... Ich kann es kaum erwarten, dein Gehirn zu sezieren – und genau das werden wir jetzt auch tun!“]],
				['6'] = [[„Nein. Du weißt es nicht. Nun, wir werden es ein anderes Mal versuchen ... und jetzt wirst du sehen, was passiert, wenn du mir dumme Antworten gibst.“<new_line><new_line>Der Pestarzt warf etwas in seinen Kessel, und der Dampf, der daraus hervorkam, wurde noch dicker. Als sich der Dampf verzogen hatte, war der Doktor nirgends mehr zu sehen, und ich war von Zombies umgeben.]],
				['5'] = [[„Ich habe einige meiner Tränke aufgebraucht, also braue ich jetzt neue. Sag mir, was würde ich bekommen, wenn ich pulverisierte Asphodelwurzel mit einem Aufguss aus Wermut mische?“]],
				['4'] = [[Der Arzt schüttelte enttäuscht den Kopf, als ich nach meiner Waffe griff. Er winkte mit der Hand und verschwand in dem grünen Nebel, der ihn einhüllte. Ich werde es noch einmal versuchen müssen, wenn er auf den Friedhof zurückkehrt.]],
				['9'] = [[„Und ich dachte schon, du wärst überfordert! Nicht schlecht, nicht schlecht ... Und ich habe hier deinen nächsten Vertrag.“]],
				['10'] = [[„Ach, du bist es wieder. Nun denn, können wir zu unserem Gespräch zurückkehren?“]],
				['8'] = [[„Es macht so viel Spaß, dir zuzusehen, Sterblicher! Benutze deinen Verstand und komm später wieder – der Arzt ist hier für eine lange Zeit versorgt ...“]]
			}
		},
		['newyearEvent_fight_medium'] = {
			['episode'] = {
				['3'] = [[Ich bin der Umzingelung entkommen, aber ich muss noch kämpfen.]],
				['2'] = [[Während ich den Stützpunkt räumte, gelang es den Gaunern, Alarm zu schlagen, und ihre Verstärkung kam. Schließlich war ich umzingelt.]],
				['1'] = [[In einiger Entfernung von der Zivilisation befindet sich ein riesiger Eisblock, der den Gaunern als Unterschlupf dient. Hier führen sie Rituale durch und nehmen ihre gestohlenen Gegenstände mit.<new_line>Wenn ich die Bewohner des Eisblocks beseitige, werden die Raubüberfälle in den Dörfern für eine Weile aufhören. Aber die Gauner werden ihre Basis nicht einfach so verlassen.]],
				['4'] = [[Ich war nicht in der Lage, aus der Umzingelung zu entkommen, also muss ich unter den derzeitigen Bedingungen kämpfen.]]
			},
			['choice'] = {
				['6'] = [[Kämpfen]],
				['5'] = [[Versuchen, der Umzingelung zu entgehen]],
				['3'] = [[Kanalisation]],
				['2'] = [[Linker Kontrollpunkt]],
				['1'] = [[Rechter Kontrollpunkt]]
			}
		},
		['embacity_biolab_4'] = {
			['episode'] = {
				['1'] = [[Ich bemerkte nur eine Gestalt in dieser Halle. Aus dieser Entfernung sah sie ziemlich menschlich aus, aber ihre Bewegungen hatten etwas erschreckend Unnatürliches.<new_line>Die Gestalt ging in der Halle umher und überprüfte die dortigen Autoklaven. Als sie mich bemerkte, bereitete sie sich auf einen Angriff vor.]]
			}
		},
		['lair_chiken'] = {
			['episode'] = {
				['7'] = [[Obwohl der Hahnenschrei nicht nachließ, nahm ich mich zusammen und nahm die Hände von den Ohren.<new_line>Das änderte gar nichts: Der Schrei schien direkt aus meinem Kopf zu kommen. Verunsichert drehte ich mich um und erblickte sogleich eine ganze Bande dieser Kreaturen, die auf mich zurannten.]],
				['6'] = [[Gleich nachdem ich den ersten Schritt gemacht hatte, hörte ich einen ohrenbetäubenden Hahnenschrei. Ich verschloss meine Ohren, aber das half nichts. Der durchdringende Schrei wurde dadurch nicht leiser.]],
				['5'] = [[Ich trat vorsichtig den Rückzug an, ohne die Farm aus den Augen zu lassen. Niemand zeigte sich.]],
				['4'] = [[Nach dem Kampf erkundete ich jeden Winkel der Farm. Die blutigen Klumpen schienen das Produkt seiner Bewohner zu sein, die glücklose Wanderer anlocken und verspeisen.<new_line>Diese Kreaturen sind vielleicht nicht wendig, aber sehr hinterlistig. Warum von allen Mutanten gerade die Hühner einen solchen Jagdinstinkt entwickelt haben, wird wohl für immer ungeklärt bleiben.]],
				['3'] = [[Sobald ich das Gelände der Farm betrat, erschienen von allen Seiten mutierte Hühner. Hätten sie nicht so aggressiv ausgesehen, wäre es fast lustig gewesen. Es war bereits zu spät, umzukehren.]],
				['2'] = [[Als ich mich näherte konnte ich keine Anzeichen von Leben erkennen.]],
				['1'] = [[Nachdem sie den Bombenhagel teilweise überstanden hat, sieht die Farm von weitem friedlich aus. Wären da nicht die blutroten Klumpen, die überall zu sehen sind, würde sie sich für ein Nickerchen anbieten.<new_line>Die Anrainer, so scheint es, haben Leine gezogen, nachdem sie ein frisches Opfer ausgemacht haben.]],
				['8'] = [[Mein Ablenkungsmanöver funktionierte und die Kreaturen zögerten. Meine Chance, wegzulaufen, bevor der Hahn wieder krähte.]]
			},
			['choice'] = {
				['3'] = [[Verjagen]],
				['2'] = [[Deine Hände von deinen Ohren nehmen]],
				['1'] = [[Nahe herangehen]],
				['4'] = [[Fliehen]]
			}
		},
		['easter_rabbit'] = {
			['episode'] = {
				['21'] = [[„Nun ...“]],
				['29'] = [[„Ich bin jetzt nur noch ein ganz normaler Mensch, und was vorher passiert ist, ist nicht mehr wichtig. Wenn du noch mehr Körbe hast, bring sie her – die Festtage sind noch nicht vorbei. Ich habe in meinem Laden auch noch etwas, das ich dir gerne geben würde.“]],
				['3'] = [[„Du bist wohl etwas schwer von Begriff. Was könnte ein rosa Hase wohl wollen? Natürlich bemalte Eier.“]],
				['2'] = [[„Wer ich bin? Was ist das denn bitte für eine Frage?! Ich bin der rosa Hase, zu deinen Diensten.“]],
				['1'] = [[„Hey, Kumpel. Ich brauche deine Hilfe, komm mal schnell her!“]],
				['28'] = [[„Tja, sieht so aus, als hättest du genug Körbe mitgebracht und langsam gehen mir die Kunden aus. Das heißt, du hast dir eine Belohnung verdient und ich lege sogar noch etwas oben drauf. Und ich habe mir meinen wohlverdienten Lohn gesichert.“]],
				['7'] = [[„Ich schenke dir meinen Hasenanzug.“]],
				['6'] = [[„Du gehst mir mit deinem sinnlosen Hinterfragen auf den Geist! Schalt doch mal dein Hirn ein! Ostern steht vor der Tür, und an Ostern verteilt man Eier. Ich habe ein Hasenkostüm gebastelt und plane, es zu vermarkten.<new_line>Ich werde reiche Familien besuchen und ihnen Geschenke bringen, um Profit zu machen. Deine Aufgabe ist es, die Eier zu bemalen und Geschenkkörbe zu basteln.“]],
				['5'] = [[„Warum muss ein Pinguin aufs Klo? Man hat mich aus dem Hasenministerium rausgeworfen und ich will ihr Vertrauen wieder verdienen und wieder lieb und flauschig werden.“]],
				['4'] = [[„Ernster als ernst.“]],
				['22'] = [[„Ich habe gehört, dass auf den verlassenen Farmen im Ödland Menschen verschwinden. Angeblich sind Mutanten-Hühner dafür verantwortlich. Du könntest ihnen einen Besuch abstatten, die Hühner haben bestimmt ein paar Eier.“]],
				['23'] = [[„Halte im Ödland oder im radioaktiven Ödland nach ihnen Ausschau, dort stößt du manchmal auf Hühner. Und wo Hühner sind, gibt es auch Eier.“]],
				['9'] = [[„Ich gebe dir den Anzug trotzdem, aber nur, wenn du es schaffst, mir die richtige Anzahl von Körben zu bringen, um den Bedarf zu decken. Ich habe mir sehr viel Mühe gegeben, diesen Anzug zu machen.“]],
				['8'] = [[„Keine Ahnung, aber so etwas bekommst du im Ödland nicht noch einmal, der ist selten. Ich sehe schon, wie sich die Rädchen in deinem Kopf zu drehen beginnen. Ich könnte auch einen Teil des Gewinns für jeden Korb mit dir teilen?<new_line>Außerdem habe ich noch ein paar andere seltene Dinge, die ich dir geben könnte.“]],
				['17'] = [[„Das ist gut. Zeig mir, was du hast.“]],
				['16'] = [[„Was hast du noch?“]],
				['15'] = [[„Es gibt keine besonderen Bedingungen, ich kaufe sie einfach zu einem guten Preis, besser als das, was der Junkie anbietet. Wenn du genug verkaufst, gebe ich dir eine Belohnung. Der Preis ist sowieso gut, also behalte das im Hinterkopf.“]],
				['14'] = [[„Hör mal, warum willst du für diesen Psycho arbeiten, hm? Vielleicht solltest du mir die bemalten Eier verkaufen, ich sorge dafür, dass der Preis stimmt. Die Nachfrage ist groß. Und niemand wird diesen Junkie in sein Haus lassen.“]],
				['24'] = [[„Du könntest sie zum dreifachen Preis bei einem Händler kaufen, aber tu das nur, wenn du sie nirgendwo sonst findest.“]],
				['25'] = [[„Geh zu irgendeiner verlassenen Stadt und durchsuche alles. Dort findest du Kaninchenlöcher, grabe da und hole die Farbe. Vergiss aber nicht, eine Schaufel mitzunehmen.“]],
				['26'] = [[„Wo kommt die Milch in den Kühen her? Wenn du keine dummen Fragen stellst, musst du dir auch keine dummen Antworten anhören.“]],
				['27'] = [[„Ich habe mal im Gefängnis gesessen. Ich war zweimal verheiratet. Dann wurde ich zur Armee eingezogen und musste zweieinhalb Jahre am Arsch der Welt in Kasachstan verbringen, ohne jeglichen Grund.<new_line>Ich hatte einen Gesichtsbruch, mir wurde eine Niere entfernt und ich habe einen Knochensplitter in meinem Knöchel, der nie wieder heilen wird. Ich habe in meinem Leben schon alles Mögliche gesehen ...“]],
				['30'] = [[„Nicht mal Fliegen fliegen gratis. Wenn du mir etwas bringst, werde ich einfach etwas Wohltätigkeit betreiben und es an die Leute verteilen. So kannst du eine gute Tat vollbringen.“]],
				['20'] = [[„Oh, gut, hier ist dein Anteil. Hast du noch mehr?“]],
				['19'] = [[„Gut gemacht, ich bin zufrieden, hier ist dein Anteil. Hast du noch mehr?“]],
				['18'] = [[„Da hättest du auch gleich etwas Schrott mitschleppen können. Hier ist dein Anteil. Hast du noch mehr?“]],
				['12'] = [[„Dann hör auf, hier herumzustehen und erledige den Auftrag, die Kunden stehen schon Schlange.”]],
				['13'] = [[„Psst, komm doch mal kurz her, ja?”]],
				['10'] = [[„Hier hast du eine Schaufel. Geh durch die Städte, such nach Kaninchenlöchern in den verlassenen Gebäuden und grabe. Dort wirst du die Farbe finden.“]],
				['11'] = [[„Das musst du selbst rausfinden. Du könntest sie kaufen. Aber die Nachfrage ist im Moment so hoch wie die Preise. Du könntest auch an anderen Orten nach ihnen suchen.“]]
			},
			['choice'] = {
				['3'] = [[„Meinst du das ernst?“]],
				['2'] = [[„Also, was willst du von mir, rosa Hase?“]],
				['1'] = [[„Wer bist du überhaupt?“]],
				['7'] = [[„Warum sollte ich das wollen?“]],
				['6'] = [[„Warum sollte mich das interessieren?“]],
				['5'] = [[„Echt jetzt?“]],
				['4'] = [[„Warum brauchst du denn bemalte Eier?“]],
				['23'] = [[„Ich wollte noch etwas fragen.“]],
				['9'] = [[„Na schön. Aber wo finde ich Eier und Farbe?“]],
				['8'] = [[„Schon besser.“]],
				['17'] = [[Gib ihm die Körbe]],
				['16'] = [[„Ich bin nur auf der Durchreise.“ (Gehen)]],
				['15'] = [[„Ich habe ein paar Fragen.“]],
				['14'] = [[„Ich will dir ein paar Körbe geben.“]],
				['24'] = [[„Warum soll da Farbe in den Löchern sein? Und was sind das überhaupt für Löcher?“]],
				['25'] = [[„Könntest du vielleicht mit offenen Karten spielen? Wer bist du?“]],
				['26'] = [[„Kannst du mir das nicht gratis geben?“]],
				['22'] = [[„Ich habe keine weiteren Fragen.“]],
				['21'] = [[„Erzähl mir von dir.“]],
				['20'] = [[„Sag mir doch noch mal, wo ich die Farbe finde.“]],
				['19'] = [[„Ich finde keine Eier.“]],
				['18'] = [[„Das wars für den Augenblick.“]],
				['12'] = [[„Wie lauten die Bedingungen?“]],
				['13'] = [[„In Ordnung.“ (Gehen)]],
				['10'] = [[„Und die Eier?“]],
				['11'] = [[„Verstanden.“]]
			}
		},
