		['camp_defence_quest'] = {
			['episode'] = {
				['10'] = [[Als ich ein Geräusch hörte, versteckte ich mich. Ein paar Fremde wanderten um das Lager.<new_line>Ich war zu spät. Die Räuber hatten das Lager bereits übernommen.]],
				['3'] = [[Am Treffpunkt sah ich nur eine Leiche auf dem Boden.]],
				['2'] = [[Als ich am Außenposten ankam, hörte ich Gewehrfeuer. Es gab keinen Zweifel: Die Räuber hatten das Lager bereits angegriffen.]],
				['1'] = [[Ein bewaffneter Mann steht am Außenposten:<new_line>„Kommst du zu Hilfe? Okay, begib dich in Position. Sie sind schon unterwegs.“]],
				['11'] = [[Die Räuber sind letztendlich verschwunden. Ich habe das Lager durchsucht, fand aber nur die Leichen unserer Verbündeten.]],
				['7'] = [[Wir haben alles unter Kontrolle. Wir haben die Stellung gehalten. Niemand wird uns so schnell wieder ärgern.]],
				['6'] = [[Dort sind die Räuber. Eine Menge Räuber ...<new_line>Aber wir sind auch eine Menge. Und wir sind bereit!]],
				['5'] = [[Ich habe den perfekten Ort für eine Wolfsgrube neben einem umgestürzten Baum gefunden und konnte sie ziemlich schnell ausheben.]],
				['4'] = [[Ich hängte den Stamm mit Pflöcken erfolgreich auf.]],
				['12'] = [[Ich konnte diesen Menschen nicht helfen, aber ich werde sie rächen.]],
				['13'] = [[Wenn du der Karte vertraust, komme ich nahe ans Lager heran.]],
				['9'] = [[Aber insgesamt ist es nicht so schlecht. Sie werden sich erholen, es ist nur eine Frage der Zeit.]],
				['8'] = [[Wir haben es geschafft, die Stellung zu halten, aber einige unserer Kämpfer wurden schwer verwundet.]]
			},
			['choice'] = {
				['7'] = [[Eine Wolfsgrube graben]],
				['16'] = [[Die Leiche untersuchen]],
				['18'] = [[Warten, bis die Räuber verschwinden]],
				['14'] = [[Mitkämpfen]],
				['17'] = [[Die gefallenen Kameraden rächen]],
				['9'] = [[In Position gehen und warten]],
				['10'] = [[Unser Lager verteidigen]],
				['8'] = [[Umschauen, was es noch zu tun gibt]]
			}
		},
		['embacity_biolab_7'] = {
			['episode'] = {
				['1'] = [[Der unterirdische Boden dieser Bio-Station diente einst als Kontrollraum. Aber jetzt ist der Boden hier löchrig, Wurzeln ragen über die Computerantennen hinaus, und auf den Tischen türmen sich Haufen von Biomasse.<new_line>In der Mitte des Raums, auf dem Schaltschrank, bemerkte ich eine Bewegung. Sofort surrten die Computer, und Biomutanten traten aus den Schatten der Wand hervor.]],
				['2'] = [[Das Summen der Computer und des Schaltschranks in der Mitte verstummte. Der Myzelklumpen zeigte keine Anzeichen von Leben. Wer weiß, was es war, sollen sich die Wissenschaftler damit befassen. Ich habe alles getan, was ich konnte, jetzt muss ich ihnen nur noch die Blackbox bringen.]]
			}
		}
