				['6'] = [[Combattre]]
			},
			['episode'] = {
				['4'] = [[Je n'ai pas réussi à me dégager, je vais devoir me battre dans ces circonstances.]],
				['1'] = [[À l'écart de toute trace de civilisation se trouve un énorme bloc de glace servant de repaire aux vauriens. Tous leurs rituels sont menés ici, et tous les objets volés sont stockés ici.<new_line>Si j'élimine ceux qui se trouvent à l'intérieur, cela mettra fin aux vols et cambriolages dans les villages pendant quelques temps. Mais les vauriens ne quitteront pas leur base comme ça.]],
				['2'] = [[J'étais en train de nettoyer la base quand des vauriens ont réussi à donner l'alerte, qui précéda l'arrivée de renforts. J'étais encerclé.]],
				['3'] = [[J'ai réussi à me dégager, mais je dois encore combattre.]]
			}
		},
		['portal'] = {
			['episode'] = {
				['1'] = [[Droit devant, j'aperçois une grotte dont l'entrée, façonnée à même d'énormes blocs de pierre, est enveloppée d'une épaisse brume.]],
				['2'] = [[À peine ai-je posé un pied dans la grotte que l'épaisse brume se referme derrière moi, m'isolant complètement du monde extérieur. Que devrais-je faire ?]],
				['3'] = [[Les yeux fermés et les sourcils froncés, le chasseur est assis, une boule de cristal dans les mains. Peut-être vaut-il mieux ne pas le déranger.]]
			},
			['choice'] = {
