			['episode'] = {
				['5'] = [[J'ai piégé l'échelle, en faisant en sorte qu'elle semble normale. J'entends un bruissement, non loin. Quelqu'un arrive, il est temps de se cacher !]],
				['41'] = [['Bien, merci. Tout est réglé, maintenant. Ces voleurs me mettaient sur la paille ! Tenez, voici votre dû.']],
				['40'] = [[Le chef de l'entrepôt peut se montrer très convaincant. Le voleur a craqué et il a vendu ses complices. Le patron m'a dit :<new_line>'C'est nouveau, ça. Je vais devoir demander de l'aide à la base. Sinon, vous pourriez vous charger de ce gang ? Vos efforts seront récompensés.']],
				['20'] = [[Le loup m'a guidé vers un camp plein de bandits.]],
				['21'] = [[Après les avoir vaincus, j'ai inspecté le camp. Je n'étais pas assez costaud pour rapporter tout leur butin, mais j'ai récupéré tout ce que je pouvais.]],
				['35'] = [[Au bout d'un vrai parcours du combattant, le voleur n'ira plus nulle part. Mais j'ai entendu un léger bruissement. Quelqu'un arrive, il faut se cacher !]],
				['34'] = [[J'ai vu le voleur jeter une échelle par-dessus la barrière et commencer à descendre. Mais il a fait une mauvaise chute au dernier moment. Il s'est foulé la jambe, voire pire. Au moins, il n'ira nulle part !]],
				['32'] = [[C'était astucieux. Beau travail ! J'ai failli ne pas comprendre. Vous méritez une belle récompense.']],
				['31'] = [[Au bout d'un moment, le chef a fait irruption dans l'entrepôt, confus.<new_line><new_line>'Que se passe-t-il ? Comment les bandits sont entrés ici ?']],
				['30'] = [[J'ai vaincu les bandits, mais je dois réfléchir à la suite.]],
				['29'] = [[Le chef de bande m'a donné ma part du butin.]],
				['28'] = [[Les bandits avaient une dent contre moi, mais leur humeur a changé quand je leur ai montré la clé de l'entrepôt. Nous avons vidé les réserves d'un coup.]],
				['25'] = [[Eh bien, j'ai réussi à attraper le voleur. Mais il m'avait attiré dans un guet-apens, et ses acolytes n'étaient pas ravis de me voir.]],
				['24'] = [[Clairement, ce voyou connaissait mieux la forêt que moi. Au bout de quelques minutes, je l'ai perdu de vue.]],
				['23'] = [[Le voleur m'a repéré. Il a laissé tomber son larcin sur le sol pour courir et s'est enfui dans les bois.]],
				['22'] = [[Le chef de l'entrepôt était satisfait du résultat.<new_line><new_line>'Je savais que je pouvais compter sur vous. Vous avez bien mérité votre récompense.']],
				['4'] = [[J'ai vu des traces de pas près de la barrière, en direction des bois, et comme si quelque chose avait été traîné au sol.<new_line>Une échelle se trouvait dans l'arbuste.]],
				['18'] = [[Je n'ai pas pu capturer le voleur qui a disparu dans la nuit.]],
				['17'] = [[Son job terminé, le voleur est revenu pour grimper à l'échelle. Heureusement, les barreaux que j'avais sabotés ont cédé sous son poids et il est tombé lourdement au sol.]],
				['16'] = [[J'ai entendu un bruissement dans les arbustes et vu la silhouette d'un homme tenant quelque chose entre les mains, sans savoir quoi.]],
				['12'] = [[J'ai entendu le son d'une échelle posée de l'autre côté de la barrière. Quand le voleur a grimpé pour passer l'obstacle, j'étais là pour lui tendre une embuscade.]],
				['13'] = [[En ligotant le voleur, j'ai vu qu'il s'était empalé sur les barreaux de fer et qu'il saignait abondamment.]],
				['6'] = [[Le fil de détente est posé et bien caché.]],
				['10'] = [[Le chef de l'entrepôt m'a remercié avant d'ajouter :<new_line><new_line>'J'ai l'impression qu'il n'opérait pas seul. Désormais, impossible d'en savoir plus.']],
				['8'] = [[J'ai entendu un grand fracas dans la forêt. Clairement, mon piège mortel a fonctionné.<new_line>Je devrais avertir le chef de l'entrepôt.]],
				['7'] = [[C'est la nuit. Je dois trouver un endroit pour espionner.]],
				['19'] = [[Le lendemain matin, j'ai tout raconté au chef de l'entrepôt. Il était visiblement très déçu.<new_line>'Désolé mon vieux, le voleur court toujours, je ne peux pas vous donner de récompense.']],
				['14'] = [[Le voleur a jeté l'échelle de notre côté de la barrière. Comme il descendait, j'ai surgi de ma cachette. Il a sursauté et l'échelle s'est cassée sous son poids, le faisant tomber au sol.]],
				['15'] = [[Le chef de l'entrepôt a jeté un œil au voleur.<new_line><new_line>'Il vit encore ? Tant mieux, on va pouvoir l'interroger.']],
				['1'] = [[D'après le chef de l'entrepôt, quelqu'un a pris l'habitude de chaparder tout ce qui n'était pas cloué au sol dans les réserves. Il faut capturer ce voleur.]],
				['39'] = [[Le chef de l'entrepôt peut se montrer très convaincant. Le voleur a craqué et nous a dit où il cachait le butin. Le patron m'a demandé :<new_line>'Vous pourriez récupérer ce que ce parasite a chapardé ? Vos efforts seront récompensés.']],
				['38'] = [[Le criminel n'a rien voulu admettre. C'était peut-être un simple voleur de passage, et quelqu'un d'autre a commandité le cambriolage de l'entrepôt.]]
			}
		},
		['embacity_biolab_6'] = {
			['choice'] = {
				['1'] = [[C'est votre voix, celle dont parlait Gerin ?']]
			},
			['episode'] = {
				['1'] = [[Un autre désagrément... Quoi, cet idiot de Gerin n'a pas survécu ? D'un autre côté, je suis content quand vous vous entretuez.']],
				['2'] = [[On peut dire ça. Vous pouvez m'appeler Émissaire. Vous savez déjà quelque chose, à ce que je vois, et vous avez fait beaucoup. Mais vous n'auriez pas dû vous mêler de mes affaires, j'ai passé tant de temps à travailler sur le Cultivateur. Maintenant, c'est à votre tour de mourir.']],
				['3'] = [[Dans la salle, j'ai vu un homme vêtu d'une lourde combinaison protectrice. Il allait et venait vers les autoclaves.<new_line>Me remarquant, l'homme se prépara à attaquer.]]
			}
		},
		['leprechaun_debts'] = {
			['episode'] = {
				['1'] = [[À peine ai-je commencé à m'expliquer qu'une rage sourde s'est emparée des lutins. Le visage déformé par la fureur, ils m'ont attaqué.]]
			}
		},
		['emba_def_easy'] = {
			['choice'] = {
				['2'] = [[Refuser et partir]],
				['1'] = [[Boire et partir]]
			},
			['episode'] = {
				['2'] = [[Les fermiers me remercient de les avoir aidés et me servent un verre pour la route.]],
				['1'] = [[La ferme est vide, comme si les humains venaient de la quitter. Je n'ai pas eu le temps de m'en étonner : des coups de feu et un rugissement inhumain ont retenti quelque part derrière la grange.<new_line>Les fermiers repoussent une attaque de biomutants ! S'en sortiront-ils sans mon aide ? Je dois me décider.]]
			}
		},
		['forest_pithouse'] = {
			['episode'] = {
				['4'] = [[Tu ferais bien de dégager]],
				['5'] = [[Il m'a jeté un regard méfiant avant de se détendre. <new_line>]],
				['2'] = [[Il n'a pas fallu attendre très longtemps pour que le propriétaire des lieux revienne. C'était un homme trapu tenant d'une main son fusil, et de l'autre, un panier rempli de champignons. Il a jeté un regard inquisiteur autour de lui avant d'alimenter le feu.]],
				['3'] = [[L'homme s'est redressé brutalement et a pointé son fusil dans ma direction.<new_line>]],
				['8'] = [[La porte était juste fermée par un petit loquet, manifestement pour empêcher les animaux d'entrer. Il faisait plutôt chaud à l'intérieur. Des sacs et des caisses remplis de bric et de broc étaient entassés dans les coins.]],
				['9'] = [[J'ai trouvé de nombreuses provisions à l'intérieur de l'abri : vivres, médicaments, outils et munitions pour n'en citer que quelques-unes. Je devais juste sortir avant le retour du maître des lieux.]],
				['6'] = [[Lorsque l'homme a réalisé qu'il ne risquait rien, son visage s'est décrispé. Nous avons bu, mangé et bavardé, notamment du passé, et de nos idées pour l'avenir.]],
				['7'] = [[Quand le moment est venu de se dire au revoir, il m'a demandé si je voulais échanger du sel, des cigarettes et des munitions.]],
				['11'] = [[À côté de l'abri, j'ai trouvé quelques baies, de la viande, ainsi que des peaux qui séchaient et un panier de charbon.]],
				['10'] = [[Dès que je suis sorti avec mon sac plein à craquer, j'ai aperçu un homme, probablement le propriétaire. L'air résolu, il pointait son fusil dans ma direction. J'ai alors entendu un coup de feu et senti une douleur fulgurante dans l'épaule droite.]],
				['1'] = [[Dans les bois, je suis tombé sur un abri creusé à même la terre. Les charbons dans le foyer sont encore chauds, quelqu'un vit donc ici et ne doit pas être bien loin. Je n'entends aucun bruit à l'intérieur.]],
				['12'] = [[J'ai installé le piège à grenade avant de me cacher dans des buissons. Quelques instants après, le propriétaire des lieux est arrivé. À peine est-il entré dans son logis de fortune que la grenade a explosé. L'inconnu était déjà mort quand je suis arrivé. Je peux fouiller la zone sans encombre.]],
				['13'] = [[Alors que je m'apprêtais à partir avec mon butin, une meute de loups a surgi des ténèbres de la forêt. Il semblerait que la détonation de l'explosion et mon intrusion les aient attirés. Je dois me défendre.]]
			},
			['choice'] = {
				['4'] = [[Installer un piège à grenade]],
				['5'] = [[Sortir des buissons et dire bonjour]],
				['2'] = [[Entrer]],
				['3'] = [[Fouiller partout]],
				['8'] = [[Dire au revoir et partir]],
				['9'] = [[Sortir]],
				['6'] = [[Je ne fais que passer]],
				['7'] = [[Ça fait un bail que je n'ai pas parlé à qui que ce soit ! Ça vous dit de bavarder.]],
				['10'] = [[Oh, doucement ! Je veux juste parler !]],
				['1'] = [[Ne pas se faire voir et attendre]],
				['11'] = [[C'est bon, je m'en vais]],
				['12'] = [[Fouiller de fond en comble]]
			}
		},
		['halloween_boss_2022_4'] = {
			['episode'] = {
				['2'] = [[Il n'y a rien, ici. Libre à vous de jeter un œil au moulin et à la grange. Ce n'est pas possible ! Il y a un festival en ce moment, et je n'ai pas une seule citrouille à présenter...']],
				['1'] = [[L'agriculteur m'a accueilli près de son grenier vide :<new_line>'Il n'y a plus rien, mes récoltes ont disparu ! Dire que l'an dernier, j'avais gagné un prix à la foire... celui de la plus grosse citrouille !']]
			},
			['choice'] = {
				['1'] = [[Vous avez une piste ?']]
			}
		},
		['halloween_boss_2022_4_2'] = {
			['episode'] = {
				['4'] = [[J'ai fait de mon mieux pour traverser le champ de maïs sans faire de bruit, mais quelque chose a craqué sous mon pied. J'ai immédiatement entendu : 'Vite, courez vers la forêt ! Par toutes les graines, ma plus grande peur est de finir dans une tarte à la citrouille !']],
				['5'] = [[Arrivé à une clairière dans la forêt, je vis au milieu une grande silhouette avec une énorme citrouille à la place de la tête, coiffée d'une couronne. Une flamme s'agita derrière les fentes des yeux de la citrouille, et la créature se tourna vers moi : 'Pourquoi poursuis-tu mes sujets, misérable humain ?']],
				['2'] = [[Chut toi-même ! Le mange-citrouilles est parti ? Je ne vois rien.']],
				['3'] = [[Aaargh ! Courez ! Il va boire tout notre jus de citrouille !'<new_line>J'ai distingué quelques petits hommes à tête ronde, cachés derrière le mur. Je n'ai pas eu le temps de bien les voir, ils ont aussitôt disparu derrière des arbres fruitiers.]],
				['8'] = [[Bon, la foire rurale va pouvoir suivre son cours habituel ! Passe me voir, au fait. J'ai des choses à te proposer en exclusivité depuis le monde des morts.']],
				['1'] = [[Je n'avais pas fait cinq pas en direction du bâtiment que j'ai entendu un murmure dans un coin. 'Chuuut ! Quelqu'un vient nous transformer en soupe. Chut, silence !']],
				['6'] = [[Je sais. J'ai donné vie à toutes les citrouilles afin qu'elles puissent lui échapper. Ce fermier élevait mes sujets au milieu d'une friche radioactive, sans protection. J'ai aussi repris la citrouille qu'il avait plantée dans les décombres de cette ville horriblement irradiée juste pour gagner un prix.']],
				['7'] = [[Je tenais à punir moi-même l'agriculteur en le faisant venir ici. Mais puisque tu es là, que ta mort lui serve d'ultime avertissement.']]
			},
			['choice'] = {
				['4'] = [[Écouter]],
				['5'] = [[S'approcher]],
				['2'] = [[Les récoltes de l'agriculteur ont disparu.']],
				['3'] = [[Et maintenant ?']],
				['1'] = [[Poursuivre les fuyards]]
			}
		},
		['lair_ant'] = {
			['episode'] = {
				['1'] = [[C'était une vision d'horreur. Les fourmis avaient construit leur tanière à partir de tout ce qu'elles pouvaient trouver : déchets, ordures et pourritures. Mais même ce bazar putride pouvait cacher quelque chose de précieux.]]
			}
		},
		['newyearEvent_search_easy'] = {
			['choice'] = {
				['4'] = [[Vous voulez faire manger ça aux gens ?']],
				['5'] = [[Goûter la soupe]],
				['2'] = [[Non, je n'ai rien. Je vais chercher.' (Secouer la tête et partir)]],
				['3'] = [[Rattraper Nomade]],
				['1'] = [[Tenez.' (Donner les herbes)]],
				['6'] = [[Et si on goûtait, d'abord ? Ils risquent l'intoxication.']]
			},
			['episode'] = {
				['4'] = [[Nomade approuva d'un signe de la tête.]],
				['5'] = [[Nomade plongea sa tête dans la cuve pour boire une généreuse portion de son contenu.<new_line>'Miam-miam !'<new_line>C'est bien beau, mais son estomac de pierre doit être capable de digérer tout et n'importe quoi. L'estomac des humains ordinaires, en revanche, n'a forcément pas le même degré de tolérance. Je suppose qu'il va falloir y goûter moi-même.]],
				['2'] = [[Groooooooooh !'<new_line>Nomade sauta de joie, m'arracha les herbes des mains et partit.]],
				['3'] = [[Après avoir couru un peu, j'ai découvert un énorme bol de pierre, chauffé de tous côtés par une flamme magique. Des gens se tenaient autour. Nomade cuisinait avec ferveur, ajoutant constamment des ingrédients. Finalement, il termina sur un résonnant :<new_line>'Miam-miam !']],
				['1'] = [[Avec son raffinement habituel, Père Nomade essayait de m'expliquer qu'il avait besoin d'herbes pour une recette de Noël. C'est le Père Négoce qui m'en avait parlé, ayant lui-même tenté d'aider Nomade avant de recevoir un coup sur la tête.<new_line>'Scrunch-scrunch... Aah, groovaah.']],
				['6'] = [[Je me suis immédiatement senti mal, et Nomade commença à devenir intelligible dans ses propos.<new_line>'Ah, on dirait que vous êtes trop éloigné de l'énergie verte : vos chakras pancréatiques ne digèrent pas très bien les smoothies hautement spirituels. Merci d'avoir fait ce grand sacrifice pour les autres.']],
				['7'] = [[La soupe était finalement un délice de gourmet. Quand les gens virent ma satisfaction, ils se servirent à leur tour. Père Nomade profita du spectacle d'un repas au calme, un sourire béat sur le visage.]]
			}
		},
		['halloween_def_easy'] = {
			['choice'] = {
				['4'] = [[Utilisez les barils.']],
				['1'] = [[Je vous aiderai, bien sûr.']],
				['2'] = [[Je n'ai pas le temps.' (Partir)]],
				['3'] = [[Posez les pièges.']]
			},
			['episode'] = {
				['1'] = [[Aimable étranger, tu viens vraiment à mon secours ? Ces démons sont une plaie. Ils ont dévoré toute la récolte. Mes voisins ne croient pas à ces créatures surnaturelles. Ils racontent que je suis un vieux fou, victime d'hallucinations, et que j'ai épuisé mes réserves. M'aideras-tu ? Ça en vaudra la peine.']],
				['2'] = [[Merci, je n'oublierai pas ton geste. Il faut se préparer. Je peux faire rouler des barils de carburant pour que tu les fasses exploser. Sinon, je peux poser des pièges, mais attention où tu mettras les pieds.']],
				['3'] = [[Oh ! Tu entends ça ? Les monstres approchent. On va leur mettre le feu !']]
			}
		},
		['comrade_in_trouble_animal'] = {
			['episode'] = {
				['1'] = [[Des prédateurs ont encerclé un pylône rouillé, au sommet duquel est perché le pauvre éclaireur.]]
			}
		},
		['sled_repair'] = {
			['episode'] = {
				['1'] = [[Il n'a pas fallu bien longtemps pour retrouver le traîneau. Abandonné sur une route de campagne, il est dans un état pitoyable. Voyons voir ce qu'on peut faire.]]
			},
			['choice'] = {
				['1'] = [[Examiner le traîneau]]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['episode'] = {
				['4'] = [[Au zézaiement a répondu une voix tonitruante, comme un grondement :<new_line>'Igoooor, ça vient d'un ivroooogne. Je n'en veux paaaas. J'aurais honte de sortir en public avec un foie comme çaaaa !']],
				['5'] = [[J'étais presque à côté d'eux. Le zézaiement a repris de plus belle :<new_line>'Quel public, ze te le demande ? Et pour aller où ? Tu es un monztre, et ze te propose zuste d'avoir un foie !']],
				['2'] = [[À l'intérieur se trouvait un corps sans vie. Il semblait encore intact, mais mon dosimètre commença à s'agiter furieusement. Cette personne devait être enfermée là-dedans, elle est morte de faim ou n'a pas supporté les radiations. Je ferais mieux de partir pour éviter de connaître le même sort.]],
				['3'] = [[Ayant entendu une voix qui zozote, j'ai décidé de me frayer un chemin à travers les fourrés pour ne faire peur à personne.<new_line><new_line>'Z'ai l'imprezzion que tu t'en fous ! C'est un FOIE, grand dézénéré !']],
				['8'] = [[Le bossu se précipita vers la grosse électrode et plusieurs autres créatures, visiblement d'autres expériences du laboratoire de Victor Frankenstein, rejoignirent le monstre.]],
				['9'] = [[Après m'être occupé du monstre, j'ai voulu chercher l'homme qui s'était enfui. Je l'ai trouvé non loin : apparemment, dans sa course éperdue, il était tombé dans un ravin. Son cou était brisé.]],
				['6'] = [[J'ai vu un petit bossu et un énorme monstre humanoïde. Les deux se sont tournés vers moi. Surpris, le monstre a lâché un homme contusionné qui a pris la fuite sans demander son reste. Après quoi, le visage du monstre s'est fendu d'un sourire menaçant :<new_line>'Igooooor ! V'là une toute autre histoire. Je veux SON foie à lui !']],
				['7'] = [[Le bossu zézaya :<new_line>'Un beau zpézimen, il est vrai. Ze vais te montrer quelque soze, l'ami—vise un peu cette zolie électrode là...']],
				['1'] = [[Quelqu'un doit mourir dans ce bâtiment. Or, je ne vois personne... Il y a bien une pièce dont l'entrée est bloquée, peut-être qu'elle se trouve derrière ?]],
				['10'] = [[Tu vois, nul n'échappe à son destin. Surtout quand il est écrit dans le Livre de la mort. Viens me retrouver dès que tu auras le temps, le dernier contrat t'attend.']]
			},
			['choice'] = {
				['4'] = [[S'approcher encore plus...]],
				['5'] = [[Attaquer depuis les fourrés]],
				['2'] = [[Retirer l'obstacle avec une pelle]],
				['3'] = [[S'approcher en rampant]],
				['1'] = [[Retirer l'obstacle avec les mains]],
				['6'] = [[Hein ?']],
				['7'] = [[Ne bougez plus !']]
			}
		},
		['embacity_radio2'] = {
			['episode'] = {
				['4'] = [[Dans les quelques vitrines qui restent du magasin, on aperçoit parfois les silhouettes de biomutants errants. Mieux vaut éviter de les approcher sans combinaison de protection.]],
				['1'] = [[Le magasin est complètement envahi par la végétation, mais il est loin d'être délabré. Il y a peu de traces de mutants, mais quelque chose m'attend à l'intérieur, c'est certain. Avant d'entrer, je dois vérifier si mes défenses sont fonctionnelles.]],
				['2'] = [[Le magasin était couvert de moisissures, et de gros bourgeons écarlates poussaient au pied des racines vertes. Alors que je les regardais, j'ai failli ne pas remarquer les biomutants. Je dois m'assurer que ma combinaison de protection est fonctionnelle.]],
				['3'] = [[La moisissure a pratiquement détruit le bâtiment, transformant le béton et le fer en un terrain fertile pour biomutants. J'aurai besoin d'une combinaison de protection pour m'occuper d'eux.]]
			}
		},
		['emba_isaev_surgut'] = {
			['choice'] = {
				['4'] = [[Que voulez-vous dire ?']],
				['5'] = [[Aller voir Isaev]],
				['3'] = [[Pourquoi ? Vous avez de nouvelles données ?']],
				['1'] = [[Quelle sera l'application de cette technologie ?']],
				['6'] = [[D'accord. Bonne chance, docteur.']]
			},
			['episode'] = {
				['4'] = [[Il ne reste plus qu'à découvrir ce que cache cette cité des sciences. Veuillez récupérer la boîte noire de la biostation.'<new_line>(Tend un bout de papier avec le code)]],
				['5'] = [[Quoi ?! Cette chose parlait ?! C'est impossible, c'est du grand n'importe quoi... Vous dites que le nom de Gerin a été mentionné ?<new_line>Nom d'un chien, qu'est-ce que ça veut dire ? Nous mettons fin immédiatement à tout développement plus avancé de cette technologie !']],
				['2'] = [[Nous sommes déjà en pourparlers avec les principales factions. Nous avons prévu de partager cette technologie en l'échange de leur aide pour développer un système de sécurité.<new_line>Si ce problème de sécurité devait persister, nous devrions condamner tout le programme et éliminer toute trace de la technologie. Vous voyez donc la gravité des enjeux. Nous ne pouvons prendre aucun risque.']],
				['3'] = [[Malheureusement, il n'y a aucune mention de développement dans les documents. C'est un autre laboratoire d'analyse de données.<new_line>Mais ce qui est étonnant, c'est que nous recevons des signaux radio de cette biostation ! Nous n'avons pas pu les déchiffrer, mais il pourrait y avoir quelqu'un là-bas qui continue de travailler.']],
				['8'] = [[Nous allons préparer l'arrêt total des recherches, et il y aura encore quelques missions pour vous. Revenez d'ici quelques heures.']],
				['9'] = [[Isaev, vous êtes un homme intelligent. Donnez-moi la valise, et repartez.']],
				['6'] = [[Oui. La boîte noire a enregistré le processus de formation de l'objet que vous avez rencontré. Le mycélium dans son entièreté serait, selon toute vraisemblance, un organisme unique. Un peu comme un réseau téléphonique, si vous voulez.<new_line>L'absence d'hôte fixe ne l'entrave pas du tout. Pour nous, c'est un grave problème !']],
				['7'] = [[S'il est impossible d'avoir le contrôle total du mycélium, alors il ne faut pas l'utiliser, même partiellement. Nous pouvons travailler avec un fragment de cet organisme mais, tôt ou tard, une autre partie de celui-ci commencera à muter.<new_line>Surviendra un Émissaire comme celui-ci, ou un Cultivateur. Alors, il ne sera absolument plus question d'un quelconque contrôle, même sur notre fragment de mycélium.']],
				['13'] = [[(chuchote) 'Voilà, l'ami. Il faut partir d'ici, tout de suite. J'ai contacté mes collègues de S.O.V.A., nous allons dans leur ville.<new_line>Nous avons vraiment besoin de votre aide, veuillez vous y rendre dès que possible.']],
				['12'] = [[Très bien. Tenez, prenez-la et laissez-nous partir.']],
				['1'] = [[Nous travaillons d'arrache-pied sur la technologie de végétalisation. Et grâce à l'analyse de l'œuvre scientifique de Gerin, les choses ont beaucoup avancé !<new_line>Il nous reste encore à développer un système de protection contre les activations non autorisées. En fait, c'est là que nous espérons avoir des réponses pour trouver le moyen de résoudre ce problème.']],
				['11'] = [[Comme l'ont prouvé les scientifiques britanniques, l'argent n'a pas d'odeur, doc. Ne faites pas l'imbécile, je n'ai aucune envie de vous tuer.']],
				['10'] = [[Vous ne comprenez pas ! Cette technologie est instable et dangereuse !']]
			}
		},
		['stock_bandit'] = {
			['episode'] = {
				['4'] = [[L'un d'eux s'est hissé jusqu'à la caisse et a commencé à la fouiller, mais il ne m'a pas aperçu.]],
				['5'] = [[Quelques instants plus tard, les pilleurs sont partis, emportant tous les objets qui avaient la moindre valeur.]],
				['2'] = [[Trois hommes armés sont entrés. L'un d'eux s'est assis juste en dessous de ma cachette et a ordonné aux deux autres de fouiller l'entrepôt.]],
				['3'] = [[D'un coup sec, j'ai poussé la caisse qui est tombée sur l'un des pilleurs, le tuant sur le coup et se brisant en mille morceaux. Maintenant, je dois m'occuper de ses compagnons.]],
				['1'] = [[Alors que je commençais à jeter un coup d'œil à l'intérieur de l'entrepôt, j'ai entendu des voix approcher de l'extérieur. Probablement des pilleurs...]],
				['6'] = [[Les pilleurs n'ont pas eu le temps de réagir lorsque la grenade est tombée à leurs pieds. Deux d'entre eux sont morts sur le coup. Quant au troisième, il a pris la fuite.]]
			},
			['choice'] = {
				['4'] = [[Ne pas se faire voir et attendre]],
				['5'] = [[Faire tomber le pilleur et attaquer les autres]],
				['2'] = [[Sortir et se cacher derrière la caisse]],
				['3'] = [[Pousser la caisse sur le pilleur]],
				['1'] = [[Les attaquer]],
				['6'] = [[Attendre]],
				['7'] = [[S'échapper par la porte latérale]]
			}
		},
		['gravedigger'] = {
			['episode'] = {
				['2'] = [[Le fossoyeur a refusé de parler. J'ai trouvé dans sa poche un étrange message ressemblant à une liste de courses macabres : des bras, des jambes et des têtes, avec « Igor » en guise de signature. Je dois montrer ce morceau de papier au tavernier. Il saura me renseigner.]],
				['1'] = [[Le fossoyeur n'était pas bien difficile à trouver. Cet homme à la silhouette décharnée est venu à ma rencontre devant les portes du cimetière. Dans la main droite, il tenait une pelle et dans la gauche, une jambe gangrenée. Il m'a regardé avec un rictus sinistre, a balancé la jambe dans un chariot rempli de membres amputés en état de décomposition plus ou moins avancé, avant de poser la pelle sur son épaule et de se précipiter dans les profondeurs du cimetière. Le moment est venu de s'occuper de cet individu.]]
			}
		},
		['embacity_square'] = {
			['episode'] = {
				['2'] = [[Sur la place envahie par la végétation, plusieurs mutants s'affairent à absorber lentement des blocs de moisissure. Leur élimination s'annonce risquée, mais cela vaut quand même la peine d'essayer.]],
				['1'] = [[L'endroit est recouvert de végétation et de moisissure. Sur la place, les habitants chancellent, comme si leurs fonctions vitales étaient à l'arrêt. Leur élimination s'annonce risquée, mais cela vaut quand même la peine d'essayer.]]
			}
		},
		['newyearEvent_def_easy'] = {
			['choice'] = {
				['4'] = [[Père Nounours]],
				['5'] = [[Cyber Noël]],
				['2'] = [[Père Négoce]],
				['3'] = [[Père Nomade]],
				['1'] = [[Appeler à l'aide]]
			},
			['episode'] = {
				['4'] = [[Un portail se matérialisa à mes côtés, et Nomade en sortit.<new_line>'Greeuh aaaah !', cria-t-il. Sur ces belles paroles, il fonça dans la mêlée. Il fallait bien que je le suive.]],
				['5'] = [[Un portail se matérialisa à mes côtés, et Nounours en sortit.<new_line>'Les gens ont besoin d'aide. Avec moi, camarade ! Pour la solidarité du prolétariat !']],
				['2'] = [[Après une concentration intense, j'ai souhaité de toutes mes forces à un miracle de Noël. Rien ne s'est passé. Peut-être parce que le miracle de Noël, c'est moi. Après tout, je suis une sorte de Père Noël, moi aussi. Je dois penser à quelque chose de précis.]],
				['3'] = [[Derrière moi, j'ai entendu le bruit d'un moteur. C'était une petite voiture rouge, à l'ancienne. Elle s'est arrêtée, et Négoce est sorti du véhicule.<new_line>'Ho-ho-ho ! Encore toi, cher ami ? Viens, les gens ont besoin d'aide. Il n'y a pas de temps à perdre.']],
				['1'] = [[En approchant des festivités, j'aperçois un épais nuage de fumée. Des vauriens sont venus gâcher la fête. Je dois protéger les villageois, mais en le faisant moi-même, je risque de les exposer au danger. J'ai besoin d'aide.]],
				['6'] = [[Un portail se matérialisa à mes côtés, et Cyber Noël en sortit.<new_line>'Hé, choom, ça gaze ! C'est quoi, cette affaire ? Encore un cas de cyberpsychose ? Flanquons-leur une bonne raclée !']],
				['7'] = [[Un portail se matérialisa à mes côtés, et Mago en sortit.<new_line>'Encore cet imbécile ? Il faut vraiment que je me coltine tous tes problèmes insignifiants ? Bah, autant donner un coup de main.']]
			}
		},
		['halloween_reaper'] = {
			['episode'] = {
				['4'] = [[Tu trouveras, en plus de toutes sortes de créatures, des personnages différents qui te paieront des services rendus.']],
				['5'] = [[Tu sais à quoi le Médecin de la peste doit son nom ? Sans vouloir m'immiscer dans tes affaires, je te conseille de revêtir ton armure démoniaque avant de lui rendre visite.']],
				['2'] = [[Je te propose des contrats pour ces démons. Tu pourras glaner de précieux artéfacts en affrontant chacun d'eux.']],
				['3'] = [[Les Faucheuses sont payées en pièces. Voici ton premier contrat. Lis-en les termes, si tu y arrives.']],
				['8'] = [[Cet Igor... C'est un type insaisissable. Sa capture s'annonce difficile, alors je vais t'aider. Tu as entendu parler du Livre de la mort ?']],
				['9'] = [[C'est un registre des décès à venir, avec lieu et date. Voici une liste des futures victimes à proximité de la scène de crime la plus récente du Monstre de Frankenstein.'<new_line>(Déchire une page du livre et la tend)]],
				['6'] = [[Hé, mortel. J'ai une blague pour toi—c'est l'histoire d'un mec tellement attaché à son lustre qu'il en est mort.']],
				['7'] = [[Tiens, écoute celle-là. Pourquoi un noyé n'arrive jamais en retard ? Parce qu'il est toujours dans l'étang !']],
				['1'] = [[Ô, mortel ! Pas de panique, tu n'es pas dans ma liste. En revanche, des esprits maléfiques sont passés par ici, et je dois les faucher.']],
				['11'] = [[Que dire ? Il faut de tout pour faire un monde, mortel. Prends ta décision, sachant que cet Âne parlant vaut une grosse prime ! À en crever... Bref, tu peux partir. J'ai des choses à faire.']],
				['10'] = [[Pourquoi tu me regardes comme ça ? Un contrat en vaut un autre...']]
			},
			['choice'] = {
				['4'] = [[Ça marche.']],
				['5'] = [[Non.']],
				['2'] = [[Qu'attendez-vous en échange des contrats ?']],
				['3'] = [[Comment obtenir ces pièces ?']],
				['1'] = [[En quoi ça me concerne ?']],
				['6'] = [[Prendre la page et partir]],
				['7'] = [[Un âne qui parle ? C'est sérieux ?']]
			}
		},
		['embacity_house5'] = {
			['episode'] = {
				['4'] = [[On peut entendre des cris humains surgir des ruines de la maison rongée par la moisissure. Que ses anciens habitants aient muté ou que de nouveaux soient arrivés, il me faudra une combinaison de protection pour les affronter.]],
				['1'] = [[Si j'en crois le bruit provenant des ruines de la maison, cet endroit grouille de créatures. Pour les combattre, je ferais mieux de porter une combinaison de protection.]],
				['2'] = [[Le champignon s'est répandu dans tout le bâtiment, dévorant même à certains endroits des vêtements et des livres éparpillés. Quelques créatures se nourrissent de la substance moisie, mais ce serait dangereux de les approcher sans protection spéciale.]],
				['3'] = [[Des racines épaisses sortent des fenêtres ; des biomutants se sont installés dessus, ils grignotent des excroissances moisies. Une racine a germé à travers une armoire, qui est maintenant renversée sur le balcon. Je ne dois pas oublier de porter une protection spéciale.]]
			}
		},
		['impostor3'] = {
			['choice'] = {
				['1'] = [[Faire des dons]]
			},
			['episode'] = {
				['1'] = [[Les gens étaient heureux de me voir revenir avec leurs effets personnels.]]
			}
		},
		['emba_energy_hard'] = {
			['choice'] = {
				['4'] = [[C'est quoi ?']],
