			['episode'] = {
				['4'] = [[Maintenant, saute.']],
				['1'] = [[En m'approchant du terrier de lapin, j'ai entendu une voix dans ma tête :<new_line>'Bien le bonjour, étranger. Je vois que tu entreprends un grand périple.']],
				['2'] = [[La voix s'est tue pendant quelques secondes, avant de reprendre : <new_line>'Nous avons accès à certaines villes en ce moment. Laquelle choisiras-tu ?']],
				['3'] = [[La voix dans ma tête a résonné une autre fois encore : <new_line>'Bon choix. Lance une friandise dans le terrier, et saute dedans.']]
