				['12'] = [[Briser le pot de miel sur la pierre]],
				['11'] = [[Lancer le poisson à la face de l'ours]]
			},
			['episode'] = {
				['4'] = [[Les traces m'ont guidé à travers les bois jusqu'au camp des bandits. Ce sont eux qui avaient pillé les planques, c'est certain. Je devrais retourner à la base et le signaler. En même temps, si je récupère moi-même les marchandises des bandits, je parie qu'ils me paieront plus.]],
				['5'] = [[Les traces m'ont guidé loin dans la forêt jusqu'à un cadavre. Le corps gisait au sol et tenait encore fermement une caisse couverte de terre entre ses bras.]],
				['2'] = [[En arrivant à l'endroit indiqué, j'ai vu un trou fraîchement creusé et des traces au sol.]],
				['3'] = [[Celui qui a laissé cette planque a manqué de temps, le trou n'est pas profond. J'ai rapidement trouvé la caisse.]],
				['8'] = [[Alors que j'examinais le corps, j'ai un entendu un lourd craquement derrière moi. J'ai fait volte-face. C'était un ours.]],
				['9'] = [[J'ai réussi à extirper la caisse des mains du cadavre. Soudain, j'ai un entendu un lourd craquement derrière moi. J'ai fait volte-face. C'était un ours. Il s'est léché les babines, comme s'il avait trouvé son déjeuner.]],
				['6'] = [[C'était compliqué, mais j'ai réussi à vaincre les bandits.]],
				['7'] = [[J'ai la caisse. Mon travail ici est terminé.]],
				['1'] = [[J'ai trouvé la cachette sans problème.]],
				['11'] = [[Ma diversion a fonctionné. L'ours est distrait et ne se soucie plus de moi.]],
				['10'] = [[Et c'est qui le roi de la forêt, maintenant ?']]
			}
		},
		['comrade_in_trouble_bar'] = {
			['episode'] = {
				['1'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'C'était donc ça. Merci du renseignement, vous avez bien travaillé.']],
				['2'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'Message reçu, nous lui envoyons de l'aide. Merci de votre aide !']],
				['3'] = [[Le chef des gardes a hoché de la tête en écoutant mon rapport.<new_line>'Beau travail ! Vous savez vous rendre utile. Voici un petit supplément pour compenser.']]
			}
		},
		['lair5'] = {
			['episode'] = {
				['1'] = [[Je parie que les vampires m'attendaient. Je me suis retrouvé encerclé par ces créatures de la nuit, avant même de m'être aventuré dans leur tanière.]]
			}
		},
		['emba_commander_medium'] = {
			['choice'] = {
				['4'] = [[Commencer la chasse]],
				['1'] = [[Quel genre de projet ?']],
				['2'] = [[Vous avez une idée de la marche à suivre ?']],
				['3'] = [[C'est d'accord. Donnez-moi la radio et emmenez-moi aux biomutants.']]
			},
			['episode'] = {
				['4'] = [[J'ai une idée ! Vous pourriez nous guider, non ? On a des radios. Vous pouvez corriger nos actions. Dans ce cas, on peut y aller même avec ces combinaisons en lambeaux.']],
				['5'] = [[Oui, nous sommes prêts à démarrer l'attaque. Nous attendons vos ordres.']],
				['2'] = [[Alors ça, c'est quelque chose, merci ! Avec mon partenaire, on allait dans la ville infectée. On voulait confirmer une théorie. C'est comme ça qu'on a abîmé nos combinaisons... La visibilité et la mobilité sont terribles maintenant.<new_line>Et il nous reste le projet des biomutants sur les bras.']],
				['3'] = [[Mon directeur de recherche en est le responsable : le Dr Isaev, un grand scientifique. J'ai maintenant pour mission de tester une nouvelle technologie pour extraire le mycélium de biomutants abattus. Mais je ne sais même pas comment m'en approcher avec nos combinaisons dans cet état.']],
				['1'] = [[Bonjour ! Attendez, on n'en peut plus. On vient d'arriver. Syoma, avez-vous une boisson bioénergétique ? Non ? Mince, quel... <new_line>Ah ! Pourriez-vous m'apporter un rafraîchissement ?']],
				['6'] = [[Merci, camarade ! On les a tous abattus, c'est l'heure de passer à l'expérience. Je mentionnerai votre nom, on vous doit une fière chandelle. Laissez la radio là-bas, sur la souche, on la récupérera plus tard.']]
			}
		},
		['halloween_search_medium'] = {
			['choice'] = {
				['4'] = [[Bon...' (Partir)]],
				['1'] = [[Et voilà.']],
				['2'] = [[Non, mais je vais chercher.' (Partir)]],
				['3'] = [[Mais notre marché...']]
			},
			['episode'] = {
				['1'] = [[Bonjour, gamin. Je concocte une potion particulièrement coriace, aujourd'hui. Tu as peut-être des ingrédients ?']],
				['2'] = [[Oh, gamin. Il manque encore une herbe. J'avais complètement oublié.']],
				['3'] = [[Apporte-moi encore une herbe, et le marché sera conclu.']]
			}
		},
		['emba_craft_easy2'] = {
			['choice'] = {
				['2'] = [[Bonne chance !']]
			},
			['episode'] = {
				['2'] = [[Il faut rester vigilants avec ces nouveaux mutants. On prépare tous les membres du camp un tant soit peu capables de faire quoi que ce soit à attaquer. Mais on n'a pas assez d'armes pour tout le monde. Vous auriez peut-être quelque chose que nos hommes sauraient utiliser ?']],
				['3'] = [[Super, merci ! On va montrer à ces monstres de quel bois on se chauffe !']]
			}
		},
		['people_bonfire'] = {
			['episode'] = {
				['4'] = [[Les étrangers ont allumé leurs cigarettes et m'ont relaté quelques-unes de leurs aventures et mésaventures. Leurs histoires se sont avérées très instructives. Deux ou trois heures plus tard, je leur ai dit au revoir et suis parti.]],
				['5'] = [[Grâce à mon camouflage, j'ai pu m'approcher et écouter leur conversation. Ils parlaient de leur cachette. Elle n'est pas très loin, et je pense pouvoir la trouver.]],
				['2'] = [[Les étrangers ont été surpris de me voir, mais ont tout de suite compris qu'ils n'avaient rien à craindre. Ils m'ont alors proposé de rejoindre les festivités.]],
				['3'] = [[Nous avons bavardé de tout et de rien, principalement de banalités. Après un certain temps, je leur ai dit au revoir et suis parti.]],
				['1'] = [[Alors que je traversais la friche, j'ai remarqué de la fumée dans le ciel. Il ne m'a pas fallu très longtemps pour en découvrir l'origine : un feu de camp autour duquel des gens étaient assis et discutaient.]]
			},
			['choice'] = {
				['1'] = [[Approcher d'eux]],
				['2'] = [[Se camoufler, s'approcher furtivement et écouter]],
				['3'] = [[Partager des cigarettes]]
			}
		},
		['supply_quest'] = {
			['choice'] = {
				['6'] = [[Donner la caisse]],
				['5'] = [[Garder la commande et partir]],
				['2'] = [[Prendre la récompense partielle]],
				['3'] = [[Demander la somme totale]]
			},
			['episode'] = {
				['4'] = [[L'homme a ricané avec dédain.<new_line>'Gardez ce bazar et fichez le camp d'ici.']],
				['5'] = [[Alors, vous avez tout le nécessaire ?']],
				['2'] = [[L'homme m'a lancé un regard soupçonneux, avant de vérifier l'intégralité de la boîte.<new_line>'Je ne paierai pas la somme entière pour ça.']],
				['3'] = [[Une vraie pince, hein ! Heureusement pour vous, je suis de bonne humeur.']],
				['1'] = [[L'homme a jeté un œil dans la boîte.<new_line>'Ravi d'avoir fait affaire avec vous.']]
