		['set_up_devices_bar'] = {
			['episode'] = {
				['1'] = [[C'est fait ? Excellent ! Ne soyez pas étonné, de nombreuses recherches scientifiques avancent grâce à ce genre de petites études.']]
			}
		},
		['halloween_energy_medium'] = {
			['choice'] = {
				['4'] = [[Sauter dans le chaudron]],
				['5'] = [[Non, vous d'abord.']],
				['2'] = [[D'accord, je vous aide.']],
				['3'] = [[Attendre]],
				['8'] = [[Sauter dans le portail]],
				['1'] = [[Il y a quelque chose de louche. Que fait-on dans un entrepôt ?']],
				['6'] = [[Prendre la caisse et la placer dans le portail]],
				['7'] = [[Je n'ai rien fait ! Vous m'avez poussé !']]
			},
			['episode'] = {
				['4'] = [[Et alors, on a la frousse ? Pas besoin. Je suis une gentille sorcière. Je ne te veux aucun mal.'<new_line>La Vieille sorcière fit un bond agile dans le chaudron et disparut.]],
				['5'] = [[Nous sommes arrivés dans un entrepôt bien entretenu. La Vieille sorcière murmura : <new_line>'Prends cette caisse et jette-la dans le portail. Mais ne fais pas de bruit.'<new_line>]],
				['2'] = [[Alors, n'es-tu pas un brave petit gars ? Accorde-moi juste une seconde.'<new_line><new_line>La Vieille sorcière retira le couvercle d'un grand chaudron et se dépêcha d'y jeter une poignée d'herbes.]],
				['3'] = [[Voilà, gamin, j'ai fini. C'est un portail qui nous évitera d'avoir à faire toute une trotte pour aller chercher mes affaires. Saute dedans. Je te suis.']],
				['12'] = [[Merci beaucoup, gamin. Tu es un brave garçon.']],
				['13'] = [[Chuuut... On récupère mes affaires, gamin. Je t'ai dit. Si tu fais le moindre bruit, je vais devoir sévir.']],
				['14'] = [[J'ai sauté dans le portail, qui m'a ramené dans la cabane de la sorcière. Je crois qu'elle m'en veut. Je ferais mieux de vider les lieux rapidement.]],
				['10'] = [[Les portes de l'entrepôt s'ouvrirent et un garde apparut à la porte :<new_line>'Hé ben mince alors, qu'avons-nous là ?! Des voleurs ? Je vais vous montrer ce qui leur arrive, aux voleurs !'<new_line>]],
				['9'] = [[En prenant une autre caisse, quelque chose me poussa violemment dans le dos. Ma charge tomba sur le sol avec fracas.<new_line>'Nom d'un balai, qu'as-tu fait ?! Mais qu'as-tu fait ?'<new_line>]],
				['8'] = [[J'ai jeté une autre caisse dans le portail.<new_line>'Très bien, on peut y aller.'<new_line>]],
				['7'] = [[Parfait, celle-ci aussi.'<new_line>La sorcière pointa du doigt une autre caisse.<new_line>]],
				['1'] = [[Ah, tu tombes à pic, gamin ! Donne-moi un coup de main, hein ? J'ai vraiment besoin d'un costaud pour m'aider à déplacer une partie de mes affaires. Impossible d'y arriver seule.']],
				['6'] = [[J'ai jeté la caisse dans le portail. La Vieille sorcière fit de même avec une agilité déconcertante.<new_line>'Maintenant, celle-là.']],
				['11'] = [[Pendant que je combattais le garde, la sorcière continuait à trimballer des caisses avec entrain.<new_line><new_line>'C'est bon, on peut partir maintenant.'<new_line>]]
			}
		},
		['reactor_guard'] = {
			['episode'] = {
				['1'] = [[Trois ours gigantesques ont transformé la salle du réacteur en tanière. Je dois me débarrasser d'eux pour obtenir ce dont j'ai besoin.]]
			},
			['choice'] = {
				['1'] = [[Entrer]]
			}
		},
		['emba_search_easy2'] = {
			['episode'] = {
				['1'] = [[Bonjour ! Voulez-vous aider la science ? Nous étudions l'interaction du mycélium avec les plantes.<new_line>Pouvez-vous nous rapporter des échantillons de la flore locale ?']],
				['2'] = [[Avez-vous cueilli les échantillons de l'herbier ? Nous sommes impatients de commencer l'expérience !'<new_line>]],
				['3'] = [[Le scientifique a pris les plantes avant de grimper dans le véhicule tout-terrain. Il est revenu après quelque temps.<new_line><new_line>'C'est prometteur, camarade ! Le mycélium a commencé une interaction active dès qu'il a été mis en contact avec des tissus étrangers.<new_line>Bon, je retourne à mes observations ! J'espère retravailler avec vous !']]
			}
		},
		['traveler_salvation'] = {
			['episode'] = {
				['1'] = [[Je l'ai retrouvé recroquevillé sous un arbre. À première vue, il était à bout de force et s'est endormi... mais un soupçon de vie brûle encore en lui. Si j'étais arrivé un peu plus tard, il serait mort de froid.]]
			}
		},
		['stash_bandit'] = {
			['episode'] = {
				['4'] = [[Désamorcer le piège s'est avéré plutôt simple. Maintenant, je peux fouiller la cachette.]],
				['5'] = [[Il ne m'a pas fallu très longtemps pour repérer une caisse dans l'un des tunnels des égouts. Je n'ai plus qu'à me servir et m'en aller.]],
				['2'] = [[Ça n'a pas été facile, mais j'ai réussi à ouvrir la bouche d'égout. Heureusement, l'échelle qui descend dans l'obscurité a l'air solide. J'aurai besoin d'une lampe ou d'une torche pour trouver la cachette dans cette pénombre.]],
				['3'] = [[Après être descendu, j'ai regardé autour de moi et remarqué que les bandits avaient posé un piège : un fil relié à une grenade.]],
				['8'] = [[J'ai avancé de plusieurs pas. Soudain, quelque chose m'a effleuré la jambe. Peu de temps après, une explosion m'a frappé de plein fouet ! Un piège ! Le corps criblé de débris, je me tordais de douleur.]],
				['1'] = [[L'entrée du repaire des bandits s'est avérée être une simple bouche d'égout. J'aurais besoin d'un pied de biche pour en soulever la plaque.]],
				['6'] = [[J'ai préféré ne pas prendre de risque et contourner le piège. Maintenant, je peux fouiller la cachette.]],
				['7'] = [[Après être descendu, j'ai attendu que mes yeux s'habituent à l'obscurité. La lumière provenant de la bouche d'égout devrait suffire pour trouver la cachette.]]
			},
			['choice'] = {
				['4'] = [[Descendre dans l'obscurité]],
				['5'] = [[Essayer de le désarmer]],
				['2'] = [[L'ouvrir avec des outils fait-maison]],
				['3'] = [[Sortir une lampe et descendre]],
				['8'] = [[Prendre les objets et partir]],
				['9'] = [[Trouver la sortie]],
				['6'] = [[Avancer à tâtons]],
				['7'] = [[Fouiller la cachette]],
				['1'] = [[Ouvrir la bouche d'égout avec un pied-de-biche]]
			}
		},
		['emba_craft_easy3'] = {
			['choice'] = {
				['5'] = [[Lancer les explosifs]]
			},
			['episode'] = {
				['1'] = [[L'ouverture du puits d’aération se trouve au milieu d'un enchevêtrement de plantes. C'est étrange qu'elles poussent ailleurs, mais fassent surface ici.]],
				['2'] = [[Le puits d’aération est très étroit, impossible de s'y introduire. Cela signifie que le lieu où poussent ces plantes restera un mystère. Pour l'instant, je ne peux que détruire l'ouverture du conduit à la surface.<new_line>Je n'ai pas d'autres options que de jeter quelque chose d'inflammable ou d'explosif.]],
				['3'] = [[La terre a grondé. Ça veut dire que quelque chose s'est écroulé. Mais les tiges et les racines noires semblent avoir tremblé toutes seules, et non pas à cause du grondement souterrain.<new_line><new_line>Le silence est revenu au bout de quelques secondes. Toute cette verdure va faner et mourir, maintenant que ses racines ont été coupées. Les feuilles et les pousses moisies semblent déjà s'assécher. Beau travail.]]
			}
		},
		['easter_buyer'] = {
			['choice'] = {
				['1'] = [[Je suis là pour la récompense.']],
				['2'] = [[On avait conclu un marché... Besoin que je te rafraîchisse la mémoire ?' (Poser votre main sur votre arme.)]],
				['3'] = [[On avait conclu un marché...']]
			},
			['episode'] = {
				['4'] = [[Du calme, du calme, je plaisantais ! Il ne faut pas s'énerver comme ça ! Tiens ! La voilà, ta récompense.']],
				['1'] = [[Bien le bonjour ! Besoin d'aide ?']],
				['2'] = [[Je ne sais pas de quoi vous parlez. Est-ce que vous voulez vendre quelque chose ?']],
				['3'] = [[C'est la première fois de ma vie que je vous vois ! Si vous n'avez rien à vendre, passez votre chemin.']]
			}
		},
		['halloween_boss_2022_6'] = {
			['episode'] = {
				['4'] = [[L'Âne m'a entendu sauter et a esquivé d'un pas sur le côté.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['5'] = [[J'ai entendu un fracas et un rugissement provenant de cabinets en bois que je n'avais même pas remarqués auparavant. La bête des marais !<new_line>La Faucheuse est alors apparue à côté des cabinets pour bloquer la porte avec sa faux.<new_line>'Eh bien, mortel, c'est la dernière fois que je t'aide ! Cours après l'Âne parlant, il s'enfuit ! Je vais rester et tenir la porte.']],
				['2'] = [[J'ai lancé une corde autour de son cou, mais l'Âne a réussi à la couper en la mordant ! Au même moment, j'ai vu quelque chose tomber de sa bouche.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['3'] = [[L'Âne a réussi à capter la grenade entre ses dents et à la jeter dans ma direction. Au même moment, j'ai vu quelque chose tomber de sa bouche.<new_line>Il a fui en appelant bruyamment à l'aide.]],
				['1'] = [[J'ai avancé vers une hutte dans le marais. L'Âne parlant s'y trouvait ! Il fouillait les buissons en maugréant :<new_line>'Fleurs bleues, épines rouges...'<new_line>Je suis assez proche de lui pour tenter ma chance.]]
			},
			['choice'] = {
				['4'] = [[Suivre l'Âne]],
				['1'] = [[Lancer une corde]],
				['2'] = [[Lancer une grenade]],
				['3'] = [[Sauter et l'attraper]]
			}
		},
		['frozen'] = {
			['episode'] = {
				['1'] = [[Alors que les brigands chargeaient les immenses blocs de glace sur leurs traîneaux, j'ai pu m'approcher. À travers la glace brumeuse, j'ai aperçu les silhouettes des captifs. Il faut vite les libérer.]]
			}
		},
		['santa_cyber'] = {
			['episode'] = {
				['4'] = [[Gruh graaah...']],
				['5'] = [[Est-ce qu'on a vraiment le choix ? Il faut se rendre à ce concert pour essayer de lui parler. Camarade Négoce, je crois que tu ferais mieux de rester ici.']],
				['2'] = [[Fais gaffe à ce que tu dis, Cyber Noël ! Quant à vous, mes amis, surveillez bien vos poches avant qu'il ne vole quoi que ce soit.']],
				['3'] = [[Va mourir ! Je vais prendre la tangente delta. Ceux qui veulent venir rocker avec moi n'ont qu'à me retrouver au concert.']],
				['1'] = [[Yo, les chooms, ça gaze ? Hé, qui a invité ce vendu corporatiste pour cette sauterie ?']]
			},
			['choice'] = {
				['1'] = [[Bonjour, nous avons besoin...']],
				['2'] = [[Il nous faut des informations sur...']],
				['3'] = [[Et maintenant ?']]
			}
		},
		['mummy'] = {
			['episode'] = {
				['4'] = [[Le passage du milieu débouche sur un dédale de couloirs. Me fiant à l'odeur des herbes pour m'orienter, je me fraye un chemin à travers ce labyrinthe. Mon odorat ne m'a pas trahi : après quelques minutes, j'arrive dans une grande salle, au milieu de laquelle se dresse un bloc de pierre.]],
				['5'] = [[Sur le socle en pierre gît une momie enveloppée de bandages. L'individu que j'ai suivi se tient dans un coin de la salle. Il murmure des paroles inintelligibles tout en remuant une décoction visqueuse et verdâtre dans un chaudron rouillé d'où émane une odeur d'ortie et de plantain.]],
				['2'] = [[L'homme balance sans ménagement le rat dans une cage avant de se diriger solennellement vers une maison dans laquelle il entre. Quelques instants plus tard, j'y pénètre à mon tour. Une fois à l'intérieur, je me retrouve devant un gigantesque couloir bordé de murs en pierre, avec pour seul éclairage, la lueur blafarde de torches moribondes. Frappé de stupeur, je scrute les ténèbres insondables de ce passage cyclopéen. Parmi les effluves putrides qui assaillent mes sens, je perçois l'arôme agréable d'herbes fraîches.]],
				['3'] = [[Je continue d'avancer pendant quelques minutes avant d'atteindre trois petits passages voûtés, tous plongés dans l'obscurité. Devrais-je aller tout droit, à gauche ou à droite ?]],
				['8'] = [[Je suis revenu à l'embranchement. Que vais-je faire à présent ? Je dois prendre une décision.]],
				['9'] = [[J'arrive presque immédiatement dans une petite salle dont le plafond est voûté. Au centre de celle-ci, je remarque un coffre.]],
				['6'] = [[Je m'approche légèrement pour mieux observer l'étrange spectacle, mais la momie remarque ma présence. Lorsqu'elle tourne la tête dans ma direction, son cou décharné produit un craquement sinistre. D'une voix rauque et dans une langue qui m'est inconnue, la créature vocifère des syllabes cabalistiques, sans nul doute quelque malédiction égyptienne ancestrale.]],
				['7'] = [[De l'autre côté du passage voûté, un escalier descend vers une petite salle truffée de cages où sont enfermés des rats. D'innombrables yeux rouges se posent sur moi. Réalisant ma situation, je fais volte-face, mais dans ma précipitation, mon pied bute contre une dalle en pierre. Une seconde plus tard, j'entends le clic des cages qui s'ouvrent, suivi des hurlements de rats voraces.]],
				['10'] = [[Dès que j'atteins le coffre, son couvercle s'entrouvre, exposant une rangée de crocs acérés. „Bas les pattes“, vocifère une voix bourrue et caverneuse provenant de l'intérieur de l'étrange caisse ouvragée et ornementée. Surpris, je sursaute et recule. „Votre première fois ? Vous m'apportez un rat ?“, questionne le coffre.]],
				['1'] = [[Approchant de ma destination, je tends l'oreille. À dix mètres devant moi, se dresse une personne vêtue d'une longue robe et d'un masque insolite en forme de bec. Des couinements désespérés provenant de sa direction semblent indiquer que l'inconnu vient d'attraper un rat.]],
				['11'] = [[„Très bien, apportez-le ici“, dit le coffre en ouvrant une gueule béante à la dentition intimidante. Sans oser hasarder un regard à l'intérieur de cette monstruosité buccale, j'y balance la carcasse du rat. Le coffre referme aussitôt la gueule et mâche bruyamment avant de me cracher dans la main quelques sacs de friandises. „Maintenant, du balai !„]],
				['12'] = [[„Alors, allez-vous-en et ne revenez pas“, dit le coffre, d'un ton menaçant avant de se refermer.]]
			},
			['choice'] = {
				['4'] = [[Aller à gauche]],
				['5'] = [[Aller à droite]],
				['2'] = [[Avancer dans le couloir]],
				['3'] = [[Aller tout droit]],
				['8'] = [[Rebrousser chemin]],
				['1'] = [[Suivre l'homme masqué]],
				['6'] = [[Continuer à regarder]],
				['7'] = [[Ouvrir le coffre]]
			}
		},
		['emba_navigator'] = {
			['choice'] = {
				['18'] = [[Entendu.']],
				['4'] = [[Pourquoi sortirait-il ?']],
				['5'] = [[Que pensez-vous du projet E.M.B.A. ?']],
				['2'] = [[Où se trouve exactement le briseur d'os ?']],
				['3'] = [[Il faut aller là-dedans et l'éliminer ?']],
				['8'] = [[Moi aussi. Quel est le plan d'action ?']],
				['9'] = [[Ça marche. Rendez-vous au labo.']],
				['6'] = [[Je suis prêt.']],
				['7'] = [[Mais il n'y a pas de réseau.']],
				['10'] = [[Pas encore. Est-ce qu'on suit le plan d'origine ?']],
				['11'] = [[Comment faire pour avancer ?']],
				['12'] = [[Le docteur a mentionné le Colonel Kourganov...']],
				['13'] = [[Son code d'accès a récemment été utilisé pour entrer dans le labo.']],
				['14'] = [[Bien, je suis prêt à continuer.']],
				['15'] = [[C'est un labo de quel genre ?']],
				['16'] = [[Ils ont un problème ?']],
				['17'] = [[Encore une question : pourquoi avons-nous besoin de fragments du mycélium ?']]
			},
			['episode'] = {
				['28'] = [[Au fait, l'accès aux cités des sciences n'est pas toujours facile à cause des problèmes de spores. Et puis, il y a aussi beaucoup de biomutants dans les friches ordinaires. Allez vers l'un des points indiqués et récupérez quelques fragments de mycélium.<new_line>Je parie que vous avez hâte de recueillir de la biomasse et d'obtenir une arme stylée du biologiste. J'ai marqué votre position sur la carte, allez vous battre un peu, combattant.']],
				['26'] = [[Ma foi, si on a fait le tour de la question, partons.']],
				['25'] = [[Très bien. D'autres questions ?']],
				['24'] = [[Ma foi, si on a fait le tour de la question, allons-y. Rendez-vous à la biostation du centre.']],
				['23'] = [[Ça alors ! Je peux vous dire une chose avec certitude : il valorise l'amitié et le dévouement. Il a dû se faire des amis dans son nouveau lieu d'affectation.<new_line>Kourganov est un combattant très expérimenté, il aurait bien pu survivre et continuer à aider les autres dans le cadre du projet E.M.B.A.']],
				['22'] = [[« Colonel-général, vous voulez dire. J'ai servi dans la division qu'il commandait.<new_line>Un gars intelligent, et surtout, très fidèle à ses camarades. Et puis, je me rappelle, il a été promu. Quelques années plus tard, j'ai lu dans les journaux qu'il prenait part au projet E.M.B.A. pour l'armée.']],
				['21'] = [[Vous savez, allons-y quand même, à l'ancienne. Pompez la biomasse hors du réservoir central. Ça a déjà marché pour dégager le chemin, si ça se trouve, ça fera l'affaire ici aussi.<new_line>Et je vais jeter un autre coup d'œil à l'étage supérieur du labo. Je vais voir si je peux pirater leur panneau de contrôle pour voir ce qui nous attend en bas.']],
				['20'] = [[Non, à mon avis, ça ne marchera pas. Cette fois, la biostation n'est pas équipée de tourelle. De toute évidence, une petite surprise nous attend.']],
				['19'] = [[Tiens, vous revoilà, l'ami ! J'espère que votre cerveau n'a pas été mis en bouillie à force de résoudre les problèmes scientifiques du monde entier avec le docteur.']],
				['18'] = [[Il n'y a qu'une seule tourelle ici, mais elle a plus d'un tour dans son sac. Ou plutôt, deux canons dans sa structure. Pour bénéficier d'un véritable avantage tactique, vous me direz comment tirer.<new_line>Mais d'abord, pompez la biomasse hors du réservoir central. Comme la dernière fois.']],
				['17'] = [[Vous voilà ! Nos petites escapades commençaient à me manquer.']],
				['4'] = [[Beau travail, combattant. Dites au Dr Isaev que tout s'est bien passé. Ensuite, revenez me voir. Nous organiserons une inspection de la cité des sciences, hors site. Vous aurez les détails plus tard, allez-y.']],
				['5'] = [[Salut à vous ! Prêt à partir à la recherche de la boîte noire ? Laissez-moi vous faire un briefing.']],
				['2'] = [[Les habitants du coin n'aiment pas les sommités scientifiques, il faut leur faire changer d'avis. Honnêtement, c'est un miracle que ce camp ait survécu : certes, il y a une cité des sciences à quelques centaines de kilomètres, mais j'ai trouvé un laboratoire de terrain abandonné juste à côté.<new_line>Les biomutants sortent de là comme des fourmis d'une fourmilière.']],
				['3'] = [[Ça a sans doute quelque chose à voir avec ce mystérieux projet recherché par le camarade Isaev. J'ai fait un petit repérage des lieux... j'ai failli y laisser ma peau.<new_line>Une archive de documents a survécu, si j'ai bien compris. Je parie qu'ils seront d'une grande utilité. Bien évidemment, éliminez tous les mutants. Ensuite, la colonie nous accueillera en héros.<new_line>L'entrée du laboratoire est bien camouflée, mais je vais vous la marquer sur la carte. Et voilà, à vous de jouer !']],
				['8'] = [[Bien, passons aux choses sérieuses. Ce qui nous intéresse en ce moment, c'est la boîte noire. Elle devrait se trouver au centre de la cité des sciences, à la biostation, sous un grand dôme. Vous ne pouvez la récupérer qu'en disposant du plus haut niveau d'accès.<new_line>Les codes d'accès sont consignés dans les documents secrets du projet E.M.B.A.. Le code de ce laboratoire se trouvait dans les documents que vous avez trouvés dans ce bunker, vous vous souvenez ? Je l'ai déchiffré et je l'ai noté pour vous.<new_line>Oh, et n'oubliez pas d'enfiler votre kit AIDA dans les cités des sciences. Les spores volent de partout, et sans ça, vous ne ferez pas long feu au combat. Alors, pionnier, prêt à partir ?'<new_line>(Tend un bout de papier avec le code)]],
				['9'] = [[Beau travail ! Apportez la boîte noire au biologiste. Mais pour les recherches au labo, vous aurez sans doute besoin de ces fameux... sporiflores et génofruits. Vous pouvez en trouver dans divers bâtiments de la cité des sciences.<new_line>Ma foi, c'est tout. L'essentiel est de ne pas oublier de porter une combinaison spéciale AIDA, si vous comptez vous battre dans la cité des sciences. Et faites le plein d'herbicide, vous pouvez en obtenir auprès des scientifiques du service Consommables.']],
				['6'] = [[Bien, commençons. Bien des choses insolites et utiles se trouvent dans les cités des sciences. En plus du biolaboratoire au centre, il y a d'autres bâtiments.<new_line>Ceux couverts de fleurs rouges sont généralement habités par des biomutants. Nous avons justement aussi besoin de ces fleurs rouges, la sporiflore, pour les recherches au laboratoire de ProgrèS.<new_line>Vous aurez également besoin de ces fruits jaunes que les scientifiques appellent génofruits pour améliorer le laboratoire. Vous en trouverez dans d'autres bâtiments où les biomutants ne rôdent jamais. Mais il y a un autre problème.']],
				['7'] = [[Ce n'est pas extrêmement dangereux, mais tout est recouvert de végétation. Vous aurez besoin d'herbicide pour vous faufiler à l'intérieur et récupérer les génofruits.<new_line>Vous pouvez en obtenir auprès des archivistes contre des disquettes de données. Passez au service des Consommables.']],
				['10'] = [[Oh, c'est ce qui provient des biomutants ? Eh bien, vous pouvez extraire de la biomasse du mycélium. Vous en aurez besoin pour créer toutes sortes de choses utiles dans le laboratoire de ProgrèS.<new_line>On peut extraire de la biomasse des fragments de mycélium « à l'ancienne », avec un couteau. Mais c'est plus efficace d'utiliser ce bidule high-tech : l'extracteur. Avec ça, vous obtiendrez plus de biomasse.<new_line>L'extracteur, tout comme les kits AIDA et l'herbicide, peut aussi être obtenu en échange de disquettes de données avec les archivistes. N'oubliez pas d'aller faire un tour dans leur service Consommables.']],
				['11'] = [[Salut ! Pendant que vous écoutiez la conférence du médecin, j'étais en repérage. Et je n'ai pas enfilé des perles : j'ai piraté le système de contrôle des tourelles, pour vous apporter de la puissance de feu en appui.']],
				['14'] = [[Ce n'est pas si simple. Je n'ai pas pu entrer dans la partie souterraine de la biostation. Et on dirait bien que le briseur d'os se trouve là, avec la boîte noire. Il faut attirer cette créature au dernier étage du labo et la faire passer devant les tourelles, de manière à ce que la voie soit libre pour accéder à la boîte noire.']],
				['12'] = [[D'après mes données, il se trouve au centre de la cité des sciences. À côté du dôme de la biostation, il y a un énorme réservoir rempli de biomasse. Le briseur d'os s'est installé sous la biostation et se gave dans les réserves.']],
				['13'] = [[J'ai pensé à tout... ma ne me sert pas qu'à manger des flocons d'avoine. Il y a une petite citerne, à la périphérie de la ville, avec des pompes. Rendez-vous là-bas et activez les pompes.<new_line>Elles aspireront la biomasse du réservoir central de la biostation jusqu'à ce petit réservoir. Privé de nourriture, le briseur d'os rampera à l'étage pour s'inviter à notre table.<new_line>N'oubliez pas notre tactique : il faut attirer le briseur d'os dans la ligne de tir des tourelles !']],
				['1'] = [[Je suis le navigateur de l'unité opérationnelle Vecteur. Nous aidons l'expédition ProgrèS à accomplir leur mission. Merci de vous joindre à nous.']],
				['15'] = [[De mon point de vue, ce qui s'est passé dans toutes les biostations n'était pas accidentel. Croyez-en mon expérience en la matière : un tel pic d'activité synchrone n'est possible qu'en présence d'un signal radio puissant.']],
				['16'] = [[Justement. Tout le réseau de radiodiffusion est tombé en panne après les frappes nucléaires. Chez Vecteur, nous sommes en pleine réhabilitation du réseau dans les friches. Ce qui a pu générer un tel signal, et de quelle manière reste un mystère.']]
			}
		},
		['brick_fall'] = {
			['episode'] = {
				['2'] = [[Après m'être débarrassé des rats, j'ai trouvé quelques objets utiles dans les débris du bâtiment.]],
				['1'] = [[Alors que je traversais les rues désertes de la ville, le mur en briques de l'immeuble bancal s'est effondré. Manque de chance, j'étais juste à côté et j'ai reçu une brique en plein sur le crâne.]]
			},
			['choice'] = {
				['2'] = [[Prendre la brique comme souvenir]],
				['1'] = [[Examiner les débris]]
			}
		},
		['comrade_in_trouble_fire'] = {
			['episode'] = {
				['1'] = [[Le feu est presque éteint, mais du bois sec devrait s'enflammer rapidement au contact des braises.]]
			}
		},
		['quest_order'] = {
			['choice'] = {
				['2'] = [[Donner la commande et accepter la récompense]],
				['1'] = [[Pas maintenant']]
			},
			['episode'] = {
				['1'] = [[Le barman m'a appelé de derrière le comptoir au moment où je venais d'entrer.<new_line>'Alors, vous avez tout ?']]
			}
		},
		['catch_thief_extra'] = {
			['choice'] = {
				['2'] = [[Ne pas intervenir et revenir pour la récompense]],
				['1'] = [[Abandonner la caisse et revenir pour la récompense]]
			},
			['episode'] = {
				['1'] = [[Le voleur a caché le butin dans un ravin isolé. J'ai tout mis dans une caisse qui se trouvait sur place. Je dois la récupérer et rentrer à l'entrepôt.]],
				['2'] = [[J'ai atteint le camp des bandits. Ils triaient le butin et ne m'ont pas remarqué tout de suite :<new_line>'Hein ? Quel entrepôt ? On t'a pris tout ce que t'avais, alors avance, le vieux !']],
				['3'] = [[J'ai laissé les bandits encore vivants emporter les corps de leurs camarades tombés au combat. Après une raclée comme celle-là, ils ne reviendront pas de sitôt à l'entrepôt, ni même au camp.]]
			}
		},
		['new_year_boss_6_2'] = {
			['episode'] = {
				['4'] = [[Bien vu ! Pour que les gens puissent fêter Noël, il va falloir se retrousser les manches. Je ne pense pas que le Père Noël soit capable de faire un tel travail.']],
				['5'] = [[Tu as fait du bon travail en assumant mes responsabilités si longtemps. Certainement mieux que moi... Et Nounours a raison : la seule chose pour laquelle je suis bon maintenant, c'est la retraite.']],
				['2'] = [[C'est toujours comme ça, choom.']],
				['3'] = [[Tout le monde s'excuse, toujours ! Mais qui s'occupe de tout régler, hein ?! Alors, Noël aura lieu tout seul, sans le Père Noël ?']],
				['8'] = [[Gruh Nogrr !']],
				['1'] = [[Les amis, je ne sais pas quoi dire pour ma défense. Au départ, j'ai voulu faire le bien. Mais ensuite... Pour tout ce qui est arrivé, je n'ai absolument aucune excuse.']],
				['6'] = [[On va t'aider, mon vieux. Je n'en reviens pas de dire ça, mais assez parlé affaires !<new_line>On mérite bien de faire une petite fête entre nous. C'est l'occasion ou jamais !']],
				['7'] = [[D'accord. On va fêter ça, avant de se remettre au travail ! Vous aimez faire la fête mais, comme toujours, c'est moi qui dois faire avancer les choses. Quoi qu'il en soit, joyeux Noël à tous !']]
			},
			['choice'] = {
				['4'] = [[Mais juste un an !']],
				['5'] = [[C'est clair.']],
				['2'] = [[Nous avons un tas de Pères à disposition.']],
				['3'] = [[De quoi parlez-vous ?']],
				['1'] = [[L'essentiel, c'est d'être revenu à la raison.']],
				['6'] = [[Joyeux Noël !']],
				['7'] = [[Hourra !']]
			}
		},
		['lost_soul'] = {
			['episode'] = {
				['4'] = [[Le sentier mène à une clairière, où plusieurs bruits proviennent simultanément de différents endroits. Un craquement de glace au niveau du lac, un hurlement ressemblant à celui d'un loup dont l'origine semble être le fin fond de la forêt et (apparemment) un coup de feu provenant de la colline. Où aller ?]],
				['5'] = [[La source du cri s'est avérée être un oiseau perché sur un arbre. Il me jette un coup d'œil et recommence à crier. Je dois retourner vite au camp.]],
				['2'] = [[Les empreintes s'enfoncent dans les bois et reviennent à la tente.]],
				['3'] = [[Mon intuition me dit que je touche au but. Le chemin en face de moi se scinde encore en deux. Les empreintes se dirigent dans un sens, mais dans l'autre, il y a un morceau de tissu accroché à un buisson truffé d'épines. Par où est passé l'homme qui a disparu ?]],
				['8'] = [[Les hurlements étaient en fait juste le vent. Je ferais mieux de revenir vite dans la clairière.]],
				['13'] = [[Je trouve l'homme au sommet de l'arbre, au pied duquel plusieurs loups font les cent pas. Les animaux tournent la tête dans ma direction.]],
				['11'] = [[Au centre du lac glacé, j'aperçois un énorme trou dans lequel l'homme est tombé. Je dois l'aider !]],
				['10'] = [[En me frayant un chemin à travers les buissons et arbustes, j'ai, moi aussi, arraché mes vêtements... avant de me retrouver de nouveau au camp.]],
				['7'] = [[Une fois que j'ai secouru l'homme, nous retournons au camp pour nous réchauffer au coin du feu. L'homme me remercie et nous partons chacun de notre côté.]],
				['6'] = [[En grimpant la colline, j'aperçois l'homme de l'autre côté. Il tire sur un ours qui s'approche de lui en zigzaguant entre les arbres. Je ferais mieux d'intervenir !]],
				['1'] = [[J'ai trouvé le camp. La tente gisait sur la neige et le feu s'était éteint, mais les cendres étaient encore chaudes. A priori, l'occupant des lieux venait tout juste de partir. Soudain, j'ai entendu un cri provenant de la gauche, mais les empreintes quittant la droite se dirigent vers la droite. Quelle direction dois-je prendre ?]],
				['12'] = [[Arrivé au sommet de la colline, je ne trouve rien. Le bruit devait sûrement être une branche qui a craqué.]],
				['9'] = [[Il n'y a rien ni personne au lac. Le craquement était en fait la glace sur la berge. Je ferais mieux de retourner dans la clairière.]]
			},
			['choice'] = {
				['4'] = [[Courir dans la forêt]],
				['5'] = [[Courir jusqu'au lac]],
				['2'] = [[Suivre le cri]],
				['3'] = [[Morceau de tissu]],
				['8'] = [[Courir à son aide]],
				['1'] = [[Suivre les empreintes]],
				['6'] = [[Courir jusqu'à la colline]],
				['7'] = [[Lui envoyer une corde]]
			}
		},
