		['new_year_boss_4_4'] = {
			['episode'] = {
				['4'] = [[L'arme carbure à la magie. Si on pouvait la pirater, y aurait pas de problème.<new_line>Après, pour ce qui est de quoi faire avec la magie de Noël, j'en sais rien... mais je connais un magicien. Ouaip !']],
				['5'] = [[J'ai appris plein de trucs grâce à lui, quand j'étais jeune. S'il pouvait rappliquer, on aurait une chance !<new_line>Bon, t'es prêt à faire tes trucs vaudous et à décorer le sapin ?']],
				['2'] = [[C'est horrible ! Tout ce qu'on a fabriqué va servir à augmenter la puissance de Fouettard !']],
				['3'] = [[Tu sais où tu peux te la mettre, ton industrie toute entière ?']],
				['1'] = [[Ils ont fabriqué une arme qui capte la magie de Noël. Fouettard n'a plus besoin de faire brûler des cadeaux en grande pompe.<new_line>Il suffit juste de pointer l'arme dessus, et bim : ça t'aspire toute la magie des fêtes ! Il peut le faire à l'échelle industrielle. Voire à l'échelle mondiale !']]
			},
			['choice'] = {
				['1'] = [[Hé, ça suffit ! Comment faire pour l'arrêter ?']]
			}
		},
		['halloween_boss_2022_3'] = {
			['episode'] = {
				['1'] = [[Enfin, quelqu'un vole à mon secours ! Des vampires nous ont attaqués, et je suis la seule à avoir survécu ! Par pitié, aidez-moi à rentrer chez moi. Vite, avant qu'ils reviennent finir le travail !']]
