				['2'] = [[Nous avons vaincu l'ennemi, mais...']],
				['1'] = [[Nous les avons écrasés']]
			},
			['episode'] = {
				['1'] = [[Dites-moi, comment ça s'est passé ?']],
				['2'] = [[Je n'ai jamais douté de vous. Tenez, c'est mérité.'<new_line>Le chef des gardes m'a récompensé pour mon aide.]],
				['3'] = [[Le chef des gardes a tout compris en me voyant.<new_line>'Vous avez fait votre possible.'<new_line>Il m'a donné ma récompense.]]
