		['wounded_bandit'] = {
			['episode'] = {
				['4'] = [[Quand je lui ai dit que je voulais simplement l'aider, il m'a jeté un regard méfiant. Je lui ai alors montré mon matériel médical. Il a posé son arme et m'a laissé m'approcher.]],
				['5'] = [[Je lui ai donné des antidouleurs, et j'ai traité ses blessures, puis je l'ai aidé à les panser. Très surpris, le bandit m'a remercié en me dressant une carte où est indiquée l'une de ses cachettes.]],
				['2'] = [[Quand le bandit a compris mes intentions, il a ouvert le feu, mais la balle, manquant sa cible, ne m'a touché qu'à la jambe gauche. La balle que j'ai tirée, quant à elle, n'a pas manqué sa cible. Le bandit est mort sur le coup.]],
				['3'] = [[Me précipitant pour me cacher derrière le mur, j'ai senti une balle me transpercer l'épaule gauche. Si je veux sortir vivant de cet immeuble, il va falloir que je m'occupe de ce bandit !]],
				['1'] = [[Je suis tombé nez à nez avec un bandit dans un immeuble. Il était par terre, contre un mur, essayant tant bien que mal de panser ses nombreuses blessures. Quand l'homme m'a aperçu, il a brandi son pistolet et à bout de souffle, m'a menacé d'une voix rauque, à peine audible.]],
				['6'] = [[Lorsque je me suis approché, j'ai remarqué que le bandit était sans défense. Qu'importe, il est mort désormais, et je n'ai plus qu'à fouiller son corps.]]
			},
			['choice'] = {
				['4'] = [[L'aider]],
				['5'] = [[Le désarmer et tirer]],
				['2'] = [[S'abriter derrière le mur]],
				['3'] = [[Offrir votre aide]],
				['1'] = [[Lui tirer dessus]],
				['6'] = [[Le désarmer et le frapper d'un coup de hache]],
				['7'] = [[Fouiller le bandit]]
			}
		},
		['cold_footprints'] = {
			['episode'] = {
				['4'] = [[L'image d'une grande femme, blanche de la tête aux pieds, un bâton dans la main, s'est matérialisée dans le miroir. Elle est assise sur un trône de glace et à ses côtés, enchaîné par des entraves glacées, gît un vieillard à la barbe grise. C'est Ded Moroz !]],
				['5'] = [[Vous les avez tous tués, n'est-ce pas ?, dit soudainement la femme, lançant à travers le miroir un regard glacial dans ma direction. «Vous autres n'êtes bons qu'à ça, après tout. Mais qu'importe, bientôt un hiver perpétuel s'abattra sur votre monde pitoyable et mettra fin à toutes les souffrances.» D'un coup de bâton ferme, la femme frappe le sol. Le miroir se fend et je ne vois plus rien.]],
				['2'] = [[Après m'être occupé des brigands des glaces, j'en aperçois un dernier surgir de la tente centrale. Il est grand et armé d'une hache surdimensionnée. C'est probablement le chef. C'est parti.]],
				['3'] = [[Lorsque je suis entré dans la tente du chef, j'ai tout de suite remarqué un grand miroir de glace bien en évidence. Comme envoûté par cet artefact, je m'en suis approché et j'en ai effleuré la surface incroyablement lisse.]],
				['1'] = [[Une gigantesque tente confectionnée à l'aide de peaux d'animaux recouvertes d'une épaisse couche de neige, et quelques tentes plus petites. La zone est protégée par un rempart de neige vertigineux. Voilà à quoi ressemble le camp des brigands des glaces. Ils m'ont déjà repéré. Le moment est venu d'attaquer.]]
			},
			['choice'] = {
				['1'] = [[Qu'est-ce qui se passe ?]]
			}
		},
		['new_year_boss_3_2'] = {
			['episode'] = {
				['4'] = [[Père Nounours leva la main et fit un pas vers Fouettard.<new_line>'Attendez, attendez ! Ce n'est pas ce que nous avons convenu. Discutons de la manière...'<new_line><new_line>Un lourd craquement retentit. Père Nounours fut frappé à la tête et il tomba au sol.]],
				['5'] = [[Ha, encore toi ? Hé, les mecs, occupez-vous de ces mauviettes ! Brûlez et dévorez la moitié des cadeaux. Jetez le reste dans le bunker. Je reviendrai vérifier !'<new_line><new_line>Père Fouettard posa l'étrange bonhomme de neige sur sa moto, sauta dessus et appuya sur l'accélérateur. Toute sa bande fondit sur moi.]],
				['2'] = [[Bougre d'âne ! Tu veux parler de redistribution ? Mes gars ont besoin de plus de cadeaux ! De tous les cadeaux, de toute leur énergie ! Noël brûlera pour nous donner plus de pouvoir !']],
				['3'] = [[Ouah, Fouettard. Ils ont apporté des tas de trucs. Y a même de quoi essayer notre arme secrète.']],
				['8'] = [[Oui, j'ai fait une belle gaffe. Désolé, camarades. Cette révélation d'une arme secrète fait froid dans le dos. Il faut découvrir de quoi ils parlent. Nous avons besoin d'un espion.']],
				['9'] = [[Oh, non. Pas cet énergumène arrogant ! On ne peut pas faire sans lui ?']],
				['6'] = [[Pendant le combat, deux de ces vauriens ont emporté tous les cadeaux ! Je n'ai rien pu faire.']],
				['7'] = [[C'est vrai ! Nounours, ça va ? Tu as entendu ? S'ils venaient en plus à mettre la main sur une arme secrète... les fêtes seraient finies, et nous aussi.']],
				['1'] = [[Père Fouettard, je sais que tu as travaillé sans salaire et qu'on t'a privé des fruits de ton labeur. Nous proposons une distribution plus équitable des...']],
				['10'] = [[Nous n'avons même pas pu sauver les cadeaux ! Non, impossible de se passer de lui.<new_line>Ne pose pas de question, camarade. Le plus simple est encore de le rencontrer. Il faudra encore une fois décorer le sapin pour faire venir un autre visiteur.']]
			},
			['choice'] = {
				['4'] = [[Ils voulaient l'essayer.']],
				['5'] = [[Qui ?']],
				['2'] = [[Cette fois, je vais vous massacrer !']],
				['3'] = [[Ils ont parlé d'une arme, non ?']],
				['1'] = [[Qui c'est, celui-là ?']],
				['6'] = [[Mais de qui parlez-vous ?!']],
				['7'] = [[Soit.']]
			}
		},
		['scientist_rescue_quest'] = {
			['choice'] = {
				['4'] = [['On rafistolera ça en cours de route.']],
				['5'] = [[Se rendre au camp]],
				['2'] = [['Tout est prêt, on décolle.']],
				['3'] = [['Il y a beaucoup de bandits, là-bas ?']],
				['1'] = [['Prêt à rentrer ?']],
				['6'] = [[Dévisser avec des outils]]
			},
			['episode'] = {
				['4'] = [['Pas tant que ça, je pense. Laissez-moi vous montrer où se trouve leur camp sur la carte, et vous déciderez de la marche à suivre : se rendre sur place ou rentrer.']],
				['5'] = [['Je suis prêt, bien sûr, mais il y a une chose. Des bandits sont passés par ici. Je les ai accueillis comme il se doit, mais pour faire court, j'ai une petite blessure. Je ne peux pas trop me plaindre.']],
				['2'] = [['D'accord, laissez-moi juste mettre les échantillons de sol dans mon sac à dos, et je serai à votre service. Je peux garder mon arme attachée sur le côté ?']],
				['3'] = [['Voilà le topo. Un gang a déjà réussi à me voler un appareil. On peut rentrer directement au camp, mais ce serait nous apporter une aide précieuse de récupérer cet équipement avant.']],
				['8'] = [['Je suis plus ou moins prêt. J'ai pris tous les instruments, ou presque. L'un d'eux m'a posé problème, sur le toit. J'ai essayé de le dévisser mais ça n'a fait qu'empirer les choses.<new_line><new_line>Si vous avez le temps, vous pourriez tenter de le démonter ? L'essentiel, c'est qu'on ramène le reste du matériel au camp, alors je me fie entièrement à votre expérience.]],
				['9'] = [[Quelqu'un s'est assuré que l'appareil soit fermement riveté au toit. En plus, les fixations sont un peu rouillées. Ça promet d'être laborieux.]],
				['6'] = [['Désolé, mais la médecine de combat... je préfère éviter. J'ai des instruments stérilisés ici, tout est plus ou moins prêt. Et je peux toujours attendre qu'on rejoigne notre centre médical. C'est juste une égratignure, vraiment.']],
				['7'] = [[Le scientifique attend patiemment avec son sac à dos rempli de matériel, à l'exception de la trousse de secours.]],
				['1'] = [[J'ai frappé à la porte du poste de garde. Le scientifique a ouvert le verrou d'un coup avant de pointer immédiatement un fusil de chasse sur moi.<new_line><new_line>Il a fallu du temps pour le convaincre que je venais du camp.]]
			}
		},
		['reinforcement_quest'] = {
			['choice'] = {
				['4'] = [[Aider à supprimer la tanière des mutants]],
				['5'] = [[Aider à exterminer les bandits]],
				['2'] = [[Aider à attaquer le camp]],
				['3'] = [[Aider à défendre le camp]],
				['8'] = [[Les laisser et rentrer à la base]],
				['9'] = [[Se préparer]],
				['6'] = [[Fouiller les cadavres et partir]],
				['7'] = [[Utiliser la trousse de soins]]
			},
			['episode'] = {
				['4'] = [[Ce n'était pas juste des bandits, mais des mutants. Mais les hommes ont fait du bon travail en les rattrapant avant d'atteindre le camp.]],
				['5'] = [[Les bandits sont entrés dans le camp, mais les hommes ont dégainé à temps et les ont piégés dans le dépôt de carburant.]],
				['2'] = [[Les hommes ont dégainé à temps. Ils ont capturé les bandits en route vers la base.]],
				['3'] = [[Ce n'était pas juste des bandits, mais des mutants. Ils avaient déjà fait irruption dans le camp.]],
				['8'] = [[J'ai apporté les premiers soins. Maintenant, je dois filer.]],
				['1'] = [[J'ai fait aussi vite que j'ai pu. C'était la crise : les bandits avaient déjà fait irruption dans le camp.]],
				['6'] = [[La victoire est à nous. Les hommes m'ont remercié de mon aide.]],
				['7'] = [[Nous avons gagné, malgré quelques blessés. Il faut vite les soigner, ils risquent de mourir.]]
