		['hunt_quest'] = {
			['choice'] = {
				['4'] = [[Apporter le reste de viande au client]],
				['5'] = [[Livrer la commande]],
				['2'] = [[Besoin de quelque chose ?']],
				['3'] = [[Je peux vous donner un peu de viande']],
				['8'] = [[Voilà un peu de nourriture en conserve']],
				['9'] = [[Je n'ai rien pour vous']],
				['7'] = [[Tout est prêt']],
				['1'] = [[Rendre le butin]],
				['12'] = [[Aller voir le client]],
				['10'] = [[Tenter de négocier]]
			},
			['episode'] = {
				['4'] = [[La femme m'a regardé fixement, sidérée.<new_line>'Merci ! Vous êtes bien gentil !']],
				['5'] = [[Le client a froncé les sourcils.<new_line>'Non, un marché est un marché. Il n'y a pas assez de viande. Je ne donnerai pas un kopek.']],
				['3'] = [[La femme m'a regardé avec des yeux pleins de désespoir.<new_line>'Pourriez-vous... nous donner de quoi manger ?']],
				['8'] = [[Le visage de la femme s'est assombri.<new_line>'Désolée du dérangement.'<new_line>]],
				['9'] = [[Le client a hoché de la tête, satisfait.<new_line>'Alors, tout s'est bien passé ?']],
				['6'] = [[Mon patron m'a dévisagé attentivement.<new_line>'C'est fait ?']],
				['7'] = [[La femme m'a salué de la tête.<new_line>'Merci. Avec ça, je devrais tenir quelques jours...'<new_line>]],
				['1'] = [[Une femme s'est approchée de moi, un enfant dans les bras. Elle n'a rien dit, mais elle s'est placée juste à côté de moi, les yeux vers le sol.]],
				['11'] = [[On avait conclu un marché. Donnez la viande, ou dégagez !']],
				['10'] = [[Ça va. Je vous paierai un peu plus.']]
			}
		},
		['halloween_boss_2022_6_2'] = {
			['episode'] = {
				['4'] = [[J'ai rejoint la rivière. Bizarrement, je n'ai pas le souvenir de l'avoir vue sur une carte.<new_line>La Faucheuse, qui se tenait sur l'autre rive, m'a fait signe :<new_line>'Il y a un gué ici. Traverse, dépêche-toi, l'Âne s'en va !'<new_line>Sur ces mots, j'ai entendu des branches craquer juste derrière moi.]],
				['5'] = [[J'ai vu encore plus de monstres sortir du bois ! Contre une telle meute, je n'avais aucune chance de m'en sortir. J'ai dû traverser la rivière pour gagner la rive opposée.]],
				['2'] = [[La forêt devenait plus sombre à chacun de mes pas et les yeux des crânes brillaient, rouges. D'un coup, le sentier fit un virage et je manquai de percuter une bande hétéroclite de monstres.]],
				['3'] = [[Tous ces ennuis pour un Âne galeux...<new_line>Mais j'entends ses braiments, pas très loin. Cette fois, il ne m'échappera pas !]],
				['8'] = [[Oublie le contrat—j'ai d'autres objectifs en tête. Tes pouvoirs démoniaques sont devenus colossaux. La plupart des Faucheuses ne peuvent que rêver de moissonner une âme aussi puissante.']],
				['9'] = [[J'en doute beaucoup, tu sais. Tu as traversé la Rivière de l'oubli. Et maintenant, nos amis communs vont venir ici et t'anéantir.'<new_line>D'un claquement de doigts de la Faucheuse, des portails sont apparus tout autour en projetant des étincelles électriques.]],
				['6'] = [[Cette berge était sombre et déserte, en réalité, comme si elle venait d'un autre monde. Un brouillard épais est tombé sur la rivière, d'un seul coup. Cerise sur le gâteau, il s'est aussi mis à pleuvoir. Un éclair a déchiré le ciel, et un orage a éclaté.<new_line>L'Âne demeurait introuvable. La Faucheuse, elle, se tenait devant moi.]],
				['7'] = [[Ça, mortel, c'est mon monde. Bienvenue ! Et ne t'inquiète pas pour l'Âne, il est exactement là où il devrait être.<new_line>Vois-tu, toutes ces bêtes et tous ces monstres sont de gros poissons, bien sûr... mais personnellement, je veux attraper un vrai requin.']],
				['15'] = [[Non, tu as également droit à une compensation—tiens. Au fait, j'ai d'autres affaires à régler dans ton monde, alors n'hésite pas à passer voir ma marchandise si tu as besoin de quelque chose.']],
				['14'] = [[Ne t'inquiète pas, l'ami. Je suis une autre Faucheuse. On m'a envoyée ici pour remplacer mon collègue véreux. Cette histoire de fraude, de contrats et de sous-traitance... quelle honte pour la profession.<new_line>Au nom du monde des morts, je m'excuse. Je te renvoie immédiatement dans ton monde natal.']],
				['13'] = [[En me voyant tendre la main vers mon Gigawattor, la Faucheuse s'est mise à siffler avec colère : 'Crapule ! Qu'importe, tu ne fermeras pas tous mes portails. Simple mortel, tu n'es pas assez fort !']],
				['12'] = [[Comme pour souligner l'instant tragique, des éclairs ont retenti dans un grondement terrible. Et l'un des portails a été frappé par la foudre ! Dans un flash aveuglant, il a disparu. Pas mal ! Ainsi donc, mon Gigawattor peut détruire des portails...]],
				['1'] = [[Étant à mi-chemin de ma vie, je me trouvai dans une forêt obscure. Au bord du sentier se trouvaient des crânes empalés sur des piquets, et des voix effrayantes hurlaient derrière les arbres.]],
				['11'] = [[Que diable... Comment ?! Au dos de la même feuille ?! Mais c'est moi qui te l'ai donnée...']],
				['10'] = [[En effet, tes forces ont décuplé depuis le premier contrat que je t'ai donné. Mais il y a un léger imprévu.<new_line>J'ai commencé à comploter contre toi quand j'ai vu ton nom listé dans le Livre de la mort. En d'autres termes... Nul n'échappe à son destin, tu te rappelles ?'<new_line>(La Faucheuse sort le Livre et commence à le feuilleter)]]
			},
			['choice'] = {
				['4'] = [[Je vous enquiquine, et je rentre à ma maison.']],
				['5'] = [[Je suis imbattable !']],
				['2'] = [[Où sommes-nous ? Et où est passé l'Âne ?']],
				['3'] = [[De quoi parlez-vous ? J'ai rempli le contrat, au final ?']],
				['8'] = [[Dégainer l'arme]],
				['9'] = [[Quoi, c'est tout ?']],
				['6'] = [[C'est ça que vous cherchez ?' (Sortir la page de la liste des victimes du Monstre de Frankenstein)]],
				['7'] = [[Contemple la puissance des paperasses et de la bureaucratie !' (Déchirer la page)]],
				['1'] = [[Traverser à gué]],
				['10'] = [[Continuer d'avancer]]
			}
		},
		['herb_collect'] = {
			['episode'] = {
				['1'] = [[Snegurochka était assise dans la clairière. De nombreuses bêtes de la forêt étaient également là : des oiseaux, de petites créatures et même deux cerfs. Ils avaient tous besoin d'aide. Snegurochka m'a indiqué un endroit, juste à côté d'elle, où déposer les herbes.]]
			},
			['choice'] = {
				['1'] = [[give herbes]]
			}
		},
		['halloween_energy_easy'] = {
			['choice'] = {
				['4'] = [[D'accord.' (Aller voir l'agriculteur)]],
				['5'] = [[Pourquoi leur venir en aide ?']],
				['2'] = [[Pas le temps.' (Partir)]],
				['3'] = [[Se lancer]],
				['1'] = [[De la maladresse, hein ? Bien, je vous aiderai.']],
				['6'] = [[Est-ce qu'on a retrouvé le commandant ?']]
			},
			['episode'] = {
				['4'] = [[Beau travail. Il ne reste plus qu'à fertiliser le sol. Vous pouvez demander de l'engrais à l'agriculteur ? Il devrait être chez lui.']],
				['5'] = [[N'oubliez pas de bien fermer. Cet engrais vaut son pesant d'or pour les têtes-de-citrouilles. J'y ai travaillé jour et nuit.']],
				['2'] = [[Merci beaucoup ! D'abord, il faut creuser un jardin. C'est quand vous voulez pour commencer.']],
				['3'] = [[Parfait ! Maintenant, il faut planter les graines. C'est quand vous voulez pour commencer.']],
				['8'] = [[Non... Nous avons peut-être fait une erreur. Il faudra réessayer. Merci beaucoup de votre aide.']],
				['9'] = [[D'abord, il faut creuser un jardin. C'est quand vous voulez pour commencer.']],
				['6'] = [[Ce sont les affaires, et les affaires passent avant tout. Bien, voilà. Au travail, maintenant.']],
				['10'] = [[Maintenant, il faut planter les graines. C'est quand vous voulez pour commencer.']],
				['1'] = [[Bonjour, étranger ! Vous pouvez nous aider ? Notre commandant nous manque vraiment. Sans lui, tout est difficile. Nous voulons en faire pousser un autre. Nous avons déjà trouvé des graines et un jardin, mais notre maladresse nous empêche d'y arriver.']],
				['7'] = [[Ô Empereur au visage solaire, reviens vers nous. Pardonne-nous de t'avoir abandonné. Nous avons besoin de toi. Reviens, et nous te serons fidèles !'<new_line><new_line>Le rituel a duré plusieurs minutes, après quoi les têtes-de-citrouilles se sont dispersées, l'air abattu.<new_line>]],
				['11'] = [[Tout est prêt, ou presque. Il faut fertiliser le sol.']]
			}
		},
		['lair_chiken'] = {
			['choice'] = {
				['4'] = [[S'échapper]],
				['1'] = [[S'approcher]],
				['2'] = [[Retirer les mains des oreilles]],
				['3'] = [[Effrayer]]
			},
			['episode'] = {
				['4'] = [[Après le combat, j'ai exploré la ferme de fond en comble. Les amas sanglants étaient sans doute l'œuvre des habitants, qui attirent les voyageurs malchanceux et les dévorent.<new_line>Ces créatures ne sont peut-être pas agiles, mais elles sont extrêmement rusées. Pourquoi, de tous les mutants, les poulets ont développé un instinct de chasse si affûté ? Cela reste un mystère.]],
				['5'] = [[En prenant soin de garder les yeux sur la ferme, j'ai reculé prudemment. Personne n'a signalé sa présence.]],
				['2'] = [[J'ai approché de la ferme, et il n'y avait toujours aucun signe de vie.]],
				['3'] = [[Dès que j'ai mis les pieds sur le territoire de la ferme, des poulets mutants sont apparus dans tous les coins. La chose aurait été drôle s'ils n'avaient pas eu l'air si agressifs. Il était déjà trop tard pour rebrousser chemin.]],
				['8'] = [[Ma distraction a fonctionné, et les créatures ont hésité un instant. C'est ma chance de m'enfuir avant que le coq ne se remette à chanter.]],
				['1'] = [[La ferme, qui a partiellement survécu aux bombardements, a l'air paisible de loin. Sans les amas rouge sang qui maculent la ferme de tous les côtés, ce serait un endroit agréable pour se reposer.<new_line>Les habitants du coin, semble-t-il, se sont faits rares en sentant une nouvelle victime.]],
				['6'] = [[Je m'éloignais à peine de la ferme quand j'ai entendu un chant de coq assourdissant. Se boucher les oreilles ne servait à rien. Le cri perçant n'en était pas moins strident.]],
				['7'] = [[Même si le chant du coq se poursuivait sans relâche, je me suis ressaisi et j'ai retiré mes mains de mes oreilles.<new_line>Cela n'y changeait rien : c'est comme si le cri venait de l'intérieur de ma tête. Perplexe, j'ai fait volte-face, seulement pour voir une foule de créatures enragées s'élancer vers moi.]]
			}
		},
		['destroy_lair_quest'] = {
			['choice'] = {
				['3'] = [[Se défendre]]
			},
			['episode'] = {
				['4'] = [[En arrivant à l'endroit indiqué, j'ai aperçu des scorpions.]],
				['5'] = [[En m'écartant de quelques pas du lieu indiqué, je suis tombé sur un nid d'abeilles mutantes.]],
				['2'] = [[Je suis arrivé au lieu indiqué. Au bout de quelques minutes, une meute de loups m'a attaqué.]],
				['3'] = [[J'ai croisé un nid de sangliers dans les bois. Vraisemblablement, j'étais au bon endroit.]],
				['12'] = [[Cette meute de loups ne causera plus d'ennuis.]],
				['13'] = [[Le nid est détruit. Je peux retourner à la base.]],
				['11'] = [[J'ai neutralisé les ours, ils ne blesseront personne.]],
				['15'] = [[J'ai exterminé le nid d'insectes mutants.]],
				['9'] = [[Les coordonnées m'ont guidé dans les profondeurs de la forêt. Avant de pouvoir inspecter la zone, j'ai été attaqué par des créatures mutantes.]],
				['1'] = [[Dès mon arrivée sur place, je suis tombé sur l'antre d'un ours.]],
				['16'] = [[J'ai massacré les mutants du coin. Ils ne causeront plus de problèmes.]],
				['14'] = [[J'ai supprimé le nid de scorpions.]]
			}
		},
		['emba_isaev_krasnoyarsk'] = {
			['choice'] = {
				['22'] = [[C'est plutôt pessimiste.']],
				['4'] = [[Qu'est-ce que c'est, le vestige de mycélium ?']],
				['5'] = [[Mais du coup, les mutations ?...']],
				['2'] = [[Et les technologies, là-dedans ? Notre but était de sauver le monde.']],
				['16'] = [[Et la végétalisation des friches ?']],
				['12'] = [[Qu'est-ce que vous allez faire maintenant ?']],
				['9'] = [[Alors, toutes ces biostations...']],
				['11'] = [[Et le prototype des pétroliers ?']],
				['7'] = [[Qu'y a-t-il, docteur ?']],
				['15'] = [['Je suis prêt'.]],
				['14'] = [[À quoi dois-je m'attendre à la biostation ?']],
				['19'] = [[Au revoir, Dr Isaev.']],
				['17'] = [[Que va-t-on faire du vestige de mycélium ?']],
				['18'] = [[Donc, pas de végétalisation des friches...']],
				['1'] = [[Comment les arrêter ? Il y a toute une armée.']],
				['20'] = [[Alors, il suffit d'aller à la biostation et de pénétrer dans la salle du supercalculateur ?']],
				['21'] = [[Et nous avons été perçus comme une menace ?']]
			},
			['episode'] = {
				['23'] = [[En toute franchise, nous n'avons pas eu le cœur de l'anéantir complètement. Nous l'avons placé dans la grotte où il se trouvait depuis des millénaires. Un retour à son milieu naturel, sans ordinateur.<new_line>Le biologiste et certains de mes collègues resteront ici quelques temps, pour retirer toutes les technologies des biostations. Certaines seront détruire, et nous en emporterons d'autres avec nous.']],
				['22'] = [[Non, car je crois que les gens pourraient tirer une leçon de toutes ces catastrophes. En attendant, nous devenons des archivistes dans le vrai sens du terme.<new_line>Nous stockerons des informations afin d'éviter que les pires erreurs soient commises par ceux qui suivront le chemin du savoir.']],
				['21'] = [[Nous avons décodé la boîte noire. Le signal a été généré, il a déjà été émis vers le prototype de mycélium. L'appareil s'autodétruira, et les pétroliers n'auront plus rien.<new_line>Nous avons besoin de temps pour faire de nouvelles vérifications, après quoi, nous partirons d'ici. Revenez tout à l'heure pour faire vos adieux.']],
				['20'] = [[C'est difficile à prévoir. Tout ce que nous savons, d'après les documents, c'est qu'il s'agit d'une très grande cité des sciences. Et en son centre, sous la biostation, se cache le vestige de mycélium, connecté à un supercalculateur.<new_line>Nul ne peut prédire comment les mutations et l'ordinateur peuvent affecter son activité vitale.']],
				['19'] = [[Les pétroliers vont exploiter le mécanisme de végétalisation à des fins commerciales.<new_line>Ils ne se soucient guère de comprendre que le vestige de mycélium prendra le contrôle immédiatement et qu'une nouvelle catastrophe se produira.<new_line>En d'autres termes, notre objectif est d'infiltrer la cité des sciences et d'envoyer un signal d'autodestruction au prototype de mycélium utilisé dans notre mécanisme.']],
				['18'] = [[Hélas, mon ami, hélas. Pas à ce prix. Mais vous avez apporté une contribution inestimable à la défense de ce monde chancelant. Maintenant, qui sait, avons-nous une toute petite chance de survivre ? Bonne chance à vous, cher camarade. Et adieu !']],
				['17'] = [["Nous avons été obligés de mettre à l'arrêt toutes les recherches et de changer la direction du développement de notre organisation. Toute cette histoire avec le vestige de mycélium...<new_line>Vous l'avez bien vu : les gens sont ambitieux, cupides et sont le plus souvent incapables de faire un choix, même si c'est pour la bonne cause.']],
				['4'] = [[Bonjour, collègue. Grande nouvelle ! D'après les documents, il y a un vestige de mycélium à la biostation, connecté à un supercalculateur. C'est là que se trouve le centre de décision.']],
				['5'] = [[Au départ, nous pensions que les mutations du mycélium étaient spontanées, et c'était ce caractère aléatoire qui posait le plus problème.<new_line>Toutefois, d'après les informations que vous avez obtenues, on dirait que son fonctionnement est différent. Non manipulé, le mycélium s'avère moins changeant.<new_line>Ce vestige de mycélium existait depuis des milliers d'années sous forme stable, dans des grottes isolées.']],
				['2'] = [[Bien entendu, un conflit militaire n'est pas la solution. Cela dit, nous avons un plan. Je n'entrerai pas dans les détails pour l'instant.']],
				['3'] = [[Maintenant que le prototype est entre les mains des pétroliers, cela n'a aucun sens de condamner notre projet.<new_line>Nous devons essayer par tous les moyens de sécuriser la technologie de végétalisation. Encore une fois, je dois vous demander de trouver les documents. C'est nécessaire pour notre plan et pour l'étude dans sa globalité.']],
				['8'] = [[Eh bien, bonne chance.'<new_line>(Tend un bout de papier avec le code)]],
				['9'] = [[Étonnant ! Incroyable ! Extraordinaire !']],
				['6'] = [[Et toutes ses mutations ont eu lieu suite à des stimuli externes. L'erreur fatale du projet E.M.B.A. a été de connecter le vestige de mycélium à un supercalculateur.<new_line>Ce changement a produit des réactions radicales. Apparemment, l'émergence de biomutants et d'individus autonomes comme l'Émissaire est la conséquence de l'accès du mycélium aux circuits logiques d'ordinateurs très puissants.']],
				['7'] = [[Vous devrez naviguer selon les circonstances. Essayez au moins de vous emparer de la boîte noire sur place.<new_line>Avec l'aide du biologiste, vous nous avez fournis un excellent laboratoire mobile, et nous pouvons potentiellement émettre un signal direct à partir de celui-ci en nous basant sur les codes de cette boîte noire. Alors, camarade, prêt pour une dernière mission ?']],
				['10'] = [[Ce vestige de mycélium semble être doué de conscience. Pas d'une seule, comme la nôtre, mais plutôt d'une conscience partagée. C'est sans doute la raison pour laquelle il a communiqué si facilement avec le supercalculateur.']],
				['11'] = [[Le vestige de mycélium a été extrait des entrailles de la terre et soumis à une sélection intense. Tous les événements survenus jusqu'ici ne sont dus à rien d'autre qu'à une créature qui tente de se protéger à nouveau de l'humanité.<new_line>La symbiose avec l'ordinateur lui a offert des opportunités sans précédent, certes, mais elle a aussi été motivée, si je puis dire, par nos méthodes de travail.']],
				['14'] = [[Plus tard, l'ami, plus tard.']],
				['12'] = [[Oui ! Tout cela est logique. Rien n'empêcherait cette chose de répandre des spores dans le monde entier et de tisser son réseau sur toutes les villes. D'autant plus qu'elle semble friande de fortes radiations.<new_line>Mais tout ce qu'elle attend de nous, c'est la paix. Apparemment, elle ne fait pas la distinction entre les individus et considère l'humanité comme un unique organisme. Pour elle, chaque être humain est l'humanité.']],
				['13'] = [[C'est vrai. Je suis plutôt d'accord avec notre camarade fongique. L'ambition mêlée aux meilleures intentions de l'humanité lui a donné tous les outils nécessaires. À qui incombe la plus grande part de responsabilité ?<new_line>La réponse n'a pas l'air de pencher en notre faveur. C'est pourquoi l'Émissaire nous ressemble tant, sans parler du sort de Gerin et Kourganov...']],
				['1'] = [[Enfin, vous voilà. Nous avons de gros ennuis. Le Syndicat Pétrolier s'est emparé de notre prototype de mécanisme de végétalisation. Il faut les empêcher de l'utiliser, faute de quoi, toutes les friches vont devenir un véritable enfer vert.']],
				['15'] = [[Bonjour, cher camarade. C'est confirmé : le prototype a bel et bien été détruit.']],
				['16'] = [[Et nous sommes à la croisée des chemins.']]
			}
		},
		['camp_defence_quest'] = {
			['choice'] = {
				['7'] = [[Creuser un piège à loup]],
				['18'] = [[Attendre le départ des bandits]],
				['17'] = [[Venger les camarades tués]],
				['16'] = [[Examiner le cadavre]],
				['8'] = [[Voir ce qu'il y a à faire]],
				['9'] = [[Se mettre en position et attendre]],
				['14'] = [[Rejoindre le combat]],
				['10'] = [[Défendre notre camp]]
			},
			['episode'] = {
				['4'] = [[J'ai réussi à accrocher le rondin avec les pieux.]],
				['5'] = [[J'ai vu l'endroit idéal pour un piège à loup près du camp et j'ai pu en creuser un rapidement.]],
				['2'] = [[En approchant de l'avant-poste, j'ai entendu des coups de feu. Aucun doute : les bandits étaient déjà en train d'attaquer le camp.]],
				['3'] = [[Au point de rendez-vous, je ne vois qu'un cadavre gisant au sol.]],
				['8'] = [[Nous avons tenu bon, mais certains de nos combattants ont été grièvement blessés.]],
				['9'] = [[Au final, c'est plutôt positif. Ils s'en sortiront, c'est juste une question de temps.]],
				['6'] = [[Les bandits arrivent. Ils sont nombreux...<new_line>Mais un comité d'accueil est prêt à les recevoir !]],
				['7'] = [[La situation est sous contrôle. Nous avons tenu bon. Plus personne ne nous ennuiera pendant longtemps.]],
				['13'] = [[Si j'en crois la carte, le camp n'est plus très loin.]],
				['12'] = [[Je n'ai pas pu aider ces gens, mais je les vengerai.]],
				['1'] = [[Un homme armé se trouve à l'avant-poste :<new_line>'Vous venez aider ? Bon, mettez-vous en position. Ils sont déjà en chemin.']],
				['11'] = [[Les bandits sont enfin partis. J'ai fouillé le camp, il ne restait que les cadavres de nos alliés.]],
				['10'] = [[J'ai entendu bruit et je me suis caché. Des étrangers rôdaient près du camp.<new_line>C'était trop tard. Les bandits avaient déjà pris le contrôle.]]
			}
		},
		['new_year_boss_2_2'] = {
			['episode'] = {
				['4'] = [[C'était comme si personne ne m'avait entendu. Le cadeau finit dans le feu et des flammes azurées éclatèrent, laissant échapper des volutes de fumée bleue. Le vieil homme et sa bande, les yeux révulsés, se tenaient autour du feu pour inhaler les vapeurs.]],
				['5'] = [[Tout d'un coup, le vieil homme se tourna vers moi et se mit à rire.<new_line>'Ah, tu veux parler à l'ancien ? Hé, les mecs, jetez tout ce bric-à-brac au feu !']],
				['2'] = [[Sur la place du village, les vandales empilent les décorations de Noël près d'un grand feu. Des riverains se blottissent contre les murs des maisons. Ils sont menés par un homme maussade portant des cadeaux dans les mains.]],
				['3'] = [[Hé, l'ancien ! T'as récupéré les cadeaux de tout le monde ? Aboule !'<new_line>Le vieillard au masque effrayant arracha des mains de l'homme un paquet emballé, qu'il jeta au feu.]],
				['8'] = [[En réponse, le vieil homme éclata de rire derechef. Toujours en riant, il courut jusqu'à sa moto et partit de la place en trombe.<new_line>Les voyous, menés par l'ancien défiguré, s'approchèrent lentement vers moi.]],
				['9'] = [[Malédiction ! Je n'ai pas pu vous aider à cause de cette horrible fumée.']],
				['6'] = [[Tout un tas de décorations de Noël alimentèrent les flammes, qui déversèrent un flot de fumée âcre encore plus épais que le premier. Le vieil homme tira à lui le pauvre ancien du village.]],
				['7'] = [[La fumée se dissipa. À côté du vieil homme, là où se trouvait l'ancien, se tenait désormais une sorte de monstre.<new_line>'Les mecs, la bande du Père Fouettard a une nouvelle recrue ! Le bleu, ta première mission sera d'éliminer ce pauvre type !']],
				['1'] = [[Le fuyard me conduisit jusqu'à un village décoré pour les fêtes. Sous mes yeux, des voyous retiraient des guirlandes lumineuses des poteaux, arrachaient des décorations sur les arbres, et disparaissaient derrière des maisons.]],
				['11'] = [[Oh, vous ne l'avez pas reconnu ? Ce vieil homme, c'était le Père Noël ! J'ignore comment il a pu si mal tourner...<new_line>Je suis incapable même d'imaginer une telle abomination. Peut-être le Père Négoce saura-t-il quoi faire. Nous devons le retrouver.']],
				['10'] = [[Là n'est pas la question. Leur façon de l'ingérer... tout en détruisant tout ce qui a trait à Noël. Votre monde est-il entièrement corrompu ?']]
			},
			['choice'] = {
				['4'] = [[Laissez-le tranquille !']],
				['5'] = [[Sauter à travers la fumée]],
				['2'] = [[Hé, ça suffit !']],
				['3'] = [[Demander à l'ancien ce qui se passe]],
				['8'] = [[Tout ça est l'œuvre de ce vieillard !']],
				['1'] = [[Qu'est-ce que...']],
				['6'] = [[Que lui avez-vous fait ?!']],
				['7'] = [[Est-elle toxique ?']]
			}
		},
		['new_year_boss_2_3'] = {
			['episode'] = {
				['4'] = [[Enfin, tu retrouves l'usage de la parole ! Que s'est-il passé ?']],
				['5'] = [[J'ai du mal à le croire. Qu'a-t-il bien pu arriver pour que le Père Noël devienne cet individu... En tout cas, on aura besoin de renforts pour affronter une bande aussi dangereuse. Je connais quelqu'un d'assez fort.']],
				['2'] = [[Vous avez raison. Il vaudrait mieux que vous compreniez tout le monde plutôt que moi seul. Tenez, voici une potion. Elle vous aidera.'<new_line><new_line>Père Nomade me tendit une tasse en bois. J'en bus une gorgée avec prudence. Pas mauvais !]],
				['3'] = [[Je me suis encore fait avoir ! Alors que je penchais la tête en arrière pour boire toute la potion, Père Nomade m'administra un autre coup de massue !]],
				['1'] = [[Grooh aagh. Aagh grooh !']],
				['6'] = [[Il ne m'a pas donné de nouvelles depuis un moment... Il s'occupait des problèmes de gouvernement dans ce monde.<new_line>Mais il ne manque pas d'expérience au combat. C'est décidé ! Décore le sapin à nouveau et ouvre un autre portail pour aller à sa rencontre.']]
			},
			['choice'] = {
				['4'] = [[Parler du Père Fouettard]],
				['5'] = [[Qui ?']],
				['2'] = [[Boire le tout]],
				['3'] = [[Ce n'est pas bientôt fini ?!']],
				['1'] = [[Il recommence à baragouiner...']]
			}
		},
		['mad_man_base'] = {
			['episode'] = {
				['1'] = [[La clôture de la maison délabrée était maculée de sang. La signature des fous furieux. Ils adorent entasser du bric-à-brac, je devrais trouver des choses intéressantes à l'intérieur. Mais je doute qu'ils partagent facilement leurs petits trésors.]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['2'] = [[Les vestiges de la porte d'entrée étant éparpillés par terre, l'édifice est ouvert à tous les vents. À l'intérieur règne une forte odeur d'ozone. Le sol est jonché de câbles enchevêtrés qui mènent dans une salle où ont eu lieu des actes contre nature. Au centre, le monstre gît sur une table en métal autour de laquelle s'affaire un étrange bossu. Lorsque celui-ci m'aperçoit, il pousse un hurlement de terreur et s'empresse d'actionner un interrupteur sur le mur. S'ensuivent une détonation assourdissante et un éclair d'électricité qui illumine toute la pièce. Quand ce maelström de son et de lumière s'apaise, le monstre ouvre les yeux.]],
				['1'] = [[La bobine s'est complètement déroulée. L'endroit où se trouve le monstre ne pourrait pas être plus évident : non loin, j'aperçois un édifice au sommet duquel des paratonnerres grotesquement difformes attirent la foudre qu'engendre un nuage noir menaçant.]]
			}
		},
		['comrade_in_trouble_collapse'] = {
			['episode'] = {
				['1'] = [[L'éclaireur n'est pas sorti du bâtiment fermé.]]
			}
		},
		['emba_intro2'] = {
			['choice'] = {
				['4'] = [[D'accord.']],
				['5'] = [[Ces anomalies... d'où viennent-elles ?']],
				['2'] = [[Comment puis-je vous aider ?']],
				['3'] = [[À qui ai-je l'honneur ?']],
				['1'] = [[Approcher]],
				['6'] = [[Vous savez ce qui est arrivé à notre monde ?']],
				['7'] = [[Non, tout est limpide.']]
			},
			['episode'] = {
				['4'] = [[Notre expédition a été lancée par l'organisation ProgrèS. Les responsables se trouvent à Tver actuellement. Vous pourrez les rencontrer quand l'heure sera venue.<new_line>En attendant, tâchez de nous aider à recueillir des données. Les technologies de pointe à notre disposition ne manqueront pas de vous intéresser, c'est certain, et nous sommes prêts à vous en faire profiter en échange de disquettes contenant les informations qui nous manquent.'<new_line><new_line>(Vous devez être de niveau 25 pour rencontrer les responsables de l'expédition à Tver)]],
				['5'] = [[D'autres questions ?']],
				['2'] = [[Bonjour, camarade ! Nous analysons les anomalies biologiques, et nous en avons rencontré beaucoup ces derniers temps.<new_line>Aidez-nous dans nos recherches, et nous vous laisserons accéder à nos technologies de pointe.']],
				['3'] = [[En ce moment, nous nous intéressons à tout ce qui concerne la végétation et les animaux qui présentent des mutations inédites. Enregistrez toutes les données les concernant sur des disquettes au moyen de cet appareil, et revenez nous les donner.<new_line>En outre, les gens du coin peinent à faire face aux nouvelles menaces biologiques. Aidez-les : si vous obtenez au passage des informations sur de nouvelles anomalies, vous ferez d'une pierre deux coups.']],
				['8'] = [[Excellent, nous avons hâte d'avoir de vos nouvelles ! Prenez des notes sur tout ce qui concerne les nouvelles plantes et autres biomutants, et rapportez-les-nous. Je suppose que votre intérêt réside principalement dans les technologies que nous sommes prêts à vous fournir en retour.<new_line>Et quand vous serez prêt, rendez-vous à Tver. Parlez au Dr Isaev d'autres recherches plus globales.'<new_line><new_line>(Vous devez être de niveau 25 pour rencontrer les responsables de l'expédition à Tver)]],
				['1'] = [[J'ai entendu le bruit d'un moteur, et puis un gros véhicule tout-terrain orange est apparu. Il a freiné devant moi, et un homme vêtu d'une combinaison étrange en est sorti. Il m'a fait signe : il voulait me parler.]],
				['6'] = [[Nous sommes en train de recueillir des données pour le savoir. Pour l'heure, nous ne pouvons que supposer qu'elles ont été causées par la catastrophe planétaire. Nous travaillons, pour ainsi dire, sur le terrain, tandis que nos collègues près de Tver s'occupent de la théorie.<new_line>Vous pourrez leur parler une fois sur place. En attendant, aidez-nous à recueillir des données.']],
				['7'] = [[Non. Pour l'instant, nous étudions les anomalies biologiques. C'est notre principal objectif scientifique actuel. Et échafauder des théories sans fondement n'est pas la méthode des vrais scientifiques.<new_line>Cependant, tout est probablement lié. Probablement pourriez-vous en discuter avec le Dr Isaev, le responsable de l'expédition. Mais pour cela, vous devrez aller le rencontrer à proximité de Tver.']]
			}
		},
		['new_year_boss_4_3'] = {
			['episode'] = {
				['18'] = [[Bienvenue dans le monde réel !<new_line>J'ai téléchargé toutes les infos. On se casse. Allez, choom, bouge de là ! Suis-moi !']],
				['17'] = [[Le téléphone sonne.]],
				['4'] = [[À part les murs de glace, c'est une salle ordinaire avec du mobilier et une autre porte de l'autre côté, en face de l'entrée.<new_line>Cyber Noël a connecté une sorte de boîte avec des boutons à un disjoncteur sur le mur, et il bricole avec.]],
				['5'] = [[La bande stocke son arme secrète ici ! Bien joué.]],
				['2'] = [[Avant même d'avoir pu fixer les explosifs dessus, la porte s'ouvrit.<new_line>'Quel genre d'espion emprunte l'accès principal ? J'ai piraté une porte dérobée, ça fait un bail que je suis là-dedans. Allez, viens, on a du taf.']],
				['3'] = [[Avance vers la salle de contrôle, le temps que je prépare un truc pour le piratage.']],
				['12'] = [[Un clic mélodieux s'est fait entendre à l'intérieur du mur, mais la porte ne bouge pas d'un pouce.]],
				['9'] = [[Près de la porte, il y a un panneau avec des boutons. Des instructions sont écrites au-dessus.<new_line>'Saisir la bonne séquence.']],
				['6'] = [[Les étagères sont vides.]],
				['10'] = [[T'as pas encore piraté cette camelote ? Regarde sous la table. Ces gonks ont griffonné le code juste en dessous.']],
				['13'] = [[J'ai entendu le clic de la serrure de la porte. J'ai réussi !]],
				['11'] = [[Après un grincement désagréable, les boutons reviennent dans leur position d'origine.]],
				['14'] = [[Parfait ! Prends ce câble, tire-le dans la pièce voisine et raccorde-le à la... boîte en métal.<new_line>Il y a aussi un téléphone, là-bas. Je le ferai sonner quand tout sera prêt. Décroche-le. C'est tout.']],
				['7'] = [[Sur la table, il y a des papiers avec des schémas loufoques et un bocal. Au fond du bocal se trouvent deux derniers bonbons : un bleu, et un rouge.]],
				['8'] = [[Un code est écrit sous la table :<new_line>'E9, 7A, 1C, 55']],
				['1'] = [[Je suis sur place. Le bâtiment est un bloc de glace. La porte est remarquablement lisse et robuste. Il n'y a pas le moindre interstice entre la porte et le montant. Impossible d'entrer sans explosifs.]],
				['15'] = [[Écoute, choom, j'adorerais discuter avec toi, mais les corpos ne vont pas tarder. D'une minute à l'autre, ils vont nous foncer dedans en un éclair. Allez, zou !']],
				['16'] = [[La salle est petite et peu éclairée. Effectivement, il y a une boîte en métal sur la table, et quelque part dans le noir, sur une commode, se trouve un téléphone.]]
			},
			['choice'] = {
				['18'] = [[Suivre Cyber Noël en courant]],
				['17'] = [[Décrocher le téléphone]],
				['4'] = [[Aller vers les étagères]],
				['5'] = [[Manger le bonbon bleu]],
				['2'] = [[Aller vers la porte]],
				['3'] = [[Aller vers la table]],
				['8'] = [[Appuyer sur le bouton 7A]],
				['9'] = [[Appuyer sur le bouton 55]],
				['11'] = [[Appuyer sur le bouton E9]],
				['10'] = [[Appuyer sur le bouton 1C]],
				['12'] = [[Appeler Cyber Noël]],
				['13'] = [[Cyber Noël, la voie est libre.']],
				['14'] = [[Je ne suis pas très...']],
				['6'] = [[Manger le bonbon rouge]],
				['7'] = [[Regarder sous la table]],
				['1'] = [[Faire sauter la porte]],
				['15'] = [[Tirer le câble dans la pièce]],
				['16'] = [[Raccorder le câble]]
			}
		},
		['boatman_camp_chat'] = {
			['choice'] = {
				['1'] = [[Non, je veux savoir ce qui est arrivé. Qui a provoqué la guerre, ce que cache l'épidémie...']],
				['2'] = [[Où est l'institut ?']],
				['3'] = [[C'était convaincant. À la prochaine.']]
			},
			['episode'] = {
				['1'] = [[Le batelier préparait de la soupe de poisson dans une grande cuve. Opinant du chef :<new_line>'Alors, c'est fait. Vous avez trouvé ce que vous cherchiez à Belomorsk ?']],
				['2'] = [[De la soupe ?' Le batelier m'a souri. 'Pas besoin de faire autant d'histoires. J'ai entendu parler d'un institut scientifique qui devait sans doute travailler sur ce virus. Mais c'est un endroit dangereux. Les radiations, les mutants... les gens. Vous feriez mieux de ne pas vous en approcher.']],
				['3'] = [[Vous n'allez jamais lâcher l'affaire ! Je vous l'ai dit, c'est dangereux et vous n'êtes pas prêt ! Restez encore un peu, commencez par affiner vos compétences. Aidez les gens du coin, acceptez des missions au bar. Vendez de la marchandise, discutez avec le contremaître. Et puis, quand vous serez prêt, revenez me voir. Je vous dirai tout.']]
			}
		},
		['gift_delivery'] = {
			['episode'] = {
				['1'] = [[C'est un camp de petite taille, avec un appentis confectionné à partir de branches d'épicéas recouvertes de neige. Il y a un feu, et de l'eau en train de bouillir dans une marmite. On dirait que l'homme vient tout juste de partir. Il ne va pas en croire ses yeux quand à son retour, il trouvera un cadeau.]],
				['2'] = [[À mon arrivée, je n'ai rien vu d'inhabituel. Je me demandais où laisser le cadeau lorsque j'ai entendu un fusil qu'on rechargeait et une voix rauque dire : «Pose la caisse sur le sol et dégage. Ne te retourne pas.»]],
				['3'] = [[À ma grande surprise, j'ai trouvé un sapin du Nouvel An avec des décorations improvisées. Quelqu'un est venu ici et a dormi, emmitouflé dans plusieurs couvertures. J'aurais tout intérêt à laisser un cadeau sous le sapin et à m'en aller.]]
			},
			['choice'] = {
				['1'] = [[Placer le cadeau]]
			}
		},
		['emba_various_hard'] = {
			['choice'] = {
				['4'] = [[Aller voir le réalisateur]],
				['5'] = [[Aller voir le docteur]],
				['2'] = [[Bien. Je vais aller tirer sur ceux qui sont là-bas.' (combattre)]],
				['3'] = [[Où dois-je aller ?']],
				['8'] = [[Retourner voir le biologiste]],
				['9'] = [[Regarder le film]],
				['6'] = [[Aller voir le chef]],
				['7'] = [[Aller voir les fermiers]],
				['1'] = [[Quelles sont ces sources ?']],
				['11'] = [[Accepter (combattre)]],
				['10'] = [[Transporter les blessés]]
			},
			['episode'] = {
				['19'] = [[Voilà ! J'ai aussi rendu visite à quelques personnes, ça devrait suffire pour le rapport. Merci beaucoup, je ne voulais pas avoir la honte devant le Dr Isaev.']],
				['18'] = [[J'ai pris des notes sur tout ce que j'ai vu dans la serre qui me semblait important, et j'ai même fait des croquis du mycélium.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['17'] = [[Oui, c'est arrivé. Nous avons abandonné la serre assez vite, parce que ça nous aurait coûté cher de tenter quoi que ce soit. Je peux vous faire visiter, mais je n'irai pas sans protection. Il nous faut une sorte de filtre pour respirer.']],
				['4'] = [[Mes collègues et moi avons rendu visite à un vieil homme... pour les affaires. Il était très intelligent ! Il cultivait presque du mycélium dans son cellier.<new_line>En gros, il nous a raconté plein de trucs. Je me souviens de presque tout, mais il était réticent à parler du mycélium. Vous devriez essayer de lui parler.']],
				['5'] = [[Il y a un réalisateur qui fait des films sur les mutants. Il m'a même interviewé, vous imaginez ? En gros, il a recueilli plein d'observations utiles, demandez-lui de vous en parler.']],
				['2'] = [[Eh bien, j'ai dû aller interroger toutes sortes de personnes et visiter différents endroits... Ça m'a pris un temps fou. Je ne peux pas refaire tout ça tout seul.<new_line>Aidez-moi d'abord à obtenir des corps de biomutants pour une autopsie. Il me faut des infos de première main, si je puis dire.']],
				['3'] = [[Je vais m'occuper des autopsies pendant que vous interrogez les gens, d'accord ?']],
				['8'] = [[Regardez, il y a une ferme derrière ce champ. J'ai entendu dire que du mycélium avait recouvert les cultures. Vous devriez interroger les gens là-bas, ils vous montreront peut-être même les plantes infectées.']],
				['9'] = [[Vous quoi ? Vous parler du mycélium ? Vous voulez peut-être aussi les clés de mon appartement où j'ai laissé mon argent ?<new_line>Bien, apportez-moi des ingrédients à distiller et je vous dirai tout.']],
				['6'] = [[Il y a un docteur pas loin d'ici qui a organisé un hôpital de campagne. Il soigne les personnes qui ont été infectées par les spores de mycélium. Il devrait avoir une tonne d'informations utiles sur la progression de la maladie.<new_line>L'argent ne l'intéresse pas, donc il devrait nous aider.']],
				['7'] = [[J'ai parlé au chef des combattants près du camp voisin. Je ne sais pas s'il acceptera de répéter ce qu'il m'a dit, il n'était pas très bavard. Mais essayez quand même.']],
				['10'] = [[Tenez, prenez ce carnet, j'y ai noté toutes les étapes pour le cultiver ici. Et dites au blanc-bec de me rapporter le carnet quand il aura fini. Sinon, je lui ferai... une injection dans le derrière !']],
				['11'] = [[Vous en parler ? Vous croyez que je suis conférencier ou quoi ? Tout est dans mes films ! Asseyez-vous, je vais vous montrer les deux derniers épisodes.']],
				['14'] = [[J'ai fini de transporter les blessés et j'ai pris la boîte des dossiers de patients.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['12'] = [[Luttant pour rester éveillé, j'ai pris des notes sur ce qui me semblait le plus important.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.]],
				['13'] = [[Oh, vous voulez dire Alexander ? Oui, je le connais. Je peux vous donner les dossiers médicaux de mes anciens patients. Mais j'ai aussi besoin d'aide. Le camp a été attaqué et les blessés doivent être transportés à mon hôpital. Pouvez-vous vous en charger ?']],
				['1'] = [[Bonjour ! Comment ça va ? Je suis dans le pétrin. J'ai perdu mes rapports de recherche. Le Dr Isaev m'a chargé de recueillir des informations sur les biomutants et le mycélium de sources externes.']],
				['15'] = [[Vous voulez que je reparle au bigleux ? Non, j'ai un raid à faire.<new_line>Enfin... si vous combattez à la place d'un de mes soldats, je peux l'envoyer discuter avec les scientifiques.']],
				['16'] = [[J'ai payé les gars pour les remercier de leur aide.<new_line>Je dois maintenant retourner voir le Biologiste et lui demander si je dois interroger d'autres personnes.']]
			}
		},
		['forester'] = {
			['episode'] = {
				['1'] = [[Le bûcheron m'a accueilli avec grande gentillesse et m'a invité à sa table où nous avons festoyé. Pendant ce repas copieux, il m'a conté nombre de ses aventures et mésaventures. Ensuite, nous nous sommes aventurés dans les bois. Après quelques minutes, une meute de loups nous a encerclés. Derrière moi, j'ai entendu un bruit sinistre. Le bruit d'os qui craquent et de vêtements qui se déchirent. Faisant volte-face, j'ai alors aperçu avec horreur, en lieu et place du bûcheron, un gigantesque loup-garou gris. « Igor dit bonjour ! », a-t-il grogné en se précipitant sur moi.]]
			}
		},
		['santa_player'] = {
			['episode'] = {
				['2'] = [[Je doute qu'il ait eu le temps de se remettre de sa défaite. D'un autre côté, qui sait ? Il faudra peut-être les combattre tous les deux. Bonne chance, camarade !']],
				['1'] = [[Ma foi, je suis prêt à t'envoyer devant Glagla. Tu dois le vaincre et détruire la pièce de l'arme qui a absorbé notre magie.']]
			},
			['choice'] = {
				['2'] = [[Merci !']],
				['1'] = [[Et Fouettard ?']]
			}
		},
		['emba_energy_medium'] = {
			['choice'] = {
				['4'] = [[Fuir]],
				['5'] = [[Tomber face contre terre]],
				['2'] = [[D'accord, je veux bien participer.']],
				['3'] = [[Regarder aux alentours]],
				['8'] = [[Courir jusqu'à l'équipe de tournage]],
				['9'] = [[Descendre et continuer à courir]],
				['6'] = [[Revenir en arrière]],
				['7'] = [[Courir jusqu'à l'arbre]],
				['1'] = [[C'est quoi cette scène de poursuite que vous filmez ?']],
				['11'] = [[Essayer de me lever]],
				['10'] = [[Laisser tomber la nourriture et partir inaperçu]]
			},
			['episode'] = {
				['4'] = [[J'avais déjà commencé à courir quand je me suis retourné et que j'ai vu toute une horde de biomutants féroces se précipiter vers moi. Je ferais mieux d'accélérer.]],
				['5'] = [[J'ai pris de l'avance sur mes poursuivants, mais je sens mes forces me lâcher. Je peux retourner vers l'équipe de tournage maintenant ou essayer de grimper dans un arbre pour réfléchir à une solution.]],
				['2'] = [[C'est un documentaire de plusieurs épisodes. Notre public doit voir de ses propres yeux ces biomutants et les dangers qu'ils posent.<new_line>J'en ai attrapé deux. Il me faut un acteur coriace qui s'enfuit en les voyant pendant que je filme. Mais où trouver ça de nos jours ? Enfin vous, vous seriez parfait pour le rôle.']],
				['3'] = [[Excellent ! Vous êtes prêt ? Grisha, ouvre la cage ! Action ! Oui, allez, allez !']],
				['8'] = [[Je suis arrivé à l'arbre et j'ai grimpé. On dirait que les biomutants m'ont perdu de vue, mais ils errent à proximité.<new_line>Je peux sauter de l'arbre et courir jusqu'à l'équipe de tournage ou distraire les mutants avec un truc à manger et m'éclipser en douce.]],
				['9'] = [[Ma ruse a fonctionné et je suis revenu voir le réalisateur ni vu ni connu.<new_line><new_line>'Pas mal pour un amateur. Ça devrait en jeter sur la vidéo. Je vous inviterai à la projection quand le film sera prêt.']],
				['6'] = [[J'ai changé de trajectoire et je cours maintenant vers l'équipe de tournage. Les biomutants à mes trousses seront bientôt leur problème.]],
				['7'] = [[Je suis presque à leur niveau.... Plus que dix mètres... J'entends alors des coups de feu et je vois les biomutants s'effondrer, tous morts.<new_line><new_line>'Vous n'auriez pas dû mettre l'équipe de tournage en danger, même si votre retour était impressionnant. Je vous inviterai à la projection quand le film sera prêt.']],
				['12'] = [[Franchement, vous auriez pu essayer de faire mieux, pour l'amour de l'art. Je dois aller attraper de nouveaux biomutants maintenant.<new_line>Bon, buvez ça et reprenez vos esprits. J'ai du pain sur la planche.']],
				['1'] = [[Bonjour ! Regardez un peu ce qu'il se passe ! J'ai du mal à immortaliser ça pour le septième art ! Je dois filmer une scène de poursuite là. Vous voulez participer ?']],
				['11'] = [[Franchement, vous auriez pu essayer de faire mieux, pour l'amour de l'art. Je dois aller attraper de nouveaux biomutants maintenant. Bon, buvez ça et reprenez vos esprits. J'ai du pain sur la planche.']],
				['10'] = [[J'ai trébuché et je suis tombé. J'ai entendu des coups de feu et les hurlements des biomutants.<new_line>J'ai alors entendu le réalisateur jurer dans son mégaphone.]]
			}
		},
		['newyearEvent_commander_hard'] = {
			['choice'] = {
				['4'] = [[Applaudir]],
				['5'] = [[Accéder à l'arène]],
				['2'] = [[Lire le règlement]],
				['3'] = [[...']],
				['1'] = [[Quel genre de tournoi ?']],
				['6'] = [[Se reposer]]
			},
			['episode'] = {
				['18'] = [[T'es vraiment cool, choom ! Si jamais mon unité centrale est en overdose de doute sur ta coolitude, je me souviendrai de ce moment. T'as apporté aux gens un peu plus de bonheur et de quoi vraiment croire au Père Noël.']],
				['17'] = [[Et nous revoilà enfin ! À suivre, non pas un événement bateau, mais un combat de champions opposant la crème de la crème !<new_line>Le seul, l'unique, l'incroyable Père Noël, le favori des fans du tournoi, affrontera... euh, y a une erreur, là... non ? Ce sera Père Négoce. Je parie que ce sera du gâteau pour notre recordman.']],
				['4'] = [[Le nombre de personnes autour du dôme ne fait qu'augmenter. Certains grimpent directement dessus. Au bout d'un moment, Cyber Noël apparaît sur scène, juste à côté du dôme.<new_line>'Yo, les chooms ! Bienvenue à tous dans le tournoi trop frais de la Coolitude de Noël !']],
				['5'] = [[Je vous présente les concurrents. Un horrible corporatiste cupide, Père Négoce ! Un visiteur de la préhistoire, Père Nomade ! Mago, un grand cultivateur de magie ! Et une bande de vauriens au cœur de pierre !<new_line>Le gang du bonhomme de neige, plus froid que l'arctique ! Le mystérieux Yéti ! L'Elfe récidiviste, sans foi ni loi ! Eeeeet celui que vous attendiez tous, notre champion bien de chez nous... le Père Noël !!!']],
				['2'] = [[En gros, voilà les règles : il faut se servir de l'équipement autorisé, donc pas la peine de compter sur les cyberdecks. Il faudrait aussi lire la liste des coups interdits... Ah, et pas le droit aux animaux !']],
				['3'] = [[Bon, je dois filer prendre le micro. Attends vers l'entrée du dôme.']],
				['8'] = [[Et dans le combat d'ouverture des quarts de finale, le gang du bonhomme de neige affrontera l'inimitable Père Noël !']],
				['9'] = [[Et dans le combat d'ouverture des quarts de finale, la bande de vauriens affrontera l'inimitable Père Noël !']],
				['6'] = [[Et dans le combat d'ouverture des quarts de finale, l'Elfe récidiviste affrontera l'inimitable Père Noël !']],
				['7'] = [[Et dans le combat d'ouverture des quarts de finale, le Yéti affrontera l'inimitable Père Noël !']],
				['10'] = [[La foule est en délire, et Cyber Noël reprend vie.<new_line>'Quel combat ! Bon voyage retour au perdant, et bienvenue au Père Noël dans les demi-finales !']],
				['11'] = [[Des combats ont eu lieu pendant mon temps de repos. Après une courte pause, finalement, tout le monde s'est réuni à nouveau.<new_line><new_line>'J'ai le plaisir de vous accueillir pour les demi-finales ! Nous avons dit adieu à l'Elfe récidiviste, au gang du bonhomme de neige, au Yéti et à la bande de vauriens.']],
				['14'] = [[C'était un combat vicieux... tout ce qu'on aime ! Maintenant, laisser notre combattant préféré se reposer un peu. Assistons ensemble au combat suivant !']],
				['12'] = [[Pour l'ouverture des demi-finales, nous accueillons notre cher Père Noël face à ce cher Père Négoce, mais pas dans le même sens du terme ! Voyons lequel des deux ira en finale.']],
				['13'] = [[Pour l'ouverture des demi-finales, nous accueillons notre cher Père Noël face au charmant Père Nomade ! Voyons lequel des deux ira en finale.']],
				['1'] = [[Choom ! Je commençais à croire que tu viendrais pas ! Le tournoi va commencer fissa : allez, vite, inscris-toi ! Tu dois te faire une vraie réputation dans le coin, celle d'un padre trop preem.']],
				['15'] = [[Et nous revoilà enfin ! À suivre, non pas un événement bateau, mais un combat de champions opposant la crème de la crème !<new_line>Le seul, l'unique, l'incroyable Père Noël, le favori des fans du tournoi, affrontera le grand et sage Mago ! Ça promet d'être le combat du siècle, alors ne perdons pas de temps. Zou, c'est parti !']],
				['16'] = [[Et nous revoilà enfin ! À suivre, non pas un événement bateau, mais un combat de champions opposant la crème de la crème !<new_line>Le seul, l'unique, l'incroyable Père Noël, le favori des fans du tournoi, affrontera le jovial et néanmoins costaud Père Nomade ! Ça promet d'être le combat du siècle, alors ne perdons pas de temps. Zou, c'est parti !']]
			}
		},
		['quest_passenger'] = {
			['choice'] = {
				['1'] = [[Accepter la récompense et partir]]
			},
			['episode'] = {
				['1'] = [[Nous avons rejoint la base du compagnon. Il était visiblement soulagé.<new_line>'Merci. Bien sûr, ma gratitude ne se limitera pas à de simples mots.']]
			}
		},
		['science_polygon'] = {
			['choice'] = {
				['1'] = [[Sortir les barils]]
			},
			['episode'] = {
				['4'] = [[Vous nous avez vraiment dépannés, camarade ! Voici votre récompense.']],
				['1'] = [[Un homme de la cabane blindée est venu à ma rencontre.<new_line>'Bonjour, camarade ! Je suis ravi que vous ayez pris le temps de venir. Satanés biomutants. Mais vous pouvez leur régler leur compte, pas vrai ?']],
				['2'] = [[J'ai une idée. Nous avons plusieurs barils de carburant stockés ici. Si vous les faites rouler sur le champ de test et que vous verser un sac de substrat de mycélium autour, les biomutants sont sûrs de mordre à l'hameçon.<new_line>Ça les attire comme des aimants. Et c'est à ce moment-là que vous les faites griller.']],
				['3'] = [[J'ai fait rouler les barils et j'ai versé la substance verdâtre à l'odeur de moisissure entre eux.<new_line><new_line>Il n'aura fallu que cinq minutes pour que les biomutants apparaissent et commencent à manger la biomasse.]]
			}
		},
		['frankenstein2020'] = {
			['episode'] = {
				['2'] = [[Lorsque le monstre s'est lourdement écroulé sur le sol, Igor a poussé un hurlement. Il m'a lancé un regard empreint d'une rage meurtrière, puis s'est précipité vers le bureau pour s'emparer d'un gigantesque grimoire avant de s'élancer en direction de la fenêtre.]],
				['1'] = [[Je n'avais jamais rien vu de tel. Le monstre était atroce. Imbroglio macabre de bras, de jambes et de lambeaux de chair sanguinolents auquel quelque électricité impie avait insufflé une vie maléfique, la créature se tenait devant moi, le regard glacial. Nous savions tous deux la raison de ma venue. À ce moment précis, l'électrode sur ma droite a généré une étincelle. J'ai alors mis en joue le colosse.]]
			}
		},
		['cover_the_fighter'] = {
			['choice'] = {
				['4'] = [[Exploser la serrure]],
				['1'] = [[Entrer dans le bâtiment]],
				['5'] = [[Passer la porte]],
				['3'] = [[Crocheter la serrure]]
			},
			['episode'] = {
				['4'] = [[Un bruissement et un raclement se font entendre dans l'obscurité. Nous sortons nos lampes de poche juste à temps : de viles créatures ont élu domicile ici, elles s'apprêtent à nous attaquer.]],
				['5'] = [[Mon compagnon baisse son arme et, respirant bruyamment, inspecte les lieux :<new_line><new_line>'Ouf, heureusement que je t'ai attendu. Sinon, je ne serais pas sorti d'ici vivant. Maintenant, il faut tout fouiller avant que ces monstres reviennent au pas de course.']],
				['2'] = [[L'éclaireur fume, assis à l'entrée d'un hangar rouillé. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['3'] = [[L'éclaireur fume, assis à l'entrée d'un abri anti-aérien. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['8'] = [[Au prix d'un grand effort, j'ai réussi à ouvrir le coffre-fort. À l'intérieur se trouvaient des appareils électroniques en excellent état et des boîtes de munitions. L'éclaireur siffla d'étonnement avant de me donner une tape sur l'épaule.]],
				['9'] = [[L'éclaireur est occupé à examiner tous les casiers et les recoins sombres des lieux. Le coffre-fort est resté là, intact.]],
				['6'] = [[Mon compagnon baisse son arme et, respirant bruyamment, inspecte les lieux :<new_line><new_line>'Ouf, heureusement que je t'ai attendu. Sinon, je ne serais pas sorti d'ici vivant. Maintenant, il faut tout fouiller avant que ces monstres reviennent au pas de course. Merci de ton aide, et bonne chance à toi !']],
				['7'] = [['Mince, vise un peu ce coffre-fort ! Avec une serrure comme celle-là, le contenu doit valoir son pesant de cacahuètes. Dommage que le temps presse. On a fait le gros du boulot, mais si tu pouvais ouvrir le coffre, nos gars te paieraient un joli supplément.']],
				['12'] = [[L'éclaireur est mort. J'ai échoué dans ma mission de protection.]],
				['1'] = [[L'éclaireur fume, assis à l'entrée d'un long garage. En me voyant, il se lève d'un bond :<new_line>'Des renforts. Enfin une bonne nouvelle, mec ! J'ai déjà crocheté la serrure. On s'y met, d'accord ?']],
				['11'] = [['Le butin n'est pas dingue mais, au moins, il est à nous. Il y a une autre porte, là-bas. Peut-être qu'on trouvera quelque chose derrière pour nous maintenir en vie. Mais d'après ce que j'entends, ça promet du grabuge. On se risque au combat, ou on rentre au bercail ?']],
				['10'] = [[Il n'y avait personne à l'intérieur. L'éclaireur jeta un coup d'œil aux alentours avant de revenir dans ma direction.]]
			}
		},
		['march_skeletons'] = {
			['episode'] = {
				['4'] = [[Pour terminer le rituel, je dois invoquer l'esprit. Insuffle la vie à ces os, ô Grand...]],
				['5'] = [[Dès que j'ai terminé, un vent glacial s'est levé et les squelettes se sont dressés dans d'atroces claquements d'os, formant un cercle autour de moi. Les voilà prêts à recevoir mes ordres.]],
				['2'] = [[Une fois les squelettes positionnés en cercle, je dois saupoudrer les crânes de...]],
				['3'] = [[Pendant que je saupoudre, je devrais faire le tour du cercle trois fois...]],
				['1'] = [[Eh bien, j'ai les squelettes et le parchemin. Je suis au bon endroit. J'imagine que je peux commencer.<new_line><new_line>Je dois positionner les squelettes en cercle.]],
				['6'] = [[Plusieurs squelettes ont commencé à bouger et m'ont attaqué. Apparemment, j'ai commis des erreurs.]]
			},
			['choice'] = {
