	['barQuest'] = {
		['halloween_boss_2022_3'] = {
			['text'] = [[Le Comte Dracula est un vampire légendaire, un amateur d'art qui apprécie la compagnie des belles femmes. On raconte qu'il est poli et galant, ce qui ne l'empêche pas de traquer ses victimes pour se repaître de leur sang.]],
			['step'] = {
				['1'] = [[On signale qu'un groupe de voyageurs a été attaqué par des vampires. Je dois me rendre sur le lieu de l'incident.]],
				['2'] = [[J'ai trouvé une survivante de l'attaque des vampires. Je dois d'abord l'escorter jusqu'à chez elle avant de l'interroger sur l'embuscade.]],
				['3'] = [[J'étais au menu du Comte Dracula. Il m'a invité à me détendre et à profiter... avant de passer aux choses sérieuses.]]
			}
		},
		['sled_repair'] = {
			['step'] = {
				['1'] = [[Snegurochka m'a demandé de réparer son traîneau.]]
			},
			['name'] = [[Réparation du traîneau]],
			['text'] = [[J'ai parcouru tellement de kilomètres avec vous que mon traîneau est vraiment en mauvais état. Pourriez-vous le réparer ?]]
		},
		['hunt_monsters'] = {
			['text'] = [[La zone où se trouve le portail est infestée de morts-vivants qui ont sombré dans la folie. Le moment est venu de mettre tous vos talents à l'œuvre pour calmer ces zombies.]],
			['step'] = {
				['1'] = [[On m'a demandé de réduire le nombre de morts-vivants dans la zone.]]
			}
		},
		['lair'] = {
			['text'] = [[Selon des rapports que j'ai reçus, une bête sauvage a élu domicile non loin d'ici. Vous devez vous en charger.]],
			['step'] = {
				['1'] = [[Je dois localiser la tanière d'une créature. Si l'animal s'avère agressif, on m'a demandé de le tuer.]]
			}
		},
		['mirror_lady'] = {
			['step'] = {
				['1'] = [[Le tavernier a dit qu'Igor se cachait peut-être avec sa dulcinée. Je devrais aller voir tout ça de plus près.]]
			},
			['name'] = [[Beauté dans le miroir]],
			['text'] = [[Selon les rumeurs qui courent, Igor courtise Marie la Sanglante. Peut-être se cache-t-il avec elle ? Mais soyez prudent, cette femme est redoutable. Pour qu'elle vienne à vous, il faut allumer une bougie et murmurer son nom devant un miroir.]]
		},
		['progress_briansk'] = {
			['step'] = {
				['1'] = [[Pendant la catastrophe, un énorme afflux de réfugiés traversa Briansk et accusa un taux de mortalité incroyablement élevé. Cependant, le nombre de survivants était inhabituellement important, lui aussi, et ils avaient développé en conséquence une forme d'immunité.<new_line><new_line>Le problème, c'est qu'ils sont toujours coincés dans les environs de Briansk, sans véritable aide ni aucune organisation. Beaucoup lorgnent déjà sur leur camp : ils sont nombreux à vouloir exploiter une telle masse de ressources humaines. Et, comme nous ne le savons que trop bien, pas dans un but noble.]]
			}
		},
		['progress_magnitogorsk'] = {
			['step'] = {
				['1'] = [[Lors du bombardement de l'Oural, c'est dans les mines de Magnitogorsk que se sont abrités ceux qui dirigent aujourd'hui le Front du Fer. Aujourd'hui, beaucoup triment dans ces mines, bien que beaucoup d'entre eux n'entreprennent pas volontairement ce travail exténuant.<new_line><new_line>Je crois avoir assez de force pour m'installer ici en tant que partenaire et éviter de faire partie de la main-d'œuvre gratuite.]]
			}
		},
		['order_delivery'] = {
			['step'] = {
				['4'] = [[J'ai livré la marchandise et le montant a été remboursé.]],
				['5'] = [[Je n'ai pas réussi à livrer la marchandise.]],
				['2'] = [[J'ai la marchandise. Je dois rentrer à la base.]],
				['3'] = [[La marchandise est arrivée indemne.]],
				['1'] = [[Je dois aller à la base et récupérer la marchandise.]],
				['6'] = [[Le client a refusé d'acheter la marchandise, je suis forcé de la garder.]]
			},
			['name'] = [[Livraison]],
			['text'] = [[Il faut transférer la marchandise d'une autre base. Ça vous dit ?']]
		},
		['wood_delivery'] = {
			['step'] = {
				['1'] = [[Snegurochka m'a demandé de réunir du petit bois et de le donner aux gens.]]
			},
			['name'] = [[Livraison de bois]],
			['text'] = [[Par un tel froid, un fagot de bois peut sauver une vie. Réunissez du bois et mettez-le à un endroit où tout le monde pourra le trouver.]]
		},
		['search_killer'] = {
			['step'] = {
				['1'] = [[Je dois traquer le tueur et l'empêcher de poursuivre sa coupable entreprise. Il se trouve quelque part dans le Monde au-dessus.]]
			},
			['name'] = [[Trouver le tueur]],
			['text'] = [[Le tueur s'est rué en direction du portail qui mène vers le Monde au-dessus. Vous devez retrouver ce malfaiteur et mettre un terme à ses exactions.]]
		},
		['forester'] = {
			['step'] = {
				['1'] = [[Quelqu'un a un travail à me proposer. Ce bûcheron m'a promis une forte récompense si je lui prête main-forte.]]
			},
			['name'] = [[Bûcheron]],
			['text'] = [[Un type est venu, un bûcheron. Et il a un travail pour vous. Une besogne qui m'a l'air fort aisée, avec une bonne récompense à la clé. Personnellement, je le ferais sans hésiter.]]
		},
		['new_year_deliver_cloth'] = {
			['step'] = {
				['2'] = [[Je dois apporter des affaires à un autre groupe de colons partis pour affaires.]],
				['1'] = [[J'ai décidé de livrer des vêtements chauds à ceux qui travaillent hors du camp.]]
			},
			['name'] = [[Livrer des vêtements]],
			['text'] = [[Les enfants ont tricoté des chaussettes et des bonnets pour les gens obligés, malgré un temps aussi froid, de travailler à l'extérieur de l'enceinte du camp. Pourriez-vous leur déposer quelques colis ?]]
		},
		['gift_delivery'] = {
			['step'] = {
				['1'] = [[Je suis chargé de livrer quelques cadeaux.]]
			},
			['name'] = [[Livraison des cadeaux]],
			['text'] = [[Il faut livrer les cadeaux coûte que coûte. Prenez ces cartons et apportez-les aux gens. Ils ont énormément besoin d'aide en ce moment.]]
		},
		['progress_tver'] = {
			['step'] = {
				['1'] = [[Des gens sont venus insuffler une seconde vie au port longtemps resté à l'abandon. Désormais, des bateaux de Tver sillonnent le fleuve vers le nord et vers le sud. Il est également devenu possible, dans le bruyant bâtiment principal, d'acquérir des objets rares obtenus à grands risques à Moscou ou échangés à Riazan.<new_line><new_line>Les habitants du camp s'occupent de leurs propres affaires mais accueillent avec plaisir les voyageurs errants dans leur vie trépidante, dont l'odeur caractéristique rappelle autant le poisson que le goudron. Seuls les flemmards ne sont pas les bienvenus.]]
			}
		},
		['halloween_boss_2022_1'] = {
			['step'] = {
				['7'] = [[Je dois anéantir ce Démon et son culte.]],
				['1'] = [[Les cultistes se réunissent dans une grange abandonnée, quelque part dans la friche. Je dois trouver le moyen d'y accéder.]],
				['5'] = [[Les cultistes réclament une offrande comme droit d'entrée. Je dois leur donner soit une bouteille de vodka, soit une bougie artisanale. En tout cas, le gardien est ouvert au bakchich.]],
				['3'] = [[Il me faut un déguisement pour entrer là-dedans. Avec les bons matériaux, je pourrais fabriquer une robe.]]
			},
			['name'] = [[Démon Balthazar]],
			['text'] = [[Les cultistes invoquent le Démon Balthazar pour le servir. Ce dernier les récompense avec des Cadeaux d'une puissance telle qu'un humain s'emparant de tous ces dons pourrait rivaliser avec lui.]]
		},
		['flying_dutchman'] = {
			['step'] = {
				['1'] = [[Le tavernier m'a donné une clochette qui me permettra d'atteindre le navire fantôme où Igor se cache probablement avec le grimoire.]]
			},
			['name'] = [[Hollandais volant]],
			['text'] = [[Vous dites qu'il n'est pas possible de trouver ce livre sur la terre ferme ? À en croire les rumeurs, le Hollandais volant pourfend à nouveau les eaux au large de nos vertes contrées. Une coïncidence ? J'en doute fort ! Si vous n'êtes pas du genre à vous effaroucher facilement, prenez cette clochette. Rendez-vous sur la côte et faites-la sonner pour que se matérialise un chemin menant au navire fantôme.]]
		},
		['reinforcement'] = {
			['step'] = {
				['4'] = [[Nous les avons repoussés, malgré quelques pertes.]],
				['1'] = [[Je dois me dépêcher d'aider nos hommes. On ne sait pas combien de temps ils tiendront sans renforts.]],
				['2'] = [[L'affrontement est terminé. Je peux rentrer à la base pour la récompense.]],
				['3'] = [[En luttant ensemble, nous avons réussir à les vaincre.]]
			},
			['name'] = [[Renforts]],
			['text'] = [[Un message urgent vient d'arriver : une unité de notre base demande des renforts. Allez les aider.']]
		},
		['frankenstein'] = {
			['step'] = {
				['1'] = [[On m'a demandé d'empêcher la réanimation du monstre de Frankenstein.]]
			},
			['name'] = [[Monstre de Frankenstein]],
			['text'] = [[À en juger par le message d'Igor, il essaye de ramener à la vie une monstruosité maudite. Je pense savoir où vous pourriez trouver le fossoyeur fou. Les gens parlent d'une demeure régulièrement frappée par la foudre. Vu que vous avez commencé, mieux vaut en finir une bonne fois pour toutes.]]
		},
		['letter_of_hope'] = {
			['step'] = {
				['1'] = [[Snegurochka m'a demandé de collecter les lettres à l'attention de Ded Moroz.]]
			},
			['name'] = [[Lettres d'espoir]],
			['text'] = [[Même en ces temps troublés, les gens sont toujours prêts à écrire des lettres à Ded Moroz. Ils les laissent ici et là. Collectez ces missives et apportez-les-moi.]]
		},
		['passenger'] = {
			['step'] = {
				['1'] = [[Un des colons de la région m'a recruté pour l'escorter jusqu'à une autre base.]],
				['2'] = [[Le colon a réussi à rejoindre sa destination sain et sauf.]],
				['3'] = [[Je n'ai pas pu protéger mon compagnon. Il est mort avant d'atteindre sa destination.]]
			},
			['name'] = [[Passager]],
			['text'] = [['Bonjour ! Vous pourriez me raccompagner jusqu'au prochain camp ? Voyager en solitaire à travers les friches, c'est dangereux. Vous avez l'air d'être quelqu'un de fiable.']]
		},
		['toy_story'] = {
			['name'] = [[Histoire de jouets]],
			['text'] = [[Grâce à vous, une partie de la magie a été restaurée, et j'ai trouvé un moyen d'atteindre notre terem, mais je crains que le sentier ne soit jonché de pièges. Veuillez vous rendre sur place pour en avoir le cœur net, mais je vous en prie, soyez prudent.]]
		},
		['scientist_rescue'] = {
			['step'] = {
				['4'] = [[J'ai trouvé le scientifique, je dois maintenant le ramener au camp.<new_line><new_line>En chemin, nous avons repris l'équipement volé au scientifique.]],
				['5'] = [[J'ai trouvé le scientifique, il m'attend pour je le ramène au camp.]],
				['2'] = [[J'ai trouvé le scientifique, je dois maintenant le ramener au camp.]],
				['3'] = [[Prendre l'équipement des bandits.]],
				['8'] = [[J'ai trouvé le scientifique, je dois maintenant le ramener au camp.<new_line><new_line>Avant de partir, j'ai aidé le scientifique à démonter l'appareil restant.]],
				['9'] = [[Détacher du toit l'appareil restant.]],
				['6'] = [[J'ai trouvé le scientifique, je dois maintenant le ramener au camp.<new_line><new_line>Avant de partir, j'ai aidé le scientifique à panser une blessure légère.]],
				['7'] = [[Aider le scientifique à panser une blessure légère.]],
				['1'] = [[Je dois fouiller l'éco-station et ramener le scientifique au camp.]]
			},
			['name'] = [[Ramener le scientifique]],
			['text'] = [[Notre scientifique est en poste à l'éco-station. Il nous a contactés pour signaler des problèmes avec des bandits. Allez le chercher et ramenez-le.]]
		},
		['pumpkin_seed'] = {
			['step'] = {
				['1'] = [[On m'a remis une graine que je dois planter dans la friche. Je ne sais pas ce qui terrifie autant le tavernier ni en quoi cette graine va m'être utile. Elle a l'air totalement inoffensive.]]
			},
			['name'] = [[Graine de citrouille]],
			['text'] = [[Si la tête dit la vérité, nous sommes dans le pétrin jusqu'au cou. Nous avons un joker... mais j'espérais ne pas avoir à l'utiliser. Tant pis, prenez cette graine de citrouille et plantez-la au beau milieu de la friche.]]
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[Le Kremlin de Tula accueille les vagabonds avec une batterie de canons modernes placée le long des créneaux de l'enceinte de la forteresse ancienne. Gagner la confiance des anciens ouvriers de l'Usine d'armement promet d'être difficile.<new_line><new_line>Plusieurs factions convoitent ardemment les importants stocks d'armes de Tula, en particulier les gangs à la tête de Riazan. Toutefois, les courageux habitants se tiennent à distance des affaires et des alliances avec autrui, se contentant d'un commerce basique et d'une surveillance étroite depuis leurs murs.]]
			}
		},
		['secret_documents'] = {
			['step'] = {
				['2'] = [[Je me suis frayé un passage. Il ne me reste plus qu'à forcer le coffre-fort pour prendre les documents secrets.]],
				['1'] = [[Je dois examiner le laboratoire abandonné. C'est là que se trouvent les documents secrets qu'il faut au Biologiste pour continuer ses recherches.]]
			},
			['name'] = [[Documents secrets]],
			['text'] = [[Camarade, vos efforts n'ont pas été vains ! Nous avons enfin découvert où se trouvent les documents secrets liés à la biostation la plus proche ! Allez au laboratoire abandonné, les documents devraient s'y trouver.']]
		},
		['science_polygon'] = {
			['step'] = {
				['1'] = [[Je dois aider les scientifiques de Progress à se débarrasser des biomutants.]]
			},
			['name'] = [[Champ de test]],
			['text'] = [[Au champ de test, nous conduisons des expériences pour mieux comprendre le projet EMBA. Mais on a des soucis avec des biomutants qui nous attaquent. Vous avez beaucoup d'expérience de combat, pouvez-vous contenir cette menace ?']]
		},
		['help_hunter'] = {
			['step'] = {
				['1'] = [[Je dois aller prêter main-forte à un autre chasseur.]]
			},
			['name'] = [[Aider le chasseur]],
			['text'] = [[Un chasseur s'est attiré des ennuis. Allez lui donner un coup de main. J'indique sur la carte l'endroit où vous le trouverez.]]
		},
		['undertaker'] = {
			['step'] = {
				['2'] = [[Les nouveaux cercueils doivent être livrés au cimetière en ruine.]],
				['1'] = [[Je dois trouver des matériaux et demander au Croque-mort de fabriquer des cercueils.]]
			},
			['name'] = [[Le Croque-mort]],
			['text'] = [[Une tempête s'est abattue sur un cimetière. Les zombies se sont calmés, mais ils ont besoin de nouveaux cercueils. Allez-y avec le Croque-mort. Vous trouverez les matériaux et lui, fabriquera les cercueils. Après quoi, il ne vous restera plus qu'à les apporter au bon endroit.]]
		},
		['progress_chelyabinsk'] = {
			['step'] = {
				['1'] = [[Tcheliabinsk est un camp industriel densément peuplé. Il forme la base à partir de laquelle le Contremaître contrôle la production et tous les autres aspects de la vie sur le Front du Fer.<new_line><new_line>Logique ; c'est de là qu'est assurée la liaison ferroviaire avec d'autres villes. Le Front du Fer inonde la Sibérie occidentale d'une grande quantité de métaux en échange de nourriture, étant donné que la région de Tcheliabinsk ne permet aucun type d'exploitation agricole.]]
			}
		},
		['provision'] = {
			['step'] = {
				['2'] = [[J'ai récupéré tout ce qu'il fallait. Je dois donner ces provisions aux archivistes du camp.]],
				['1'] = [[Un membre de Progress m'a demandé de trouver des provisions pour l'expédition.]]
			},
			['name'] = [[Provisions]],
			['text'] = [[Nos éclaireurs vont chercher les installations connectées au projet EMBA. Aidez-les à trouver des provisions pour cette expédition.']]
		},
		['camp_supply'] = {
			['step'] = {
				['4'] = [[J'ai aidé les alliés à repousser une attaque avant de livrer les provisions.]],
				['5'] = [[Personne n'a survécu sauf moi. Les provisions n'ont pas pu être livrées.]],
				['2'] = [[Les provisions ont été livrées, je ne peux rien faire de plus. Je rentre chez moi.]],
				['3'] = [[J'ai livré les provisions. Nos alliés nous en sont reconnaissants.]],
				['8'] = [[Aider les alliés à repousser l'attaque.]],
				['9'] = [[Soigner l'allié blessé dans le camp.]],
				['6'] = [[À mon arrivée, le camp était jonché de cadavres. Les provisions n'ont pas pu être livrées.]],
				['7'] = [[Le camp de nos alliés est attaqué. Je devrais dire au nôtre d'envoyer de l'aide.]],
				['1'] = [[On m'a demandé d'apporter des provisions au camp de nos alliés.]]
			},
			['name'] = [[Provisions de camp]],
			['text'] = [[Le camp de nos alliés traverse une période difficile. Nous avons choisi de leur envoyer des provisions. Pouvez-vous les leur apporter ?']]
		},
		['intelligence'] = {
			['step'] = {
				['2'] = [[J'ai les données, il est temps de retourner au camp.]],
				['1'] = [[Les scientifiques de Progress m'ont chargé d'obtenir des données de reconnaissance à la base militaire.]]
			},
			['name'] = [[Données de reconnaissance]],
			['text'] = [[Nous avons découvert une base militaire liée au projet EMBA. On devrait y trouver des données sur les installations secrètes de la région. Celles-ci pourraient nous mener aux documents concernant le projet.']]
		},
		['halloween_boss_2022_5'] = {
			['text'] = [[Le scientifique Victor Frankenstein a fabriqué un monstre à partir des cadavres d'autres personnes et lui a donné vie grâce à l'électricité.<new_line>Le Monstre de Frankenstein et son ami bossu Igor sont venus en ce bas monde assez récemment, mais ils totalisent déjà un grand nombre de meurtres sanglants : on rapporte de nombreux cas de cadavres aux organes sectionnés.]],
			['step'] = {
				['1'] = [[La Faucheuse m'a donné une liste tirée du Livre de la mort détaillant les prochaines personnes qui mourront dans la région où Igor et le Monstre ont tué la dernière fois. Je dois vérifier chaque entrée de la page déchirée—la prochaine victime doit en faire partie.]],
				['2'] = [[Je dois me rendre à l'endroit suivant mentionné par le Livre de la mort.]],
				['3'] = [[J'ai retrouvé la trace d'Igor et du Monstre de Frankenstein. Je dois les affronter et les vaincre.]]
			}
		},
		['new_year_boss_2022_1'] = {
			['step'] = {
				['1'] = [[Un nouveau Père est apparu près du sapin. Allons voir ce qu'il a à offrir.]]
			}
		},
		['camp_defence'] = {
			['step'] = {
				['4'] = [[Eh bien, nous avons gagné, malgré quelques victimes.]],
				['5'] = [[Nous avons arraché la victoire, mais certains sont tombés au combat.]],
				['2'] = [[J'ai fait tout mon possible. Je dois rentrer à la base et expliquer ce qui s'est passé au camp.]],
				['3'] = [[Les bandits ont été repoussés facilement, les défenses ont tenu.]],
				['1'] = [[Je dois me rendre immédiatement au camp de nos alliés pour les aider à se défendre.]],
				['6'] = [[Le temps que j'arrive sur place, c'était déjà trop tard.]]
			},
			['name'] = [[Défense de camp]],
			['text'] = [[Les bandits se sont rassemblés pour attaquer notre camp. Rendez-moi service, participez à la défense de notre territoire.']]
		},
		['hunt'] = {
			['step'] = {
				['4'] = [[Je n'ai pas apporté assez de viande, le client n'a pas payé.]],
				['1'] = [[Je dois chercher de la viande. C'est l'heure de chasser.]],
				['2'] = [[Je dois apporter la viande au client.]],
				['3'] = [[Tout s'est bien passé, le client est satisfait.]]
			},
			['name'] = [[Chasse]],
			['text'] = [[On vient d'avoir une commande pour de la viande. Envie de chasser ?']]
		},
		['new_year_boss_2022_5'] = {
			['step'] = {
				['2'] = [[Nous avons vaincu le Père Fouettard, mais le maléfique Glagla s'est emparé de l'énergie magique libérée pendant le combat. Je dois discuter de notre plan avec mes compères.]],
				['1'] = [[Nous avons fait notre possible dans l'optique d'un combat contre le Père Fouettard et son arme secrète. Le moment est venu de l'affronter.]]
			}
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Étonnamment, je connaissais déjà les habitants de Kourgan. Il s'avère que ce sont eux qui gèrent tout le réseau ferroviaire. Si un homme du coin ne roule pas sur les chemins de fer, il les répare. Certains désaccords ont ainsi plus de conséquences que d'autres.<new_line><new_line>Leurs trains blindés sont pratiquement invulnérables aux attaques de bandits et de mutants, bien qu'on puisse tout imaginer dans les friches. C'est pourquoi il est tout aussi important pour Kourgan d'entretenir de bonnes relations avec ses partenaires, ne serait-ce que pour les besoins en carburant et les services d'entretien de leurs trains.]]
			}
		},
		['gigahive_ufa'] = {
			['name'] = [[Pas les bonnes abeilles]],
			['step'] = {
				['1'] = [[Je dois aller à Izhevsk et interroger le barman au sujet de la ruche à Oufa.]],
				['2'] = [[À Oufa, il y a une ruche dirigée par une reine des abeilles. Avant de m'y rendre, je dois trouver un antidote. Pour cela, je devrai tuer d'autres reines et apporter la cire à un spécialiste basé à Izhevsk. D'ailleurs, le barman m'a promis une récompense contre la tête de la reine d'Oufa.]],
				['3'] = [[J'ai tué la reine des abeilles. Je dois apporter sa tête au barman d'Izhevsk pour toucher ma récompense.]]
			}
		},
		['halloween_boss_2022_2'] = {
			['text'] = [[Le Médecin de la peste mène ses expériences diaboliques sous couvert de vapeurs toxiques : il ressuscite les morts et sème les maladies parmi les vivants. L'armure démoniaque peut contrer les malédictions du Médecin de la peste, mais lui-même n'est pas facile à confronter. Ce maître des potions est rusé, et il est gardé par des hordes de zombies.]],
			['step'] = {
				['1'] = [[Une petite communauté est harcelée par des zombies. C'est la preuve que le Médecin de la peste se trouve dans les parages. Je dois rencontrer ces gens pour les interroger.]],
				['2'] = [[Les gens disent que le Médecin de la peste s'est installé dans le cimetière du coin, ma prochaine destination.]],
				['3'] = [[Je dois vaincre le Médecin de la peste. La Faucheuse m'a fait comprendre que l'Apparence démoniaque me protègera de ses attaques.]]
			}
		},
		['progress_novosibirsk'] = {
			['step'] = {
				['1'] = [[Demandez à n'importe qui en Sibérie le meilleur endroit pour se faire soigner correctement, et ils vous enverront tous à Novossibirsk. Il paraît que c'est grâce au centre de recherche local.<new_line><new_line>Bien sûr, des tas de rumeurs circulent à ce sujet... Par exemple, qu'il faut rembourser son traitement pendant des années ; pour les plus chanceux, avec un simple travail ordinaire. Cependant, vous ne trouverez nulle part ailleurs un tel niveau de soins et de pharmacologie, ce qui donne une place d'honneur à Novossibirsk en S.O.V.A.]]
			}
		},
		['new_year_boss_2022_3'] = {
			['step'] = {
				['1'] = [[Il y a eu une grosse erreur au parc d'attractions, mais nous pouvons encore négocier avec les représentants de la bande de Fouettard.]]
			}
		},
		['firebird_feather'] = {
			['name'] = [[La plume de l'Oiseau de feu]],
			['text'] = [[Dans les vieilles légendes, on raconte que les pommes dorées attirent l'Oiseau de feu. Nous n'en avons pas ici, mais j'ai réussi à enchanter quelques pommes ordinaires. Si vous les recouvrez de miel, l'oiseau ne verra pas de différence et devrait virevolter dans votre direction.]]
		},
		['comrade_in_trouble'] = {
			['step'] = {
				['31'] = [[Ramener le corps de l'éclaireur au camp.]],
				['30'] = [[L'éclaireur est mort. Je dois retourner au camp et faire mon rapport.]],
				['29'] = [[L'éclaireur est mort. J'ai décidé de ramener son corps.]],
				['26'] = [[L'éclaireur était blessé. Je dois retourner au camp et faire mon rapport.]],
				['25'] = [[L'éclaireur était blessé. Je lui ai donné les premiers soins. Je dois retourner au camp et faire mon rapport.]],
				['23'] = [[L'éclaireur s'est retrouvé coincé dans une vieille maison qui s'est effondrée. Je dois retourner au camp et faire mon rapport.]],
				['22'] = [[L'éclaireur s'est retrouvé coincé dans une vieille maison qui s'est effondrée. Je l'ai aidé à sortir. Je dois retourner au camp et faire mon rapport.]],
				['20'] = [[Les bandits ont poursuivi l'éclaireur jusqu'à une maison délabrée, mais il tient bon. Je dois retourner au camp et faire mon rapport.]],
				['19'] = [[L'éclaireur s'est barricadé dans une maison délabrée contre des bandits. J'ai réglé le problème. Je dois retourner au camp et faire mon rapport.]],
				['4'] = [[La réponse est simple—l'éclaireur a fait la fête. J'ai remis du bois dans son feu, au cas où. Je dois retourner au camp et faire mon rapport.]],
				['5'] = [[La réponse est simple—l'éclaireur a fait la fête. Je dois retourner au camp et faire mon rapport.]],
				['2'] = [[L'éclaireur va très bien. Je dois retourner au camp et faire mon rapport.]],
				['3'] = [[L'éclaireur s'est arrêté pour peindre en plein air. Heureusement, il n'a pas eu d'ennuis. Je dois retourner au camp et faire mon rapport.]],
				['8'] = [[L'éclaireur a reçu une dose de radiations. Je dois retourner au camp et faire mon rapport.]],
				['9'] = [[Sauver l'éclaireur sans aide extérieure.]],
				['6'] = [[Allumer un feu au camp.]],
				['7'] = [[L'éclaireur a reçu une dose de radiations. Je lui ai donné un médicament. Je dois retourner au camp et faire mon rapport.]],
				['10'] = [[J'ai protégé l'éclaireur contre les prédateurs. Je dois retourner au camp et faire mon rapport.]],
				['11'] = [[Des prédateurs ont attaqué l'éclaireur. Je dois retourner au camp et faire mon rapport.]],
				['16'] = [[L'éclaireur a mis du temps à récupérer le butin, il ne rentre que maintenant. J'ai proposé de livrer la caisse restante. Je dois retourner au camp et faire mon rapport.]],
				['13'] = [[L'éclaireur est tombé en panne. Je lui ai donné du carburant. Je dois retourner au camp et faire mon rapport.]],
				['14'] = [[L'éclaireur est tombé en panne. Je dois retourner au camp et faire mon rapport.]],
				['1'] = [[Je dois découvrir ce qui est arrivé à l'éclaireur disparu. Le chef des gardes m'a indiqué sur la carte où le trouver.]],
				['17'] = [[L'éclaireur a mis du temps à récupérer le butin, il ne rentre que maintenant. Je dois retourner au camp et faire mon rapport.]],
				['18'] = [[Apporter la caisse restante au camp.]]
			},
			['name'] = [[Camarade en détresse]],
			['text'] = [[Un de nos gars devait rentrer hier d'une expédition, mais il ne donne aucun signe de vie. Allez voir s'il lui est arrivé quelque chose et s'il a besoin d'aide.]]
		},
		['new_year_boss_2022_2'] = {
			['step'] = {
				['4'] = [[Nous avons découvert que le Père Noël s'était transformé en Père Fouettard et que sa bande détruisait tout ce qui avait trait à Noël. Il faut retrouver le Père Négoce pour discuter de la marche à suivre.]],
				['5'] = [[Nous sommes sur la piste du Père Noël disparu. Le vaurien qui a échappé à l'embuscade pourrait nous mener jusqu'à notre cible. Mieux vaudrait le suivre.]],
				['2'] = [[Je dois cacher le trou de Père Nomade avec de l'herbe.]],
				['3'] = [[Nous tendons un piège, et je dois faire le leurre.]],
				['1'] = [[Je dois creuser un trou. Père Nomade veut sans doute fabriquer un piège.]]
			}
		},
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Izhevsk est séparée du Front du Fer par la chaîne de l'Oural et des kilomètres de terre infectée grouillant de mutants. C'est sans doute pour cela que le Front du Fer n'a toujours pas mis la main sur l'industrie d'armement locale.<new_line><new_line>Ainsi, les colons d'Izhevsk tiennent à maintenir le contact avec Riazan—ils peuvent échanger en paix et à distance leurs armes et les produits de leur industrie d'abattage d'abeilles.]]
			}
		},
		['progress_gorenichi'] = {
			['step'] = {
				['1'] = [[Au moment de la catastrophe, les terres environnantes furent soumises à des bombardements massifs. Certaines parcelles de terre noire et fertile, cependant, survécurent.<new_line>Des habitants trouvèrent ces territoires et commencèrent à les cultiver.<new_line><new_line>C'est alors qu'apparut une nouvelle menace issue des radiations : des créatures mutantes assiégèrent le camp par vagues incessantes. Toutefois, ce sont les loups noirs qui finirent par se hisser au sommet de la chaîne alimentaire en décimant la concurrence. Reste que la force des survivants humains décline sous leurs assauts féroces.]]
			}
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[Le premier camp habité de mon voyage. C'est éprouvant pour les nerfs de se retrouver à nouveau avec de la compagnie. Ici, chacun a pris l'habitude de compter sur l'autre et sur ses propres ressources pour faire face à l'adversité. Ils vivent probablement de la même façon que le siècle dernier.<new_line><new_line>Les pêcheurs ne vont pas plus loin que le lac Ladoga, et je doute que je trouve des réponses à toutes mes questions ici, mais c'est un bon endroit pour me tenir au courant des nouvelles avant de m'aventurer plus loin.]]
			}
		},
		['new_year_tree_quest'] = {
			['step'] = {
				['2'] = [[J'ai récupéré les sapins, je dois maintenant les apporter au camp.]],
				['1'] = [[Je dois récupérer et livrer au camp un chargement de sapins de Noël. Cela devrait remonter le moral des troupes et les aider à passer l'hiver.]]
			},
			['name'] = [[Livrer les sapins de Noël]],
			['text'] = [[Cet hiver, chaque maison devrait avoir un sapin de Noël ! Les fêtes de fin d'année sont l'un des rares plaisirs qui nous restent, par ici. Allez voir les bûcherons, ils ont mis des petites choses de côté pour nous.']]
		},
		['progress_leningrad'] = {
			['step'] = {
				['1'] = [[Le camp de touristes abandonné se situe juste à la limite des forêts brûlées par les radiations et des friches qui bordent Léningrad. Des hommes intrépides de Tver ont fondé un relais là-bas.<new_line><new_line>Les 'touristes' (comme on appelle les explorateurs vétérans du secteur dangereux) font des expéditions à Léningrad pour rapporter leur butin à la base, avant de l'envoyer à Tver par voie fluviale.]]
			}
		},
		['progress_irkutsk'] = {
			['step'] = {
				['1'] = [[Parfois, des miracles se produisent. L'un d'eux s'est produit à Irkoutsk. Ni la centrale hydroélectrique d'Angara, ni les eaux du lac Baïkal n'ont été touchées par la catastrophe. Les habitants d'Irkoutsk chérissent maintenant la centrale comme leur enfant. Ils se sont même installés directement sur le barrage, fournissant de l'électricité et de l'eau potable à l'ensemble de S.O.V.A.]]
			}
		},
		['snowmen_fortress'] = {
			['step'] = {
				['1'] = [[Ma mission est de détruire la forteresse de neige et de me débarrasser des bonshommes de neige de la région.]]
			},
			['name'] = [[Forteresse de neige]],
			['text'] = [[Les bonshommes de neige ont construit une forteresse de neige et sèment le chaos dans la zone. Rendez-vous sur place et mettez un terme à leurs vilains tours.]]
		},
		['order'] = {
			['step'] = {
				['1'] = [[J'ai accepté d'accepter le travail pour le camp. Je dois leur procurer des provisions essentielles.]],
				['2'] = [[J'ai réuni tout ce qu'on m'a demandé. Il est l'heure de rentrer au camp.]],
				['3'] = [[J'ai livré toutes les provisions demandées.]]
			},
			['name'] = [[Commande]],
			['text_barman'] = [['Il faut qu'on ravitaille la cuisine. Vous voulez bien m'apporter des provisions ?']],
			['text_workshop'] = [['Il me manque quelques petites choses pour terminer mon travail. Vous pouvez aller les chercher ?']],
			['text_nurse'] = [['Certains médicaments arrivent à expiration, mais les gens tombent encore malades. Vous pouvez m'aider à en obtenir d'autres ?']],
			['text'] = [[J'ai une commande pour des provisions. Rien ne presse. Vous pouvez aller les chercher à ma place ? Voici la liste.']]
		},
		['destroy_lair'] = {
			['step'] = {
				['2'] = [[J'ai réussi à détruire la tanière.]],
				['1'] = [[Je dois localiser la tanière et supprimer ses occupants.]]
			},
			['name'] = [[Détruire la tanière]],
			['text'] = [[Le chef des gardes touche la carte accrochée derrière lui. 'Cette zone est infestée de bêtes dangereuses. Allez détruire leur tanière.']]
		},
		['new_year_boss_2022_4'] = {
			['step'] = {
				['4'] = [[Je crois que la mission est accomplie. Je dois rattraper Cyber Noël et découvrir le fruit de nos recherches.]],
				['1'] = [[Nous devons aider Cyber Noël à décuver. Il a besoin d'eau.]],
				['2'] = [[Cyber Noël va nous aider à nous renseigner au sujet de l'arme secrète. Je dois m'infiltrer dans un lieu où se trouvent ces informations sensibles.]],
				['3'] = [[Je me trouve impliqué dans les frasques déroutantes de Cyber Noël. Maintenant, je dois raccorder un câble quelque part et attendre un coup de téléphone.]]
			}
		},
		['progress_surgut'] = {
			['step'] = {
				['1'] = [[Depuis la grande catastrophe, Sourgout est cernée par le débordement de la rivière Ob. Sa seule connexion avec le monde extérieur se fait par la route qui subsiste jusqu'à Tioumen.<new_line><new_line>Tout ce que le camp peut faire, c'est approvisionner ses partenaires du Syndicat en pétrole brut. Heureusement, la ressource continue de couler à flots. Des voies de transport fluvial permettraient à Sourgout de commercer avec d'autres villes de Sibérie, mais qui leur en fournira les moyens matériels ?]]
			}
		},
		['traveler_salvation'] = {
			['step'] = {
				['1'] = [[Quelque part dans la forêt, un homme risque de mourir de froid. Je devrais me dépêcher et lui porter secours.]],
				['2'] = [[L'homme est très froid et épuisé, il a besoin d'aide.]],
				['3'] = [[état des voyageurs est améliorée. Maintenant, je dois lui rendre à un endroit sûr.]]
			},
			['name'] = [[À la rescousse]],
			['text'] = [[Un écureuil a dit qu'un homme meurt de froid au beau milieu de la forêt. Rendez-vous sur place le plus vite possible pour le secourir ! J'espère qu'il est encore vivant.]]
		},
		['progress_tymen'] = {
			['step'] = {
				['1'] = [['Bien sûr qu'ils vivent bien là-bas, avec leur or noir.' Voilà ce que j'entends le plus souvent à propos de Tioumen. Le camp doit sa richesse à ses raffineries de pétrole de haute capacité.<new_line><new_line>Les habitants seraient-ils devenus oisifs ? Sans les matières premières et le soutien logistique fournis par leurs partenaires du Syndicat, ce camp ne tiendrait pas un an.]]
			}
		},
		['exp_recovery'] = {
			['name'] = [[Inconscient]],
			['step'] = {
				['2'] = [[J'ai retrouvé mes souvenirs perdus.]],
				['1'] = [[À l'article de la mort, j'ai perdu connaissance. J'ai été sauvé par miracle, mais j'ai perdu les souvenirs des événements passés. Pour raviver ma mémoire, je dois retourner à l'endroit où tout est arrivé.]]
			}
		},
		['progress_rostov'] = {
			['step'] = {
				['1'] = [[Lorsque les steppes autour du Don se transformèrent en friches, l'Ataman unifia tous les survivants sous son commandement. Un groupe d'aventuriers se rallia autour de lui ; certains avaient des ancêtres cosaques, d'autres étaient simplement épris d'un sentiment romantique de liberté.<new_line><new_line>Ainsi, la steppe reprit vie sous le signe des cosaques, du moins, l'interprétation qu'en donnait l'Ataman. Sous la protection de la droujina, les gens ordinaires purent à nouveau cultiver la terre, ce qui sauva finalement tout Rostov de la famine.]]
			}
		},
		['doll'] = {
			['step'] = {
				['1'] = [[Le tavernier a indiqué sur la carte l'emplacement de l'atelier du marionnettiste. Peut-être y trouverai-je des réponses.]]
			},
			['name'] = [[Marionnette]],
			['text'] = [[Vous vouliez en savoir plus au sujet de la marionnette. Je ne connais qu'une personne capable de contrôler les faits et gestes de cette entité de bois : le marionnettiste. C'est un vil individu... mais nous n'avons pas le choix. Je vais indiquer sur la carte où le trouver.]]
		},
		['supply'] = {
			['step'] = {
				['4'] = [[J'ai été récompensé de mon travail, mais je m'attendais à plus de générosité.]],
				['5'] = [[Je n'ai touché aucune récompense. Ma foi, je trouve que ces trucs pourraient m'être utiles.]],
				['2'] = [[J'ai trouvé tout le nécessaire. Maintenant, je peux livrer les marchandises à la base.]],
				['3'] = [[J'ai été récompensé de mon travail.]],
				['1'] = [[On m'a envoyé chercher tout ce dont l'autre base avait besoin.]]
			},
			['name'] = [[Expédition]],
			['text'] = [[La base voisine nous demande des provisions. Vous pouvez tout réunir et vous charger du transport ?']]
		},
		['progress_voronej'] = {
			['step'] = {
				['1'] = [[Le camp de Voronej est protégé par des parachutistes en poste à Riazan peu de temps avant la catastrophe. L'aérodrome, qui a résisté, est devenu leur base.<new_line><new_line>Les soldats veulent depuis longtemps retourner à Riazan et en arracher le contrôle au gang qui le dirige. Il semblerait qu'ils aient la main-d'œuvre et l'équipement pour y parvenir, mais quelque chose les empêche d'agir.]]
			}
		},
		['progress_ryazan'] = {
			['step'] = {
				['1'] = [['Voir Riazan, et mourir'. C'est le trait d'humour noir qu'on raconte à des centaines de kilomètres autour du camp. Jadis, un clan de bandits prit le contrôle de la raffinerie de pétrole et lança un commerce prospère de carburant avec leur propre monnaie—le rouble noir. Bientôt, tout un bourg se développa autour de l'usine.<new_line><new_line>Aujourd'hui, ce camp est le carrefour commercial des friches. Toutefois, sa face cachée criminelle n'est jamais trop loin : tromperie, violence et débauche débordent des enseignes lumineuses et du luxe affiché des casinos.]]
			}
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk, qui a fait sécession de S.O.V.A., est maintenant dirigée par l'armée. Le camp est traversé par la seule voie commerciale reliant l'Est à l'Ouest.<new_line>Il semblerait que ce soit l'unique raison de la survie de ce camp refermé sur lui-même.<new_line><new_line>Cependant, des indices et des rumeurs suggèrent que l'armée dispose d'autre chose pour tenir à distance les autres puissances de l'Oural et de la Sibérie. Il est possible d'en savoir plus sur Omsk, mais peu de gens parviennent à établir des relations ne serait-ce qu'avec le périmètre extérieur de la ville.]]
			}
		},
		['impostor'] = {
			['step'] = {
				['1'] = [[Un bandit se faisant passer pour Ded Moroz sévit dans la zone et détrousse les badauds. Il faut l'en empêcher !]],
				['2'] = [[Maintenant, je sais où trouver l'imposteur, il est temps de lui rendre visite.]],
				['3'] = [[Le sac de Loot est avec moi. Je dois le rendre au peuple.]]
			},
			['name'] = [[Imposteur]],
			['text'] = [[Selon des rapports, un imposteur fait des siennes. Un bandit se fait passer pour Ded Moroz ! Il détrousse les badauds et met notre réputation en péril. Vous devez l'empêcher de continuer.]]
		},
		['herb_collect'] = {
			['step'] = {
				['1'] = [[Snegurochka m'a demandé de réunir quelques herbes pour aider les animaux.]]
			},
			['name'] = [[Herbes médicinales]],
			['text'] = [[L'hiver est une saison redoutable pour les animaux. Du coup, je les aide comme je peux. Réunissez ces herbes médicinales et apportez-les-moi dans la clairière.]]
		},
		['progress_sverdlovsk'] = {
			['step'] = {
				['1'] = [[Le Front du Fer considère Sverdlovsk comme sa capitale d'armement et l'origine de ses meilleures troupes. Les combattants d'ici sont bien armés et très motivés pour lancer des raids dans les friches.<new_line><new_line>Les pillards prétendent qu'ils n'attrapent que des bandits notoires pour les rééduquer en leur imposant un rude travail dans l'industrie. En réalité, même les voyageurs solitaires pacifiques se mettent en danger en passant par cette région.]]
			}
		},
		['halloween_boss_2022_6'] = {
			['step'] = {
				['1'] = [[L'Âne parlant se cache dans le marais. Je dois le localiser, si possible en évitant de tomber sur la bête qui le protège.]],
				['2'] = [[L'Âne est parvenu à s'échapper, et la bête des marais qui le protège peut surgir à n'importe quel moment. Vite, le temps presse !]],
				['3'] = [[La Faucheuse m'a tendu un piège avec ses contrats et veut prendre mon âme. J'ai découvert que mon Gigawattor pouvait fermer ses portails.<new_line>Je dois vaincre la Faucheuse au combat.]]
			},
			['name'] = [[Âne parlant]],
			['text'] = [[Un Âne parlant terrorise la population locale : il dévore leurs récoltes, fait circuler de fausses pièces de monnaie, et tant parlé à trois agriculteurs qu'ils en sont morts.<new_line>Les villageois lui auraient rendu justice à coups de fourche depuis bien longtemps si l'Âne n'était pas protégé par une créature qui vit dans le marais voisin.]]
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[Le camp de Krasnoyarsk ressemble à une authentique utopie scientifique. Les gens travaillent dans des serres dirigées par des scientifiques. Le système BIOS-4 qu'ils ont développé permet d'obtenir des récoltes sans précédent, voire de nourrir d'autres camps. Pas gratuitement, bien évidemment.<new_line><new_line>Tout a un prix : le processus entraîne une consommation importante d'électricité et d'eau propre. De plus, le processus est si sophistiqué que la perte d'un seul intervenant qualifié peut faire s'effondrer tout le système.]]
			}
		},
		['stash_polar_fox'] = {
			['step'] = {
				['1'] = [[Le renard polaire a trouvé une chose intéressante à proximité. Mieux vaut vérifier.]]
			}
		},
		['stash_handcar'] = {
			['step'] = {
				['1'] = [[J'ai trouvé un bout de papier avec l'adresse d'une maison à Kola. Je devrais y faire un tour. Si ça se trouve, il y a une planque avec des provisions, là-bas.]]
			}
