			['name'] = [[Cavalier sans tête]],
			['text'] = [[La fille que vous avez sauvée est venue tout à l'heure. Elle a répété une conversation qu'elle a entendue sur le navire. Il s'avère que Dracula veut utiliser ce grimoire pour extirper l'Entité ancestrale de son sommeil éternel et détruire la frontière entre les mondes. Nous devons l'en empêcher à tout prix ! Interceptez le Cavalier et prenez-lui le grimoire !]]
		},
		['gravedigger'] = {
			['step'] = {
				['1'] = [[On m'a chargé de m'occuper du fossoyeur fou.]]
			},
			['name'] = [[Fossoyeur]],
			['text'] = [[Ah, exactement le gars que je voulais voir. J'ai un travail pour vous. Un fossoyeur a sombré dans la folie. Au lieu d'enterrer les cadavres, il les déterre. Vous devez mettre un terme à ses actes odieux. Vous pouvez vous en charger ?]]
		},
		['progress_arzamas'] = {
			['step'] = {
				['1'] = [[Un homme errait dans les friches durant de longues années. Enfin libéré des chaînes de sa vie mondaine, il vit apparaître devant lui un buisson ardent d'herbes sacrées. Enveloppant l'homme de fumée, Il l'appela le Prophète et lui ordonna de fonder un Nouveau Paradis.<new_line><new_line>Telle est la fable des habitants du vieux monastère d'Arzamas. Nul ne sait ce qu'ils font réellement là-bas. Quelques membres de la communauté sont en contact avec le monde extérieur, et des caravanes partent encore vers Riazan, mais c'est tout ce que l'on sait.]]
			}
		},
		['frozen'] = {
			['step'] = {
				['1'] = [[Les brigands des glaces kidnappent de pauvres gens que je dois secourir au plus vite.]]
			},
			['name'] = [[Gelé]],
			['text'] = [[Les brigands des glaces ont kidnappé des badauds et les ont gelés dans des blocs de glace ! Si vous vous dépêchez, vous pourrez les sauver. Rendez-vous sur place le plus vite possible : le hibou vous montrera le chemin.]]
		},
		['ambush'] = {
			['step'] = {
				['4'] = [[J'ai été vaillant au combat et j'ai aidé les combattants blessés.]],
