			['name'] = [[Embuscade]],
			['text'] = [[Hé, vous, là-bas ! Nos gars préparent une embuscade. Allez les aider. Vous en tirerez peut-être quelque chose.']]
		},
		['witch_delivery'] = {
			['step'] = {
				['2'] = [[Je les potions. Maintenant, je dois leur donner au barman.]],
				['1'] = [[Je dois aller chez une sorcière pour lui apporter une caisse d'ingrédients puis récupérer chez elle un lot de potions avant de retourner au portail.]]
