			['name'] = [[Livraison chez une sorcière]],
			['text'] = [[Je dois aller chez une sorcière de la région pour lui apporter une caisse d'ingrédients en échange d'un nouveau lot de potions.]]
		},
		['snowman_giant'] = {
			['name'] = [[Le Roi des bonshommes de neige]],
			['text'] = [[Un bonhomme de neige géant est apparu à l'horizon. Il faut l'empêcher d'atteindre les communautés et d'y semer le chaos. Nous vous retrouverons sur place, je dois vous dire quelque chose.]]
		},
		['new_year_boss_2022_6'] = {
			['step'] = {
				['2'] = [[Dans la bataille finale, je tiens le rôle principal. C'est parti !]],
				['1'] = [[J'ai éliminé le méchant Glagla. Mais était-ce un miracle de Noël ? Le Père Noël était-il vraiment de retour ?]]
			}
		},
		['snowmen'] = {
			['text'] = [[Ces satanés bonshommes de neige causent des problèmes et agressent chaque personne qu'ils croisent. Qu'à cela ne tienne : cognez-les pour les projeter au sol ! Qu'ils gisent par terre, impuissants ! Ça leur donnera le temps de réfléchir à leur comportement. Ded Moroz et moi, on les remettra debout plus tard.]],
			['step'] = {
				['1'] = [[J'ai besoin d'accéder à cette zone pour faire passer un sale quart d'heure à quelques bonshommes de neige.]]
			}
		},
		['lost_soul'] = {
			['step'] = {
				['1'] = [[Quelque part dans la forêt, quelqu'un a besoin d'aide. Je devrais me dépêcher.]]
			},
			['name'] = [[Âme perdue]],
			['text'] = [[Au beau milieu de la forêt, un homme est dans le pétrin. Trouvez son camp et suivez ses empreintes, peut-être a-t-il besoin d'aide.]]
