	['npc'] = {
		['varya'] = {
			['choice'] = {
				['4'] = [[Hum... Vous ne pouvez vraiment pas marcher toute seule ?]],
				['5'] = [[(Porter Varya dans vos bras et sortir de la grange.)]],
				['2'] = [[Non. Et ils ne sont plus là, d'ailleurs, je les ai tués. Allez debout, il faut partir d'ici. Vous pouvez marcher toute seule ?]],
				['3'] = [[(Enchanté, Varya. Je vous raconterai tout en chemin. Vous pouvez marcher seule, alors ?)]],
				['8'] = [[Ensemble ?!]],
				['9'] = [[Euh... Et si on faisait comme ça : je vous ramène chez votre tante et je pars pendant quelque temps. Comme ça, ça vous laissera le temps de réfléchir à la façon de me remercier, hum ?]],
				['6'] = [[Pardon, vous disiez ?]],
				['7'] = [[Non, avant ça. Il y a un village de survivants près de Bryansk ? Vous pouvez m'y conduire ?]],
				['1'] = [[(La libérer)]],
				['10'] = [[(Mettre fin gentiment mais fermement à la conversation)]]
			},
			['episode'] = {
				['4'] = [[(Varya se lève et fait un pas avant de vaciller. Elle se rattrape au mur.)<new_line><new_line>Désolée, mes jambes ne me répondent pas bien. Qu'est-ce qu'on fait ? Vous... Vous pourriez me porter ? Pas longtemps, juste un peu, le temps que je retrouve le contrôle de mes jambes.]],
				['5'] = [[Ben vous voyez bien que non.]],
				['2'] = [[(La fille me lance un regard haineux au départ, mais son expression change rapidement.)<new_line><new_line>Hé, mais je ne vous connais pas ! Vous n'êtes pas l'un d'entre eux ?]],
				['3'] = [[Vous les avez tués ? (Elle en reste bouche bée.) Tous ces types ? À vous seul ? Naaan ?! Comment vous avez fait ? Au fait, moi c'est Varya.]],
				['8'] = [[Ouais... Mais vous savez ce que je me suis dit ? Je n'ai pas envie de retourner chez ma tante. On pourrait voyager ensemble !]],
				['9'] = [[(J'avoue que j'ai failli lâcher Varya, sur le coup.)<new_line><new_line>Bah oui. Vous êtes fort, courageux et vous êtes capable de me protéger. Après tout, vous avez risqué votre vie pour me sauver. Alors j'ai genre une dette envers vous ! Parce que je vais faire quoi, une fois chez ma tante, hein ? Je vais m'ennuyer comme un rat mort !]],
				['6'] = [[(Alors que l'on se dirige vers la forêt, Varya ma raconte toutes ses tribulations avec les bandits. Puis elle me parle de son horrible enlèvement, avant de m'énumérer tous les défauts de sa tante qui vit dans un village près de Bryansk. Je l'écoute à peine, trop perdu dans mes propres pensées.)]],
				['7'] = [[J'ai dit qu'à mon avis, elle est juste jalouse de moi.]],
				['1'] = [[(Il n'y a aucune source de lumière dans la grange autre que les rayons du soleil qui filtrent par les trous du toit. Les lattes du parquet grincent sous mes pas.<new_line>La prisonnière est recroquevillée dans le coin sombre.)]],
				['11'] = [[Pff, je m'ennuie et je n'ai rien à faire... Enfin, pas comme si j'avais envie de faire quelque chose. Hé, vos cartouches sont de quel calibre ? Aussi grosses que mon oncle ou plus petites ?]],
				['10'] = [[Retourner chez ma tante ? Eurk... Bah, d'accord. Hé, vous m'apprenez à tirer ?]]
			},
			['name'] = [[Varya]]
		},
		['nadezhda'] = {
			['choice'] = {
				['4'] = [[Oui, je le ramènerai.]],
				['5'] = [[Désolé de vous déranger, mais j'ai besoin de votre aide. Vous pourriez m'accorder quelques minutes ? Je ne vous ennuierai plus jamais, ensuite.]],
				['2'] = [[Je peux trouver le corps de votre fils et vous le ramener.]],
				['3'] = [[Toutes mes condoléances...]],
				['8'] = [[(Retourner à la porte et attendre)]],
				['9'] = [[(Attendre encore)]],
				['6'] = [[Je voulais vous poser une question à propos de votre mari.]],
				['7'] = [[Nadezhda, écoutez-moi, c'est important...]],
				['14'] = [[Je ne suis pas encore allé le chercher.]],
				['13'] = [[Rien... Excusez-moi.]],
				['12'] = [[Que lui est-il arrivé ?]],
				['1'] = [[Nadezhda ? N'ayez pas peur, je ne vais pas vous faire de mal.]],
				['11'] = [[Quelqu'un proche de vous a disparu. Mais qui ?]],
				['10'] = [[Je veux juste vous aider.]]
			},
			['episode'] = {
				['17'] = [[Oh, c'est vous. Excusez-moi, mais je suis occupée. Mais encore merci...]],
				['4'] = [[Partez d'ici. S'il vous plaît.]],
				['5'] = [[(Nadezhda a sans doute du mal à croire qu'un étranger veuille l'aider, alors que les autres habitants du camp s'y refusent. Mais elle se maîtrise parfaitement et murmure)<new_line><new_line>Très bien... Dans ce cas, revenez au plus vite. Et soyez prudent.]],
				['2'] = [[(La femme, vieille, mais seulement d'apparence, ne cille même pas. Ses yeux gris fatigués me dévisagent avec méfiance et elle ne dit rien)]],
				['3'] = [[(Nadezhda me regarde, suspicieuse)<new_line><new_line>Vous feriez ça ? Vraiment ? Me ramener Gena ?]],
				['8'] = [[(Les lèvres de la femme tremblent. Elle me lance un regard furieux)<new_line><new_line>Important ? Ce n'est pas le moment ! Sortez d'ici !]],
				['9'] = [[(Nadezhda reste immobile quelques minutes. Elle se lève ensuite avec difficulté et prend un pichet pour se servir un peu d'eau. C'est à ce moment que je remarque qu'il y a deux tasses sur la table.<new_line>Il y a un lionceau de dessin animé sur la deuxième tasse. Un magazine pour enfants dépasse de l'oreiller sur le deuxième matelas)]],
				['6'] = [[Qui êtes-vous ? Qu'est-ce que vous me voulez ?]],
				['7'] = [[(Nadezhda cille quand je parle de son mari. Puis elle fronce les sourcils et se détourne)<new_line><new_line>Partez, s'il vous plaît.]],
				['10'] = [[(Je me souviens des regards hostiles des habitants du camp. Est-ce qu'ils auraient quelque chose à voir avec la disparition du ou de la colocataire de Nadezhda ? Après avoir bu de l'eau et s'être un peu calmée, Nadezhda se tourne vers moi)<new_line><new_line>Vous êtes encore là ? Pourquoi vous me regardez ? Laissez-moi tranquille !]],
				['11'] = [[Si vous voulez m'aider, alors partez d'ici ! Maintenant !]],
				['14'] = [[Encore vous ? Qu'est-ce que vous me voulez ?]],
				['13'] = {
					['2'] = [[Je ne demandais pas grand-chose. Je voulais juste enterrer mon fils, le regarder une dernière fois, vous comprenez ? J'ai demandé à nos hommes de me ramener son corps mais ils ont refusé. À quoi bon risquer la mort pour un cadavre ? Ça ne valait pas le coup, selon eux. J'ai demandé partout, en vain.]],
					['1'] = [[(Nadezhda laisse échapper un sanglot et couvre le bas de son visage avec un mouchoir. Un nouveau silence tendu s'éternise. Nadezhda finit par relever les yeux sur moi.)<new_line><new_line>Il est parti avec un de ses amis, Vanya, à Novomoskovsk. Il y a deux jours, pour chercher de la nourriture. Mais Vanya est revenu seul. Il a dit que le bâtiment s'était effondré, que Gena n'avait pas survécu...]]
				},
				['12'] = [[(Le mari de cette femme est la seule piste que j'ai pour découvrir la vérité sur les événements qui se sont passés il y a bien longtemps. Je dois la convaincre de parler)<new_line><new_line>Gena. Mon fils.]],
				['1'] = [[(Il fait froid et sombre dans la petite maison. Une femme aux cheveux gris est assise sur un vieux matelas collé au mur. Nadezhda Kulik, certainement. Un autre matelas se trouve de l'autre côté de la pièce, mais il n'y a personne d'autre.<new_line>Je m'approche de Nadezhda.)]],
				['16'] = [[Oh, bon... Dépêchez-vous, d'accord ?]],
				['15'] = [[Oh, c'est vous. Où est Gena ?]]
			},
			['name'] = [[Nadezhda]]
		},
		['zaycev'] = {
			['choice'] = {
				['22'] = [[Non, j'ai entendu tout ce que je voulais savoir. Merci.]],
				['21'] = [[Oui, encore une chose. Comment la guerre nucléaire a-t-elle commencé ?]],
				['20'] = [[Merci de m'avoir tout dit.]],
				['19'] = [[Oui, merci de m'avoir tout dit. Je dois partir, à présent...]],
				['18'] = [[Mais le virus a quand même été utilisé.]],
				['17'] = [[Alors qu'est-ce que vous avez fait ?]],
				['4'] = [[J'attends.]],
				['5'] = [[Et qu'ils tueraient les survivants eux-mêmes.]],
				['2'] = [[Day R.]],
				['3'] = [[Non, encore une chose. Comment la guerre nucléaire a-t-elle commencé ?]],
				['8'] = [[L'URSS a riposté ?]],
				['9'] = [[Et il n'y avait rien à faire ? Personne ne pouvait y remédier ?]],
				['6'] = [[Pour fuir le pays mourant ?]],
				['7'] = [[Mais ils étaient trop nombreux...]],
				['10'] = [[Ça veut dire qu'il ne reste plus rien des autres continents ? Que le monde entier est une gigantesque friche ?]],
				['11'] = [[Les loups ont tué Gavrilov. Je vous écoute.]],
				['14'] = [[Sur quoi travaillaient-ils ?]],
				['12'] = [[Parlez-moi de Nesterov et des autres scientifiques qui ont travaillé avec lui. Et tout le reste, aussi.]],
				['13'] = [[Allons, ne soyez pas timide.]],
				['1'] = [[On avait un accord. J'ai tenu ma promesse, la louve est morte. À votre tour, maintenant.]],
				['15'] = [[On parie ?]],
				['16'] = [[Que s'est-il passé ?]]
			},
			['episode'] = {
				['20'] = [[Hmm ? Autre chose, jeune homme ?]],
				['15'] = [[Je pourrais vous l'expliquer, mais vous ne comprendrez rien.]],
				['19'] = [[Hélas, oui. Apparemment, Nesterov avait eu le temps de confier les cultures contaminées à la CIA. Le 17 août 1984, des agents ennemis ont dispersé le R17 dans l'air dans différentes villes grâce à des aérosols spéciaux.]],
				['18'] = {
					['1'] = [[Je... Je n'avais pas le choix. Nous les avons dénoncés aux autorités, tous les trois. Beletsky et Shashkov ont signé la déclaration aussi.<new_line>L'affaire a rapidement été prise en charge. Nesterov, Kulik et Karbyshev ont été arrêtés.]],
					['2'] = [[Nous avons ensuite eu la visite d'hommes du KGB. Ils nous ont interrogés et ont fouillé tout l'institut. Ils ont pris tout ce qu'il pouvait dans le labo de Nesterov, même la plaque sur sa porte. Ils ont détruit tout le biomatériel.]],
					['3'] = [[L'audience s'est tenue à l'abri des regards, mais j'ai pu y assister en tant que témoin. Ça n'a pas duré longtemps. Les preuves étaient accablantes et Karbyshev a tout avoué. Article 64, trahison de notre mère patrie. Le peloton d'exécution les attendait dès leur sortie de la cour.]]
				},
				['4'] = [[(Le directeur me regarde, interloqué, avant d'acquiescer à nouveau et de reprendre son nettoyage de lunettes.)]],
				['5'] = [[Très bien, très bien... Mais c'est très simple, en vérité. Les agences de renseignements américaines ont clairement surestimé l'efficacité de leur petit R17 mortel. Ou peut-être l'ont-ils sous-estimé... Ils pensaient que grâce à lui, le peuple de la grande nation soviétique serait proche de l'extinction, désemparé et affaibli par la maladie...]],
				['2'] = [[(Zaitsev tourne le croc entre ses doigts et me regarde. Il semble un peu mal à l'aise.)<new_line><new_line>Où est Pavel ?]],
				['3'] = [[Oui, c'est comme ça que l'épidémie a commencé, éliminant la plus grande partie de la population de l'URSS.<new_line>Content ?]],
				['8'] = {
					['2'] = [[Une session secrète du Club nucléaire s'est tenue à la fin d'octobre, et une motion est passée pour purifier le 'territoire contaminé. Ils ont utilisé toute la puissance de feu qu'ils avaient. Même les petits villages ont été 'désinfectés' aux bombes. Mais ils ont encore fait une erreur de calcul.]],
					['1'] = [[Tout à fait. Pour un mort, deux autres arrivaient. Et la frontière est si grande que les gens assez intelligents ou assez désespérés trouvaient toujours un moyen de la franchir. Et quand les premiers signes d'épidémie se sont manifestés en Chine, en Mongolie, en Turquie, en Pologne et en Finlande, des mesures extrêmes ont été prises.]]
				},
				['9'] = [[Oui... Nous avons utilisé tout notre arsenal, sans attendre, sur toutes les cibles. Même si nous étions condamnés, le pays refusait de mourir. La vague de réfugiés contaminés a profité du chaos pour prendre l'ascendant sur les armées alliées. Et c'est ainsi que le R17 s'est répandu dans le reste du monde.]],
				['6'] = [[Exactement. Et après avoir répandu un peu de sang, ils auraient eu accès aux terres prospères et infinies de notre mère patrie. Mais le R17 les a trahis. L'épidémie s'est propagée trop lentement, la période d'incubation était trop longue et trop de personnes y étaient insensibles. Et dès octobre, des dizaines, voire des centaines de milliers de personnes affluaient aux frontières de l'URSS...]],
				['7'] = [[Bien sûr. Presque tous étaient contaminés. Nos voisins ont fermé leurs frontières et instauré la loi martiale. Ils ont rassemblé leurs troupes et ils ont abattu des réfugiés par milliers...]],
				['10'] = [[(Zaitsev rit avec amertume.)<new_line>Non. Mais à ce moment-là, chaque nation défendait ses intérêts. Les États-Unis se sont mis à bombarder l'Europe, l'Inde a attaqué la Chine. De profondes rancunes ont donné naissance à des conflits sanglants. C'était comme si le monde entier avait sombré dans la folie. Le seul endroit sûr était l'Antarctique. Mais comme toujours, l'humanité a réussi à empirer encore les choses...]],
				['11'] = [[Honnêtement, je n'en sais rien. Excusez-moi, jeune homme, mais nous avons beaucoup parlé. Il est temps que je prenne mes médicaments.]],
				['12'] = [[Très bien, comme vous voulez. Rappelez-moi ce que vous vouliez savoir.]],
				['13'] = [[(Le directeur acquiesce, retire ses lunettes pour les nettoyer soigneusement avec un chiffon. Il passe de longues minutes à les frotter.)]],
				['14'] = [[Que voulez-vous que je vous dise ? Georgy Nesterov était un scientifique de renommée nationale, la fierté de notre institut. Nous avons fait de grandes découvertes avec lui, Kulik et Shashkov pendant de nombreuses années. Karbyshev nous a fait venir de Léningrad. Beletsky, le plus jeune, est arrivé plus tard, en fin 83.]],
				['1'] = [[(L'ancien directeur de l'Institut de virologie somnole dans le même fauteuil que la dernière fois. Je le réveille et m'assois sur la chaise près de lui. Zaitsev me regarde, encore ensommeillé. Je lui tends le croc de la louve.)<new_line><new_line>Qu'est-ce que c'est ?]],
				['16'] = {
					['2'] = [[Ils ont caché leurs travaux aux autres. Je les soupçonnais de ne pas passer leurs soirées au labo que pour prendre le thé, mais j'ai préféré ne pas m'en mêler. Mais il a bien fallu que j'intervienne à un moment.]],
					['1'] = [[Sur de la génétique moléculaire. Ils étudiaient les protéines et les gènes des virus les plus répandus. Un projet très inoffensif tant que vous respectez certaines limites.<new_line>Mais Nesterov a décidé de les franchir. Il a commencé à jouer avec les mutations. Et il a convaincu Kulik et Karbyshev de l'aider.]]
				},
				['17'] = {
					['2'] = [[Nesterov et ses copains développaient une nouvelle épidémie. Non, pas une épidémie... Ce virus, R17, était bien plus dangereux que la peste noire. Et en plus, il était fait sur mesure pour un commanditaire : la CIA ! Nesterov avait créé une nouvelle arme bactériologique pour nos ennemis !]],
					['1'] = [[Beletsky a trouvé la cachette de Nesterov. Il m'a apporté les notes de Nesterov et son dictaphone. Au départ, je ne voulais pas y croire, mais c'était bien son écriture et sa voix !]]
				}
			},
			['name'] = [[Zaitsev]]
		},
		['belaz'] = {
			['choice'] = {
				['4'] = [[Pas encore.]],
				['5'] = [[À bientôt!]],
				['2'] = [[Mais tu es vraiment fou! C'est impossible, ton drole d'idée. Je ne veux pas faire ça.]],
				['3'] = [[Oui, tout ce qu'il y avait dans la liste!]],
				['1'] = [[Euh, c'est vraiment un plan ambitieux! Je ne sais pas même comment on peut commencer... Mais ça vaut le coup d'essayer. Qu'est-ce que dois-je faire?]]
			},
			['episode'] = {
				['4'] = [[Alors? T'as trouvé quelque chose?]],
				['5'] = [[Super! Mon rêve se réalisera enfin! Tu n'as aucune idée de comment je suis heureux. Je te dois beaucoup: bienvenue à bord! Quand notre ville en marche sera prêt à partir, tu pourras choisir la route. Nous accepterons ta décision, quelle qu'elle soit.]],
				['2'] = [[Alors, voilà mon liste. Il faudra apporter quelque chose de la centrale nucléaire de Tchernobyl. J'y travaillais avant et maintenant je veux utiliser quelques piéces spéciales. En plus, j'ai besoin de quelques légumes pour l'installation hydroponique.]],
				['3'] = [[Ah, tu parles comme tout le monde! Et personne ne s'intéresse pas au détails... Qu'est-ce qui se passe avec vous tous? Vous voudriez de l'argent facile, n'est-ce pas? Je dois continuer mon travail. Adieu.]],
				['1'] = [[Ah, tu es mon nouveau assistant, n'est-ce pas? Parfait! Alors, écoute: j'ai toujours rêvé de construire un véhicule tout terrain qui n'aurait pas besoin de carburant. Ça serait... Comme une ville en marche personnel! Mais je n'ai pas réussi à trouver quelques piéces nécessaires. Et personne ne veut m'aider... J'avais presque perdu l'éspoire. Il faut trouver toutes ces piéces - regarde la liste. Si tu m'aideras, je te ferai conducteur. Tu pourras voyager où tu voudras avec moi et ma famille!]],
				['6'] = [[Ne perds pas ton temps donc! Continue!]],
				['7'] = [[La maison est vide. Vladimir et sa famille vivent maintenant dans le BelAZ.]]
			},
			['name'] = [[Vladimir]]
		},
		['vanya'] = {
			['choice'] = {
				['4'] = [[Dis-moi juste dans quel bâtiment tu l'as laissé.]],
				['5'] = [[Je veux ramener le corps de Gena, qu'il puisse avoir de vraies funérailles.]],
				['2'] = [[Comme quoi, tout arrive. Je voulais vous demander quelque chose.]],
				['3'] = [[Une fois de plus ou une fois de moins...]],
				['1'] = [[Je suis un vagabond.]],
				['6'] = [[Je n'ai pas vraiment envie de boire avec vous.]],
				['7'] = [[Merci, une autre fois, peut-être.]]
			},
			['episode'] = {
				['4'] = [[(Vanya rit et jette sa cigarette dans les buissons. Puis il me lance un regard moqueur. Sale gosse.)<new_line><new_line>Bon, alors qu'est-ce que vous voulez savoir, ô seigneur ?]],
				['5'] = [[Dans un bâtiment de cinq étages, avec des garages, pas loin. Il est dans un appartement au deuxième étage. Qu'est-ce que ça peut vous faire ?]],
				['2'] = [[(Vanya siffle.)<new_line><new_line>Un vagabond ? Ha, je pensais pas qu'il restait encore des idiots qui parcouraient la friche tout seuls.]],
				['3'] = [[C'est à propos de Gena, pas vrai ? Je vous ai vu parler à sa mère. Mais je vous dirai rien. Ça fait au moins trois fois que je raconte cette histoire, j'en ai ras le bol.]],
				['8'] = [[Écoutez, hé, le vagabond, là... Gena ira nulle part, mais l'alcool, ça n'attend pas. Alors, vous avez changé d'avis ?]],
				['9'] = [[(Vanya est chez lui et apparemment, pas seul. La dernière chose dont j'ai envie, c'est d'entrer là-dedans.)]],
				['6'] = [[(Vanya ricane encore.)<new_line><new_line>Sérieux, vous allez aller jusqu'à Novomoskovsk pour un cadavre ? (Le jeune homme se tait et me lance un regard étrange.) Écoutez, j'ai une idée. Allons chez moi. J'ai ramené de l'alcool de Novomoskovsk. Il y a marqué 'Bryanskaya' sur l'étiquette, vous connaissez la marque ? Mon seul problème, c'est que personne veut en boire avec moi, ici. C'est rien que des fragiles.]],
				['7'] = [[Quoi ? Et vous osez vous dire Russe ?]],
				['1'] = [[(Vanya est assis sous le porche à fumer. J'ai compris dès ses premiers mots qu'il était un peu ivre.)<new_line><new_line>Hé ! Ça fait longtemps qu'on a pas vu de nouveau venu, par ici. D'où vous venez ? De quel trou paumé vous sortez ?]]
			},
			['name'] = [[Vanya]]
		},
		['gavrilov'] = {
			['choice'] = {
				['8'] = [[...]],
				['7'] = [[Attendez, ne partez pas. Vous êtes Pavel Gavrilov, l'ancien détective, pas vrai ?]],
				['6'] = [[Comment je peux le prouver ? Je n'en sais rien... Je peux vous aider à transporter le corps et vous défendre contre les loups.]],
				['5'] = [[(Mentir) Je viens de Tver.]],
				['4'] = [[Doucement, je suis un ami.]],
				['19'] = [[Non. J'ai vu de quoi ces loups sont capables. J'accepte de risquer ma vie, mais aller dans leur tanière et affronter toute une meute... c'est du suicide pur et simple.]],
				['18'] = [[Sinon, vous êtes fichus d'avance... Très bien, je viens.]],
				['17'] = [[Je vous aiderai. Il faut que quelqu'un le fasse. Ces loups noirs sont trop dangereux.]],
				['16'] = [[Je vois. Mais pourquoi vous n'avez pas simplement incendié l'abattoir pour piéger la louve et la tuer ? Ou au moins pour l'en faire sortir ?]],
				['12'] = [[J'ai déjà traversé beaucoup d'épreuves pour arriver ici. Ça ne sera pas la première fois.]],
				['13'] = [[Ma promesse ? De quoi vous parlez ?]],
				['14'] = [[Et qu'est-ce que vous attendez de moi ?]],
				['15'] = [[Non, je n'en sais rien.]],
				['9'] = [[Je veux tout savoir sur les scientifiques qui ont été exécutés et sur l'épidémie qui s'est déclarée en même temps dans tout le pays. Ah, et sur le Day R, le 17 août 1984.]],
				['11'] = [[Ça me regarde.]],
				['10'] = [[Quand bien même... Je veux connaître la vérité.]],
				['20'] = [[On se comprend.]],
				['21'] = [[Nan. J'ai toujours pas envie de me suicider bêtement.]],
				['1'] = [[Ce n'est pas important. Je dois vous parler de quelque chose, à propos des scientifiques de l'Institut de virologie.]],
				['2'] = [[Bien, on discutera là-bas.]],
				['3'] = [[(Je tousse pour attirer leur attention)]]
			},
			['episode'] = {
				['27'] = [[Comme vous voulez. J'imagine que c'est comme ça que les vagabonds solitaires survivent aussi longtemps.]],
				['12'] = [[Où est-ce que vous avez entendu ça ?]],
				['11'] = [[(Gavrilov me dévisage avec méfiance, puis secoue la tête)<new_line><new_line>C'est bon, on se débrouillera.]],
				['10'] = [[Quoi, vous débarquez de Moscou ? Et pourquoi on devrait vous croire ? Moi aussi, je sais raconter des salades.]],
				['9'] = [[Un ami ? Je ne vous connais pas, moi. De quel camp vous venez ?]],
				['8'] = [[(Les trois hommes se tournent immédiatement vers moi. Les trois Makarov sont désormais pointés droit sur moi)<new_line><new_line>Hé ! Vous êtes qui, vous ?]],
				['7'] = [[(L'homme se penche de façon menaçante sur le jeune homme)<new_line><new_line>Si quelqu'un n'avait pas emporté le dernier flingue et poursuivi les loups comme un abruti, Kirill serait encore en vie !]],
				['6'] = {
					['2'] = [[Vous êtes... que des lâches ! Kirill s'est sacrifié pour que tu échappes au loup, Gavrilov ! Quoi, la vérité te dérange ? Alors ce qu'on va faire, c'est aller là-bas, et les massacrer jusqu'au dernier !]],
					['1'] = [[(Le jeune homme semble toiser Gavrilov et l'autre homme, puis dégaine lentement son pistolet pour le pointer sur eux)]]
				},
				['5'] = [[Continuer ? On n'est plus que trois ! Et on avait déjà du mal à survivre à quatre ! Tu réussiras peut-être à venger ton beau-père plus tard. Tu auras d'autres occasions, mais pour l'instant, on doit rentrer !]],
				['3'] = [[Nikita, on rentre. Soit tu viens avec nous, soit tu continues à tuer les loups tout seul.]],
				['2'] = [[(Gavrilov étrécit les yeux)<new_line><new_line>Vous avez piqué ma curiosité. Bien, on en discutera. Quand vous reviendrez. Horenitchi, au centre communautaire.]],
				['19'] = [[(L'ancien détective reste pensif, en me regardant intensément)<new_line><new_line>Très bien. Vous connaîtrez toute la vérité. Mais d'abord, il faudra tenir votre promesse.]],
				['18'] = [[Très bien (Il met ses mains dans le dos et fait les cent pas dans la pièce). Et qu'êtes-vous prêt à faire pour connaître cette vérité ? Vous risqueriez votre vie, par exemple ?]],
				['17'] = [[La vérité ? Et qu'est-ce que vous ferez de la vérité, vagabond anonyme ? La raconter à tout le monde ?]],
				['16'] = [[(Le détective et le directeur échangent un regard. Zaitsev fronce les sourcils et ouvre la bouche pour parler, mais Gavrilov lui intime de se taire d'un geste. Il se rapproche de moi et m'observe des pieds à la tête)<new_line><new_line>Le Day R ? Pourquoi ça vous intéresse ? Ça va changer quoi de le savoir ? C'était il y a une éternité.]],
				['25'] = [[(Gavrilov esquisse un sourire, même s'il s'attendait à cette réponse)<new_line><new_line>C'est vous qui voyez, mais dans ce cas, je ne vous dirai rien du tout. C'est le marché. Si vous changez d'avis, n'hésitez pas à revenir. On a encore un peu de temps.]],
				['24'] = [[Parfait. Vu votre équipement, vous avez l'air d'être un combattant expérimenté. On aura plus de chances de réussir si vous vous battez avec nous. Quand vous serez prêt, faites le plein de munitions, de nourriture et de pansements, et rejoignez le point de départ. On vous attendra là-bas.]],
				['20'] = [[À Kiev, vous nous avez dit que vous nous aideriez à nous occuper des loups. Alors, allez-y. Nous vous dirons tout si vous le faites.]],
				['21'] = [[Pas grand-chose. Participez à la battue, c'est tout. Vous ne savez sans doute pas ce qu'il s'est passé ici, pas vrai ?]],
				['14'] = [[(En entendant son nom, j'ai senti l'agitation me gagner. Le directeur de l'Institut de virologie ! On dirait que je vais enfin avoir les réponses à mes questions)]],
				['15'] = [[C'est quoi ce silence ? Qu'est-ce que vous vouliez me dire ?]],
				['22'] = {
					['4'] = [[On n'a plus de temps à perdre. La louve se cache dans un vieil abattoir. C'est là qu'on va attaquer. C'est notre seule chance de les exterminer une bonne fois pour toutes.]],
					['1'] = [[Ces créatures, les loups noirs, ont fait leur apparition sur nos terres il y a environ trois ans. Une louve et deux louveteaux énormes, noirs comme la nuit et aveugles. Les deux premières se sont bien passées, mais ensuite, toute la forêt a été vidée de son gibier. Peut-être que les loups ont contracté une sorte de rage ou autre chose. Mais quoi qu'il en soit, ils se sont mis à chasser les gens.]],
					['2'] = [[On s'est protégés du mieux qu'on a pu, mais ils se sont multipliés et nous avons perdu de plus en plus de gens. La semaine dernière, ils sont entrés dans une maison et ont massacré toute une famille. On a envoyé une escouade pour abattre cette bête, mais personne n'est jamais revenu.]],
					['3'] = [[Mais on garde espoir. Tous les loups qu'on a tués étaient des mâles. Ça veut dire que si on tue la louve, il n'y aura plus jamais d'autres louveteaux.]]
				},
				['23'] = [[On a déjà essayé, mais on n'a jamais réussi.<new_line>Vous allez nous aider, alors ? Vous sauverez de nombreuses vies. Ensuite, on vous dira tout. Sinon, tant pis, on trouvera quelqu'un d'autre.]],
				['13'] = {
					['2'] = [[Le voilà. Faites comme chez vous. Mais d'abord, laissez-moi vous présenter mon ami. Il a hâte de discuter avec vous. Timofey Zaitsev, ancien directeur de l'Institut de virologie. Tout comme moi, il voulait partir pour la Pologne, mais il n'en a pas eu le temps.]],
					['1'] = [[(Pavel Gavrilov m'attendait dans le bureau du manager dans l'ancien centre communautaire. Il était accompagné par quelqu'un d'autre : un homme d'une soixantaine d'années, piquant du nez dans un fauteuil près du poêle. Lorsque je suis entré, Gavrilov a rempli un verre d'eau, a réveillé l'autre homme et lui a donné un cachet)]]
				},
				['1'] = [[Nikita, je comprends ce que tu ressens, mais on doit partir. On ramène Kirill et on l'enterrera une fois rentrés.<new_line>Tu m'écoutes, Nikita ? On y va. Maintenant. On rentre à Horenitchi. C'est trop dangereux ici. Et Seva arrive à peine à marcher, il lui faut un médecin !]],
				['26'] = [[Alors ? Vous avez réfléchi ? Mon offre tient toujours.]],
				['4'] = [[(Le jeune homme tourne brusquement son regard paniqué et injecté de sang vers l'homme)<new_line><new_line>Hein ? On s'en va ?! C'est trop dangereux ? Qu'est-ce que tu racontes, Gavrilov ?! Il faut qu'on continue ! Ces monstres respirent encore ! On a assez de cartouches pour un ou deux de plus.]]
			},
			['name'] = [[Gavrilov]]
		},
		['sasha'] = {
			['choice'] = {
				['19'] = [[Qui a la clé de la cellule ?!]],
				['29'] = [[(La laisser tranquille)]],
				['17'] = [[Mais pourquoi...]],
				['27'] = [[Boris ne m'a jamais dit qu'ils t'avaient abandonnée.]],
				['31'] = [[C'est dingue...]],
				['13'] = [[Tu n'as pas pu me tuer. La dernière chose dont je me souviens, c'est le silence. Tu as hésité. Tu aurais pu me tuer et fuir, mais tu as traîné. Et Boris est arrivé et t'a blessée.]],
				['23'] = [[C'est bien ce que je pensais. Bon, si tu ne veux pas parler de ça, alors dis-moi ce qui t'est arrivé.]],
				['21'] = [[On peut parler ?]],
				['43'] = [[De quoi tu parles ?]],
				['37'] = [[Il t'a parlé de l'escarmouche ?]],
				['39'] = [[Tu ne voyais pas qu'il cherchait à te rallier à sa cause ?]],
				['5'] = [[Sasha, je... Tout est de ma faute. C'est à cause de moi si toi, ta mère et...]],
				['9'] = [[Sasha...]],
				['41'] = [[Il est odieux ! C'est un bandit ! Ses mains sont couvertes de sang d'innocents !]],
				['40'] = [[Tu t'es mise à lui faire confiance ?]],
				['25'] = [[Et après ?]],
				['36'] = [[Tu vas me tuer ?]],
				['35'] = [[Ce n'est pas un peu ambitieux ? Vous n'êtes plus que trois ou quatre.]],
				['33'] = [[Tu te trompes. Je comprends ce que tu as vécu. Tu voulais du respect et de la reconnaissance, tu voulais être libre. Et tes nouveaux amis te l'ont permis.]],
				['34'] = [[Qu'est-ce que je ne comprendrai pas ?]],
				['16'] = [[Tes bras. Ils t'ont frappée ?]],
				['15'] = [[Qui a fait ça ?]],
				['20'] = [[Tu sors d'ici. Ce soir. Tiens-toi prête.]],
				['12'] = [[Tu n'aurais pas dû te contenter d'espérer, tu aurais dû tirer quand tu en avais l'occasion. Mais tu n'as pas pu.]],
				['14'] = [[Tu n'as pas réussi à tirer.]],
				['11'] = [[Je peux t'aider à t'échapper.]],
				['10'] = [[Sasha ?]],
				['7'] = [[Ils... Ils m'ont dit qu'on t'avait capturée... Je voulais... Je voulais juste... te sauver...]],
				['28'] = [[Chut !]],
				['18'] = [[Qui a la clé de la cellule ?]],
				['26'] = [[Tu as repoussé l'attaque ?]],
				['3'] = [[Sasha ?]],
				['8'] = [[Sasha... Attends...]],
				['30'] = [[Tu ne m'as toujours pas tout dit.]],
				['32'] = [[Et tu n'as pas regretté ?]],
				['22'] = [[De quoi ? Peut-être du fait que tu voulais me tuer, et que je t'ai sauvée du peloton d'exécution ?]],
				['6'] = [[Sasha, je suis désolé. Pardonne-moi...]],
				['42'] = [[Et tu veux être un loup ? Un bandit ?]],
				['2'] = [[(Tirer sur la fille)]],
				['44'] = [[Et Ruslan les a relâchés ?]],
				['4'] = [[Sasha... Je te cherche depuis si longtemps...]],
				['1'] = [[N'aie pas peur de moi. Je suis venu pour te sauver!]],
				['24'] = [[Arrête de faire la maligne. Dis-moi ce qui s'est passé après avoir tué les bandits chez ton grand-père.]],
				['38'] = [[Il a cherché à t'intimider ?]]
			},
			['episode'] = {
				['34'] = [[(Sasha se sent beaucoup mieux. Elle est capable de se lever et même de marcher un peu en s'appuyant sur moi)]],
				['19'] = [[Quoi 'ça' ?]],
				['29'] = {
					['2'] = [[Mais le travail dans les cuisines était immonde. J'ai refusé de le faire. Les filles se sont mises à m'en vouloir et ont dit au chef que j'étais paresseuse. Mais cet enfoiré a juste répondu : 'C'est pas grave, elle changera d'attitude après quelques jours sans manger...']],
					['1'] = [[C'est marrant ça, je ne me rappelle pas que ma vie t'intéressait, avant. (Sasha secoue la tête) Bon, d'accord. Les gens de Novokouznetsk m'ont accueillie dans leur camp. Mais ils ne m'ont pas laissé me battre, ils m'ont même confisqué le flingue de Papy. Je suis allée voir le major, mais il a dit qu'une fille n'avait rien à faire parmi ses soldats. Il m'a collée aux corvées ménagères.]]
				},
				['17'] = [[Abruti, je... Je profitais du moment !]],
				['27'] = [[(Sasha se détourne)<new_line><new_line>C'est pas le meilleur sujet de conversation.]],
				['31'] = {
					['4'] = [[J'en ai buté quatre avant de tomber à court de munitions. Ils m'ont attrapée et ils voulaient me tuer sur-le-champ, mais leur chef s'est interposé. Il leur a dit de me ramener à leur base.]],
					['1'] = [[Ouais, on les a chassés. Après ça, ils m'ont rendu mon flingue, et ils m'ont laissé participer aux expéditions de chasse. Ils m'ont même intégrée à une escouade de soldats. Le commandant, c'est Vasily. Ouais, le fils de Boris.]],
					['2'] = [[Je m'entendais bien avec les gars de l'escouade. Ils disaient qu'ils me respectaient, que j'étais l'une des leurs. Et moi, comme une débile, je me jetais sous les balles pour leur montrer ce que j'avais dans le ventre. (La mâchoire de Sasha se crispe) Bon sang, j'étais tellement stupide...]],
					['3'] = [[Bref, le conte de fées a vite tourné au cauchemar. On a affronté les Abakans. Ouais, ils nous ont tendu une embuscade, cela dit, on était plus nombreux et mieux armés qu'eux. Mais ce sale lâche de Vasily nous a ordonné de fuir. Alors qu'il avait bien vu que j'avais été séparée du reste de l'escouade.]]
				},
				['13'] = [[(La cave sombre est froide et humide, même les rats ne voudraient pas vivre ici. Grâce à la lumière des torches, je vois trois bancs en bois solides derrière d'épais barreaux. Sasha est assise sur l'un d'entre eux. Le garde me laisse approcher de la cellule pendant qu'il va fumer une cigarette dehors)]],
				['23'] = [[Boris. Qu'est-ce que tu mijotes ?]],
				['33'] = [[(Un coup de feu venant de Novokouznetsk résonne au loin. Puis un autre. J'attrape mon arme et je grimpe la colline la plus proche pour regarder ce qu'il se passe. La friche grise s'étend sous mes yeux, totalement dénuée de vie, comme toujours.<new_line>Quand je rejoins Sasha, elle dort déjà)]],
				['43'] = [[Non, il s'est contenté de fumer et de parler. Il est revenu le deuxième jour. Il a dit que les gars avaient refusé de payer ma rançon, alors qu'il n'avait demandé que quelques armes à feu et une caisse de conserves. Il a dit qu'aucun d'entre eux n'avait tenté de venir me récupérer, et d'autres trucs comme ça. Après ça, il venait lui-même m'apporter ma nourriture. J'avais le droit à des rations beaucoup plus importantes que là-bas. Et puis c'était meilleur.]],
				['4'] = [[(Je lui tire dessus, mais elle s'esquive avant de se jeter sur moi avec une agilité incroyable et de m'asséner un coup de couteau.<new_line>Le souffle coupé, je laisse tomber mon arme avant de m'effondrer. La fille me donne un coup de pied en plein visage.)<new_line><new_line>T'es aussi fourbe que les autres !<new_line><new_line>(Elle désigne les corps criblés de balles)]],
				['45'] = [[Non, pas à ce moment-là. Mais je voyais qu'il me comprenait. Il me comprenait bien mieux que la plupart de ces ordures de Novokouznetsk. Ceux qui m'ont... utilisée et m'ont jetée comme un vieux mouchoir usagé.]],
				['21'] = [[Il voulait savoir qui avait tué son cher petit Vasily. Je ne lui ai rien dit. <new_line><new_line>(Sasha ricane, amusée)<new_line><new_line>Quand ils me tiendront au bout de leurs canons, je lui dirai tout, dans les moindres détails... Content ? Alors tire-toi d'ici.]],
				['37'] = [[Quoi que je dise, tu ne comprendras pas.]],
				['47'] = [[Non, Papa. On n'est pas des bandits, pas du tout. On est bien plus intéressants que ça.]],
				['39'] = [[La différence entre vivre et survivre ! (Les yeux de Sasha s'animent d'une lueur féroce) Est-ce que le mot 'missions' t'inspire quelque chose ?<new_line>On est pas des bandits, Papa. On est des colons. On déchiquette les restes pourris du vieux monde pour en bâtir un nouveau, mieux, plus juste.<new_line>]],
				['49'] = {
					['2'] = [[(Elle marque une pause)<new_line><new_line>Même tuer les monstres qui ont assassiné Papy n'avait pas été aussi jouissif. Je les ai regardés se vider de leur sang, et je me suis sentie libre. Heureuse. Forte ! Je n'avais pas soif de sang, je voulais juste me venger parce qu'ils m'ont trahie. Ce n'était que justice.]],
					['1'] = [[Oui. Mais ils ne sont pas allés très loin. J'ai pris le couteau de Ruslan et je leur ai tranché la gorge.]]
				},
				['5'] = [[(La fille crache et essuie le filet de sang qui coule sur son visage. Puis elle prend le fusil d'un des cadavres avant de me mettre en joue.<new_line>C'est à ce moment-là que je vois enfin correctement son visage.<new_line>Non...<new_line>C'est impossible...)]],
				['9'] = [[(Sasha fait une moue écœurée)<new_line><new_line>Beurk. Bah, vas-y, chouine, Papa. Ça aidera p'têt. Peut-être même que je te pardonnerai. Oh, j'ai failli oublier, tu as toujours mon couteau. Je le récupère.<new_line><new_line>(Sasha attrape la poignée violette et arrache la lame d'un coup sec. Le sang jaillit de la plaie)]],
				['41'] = {
					['1'] = [[Non, tu m'as sauvée, alors que j'essayais de te tuer. On t'épargnera. C'est juste. La justice avant tout.<new_line>Ruslan, aide-moi.]],
					['2'] = [[(Un des hommes aide Sasha à se relever. Un moteur rugit depuis les bosquets et un vieil UAZ apparaît sur le gravier de la berge)<new_line><new_line>On est quittes. Une vie contre une autre.<new_line>Maintenant, va-t'en. Pars le plus loin possible, qu'on ne se recroise jamais. Je ne veux plus jamais te revoir ni entendre parler de toi.]],
					['3'] = [[(Ruslan aide Sasha à monter dans la voiture, puis s'installe près du chauffeur. Le troisième bandit tente de fermer la porte, mais Sasha la maintient ouverte)<new_line><new_line>Je n'oublierai jamais ce que tu as fait et je ne te pardonnerai jamais pour nous avoir quittées, Maman et moi. Si tu étais resté, rien de tout cela ne serait arrivé. T'as pigé ? Rien !]]
				},
				['40'] = [[Non, nous sommes nombreux. Des milliers. Et bientôt, nous serons encore plus. (Sasha sourit en coin) Et puis trois ou quatre personnes peuvent déjà faire beaucoup. Derrière toi !<new_line><new_line>(Je me retourne. Deux bandits se tiennent à quelques mètres de moi, visant ma tête.)]],
				['24'] = [[(Sasha est assise au fond de la cellule de la prison de fortune, et me regarde avec dédain. Difficile de savoir si elle m'a cru ou pas)]],
				['36'] = [[Le lendemain, Ruslan est revenu pour poser un flingue devant moi. Il a dit qu'il ne me nourrirait plus gratuitement, qu'il n'avait pas besoin de prisonniers mais de soldats. Il m'a donné le choix : le rejoindre ou retourner à Novokouznetsk. Et tu sais ce que j'ai choisi.]],
				['35'] = {
					['2'] = [[Et puis leur chef, Ruslan, est enfin venu me voir. C'est plus tard que j'ai appris qu'il avait été prisonnier politique. Il est resté silencieux un long moment, et puis d'un coup, il m'a demandé ce que j'avais fait pour que mes camarades m'abandonnent.]],
					['1'] = [[Bon, bon, mais écoute bien. (Elle s'étire, fatiguée) Ils m'ont emmenée jusqu'à leur base et m'ont enfermée. J'ai cru qu'ils allaient vouloir profiter de moi, mais non. La première semaine, une fille venait tous les jours m'apporter de la nourriture. Je n'ai vu personne d'autre.]]
				},
				['25'] = [[(Sasha ne gémit plus. Elle est tombée dans une étrange léthargie, et ses yeux roulent dans leurs orbites de façon aléatoire)]],
				['16'] = [[Hein ?! Qu'est-ce que tu racontes ?]],
				['20'] = [[Pourquoi ils se seraient retenus ? Tu penses qu'ils m'auraient bichonnée, Judas ? (l'amertume dans sa voix est palpable) Boris n'a pas été tendre.]],
				['14'] = [[(En entendant ma voix, Sasha lève la tête et grimace, comme si elle avait une rage de dents)<new_line><new_line>Toi ?! Mince, t'es pas mort ? J'espérais, pourtant.]],
				['12'] = [[(La flaque de sang à mes pieds ressemble à un océan. Recroquevillé et impuissant, je ne peux plus rien faire d'autre qu'attendre. Les secondes étaient aussi longues que des heures...)]],
				['15'] = {
					['2'] = [[(Le rire hystérique de Sasha se transforme en sanglots étouffés. J'ai déjà entendu ces bruits, il y a longtemps, quand j'ai dit à ma fille que je partais pour de bon. Sasha se reprend, arrête de pleurer avec un rugissement frustré, et s'éloigne des barreaux.)]],
					['1'] = [[(Sasha éclate de rire, des larmes coulant sur ses joues)<new_line><new_line>Et c'est moi que Boris traitait de traître ?! Moi ?! Tu es cent fois pire que moi ! Trahir, c'est une seconde nature, chez toi ! Boris t'a sauvé la vie, et maintenant, tu veux aider sa prisonnière à s'échapper ?!]]
				},
				['11'] = [[(Ma vue commence à se troubler et mes oreilles bourdonnent à cause de la perte de sang de plus en plus importante. Sasha lève une nouvelle fois le fusil)<new_line><new_line>Dommage que la maison de Papy ait brûlé. Tu aurais trouvé des tas de dessins de toi, mon petit papa. Tous gribouillés par ta petite fille. Et dans... chaque... image... tu meurs...]],
				['10'] = [[(Elle se tait et se penche à nouveau vers moi)<new_line><new_line>Oh, Papa, si tu savais à quel point ça va me faire plaisir de te mettre une balle dans la tête. Puis une autre. Et encore une autre. Encore et encore jusqu'à ce que j'aie épuisé toutes les munitions de ce putain de bâtiment.]],
				['7'] = [[(Sasha se rapproche jusqu'à être pratiquement nez à nez avec moi. Elle sourit avec douceur)<new_line><new_line>Oooh, tu me cherchais ? Désolée, Papounet, mais tu arrives un peu tard.]],
				['28'] = [[Ce qui m'est arrivé ? Bah déjà, Maman a accouché de moi.]],
				['18'] = [[(Sasha renifle et me tourne le dos. La torche éclaire brièvement ses bras couverts d'ecchymoses. Des bleus très, très récents)<new_line><new_line>Si ça te fait plaisir... Je m'en carre. Demain, ils vont me fusiller.]],
				['26'] = [[(Sasha en a assez de me regarder et soupire profondément)<new_line><new_line>De quoi ?]],
				['3'] = {
					['2'] = [[Tu es stupide. C'est pas parce que je suis une fille que je ne sais pas me défendre.]],
					['1'] = [[(La colère quitte son visage d'un coup. La fille est secouée par une quinte de toux avant de hocher la tête en baissant son arme. Je m'approche en tendant la main.<new_line>Il ne lui faut qu'une seconde pour me planter le couteau dans le ventre.<new_line>Le souffle coupé, je laisse tomber mon arme avant de m'effondrer. La fille me donne un coup de pied en plein visage.)]]
				},
				['8'] = [[Allons, allons, Papa. Pourquoi tu t'excuses ? Bon, Maman est morte à cause de toi, c'est vrai. Bah. Et puis oncle Arthur et tante Marina, aussi. Et, ah oui, Papy, mais il était vieux. Aujourd'hui tu débarques et tu tues mes amis. Mais et alors ? Je peux toujours m'en faire d'autres, pas vrai ? Pas vrai ?!]],
				['30'] = [[Après... Ils nous ont attaqués, en pleine nuit. Une dizaine de gens avaient déjà été blessés quand l'alarme a réveillé tout le monde. Plusieurs bandits avaient franchi le portail, les filles étaient paniquées, mais j'ai assommé un de ces fumiers, je lui ai piqué son flingue, et j'ai commencé à tirer sur le reste.]],
				['32'] = [[Pff, en même temps, il n'aurait jamais rien dit qui puisse faire du tort à son cher fi-fils. Tu sais il...]],
				['22'] = [[(Sasha ne réagit pas)]],
				['6'] = [[(Sasha me dévisage d'un air perplexe et son regard devient plus intense. Enfin, elle semble me reconnaître. Mais son regard s'emplit d'une haine brûlante comme je n'en avais jamais vue. Je n'ai jamais vu personne regarder quelqu'un comme ça)<new_line><new_line>Tiens, tiens. Papa est revenu.]],
				['42'] = [[Ouais. Il a dit que j'avais eu du cran, que je valais dix de ses soldats. Qu'il n'aurait jamais fait ce que Vasily avait fait en abandonnant un soldat contre toute une escouade. Il a dit que le camp de Novokouznetsk pouvait les vaincre, mais qu'ils n'en avaient plus le courage. Il a dit beaucoup de choses de ce genre. J'avais envie de le contredire, mais...]],
				['2'] = [[T'approche pas ! Encore un pas, et je t'étripe !]],
				['44'] = [[Oh si. Mais tu sais, il n'avait pas tort. Je m'en rendais bien compte. Il est venu me voir tous les jours pendant une semaine. Il m'a parlé de lui et de sa famille, puis d'autres types du camp. Alors je lui ai parlé de moi. Il m'a vraiment écoutée, il me posait des questions et tout.]],
				['46'] = {
					['2'] = [[Regarde autour de toi. Le pays n'existe plus. Il n'y a plus de joli enclos sûr où tu peux brouter ton herbe tranquillement et faire pousser ta laine pour la tonte. On est dans les bois. Et ici, soit tu es un mouton... soit tu es un loup.]],
					['1'] = [[(Sasha rit avec mépris)<new_line><new_line>Des innocents ? Des innocents ?! Où tu les as vus, ces innocents ?! C'est comme ça que tu appelles ces moutons stupides, cupides et paresseux qui ne font que bêler quand on les massacre ?]]
				},
				['1'] = [[(J'ai couru jusqu'au dernier étage. Les corps de nos soldats étaient en charpie, et une fille blessée se trouvait parmi eux. Son visage était couvert d'un mélange de peinture de camouflage et de sang. Dès qu'elle m'a vu, elle a dégainé un petit couteau avec une poignée violette)]],
				['48'] = {
					['2'] = [[Ruslan leur a fait une proposition : lui dire tout ce qu'ils savaient de Stepnoy. Sa population, son arsenal, les points faibles de ses fortifications. Et en échange, Ruslan les laissait partir.<new_line>Il avait à peine fini de leur expliquer le marché que ces cafards lui ont tout révélé en tremblant.]],
					['1'] = [[J'avais pas fini. Une semaine plus tard, quelques 'innocents', comme tu dis, ont été jetés dans ma cellule. Vasily et deux autres types de Novokouznetsk.]]
				},
				['38'] = [[Le respect, la reconnaissance... Mais ce n'était que le début. Ils m'ont donné tellement plus. Les types de Novokouznetsk n'auraient jamais pu me donner autant.]]
			},
			['name'] = [[Sacha]]
		},
		['boris'] = {
			['choice'] = {
				['26'] = [[J'essaierai.]],
				['25'] = [[Boris, est-ce que je peux la voir ? Juste une dernière fois ?]],
				['24'] = [[Vous allez exécuter Sasha ?]],
				['23'] = [[Où est-elle, maintenant ?]],
				['22'] = [[Vous... Vous l'avez tuée ?]],
				['21'] = [[Et après ?]],
				['20'] = [[Boris, comment avez-vous réussi à me sauver ? Là, tout en haut du bâtiment ?]],
				['19'] = [[Ravi d'avoir été utile.]],
				['18'] = [[À la victoire... (prendre une gorgée)]],
				['17'] = [[Je l'ai, major.]],
				['4'] = [[Des bandits l'ont enlevée ? Des types d'ici ?]],
				['5'] = [[C'est ma fille.]],
				['2'] = [[Prochaine destination, Oufa.]],
				['3'] = [[Et je peux le comprendre... C'est pour ça que vous n'envoyez personne là-bas. En plus, c'est très loin d'ici. Je vais chercher le système à un autre endroit. De préférence, où il n'y a pas d'abeilles géantes.]],
				['8'] = [[Vous voulez venger la mort de votre fils ?]],
				['9'] = [[Peut-être qu'on pourrait détruire la base d'Abakan sans armée ? Ça vous paraît faisable ?]],
				['6'] = [[Alors qu'est-ce que vous attendez ? Pourquoi ne les avez-vous pas encore attaqués pour vous venger ? Vous avez assez de soldats et d'armes pour ça.]],
				['7'] = [[Mh. Vous pensez que Sasha y est toujours ?]],
				['10'] = [[Je peux peut-être trouver un autre système ?]],
				['11'] = [[J'essaierai quand même.]],
				['14'] = [[Non.]],
				['12'] = [[Oui... J'ai besoin d'informations sur Oufa.]],
				['13'] = [[Parfait. C'est ce que je vais faire.]],
				['1'] = [[Bonjour ! Je viens de la friche. Je cherche une fille, Sasha. Elle a dû venir ici depuis Barnaoul, en 1986...]],
				['16'] = [[Non, je ne l'ai pas. À plus tard.]],
				['15'] = [[Je l'ai récupéré, assemblé et ramené. Allez, pas de temps à perdre, allons-y.]]
			},
			['episode'] = {
				['29'] = [[(Boris me regarde en silence sans rien dire. Il voulait sans doute compatir.<new_line>Je n'ai rien à lui dire)]],
				['19'] = [[(Cette fois, il n'y avait aucun des commandants à part Boris. Quand je suis entré, j'ai remarqué une bouteille de très bon vin et deux verres. Boris m'a proposé de boire avec lui. Il était de très bonne humeur. J'ai accepté)<new_line><new_line>À la victoire ! Que toutes les victoires ressemblent à celle-ci !]],
				['27'] = [[(Le major se gratte la tête, puis vient me poser une main sur l'épaule)<new_line><new_line>Bon, bon. Vous êtes son père, tout de même. Mais je n'arrive même pas à imaginer comment je réagirais si mon Vasily avait fait... ce que votre Sasha a fait en travaillant avec ces bandits. Je crois que je l'aurais tué moi-même... Enfin... Bref, soyez fort. C'est mon seul conseil.]],
				['26'] = [[(Boris s'assoit et pianote sur la table)<new_line><new_line>Écoutez. On est en guerre. Ce n'est pas le moment de se ramollir. Votre fille et les bandits bossaient ensemble. Elle a tué des gars à moi. Elle a même failli vous faire sauter la cervelle ! Et vous venez pleurnicher quand même ?]],
				['25'] = [[Dans ce qu'on appelle la prison. Ce n'est pas vraiment une prison, c'est juste une cave normale. Elle ne va pas y rester longtemps. On ne va pas s'encombrer d'une bouche à nourrir de plus.]],
				['24'] = [[Presque. Elle était aussi vive qu'un chat, la balle n'a atteint aucun organe vital. Mais elle n'en est pas sortie indemne. Les gars l'ont maîtrisée et se sont occupés de vous. Ensuite, ils vous ont ramené ici en urgence. J'ai bien cru que vous alliez y rester.]],
				['23'] = [[Devinez. J'ai vu que vous aviez des problèmes, et cette nana allait vous tuer. Mais j'ai tiré le premier. Vous avez une dette envers moi, vieux.]],
				['22'] = [[Bah j'ai fini par m'inquiéter de ne plus voir mon meilleur soldat. J'ai demandé aux autres gars où vous étiez, et on a passé le coin au peigne fin.]],
				['21'] = [[Nos pertes ont été plus importantes que prévu... Et ces trois-là se sont enfuis à bord d'un UAZ. Mais on a eu le reste de la bande et on a détruit leur base. Ce n'est pas rien.]],
				['20'] = [[(Boris me tapote l'épaule)<new_line><new_line>Allez, buvez. Vous devez rassembler vos forces. Et du vin de bonne qualité fait des merveilles.<new_line>Ouais, la bataille a été épique ! J'ai eu l'impression de rajeunir de vingt ans.<new_line>Vous avez été d'une grande aide ! Sans ce système de missile, on n'aurait pas pu y arriver !]],
				['18'] = [[(La major n'est pas là. Apparemment, il prépare l'attaque.)]],
				['16'] = [[(Boris est manifestement choqué de me revoir. Il ne pensait manifestement pas que je reviendrais)<new_line><new_line>Vous avez des tripes. Vous avez vraiment récupéré le système ?]],
				['4'] = [[Mais ça, c'est une autre histoire. Vous ne trouverez rien. Mais si par chance, je me trompe, il faudra quand même que vous l'assembliez. Il y a un ingénieur de l'armée qui vit dans le camp près de Tcheliabinsk : Spiridon Kugel. Il a travaillé à Oufa, une fois. Il devrait pouvoir vous aider... enfin, s'il ne s'est pas déjà noyé dans l'alcool. Mais dites-lui que son vieux copain de comptoir lui passe le bonjour.]],
				['5'] = [[(Boris a souri. Il voulait dire autre chose, mais s'est ravisé au dernier moment.)<new_line><new_line>Bonne chance, alors. Je connais pas d'endroit plus sûr qu'ici, dans les environs. Il faudra vous débrouiller.]],
				['2'] = [[(Boris, un ancien major et chef de la petite ville militaire de Stepnoy, m'a réservé un accueil des plus froids. Fumant une cigarette écrasée, il m'a ordonné de me dépêcher de lui dire ce que je venais faire ici.)]],
				['3'] = [[Oui, je me souviens d'elle. C'était une vraie rebelle... Mais vous arrivez trop tard. Il y a un an ou deux... (Boris secoua la tête) Les bandits l'ont capturée après un raid. On ne l'a plus jamais revue.]],
				['8'] = [[Croyez-moi, tous les gens d'ici n'ont qu'une envie : massacrer ces fumiers. Malheureusement, c'est impossible. Ils occupent une ancienne base de l'armée, et elle est dix fois mieux protégée que Stepnoy. J'aimais mon fils, mais je ne peux pas envoyer des personnes se suicider pour le venger.]],
				['9'] = [[Comment je pourrais le savoir ? Écoutez, je compatis vraiment, mais... ce serait plus simple d'accepter la vérité, pour continuer à vivre. Vous ne récupérerez pas votre fille. Déjà, parce qu'il vous faudrait toute une armée pour faire plier les Akabans. Et ensuite...<new_line><new_line>(En observant le major, j'ai compris qu'il voulait vraiment anéantir ces bandits. Alors j'ai décidé d'essayer autre chose)]],
				['6'] = [[Non, ils ne venaient pas d'ici, mais d'Abakan. Pourquoi vous la cherchez ? Elle est de votre famille ?]],
				['7'] = [[(Boris soupire)<new_line><new_line>Votre fille ? Je vois. Ils ont tué mon fils aussi. Les mêmes, venus d'Abakan, et ils nous ont balancé sa tête par-dessus le portail. Vasily était notre héros, il était né pour diriger... On était tous fiers de lui. Je rêve toujours de trancher la gorge de celui qui lui a fait ça.]],
				['15'] = [[Vous allez commencer par m'écouter. Vous ne trouverez rien. Mais si par chance, je me trompe, il faudra quand même que vous l'assembliez. Il y a un ingénieur de l'armée qui vit dans le camp près de Tcheliabinsk : Spiridon Kugel. Il a travaillé à Oufa, une fois. Il devrait pouvoir vous aider... enfin, s'il ne s'est pas déjà noyé dans l'alcool. Mais dites-lui que son vieux copain de comptoir lui passe le bonjour.]],
				['10'] = [[(Boris se tait un instant)<new_line><new_line>Bien sûr. Mais ces garçons... mes soldats... ce sont tous les fils de quelqu'un ! Et je ne vais pas les conduire là-bas comme des agneaux à l'abattoir. Je refuse.]],
				['14'] = [[Des questions ?]],
				['12'] = [[(Boris ricane)<new_line><new_line>Vous dites ça comme si j'avais juste besoin d'un flingue Makarov. Vous avez la moindre idée de ce que c'est ?]],
				['13'] = [[Attendez, j'ai une idée. Si vous n'avez rien à perdre, allez à Ufa, dans la base militaire. En 83, des systèmes Metis ont été envoyés directement depuis l'usine. Je doute qu'ils aient tous été mis en service, et vu qu'Oufa a été colonisé par les abeilles géantes, personne n'y est retourné.]],
				['1'] = [[(Au départ, ils ne voulaient pas me laisser entrer dans la ville militaire. Ils m'ont fouillé et ont confirmé que je n'étais pas un espion d'un des gangs locaux. Ce n'est qu'après tout ça qu'ils m'ont laissé voir leur chef. J'ai dû leur laisser mes armes pour le moment, à l'entrepôt. J'ai été escorté tout au long du chemin par deux hommes armés)]],
				['11'] = {
					['2'] = [[On avait un système de missiles antitank, Metis. Un idiot, je ne dirai pas qui, a eu l'idée stupide de le jeter dans le marais. On a quelques missiles, mais rien pour les lancer.]],
					['1'] = [[(Boris se gratte le menton)<new_line><new_line>Théorique, ça serait possible. Le portail sud est plus faible que le principal. On pourrait le faire exploser. Mais ils ne vont pas nous laisser y mettre de la dynamite sans rien faire. Il y a de vieilles tranchées pas loin, mais il faut traverser un grand terrain à découvert ensuite. Une mitrailleuse, et tous nos gars y passent.]]
				},
				['17'] = [[(Boris est sur le point de dire quelque chose, mais il se perd dans ses pensées. Puis il tape du poing sur la table)<new_line><new_line>Bon, c'est décidé. On va leur donner ce qu'ils méritent ! Je vais rassembler les gars, et on part demain à l'aube. On vengera Vasily et les autres !]]
			},
			['name'] = [[Boris]]
		},
		['ignat'] = {
			['choice'] = {
				['20'] = [[À plus, Ignat.]],
				['17'] = [[Hein ?]],
				['19'] = [[Très bien, en voilà une...]],
				['18'] = [[Désolé, mais il ne vaut mieux pas plaisanter avec l'asthme.]],
				['4'] = [[Avec une telle toux, pas sûr qu'une cigarette soit une bonne idée. Une bouffée vous tuera.]],
				['5'] = [[Qu'est-ce qu'il vous faut ?]],
				['2'] = [[Vous allez mourir ?]],
				['3'] = [[D'aucun. Je suis un vagabond.]],
				['8'] = [[J'ai une idée. Je vais vous aider. Je vous construirai une forge. Vous savez, comme dans les villages. Avec une enclume, une fournaise, un soufflet... Vous pourrez continuer à travailler le métal. Et en échange, vous m'apprendrez ce que vous savez.]],
				['9'] = [[Faites-le. Qu'est-ce que vous risquez ? Si ça ne marche pas, vous pouvez toujours retourner sur votre tabouret et vous cramponner aux jupes de votre femme, en mendiant des cigarettes aux bandits.]],
				['6'] = [[Et vous en avez marre ?]],
				['7'] = [[Vous étiez fondeur ?]],
				['10'] = [[Et comme ça, vous pourrez remercier votre femme et les autres d'avoir pris soin de vous. Mais doucement, ou vous allez vous blesser.]],
				['11'] = [[Commençons par vous construire une forge. Des briques, de la ferraille, du ciment, on a tout ce qu'il faut.]],
				['14'] = [[Bientôt, bientôt, je n'ai pas encore tout trouvé.]],
				['12'] = [[Il y a assez de fenêtres ?]],
				['13'] = [[(Aller au vieil entrepôt pour construire la forge)]],
				['1'] = [[Je me disais que vous aviez peut-être besoin d'aide.]],
				['15'] = [[Je les ai ramenés ! Regardez !]],
				['16'] = [[Arrêtez de dire ça, je ne suis pas un bandit.]]
			},
			['episode'] = {
				['21'] = [[Merci pour tout, mec ! Je revis !]],
				['16'] = [[Merci pour tout, mec. Tu m'as bien aidé. Je ne sais pas quoi te dire... Si seulement tous les bandits étaient comme toi !]],
				['18'] = [[Tu vas quand même me filer une cigarette, non ? Ça fait cinq piges que j'en ai pas vue. J'avalerai pas la fumée, promis. J'ai juste envie de la goûter. Ça risque rien.]],
				['20'] = [[Ah, là tu me causes ! Merci, mec !<new_line>Si t'as besoin de la forge, hésite pas. J'alimenterai le feu.]],
				['19'] = [[Gna gna gna, vous vous croyez intelligents, tous, hein ? Bah, merci quand même, mec. J'ai une sacrée dette envers toi pour cette forge.<new_line>Si t'en as besoin, hésite pas. J'alimenterai le feu.]],
				['4'] = [[Ah, un bandit, quoi. (Ignat tousse encore). Personne survit dans cette friche. Hé, bandit, t'aurais une clope ?]],
				['5'] = {
					['2'] = [[(Le visage d'Ignat devient gris. Respirant très difficilement, il baisse la tête et vacille. Il retourne s'asseoir sur son tabouret en fixant le sol. Une minute plus tard, il me regarde, presque implorant)<new_line><new_line>Hé... Viens par là.]],
					['1'] = [[(Ignat se lève soudain et s'approche de moi. Une fois tout près, il crache au sol, manquant mes chaussures de peu)<new_line><new_line>Et si c'est c'que je veux ?]]
				},
				['2'] = [[Non. J'ai tout un camp de gens qui m'aident. Ils m'apportent des médocs, des bols d'eau chaude pour mes poumons. Et quand mon heure viendra, ils m'pleureront et m'enterreront. J'ai assez de gens pour ça.]],
				['3'] = [[P'têt bien que oui, p'têt bien que non. Ça te regarde pas. Et puis d'où tu viens, hein ? De quel camp tu sors ?]],
				['8'] = [[Fondeur, mécano, j'ai même été soudeur. J'ai travaillé l'métal toute ma vie.]],
				['9'] = [[(Les yeux d'Ignat brillent un instant avant qu'il ne baisse de nouveau la tête)<new_line><new_line>Je sais pas, mec. Ça fait si longtemps... Et j'ai jamais travaillé dans une forge comme ça. Je regardais mon grand-père forger des trucs, mais je saurai pas faire.]],
				['6'] = [[Je vais bientôt mourir. Ici, ils s'agitent, ils m'nourrissent et ils m'soignent, et moi, j'suis juste un parasite, assis là sans rien faire. Ils m'ont fait une fleur, aujourd'hui : ils m'ont laissé laver mes vieilles fringues tout seul. Ils disent que je devrais pas m'épuiser alors que j'suis malade.]],
				['7'] = [[J'étais un sacré travailleur moi. J'étais l'premier à décrocher le titre de stakhanoviste dans notre usine métallurgique. C'est là que tout a basculé. L'épidémie a tué tous mes fils. Pis après, c'était les bombes. J'ai cru que c'était bon, j'avais assez morflé comme ça, mais non. Mes poumons fonctionnent plus. Ma femme est infirmière, et si j'la laissais faire, elle me donnerait la becquée. Mais je suis pas un vieillard ! J'ai même pas quarante piges !]],
				['10'] = [[Je vais être forgeron... Comme papy. Ok, mec, ça marche. On va construire une forge et j't'apprendrai ce dont j'me souviens.]],
				['12'] = [[Il y a un vieil entrepôt, pas loin, près de la rivière. C'est un bon bâtiment en briques, bien solide. Niveau ventilation, y'aura pas mieux.]],
				['15'] = [[J'ai pas cru un instant que tu tiendrais ta promesse.<new_line>Ma femme a pété un plomb quand j'lui ai parlé de la forge, d'ailleurs. 'Je te laisserai pas faire !', qu'elle a dit. Ben trop tard. Elle fera la lessive toute seule !]],
				['13'] = [[Il n'y a pas de murs.]],
				['14'] = [[Commence, et j'te filerai un coup de main.]],
				['1'] = {
					['2'] = [[Qu'est-ce que tu r'gardes ? Tire-toi.]],
					['1'] = [[(Un homme pâle assis sur un tabouret en bois a attiré mon attention. Il fait sa lessive dans une bassine en métal.<new_line>Il est soudain pris d'une toux violente, et couvre ses lèvres d'un vieux chiffon gris. Il remarque enfin que je le regarde, et il rougit)]]
				},
				['11'] = [[Tu vas ramener les matériaux, un jour ? J'vais pas supporter de faire la lessive très longtemps.]],
				['17'] = [[Ok, ok, si tu dis que t'es pas un bandit, j'te crois. Viens voir. J'suis juste en train de faire une bassine pour la lessive. Pour ma femme, qu'elle voie que ça va, je peux m'en occuper. Elle devra s'occuper de la lessive, maintenant.<new_line>C'est déjà ça !]]
			},
			['name'] = [[Ignat]]
		},
		['default'] = {
			['choice'] = {
				['1001'] = [[Examiner les alentours et parler aux habitants]],
				['1005'] = [[Partir]],
				['1004'] = [[Aller au dortoir]],
				['1006'] = [[Je reviens vite.]],
				['1002'] = [[Aller voir le chef de camp]],
				['1003'] = [[Aller voir le marchand]]
			},
			['episode'] = {
				['1001'] = [[Ce camp déborde d'activité. Les gens sont très occupés par leurs tâches, et me lancent parfois des regards peu intéressés.]],
				['1002'] = [[Si tu as quelque chose dont j'ai besoin, tu peut l'échanger contre quelque chose dont tu as besoin.]],
				['1004'] = [[Je peux vendre]],
				['1005'] = [[Je peux faire]],
				['1003'] = [[Si tu as des matériaux nécessaires, je peux créer des choses dont tu as besoin.]]
			},
			['name'] = {
				['2'] = [[Spécialiste]],
				['1'] = [[Marchand]]
			}
		},
		['spiridon'] = {
			['choice'] = {
				['28'] = [[Oui, tenez. Avec les intérêts, comme prévu.]],
				['27'] = [[Non, Spiridon, désolé. Je ne l'ai pas encore. Vous savez bien qu'on ne trouve pas ce genre de choses sous le sabot d'un cheval.]],
				['26'] = [[Bonjour.]],
				['9'] = [[Hé, ne le frappez pas, il dit la vérité ! Je suis là pour affaires. J'ai besoin d'aide.]],
				['24'] = [[Quatre ? Bon sang... Je les ramène le plus vite possible.]],
				['23'] = [[Et voilà, trois bouteilles, comme promis.]],
				['22'] = [[Écoutez, désolé. Je ne l'ai pas. Je vous les donnerai la prochaine fois.]],
				['4'] = [[Boris, le major de Stepnoy. On se disputait pour savoir si Kugel, l'ingénieur qui a reçu une distinction officielle du comité régional, serait capable de construire un système de missiles antitank. Et Boris disait que vous aviez sans doute perdu le coup de main depuis longtemps.]],
				['5'] = [[Oui, enfin... Il y a longtemps. Maintenant avec l'âge et l'alcool...]],
				['2'] = [[(manipuler)<new_line>Dommage. On dirait que Boris avait raison. Vous vous faites vieux, l'ingénieur.]],
				['3'] = [[Qu'il n'y avait pas de raisons d'éliminer les ingénieurs de talent dont le pays a cruellement besoin... Dommage. Il faudra que je me débrouille tout seul.]],
				['25'] = [[Ça va, d'accord, je ne lui dirai rien.]],
				['13'] = [[Je ne vois pas de quoi vous parlez. Mais j'ai vraiment besoin de votre aide. J'ai besoin de trouver et d'assembler quelque chose à Oufa.]],
				['6'] = [[Attendez, vous me confondez avec quelqu'un d'autre. Je suis venu voir votre mari.]],
				['21'] = [[Je lui dirai.]],
				['14'] = [[Et où je suis censé trouver un autre ingénieur militaire ?]],
				['19'] = [[Merci, vous m'avez bien aidé.]],
				['16'] = [[À Oufa ? Ok, si vous le dites.]],
				['17'] = [[Hé, doucement.]],
				['18'] = [[Allez, allez, on y va.]],
				['1'] = [[Et si je vous rapportais de la vodka ? De la vraie, sortie de la distillerie.]],
				['20'] = [[Merci, vous m'avez bien aidé.]],
				['15'] = [[Ça marche !]]
			},
			['episode'] = {
				['27'] = [[Encore vous ? Je vous ai déjà dit que je n'irai nulle part. C'est hors de question !]],
				['26'] = [[Bonne chance. Comment ça se passe ? Bien ? Eh ben à plus tard, alors. Lyuba n'est pas de bonne humeur, aujourd'hui. Elle risque de nous tomber dessus, vous comme moi.]],
				['23'] = [[(Après une courte, mais très désagréable, conversation avec Lyuba, je peux enfin de nouveau parler avec Spiridon)]],
				['25'] = [[Ouais, je sais... Bon, je vais prendre mon mal en patience.]],
				['24'] = [[(Spiridon m'accueille d'une voix exagérément forte, jetant des regards soupçonneux à la porte derrière laquelle se cache Lyuba)<new_line>Bonjour, bonjour !<new_line>(Puis il baisse la voix)<new_line>Alors, vous avez la vodka ? Quatre bouteilles, hein ?]],
				['17'] = [[Regardez. Magnifique, hein ? Bon, le trépied est un peu défectueux donc il faut mettre une brique dessous. Le coordinateur n'est pas bien au centre, mais c'est un défaut de fabrication, tous les modèles ont le même problème. Et ça ne se cale pas bien dans les sillons, il faut le tenir délicatement. Ah, et autre chose...]],
				['20'] = [[Pas besoin de me ramener, j'ai remarqué une moto dans le garage. Presque neuve et d'une série limitée. Je serai bien plus rapidement rentré là-dessus. J'ai bricolé deux-trois trucs dessus. Même les abeilles ne pourront pas me rattraper. À plus !]],
				['22'] = [[Merci ! Ça c'est de la qualité ! Ça va faire plaisir aux copains, demain ! Soyez juste sympa de ne rien dire en présence de ma femme, hein ? Elle a un bon crochet du droit, ma colombe. Qui l'eût cru, hein ? Alors qu'elle était douce comme un agneau, avant. Bah, c'était le bon temps...]],
				['21'] = [[(Kugel secoue la tête avec reproche)<new_line><new_line>Bon, alors la prochaine fois, camarade. Vous ne voulez pas que je redésassemble le système, pas vrai ? Parce que là, vous me devez quand même quatre bouteilles.]],
				['19'] = [[Je me fais pas payer en remerciements. Ramenez-moi de la vodka.]],
				['18'] = [[Au fait, dites à Boris que les vieux de la vieille sont toujours là ! Il peut venir me rendre visite. Lyuba nous fera une bonne tourte et on se rappellera du bon vieux temps autour de la table.]],
				['4'] = [[Ah... Désolé... Mais vous comprenez... Je ne peux pas laisser Lyuba toute seule ici... Mais appelez-moi si vous avez besoin de quelque chose.]],
				['5'] = [[Un système de missile ? Quoi ? Je peux réparer un tank les yeux fermés ! On m'a même invité à Severodvinsk pour construire un sous-marin nucléaire !]],
				['2'] = [[(Kugel s'humecte les lèvres avant de claquer de la langue)<new_line><new_line>De la distillerie ? C'est bon, ça. Notre vodka maison ne lui arrive pas à la cheville.<new_line>Bon, là vous m'intéressez. Trois bouteilles, ok ? En attendant, il faut que je trouve un endroit pour échapper à Lyuba.]],
				['3'] = [[Boris qui ? Qu'est-ce qu'il disait ?]],
				['8'] = [[(Un petit homme apparaît derrière la femme imposante. Il a l'air intelligent mais soucieux)<new_line><new_line>Lyuba, attends. Je ne le connais pas, il ne vient pas de notre camp.]],
				['9'] = [[Tiens donc ! Comme par hasard ! Et lui ne te connaît pas non plus, j'imagine ? Tu me prends encore pour une idiote ?!<new_line><new_line>(Lyuba Kugal gifle son mari)]],
				['6'] = [[N'importe quoi ! Ce n'est pas un truc qui s'oublie ! Et dans mon domaine, l'âge est un avantage. Regardez les jeunes d'aujourd'hui, ce sont tous des espèces de singes maladroits !<new_line>Faites vos valises, on va à Oufa !]],
				['7'] = [[Je sais pourquoi t'es là ! Pour vous saouler et aller gueuler dans les bars, voilà pourquoi ! Alors, tu te barres d'ici, ou il te faut de l'aide ? Je peux t'aider, moi ! Oh, oui, je vais t'aider...]],
				['10'] = [[D'aide ? Comme s'il pouvait aider qui que ce soit, ce pochtron ! Tout ce qu'il sait faire, c'est rester assis toute la journée ! Spiridon, retourne dans la cuisine !]],
				['11'] = [[Comment ça, 'tout ce que je sais faire' ? Le pays a toujours besoin de bons ingénieurs ! J'ai même un certificat de remerciement du comité du parti régional !]],
				['15'] = [[Ah ben désolé, hein. Mais j'ai Lyuba. Et même si elle n'est pas toujours la plus agréable, il n'y en a qu'une. Les abeilles, il y en a trop. Je n'irai pas à Oufa, hors de question !]],
				['13'] = [[(Lyuba claque violemment la porte et s'enferme dans la cuisine. Spiridon Kugel fronce les sourcils et se tourne vers moi)<new_line><new_line>Elle est sur les nerfs depuis ce matin, je ne sais pas pourquoi. Vous savez quoi ? Dites à nos gars que je ne viendrai pas, aujourd'hui. Vous comprenez bien pourquoi. Mais demain, promis !]],
				['14'] = [[Ah, je pensais juste... Vous me rappelez un vieux copain. Attendez, Oufa ? Mais ça grouille d'abeilles !<new_line>Non non non, je ne vais pas à Oufa, j'ai envie de vivre, moi ! Allez trouver un autre inconscient.]],
				['1'] = [[(Les Kugel vivent au premier étage d'un petit immeuble. Une femme très en chair ouvre la porte, l'air très remonté)<new_line><new_line>Ah, encore là, toi ? Allez, va-t'en d'ici, ivrogne ! Y'en a assez, maintenant ! Pour la dernière fois, il n'ira nulle part !]],
				['12'] = [[(La femme gifle encore son mari, assez fort pour lui arracher un cri)<new_line><new_line>Tiens, le voilà ton remerciement ! Et tu peux aller te faire foutre, bâtard ! Sale porc ! Tu as ruiné ma vie alors que je t'ai tout donné ! Ma mère avait raison, puisse-t-elle reposer en paix. J'aurais dû épouser Ptichkin... Avec un peu de chance, tu te serais saoulé jusqu'à en crever !]],
				['16'] = [[Allez, on se dépêche, ou ma femme va me tuer !]]
			},
			['name'] = [[Spiridon Kugel]]
		},
		['svetlana'] = {
			['choice'] = {
				['19'] = [[À l'Institut de virologie ? À Moscou ? Bien, c'est la seule piste que j'ai. Direction Moscou, alors.]],
				['29'] = [[Je ne vous suis pas.]],
				['17'] = [[À Novgorod, sur un cadavre. Mais ce n'est pas tout. J'ai trouvé cette bombe aérosol sur laquelle il est marqué 'Day R' aussi.<new_line>Vous savez ce que ça veut dire ?]],
				['27'] = [[D'accord, mais quoi ?]],
				['31'] = [[Svetlana, même si vous aviez raison et que cette épidémie est due à quelqu'un, que c'est un complot... vous ne pourriez pas connaître la vérité. C'était il y a tant d'années... C'est de la folie.]],
				['13'] = [[Merci, Svetlana. Merci pour votre proposition, mais je ne peux pas rester ici.]],
				['23'] = [[Ce n'est pas contre vous, mais... je ne suis pas sûr d'en avoir le temps.]],
				['21'] = [[Non, attendez un peu. Je n'ai encore rien décidé. Au final, ma priorité, c'est toujours de retrouver ma femme et ma fille.]],
				['43'] = [[(Tout lui dire)]],
				['45'] = [[Pourquoi' ? C'est vous qui vouliez connaître la vérité.]],
				['4'] = [[J'en suis navré. Et je sais que mes chances sont très minces, mais ce n'est pas impossible. Je vais les chercher, et s'il le faut, je traverserai l'Oural et les autres pays...]],
				['37'] = [[Rebonjour, Svetlana.]],
				['47'] = [[Au revoir. (partir et fermer doucement la porte derrière vous)]],
				['39'] = [[Hm hm. Sûrement... Euh... Pourquoi cette fille avait aussi peur de moi ?]],
				['5'] = [[Mais vous faites quelque chose ! Vous êtes médecin, vous soignez les gens. Vous n'avez pas baissé les bras.<new_line>Et... vous n'avez pas abandonné non plus quand les bandits vous ont capturée ! Au contraire, vous les avez empoisonnés, en mettant votre vie en danger !]],
				['9'] = [[Je vous cherchais. J'ai trouvé votre dictaphone à Léningrad. Je suis aussi passé par la base des bandits, à Novgorod...]],
				['41'] = [[Oh, c'est pour ça...]],
				['40'] = [[Son premier quoi ?]],
				['25'] = [[Désolé, Svetlana, mais vous n'avez pas vraiment le profil de quelqu'un qui se fiche des autres. Après tout, vous essayez de me sauver.]],
				['36'] = [[À plus tard.]],
				['35'] = [[Bonjour. Non, je n'ai encore rien trouvé. Et vous, comment ça va ?]],
				['34'] = [[Ça avance lentement mais sûrement... Je suis juste venu voir comment vous alliez.]],
				['33'] = [[Honnêtement, pas vraiment. J'ai réfléchi. Je pense que je vais quand même aller à l'Institut de virologie de Moscou. Je verrai si je peux y trouver des indices. Qui sait, peut-être que ça m'aidera dans mes recherches.]],
				['16'] = [[Le 17 août ? Attendez... J'ai déjà vu cette date... (montrer le télégramme de l'homme décédé à Novgorod à Svetlana)]],
				['15'] = [[(Rester silencieux)]],
				['20'] = [[Je vais essayer. Mais je ne vous promets rien. Ma priorité est de retrouver ma femme et ma fille.]],
				['12'] = [[Svetlana, je...]],
				['14'] = [[Non, il faut que je reprenne la route.]],
				['11'] = [[C'est ce que je vous dis, je n'en sais rien. Je me suis réveillé à Nikel, sans savoir qui j'étais, d'où je venais ou comment j'ai fini là-bas, parmi les ruines et les cadavres en décomposition... Je me suis mis en quête de réponses. Des gens qui pourraient m'expliquer la situation. Mais personne ne sait ce qui est arrivé, ni pourquoi.]],
				['10'] = [[Je cherchais des survivants. Des gens qui pouvaient me raconter ce qu'il s'était passé en 1984. Je ne me souviens de rien... pas même de mon propre nom.]],
				['7'] = [[Qui je suis ? Un vagabond. Je viens du nord.]],
				['28'] = [[Je ne comprends pas. Tout est dit, non ? Vous savez déjà qu'une épidémie s'est déclenchée, puis il y a eu la guerre nucléaire...]],
				['18'] = [[Intéressant. Qui était votre père ?]],
				['26'] = [[Oui ? Je vous écoute.]],
				['3'] = [[Svetlana, tout ce que vous dites est vrai mais... Dans ma situation, est-ce que vous pourriez rester là sans chercher votre famille ? Malgré le danger, vous n'auriez pas envie de la retrouver ?]],
				['8'] = [[Svetlana, de Léningrad, pas vrai ? Vous êtes celle qui a trouvé le remède à l'épidémie, qui a été capturée par des bandits, s'est échappée et a rejoint Tver à pied, n'est-ce pas ?]],
				['30'] = [[Svetlana, c'est insensé. Qui voudrait faire ça et qu'est-ce que ça pourrait leur apporter ?]],
				['32'] = [[Peut-être.]],
				['22'] = [[Très bien, je vais voir ce que je peux faire.]],
				['6'] = [[Non, sans doute. Bref, c'était un plaisir de faire votre connaissance, Svetlana.. J'espère vous revoir... (partir)]],
				['42'] = [[C'est vrai.]],
				['2'] = [[Vous avez raison, mais je dois essayer. Je n'ai pas le droit d'abandonner ma famille. Et après, ma foi, il y a d'autres camps. Je poursuivrai mes recherches jusqu'à ce que je comprenne ce qui s'est passé.]],
				['44'] = [[Svetlana ?]],
				['46'] = [[Svetlana, écoutez-moi... Je sais que votre mère vous a dit que votre père était mort d'une crise cardiaque. Mais elle essayait peut-être juste de ne pas...]],
				['1'] = [[J'avais une femme et une fille. Je ne sais pas où elles sont, mais je suis convaincu qu'elles sont encore en vie et qu'elles ont besoin de moi. Il faut que je les retrouve.]],
				['24'] = [[Svetlana, c'est une mauvaise idée. La friche est trop dangereuse pour quelqu'un qui n'y est pas préparé. Je ne peux pas vous protéger, et les gens d'ici n'auront plus de médecin. Il vaut mieux que vous restiez.]],
				['38'] = [[Votre père...]]
			},
			['episode'] = {
				['19'] = [[Non, je n'en sais rien. Mais je crois déjà avoir entendu mon père parler de ça. Quand il est passé à l'institut, il a parlé à quelqu'un au téléphone et il a évoqué ce 'Day R'. Je lui ai demandé ce que ça voulait dire, mais il a éludé ma question avec une blague. Ça m'a marqué malgré tout.]],
				['29'] = [[Vous avez lu mon journal, non ? Sur l'une des pages que j'ai perdues, j'ai écrit une idée étrange. Je ne sais toujours pas comment je n'y ai pas pensé plus tôt. Mais cette idée... m'empêche de dormir.]],
				['17'] = [[(Svetlana se calme et baisse encore d'un ton)<new_line><new_line>Quoi qu'il en soit, nous savons quelque chose. La première épidémie mortelle date du 2 ou 3 septembre. Si on prend la durée d'incubation en compte, je pense que ces gens ont été contaminés autour du 17, 19 août. Ce qui veut dire...]],
				['27'] = [[(Svetlana baisse la voix, comme si elle avait peur d'être entendue)<new_line><new_line>Il y a une chose que vous pouvez faire. Moi j'ai commencé il y a bien longtemps, mais je n'y suis jamais parvenue. J'ai abandonné en cours de route. (Svetlana a un sourire ironique) Mais vous, vous pourriez !]],
				['31'] = [[Je n'en sais rien. J'ai tenté de trouver des réponses à ces questions depuis que les bandits m'ont capturée. J'ai essayé de trouver des explications rationnelles. Ensuite, une fois sortie de Tver, j'ai posé ces mêmes questions aux autres réfugiés. J'ai parcouru les journaux, l'historique de l'épidémie...]],
				['13'] = [[(Svetlana se tait, puis se lève pour aller vers la fenêtre)<new_line><new_line>Pourquoi pas ?]],
				['23'] = [[Je vais aller me préparer pour partir. Vous pouvez attendre encore un peu ?]],
				['15'] = [[(Quand je me réveille, Svetlana se tient près de moi. Elle me regarde intensément, se mordillant nerveusement la lèvre.)<new_line><new_line>Alors ? Vous avez changé d'avis ? Vous allez rester ?]],
				['43'] = [[Ce n'est pas un événement que pour elle, mais pour nous tous ! Nous avons survécu à une épidémie et une guerre nucléaire. Nous avons passé des années à lutter contre la famine ! Mais notre pire ennemi, c'est bien le désespoir. Et vous savez quoi ? Aujourd'hui, l'ennemi a perdu une grande bataille ! C'est le premier bébé depuis un temps fou ! Ça veut dire qu'on a encore un avenir !]],
				['25'] = [[Vous êtes sûr ?]],
				['45'] = [[(Une fois que j'ai fini, je remarque que Svetlana fixe le mur derrière moi d'un air vide. J'attendais une réaction de sa part, mais pas celle-là. Je bouge la tête pour tenter d'attirer son attention, mais elle ne bronche pas. Un silence tendu s'installe.)]],
				['24'] = [[(Svetlana fronce les sourcils, puis se détourne pour rejoindre la fenêtre)<new_line><new_line>Alors revenez quand vous aurez le temps.]],
				['37'] = [[Oh, très bien. Si on oublie le désespoir et la solitude.]],
				['47'] = [[Oui, je veux la vérité. Mais vous ne faites que me raconter un horrible mensonge. Je ne sais pas qui l'a inventé, mais j'espère que ce n'est pas vous. De toute façon, je ne veux plus vous écouter.]],
				['39'] = [[Ne soyez pas si solennel. Après tout, nous sommes de vieux amis, maintenant, pas vrai ? En tout cas, c'est tout comme.]],
				['20'] = [[C'était un microbiologiste, un des meilleurs du pays. Il travaillait à Moscou, à l'Institut de virologie. Je ne l'ai jamais plus revu, après ce jour-là. Il est mort d'une crise cardiaque en juin 1984. Ma mère me l'a annoncé au téléphone. Je n'ai même pas pu lui dire au revoir ni même aller à ses funérailles. J'étais clouée au lit avec une angine.]],
				['4'] = [[(Svetlana me regarde avec tristesse)<new_line><new_line>Vous savez, quand je suis arrivée ici, moi aussi j'ai essayé de trouver des membres de ma famille. Je cherchais un moyen de contacter mes collègues pour en savoir plus sur cette horrible épidémie et sur les bombardements. Je suis passée de ville en ville jusqu'à manquer d'essence. Mais j'ai fait tout ça pour rien. Tous mes efforts ont été inutiles !]],
				['2'] = [[(Svetlana se détourne)<new_line><new_line>Regardez dehors. Ce n'est qu'une friche. D'abord l'épidémie, puis la guerre nucléaire... La Terre est morte. Vous ne survivrez pas. Les radiations, les prédateurs... Les fous qui n'ont plus rien à perdre et les bandits. Vous ne trouverez personne, dehors. Mais si vous partez, vous ne reviendrez pas. Nous n'essaierons pas de vous retenir ici, mais réfléchissez un instant !]],
				['41'] = [[(Svetlana se met de nouveau à rire. Elle est vraiment de très bonne humeur.)<new_line><new_line>Ne faites pas attention. Elle est juste mal lunée. C'est son premier.]],
				['40'] = {
					['4'] = [[Merci d'avoir découvert la vérité. Excusez-moi de vous avoir hurlé dessus. Ce n'est pas votre faute. Ce sont ma douleur et mon problème, comme vous le disiez, et ceux de personne d'autre.<new_line>Encore merci et... au revoir.]],
					['1'] = [[(Svetlana m'attrape soudainement par le col. Je fais un bond en arrière.)<new_line><new_line>Ça suffit ! Ne parlez pas de mon père ! C'était quelqu'un de formidable ! Il m'aimait, il aimait ma mère et il aimait son pays ! Il est mort d'une crise cardiaque, c'est clair ? C'est ce que ma mère m'a écrit ! Une crise cardiaque, d'accord ? Une crise cardiaque...]],
					['2'] = [[(Svetlana me lâche et commence à sangloter. La regarder pleurer me fait mal au cœur. Elle pleure et moi, comme un imbécile, je me contente de la regarder sans savoir quoi dire. Je commence même à me gifler mentalement pour ne pas avoir inventé un mensonge digne de ce nom au lieu de révéler l'atroce vérité à Svetlana.)]],
					['3'] = [[(Svetlana se calme au bout d'un long moment. Elle s'essuie le nez avec un mouchoir et s'assoit sur le canapé. Je m'approche pour m'asseoir près d'elle, mais elle lève une main, m'intimant de ne rien en faire. J'acquiesce et me dirige vers la porte. Beaucoup de choses sont différentes pour Svetlana, maintenant. Et je ne peux pas l'aider à revoir ses opinions.)<new_line><new_line>Attendez...]]
				},
				['36'] = [[(Svetlana m'accueille avec un sourire.)<new_line><new_line>Bonjour ! Alors ? Des nouvelles ?]],
				['33'] = [[(Quand elle me voit, Svetlana sourit une seconde avant de se reprendre et d'arborer un masque d'indifférence.)<new_line><new_line>Alors, comment se déroule les recherches ? Vous avez des pistes pour retrouver votre femme et votre fille ?]],
				['35'] = [[Tout va bien.<new_line><new_line>(Le ton de Svetlana est glacial et détaché. Elle est clairement agacée.)]],
				['34'] = {
					['2'] = [[Je vais aller me préparer pour partir. Vous pouvez attendre encore un peu ?]],
					['1'] = [[(Le sourire de Svetlana est rempli de joie.)]]
				},
				['16'] = [[(Svetlana acquiesce lentement. Ma réponse ne l'a pas surprise. Mais pourquoi est-elle si nerveuse ?)<new_line><new_line>Je suis désolée pour vous. Les gens comme vous sont les premiers à mourir. Les gens qui aiment et qui prennent des risques pour les autres. Je pensais que tous les gens comme vous étaient morts en 84, et qu'ils ne restaient que les bandits et les gens comme moi, qui ont arrêté de s'occuper des autres pour de bon.]],
				['21'] = [[(Svetlana semble reprendre espoir)<new_line><new_line>Alors vous allez m'aider ?]],
				['12'] = [[Mais ici, chacun travaille pour le bien de la communauté. Nous ne sommes pas nombreux : chacun doit mettre la main à la pâte, et il y a du travail pour tous. Quelqu'un de plus ne sera pas de trop.]],
				['14'] = [[(La porte de la chambre de Svetlana est fermée, mais j'ai l'impression qu'elle se trouve dans la pièce. Je pourrais toquer, mais je pense qu'elle a envie d'être seule pour l'instant)]],
				['11'] = [[(Svetlana me regarde avec compassion)<new_line><new_line>C'est horrible ! Mais c'est un miracle que vous soyez arrivé jusqu'ici ! (Elle sourit gentiment) Mais tout ça, c'est du passé. Nous allons prendre soin de vous. Nous vous donnerons une chambre et de la nourriture. Petit à petit, vos souvenirs reviendront, j'en suis certaine.]],
				['10'] = [[Vous ne vous souvenez de rien ? Pas même de ce qu'il s'est passé ?]],
				['7'] = [[Ah vraiment ? (la femme paraît étonnée) C'est un voyage long et périlleux. Eh bien, entrez, asseyez-vous. Je m'appelle Svetlana.]],
				['6'] = [[(Svetlana enfouit sa tête entre ses mains)<new_line><new_line>Vous ne comprenez pas...]],
				['28'] = [[Il faut découvrir la vérité. Ce qui se cache derrière cette catastrophe.]],
				['18'] = [[Activez Day R le 17 août comme vous en avez reçu l'ordre' Qu'est-ce que c'est que ça ? Où vous l'avez trouvé ?]],
				['26'] = [[(Svetlana sourit bizarrement)<new_line><new_line>Et alors ? Je ne risque rien. Bah, tant pis pour vous. Si vous voulez partir, partez. Mais si vous y allez quand même...<new_line><new_line>(Elle se tait pour réfléchir, cherchant ses mots)]],
				['3'] = [[Bien sûr, il y a d'autres camps, mais pourquoi partir de Tver ? On a de quoi manger. Les bandits nous laissent tranquilles. Ne faites pas l'imbécile, restez !]],
				['8'] = [[(Svetlana fronce les sourcils)<new_line><new_line>En effet. Mais comment savez-vous tout ça ?]],
				['30'] = {
					['2'] = [[J'ai vérifié : le virus a attaqué partout et tout le monde en même temps. Endiguer l'épidémie était impossible.<new_line>Je sais que ça a l'air fou mais... et si quelqu'un avait délibérément provoqué cette épidémie ? Si on avait contaminé des gens de différentes villes au même moment ?]],
					['1'] = [[(Svetlana parle encore plus bas)<new_line><new_line>L'épidémie ! L'épidémie s'est déclenchée en même temps aux quatre coins de l'URSS ! Aucun virus ne s'est jamais répandu comme ça. Une maladie peut toujours être localisée, on peut mettre des quarantaines en place et prendre des mesures préventives. Ou tout du moins essayer. Mais pas avec cette maladie.]]
				},
				['32'] = [[(Svetlana se relève et me fixe, furieuse et agitée.)<new_line><new_line>Je sais bien ! Mais ce n'est pas plus fou que de ratisser la friche à la recherche de votre famille !]],
				['22'] = {
					['2'] = [[Mais on peut faire quelque chose : découvrir la vérité. Découvrir qui est responsable de la mort de millions de personnes et qui a transformé notre pays, voire notre monde, en une immense friche. Et savoir pourquoi ils ont fait ça. C'est important, n'est-ce pas ? Tout le monde mérite de le savoir, non ?<new_line>Rien ne vous y force, bien sûr. Tout ce que je peux faire, c'est vous le demander, et vous proposer une récompense.]],
					['1'] = [[Oui... Mais essayez de me comprendre. Je n'ai plus aucune famille et personne n'a plus besoin du remède contre l'épidémie. Tout ce qu'il me reste, c'est l'envie de survivre. Ma raison de vivre, c'est justement de vivre un peu plus longtemps. J'en ai assez de cette existence !]]
				},
				['9'] = [[D'accord, mais... Pourquoi me cherchiez-vous ?]],
				['42'] = [[Vous savez, son premier. Vous n'avez pas remarqué ? Elle est enceinte !]],
				['5'] = [[(Svetlana me fixe droit dans les yeux pendant un longtemps moment avant de détourner le regard)<new_line><new_line>Moi aussi, je me suis battue le plus longtemps possible. Je croyais vraiment que je pouvais faire quelque chose, que je pourrais arranger la situation...]],
				['44'] = {
					['2'] = [[(En voyant l'air concentré mais heureux de Svetlana, je perds pied, ne sachant pas par où commencer. Je baragouine quelque chose d'inintelligible, la voix soudainement rauque. Svetlana me tend un verre d'eau que j'avale d'une traite. Enfin, je me décide à parler.)]],
					['1'] = [[Bon, sinon... Vous êtes venu me dire quelque chose, pas vrai ? Alors allez-y. Vous avez appris quelque chose à propos de ce dont on parlait ?]]
				},
				['46'] = [[(Svetlana se tourne vers moi, le regard vide d'intérêt. Mais je vois ses mains trembler.)<new_line><new_line>Et pourquoi me dites-vous tout ça ?]],
				['1'] = [[(La femme lève ses yeux gris et tristes vers moi et me sourit chaleureusement)<new_line><new_line>Bonjour. Ça faisait longtemps qu'on n'avait pas vu de nouveaux visages, ici. Qui êtes-vous ? Et d'où venez-vous ?]],
				['48'] = [[Sortez, s'il vous plaît. J'ai du travail.]],
				['38'] = [[(Quand j'ai toqué à la porte de Svetlana, elle ne m'a pas ouvert tout de suite.<new_line>Une jeune fille est assise sur le petit canapé du bureau. Quand elle me voit, elle rougit, remercie Svetlana et sort en vitesse. Svetlana rit et me fait un clin d'œil.)<new_line><new_line>Tss, elle est timide. Alors, quoi de neuf ?]]
			},
			['name'] = [[Svetlana]]
		}
	},
