	['charge_ended'] = [[Kehabisan tenaga]],
	['squad_death'] = [[Aku berhasil menangkap penjarah selama berhenti untuk beristirahat. Mereka sedang santai dan memilah barang yang mereka temukan. Aku telah memutuskan langkah berikutnya sebelum mereka melihatku.]],
	['woman'] = [[Wanita]],
	['barQuest'] = {
		['progress_izhevsk'] = {
			['step'] = {
				['1'] = [[Izhevsk dibagi dari Iron Front oleh Pegunungan Ural dan berkilo-kilo bumi yang terinfeksi, yang dipenuhi mutan. Mungkin itu sebabnya Iron Front masih belum menguasai industri senjata lokal.<new_line><new_line>Jadi, para pemukim dari Izhevsk ingin mempertahankan kontak dengan Ryazan—jaraknya jauh dan mereka bisa dengan tenang memperdagangkan senjata mereka dan produk dari industri pembantaian lebah mereka.]]
			}
		},
		['firebird_feather'] = {
			['name'] = [[Bulu Burung Api]],
			['text'] = [[Di dalam dongeng kuno diceritakan bahwa Burung Api tertarik dengan apel emas. Kami tidak memiliki itu, tetapi aku berhasil menyihir beberapa buah apel biasa. Jika kamu mengolesinya dengan madu, burung itu tidak akan menyadari perbedaannya dan seharusnya terbang menghampirimu.]]
		},
		['progress_chelyabinsk'] = {
			['step'] = {
				['1'] = [[Chelyabinsk adalah permukiman industri padat penduduk. Ini adalah markas tempat Mandor mengontrol produksi dan setiap aspek kehidupan di Iron Front.<new_line><new_line>Ini masuk akal—dari situlah jalur kereta api dengan kota-kota lain berasal. Iron Front melimpahi Siberia Barat dengan logam dan sebagai imbalannya membeli makanan dari Siberia, karena tidak ada cara untuk membangun pertanian di sini.]]
			}
		},
		['progress_sverdlovsk'] = {
			['step'] = {
				['1'] = [[Iron Front menganggap Sverdlovsk sebagai ibu kota persenjataan dan sumber pasukan terbaiknya. Para pejuang yang bersenjata lengkap dan bermotivasi tinggi ini pergi dari sini untuk menyerang padang gurun.<new_line><new_line>Para perampok berkata bahwa mereka hanya menangkap bandit berbahaya lalu mendidik mereka kembali dengan kerja keras di bidang industri. Namun nyatanya, bahkan pengelana yang aman pun terancam di bagian ini.]]
			}
		},
		['place_stash'] = {
			['text'] = [[Ini petinya. Apakah kau melihat tanda silang ini di peta? Kubur peti itu di sini, tapi jangan pernah berpikir untuk membukanya, oke?']],
			['name'] = [[Simpan Wadah Rahasia]],
			['step'] = {
				['1'] = [[Aku harus pergi ke lokasi yang ditandai dan menyembunyikan peti.]],
				['4'] = [[Aku telah menyembunyikan peti itu. Semoga penerima yang dituju dapat menemukannya.]],
				['3'] = [[Pekerjaanku selesai. Aku telah menyembunyikan peti tepat di mana mereka memintanya. Sekarang, aku bisa kembali ke markas.]],
				['5'] = [[Apa tujuannya semua ini? Aku akan simpan peti itu.]]
			}
		},
		['progress_briansk'] = {
			['step'] = {
				['1'] = [[Selama bencana, ada arus besar pengungsi melewati Bryansk, dan tingkat kematian yang sangat tinggi di antara mereka. Namun, akibatnya, jumlah penyintas juga sangat tinggi, dan mereka sudah memiliki kekebalan.<new_line><new_line>Masalahnya adalah mereka masih terjebak di sekitar Bryansk, tanpa organisasi atau dukungan nyata. Berbagai pihak sudah mengincar permukimannya: banyak yang ingin mengeksploitasi sumber daya manusia seperti itu. Lalu, seperti yang ditunjukkan oleh pengalaman pahit, bukan untuk tujuan mulia apa pun.]]
			}
		},
		['progress_ryazan'] = {
			['step'] = {
				['1'] = [["Kamu harus mengunjungi Ryazan sekali sebelum kamu mati," adalah humor gelap yang diceritakan ratusan kilometer di sekitar permukiman. Dahulu kala, klan bandit mengambil alih kilang minyak dan memulai perdagangan cepat bahan bakar untuk mata uang mereka sendiri — Rubel Hitam. Satu kota pasar tumbuh dengan pesat di sekitar pabrik.<new_line><new_line>Sekarang, kota ini adalah pusat perdagangan di Padang Gurun. Namun, kejahatan kriminalnya masih sangat terlihat: penipuan, kekerasan, dan penyelewengan mengintip dari balik papan tanda berkilauan dan kemewahan kasino.]]
			}
		},
		['hunt_monsters'] = {
			['text'] = [[Ada terlalu banyak mayat hidup gila yang mengelilingi portal. Saatnya melakukan tugasmu dan tenangkan zombie tersebut.]],
			['step'] = {
				['1'] = [[Aku diminta mengurangi jumlah mayat hidup di area.]]
			}
		},
		['impostor'] = {
			['text'] = [[Aku mendengar berbagai laporan tentang adanya penyamar. Ada bandit yang berpura-pura menyamar sebagai Kakek Salju! Dia merampok orang-orang dan merusak reputasi kami. Kamu harus menghentikannya.]],
			['name'] = [[Penyamar]],
			['step'] = {
				['1'] = [[Penyamar Kakek Salju ada di wilayah ini, merampok orang-orang. Ia harus dihentikan.]],
				['3'] = [[The Sack dari Loot adalah dengan saya. Aku harus mengembalikannya kepada orang-orang.]],
				['2'] = [[Sekarang aku tahu di mana untuk menemukan penipu, waktu itu untuk membayar kunjungan.]]
			}
		},
		['construction_team'] = {
			['text'] = [[Kami tidak memiliki cukup pekerja di lokasi ini. Lakukan tugasmu untuk kebaikan bersama dan kami akan membayarmu.]],
			['name'] = [[Brigade Konstruksi]],
			['step'] = {
				['8'] = [[Bersihkan sarang mutan.]],
				['14'] = [[Bawa kembali banyak persediaan.]],
				['12'] = [[Cari tahu apa yang terjadi di beberapa tempat di sekitar pos terdepan.]],
				['1'] = [[Juru mudi mengirimku untuk membantu para pekerja di lokasi konstruksi dekat permukiman.]],
				['3'] = [[Masih ada pekerjaan yang harus kulakukan di lokasi ini. Aku perlu memulihkan kekuatanku dan kembali ke mandor.]],
				['2'] = [[Mandor meminta bantuan di lokasi.]],
				['4'] = [[Berbagi rokok dengan para pekerja.]],
				['10'] = [[Atasi serangan bandit.]],
				['6'] = [[Bersihkan sarang hama.]]
			}
		},
		['progress_gorenichi'] = {
			['step'] = {
				['1'] = [[Selama bencana, tanah di sekitar menjadi sasaran pengeboman massal, tetapi beberapa bidang tanah hitam yang subur bertahan.<new_line>Penduduk setempat menemukan sebidang tanah ini dan mulai bercocok tanam.<new_line><new_line>Kemudian ancaman baru muncul, peninggalan dari radiasi: gelombang demi gelombang makhluk mutan mengepung permukiman di sini. Namun begitu, serigala-serigala hitam sudah menetapkan tempat mereka di puncak rantai makanan dan menghancurkan kompetisi. Namun, kekuatan para penyintas manusia semakin melemah karena serangan ganas mereka.]]
			}
		},
		['pumpkin_seed'] = {
			['text'] = [[Jika kepala itu berkata sejujurnya, tamatlah kita. Tetapi ada satu kesempatan, walaupun aku berharap tak pernah perlu menggunakannya. Bawa biji labu ini dan tanam di padang gurun.]],
			['name'] = [[Biji Labu]],
			['step'] = {
				['1'] = [[Aku mendapatkan biji yang harus kutanam di padang gurun. Entah apa yang ditakuti bartender, atau bagaimana ini bisa membantu kami. Terlihat tidak berbahaya sama sekali.]]
			}
		},
		['wood_delivery'] = {
			['text'] = [[Dalam cuaca dingin seperti ini, seikat kayu bakar bisa menyelamatkan nyawa seseorang. Kumpulkan beberapa potong kayu dan letakkan di tempat yang mudah ditemukan.]],
			['name'] = [[Pengiriman Kayu]],
			['step'] = {
				['1'] = [[Gadis Salju memintaku mengumpulkan kayu bakar dan memberikannya kepada orang-orang.]]
			}
		},
		['snowmen_fortress'] = {
			['text'] = [[Boneka-boneka salju itu membangun benteng salju utuh dan merusak wilayah. Tolong pergi ke sana dan hentikan ulah mereka.]],
			['name'] = [[Benteng Salju]],
			['step'] = {
				['1'] = [[Tugasku adalah menghancurkan benteng salju dan menyingkirkan boneka-boneka salju di sana.]]
			}
		},
		['progress_omsk'] = {
			['step'] = {
				['1'] = [[Omsk memisahkan diri dari S.O.V.A., dan sekarang dijalankan oleh militer. Satu-satunya jalur perdagangan antara Timur dan Barat melewati permukiman.<new_line>Tampaknya itulah yang memungkinkan permukiman tertutup seperti ini bertahan.<new_line><new_line>Namun, petunjuk dan rumor menunjukkan bahwa militer memiliki hal lain yang menghalangi kekuatan Ural dan Siberia lainnya. Kamu bisa mencoba mencari tahu lebih banyak tentang Omsk, tetapi hanya sedikit orang yang berhasil menjalin hubungan bahkan dengan batas luar kota.]]
			}
		},
		['catch_thief'] = {
			['text'] = [[Kepala gudang mengeluh tentang pencuri. Tidak mungkin dia bisa menangkap mereka semua sendirian. Bantu dia.']],
			['name'] = [[Tangkap Pencuri]],
			['step'] = {
				['9'] = [[Mandor gudang meminta bantuan tambahan—untuk mengatasi komplotan si pencuri.]],
				['8'] = [[Mandor gudang meminta bantuan tambahan—untuk membawa kembali barang yang dijarah.]],
				['1'] = [[Hal paling pertama, aku harus pergi ke gudang dan berbicara dengan kepala.]],
				['10'] = [[Aku harus membawa kembali barang yang dijarah ke gudang.]],
				['3'] = [[Pencuri itu terjebak dalam perangkapku dan mati.]],
				['2'] = [[Aku membubarkan kamp bandit yang mencuri dari markas. Ini harus berjalan lancar mulai sekarang.]],
				['5'] = [[Aku membuat kesepakatan dengan para bandit dan membiarkan mereka masuk ke gudang, lalu aku mengeluarkan mereka semua.]],
				['4'] = [[Aku berhasil menangkap pencuri itu hidup-hidup.]],
				['7'] = [[Mengetahui bahwa orang lain akan disalahkan, Aku mengambil beberapa barang dari gudang.]],
				['6'] = [[Aku tidak punya keahlian menjadi penjaga. Pencuri berhasil kabur.]]
			}
		},
		['camp_defence'] = {
			['text'] = [[Para bandit telah berkumpul dan datang untuk menyerang kamp kita. Bantu aku dan bantu pertahanan.']],
			['name'] = [[Pertahanan Kamp]],
			['step'] = {
				['1'] = [[Aku harus pergi ke kamp sekutu kita secepat mungkin untuk membantu mereka mempertahankannya.]],
				['3'] = [[Kita dengan mudah mengalahkan bandit dan mempertahankan kamp.]],
				['2'] = [[Aku melakukan semua yang aku bisa. Saatnya kembali ke markas dan melaporkan apa yang terjadi di kamp.]],
				['5'] = [[Kita berhasil menang, tetapi kehilangan beberapa anggota kita sendiri.]],
				['4'] = [[Kita menang, tapi masih ada korban.]],
				['6'] = [[Pada saat aku sampai di sana, sudah terlambat.]]
			}
		},
		['leprechaun_debts'] = {
			['text'] = [[Leprechaun memiliki utang minum besar kepadaku. Ikuti pelangi untuk menemukan mereka. Dapatkan emasku dan bawa kembali. Tetapi jangan gunakan untukmu sendiri, emas itu terkutuk.]],
			['name'] = [[Utang Leprechaun]],
			['step'] = {
				['1'] = [[Aku diminta mengambil emas dari leprechaun yang berutang.]]
			}
		},
		['headless_horseman'] = {
			['text'] = [[Gadis yang kau selamatkan tadi di sini dan ia menceritakan percakapan yang ia dengar di kapal. Nampaknya Drakula ingin menggunakan buku ini untuk membangkitkan Sang Dewa Kuno dan menghancurkan perbatasan antar dunia. Ia harus dihentikan. Cegat Penunggang Kuda dan ambil bukunya.]],
			['name'] = [[Penunggang Kuda Tanpa Kepala]],
			['step'] = {
				['1'] = [[Aku harus mencegat Penunggang Kuda dan mengambil bukunya. Aku mendapatkan bola benang untuk membantuku menemukannya.]]
			}
		},
		['progress_olonec'] = {
			['step'] = {
				['1'] = [[Permukiman dengan penduduk pertama di perjalananku. Rasanya menegangkan bisa berada di antara orang-orang lagi. Di sini, setiap orang telah terbiasa mengandalkan satu sama lain dan pada sumber daya mereka sendiri untuk mengatasi kesulitan. Mereka hidup dengan cara yang sama di sini seperti yang mungkin mereka lakukan seabad yang lalu.<new_line><new_line>Nelayan tidak pergi melampaui Danau Ladoga, dan aku ragu bisa menemukan jawaban atas semua pertanyaanku di sini, tetapi ini adalah tempat bagus untuk mendapatkan berita dan mempersiapkan perjalanan lebih jauh.]]
			}
		},
		['march_skeletons'] = {
			['text'] = [[Pernah membangkitkan orang mati? Aku perlu jerangkong sebagai pekerja. Ambil gulungan ini, semua petunjuknya ada di situ. Jangan asal campur.]],
			['name'] = [[Barisan Jerangkong]],
			['step'] = {
				['1'] = [[Aku diminta mengumpulkan beberapa jerangkong dan melakukan ritual menggunakan gulungan.<new_line><new_line>Kumpulkan sepuluh jerangkong dan letakkan dalam bentuk lingkaran, tengkorak menghadap tengah. Taburi tengkorak dengan abu secara memutar dari barat tiga kali sembari memanggil arwah Tetriandok. Para jerangkong akan berjalan sendiri saat ritual berakhir.]]
			}
		},
		['drakula'] = {
			['text'] = [[Si Kepala Labu bertemu menghadapi Sang Dewa Kuno di padang gurun. Drakula pasti ada di sana. Temukan dan hancurkan dia, sebelum ia melakukan apa pun.]],
			['name'] = [[Pangeran Drakula]],
			['step'] = {
				['1'] = [[Inilah pertempuran terakhir, dan musuhku adalah Pangeran Drakula.]]
			}
		},
		['toy_story'] = {
			['name'] = [[Kisah Mainan]],
			['text'] = [[Berkatmu, sebagian dari sihir sudah dipulihkan dan aku menemukan jalan menuju terem kami, tapi aku takut jika di sana ada jebakan. Tolong lihat dan cari tahu apa yang ada di sana, tapi kumohon, berhati-hatilah.]]
		},
		['doll'] = {
			['text'] = [[Kau menanyakan soal boneka itu. Aku hanya tahu satu orang yang bisa mengendalikannya. Sang dalang adalah orang yang jahat, tetapi tidak ada pilihan lain. Akan kutandai di peta tempat kau bisa mencarinya.]],
			['name'] = [[Boneka]],
			['step'] = {
				['1'] = [[Bartender menandai bengkel dalang di peta. Mungkin jawabannya ada di sana.]]
			}
		},
		['set_up_devices'] = {
			['text'] = [["Kami sedang mempelajari perilaku biomutan dan cara spora itu menyebar. Pasangkan perlengkapan ini di dekat permukiman. Informasi yang kita kumpulkan akan membantu dalam riset proyek EMBA kita."]],
			['name'] = [[Pasang Perlengkapan]],
			['step'] = {
				['1'] = [[Progress sedang mengamati area di sekitar permukiman. Aku perlu memasang perlengkapan mereka di luar markas.]],
				['3'] = [[Aku sudah memasang dan menyamarkan perlengkapan itu.<new_line>Kawanan biomutan yang semestinya bisa melihatku telah dimusnahkan. Perangkat itu aman sekarang. Aku harus melaporkannya kepada anggota staf Progress.]],
				['2'] = [[Aku sudah memasang dan menyamarkan perlengkapan itu. Aku harus melaporkannya kepada anggota staf Progress.]]
			}
		},
		['new_year_boss_2022_2'] = {
			['step'] = {
				['1'] = [[Aku perlu menggali lubang. Cave Santa mungkin berencana membuat jebakan.]],
				['3'] = [[Kami memasang jebakan, dan aku seharusnya menjadi umpan.]],
				['2'] = [[Aku perlu menutupi lubang Cave Santa dengan rumput.]],
				['5'] = [[Kita berhasil mengikuti jejak Sinterklas yang hilang. Bandit yang melarikan diri dari penyergapan mungkin mengantar kita ke target kita. Ayo ikuti dia.]],
				['4'] = [[Kita telah mengetahui bahwa Sinterklas telah berubah menjadi Krampus Claus, dan gengnya sekarang menghancurkan segala sesuatu yang berhubungan dengan Natal. Kita harus menemukan Business Claus untuk membahas langkah selanjutnya.]]
			}
		},
		['polar_star'] = {
			['name'] = [[Bintang Kutub]],
			['text'] = [[Bola yang kamu temukan itu adalah Bintang Kutub. Kakek Salju menggunakannya dalam perjalanan panjangnya, tapi kamu harus memahami cara kerjanya sendiri. Ambil bintangnya, berdiri di padang gurun di malam hari yang cerah, lalu angkat bintang itu ke langit.]]
		},
		['destroy_lair'] = {
			['text'] = [[Penjaga kepala menunjuk peta yang tergantung di belakangnya. "Ada serangan binatang buas berbahaya di daerah ini. Pergi dan hancurkan sarang mereka."]],
			['name'] = [[Hancurkan Sarang]],
			['step'] = {
				['1'] = [[Aku harus menemukan sarangnya dan menghancurkan penghuninya.]],
				['2'] = [[Aku berhasil menghancurkan sarang.]]
			}
		},
		['secret_documents'] = {
			['text'] = [["Kamerad, usahamu tidak sia-sia! Akhirnya, kita tahu tempat dokumen rahasia yang berhubungan dengan biostation terdekat itu! Pergilah ke Laboratorium Telantar, dokumen itu pasti ada di sana."]],
			['name'] = [[Dokumen Rahasia]],
			['step'] = {
				['1'] = [[Aku harus memeriksa Laboratorium Telantar. Di situlah tempat dokumen rahasia yang dibutuhkan Ahli Biologi itu untuk dapat melanjutkan risetnya.]],
				['2'] = [[Aku sudah mengamankan tempat ini. Sekarang tinggal membongkar lemari besi itu dan mengambil dokumen rahasianya.]]
			}
		},
		['winter_queen'] = {
			['name'] = [[Ratu Es]],
			['text'] = [[Saatnya menyelamatkan Kakek Salju! Jika kamu sudah siap, gunakan Bintang Kutub itu. Kita akan berangkat menuju istana es bersama-sama dan melawan Ratu Es.]]
		},
		['gravedigger'] = {
			['text'] = [[Kaulah orang yang kubutuhkan. Aku ada pekerjaan untukmu. Seorang penggali kuburan menjadi gila. Ia menggali mayat, alih-alih menguburnya. Kita perlu mengakhiri itu. Bisakah kau tangani?]],
			['name'] = [[Penggali Kuburan]],
			['step'] = {
				['1'] = [[Aku disuruh menghadapi penggali kuburan gila.]]
			}
		},
		['science_polygon'] = {
			['text'] = [["Kami sedang melakukan eksperimen di Lapangan Pengujian untuk lebih memahami proyek EMBA. Namun, kami ada masalah serangan biomutan. Kau punya banyak pengalaman tempur — dapatkah kau mengatasi ancaman itu?"]],
			['name'] = [[Lapangan Pengujian]],
			['step'] = {
				['1'] = [[Aku harus membantu ilmuwan Progress menghadapi biomutan.]]
			}
		},
		['provision'] = {
			['text'] = [["Para pengintai kami akan mencari fasilitas yang terhubung dengan proyek EMBA. Bantulah mengumpulkan persediaan untuk operasi mereka."]],
			['name'] = [[Persediaan]],
			['step'] = {
				['1'] = [[Salah seorang staf Progress memintaku mengumpulkan persediaan untuk ekspedisi itu.]],
				['2'] = [[Aku sudah mengumpulkan segala yang dibutuhkan dan harus memberikan persediaan itu kepada Juru Arsip di permukiman.]]
			}
		},
		['search_killer'] = {
			['text'] = [[Sang pembunuh mencuri lewat portal ke Dunia Atas. Kau harus mencari dan menghentikannya.]],
			['name'] = [[Temukan Pembunuhnya]],
			['step'] = {
				['1'] = [[Aku harus melacak sang pembunuh dan menghentikannya. Ia di suatu tempat di Dunia Atas.]]
			}
		},
		['new_year_deliver_cloth'] = {
			['text'] = [[Anak-anak merajut topi dan kaus kaki hangat untuk orang-orang yang bekerja di luar permukiman yang dingin. Bisakah kamu mengantarkan beberapa paket kepada mereka?]],
			['name'] = [[Kirim pakaian]],
			['step'] = {
				['1'] = [[Aku sudah memutuskan untuk mengirimkan pakaian hangat kepada mereka yang bekerja di luar permukiman.]],
				['2'] = [[Aku perlu membawa barang-barang itu ke satu kelompok pemukim lagi yang pergi mengurus urusan mereka.]]
			}
		},
		['immortal_love'] = {
			['text'] = [[Aku berhasil memecahkan kode entri terakhir dari catatan. Tertulis tentang seorang gadis yang diculik atas perintah mumi. Kita harus menyelamatkannya. Ikuti kalajengking untuk menemukan sarang mumi. Pancing mereka keluar, dan mereka akan membawamu ke tuan mereka.]],
			['name'] = [[Mumi]],
			['step'] = {
				['1'] = [[Tugasku adalah menyelamatkan seorang gadis dari sarang mumi. Untuk menemukannya, aku harus memancing kalajengking dan mengikuti mereka.]]
			}
		},
		['snowman_giant'] = {
			['name'] = [[Raja Boneka Salju]],
			['text'] = [[Boneka salju raksasa makin mendekat. Ia harus dihentikan sebelum mencapai permukiman dan menimbulkan masalah. Kita akan bertemu di sana, ada sesuatu yang harus kuberitahukan padamu.]]
		},
		['undertaker'] = {
			['text'] = [[Sebuah kuburan muncul kembali. Para zombie sudah tenang tetapi mereka perlu peti mati baru. Bawa Sang Penjaga Kuburan denganmu. Cari bahan-bahannya dan ia akan membuatkan petinya. Kemudian bawa ke tempat yang tepat.]],
			['name'] = [[Penjaga Kuburan]],
			['step'] = {
				['1'] = [[Aku perlu mencari bahan-bahan dan meminta Penjaga Kuburan membuatkan peti mati.]],
				['2'] = [[Peti yang baru harus diantarkan ke kuburan yang rusak.]]
			}
		},
		['cover_the_fighter'] = {
			['text'] = [[Pengintai itu menemukan lokasi terbengkalai, tetapi menjelajahi tempat seperti itu sendirian berbahaya. Beri dia perlindungan.]],
			['name'] = [[Berikan Perlindungan]],
			['step'] = {
				['1'] = [[Pengintai itu menemukan lokasi yang terlalu berbahaya untuk didekati sendirian. Aku perlu membantunya jika ada yang menyerangnya.]],
				['3'] = [[Buka kunci brankas yang tidak sempat ditangani oleh pengintai.]],
				['2'] = [[Kami berhasil mengakses lokasi ini, dan kondisi pengintai cukup aman. Aku bisa kembali ke permukiman.]]
			}
		},
		['intelligence'] = {
			['text'] = [["Kami telah menemukan sebuah markas militer yang terhubung ke proyek EMBA. Mereka pasti memiliki data tentang fasilitas rahasia di wilayah ini. Kita mungkin bisa menggunakan data ini untuk mencari tahu lokasi dokumen rahasia untuk proyek itu."]],
			['name'] = [[Data Pengintaian]],
			['step'] = {
				['1'] = [[Ilmuwan Progress menugaskanku untuk mendapatkan data pengintaian dari Markas Militer.]],
				['2'] = [[Aku sudah mendapatkan data pengintaian itu dan kini saatnya kembali ke permukiman.]]
			}
		},
		['witch_delivery'] = {
			['text'] = [[Aku perlu menyuruhmu mengantarkan sekotak bahan-bahan ke penyihir setempat dan ambil sekumpulan ramuan darinya.]],
			['name'] = [[Pengiriman Penyihir]],
			['step'] = {
				['1'] = [[Aku harus mengirimkan sekotak bahan-bahan ke seorang penyihir, mengambil sekumpulan ramuan, dan kembali ke portal.]],
				['2'] = [[Saya memiliki ramuan. Sekarang saya harus memberikan mereka untuk bartender.]]
			}
		},
		['halloween_boss_2022_6'] = {
			['text'] = [[Ada Keledai Bisa Bicara yang meneror penduduk setempat: dia menghabiskan hasil panen mereka, mencoba menjual koin palsu, dan dia sudah berbicara panjang lebar kepada tiga petani.<new_line>Penduduk desa sudah sejak lama ingin menjatuhinya hukuman dengan garpu rumput, tapi keledai itu dilindungi oleh makhluk yang tinggal di rawa sekitar.]],
			['name'] = [[Keledai Bisa Bicara]],
			['step'] = {
				['1'] = [[Keledai itu bersembunyi di rawa. Aku harus menemukannya, sebaiknya tanpa bertemu dengan binatang buas yang melindunginya.]],
				['3'] = [[Pencabut Nyawa membuat kontrak denganku, dan sekarang dia ingin mengambil jiwaku. Aku mengetahui bahwa Gigawattor-ku bisa menutup portalnya.<new_line>Aku harus mengalahkan Pencabut Nyawa dalam pertempuran.]],
				['2'] = [[Keledai itu berhasil melarikan diri, dan binatang buas rawa yang melindunginya mungkin akan muncul. Cepat, waktunya tidak banyak!]]
			}
		},
		['new_year_boss_2022_6'] = {
			['step'] = {
				['1'] = [[Aku berhasil menyingkirkan Frosty yang jahat. Tapi apakah benar ada keajaiban Natal? Apakah Sinterklas kembali?]],
				['2'] = [[Dalam pertempuran terakhir, aku memainkan peran utama. Ayo mulai!]]
			}
		},
		['comrade_in_trouble'] = {
			['text'] = [[Salah satu anggota kami seharusnya kembali dari ekspedisi kemarin, tapi tidak ada tanda-tanda darinya. Pergi dan cari tahu apakah ada sesuatu yang terjadi padanya dan apakah dia membutuhkan bantuan.]],
			['name'] = [[Kawan dalam Masalah]],
			['step'] = {
				['16'] = [[Pengintai itu butuh waktu lama mengumpulkan semua jarahan dan baru pulang sekarang. Aku setuju untuk mengirimkan peti yang tersisa. Aku harus kembali ke permukiman dan melapor.]],
				['17'] = [[Pengintai itu butuh waktu lama mengumpulkan semua jarahan dan baru pulang sekarang. Aku harus kembali ke permukiman dan melapor.]],
