		['halloween_boss_2022_3'] = {
			['text'] = [[Count Drakula adalah seorang vampir kuno, penikmat seni, dan pengagum wanita cantik. Kata orang, dia gagah dan sopan, tapi itu tidak menghentikannya memburu orang dan mengisap darah mereka.]],
			['step'] = {
				['1'] = [[Ada laporan bahwa sekelompok pengembara diserang oleh vampir. Aku harus pergi ke tempat terjadinya insiden itu.]],
				['3'] = [[Aku sadar sudah berada di menu Count Drakula. Dia mengundangku untuk bersantai dan bersenang-senang—setelah itu segalanya akan menjadi berantakan.]],
				['2'] = [[Aku menemukan penyintas dari serangan vampir. Aku harus mengantarnya pulang dulu, lalu aku akan meminta informasi tentang penyergapan itu.]]
			}
		},
		['progress_krasnoyarsk'] = {
			['step'] = {
				['1'] = [[Permukiman Krasnoyarsk seperti utopia sains nyata. Orang-orang bekerja di rumah kaca, diarahkan oleh para ilmuwan. Sistem BIOS-4 yang mereka kembangkan memungkinkan untuk mendapatkan panen yang belum pernah terjadi sebelumnya dan bahkan memberi makan permukiman lain. Tidak gratis tentunya.<new_line><new_line>Semua ada harganya: prosesnya memerlukan penggunaan listrik dan air bersih yang signifikan. Selain itu, prosesnya sangat canggih sehingga kehilangan satu pekerja utama saja bisa meruntuhkan seluruh sistem.]]
			}
		},
		['help_hunter'] = {
			['text'] = [[Seorang pemburu dalam masalah. Bantu dia. Akan kutandai di peta.]],
			['name'] = [[Bantu Pemburu]],
			['step'] = {
				['1'] = [[Aku harus membantu pemburu lain.]]
