		['scientist_rescue'] = {
			['text'] = [[Ilmuwan kami sedang bekerja bergiliran di stasiun ramah lingkungan. Dia menghubungi kami untuk mengabari bahwa dia mengalami masalah dengan para bandit lokal. Pergi ke sana dan bawa dia pulang.]],
			['name'] = [[Bawa kembali ilmuwan itu]],
			['step'] = {
				['9'] = [[Lepaskan perangkat yang tersisa dari atap.]],
				['8'] = [[Aku sudah menemukan ilmuwan itu, sekarang aku harus membawanya kembali ke permukiman<new_line><new_line>Sebelum berangkat, aku membantu ilmuwan itu mengambil perangkat yang tersisa.]],
				['1'] = [[Aku harus mencapai stasiun ramah lingkungan dan membawa ilmuwan di sana kembali ke permukiman.]],
				['3'] = [[Ambil perlengkapan dari para bandit.]],
				['2'] = [[Aku sudah menemukan ilmuwan itu, sekarang aku harus membawanya kembali ke permukiman.]],
				['5'] = [[Aku sudah menemukan ilmuwan itu dan dia menungguku untuk membawanya kembali ke permukiman.]],
				['4'] = [[Aku sudah menemukan ilmuwan itu, sekarang aku harus membawanya kembali ke permukiman.<new_line><new_line>Dalam perjalanan kembali, kami mengambil perlengkapan yang dicuri dari ilmuwan itu.]],
				['7'] = [[Bantu ilmuwan itu memulihkan luka ringan.]],
				['6'] = [[Aku sudah menemukan ilmuwan itu, sekarang aku harus membawanya kembali ke permukiman<new_line><new_line>Sebelum berangkat, aku membantu ilmuwan itu memulihkan luka ringan.]]
			}
		},
		['frozen'] = {
			['text'] = [[Para penjarah es menculik orang dan membekukan mereka dalam balok es! Jika bergegas, kamu masih bisa menyelamatkan mereka. Cepat pergi ke sana, burung hantu akan menunjukkan arahnya.]],
			['name'] = [[Membeku]],
			['step'] = {
				['1'] = [[Para penjarah es menculik orang-orang. Aku harus menyelamatkan mereka.]]
			}
		},
		['progress_tver'] = {
			['step'] = {
				['1'] = [[Orang-orang datang ke stasiun sungai yang terbengkalai dan membawa kehidupan baru di tempat ini. Sekarang, perahu dari Tver mengarungi sungai ke utara dan ke selatan, sementara di gedung stasiun yang bising, kamu bisa membeli item langka yang diperoleh dengan risiko besar dari Moskow atau diperdagangkan di Ryazan.<new_line><new_line>Penduduk permukiman sibuk dengan urusan mereka sendiri, tetapi dengan senang hati menerima pengembara masuk ke dalam kehidupan mereka yang sibuk, dengan bau khas ikan dan tar. Hanya tulang diam saja yang tidak diterima.]]
			}
		},
		['forest_feeder'] = {
			['text'] = [[Binatang-binatang belum siap menghadapi musim dingin ini, dan tugas kita adalah membantu mereka. Aku sudah menggantungkan tempat makan di hutan, tapi sekarang saatnya untuk mengisinya kembali.]],
			['name'] = [[Tempat Makan di Hutan]],
			['step'] = {
				['1'] = [[Aku diminta mengisi beberapa tempat makan di berbagai tempat di hutan.]]
			}
		},
		['gift_delivery'] = {
			['text'] = [[Hadiah harus dikirimkan apa pun yang terjadi. Ambil kotak-kotak ini dan berikan kepada orang-orang. Saat ini, mereka membutuhkan dukungan lebih dari sebelumnya.]],
			['name'] = [[Pengiriman Hadiah]],
			['step'] = {
				['1'] = [[Aku diminta mengirimkan beberapa hadiah.]]
			}
		},
		['progress_surgut'] = {
			['step'] = {
				['1'] = [[Setelah bencana yang meluas, Surgut dikelilingi oleh Sungai Ob yang meluap. Satu-satunya koneksi ke dunia luar adalah melalui jalan yang masih selamat ke Tyumen.<new_line><new_line>Yang bisa dilakukan permukiman hanyalah memasok minyak mentah kepada para mitranya di Sindikat. Untungnya, minyak terus mengalir tanpa hambatan. Rute transportasi sungai memungkinkan Surgut untuk berdagang dengan kota-kota lain di Siberia, tetapi siapa yang akan menyediakan transportasi sungai bagi mereka?]]
			}
		},
		['progress_rostov'] = {
			['step'] = {
				['1'] = [[Saat stepa di sekitar Don berubah menjadi padang gurun, Ataman mengumpulkan semua penyintas di bawah kendalinya. Sekelompok petualang bersatu di sekelilingnya—beberapa adalah keturunan Cossack, sementara yang lain hanya terjebak dalam romansa kebebasan.<new_line><new_line>Stepa ini pun kembali hidup, hidup dengan cara Cossack. Atau setidaknya, menurut interpretasi Ataman akan itu. Di bawah perlindungan druzhina, orang-orang biasa bisa bercocok tanam lagi dan akhirnya semua Rostov selamat dari kelaparan.]]
			}
		},
		['halloween_boss_2022_5'] = {
			['text'] = [[Ilmuwan Victor Frankenstein menyusun tubuh monster dari bagian tubuh orang lain dan menghidupkannya dengan menggunakan listrik.<new_line>Monster Frankenstein dan teman bungkuknya, Igor, datang ke dunia ini belum lama ini, tapi mereka sudah meninggalkan jejak pembunuhan mengerikan: ada banyak kasus mayat dengan organ tubuh yang dipotong.]],
			['step'] = {
				['1'] = [[Pencabut Nyawa memberiku catatan dari Buku Kematian tentang mereka yang akan mati di area tempat Igor dan Monster terakhir membunuh. Aku harus memeriksa semua orang dari halaman yang dia sobek—salah satu dari mereka bisa jadi korban berikutnya.]],
				['3'] = [[Aku berhasil menemukan Igor dan Monster Frankenstein. Aku harus mengalahkan mereka dalam pertempuran.]],
				['2'] = [[Aku harus pergi ke tempat berikutnya di halaman dari Buku Kematian ini.]]
			}
		},
		['take_stash'] = {
			['text'] = [[Hei, teman, aku punya kiriman di sini. Yah, tidak persis di sini... Mereka menguburnya di sepanjang jalan. Maukah kau mengambilkannya untukku?']],
			['name'] = [[Ambil Simpanan]],
			['step'] = {
				['1'] = [[Tempat simpanan disembunyikan ditandai di peta. Aku harus pergi ke sana dan menggali peti itu.]],
				['3'] = [[Ketika aku kembali ke markas, aku memberikan peti itu kepada bartender.]],
				['2'] = [[Aku menemukan tempat itu dan menggali peti. Aku akan membawanya ke markas sekarang dan mencari tahu apa yang harus dilakukan dari sana.]],
				['5'] = [[Rebut kembali barang yang dicuri para bandit.]],
				['4'] = [[Setelah menggeledah peti itu di jalan, aku memutuskan untuk menyimpannya.]],
				['6'] = [[Seseorang di permukiman melakukan kesalahan dan menyembunyikan wadah rahasia dengan buruk, jadi itu diambil oleh para bandit. Aku harus kembali dan melaporkan ini.]]
			}
		},
		['progress_leningrad'] = {
			['step'] = {
				['1'] = [[Kamp Wisatawan yang terbengkalai berada tepat di luar hutan yang hangus karena radiasi dan padang gurun di sekitar Leningrad. Beberapa pemberani dari Tver membuat pos pemberhentian di sini.<new_line><new_line>Para "Wisatawan" (sebutan penjelajah veteran zona bahaya) meluncurkan ekspedisi menjarah area Leningrad, membawanya kembali ke markas, lalu mengirimkannya lewat sungai ke Tver.]]
			}
		},
		['sled_repair'] = {
			['text'] = [[Aku sudah menempuh jarak sejauh ini bersamamu hingga kereta luncurku terlihat mengerikan. Bisakah kau memperbaikinya?]],
			['name'] = [[Perbaikan Kereta Luncur]],
			['step'] = {
				['1'] = [[Gadis Salju memintaku memperbaiki kereta luncurnya.]]
			}
		},
		['halloween_boss_2022_4'] = {
			['text'] = [[Raja Labu adalah roh dari musim panen. Dia merawat kebun labunya dan memastikan bahwa orang-orang yang bekerja di ladang ini tidak merusak alam. Raja Labu mungkin adalah roh, tetapi dia juga keturunan bangsawan, jadi dia hanya bisa dilukai oleh Pedang Algojo.]],
			['step'] = {
				['1'] = [[Ada petani yang berkata bahwa hasil panennya menghilang. Kedengarannya seperti ulah tangan Raja Labu, tapi ini perlu diselidiki.]],
				['3'] = [[Aku harus mengalahkan Raja Labu. Kontraknya berkata bahwa hanya Pedang Algojo yang bisa melukainya.]],
				['2'] = [[Aku mengikuti jejaknya. Para pelarian ini berhasil melarikan diri... Aku harus mengikuti mereka.]]
			}
		},
		['camp_supply'] = {
			['text'] = [[Kamp sekutu kita sedang mengalami masa sulit. Kita telah memutuskan untuk membantu mereka dan mengirimkan beberapa perlengkapan. Bisakah kau membawanya ke mereka?']],
			['name'] = [[Perlengkapan Kamp]],
			['step'] = {
				['9'] = [[Sembuhkan sekutu yang terluka di kamp.]],
				['8'] = [[Bantu sekutu melawan serangan ini.]],
				['1'] = [[Aku telah diperintahkan untuk membawa perlengkapan ke kamp sekutu kita.]],
				['3'] = [[Aku mengirimkan perlengkapan. Sekutu kita senang atas bantuannya.]],
				['2'] = [[Perlengkapan telah dikirimkan dan itu yang terbaik yang bisa kulakukan. Aku kembali ke rumah.]],
				['5'] = [[Aku satu-satunya yang masih hidup. Sekarang, tidak ada yang memberikan perlengkapan.]],
				['4'] = [[Aku membantu sekutu menangkal serangan, lalu memberi mereka perlengkapan.]],
				['7'] = [[Kamp sekutu kita sedang diserang. Aku harus memberi tahu permukiman untuk mengirim bantuan.]],
				['6'] = [[Ketika aku tiba di kamp, semua orang sudah mati. Tidak ada seorang pun untuk diberikan perlengkapan.]]
			}
		},
		['halloween_boss_2022_2'] = {
			['text'] = [[Dokter Wabah melakukan eksperimen jahatnya di bawah selubung asap beracun: dia membangkitkan orang mati dan menyebarkan penyakit di antara orang-orang yang hidup. Zirah iblis bisa melindungi diri dari kutukan Dokter Wabah, tapi untuk menggapainya tidaklah mudah. Pembuat ramuan yang licik ini menjaga dirinya dengan gerombolan zombi.]],
			['step'] = {
				['1'] = [[Sekumpulan kecil penduduk sedang diburu oleh zombi. Ini adalah tanda pasti bahwa Dokter Wabah sudah tiba di suatu tempat di sekitar sini. Aku harus bertemu dengan orang-orang ini dan menanyai mereka.]],
				['3'] = [[Aku harus mengalahkan Dokter Wabah. Pencabut Nyawa mengisyaratkan bahwa Penyamaran Iblis akan melindungiku dari serangannya.]],
				['2'] = [[Orang-orang berkata bahwa Dokter Wabah sudah tiba di pemakaman setempat, jadi ke tempat itulah aku harus pergi.]]
			}
		},
		['hunt'] = {
			['text'] = [[Pesanan daging baru saja masuk. Mau berburu sedikit?']],
			['name'] = [[Perburuan]],
			['step'] = {
				['1'] = [[Aku telah diminta untuk mengumpulkan daging. Aku harus pergi berburu.]],
				['4'] = [[Aku tidak membawa cukup daging dan klien menolak untuk membayar.]],
				['3'] = [[Semuanya berjalan dengan baik dan klien senang.]],
				['2'] = [[Aku perlu membawa daging ke klien.]]
			}
		},
		['letter_of_hope'] = {
			['text'] = [[Bahkan di masa-masa sulit ini, orang-orang masih menulis surat untuk Kakek Salju. Mereka meninggalkannya di tempat tersembunyi dan sudut yang berbeda-beda. Kumpulkan surat-surat ini dan berikan padaku.]],
			['name'] = [[Surat Berisi Harapan]],
			['step'] = {
				['1'] = [[Gadis Salju memintaku mengumpulkan surat-surat yang ditinggalkan untuk Kakek Salju.]]
			}
		},
		['snowmen'] = {
			['text'] = [[Boneka-boneka salju jahil mulai membuat masalah dan menyerang orang-orang, tumbangkan mereka. Biarkan mereka terkapar di sana dan merenungkan perilaku mereka—Kakek Salju dan aku akan menumpuk mereka lagi nanti.]],
			['step'] = {
				['1'] = [[Aku harus pergi ke tempat itu dan membasmi boneka-boneka salju.]]
			}
		},
		['frankenstein'] = {
			['text'] = [[Berdasarkan catatan penggali kuburan, Igor berusaha menghidupkan kembali monster terkutuk. Aku tahu dia di mana. Aku mendengar tentang rumah yang selalu tersambar petir. Karena kau sudah memulainya, sebaiknya selesaikan segera.]],
			['name'] = [[Monster Frankenstein]],
			['step'] = {
				['1'] = [[Aku diminta menghentikan penghidupan kembali Monster Frankenstein.]]
			}
		},
		['order_delivery'] = {
			['text'] = [[Kita perlu memindahkan beberapa kargo dari markas lain. Apakah kau siap untuk pekerjaan itu?']],
			['name'] = [[Pengiriman]],
			['step'] = {
				['1'] = [[Aku harus pergi ke markas dan mengumpulkan kargo.]],
				['3'] = [[Kargo dikirim dalam keadaan utuh.]],
				['2'] = [[Aku sudah mengumpulkan kargo. Kembali ke markas sekarang.]],
				['5'] = [[Aku tidak berhasil mengirimkan kargo.]],
				['4'] = [[Aku mengirimkan kargo dan dibayar penuh.]],
				['6'] = [[Klien menolak untuk membeli kargo, jadi sekarang kargo ini terjebak denganku.]]
			}
		},
		['ice_brigands_camp'] = {
			['text'] = [[Kamp penjarah es lainnya sudah ditemukan. Kamu harus mendatangi kamp itu sebelum mereka mulai membuat masalah.]],
			['name'] = [[Kamp Penjarah]],
			['step'] = {
				['1'] = [[Kamp para penjarah es harus dihancurkan.]]
			}
		},
		['flying_dutchman'] = {
			['text'] = [[Kau bilang bukunya tak bisa ditemukan di daratan kering? Jika rumornya benar, Dutchman Terbang baru-baru ini kembali ke perairan kita. Kebetulan? Kurasa tidak. Jika kau siap mengejar sampai jauh, ambil lonceng ini. Bunyikan di tepi laut untuk menemukan jalan ke kapalnya.]],
			['name'] = [[Dutchman Terbang]],
			['step'] = {
				['1'] = [[Bartender memberiku lonceng yang akan membawaku ke kapal hantu di mana Igor mungkin bersembunyi dengan bukunya.]]
			}
		},
		['stash_handcar'] = {
			['step'] = {
				['1'] = [[Aku menemukan secarik kertas kecil dengan alamat sebuah rumah di Kola. Layak untuk dicoba. Lagi pula, mungkin aku akan menemukan wadah rahasia dengan persediaan di sana.]]
			}
		},
		['cold_footprints'] = {
			['name'] = [[Di Jalan Dingin]],
			['text'] = [[Kelinci-kelinci berhasil menemukan kamp para penjarah es yang menyerbu terem. Aku akan menandainya di petamu. Segera ke sana dan cari tahu tentang apa yang ada di sana sebisamu.]]
		},
		['winter_fishing'] = {
			['text'] = [[Aku sedang menyiapkan pesta perayaan dan perlu beberapa potong ikan. Mengapa kau tidak beristirahat saja dan pergi memancing?]],
			['name'] = [[Memancing di Musim Dingin]],
			['step'] = {
				['1'] = [[Gadis Salju memintaku memancing ikan untuk dihidangkan di pesta perayaan.]]
			}
		},
		['ice_brigands'] = {
			['text'] = [[Binatang-binatang melapor ada sekelompok penjarah es menyerbu wilayah sekitar. Para penjarah ini sangat berbahaya dan kamu harus menghentikan mereka.]],
			['name'] = [[Penjarah]],
			['step'] = {
				['1'] = [[Aku harus mengalahkan sekelompok penjarah es yang berbahaya.]]
			}
		},
		['forester'] = {
			['text'] = [[Seorang pria datang dan meninggalkan pesan untukmu. Seorang penebang kayu butuh bantuan. Sepertinya kerja mudah dengan hadiah lumayan. Menurutku ambil saja.]],
			['name'] = [[Penebang Kayu]],
			['step'] = {
				['1'] = [[Seseorang meninggalkan pesan pribadi untukku. Aku harus membantu seorang penebang kayu. Ia menjanjikan bayaran besar.]]
			}
		},
		['progress_tula'] = {
			['step'] = {
				['1'] = [[Tula Kremlin menyambut para pengembara dengan sederet senjata modern yang menonjol di antara krenelasi kuno tembok benteng. Mendapatkan kepercayaan dari mantan pekerja Pabrik Senjata tidak akan mudah.<new_line><new_line>Ada beberapa pihak yang mengincar stok senjata Tula yang kaya dengan rakus, terutama geng-geng yang mengoperasikan Ryazan. Namun, para Tulan yang ganas tidak mau ikut campur dengan bisnis dan aliansi orang lain, mereka merasa puas bisa berdagang dan menjaga tembok mereka.]]
			}
		},
		['new_year_boss_2022_4'] = {
			['step'] = {
				['1'] = [[Kita harus menyadarkan Cybersanta. Dia butuh air.]],
				['4'] = [[Sepertinya misinya sudah selesai. Aku harus mengejar Cybersanta dan mencari tahu apa yang dia temukan.]],
				['3'] = [[Aku terlibat dalam petualangan Cybersanta yang membingungkan. Sekarang aku perlu menghubungkan kabel di suatu tempat dan menunggu dia menelepon.]],
				['2'] = [[Cybersanta akan membantu kita mencari tahu tentang senjata super itu. Aku harus masuk ke tempat penyimpanan informasi itu.]]
			}
		},
		['progress_irkutsk'] = {
			['step'] = {
				['1'] = [[Terkadang keajaiban memang terjadi, dan salah satunya terjadi di Irkutsk. Baik Pembangkit Listrik Tenaga Air Angara maupun perairan Danau Baikal tidak terpengaruh oleh bencana tersebut. Orang-orang dari Irkutsk sekarang menjaga pembangkit listrik tenaga air ini seperti anak mereka sendiri. Mereka bahkan bermukim tepat di bendungan dan sekarang menyediakan listrik dan air bersih ke seluruh S.O.V.A.]]
			}
		},
		['progress_kurgan'] = {
			['step'] = {
				['1'] = [[Anehnya, aku sudah akrab dengan orang-orang Kurgan. Ternyata mereka yang menjalankan seluruh sistem kereta api. Jika ada orang Kurgan yang tidak naik kereta api, dia yang memperbaikinya. Jadi, jelas dengan siapa aku harus perselisihan.<new_line><new_line>Kereta lapis baja mereka kebal terhadap serangan bandit dan mutan, meskipun apa pun bisa terjadi di padang gurun. Oleh karena itu, penting juga bagi Kurgan untuk menjaga hubungan baik dengan mitranya, jika hanya untuk kepentingan layanan bahan bakar dan perawatan kereta apinya.]]
			}
		},
		['gigahive_ufa'] = {
			['name'] = [[Jenis Lebah yang Salah]],
			['step'] = {
				['1'] = [[Aku harus pergi ke Izhevsk dan bertanya kepada bartender tentang sarang lebah di Ufa.]],
				['3'] = [[Aku membunuh ratu lebah. Sekarang, saatnya membawa kepalanya ke bartender di Izhevsk dan mendapatkan imbalanku.]],
				['2'] = [[Ada sarang lebah di Ufa yang diperintah oleh ratu lebah. Untuk sampai ke sana, aku perlu penawarnya. Tapi aku harus membunuh beberapa induk lebah dan membawa lilinnya kembali ke spesialis di Izhevsk untuk mendapatkannya. Dan sebenarnya, bartender telah menjanjikanku imbalan untuk kepala ratu.]]
			}
		},
		['supply'] = {
			['text'] = [[Markas tetangga telah meminta beberapa persediaan kepada kita. Bisakah kau mengumpulkan semuanya dan membawanya?']],
			['name'] = [[Pengiriman]],
			['step'] = {
				['1'] = [[Aku telah dikirim untuk menemukan semua kebutuhan markas lainnya.]],
				['3'] = [[Kerja kerasku tidak sia-sia.]],
				['2'] = [[Aku telah menemukan semua yang aku butuhkan. Sekarang, aku hanya perlu mengantarkan kargo ke markas.]],
				['5'] = [[Aku tidak menerima imbalan apa pun. Aku kira barang ini bisa berguna.]],
				['4'] = [[Aku diberi imbalan untuk pekerjaanku, tetapi mereka bisa saja sedikit lebih murah hati.]]
			}
		},
		['herb_collect'] = {
			['text'] = [[Musim dingin ini berat bagi binatang-binatang, dan aku membantu mereka semampuku. Tolong kumpulkan herba penyembuh ini dan berikan padaku di tanah lapang.]],
			['name'] = [[Herba Penyembuh]],
			['step'] = {
				['1'] = [[Gadis Salju memintaku mengumpulkan herba untuk membantu binatang-binatang.]]
			}
		},
		['exp_recovery'] = {
			['name'] = [[Tidak sadar]],
			['step'] = {
				['1'] = [[Di ambang kematian, aku kehilangan kesadaran dan dengan adanya keajaiban, aku diselamatkan. Namun, ingatanku tentang peristiwa sebelumnya hilang. Untuk mengingatnya kembali, aku harus kembali ke tempat semua hal itu terjadi.]],
				['2'] = [[Ingatanku yang hilang sudah kembali.]]
			}
		},
		['progress_magnitogorsk'] = {
			['step'] = {
				['1'] = [[Selama pemboman Ural, di tambang Magnitogorsk itulah mereka yang saat ini memimpin Iron Front berlindung. Sekarang banyak orang bekerja keras di tambang itu, tetapi banyak dari mereka tidak melakukan pekerjaan yang melelahkan ini dengan sukarela.<new_line><new_line>Aku yakin aku cukup kuat untuk menempatkan diriku di sini sebagai mitra, dan bukan sebagai sumber tenaga kerja gratis.]]
			}
		},
		['new_year_boss_2022_1'] = {
			['step'] = {
				['1'] = [[Sinterklas baru muncul di sebelah pohon. Aku harus mencari tahu apa yang dia punya.]]
			}
		},
		['traveler_salvation'] = {
			['text'] = [[Seekor tupai berkata ada seseorang yang menggigil kedinginan di hutan. Cepat ke sana dan bantu dia! Kuharap dia masih hidup.]],
			['name'] = [[Menuju Penyelamatan]],
			['step'] = {
				['1'] = [[Di suatu tempat di dalam hutan, seseorang menggigil kedinginan. Aku harus bergegas menyelamatkannya.]],
				['3'] = [[Kondisi wisatawan telah membaik. Sekarang aku perlu untuk mendapatkan dia ke tempat yang aman.]],
				['2'] = [[Man sangat dingin dan kelelahan, ia membutuhkan bantuan.]]
			}
		}
