		['santa_bear'] = {
			['choice'] = {
				['1'] = [["Ini tradisi Natal."]],
				['3'] = [["Kita hanya akan mengambil hadiah darinya?"]],
				['2'] = [["Apa maksudmu sebenarnya?"]]
			},
			['episode'] = {
				['1'] = [["Halo kawan! Business, apa kabar kau rubah tua? Ooh ah, Cave!"]],
				['3'] = [["Lagi-lagi kau membahas tentang eksploitasi..."]],
				['2'] = [["Kau tahu apa yang salah dengan sistem sosialmu? Kau telah mengeksploitasi seluruh kompleks industri hari raya selama bertahun-tahun."]],
				['5'] = [["Hmm, jika kita memberi Krampus tawaran yang menarik... Ada taman hiburan tak jauh dari sini.<new_line>Aku kenal cukup baik dengan manajer di sana—aku memasok hadiah dan hiasan. Mungkin geng itu belum menemukan taman itu."]],
				['4'] = [["Aku tak tahu bagaimana keadaan di pabrikmu, tetapi di sini? Lihat akibatnya! Para pekerja tidak boleh diasingkan dari alat-alat produksi. Kita harus mendistribusikan kembali barang yang ada."]],
				['6'] = [["Business Claus sedang berurusan dengan manajer. Aku akan menghubungi geng. Dan tugasmu adalah 'mengurus' gudang hadiah di taman. Baiklah, ayo kita mulai operasinya sebelum Krampus Claus mendahului kita!"]]
			}
		},
		['infected_shop'] = {
			['episode'] = {
				['1'] = [[Dilihat dari luar gedung, serangga mutan jelas membuat diri mereka nyaman di toko tua. Mungkin ada sesuatu yang berharga di dalam, tapi ini akan jadi pekerjaan yang berbahaya.]]
			}
		},
		['camp_supply_quest'] = {
			['episode'] = {
				['9'] = [[Tidak ada satu jiwa pun yang tersisa di kamp. Aku terlambat.]],
				['10'] = [[Aku membalas dendam pada para bandit dan mempertahankan kamp. Tapi apa gunanya sekarang?]],
				['13'] = [[Pria yang terluka itu masih terbaring di sudut.]],
				['12'] = [[Aku tidak punya apa-apa,' kata pria itu dengan senyum sedih. 'Serahkan kargo atau enyahlah!']],
				['14'] = [[Sekutu masih berhasil mempertahankan kamp mereka.]],
				['1'] = [[Aku berangkat ke titik di peta. Di sana aku bertemu dengan seorang pria muram.]],
				['11'] = [[Sampah itu... Lihat! Semuanya sudah diserahkan.']],
				['3'] = [[Aku melihat gedung-gedung tua di mana aku seharusnya bertemu sekutu kami. Tiba-tiba, suara tembakan terdengar. Mereka diserang!]],
				['2'] = [[Pria itu menghela napas lega.<new_line>'Terima kasih, kau sangat membantu.']],
				['5'] = [[Pria itu menerima paketnya dengan rasa terima kasih.<new_line>Aku melihat seorang pria terluka di sudut ruangan.]],
				['4'] = [[Entah bagaimana, kita tetap melawan mereka. Sekutuku menatapku dengan terkejut.<new_line>'Aku harus berterima kasih atas bantuanmu, tapi... Siapa kau sebenarnya?']],
				['7'] = [[Sepi yang mencurigakan di dekat kamp.<new_line>Ketika aku makin dekat, aku melihat beberapa bandit berandalan sedang bertugas. Mayat sekutu kita jatuh berserakan di tanah.]],
				['6'] = [[Aku sudah mengobati lukanya. Dia akan hidup.]]
			},
			['choice'] = {
				['11'] = [[Ambil kembali paketnya]],
				['8'] = [[Mencari balas dendam]],
				['13'] = [[Serahkan paketnya]],
				['12'] = [[Ada paket untukmu, tapi kau harus membayar']],
				['1'] = [[Apakah seseorang memesan persediaan?']],
				['5'] = [[Bantuan]],
				['4'] = [[Beberapa teman mengirimkanmu persediaan']],
				['14'] = [[Cari bantuan]],
				['9'] = [[Menunggu bandit untuk pergi]]
