		['pumpkin_seed'] = {
			['episode'] = {
				['1'] = [[Aku menanam biji tersebut dan menyiramnya. Dalam hitungan detik, tumbuh kecambah, yang segera berubah menjadi sulur tebal. Tak lama, ia mengeluarkan sebuah labu kecil. Dari labu ini tumbuh mata, gigi, dan tangan kecil. Satu menit kemudian, si labu terlepas dari sulur dan memegang sepatu botku. Aku tak yakin apa yang sedang terjadi, tetapi sudah saatnya aku menghajar labu ini.]],
				['2'] = [[Sepertinya telah muncul musuh yang sepadan untukku di dunia ini. Pertempuran kami akan melegenda! Sampai jumpa, manusia, labu raksasa tersebut mengaum. Melupakanku, ia berjalan pergi, menyebabkan getaran di setiap langkahnya. Kini saatnya aku kembali ke kedai.]]
			}
		},
		['handcar'] = {
			['episode'] = {
				['16'] = [[Beberapa jam kemudian, stasiun kecil tempat rel kereta itu bercabang muncul dari kabut. Cabang rel itu akan membawa handcar ke jalan buntu, tetapi tuasnya benar-benar berkarat.]],
				['10'] = [[Dengan panik, aku mengerahkan seluruh tenaga untuk menggerakkan tuas, tetapi segera aku merasakan napas buas landak itu di leherku. Itu adalah sensasi terakhir sebelum kegelapan menelan segalanya di sekitarku.]],
				['14'] = [[Aku melompat dan jatuh ke tanggul. Dengan satu pukulan, landak itu menjatuhkan handcar dari rel. Hal terakhir yang aku lihat adalah bongkahan logam berat menuju ke arahku.]],
				['12'] = [[Aku menembak landak itu tepat di wajah beberapa kali, tetapi makhluk raksasa itu tidak peduli. Ia membuka mulutnya lebih lebar dan melompat ke depan, melahap aku dan sepotong handcar.]],
				['22'] = [[Aku berteriak dan mengayunkan oborku ke atas lynx itu. Ketakutan, hewan itu melompat pergi dan menghilang dalam kabut. Sekarang aku bisa pergi dari sini.]],
				['32'] = [[Serigala lain dalam kawanan itu ikut melolong. Aku berlari menuju handcar dan memompa tuas dengan kecepatan penuh, mencoba untuk menghentikan diriku dari menengok ke belakang.]],
				['20'] = [[Aku menemukan kaleng minyak setengah kosong tanpa banyak mencari. Aku kembali ke handcar, meminyaki tuas pengatur rel kereta, dan mengalihkan trek. Sekarang aku bisa melanjutkan perjalanan... atau tetap di sini dan mencari.]],
				['30'] = [[Saat aku meninggalkan gerbong kereta, aku melihat rangka manusia di rumput. Binatang liar telah melakukan pekerjaannya pada orang yang selamat dari kecelakaan kereta itu.]],
				['1'] = [[Setelah menempuh beberapa kilometer dengan handcar, aku melihat sungai kecil di kaki tanggul. Sungai itu dikelilingi padang rumput yang tertutup lebat oleh herba liar dan semak stroberi tumbuh tepat di pinggir aliran air. Pemandangan yang murni dan tenang.]],
				['3'] = [[Setelah memeriksa padang rumput itu, aku memetik beberapa daun otot-ototan. Namun, selagi aku sibuk dengan itu, langit berubah menjadi gelap sepenuhnya. Malam akan segera tiba di sini.]],
				['2'] = [[Aku makan stroberi hingga kenyang dan bahkan memetik beberapa untuk perjalanan. Namun begitu, pada saat aku selesai, matahari sudah hampir menghilang di balik horizon. Sebentar lagi akan gelap.]],
				['5'] = [[Aku menghentikan handcar. Tentu aku tidak ingin tertidur dan jatuh dari rel sementara aku mengendarai handcar. Aku membuat api unggun di kaki tanggul dan bersiap untuk tidur.]],
				['4'] = [[Malam pun tiba. Bumi diselubungi kabut, dan setelah beberapa kilometer, kabut menelan dunia di sekitarku sepenuhnya. Aku lelah dan mulai jatuh tertidur.]],
				['7'] = [[Meskipun kelopak mataku mulai lelah, aku terus berjalan. Tiba-tiba aku mendengar bunyi letupan kecil yang semakin keras di belakangkau, seakan-akan ada mesin uap mendekat. Melalui kabut, sekilas aku melihat siluet sesuatu yang besar...]],
				['6'] = [[Pada pagi harinya, kabut masih membentuk dinding di sekitar kemah kecilku. Biar pun begitu, aku senang setidaknya aku bisa beristirahat, dan aku melanjutkan perjalananku dengan semangat yang baru.]],
				['9'] = [[Landak itu mendekat dengan cepat. Tapak kakinya menekuk rel seperti kertas. Dalam hitungan detik, aku akan dihabisi.]],
				['8'] = [[Dari balik kabut muncul landak seukuran truk. Matanya menyala seperti dua proyektor, dan uap menyembur dari mulutnya yang terbuka. Auman yang memekakkan telinga hampir menjatuhkan aku dari handcar.]],
				['28'] = [[Tak ada yang berguna di dalam gerbong yang terbalik kecuali rongsokan logam.]],
				['38'] = [[Sepertinya radio yang aneh dan punya kabel yang keluar darinya dengan tabung logam pendek di ujungnya. Dalam kotak kulit itu, aku menemukan buklet petunjuk untuk Dosimeter-Radiometer DP-5V.]],
				['18'] = [[Aku mencoba hati-hati, tetapi tetap saja tubuhku tergores besi dan menyobek celanaku.]],
				['11'] = [[Aku membuka mata. Dinding kabut menyelimuti aku. Satu-satunya bunyi dalam keheningan itu adalah napasku yang berat. Aku pasti jatuh tertidur sambil memegang tuas handcar. Dan makhluk gila apa yang aku lihat dalam mimpiku itu?!]],
				['17'] = [[Platform beton dan bangunan bobrok. Sisa-sisa terali mencuat di luar jendela yang rusak, dan bekas cakaran yang dalam menggores pintu yang terkunci.]],
				['13'] = [[Aku membuka mata dan menemukan diriku terbaring di atas handcar. Aku pasti telah tertidur. Selama mimpi buruk itu, aku juga telah melempar pistolku ke suatu tempat. Setelah beberapa lama mencari, akhirnya aku menemukannya sedikit jauh di bawah rel.]],
				['15'] = [[Aku terbangun di kaki tanggul. Aku pasti telah tertidur dan jatuh dari handcar. Ada benjolan dan memar di seluruh tubuhku. Mimpi gila itu hampir mematahkan leherku.]],
				['33'] = [[Aku sudah dekat ke Murmansk ketika melihat sesuatu di jalur di depanku. Aku menghentikan handcar dan berjalan mendekat. Itu adalah mayat.]],
				['23'] = [[Aku menarik keluar pistolku dan menembak lynx itu. Peluru mengenai handcar, menyebabkan percikan. Ketakutan, kucing itu melompat pergi dan menghilang dalam kabut. Sekarang aku bisa pergi dari sini.]],
				['31'] = [[Jejak tulang belulang membawa ke hutan. Dari suatu tempat di antara pepohonan, lolongan serigala tiba-tiba memecah kesunyian. Lebih baik aku pergi dari sini!]],
				['21'] = [[Setelah mencari, yang kutemukan hanyalah tali dan sebotol vodka yang telah disimpan. Aku kembali ke handcar hanya untuk menemukan seekor lynx besar sedang mengendap-endap di sekitarnya. Ketika melihat aku, ia mengangkat punggungnya dan mendesis.]],
				['43'] = [[Semakin lama aku tinggal di dekat mayat itu, semakin tinggi aku akan terpapar radiasi. Lebih baik aku kembali ke handcar sesegera mungkin.]],
				['42'] = [[Saat aku hendak pergi, aku melihat secarik kecil kertas mencuat dari saku tasnya. Ada alamat sebuah rumah di kota Kola tertulis di atasnya. Ada apa di sana? Pejuang Hidup? Atau suplai?]],
				['41'] = [[Aku mungkin terpapar radiasi, namun setidaknya binatang liar tidak akan memakan mayatnya. Aku mengambil beberapa batu dan membangun gundukan makam kecil di atasnya.]],
				['40'] = [[Aku menarik mayat itu ke bawah ke kaki tanggul. Sekarang aku tahu apa yang mungkin membunuh pria malang itu: penyakit radiasi telah melahapnya dari dalam.]],
				['24'] = [[Botol vodka yang susah payah kutemukan pecah di depan lynx itu. Ketakutan, hewan itu melompat pergi dan menghilang dalam kabut. Sekarang aku bisa pergi dari sini.]],
				['37'] = [[Ada beberapa perban di dalam tas, demikian juga perangkat kecil dalam kotak kulit dengan tali pengikat.]],
				['36'] = [[Melalui masker gas, sekilas aku melihat wajah kurus pria itu yang belum mulai membusuk.]],
				['35'] = [[Pembicaraan dengan AS Memburuk...,]],
				['34'] = [[Tubuhnya diselimuti mantel kotor, masker gas tanpa filer, dan tas bepergian di pundaknya. Dalam saku mantel itu, aku menemukan surat kabar yang digulung menjadi kerucut.]],
				['26'] = [[Bagian depan lokomotif telah terkubur ke dalam tanah di kaki tanggul. Gerbong di belakangnya telah ringsek menjadi bongkahan logam, tetapi beberapa gerbong penumpang masih ada di atas rel.]],
				['25'] = [[Kabut menghilang, dan rawa-rawa berganti menjadi daerah berhutan. Dari kedalaman hutan muncul rel lain berukuran sempit, paralel dengan rel tempatku berjalan. Sedikit lebih jauh, aku melihat kereta yang tergelincir dari rel.]],
				['39'] = [[Ketika aku menyalakan saklarnya, dosimeter itu mulai berdengung. Aku melambaikan tabung sensor, dan ketika aku lambaikan ke atas mayat itu, dengungannya meningkat. Mayat itu sangat radioaktif!]],
				['29'] = [[Dalam gerbong kereta, aku menemukan beberapa kopor yang telah dijarah, satu pak perban, dan... banyak noda darah. Namun, ke mana orang-orangnya?]],
				['19'] = [[Beberapa ayunan kapak menyelesaikan masalah pintu yang terkunci. Aku masuk.]],
				['27'] = [[Lokomotif itu kosong, tetapi aku menemukan paket kecil dengan segenggam selongsong peluru dalam bilik kondektur. Namun, tak ada pistol yang aku temukan.]]
			},
