		['new_year_boss_5_3'] = {
			['choice'] = {
				['1'] = [["Kau lagi?!"]],
				['4'] = [["Apa yang akan kita lakukan sekarang?"]],
				['3'] = [[Berdiri dan kejar Frosty]],
				['2'] = [[Kejar Frosty]]
			},
			['episode'] = {
				['1'] = [[Kami sampai di tangga es yang mengarah ke bawah tanah. Seluruh tempat ini dipenuhi ornamen Natal yang hangus dan kotak kado yang robek.]],
				['3'] = [["Dasar gerombolan Santa bodoh! Krampus otak udang! Mesin itu—kau menghancurkannya! Yah, tidak masalah, ini belum berakhir. Mari kita lihat apa yang akan kau lakukan tanpa sihirmu!"]],
				['2'] = [["Oh, lihatlah siapa yang datang berkunjung! Apa kalian bawa hadiah? Ha ha! Tidak? Jangan khawatir! Aku sudah lama ingin melihat bagaimana senjataku menyedot sihir dari makhluk hidup!"]],
				['5'] = [[Pusaran angin bersalju menghantamku. Entah munculnya dari mana.]],
				['4'] = [["Kau akan bertemu denganku lagi. Dalam mimpi burukmu!"<new_line><new_line>Frosty melemparkan Krampus ke atas bahunya, menarik semacam bagian berkilau dari pecahan senjata, dan berlari menaiki tangga ke luar.]],
				['7'] = [["Keluar dari sini sebelum kita tertimbun salju! Aku telah menerima konsekuensi karena berurusan denganmu..."]],
				['6'] = [[Sekali lagi, dinding salju menjatuhkanku ke belakang!<new_line><new_line>"Percuma saja. Frosty itu pintar. Dan kini dia punya komponen senjata super yang mengisap sihir kita."]]
			}
		},
		['snowmen_fortress'] = {
			['episode'] = {
				['1'] = [[Benteng buatan boneka salju ini ternyata terbuat dari susunan balok-balok es yang cukup besar. Para penjaga di menara sudah menatapku dengan curiga dari bawah ember mereka, membentuk gumpalan salju bergantian dari tangan satu ke tangan lainnya.]]
			}
		},
		['halloween_fight_easy'] = {
			['episode'] = {
				['1'] = [[Lolongan mengerikan dari binatang buas aneh yang mengamuk di daerah itu bisa terdengar hingga bermil-mil jauhnya. Suara ini tentu saja menimbulkan ancaman bagi orang-orang di sini. Aku bisa mendapat imbalan tinggi untuk mengalahkan mereka.]],
				['2'] = [[Aku sudah sangat dekat dengan gerombolan binatang buas itu. Sekarang aku bisa menyerang.]]
			}
		},
		['order_delivery_quest_pt2'] = {
			['episode'] = {
				['1'] = [[Jadi, bagaimana semuanya?']],
				['3'] = [[Bartender itu menggelengkan kepalanya.<new_line>'Maaf, itu terlalu mahal untukku. Kau bisa menyimpannya.']],
				['2'] = [[Kenapa bersedih? Ada yang salah dengan kargonya?']],
				['5'] = [[Peti itu diisi dengan beberapa persediaan. Itu jelas tidak sepadan dengan uangnya, tetapi itu lebih baik daripada dibiarkan dengan tangan kosong.]],
				['4'] = [[Bartender itu menghela napas berat.<new_line>'Pedagang sialan itu! Baiklah, itu bukan salahmu. Aku akan membayarmu kembali untuk itu. Ini.']]
