		['new_year_boss_4_3'] = {
			['choice'] = {
				['11'] = [[Tekan tombol E9]],
				['10'] = [[Tekan tombol 1C]],
				['13'] = [["Cybersanta, jalur sudah aman."]],
				['12'] = [[Panggil Cybersanta]],
				['1'] = [[Ledakkan pintu]],
				['3'] = [[Pergi ke meja]],
				['2'] = [[Pergi ke pintu]],
				['5'] = [[Makan permen biru]],
				['4'] = [[Pergi ke laci]],
				['7'] = [[Lihat ke bawah meja]],
				['6'] = [[Makan permen merah]],
				['9'] = [[Tekan tombol 55]],
				['8'] = [[Tekan tombol 7A]],
				['16'] = [[Sambungkan kabel]],
				['17'] = [[Angkat telepon]],
				['14'] = [["Aku tak terlalu..."]],
				['15'] = [[Bawa kabel ke kamar]],
				['18'] = [[Lari mengejar Cybersanta]]
			},
			['episode'] = {
				['16'] = [[Kamar itu berukuran kecil dan remang-remang. Ada kotak logam di atas meja, dan terlihat telepon di atas meja rias yang gelap.]],
				['10'] = [["Kau masih belum bisa meretas sampah ini? Lihat di bawah meja—bocah-bocah bodoh itu menuliskan kode tepat di bawahnya."]],
				['13'] = [[Kunci di pintu berbunyi klik. Aku berhasil!]],
				['12'] = [[Terdengar bunyi klik merdu dari dinding, tetapi pintunya tidak mau bergerak.]],
				['1'] = [[Aku sudah sampai. Bangunan ini terbuat dari es. Pintunya sangat halus dan kuat. Bahkan, tidak ada celah antara pintu dan kosennya. Aku tak bisa masuk tanpa bahan peledak.]],
				['3'] = [["Kau masuk dari pintu ke ruang kendali, sementara aku menyiapkan sesuatu untuk peretasan utamanya."]],
				['2'] = [[Sebelum aku bisa memasang bahan peledak, pintunya terbuka.<new_line>"Peretas macam apa yang masuk dari pintu depan? Aku mendobrak pintu belakang mereka dan masuk dari tadi. Ayo, ada banyak hal yang harus kita lakukan."]],
				['5'] = [[Geng itu menyimpan senjata mereka di sini! Temuan yang bagus.]],
				['4'] = [[Selain dinding es, ruangan itu tampak biasa saja dengan beberapa perabot dan pintu lain di seberang pintu masuk.<new_line>Cybersanta telah menghubungkan beberapa kotak dengan tombol ke pemutus sirkuit di dinding dan mengutak-atiknya.]],
				['7'] = [[Di atas meja ada kertas dengan beberapa diagram aneh dan toples. Di bagian bawah toples ada dua permen tersisa: biru dan merah.]],
				['6'] = [[Lacinya kosong.]],
				['9'] = [[Di samping pintu ada panel dengan tombol. Di atas panel ada instruksi.<new_line>"Masukkan urutan yang benar."]],
				['8'] = [[Ada kode yang tertulis di bawah meja:<new_line>"E9, 7A, 1C, 55"]],
				['15'] = [["Dengar, choom, aku suka mengobrol, tapi geng itu bisa muncul kapan saja dan membasmi kita sebelum kau bisa berkedip. Pergi sekarang!"]],
				['17'] = [[Telepon berdering.]],
				['11'] = [[Setelah terdengar suara melengking, tombol kembali ke posisi semula.]],
				['14'] = [["Sempurna! Sekarang ambil kabel ini, tarik ke ruangan sebelah, dan sambungkan ke... kotak logam.<new_line>Ada telepon juga di sana. Aku akan meneleponnya jika semuanya sudah siap. Angkat. Itu saja."]],
				['18'] = [["Selamat datang di dunia nyata!<new_line>Aku sudah mengunduh semua infonya. Ayo pergi. Ayo, choom, cepat! Ikuti aku!"]]
			}
		},
		['comrade_in_trouble'] = {
			['episode'] = {
				['16'] = [[Si bandit bersenandung, mengambil uang, dan membawa anak buahnya pergi. Seorang pria yang bahagia memanjat keluar dari jendela.<new_line>"Wow, Kawan. Wow, kau menyelamatkanku! Kukira hidupku sudah berakhir, aku akan kehabisan peluru dan tamatlah aku."]],
				['10'] = [[Sebuah kamp didirikan di samping kendaraan. Ada seorang pria di sana, membongkar atau memuat segala macam barang.<new_line>"Apa, orang di rumah sudah merindukanku? Aku membawa setumpuk barang jarahan dan aku lupa memeriksa bensin—bahan bakarku habis di tengah jalan."]],
				['13'] = [[Beberapa pria telah mengambil posisi di sisi lain rumah. Senjata mereka dilatih di bangunan itu. Tampaknya pengintai kita sedang dikepung di dalam.]],
				['12'] = [[Sebuah kamp telah didirikan di reruntuhan bangunan satu lantai itu. Tidak ada seorang pun yang terlihat, tetapi pintu masuk ke rumah terhalang oleh tumpukan batu bata dan puing-puing. Tembakan dan sumpah serapah bisa terdengar dari sisi lain.]],
				['22'] = [[Mereka terlambat mengirimku. Pengintai itu sudah mati. Dilihat dari luka di tubuhnya, dia diserang oleh semacam pemangsa besar.]],
				['23'] = [[Aku memberi pria itu obat radiasi dan kondisinya akan segera membaik.]],
				['20'] = [[Ada seorang pria duduk di dekat api unggun. Kondisinya tak terlihat terlalu baik: ada rasa sedih di hatinya dan celananya basah berlumur darah.]],
				['21'] = [[Di dekat api unggun di kamp, ​​seorang pria mengaduk air dalam panci. Dia tersenyum padaku:<new_line>"Aaah, apakah orang di rumah mengkhawatirkanku? Tak ada yang perlu dikhawatirkan. Beri tahu mereka aku akan segera kembali. Ini ekspedisi yang sukses dan masih ada yang harus kuurus."]],
				['1'] = [[Seorang pria keluar dari kamp untuk menemuiku:<new_line>"Apa kamu salah satu dari kami? Jangan berisik, atau semua orang akan berlari ke sini. Katakan pada markas aku akan menetap di sini selama satu atau dua hari lagi—ternyata ini pekerjaan yang sulit."]],
				['3'] = [[Saat mendekati kamp, aku mendengar dengkuran berderak. Di sebelah bara api unggun, aku menemukan seorang pria yang sedang tidur, dengan hangat memeluk sebuah botol kosong.<new_line>Tidak sulit untuk mengetahui penyebab penundaan itu. Mungkin aku harus menambah sedikit kayu ke api unggun? Lagi pula, malam ini cukup dingin.]],
				['2'] = [[Di kamp, ​​ada seorang pria duduk di kursi lipat di depan kuda-kuda. Dia terkejut saat melihatku, tetapi kemudian tersenyum bersalah dan merentangkan tangannya.]],
				['5'] = [[Kamp itu kosong, tetapi tidak ada tanda-tanda perlawanan atau mundur dengan tergesa-gesa. Ada benda logam bengkok tergeletak di samping api unggun.]],
				['4'] = [[Sekarang pengintai heroik kita tidak akan mati kedinginan.]],
				['7'] = [[Menjauh beberapa meter, aku mendengar tangisan lemah. Seorang pria berkamuflase sedang merangkak menuju kamp. Saat dia melihatku, dia berkata dengan serak:<new_line>"Aku mendengar suara di kamp... Apakah kamu menyentuh perangkat keras itu? Ini berdengung sangat kencang! Aku bodoh dan meninggalkan meteran di ranselku, lalu pergi begitu saja ke kamp. Aku sudah terpapar dan sulit bernapas."]],
				['6'] = [[Item itu rusak, tampaknya karena ledakan. Tunggu, itu bagian dari misil militer! Meteranku jadi rusak—benda sialan ini radioaktif!]],
				['9'] = [[Pengintai itu turun dan mengulurkan tangannya yang kuat:<new_line>"Terima kasih! Aku benar-benar mengira akan jatuh lalu mati. Mereka menangkapku tanpa senjata. Yang bisa kupikirkan hanyalah memanjat lebih tinggi."]],
				['8'] = [[Ada suara-suara aneh yang datang dari sekitar perkemahan. Mengikuti suara ini, aku melihat bahwa para pemangsa sudah mengepung tiang berkarat, dan pengintai yang malang itu duduk di atasnya.]],
				['25'] = [[Untung aku datang tepat waktu untuk membantunya, karena dia bisa saja kehilangan banyak darah. Namun sekarang hidupnya sudah tidak dalam bahaya.]],
				['24'] = [[Aku berbagi bahan bakarku dengan pria itu. Seharusnya lebih dari cukup untuk membawanya kembali ke permukiman.]],
				['14'] = [[Itu benar-benar membuat para bandit terkejut, tetapi salah satu dari mereka tersadar dengan cepat:<new_line>"Hei, kamu di sana! Tenanglah! Apa dia temanmu yang terkurung di reruntuhan itu?<new_line>Jangan melakukan hal bodoh—beri kami sedikit uang dan kalian berdua bisa pergi dari sini dalam keadaan utuh."]],
				['15'] = [[Seorang pria yang bahagia memanjat keluar dari jendela.<new_line>"Wow, Kawan. Wow, kau menyelamatkanku! Kukira hidupku sudah berakhir, aku akan kehabisan peluru dan tamatlah aku."]],
				['17'] = [[Sebuah kamp telah didirikan di reruntuhan bangunan satu lantai itu. Tidak ada seorang pun yang terlihat, tetapi pintu masuk ke rumah terhalang oleh tumpukan batu bata dan puing-puing.]],
				['11'] = [[Ada peti dan kotak tersebar di seluruh kamp. Pengintai itu berusaha mengamankan semuanya ke sepeda motor tua.<new_line>"Lihat, tangkapan yang luar biasa, tapi aku tidak bisa membawa semuanya. Apa, orang di rumah sudah merindukanku? Baiklah, aku sedang dalam perjalanan.<new_line>Namun, peti besar itu tidak akan muat. Bantu aku dan ambil ini, ya?"]],
				['19'] = [[Seorang pria merangkak dengan gembira menuju kebebasan, mendorong karung di depannya.<new_line>"Aku mencoba meledakkan brankas baja di sana," kata pengintai itu sambil mengangkat bahu. "Begitulah caraku memberi penghalang. Terima kasih!"]],
				['18'] = [[Di sisi lain rumah ini aku menemukan jendela berjeruji. Begitu aku mendekatinya, terdengar suara teriakan dari dalam:<new_line>"Hei, hei! Aku di sini, terkurung! Bantu aku keluar dan orang-orang dari permukiman akan membayarmu."]]
			},
			['choice'] = {
				['9'] = [[Berbagi bensin]],
				['8'] = [[Bertempur!]],
				['13'] = [[Bersihkan puing-puing di pintu masuk]],
				['12'] = [[Gergaji jeruji jendela]],
				['16'] = [[Ambil petinya]],
				['15'] = [[Bawa jenazah itu kembali ke permukiman]],
				['14'] = [[Berikan pertolongan pertama]],
				['11'] = [[Cari para bandit dan lawan mereka]],
				['1'] = [[Kembali ke permukiman dengan berita]],
				['10'] = [[Periksa rumah itu]],
				['3'] = [[Periksa barang itu]],
				['2'] = [[Tambahkan kayu ke api unggun]],
				['5'] = [[Menjauh dari proyektil]],
				['4'] = [[Cari pengintai itu di sekitar]],
				['7'] = [[Cari bantuan]],
				['6'] = [[Berikan obat radiasi]]
			}
		},
		['new_year_boss_3_2'] = {
			['choice'] = {
				['1'] = [["Dan siapa dia?"]],
				['3'] = [["Apa yang mereka katakan tentang senjata?"]],
				['2'] = [["Sudah cukup—mampuslah kau!"]],
				['5'] = [["Siapa?"]],
				['4'] = [["Mereka ingin mengujinya."]],
				['7'] = [["Baiklah."]],
				['6'] = [["Maksudmu siapa?!"]]
			},
			['episode'] = {
				['9'] = [["Oh tidak. Jangan bilang si tengik sombong itu! Apa tak bisa kita melakukannya tanpa dia?"]],
				['8'] = [["Ya, aku telah gagal. Maaf, kawan. Kabar tentang senjata memang terdengar menakutkan. Seharusnya kita mencari tahu apa yang mereka bicarakan. Kita butuh mata-mata."]],
				['1'] = [["Krampus Claus, aku paham bahwa kau bekerja secara gratis dan tidak mendapatkan hasil kerjamu. Kami mengusulkan distribusi yang lebih adil untuk..."]],
				['10'] = [["Menyelamatkan hadiah-hadiah itu saja kita tidak bisa! Tidak, kita tak bisa melakukannya tanpa dia.<new_line>Jangan tanya, kawan—lebih mudah jika menemuinya. Kau perlu mendekorasi pohon lagi untuk memanggil pengunjung lain."]],
				['3'] = [["Wow, Krampus, mereka membawa begitu banyak barang. Bahkan cukup untuk mencoba senjata super kita."]],
				['2'] = [["Dasar idiot berbulu! Bagaimana bisa ini untuk distribusi? Orang-orangku butuh lebih banyak hadiah! Semua hadiah, semua energi mereka! Kami akan membakar Natal sepenuhnya untuk mendapatkan lebih banyak kekuatan!"]],
				['5'] = [["Ha, kau lagi? Hei, geng, urus pecundang ini! Kalian boleh membakar dan melahap setengah dari hadiah. Lemparkan sisanya di bunker. Aku akan kembali untuk memeriksa!"<new_line><new_line>Krampus Claus mendudukkan manusia salju aneh di atas sepeda motornya, naik, dan menginjak gas. Gengnya bergegas ke arahku.]],
				['4'] = [[Santa Claws mengangkat tangannya dan berjalan ke arah Krampus.<new_line>"Tunggu, tunggu, bukan itu yang kita setujui! Ayo bahas bagaimana..."<new_line><new_line>Terdengar letusan keras, sesuatu mengenai kepala Santa Claws, dan dia jatuh ke tanah.]],
				['7'] = [["Benar! Claws, kau baik-baik saja? Apa kau mendengar sesuatu? Jika mereka mendapatkan senjata super juga... tak akan ada lagi hari raya, begitu juga kita."]],
				['6'] = [["Saat kau berkelahi, beberapa bajingan mengambil semua hadiah! Tidak ada yang bisa aku lakukan."]]
			}
		},
		['emba_intro_bunker'] = {
			['episode'] = {
				['1'] = [[Pintu bungker tua terbuka sedikit, dan terdengar suara berderit dan berderak yang tidak menyenangkan dari bawah. Sepertinya ada sesuatu yang berbahaya di sana.]],
				['2'] = [[Aku berhasil menyingkirkan biomutan dari lab bawah tanah dan menemukan file bertanda "Rahasia". Aku harus kembali ke Navigator.]]
			},
			['choice'] = {
				['1'] = [[Kembali ke Navigator]]
			}
		},
		['pumpkin_king'] = {
			['choice'] = {
				['1'] = [[Terus hancurkan labu]]
			},
			['episode'] = {
				['1'] = [[Aku melakukan semua seperti petunjuk, berdiri di tengah lingkaran, dan mulai menghancurkan labu. Satu demi satu, labu-labu itu pecah, dan isinya tumpah memenuhi tanah. Setelah bebarapa detik, aku mendengar suara dan, dari sudut mataku, aku melihat beberapa Jack-O'-Lantern dari lingkaran itu melayang ke udara sembari badan-badan terbentuk di bawahnya keluar dari kabut.]],
				['3'] = [[Setelah aku menang, aku rasa aku mendengar teriakan terkejut dan marah. Sepertinya aku berhasil mendapatkan perhatian dari Jack. Aku harus melanjutkan.]],
				['2'] = [[Minion raja telah dikalahkan, tetapi rajanya masih mengawasiku. Aku kembali menghancurkan labuku, dan antek yang baru muncul seketika.]],
				['5'] = [[Aku menghancurkan beberapa labu lagi, dan segera lingkaran itu menyala dengan dua nyala api yang besar. Nyala api ini, yang terbakar di depan Raja Labu yang tinggi dan agung, tampak menghipnosis. Aku bersiap untuk bertarung.]],
				['4'] = [[Setelah memadamkan nyala jingga di kepala labu terakhir, aku menghapus remahan labu di wajahku. Spawn labu bergeletakan di sekitarku dengan biji-biji dan cairan mereka menutupi tanah, senjataku, dan pakaianku.]]
			}
		},
		['emba_commander_easy'] = {
			['episode'] = {
				['1'] = [["Halo, kamerad! Kau lihat biomutan itu? Kami telah membersihkan mereka dengan larutan khusus dan perilaku mereka dalam pertempuran semestinya berbeda.<new_line>Kami perlu seseorang yang akan menahan serangan mereka selagi kami melakukan observasi."]],
				['3'] = [["Bagus, bagus. Kita harus mengubah radikal bebasnya ... dan meningkatkan difusi di sekitar biomutan itu ... <new_line>Ah, kau masih di sini! Terima kasih atas bantuanmu. Datanglah lagi dan kita akan mencoba formula baru."]],
				['2'] = [["Bagus! Tentu saja kami tak akan menjadikanmu umpan. Kau hanya perlu berdiri di jarak aman dan mengirimkan sinyal ke turet selagi kami melakukan observasi.<new_line>Kau berpengalaman, jadi kau akan terbiasa dengan kontrolnya. Mari mulai!"]]
			},
			['choice'] = {
				['1'] = [["Aku siap membantumu."]],
				['2'] = [["Aku tidak siap melakukan ini."]]
			}
		},
		['new_year_boss_2_3'] = {
			['choice'] = {
				['1'] = [["Dia bicara omong kosong lagi..."]],
				['3'] = [["Bisa tidak kau berhenti melakukannya?!"]],
				['2'] = [[Minum semua]],
				['5'] = [["Siapa?"]],
				['4'] = [[Beri tahu dia tentang Krampus Claus]]
			},
			['episode'] = {
				['1'] = [["Ooh ah. Agh ooh!"]],
				['3'] = [[Aku tertipu lagi! Segera setelah aku memiringkan kepala ke belakang untuk meminum ramuan itu, Cave Santa memukulku dengan tongkatnya lagi!]],
				['2'] = [["Kau benar. Lebih baik jika kau bisa memahami perkataan orang lain daripada perkataanku saja. Minum ramuan ini—itu akan membantu."<new_line><new_line>Cave Santa memberiku cangkir kayu. Aku meminumnya dengan hati-hati. Lumayan enak!]],
				['5'] = [["Aku tidak percaya. Sebenarnya apa yang terjadi sampai Santa berubah jadi seperti itu... Tapi, apa pun itu, kita perlu bala bantuan untuk menghadapi geng berbahaya itu. Aku tahu seseorang yang cukup kuat."]],
				['4'] = [["Akhirnya, kau berbicara dengan normal! Apa yang terjadi?"]],
				['6'] = [["Sudah lama aku tak mendengar kabar darinya... Dia sibuk dengan masalah pemerintahan di dunianya.<new_line>Tapi dia pasti memiliki pengalaman tempur. Sudah diputuskan! Hiasi pohon lagi untuk membuka portal untuknya."]]
			}
		},
		['letter_of_hope'] = {
