		['lair4'] = {
			['episode'] = {
				['1'] = [[Saat berjalan menuju sarang, aku merasakan mata-mata lapar mengintaiku dan suara geraman dari semua sisi. Saat aku memanjat turun dari pohon yang jatuh, si manusia serigala memanfaatkan kesempatan dan menyerangku. ]]
			}
		},
		['halloween_fight_medium'] = {
			['episode'] = {
				['1'] = [[Ada terlalu banyak binatang buas yang berkumpul di sini. Aku tidak bisa melawan seluruh gerombolan sekaligus. Aku harus membuat mereka berpencar dan mengalahkan mereka dalam kelompok-kelompok yang lebih kecil. Pecahkan dan taklukkan, kalau kata orang.]],
				['4'] = [["Oh, kau lagi, Nak? Kau tidak bisa mengerjakan apa pun sendiri? Saat aku seusiamu, aku sudah melakukan semuanya sendiri. Baiklah. Tunjukkan siapa yang sudah jahat padamu."]],
				['3'] = [[Aku mengalahkan binatang buas tercepat dari kelompok itu, tapi yang lain masih banyak. Lolongan baru sudah terdengar. Binatang buas lainnya mendekat.]],
				['2'] = [[Aku menarik perhatian gerombolan itu dengan teriakan keras, kemudian mulai berlari. Rencananya berhasil: hanya sebagian dari binatang buas itu yang berhasil mengejarku. Aku harus mengalahkan mereka secepat mungkin, sebelum yang lain menyusul.]]
			},
			['choice'] = {
				['1'] = [[Berteriak untuk menarik perhatian]],
				['3'] = [[Panggil penyihir]]
			}
		},
		['santa_wizard'] = {
			['choice'] = {
				['1'] = [["Jika kita menyerangnya, sihir kita akan hilang."]],
				['3'] = [["Pengaman jika terjadi kelebihan muatan?"]],
				['2'] = [["Kau yakin?"]]
			},
			['episode'] = {
				['1'] = [["Apa yang terjadi di sini? Tak bisa mengurusnya tanpaku, ya?"]],
				['3'] = [["Betul sekali. Tapi benar juga bahwa serangan langsung adalah hal yang paling tidak diduga oleh musuh. Jika kita semua menyerang sekaligus, senjata super ini tak akan mampu menahan kekuatan yang diserapnya dan akan meledak."]],
				['2'] = [["Senjata ini adalah penyedot debu ajaib yang konyol! Makin aku mempelajarinya, makin aku tak tahu apa yang harus dilakukan."]],
				['5'] = [["Tepat! Jika kita menghancurkannya lebih dahulu, maka senjata itu pasti tidak akan bertahan menghadapi serangan langsung. Kadang ada baiknya bekerja dengan seseorang yang kompeten selain diriku. Cyber, tunjukkan jalan ke pintu energi."]],
				['4'] = [["Tentu saja dia yakin! Dia itu seorang penyihir!<new_line>Dan satu hal lagi, choomba, untuk ketenangan pikiranmu. Aku tahu bahwa Krampus punya beberapa pintu energi. Sekarang aku tahu siapa mereka."]]
			}
		},
		['wounded_bandit'] = {
			['choice'] = {
				['1'] = [[Tembak dia]],
				['3'] = [[Tawarkan bantuan]],
				['2'] = [[Berlindung di balik tembok]],
				['5'] = [[Jatuhkan pistol dari tangannya dan tembak]],
				['4'] = [[Bantu dia]],
				['7'] = [[Cari bandit]],
				['6'] = [[Jatuhkan pistol dari tangannya dan pukul dia dengan kapak]]
			},
			['episode'] = {
				['1'] = [[Aku berjumpa bandit di gedung apartemen. Dia di lantai, bersandar ke dinding dan mencoba memerban luka-lukanya. Saat melihatku, dia mengangkat pistolnya dan mengancamku dengan suara lirih, terengah-engah.]],
				['3'] = [[Saat aku bergegas ke balik tembok, peluru mengenai bahu kiriku. Tampaknya aku harus berhadapan dengan bandit itu jika aku ingin keluar dari sini hidup-hidup.]],
				['2'] = [[Saat bandit itu menyadari rencanaku, dia menembak, tetapi tembakannya luput dan mengenaiku di kaki kiri. Dia tak sempat menembak lagi, karena tembakanku yang jitu mengirimnya ke dunia lain.]],
				['5'] = [[Aku memberinya pereda sakit, merawat lukanya, dan membantunya memerban. Bandit itu sangat terkejut, dan menggambar peta ke salah satu simpanannya untukku sebagai rasa terima kasih.]],
				['4'] = [[Saat aku bilang aku hanya ingin membantu, dia memandangiku tak percaya, namun setelah pelan-pelan aku keluarkan obat-obatan dan menunjukkan kepadanya, dia menurunkan pistolnya dan membiarkan aku mendekatinya.]],
				['6'] = [[Saat aku mendekat, aku lihat bandit itu sepenuhnya tak berdaya, dan sekarang dia mati. Aku hanya harus menggeledah tubuhnya.]]
			}
		},
		['baba_yaga_2'] = {
			['episode'] = {
				['1'] = [[Kali ini, pondok itu sepenuhnya berputar dan Baba Yaga melangkah melalui pintu yang terbuka. Dia memberi isyarat dengan jarinya ke arah karung yang kubawa kepadanya, dan karung itu berdiri dan masuk sendiri ke dalam pondoknya. „Aku tidak mengira kamu selesai begitu cepat. Baiklah, ini bola benangmu, Nak, seperti kujanjikan. Ikuti bola benang ini dan kamu akan temukan monstermu.“ Sambil berkata begitu, Baba Yaga mengayunkan lengannya dan melemparkan bola itu jauh-jauh. „Ambillah!“ dia menjerit, terkekeh.]]
			},
			['choice'] = {
				['1'] = [[Kejar bola itu]]
			}
		},
		['emba_time_medium'] = {
			['episode'] = {
				['1'] = [["Salam, kamerad! Apakah kau mau membantu edukasi ilmu pengetahuan untuk bangsa ini? Kau akan bekerja sebagai juru kamera selagi aku memfilmkan adegan pemandangan."]],
				['3'] = [["Begini, pegang seperti ini dan tekan di sini. Lihat, ada biomutan di tanah terbuka di sana. Kau harus memfilmkan gerak-gerik mereka. Tapi jangan mendekat. Mulailah, aku perlu banyak materi."]],
				['2'] = [["Bukan "semacam film", tapi film yang hebat. Lihatlah keadaan zaman ini! Kau kira masyarakat hanya butuh vaksin dan injektor? Kau meremehkan mereka! Penting bagi mereka untuk memahami apa yang sedang terjadi, dan bukan dari artikel murahan, tapi dari karya seorang seniman. Maksudku, diriku sendiri. Dan kau bisa membantuku."]],
				['5'] = [["Cut! Sekarang kita akan melakukan bagian yang paling penting. Adegan pertarungan! Aku bahkan sudah menyiapkan beberapa figuran, tapi berhati-hatilah, anggaranku tidak terlalu besar."]],
				['4'] = [["Cut! Sekarang, pergilah ke sana dan filmkan jalanan ini. Kau lihat apa yang sedang mereka lakukan? Mereka menemukan bangkai sapi dan mereka menyantapnya. Ayo, kita perlihatkan kepada orang-orang hal yang paling menarik. Action!"]],
				['7'] = [["Cut! Bagus semuanya! Beberapa adegan lagi seperti ini dan kita akan menciptakan sebuah karya besar. Aku juga punya banyak ide, begitu banyak peristiwa yang terjadi di sekitar kita!<new_line>Pekerjaanmu bagus. Aku akan menghubungimu untuk pembuatan film seri berikutnya. Kau akan menjadi bagian dari sejarah perfilman dunia, jika dunia ini masih ada."]],
				['6'] = [["Kau akan berterima kasih kepadaku karena terlibat dalam pembuatan film ini! Bisa kita mulai?"]]
			},
			['choice'] = {
				['1'] = [["Semacam film?"]],
				['3'] = [[Mulai memfilmkan]],
				['2'] = [["Baiklah, tunjukkan kepadaku mana yang harus ditekan."]],
				['5'] = [["Baiklah, aku siap."]],
				['4'] = [["Bertarung?! Kau sedang membuat film apa?"]]
			}
		},
		['embacity_restaurant2'] = {
			['episode'] = {
				['1'] = [[Dari reruntuhan yang tertutup jamur muncul suara bising dari banyak penghuninya. Aku tidak boleh lupa mengenakan pakaian pelindung untuk membersihkan biomutan.]],
				['3'] = [[Di antara jamur liar, aku bisa melihat bunga merah yang indah. Tapi aku ragu kelompok mutan ini datang ke sini untuk mengaguminya, jadi aku perlu memikirkan perlindungan khusus.]],
				['2'] = [[Sekelompok mutan berkumpul di depan gedung yang ditumbuhi tanaman, saling bentrok memperebutkan bangkai binatang yang tertutup jamur. Aku membutuhkan pakaian pelindung untuk menanganinya.]]
			}
		},
		['halloween_def_easy'] = {
			['episode'] = {
				['1'] = [["Orang asing yang baik, apa kau benar-benar di sini untuk membantuku? Iblis-iblis ini cukup mengganggu. Mereka sudah melahap seluruh hasil panenku. Para tetangga tidak percaya bahwa mereka adalah iblis. Mereka bilang aku ini orang tua bodoh yang mengimajinasikan hal-hal itu, dan bahwa akulah yang memakan hasil panenku sendiri. Bersediakah kau membantuku? Aku akan memberikan upah yang sepadan."]],
				['3'] = [["Oh! Kau dengar? Monster-monster itu mendekat. Mari kita nyalakan tong-tong ini!"]],
				['2'] = [["Terima kasih. Aku tidak akan melupakan ini. Kita harus bersiap-siap. Aku bisa mengeluarkan tong bahan bakar untuk ditembakkan, atau memasang perangkap. Tapi jangan sampai kau terjebak di dalamnya."]]
			},
			['choice'] = {
				['1'] = [["Tentu saja aku akan membantu."]],
				['4'] = [["Gunakan tong."]],
				['3'] = [["Pasang perangkap."]],
				['2'] = [["Aku tak punya waktu." (Pergi)]]
			}
		},
		['emba_search_easy2'] = {
			['episode'] = {
				['1'] = [["Halo, kamerad! Kau mau membantu ilmu pengetahuan? Kami sedang mempelajari interaksi antara miselium dengan tanaman.<new_line>Dapatkah kau memberi kami sampel flora khas lokal?"]],
				['3'] = [[Ilmuwan itu mengumpulkan tanaman dan naik ke dalam ATV bersama mereka. Setelah beberapa lama, dia kembali.<new_line><new_line>"Ini sangat menjanjikan, rekan! Miselium itu mulai berinteraksi secara aktif dengan jaringan asing sejak mulai digabungkan.<new_line>Nah, aku harus pergi untuk melanjutkan observasiku! Aku harap kita terus bekerja sama!"]],
				['2'] = [["Jadi, kau sudah mengumpulkan herbarium itu? Kami tak sabar memulai eksperimennya!"<new_line>]]
			}
		},
		['halloween_fight_hard'] = {
			['episode'] = {
				['1'] = [[Suara garau binatang buas bisa terdengar di sekitar. Tampaknya ada banyak binatang itu di sini. Aku harus berhati-hati.]],
				['3'] = [[Sebuah dahan patah di bawah kakiku dan binatang-binatang buas ini mulai melolong dari sekelilingku. Itu jebakan!]],
				['2'] = [[Saat aku dengan hati-hati mendekati suara itu, suara itu mulai mereda secara perlahan. Pada saat aku mencapai kemungkinan asalnya, suara itu menjadi benar-benar hening.]],
				['5'] = [[Suara binatang buas ini tidak terdengar lagi. Aku tak tahu bagaimana mereka berhasil memancingku masuk ke dalam perangkap. Aku perlu memeriksa apakah masih ada dari mereka yang bersembunyi di sini.]],
				['4'] = [[Aku melawan monster-monster itu, tapi tampaknya itu mereka bukan gerombolan terakhir. Lolongan binatang buas terus menggema di sekelilingku.]],
				['7'] = [[Aku menyiapkan medan tempur jika terjadi serangan dan menyiapkan beberapa perangkap sederhana. Setidaknya aku punya pertahanan. Selanjutnya, aku akan menciptakan suara-suara, dan binatang buas yang tersisa datang berlari. Sayangnya, binatang buas ini adalah yang paling berbahaya.]],
				['6'] = [[Dengan hati-hati aku memeriksa area itu dan menemukan lebih banyak binatang buas di sekitar. Sekarang ada yang mengejutkan. Monster-monster ini terlihat lebih berbahaya daripada yang pertama.]]
			},
			['choice'] = {
				['1'] = [[Mendekat]],
				['3'] = [[Tarik perhatian pada dirimu sendiri]],
				['2'] = [[Periksa area]]
			}
		},
		['impostor2'] = {
			['episode'] = {
				['1'] = [[Bandit dalam setelan duduk di berhenti dan menghitung hal-hal yang dicuri. «Apa sampah!» penipu Moroz diredam. «Saya bilang ide bodoh!»]],
				['2'] = [[Senjata siap, saya pergi ke bandit. penipu berteriak: «Hei, man, menunggu saya tidak ingin berjuang untuk sampah ini tetapi tidak akan hanya memberikan kepada Anda Kau tahu apa Bawa kami tiga botol vodka dan karung ini adalah milikmu Mengatasi!.?.?»]]
			},
			['choice'] = {
				['1'] = [[Datang mendekat]]
			}
		},
		['construction_team_pests'] = {
			['episode'] = {
				['1'] = [[Ini tempat yang ditandai mandor di peta. Aku tak perlu mencari tempat persembunyian mereka, hama ini datang merangkak keluar untuk menemuiku.]]
			}
		},
		['emba_isaev_surgut'] = {
			['episode'] = {
				['9'] = [["Isaev, kau orang yang pintar, berikan kopernya padaku dan pergilah."]],
				['8'] = [["Kita akan menyiapkan pemusnahannya, akan ada beberapa tugas lagi untukmu. Kembalilah dalam beberapa jam."]],
				['13'] = [[(berbisik) "Di sana kau rupanya, temanku. Kita harus segera pergi dari sini. Aku telah menghubungi rekan-rekanku di S.O.V.A., kita akan pergi ke kota mereka.<new_line>Kami sangat membutuhkan bantuanmu, mohon datanglah ke sana secepat mungkin."]],
				['12'] = [["Baiklah. Ini, ambillah dan biarkan kami pergi."]],
				['11'] = [["Seperti yang telah dibuktikan oleh para ilmuwan Inggris, uang adalah uang, Dok. Jangan pelit, aku tidak ingin membunuhmu."]],
				['1'] = [["Kami mengerjakan teknologi penghijauan secara aktif. Dan berkat penelitian terhadap hasil kerja Gerin, kemajuannya sangat pesat!<new_line>Kami masih perlu mengembangkan sistem untuk melindungi dari aktivasi yang tidak semestinya. Sebenarnya, di sinilah kami berharap mendapatkan jawaban untuk bagaimana memecahkan masalah ini."]],
				['10'] = [["Kau tidak mengerti! Teknologi ini tidak stabil dan berbahaya!"]],
				['3'] = [["Sayangnya, tidak disebutkan tentang pengembangan apa pun dalam dokumen. Ini hanya sekadar lab pemrosesan data lainnya.<new_line>Tetapi luar biasanya, kami mendapatkan sinyal radio dari stasiun riset biologi ini! Kami belum mampu memecahkannya, tetapi mungkin ada seseorang di sana yang masih bersamanya."]],
				['2'] = [["Kami sudah berbicara dengan faksi-faksi besar. Kami berencana berbagi teknologi dengan mereka, jika mereka membantu kami mengembangkan sistem keamanan.<new_line>Tetapi jika kami tidak dapat menyelesaikan masalah keamanan ini, kami harus mematikan seluruh program dan menghapus teknologinya. Kau dapat melihat sendiri betapa seriusnya hal ini. Kami tidak bisa mengambil risiko apa pun."]],
				['5'] = [["Apa?! Itu bisa berbicara?! Tidak mungkin, itu omong kosong... Kau bilang itu menyebutkan nama Gerin?<new_line>Ya Tuhan, apa ini sebenarnya? Kita harus segera menghentikan semua pengembangan produk, kita tidak boleh mengembangkan teknologi ini lebih jauh!"]],
				['4'] = [["Satu-satunya hal yang harus dilakukan adalah mencari tahu apa yang disembunyikan kota sains ini. Tolong, ambilkan kotak hitam dari stasiun riset biologi."<new_line>(memberikan selembar kertas berisikan kodenya)]],
				['7'] = [["Jika kita tidak mengendalikan miselium itu sepenuhnya, kita tidak dapat menggunakannya bahkan sebagian sekalipun. Kita dapat bekerja dengan fragmen organisme ini, tetapi cepat atau lambat bagian lain darinya akan mulai bermutasi.<new_line>Dan Utusan seperti ini akan muncul. Atau Pengembang biak. Dan setelah itu mustahil untuk mengendalikannya sama sekali, bahkan pada bagian miselium kita."]],
				['6'] = [["Ya. Kotak hitam merekam proses pembentukan objek yang kau temui. Rupanya, seluruh miselium itu adalah satu organisme tunggal. Bisa dibilang, seperti jaringan telepon.<new_line>Tidak adanya satu inang tetap tidak menghalanginya sama sekali. Tapi bagi kami ini adalah masalah genting!"]]
			},
