				['1'] = [["Bagaimana kau akan menerapkan teknologinya?"]],
				['3'] = [["Mengapa? Apakah kau memiliki data baru?"]],
				['5'] = [[Dekati Isaev]],
				['4'] = [["Apa maksudmu?"]],
				['6'] = [["Baiklah. Semoga berhasil, Dokter."]]
			}
