		['frozen'] = {
			['episode'] = {
				['1'] = [[Selagi para penjarah menimbun balok-balok es besar ke atas kereta luncur, aku bisa berjalan mendekat. Dari es yang keruh aku bisa melihat bayangan orang-orang yang terkurung di dalamnya. Mereka harus segera dibebaskan.]]
			}
		},
		['newyearEvent_commander_medium'] = {
			['episode'] = {
				['1'] = [[Dikelilingi oleh penduduk desa, Cave Santa mencoba menjelaskan sesuatu kepada mereka seperti biasa.<new_line>"Oooorgh," ujarnya, memberi isyarat ke tongkatnya.<new_line>"Brrrrr!" lanjutnya, menunjuk jari ke arah hutan.]],
				['8'] = [[Selama pertarungan, kecerdasanku mulai berangsur-angsur kembali normal.<new_line>Setelah bertempur, salah satu anggota milisi mendekatiku.<new_line>"Berkatmu, sekarang kami bisa melindungi diri sendiri jika diperlukan. Itulah hadiah terbesar!"]],
				['3'] = [[Kemampuan intelektualku telah berkurang, dan pidato Cave mulai terdengar olehku.<new_line>"Syukurlah kau bisa mengerti ucapanku! Aku ingin mengajari penduduk desa cara melawan musuh. Aku bahkan berhasil membentuk pasukan. Tapi sejujurnya, aku bukan guru yang baik. Tolonglah aku."]],
				['2'] = [[Saat melihatku, Cave melompat kegirangan dan mengayunkan tongkat ke kepalaku.<new_line>"Oooooooooooooh."]],
				['5'] = [[Dua dari empat anggota pasukan memahami pelajaran dariku dengan sempurna dan mulai berlatih. Dua lainnya, tampaknya, tidak belajar apa-apa. Salah satu dari mereka bertanya:<new_line>"Ah ooomaroov?"]],
				['4'] = [[Pasukannya tidak terlalu besar—hanya empat orang. Salah satu dari mereka mendatangiku dan bertanya.<new_line>"Ooogrooooom ah ooom?"]],
				['7'] = [["Mereka mengatasinya dengan baik, tapi aku lihat sekelompok bandit lain di cakrawala. Kau perlu mengajarkan pelajaran singkat lagi. Mungkin ada penduduk desa yang belajar dan menggunakannya dalam pertarungan."]],
				['6'] = [[Anggota milisi lainnya memahami pelajaran itu dan mulai aktif berlatih juga. Setelah beberapa saat, Cave Santa berjalan ke arahku.<new_line>"Para bandit mendekat. Penduduk desa harus melawan mereka untuk menerapkan apa yang kau ajarkan. Tunjukkan pada mereka cara bertarung."]]
			},
			['choice'] = {
				['9'] = [[Ucapkan seruan pertempuran untuk menyerang]],
				['8'] = [[Tunjukkan cara menggunakan senapan laras pendek]],
				['13'] = [["Senang bisa membantu." (Pergi)]],
				['12'] = [[Tunjukkan cara menghindar]],
				['11'] = [[Tunjukkan cara melakukan serangan balasan]],
				['1'] = [["Apa yang terjadi di sini?"]],
				['10'] = [[Ambil posisi bertahan]],
				['3'] = [["Tidak, ada cara lain!" (Minum vodka)]],
				['2'] = [[Biarkan dia memukul kepalamu]],
				['5'] = [[Tunjukkan cara menggunakan senapan]],
				['4'] = [["Baiklah."]],
				['7'] = [[Tunjukkan cara menggunakan tombak]],
				['6'] = [[Tunjukkan cara menggunakan granat]]
			}
		},
		['easter_rabbit'] = {
			['episode'] = {
				['16'] = [["Kau punya apa lagi?"]],
				['10'] = [["Ini sekopnya. Berkelilinglah di kota, cari lubang kelinci di bangunan telantar dan gali lubang di sana. Di situlah kau akan menemukan cat itu."]],
				['13'] = [["Sst, kemarilah sebentar."]],
				['12'] = [["Nah, jangan diam saja, pergilah dan penuhi pesanan itu, para pelanggan sudah mengantre."]],
				['22'] = [["Aku dengar banyak orang menghilang di pertanian telantar di padang gurun. Konon itu adalah ulah ayam mutan. Kau bisa berkunjung ke sana, ayam pasti punya banyak telur."]],
				['23'] = [["Cari mereka di padang gurun atau padang gurun radioaktif, kadang-kadang kau bisa menemukan ayam di sana. Di mana ada ayam, di situ ada telur."]],
				['20'] = [["Oh, bagus, ini bagianmu. Punya lagi?"]],
				['21'] = [["Nah..."]],
				['1'] = [["Hai, Nak. Aku perlu bantuanmu, cepatlah kemari!"]],
				['3'] = [["Kau agak lambat, ya? Apa yang dibutuhkan oleh kelinci pink? Telur hias, tentu saja."]],
				['2'] = [["Apa maksudmu, "siapa kau", perhatikan baik-baik! Kelinci Pink, siap melayani."]],
				['5'] = [["Kenapa seekor penguin perlu membuang kotoran? Mereka mengeluarkanku dari Pelayanan Kelinci dan aku ingin mendapatkan kembali kepercayaan mereka agar menjadi indah dan lembut lagi."]],
				['4'] = [["Seberapa serius ini nantinya?"]],
				['7'] = [["Aku akan memberimu Kostum Kelinciku."]],
				['6'] = [["Kau membuatku bingung dengan kata "sungguh"-mu! Pakai otakmu! Paskah akan segera tiba, dan kau memberi telur untuk Paskah. Aku membuat kostum kelinci dan berencana untuk menjualnya.<new_line>Aku akan berkeliling untuk mengunjungi keluarga kaya dan memberi mereka kado untuk mendapatkan laba. Tugasmu adalah menghiasi telur dan membuat keranjang kado dengan telur-telur itu."]],
				['9'] = [["Aku akan tetap memberikan kostum itu kepadamu, tapi hanya jika kau berhasil membawakan jumlah keranjang yang cukup untuk memenuhi permintaan itu. Aku bersusah payah membuat kostum ini."]],
				['8'] = [["Aku tidak tahu, tapi kau tak akan bisa mendapatkan yang seperti ini lagi di padang gurun, ini langka. Aku bisa melihat kau mulai berpikir, jadi kenapa aku tidak berbagi sebagian laba untuk setiap keranjang denganmu?<new_line>Aku punya beberapa barang langka lainnya yang mungkin aku bagikan juga kepadamu."]],
				['30'] = [["Lalat saja tidak terbang cuma-cuma. Jika kau membawakan sesuatu untukku, aku hanya akan melakukan amal dan membagikannya kepada orang-orang. Jadi aku bisa berbuat baik."]],
				['11'] = [["Pikirlah sendiri. Kau bisa membelinya. Tapi, permintaannya sangat tinggi sekarang, begitu juga dengan harganya. Kau juga bisa mencarinya di tempat-tempat lain."]],
				['17'] = [["Bagus. Tunjukkan kepadaku yang kau punya."]],
				['27'] = [["Aku pernah dipenjara satu kali. Aku pernah menikah dua kali. Lalu, aku menjadi tentara dan harus menghabiskan waktu dua setengah tahun hidup di ujung Kazakhstan tanpa alasan sama sekali.<new_line>Tulang wajahku pernah retak, ginjalku diangkat, dan ada serpihan tulang di pergelangan kakiku yang tak akan pernah sembuh. Aku sudah mengalami segala macam kesulitan dalam hidup ini..."]],
				['26'] = [["Dari mana asalnya susu pada sapi? Kalau kau tak mengajukan pertanyaan konyol, kau tidak harus mendengar jawaban bodoh."]],
				['25'] = [["Pergilah ke kota telantar dan lakukan pencarian di sana. Kau akan menemukan lubang kelinci di sana, gali lubang, dan ambillah catnya. Jangan lupa membawa sekop."]],
				['24'] = [["Kau bisa membelinya dari pedagang dengan harga tiga kali lipat, tapi hanya lakukan itu kalau kau tidak bisa menemukannya di tempat lain."]],
				['14'] = [["Dengar, mengapa kau mau bekerja untuk orang gila itu? Mungkin, seharusnya kau menjual telur hias itu kepadaku, aku pastikan harganya cocok. Permintaannya sedang tinggi sekarang. Dan tidak orang yang akan membiarkan orang gila itu masuk ke rumah mereka."]],
				['15'] = [["Tidak ada syarat khusus, aku akan membelinya dengan harga tinggi, lebih tinggi dari yang ditawarkan orang gila itu. Jika kau menjual cukup banyak, aku akan memberimu hadiah. Harganya bagus, jadi ingat saja itu."]],
				['28'] = [["Nah, sepertinya kau sudah membawa cukup keranjang dan pelanggan sudah habis sekarang. Artinya kau sudah mendapatkan hadiah dan aku bahkan akan menambahkan sesuatu. Dan aku sudah mendapatkan uang pensiun untuk diriku."]],
				['29'] = [["Aku hanya orang biasa sekarang, dan peristiwa yang terjadi sebelumnya bukan masalah lagi. Jika ada lagi keranjang, bawalah kemari — liburan belum berakhir. Aku juga punya sesuatu di tokoku yang ingin aku berikan kepadamu."]],
				['19'] = [["Luar biasa, aku senang, ini bagianmu. Kau punya lagi?"]],
				['18'] = [["Kau juga mungkin bisa mengumpulkan sejumlah besi tua. Ini bagianmu. Masih punya lagi?"]]
			},
			['choice'] = {
				['16'] = [["Aku hanya lewat kemari." (Pergi)]],
