			},
			['episode'] = {
				['11'] = [[„Bagus, bawa ke sini,“ kata peti itu sambil membuka mulut bergigi tajamnya lebar-lebar. Tanpa melihat ke dalam, aku lemparkan bangkai tikus ke dalam peti itu. Mulutnya mengatup dengan kuat dan mengunyah dengan berisik sebelum meludahkan beberapa kantong permen ke dalam tanganku. „Sekarang, pergilah.“]],
				['10'] = [[Segera setelah aku menyentuh peti itu, tutupnya sedikit terbuka dan menampakkan sebaris gigi yang tajam. „Lepaskan tanganmu,“ kata suara yang kasar dari dalam peti. Terkejut, aku segera menarik tanganku kembali. „Pertama kali? Kamu bawakan aku tikus?“ tanya peti itu.]],
				['12'] = [[„Pergilah dan jangan kembali,“ kata peti itu sebelum menutup.]],
				['9'] = [[Hampir seketika, aku sampai ke ruang kecil dengan atap berkubah dan sebuah peti di tengah ruangan.]],
				['1'] = [[Saat aku mendekati lokasi itu, aku mendengarkan dengan saksama. Sepuluh meter di depanku, berdiri seseorang yang mengenakan jubah panjang dengan topeng berparuh. Bunyi mencicit yang putus asa menunjukkan orang ini baru saja menangkap tikus.]],
				['8'] = [[Aku kembali ke pertigaan untuk memilih arah berikutnya.]],
				['3'] = [[Aku terus berjalan selama beberapa menit dan akhirnya sampai ke pertigaan di lorong itu. Tiga gapura kecil, masing-masing membawa pada kegelapan yang identik. Haruskah aku pergi ke kiri, kanan, atau lurus?]],
				['2'] = [[Sosok itu melemparkan tikus ke dalam kurungan dan dengan hati-hati membawanya masuk ke rumah di dekatnya. Aku masuk semenit kemudian dan segera terdiam kaget. Pada sisi lain dari pintu itu ada lorong panjang dengan dinding batu yang diterangi obor. Di tengah keapakan perlintasan bawah tanah kuno ini, aku mencium aroma herba segar yang menyenangkan.]],
				['5'] = [[Pada alas tiang batu, terbaring mumi yang terbungkus perban. Dokter yang sama seperti sebelumnya berdiri di sudut dan menggumamkan sesuatu sambil mengaduk ramuan hijau kental dalam kaldron berkarat. Ramuan itu beraroma jelatang dan ribwort.]],
				['4'] = [[Gapura di tengah membawa pada labirin lorong. Aku menggunakan aroma herba untuk mengarahkan diriku saat aku menelusurinya. Hidungku tidak mengecewakan aku, setelah beberapa menit, aku sampai ke ruang besar dengan balok batu di tengahnya.]],
				['7'] = [[Pada sisi lain gapura, tangga ke bawah menuju ruang kecil yang dipenuhi barisan sangkar tikus. Puluhan pasang mata merah menatapku. Menyadari tak ada yang bisa kulakukan di sini, aku berbalik dan tanpa sengaja menginjak pelat batu. Sedetik kemudian, aku mendengar bunyi klik sangkar yang terbuka di belakangku dan bunyi mencicit tikus-tikus kelaparan.]],
				['6'] = [[Ketika aku melihat lebih dekat, mumi itu menyadari kehadiranku. Lehernya menimbulkan bunyi berkeretak yang mengerikan saat kepalanya menengok ke arahku, dan suara parau yang pasti dulunya adalah kutukan Mesir kuno.]]
			}
		},
		['catch_thief_quest'] = {
			['episode'] = {
				['16'] = [[Aku mendengar gemeresik di semak-semak dan melihat siluet gelap seorang pria dengan sesuatu di tangannya, tapi aku tidak bisa melihat apa itu.]],
				['17'] = [[Setelah menyelesaikan pekerjaannya, pencuri itu kembali dan mulai menaiki tangga. Untungnya, anak tangga yang sudah kulemahkan patah karena berat badannya dan dia jatuh ke tanah.]],
				['13'] = [[Saat sedang mengikat pencuri itu, aku melihat bahwa dia telah menusuk dirinya sendiri di jeruji besi dan sekarang mengeluarkan darah.]],
				['12'] = [[Aku mendengar bunyi menumbuk dari seseorang yang mengatur tangga di sisi lain pagar. Saat pencuri itu memanjat ke sisi lain, aku ada di sana untuk menyergapnya.]],
				['22'] = [[Kepala gudang puas dengan hasilnya.<new_line><new_line>'Aku tahu aku bisa memercayaimu. Ambil hadiahmu. Kau pantas mendapatkannya.']],
				['23'] = [[Pencuri itu melihatku. Dia menjatuhkan apa pun yang bisa mencegahnya berlari dan melarikan diri ke dalam hutan.]],
				['20'] = [[Serigala itu membawaku ke seluruh kamp bandit.]],
				['21'] = [[Setelah mengalahkan mereka, aku melihat sekeliling kamp. Aku tidak memiliki cukup kekuatan untuk mengambil kembali semua yang telah mereka curi, tapi aku meraih apa yang kubisa.]],
				['1'] = [[Menurut kepala gudang, seseorang telah terbiasa mengambil apa pun di gudang yang tidak dipaku ke tanah. Kita harus menangkap pencuri ini.]],
				['39'] = [[Mandor gudang kadang sangat meyakinkan. Pencuri itu mengaku dan memberi tahu kami tempat dia menyembunyikan jarahannya. Bos bertanya kepadaku:<new_line>"Bisakah kau pergi ke sana dan mengambil barang yang diambil perampok ini? Aku akan memberimu imbalan untuk ini."]],
				['41'] = [["Yah, terima kasih. Sekarang semuanya baik-baik saja. Para pencuri itu mengambil barangku sampai habis! Ini, imbalan yang kujanjikan."]],
				['40'] = [[Mandor gudang kadang sangat meyakinkan. Pencuri itu mengaku dan mengkhianati komplotannya. Bos bertanya kepadaku:<new_line>"Nah, itu baru berita. Sekarang aku harus meminta bantuan dari markas. Atau mungkin kamu bisa mengatasi geng itu? Aku akan memberimu imbalan untuk ini."]],
				['5'] = [[Aku memasang jebakan di tangga itu, tapi membuatnya terlihat aman. Sekarang aku mendengar suara gemeresik di dekatnya. Ada yang datang dan saatnya untuk bersembunyi!]],
				['4'] = [[Aku melihat jejak kaki di dekat pagar. Mereka menuju ke hutan dan, tampaknya, menyeret sesuatu bersama mereka.<new_line>Aku menemukan tangga buatan tangan di semak-semak.]],
				['7'] = [[Ini malam hari. Aku harus mencari tahu di mana mulai mengintai.]],
				['6'] = [[Jalur perjalanan sudah siap dan tersembunyi dengan baik.]],
				['38'] = [[Penjahat itu tidak mau mengakui apa pun. Mungkin dia hanya pencuri yang lewat, dan ada orang lain yang merampok gudang itu.]],
				['8'] = [[Aku mendengar suara tabrakan dari hutan. Jelas, jebakan mautku berhasil.<new_line>Aku harus menceritakannya dengan kepala gudang.]],
				['35'] = [[Tentu saja ini adalah hambatan, dan sekarang pencuri tidak akan bisa pergi ke mana-mana. Namun, aku mendengar suara gemeresik di sekitar. Ada yang datang, saatnya bersembunyi!]],
				['34'] = [[Aku melihat pencuri itu melemparkan tangga melewati pagar dan mulai turun. Namun, di anak tangga terakhir, dia jatuh dengan suara keras. Sepertinya kakinya terkilir. Meski setidaknya dia tidak akan bisa pergi ke mana-mana!]],
				['32'] = [[Itu trik yang cerdas. Kerja bagus! Aku bahkan tidak menyadarinya pada awalnya. Kau pantas mendapatkan hadiah yang bagus.']],
				['31'] = [[Setelah beberapa saat, kepala gudang masuk ke dalam gudang, kebingungan.<new_line><new_line>'Apa yang terjadi di sini? Bagaimana para bandit itu bisa masuk ke gudang?']],
				['30'] = [[Aku mengalahkan para bandit itu, tapi sekarang aku harus mencari tahu apa yang harus dilakukan selanjutnya.]],
				['15'] = [[Kepala gudang melihat ke arah pencuri itu.<new_line><new_line>'Dia hidup? Bagus, sekarang kita bisa menginterogasinya.']],
				['14'] = [[Pencuri itu melempar tangga ke sisi pagar kami. Saat dia turun, aku melompat keluar dari tempat persembunyian. Dia tersentak dan tangga itu patah karena berat badannya, melemparkannya ke tanah.]],
				['25'] = [[Nah, aku berhasil mengejar pencuri itu. Tapi, dia telah menuntunku langsung ke kroni-kroninya yang jelas-jelas tidak terlalu senang denganku.]],
				['24'] = [[Berandal itu jelas lebih mengenal hutan daripada aku. Setelah beberapa menit, dia menghilang dari pandanganku.]],
				['10'] = [[Kepala gudang berterima kasih kepadaku, lalu mengatakan:<new_line><new_line>'Aku merasa dia tidak bekerja sendiri. Tapi sekarang tidak ada cara untuk mengetahuinya.']],
				['28'] = [[Para bandit itu memiliki dendam padaku, tapi saat aku menunjukkan kunci gudang ke mereka, suasana hati mereka berubah. Kami segera mengosongkan gudang.]],
				['29'] = [[Pemimpin komplotan memberikan bagianku dari barang curian.]],
				['19'] = [[Di pagi hari, aku memberitahukan semuanya ke kepala gudang. Dia menggelengkan kepalanya dengan kecewa.<new_line>'Maaf, kau tidak berhasil menangkap pencuri itu, jadi aku tidak bisa memberimu hadiahmu.']],
				['18'] = [[Aku tidak berhasil menangkap pencuri itu dan dia menghilang di malam hari.]]
			},
			['choice'] = {
				['11'] = [[Bersembunyi di antara pagar dan dinding gudang]],
				['17'] = [[Jangan bergerak!']],
				['14'] = [[Kembali ke gudang dan tunggu sampai pagi]],
				['24'] = [[Menyerahlah!']],
				['22'] = [[Tunggu pencurinya kembali]],
				['32'] = [[Kejar]],
				['31'] = [[Ambil hadiahnya]],
				['21'] = [[Penyergapan]],
				['1'] = [[Tunggu sampai malam tiba pencurinya datang]],
				['2'] = [[Memeriksa di sekitar area]],
				['4'] = [[Kembali ke kepala gudang]],
				['7'] = [[Hancurkan anak tangga terbawah]],
				['6'] = [[Siapkan tripwire dalam perjalanan ke tangga]],
				['9'] = [[Sembunyi di dekat pagar]],
				['49'] = [[Pergi tangkap para bandit]],
				['48'] = [[Pergi ambil jarahan itu]],
				['47'] = [[Kembali ke perlindungan]],
				['46'] = [[Gali lubang]],
				['44'] = [[Tunggu kepala gudang]],
				['43'] = [[Ambil sesuatu yang berguna dan tinggalkan]],
				['42'] = [[Tidak ada harapan. Aku tidak punya kesempatan melawan begitu banyak orang. Lebih baik pergi.]],
				['40'] = [[Jelaskan rencana licikmu]],
				['36'] = [[Mana bagianku?']],
				['34'] = [[Hei, teman-teman! Apa pendapat kalian tentang bekerja sama?']],
				['27'] = [[Penyergapan: 'Jangan bergerak!']],
				['28'] = [[Biarkan pencuri pergi dan buat serigala mengejarnya]],
				['29'] = [[Serang]],
				['19'] = [[Rawat lukanya]],
				['18'] = [[Menunggu sampai pagi]]
			}
		},
		['newyearEvent_def_medium'] = {
			['episode'] = {
				['9'] = [["Usahamu kurang sungguh-sungguh. Jika serius, kau pasti sudah bisa merapal mantra padaku sekarang, dan bukan sebaliknya. Baiklah, aku akan menunjukkan trik baru. Jika kau berusaha sekeras usahaku, mungkin kau akan menguasainya.<new_line>Aku dapat memindahkan musuh ke mana pun aku mau untuk memudahkan kita melawan mereka. Tapi kaulah yang memilih lokasinya."]],
				['8'] = [["Jadi kau memang belajar sesuatu. Memang benar yang mereka katakan: Natal adalah hari penuh keajaiban."]],
				['13'] = [["Jika meningkatkan level kultivasi, kau tidak perlu meminta perlindungan! Kau perlu melatih diri, alih-alih menonton konser."]],
				['12'] = [["Choombas! Konsernya luar biasa! Terima kasih telah memusatkan perhatian pada psikopat cyber itu."]],
				['11'] = [[Seperti biasa, Wizard Santa memantraiku. Beberapa menit kemudian, musuh muncul di cakrawala, dan Wizard mulai melafalkan mantra yang rumit.<new_line>"Valar morghulis, ommm... alarmis."<new_line><new_line>Saat dia selesai, aku melihat kilatan putih, dan kami berada di lokasi lain dengan musuh kami.]],
				['1'] = [["'Hai, choomba! Aku akan menyelenggarakan konser besar-besaran, tetapi ada informan yang memberi tahu bahwa beberapa tamu tak diundang berencana untuk merusaknya.<new_line>Mereka harus dibereskan jika muncul. Apa kau mau membantu mengamankan acara itu?"]],
				['10'] = [["Aku sangat ahli, aku bahkan punya sedikit sihir tersisa untuk merapalkan mantra padamu. Pilih yang kau inginkan."]],
				['3'] = [[Ada banyak sekali orang di konser itu—mereka berkerumun seperti ikan sarden. Entah bagaimana aku mencapai perimeter, tetapi partnerku tak terlihat.]],
				['2'] = [["Bagus! Lalu menuju ke perimeter. Partnermu sedang menunggu di sana."]],
				['5'] = [["Hari ini kau sangat beruntung, idiot. Dan kau perlu berterima kasih kepada Cybersanta untuk itu. Apa kau tidak memperhatikan? Apa kau tak melihat musuh datang?"<new_line><new_line>Aku melihat ke kejauhan, dan musuh memang mendekat.]],
				['4'] = [[Aku melihat sekeliling, tetapi partnerku masih belum terlihat. Namun, begitu mulai berbalik ke Cybersanta, Wizard Santa muncul di belakangku.]],
				['7'] = [["Abrakadabra, selesai. Semoga setidaknya kau telah belajar sesuatu dan tak menyia-nyiakan sihir untuk omong kosong."<new_line><new_line>Sementara itu, para bandit telah sampai di perimeter dan memulai serangan mereka.]],
				['6'] = [["Kenapa mulutmu ternganga? Bisa kemasukan serangga nanti. Pilih mantra yang kau inginkan."]]
			},
			['choice'] = {
				['9'] = [[Menuju ke rawa yang tak tertembus.]],
				['8'] = [[Menuju ke pos terdepan yang aman.]],
				['12'] = [["Sama-sama."]],
				['11'] = [[Serang.]],
				['1'] = [["Tak masalah."]],
				['10'] = [[Menuju ke hutan lebat.]],
				['3'] = [["Apa kau pasanganku?"]],
				['2'] = [[Pergi ke perimeter]],
				['5'] = [["Aku ingin terbang seperti burung."]],
				['4'] = [["Jadi begitu."]],
				['7'] = [[Menuju depot bahan bakar.]],
				['6'] = [["Aku sudah mencoba."]]
			}
		},
		['halloween_boss_2022_6_2'] = {
			['choice'] = {
				['9'] = [["Hah, itu saja?"]],
				['10'] = [[Terus bergerak]],
				['1'] = [[Seberangi sungai]],
				['8'] = [[Keluarkan senjata]],
				['3'] = [["Bicara apa kau? Apa aku sudah memenuhi kontrak, atau apa?"]],
				['2'] = [["Di mana kita? Di mana Keledai itu?"]],
				['5'] = [["Kau tidak bisa mengalahkanku!"]],
				['4'] = [["Persetan, aku mau pulang."]],
				['7'] = [["Saksikan kekuatan dokumen dan birokrasi!" (Robek halaman dengan namamu di atasnya)]],
				['6'] = [["Mencari ini?" (Keluarkan halaman dengan daftar korban Monster Frankenstein)]]
			},
			['episode'] = {
				['9'] = [["Aku sangat ragu kau bisa melakukannya—kau sudah menyeberangi Sungai Yang Terlupakan. Dan sekarang teman kita akan datang ke sini dan mencabik-cabikmu."<new_line>Dengan jentikan jari Pencabut Nyawa, portal muncul di sekitar, berkilauan dengan percikan listrik.]],
				['8'] = [["Kau bisa melupakan kontraknya—ada tujuan lain yang ada di pikiranku. Kau sudah mengembangkan kekuatan iblis yang hebat. Kebanyakan Pencabut Nyawa hanya bisa bermimpi untuk memiliki jiwa yang begitu kuat."]],
				['13'] = [[Pencabut Nyawa melihatku meraih Gigawattor dan mendesis dengan marah, "Oh, dasar licik! Tak apa, kau tak bisa menutup semua portalku. Kau cuma manusia biasa, tak cukup kuat!"]],
				['12'] = [[Seolah-olah menegaskan apa yang baru saja kukatakan, petir menyambar di atas kepala dengan suara gemuruh. Petir itu juga mengenai salah satu portal! Dengan kilatan yang menyilaukan, petirnya menghilang. Boleh juga! Jadi artinya Gigawattor-ku bisa menghancurkan portal...]],
				['15'] = [["Tidak, kami juga ingin memberimu sesuatu sebagai kompensasi—ini. Dan omong-omong, aku masih punya urusan yang harus diselesaikan di duniamu, jadi kau bisa mampir ke toko kami jika perlu sesuatu."]],
				['14'] = [[“Jangan takut, Temanku. Aku Pencabut Nyawa baru. Aku dikirim ke sini untuk menggantikan rekan kerjaku yang mencurigakan. Semua penipuan dengan kontrak dan subkontrak… sayang sekali…<new_line>Mewakili seluruh dunia kematian, aku minta maaf. Aku akan mengirimmu ke dunia asalmu segera."]],
				['11'] = [["Apa-apaan... Bagaimana bisa?! Di sisi lain halaman yang sama?! Tapi aku sendiri yang menyerahkannya padamu..."]],
				['1'] = [[Pada setengah perjalananku, aku tersadar ternyata aku sedang berada di hutan yang gelap. Ada tengkorak-tengkorak yang ditancapkan ke tiang-tiang di sepanjang jalan, dan suara-suara menyeramkan melolong dari balik pepohonan.]],
				['10'] = [["Kekuatanmu memang meningkat sejak aku memberimu kontrak pertama. Tapi ada satu peringatan.<new_line>Aku mulai membuat rencana untukmu saat aku melihat namamu di Buku Kematian. Yang artinya... Kau tak bisa lari dari takdir, ingat tidak?"<new_line>(Pencabut Nyawa mengeluarkan Buku dan mulai membalik halamannya)]],
				['3'] = [[Begitu merepotkan, semua karena Keledai sial itu...<new_line>Tapi aku bisa mendengar teriakannya dari dekat sini, dan aku tak akan membiarkannya lolos kali ini!]],
				['2'] = [[Hutan ini makin gelap setiap aku melangkah, dan mata tengkorak itu bersinar merah. Jalannya berbelok tajam, dan di tikungan itu aku hampir menabrak gerombolan berbagai jenis monster.]],
				['5'] = [[Aku melihat ada lebih banyak lagi monster yang muncul dari hutan! Aku tak akan bisa menang melawan gerombolan itu. Tak ada pilihan lagi selain menyeberang ke tepi sungai di sana.]],
				['4'] = [[Aku pergi ke sungai. Anehnya, aku tak ingat pernah melihatnya di peta mana pun.<new_line>Pencabut Nyawa berdiri di tepi sungai yang lain, melambaikan tangan ke arahku:<new_line>"Ada arungan di sini, cepatlah menyeberang, Keledai itu makin menjauh!"<new_line>Segera setelah ia mengatakan itu, aku mendengar ranting-ranting pohon berderak di belakangku.]],
				['7'] = [["Ini, Manusia. Adalah duniaku. Selamat datang! Dan jangan khawatir tentang Keledai itu, dia berada di tempat yang seharusnya.<new_line>Jadi begini, semua binatang dan monster ini adalah ikan besar, tentu saja... Tapi aku ingin menangkap hiu sungguhan."]],
				['6'] = [[Tepi sungai itu ternyata redup dan sepi, seolah-olah ini berada di dunia lain. Sungai itu sendiri dengan cepat diselimuti oleh kabut. Yang memperparah situasi, hujan juga mulai turun. Dengan kilatan petir, badai petir pun menerpa.<new_line>Keledai itu tidak terlihat di mana pun, tapi Pencabut Nyawa ada di sana.]]
			}
		},
		['baba_yaga_1'] = {
			['choice'] = {
				['11'] = [[Hentikan ritual]],
				['10'] = [[Mulai lagi]],
				['13'] = [[Pemburu mengirimku. Aku harus menemukan Monster...]],
				['12'] = [[Pondok, pondok! Putar bagian depan ke hadapanku dan bagian belakangmu menghadap hutan!]],
				['14'] = [[Ambil daftar itu]],
				['9'] = [[Tengok melalui bahu kiri]],
				['1'] = [[Gambar lingkaran dari garam]],
				['8'] = [[Tengok melalui bahu kanan]],
				['3'] = [[Kelilingi lingkaran dari kanan ke kiri]],
				['2'] = [[Letakkan tulang-tulang membentuk kaki ayam]],
				['5'] = [[Tepuk tangan]],
				['4'] = [[Kelilingi lingkaran dari kiri ke kanan]],
				['7'] = [[Tepuk tangan tiga kali]],
				['6'] = [[Tepuk tangan dua kali]]
			},
			['episode'] = {
				['1'] = [[Ini tempat yang bagus untuk memanggil Baba Yaga. Aku harus memastikan ritual itu tidak gagal.]],
				['3'] = [[Ketika aku berbalik, pondok berkaki ayam telah muncul di dalam lingkaran membelakangiku, seakan-akan pondok itu selalu berada di sana.]],
				['2'] = [[Aku rasa aku gagal. Aku harus memulainya lagi dari awal.]],
				['5'] = [[„Bola benang, tentu saja. Kalian selalu datang untuk itu,“ gerutu nenek tua itu sambil menghilang ke dalam pondok lagi. Satu menit kemudian, gumpalan kertas melayang keluar dari jendela dan melesat melewatiku, diikuti suara Baba Yaga: „Kumpulkan benda di daftar itu dan panggil aku lagi, dan kamu akan mendapatkan bola benangmu.“]],
				['4'] = [[Pondok itu dengan enggan mulai menggerakkan kaki ayamnya dan berputar, tetapi berhenti setengah jalan. Wanita tua yang kesal melongokkan kepalanya dari jendela kecil yang menghadapku dan segera mengajukan pertanyaan bertubi-tubi: „Kamu siapa, Nak? Kamu berani memanggilku untuk apa? Apa yang kamu inginkan?„]],
				['6'] = [[Ketika aku memungut daftar itu dan berbalik, baik pondok dan lingkaran itu telah hilang.]]
			}
		},
		['help_hunter3'] = {
			['episode'] = {
				['1'] = [[Sembari tertawa, si penyihir merapalkan mantra demi mantra ke arah si pemburu. Senjatanya tergeletak tak jauh dari situ. Aku harus menyerang si penyihir sebelum ia membunuh si pemburu.]]
			}
		},
		['halloween_def_easy2'] = {
			['episode'] = {
				['11'] = [["Punggungku sakit. Aku sudah tidak muda lagi. Jangan tersinggung, ya. Kau sudah bekerja dengan baik! Ambil hadiahmu dan pergilah."]],
				['10'] = [["Oh, terima kasih banyak, Nak!"]],
				['9'] = [["Tiga!"]],
				['1'] = [[Kerumunan orang berkumpul di sekitar gubuk seperti penggemar di konser musik rock.<new_line>Penyihir Wanita Tua menjulurkan kepalanya keluar dari jendela:<new_line>"Cepat lari ke sini, Nak! Aku akan mengalihkan perhatian mereka!"]],
				['8'] = [["Dua..."]],
				['3'] = [["Aku sudah membuat ramuan khusus yang kurasa sangat mereka suka. Tahu tidak, aku senang ada tamu yang datang, tapi hanya jika mereka diundang. Aku punya perangkap menarik terbuat dari jamur yang bisa kita gunakan pada mereka. Haruskah aku memasangnya atau kita langsung tangani mereka saja?"]],
				['2'] = [[Saat penyihir wanita itu mengalihkan perhatian para monster, aku langsung berlari ke arah gubuk. Begitu aku masuk, rumah itu berdiri dengan kakinya.<new_line><new_line>"Itu lebih baik! Bisakah kau membantu wanita tua ini? Kita akan mengusir mereka lebih cepat bersama-sama."]],
				['5'] = [["Kau anak yang pintar! Sepertinya semuanya sudah siap. Kita lompat pada hitungan ketiga. Siap?"]],
				['4'] = [["Kau yang pilih, Nak. Bagaimana kalau minum ramuan sebelum bertarung? Omong-omong, aku belum sempat mengujinya."]],
				['7'] = [["Satu..."]],
				['6'] = [["Sepertinya semuanya sudah siap. Kita lompat pada hitungan ketiga. Siap?"]]
			},
			['choice'] = {
				['9'] = [["Kenapa kau tidak lompat?"]],
				['8'] = [[Lompat]],
