		['construction_team'] = {
			['episode'] = {
				['16'] = [[Mandor memeriksa pekerjaan dan memanggilku:<new_line><new_line>"Luar biasa, itu sesuai dengan yang dimaksud. Sekarang istirahatlah. Aku ingin mendiskusikan pekerjaan lain denganmu, di tempat lain—jika kau sempat, tentu saja."]],
				['10'] = [[Mandor memeriksa pekerjaan dan memanggilku:<new_line><new_line>"Kalau saja mereka selalu mengirim bantuan sepertimu! Sekarang istirahatlah. Aku ingin mendiskusikan pekerjaan lain denganmu, di tempat lain—jika kau sempat, tentu saja."]],
				['14'] = [[Sebuah truk muncul tepat di depanku di gerbang calon pos terdepan, dan seorang pria melompat turun dari tangga:<new_line>"Wow, bala bantuan, bagus! Aku yang bertanggung jawab atas brigade konstruksi. Ada banyak pekerjaan untuk semua orang, kau siap untuk memulai?"]],
				['12'] = [[Kami selesai membersihkan tepat saat konvoi traktor dengan trailer tiba. Mandor menepuk bahuku:<new_line><new_line>"Yah, kami kembali sesuai jadwal sekarang, terima kasih atas bantuanmu. Ini imbalan atas kerjamu. Serta sedikit tambahan karena sudah membantu masalah kami."]],
				['22'] = [[Operasi penebangan tidak berjalan dengan cukup cepat. Para pekerja kelelahan dan membutuhkan bantuanku.]],
				['32'] = [["Konstruksi ini akan segera selesai, dan mereka akan menempatkan beberapa pejuang di sini. Kita perlu mencari tahu apa yang terjadi di daerah itu sebelumnya. Di sini dan di sini para pemuda melihat aktivitas mencurigakan. Periksa apa yang terjadi di sana. Atau, jika kamu tidak sempat, bantu kami menyelesaikan pekerjaan di sini lalu aku akan mencari orang lain untuk kutugaskan."]],
				['20'] = [["Ini bukan masalah besar—kami kehabisan rokok. Namun, aku tak bisa berfungsi tanpa rokok. Kamu akan menolongku jika punya sedikit untuk dibagikan."]],
				['30'] = [["Kami sudah mencoba menanam beberapa benda sebagai eksperimen. Kemudian hama keluar dan merusak semuanya. Kamu adalah pejuang, jadi atasi masalahnya. Atau, jika kamu tidak sempat, bantu kami menyelesaikan pekerjaan di sini lalu kita semua akan pergi mengatasi masalah itu bersama-sama."]],
				['1'] = [[Seorang pria berjalan ke arahku dari balik tumpukan bahan bangunan:<new_line><new_line>"Halo, aku mandor. Ini yang akan jadi kebun bersama—kami sudah menemukan satu bidang kecil tanah yang tidak tercemar di sini. Apa kamu datang untuk membantu?"]],
				['3'] = [[Mandor memeriksa pekerjaan dan memanggilku:<new_line><new_line>"Terima kasih, pekerjaan ini bisa memakan waktu lebih lama tanpamu. Sekarang saatnya istirahat merokok. Aku ingin mendiskusikan pekerjaan lain denganmu, di tempat lain—jika kau sempat, tentu saja."]],
				['2'] = [["Kami mulai membangun gudang. Bantu mereka, kita harus menyelesaikan atapnya."]],
				['5'] = [[Mandor memeriksa batas-batas ladang yang sudah dibersihkan:<new_line>"Tanahnya bagus di sini, tetapi ini tempat yang liar. Kita harus pintar memasang pagar. Sebaiknya kau membantu para pemuda mengerjakan itu—mereka hanya mendengar tentang mutan dalam cerita saja."]],
				['4'] = [[Mandor memeriksa pekerjaan dan memanggilku:<new_line><new_line>"Terima kasih, pekerjaan ini bisa memakan waktu lebih lama tanpamu. Sekarang saatnya istirahat merokok. Ayo, aku akan tunjukkan pekerjaan selanjutnya dalam kampanye konstruksi kami."]],
				['7'] = [[Calon kebun bersama ini dilindungi dengan aman. Mandor memeriksa hasilnya:<new_line><new_line>"Jadi, para pemuda ini tahu cara bekerja saat ada seseorang yang berpengalaman untuk mengawasi mereka! Ini imbalan atas kerjamu."]],
				['6'] = [[Calon kebun bersama ini dilindungi dengan aman. Mandor memeriksa hasilnya:<new_line><new_line>"Jadi, para pemuda ini tahu cara bekerja saat ada seseorang yang berpengalaman untuk mengawasi mereka! Ini imbalan atas kerjamu. Serta sedikit tambahan karena sudah membantu masalah kami."]],
				['9'] = [["Pertama, kita harus menebang di daerah sebelah sana. Lihat tempat yang kumaksud? Hei, Vasilich! Berikan gergaji mesinmu kepada orang ini, dia akan mengambil alih."]],
				['8'] = [[Di area penebangan, aku bertemu dengan seorang pria berbaju tertutup:<new_line>"Hai, aku mandor di sini! Mereka mengirimmu tepat waktu. Kami tiba di area yang sulit, dan pekerjaan kami berjalan lebih lambat dari jadwal. Kau siap bekerja?"]],
				['28'] = [["Para bandit lokal mencuri peralatan kami dan menyerang pekerja kami. Kamu adalah pejuang, jadi atasi masalah ini. Atau, jika kamu tidak sempat, bantu kami menyelesaikan pekerjaan di sini lalu kita semua akan pergi mengatasi masalah itu bersama-sama."]],
				['18'] = [[Kami membangun menara pengawas kecil tapi kuat. Mandor memeriksanya sendiri, lalu menoleh ke arahku:<new_line><new_line>"Terima kasih atas kerja kerasmu, dan sapa orang-orang di permukiman. Ini imbalan atas kerjamu. Serta sedikit tambahan karena sudah membantu masalah kami."]],
				['11'] = [["Sekarang kita punya sedikit ruang, tempat untuk membangun pondok dan lumbung. Saat ini kami berjalan jauh 10 km setiap hari dari sini dan kembali dari lokasi lama.<new_line><new_line>Bantu kami meratakan bidang tanah, traktor akan membawa pondoknya segera."]],
				['17'] = [[Mandor menunjukkan kepada kami lembaran yang dipenuhi garis:<new_line>"Jadi, ini adalah cetak biru yang mereka kirimkan. Kita akan membangun sebuah menara, dan kemudian para pemuda kita harus mempertahankannya, jadi mari kita semua bekerja keras."]],
				['13'] = [[Kami selesai membersihkan tepat saat konvoi traktor dengan trailer tiba. Mandor menepuk bahuku:<new_line><new_line>"Yah, kami kembali sesuai jadwal sekarang, terima kasih atas bantuanmu. Ini imbalan atas kerjamu."]],
				['15'] = [["Lihat tempat mereka membawa kepingan baja dan papan itu? Bantu bongkar itu dan kita akan membangun menara."]],
				['33'] = [[Brigade sedang istirahat merokok. Meskipun mereka sebenarnya tidak punya apa-apa untuk dirokok.]],
				['23'] = [[Truk itu terdiam di sana dengan muatan penuh. Tanpa bantuanku, para pekerja tidak akan sempat menanganinya.]],
				['31'] = [["Beberapa mutan muncul di hutan. Ada kawanan mereka yang sangat dekat, dan bahkan para penjaga tidak membantu. Kamu adalah pejuang, jadi atasi masalah ini. Atau, jika kamu tidak sempat, bantu kami menyelesaikan pekerjaan di sini lalu kita semua akan pergi mengatasi masalah itu bersama-sama."]],
				['21'] = [[Para pekerja masih sibuk bekerja di atap gudang. Saatnya untuk ikut membantu demi tujuan bersama.]],
				['26'] = [[Di area yang sudah mereka tebang, kamu tidak bisa berjalan sedikit tanpa tersandung tunggul atau jatuh ke dalam lubang. Brigade ingin membangun beberapa pondok tempat tinggal di sini dan menungguku untuk membantu membersihkan bidang tanah ini.]],
				['25'] = [[Mereka memilih untuk mengambil tanah yang masih bersih di tempat yang penuh bahaya. Aku harus membantu membangun pagar pelindung yang bagus.]],
				['24'] = [["Kamu benar-benar penyelamat!"]],
				['29'] = [["Mereka seharusnya membawakan kami lebih banyak persediaan, tetapi truknya belum datang. Mungkin terjebak macet di suatu tempat. Pergi dan ambil muatan kami dari mereka, muatannya tidak terlalu banyak."]],
				['19'] = [[Kami membangun menara pengawas kecil tapi kuat. Mandor memeriksanya sendiri, lalu menoleh ke arahku:<new_line><new_line>"Terima kasih atas kerja kerasmu, dan sapa orang-orang di permukiman. Ini imbalan atas kerjamu."]],
				['27'] = [[Para pekerja sudah mulai membangun menara dan mereka semua menungguku untuk bergabung dengan mereka.]]
			},
			['choice'] = {
				['1'] = [["Benarkah, pekerjaan seperti apa?"]],
				['3'] = [["Apa tugasnya?"]],
				['2'] = [[Cari rokok]],
				['5'] = [[Mulai bekerja]],
				['4'] = [[Serahkan rokok]],
				['6'] = [[Tanggapi permintaan mandor]]
			}
		},
