		['headless_horseman'] = {
			['episode'] = {
				['11'] = [[Aku menginjak gas mencoba untuk mendahului Penunggang Kuda, tetapi dia melemparkan bom ke bawah rodaku dan aku kehilangan kendali. Aku terpaksa berhenti dan keluar dari jalan. „Cukup berputar-putar. Waktunya untuk mati!“ ia berteriak sambil mengarahkan sepeda motornya tepat ke arahku.]],
				['10'] = [[Segera Penunggang Kuda menyalipku. „Aku kecewa,“ katanya, sebelum mengebut ke kegelapan malam.]],
				['8'] = [[Aku menyadari, menginjak pedal gas sambil membelok tajam adalah ide buruk setelah aku hampir kehilangan kendali dan mendarat di parit. Melihat kesialanku, Penunggang Kuda tertawa dan menghilang di kegelapan malam. Sepertinya aku harus mencoba lagi.]],
				['1'] = [[Bayangan telah berkumpul, dan aku mendengar suara mesin dan gema kuda meringkik di belakangku. Aku rasa Penunggang Kuda telah menemukan aku.]],
				['9'] = [[Aku melambat dan berbelok dengan hati-hati, sementara Penunggang Kuda dengan mudah mengejar dan menjajariku.]],
				['3'] = [[Jalan di depan cukup rata dan lurus.]],
				['2'] = [[Ia di sana: dari cermin belakangku, aku sekilas melihat sepeda motor dengan cahaya hijau seram dan tengkorak kuda di depan. Sedetik kemudian, helmnya yang menyala melayang di sampingku. Dalam nada yang mengejek dengan gema keras, ia berkata, „Ayo kita jajal kemampuanmu!“ sebelum kembali ke tubuhnya.]],
				['5'] = [[Aku melambat hingga aku sejajar dengan Penunggang Kuda. „Sudah takut?“ tanya suara menyeramkan sambil tergelak.]],
				['4'] = [[Aku menginjak pedal gas dan meninggalkan Penunggang Kuda.]],
				['7'] = [[Ketika Penunggang Kuda mulai berbelok, aku menabraknya dan menyingkirkan ia dari jalan. Aku terkejut melihat hantu itu melayang jatuh ke dalam parit. Saat aku mendekati sisi jalan untuk memeriksanya, ia melayang kembali ke atas sepeda motornya dan berteriak, „Kamu akan membayar itu dengan hidupmu!„]],
				['6'] = [[Ada tikungan tajam di depan kami.]]
			},
			['choice'] = {
				['1'] = [[Lihat ke belakang]],
				['3'] = [[Injak pedal gas]],
				['2'] = [[Tetap awasi jalanan]],
				['5'] = [[Tabrak Penunggang Kuda]],
				['4'] = [[Lambatkan]],
				['6'] = [[Kembali ke jalan]]
			}
		},
		['emba_energy_hard'] = {
			['episode'] = {
				['9'] = [["Na-a-a-h, kalau kau menggunakan dengan pop... prop... porsi yang tepat, aku bisa m-membantai sepuluh meh... muh... mutan itu. Mari keluar ke pekarangan dan aku akan m-menunjukkannya kepadamu! Tapi kita masih perlu sa... ssa... tu..."]],
				['8'] = [["Lihat kelakuan mi-se... lium ini. Dan kita ini orang yang serius. Mari s-s-selesaikan eks... perimen ini. Baiklah kalau begitu, demi ilmu pengetahuan!"]],
				['12'] = [[Yang terjadi berikutnya sama sekali tidak aku ingat. Aku terbangun di pekarangan, semuanya terbalik, ada pisau bengkok menancap di pagar.<new_line>Gorden menutupi semua jendela di rumah petani dan pintunya terkunci. Sepertinya eksperimen itu gagal.<new_line>Aku merasa bersalah. Aku mungkin salah menghitung dosisnya — bir miselium itu bukan main-main.]],
				['11'] = [[Seolah-olah, ingatanku tentang peristiwa yang terjadi berikutnya sama sekali terpotong. Aku terbangun di pekarangan, semuanya terbalik, dan sebuah pisau bengkok menancap di pagar.<new_line>Semua jendela di pondok petani itu tertutupi gorden dan pintunya terkunci.Sepertinya eksperimen itu gagal.<new_line>Tapi aku tidak merasa terlalu buruk seperti yang aku duga — mungkin masih ada hal lain dengan bir miselium itu.]],
				['1'] = [["Hei, aku ingat kamu. Kau mau melakukan eksperimen? Aku pikir kita pernah melakukan eksperimen bersama-sama."]],
				['10'] = [[Setelah meminum tegukan terakhir, sang petani berusaha bangkit, mengambil dua langkah ke arah pintu, lalu terjatuh di lantai, dan langsung mengorok. Aku mengambil catatannya dari meja dan perlahan bergerak keluar. Aku akan membawanya ke para ilmuwan agar mereka dapat meninjau risetnya.]],
				['3'] = [["Apakah petani itu yang menyuruhmu? Aku sudah katakan kepadanya — tidak ada makanan, tidak ada barang. Kau bisa membawa makanan sendiri untuk ditukarkan denganku, kalau orang jorok itu terlalu malas."]],
				['2'] = [["Kau tahu, aku juga semacam ilmuwan. Tapi aku perlu material untuk diteliti. Aku sudah membuat kesepakatan dengan seorang pria tua, kau hanya perlu mengunjunginya dan mengambil barang itu."]],
				['5'] = [["Ini bir miselium, kawan. Para ilmuwan tidak peduli, tapi barang ini benar-benar punya efek fisik! Ini sesuatu yang penting!"]],
				['4'] = [[Orang tua itu memberiku sebuah bundel yang aku bawa kembali ke sang petani.<new_line><new_line>Dengan gembira dia membentangkan bundel itu, mengeluarkan botol cairan hijau dan mulai mencatatkan sesuatu di jurnalnya:<new_line>"Nah, sekarang. Kami melakukan eksperimen nomor... mm... Aku tidak ingat. Material... satu liter zat untuk diteliti. Baik, kau sudah siap?"]],
				['7'] = [["Pria tua itu benar-benar punya satu formula dan separuh lagi. Re... agen... murni. Tuliskan... ob... serr.. va-si-mu di jurnal ini, kita akan membawanya ke orang berpakaian kuning itu nanti. Ayo, lagi!"]],
				['6'] = [["Campuran pria tua itu masih punya banyak karakter yang belum dijelajahi. Nah, kalau begitu, demi ilmu pengetahuan. Kita mulai eksperimennya?"]]
			},
			['choice'] = {
				['1'] = [["Kau tertarik dengan ilmu pengetahuan?"]],
				['3'] = [["Tuangkan."]],
				['2'] = [[Pergi ke pria tua itu]],
				['5'] = [["Kau benar-benar seorang ilmuwan..."]],
				['4'] = [["Apa ini?"]]
			}
		},
		['toy_story'] = {
			['name'] = [[Terem]],
			['episode'] = {
				['1'] = [[Pintu terem dengan hiasan megah itu hancur, dan di dalamnya sedang terjadi kekacauan. Aku tidak ada waktu untuk menentukan posisiku sebelum aku mendengar suara asing datang dari balik meja yang terbalik, «Mereka telah kembali! Cepat, isi meriam! Siap! Bidik! Tembak!» Setelah itu, terdengar ledakan dan peluru kecil mendesing melewati telingaku. Apa yang terjadi di sini?]],
				['3'] = [[Satu jam kemudian, setelah selesai berbenah, kami mengetahui bahwa terem itu telah diserang oleh para penjarah es. Mereka merusak tempatnya tapi tidak mengambil apa pun sebelum pergi. Saat aku tiba tadi, mainan-mainan mengira aku adalah salah satu penjarah yang datang kembali. Gadis Salju berjanji akan mencari tahu dari mana datangnya para penjarah itu.]],
				['2'] = [[Hentikan! teriak Gadis Salju, berlari ke arah terem. Aku berdiri dikelilingi mainan rusak, dan si Nutcracker terdiam, pedangnya terangkat siap menyerang.]]
			}
		},
		['new_year_boss_5_4'] = {
			['choice'] = {
				['1'] = [["Kalau begitu, ayo serang!"]],
				['4'] = [["Bisakah aku melakukannya seorang diri?"]],
				['3'] = [["Portalku?"]],
				['2'] = [["Apa kau menyuruh kita menyerah?"]]
			},
			['episode'] = {
				['1'] = [["Aku bisa merasakan jejak magis yang telah dicuri dari kita... Frosty bersembunyi di suatu tempat di dekat sini dan menunggu kita menyerang balik."]],
				['3'] = [["Apa ada pilihan lain? Kita sudah mengeluarkan seluruh kemampuan!"]],
				['2'] = [["Kita menghabiskan hampir semua energi untuk penyedot itu! Langsung terjun ke perangkap Frosty bukan hanya tindakan bodoh—itu sama saja bunuh diri."]],
				['5'] = [["Ya, yang selama ini kau pijak, jenius. Kita tak punya tenaga lagi setelah itu.<new_line>Jadi, begitu sampai di tempat persembunyian Frosty, kau hanya bisa mengandalkan diri sendiri."]],
				['4'] = [["Oh, baiklah. Karena aku terjebak di sini bersamamu... Aku bisa menyarankan rencana bodoh juga.<new_line>Sepertinya aku bisa mengarahkan portal langsung ke tempat persembunyian Frosty."]],
				['6'] = [["Tidak ada pilihan lain. Baiklah, nak, semangat. Kupikir kau masih punya sedikit peluang. Hiasi pohon untuk terakhir kalinya selagi aku menyiapkan semuanya."]]
			}
		},
