		['newyearEvent_def_easy'] = {
			['episode'] = {
				['1'] = [[Saat mendekati lokasi perayaan, terlihat kepulan asap tebal. Para bandit merusak perayaan. Aku harus melindungi penduduk desa, tapi jika melakukannya sendiri, aku akan membahayakan mereka. Aku butuh bantuan.]],
				['3'] = [[Di belakangku, aku mendengar suara mesin. Ternyata itu adalah mobil kecil kuno berwarna merah. Mobil itu berhenti, dan Business melangkah keluar dari mobil itu.<new_line>"Ho ho ho! Lagi-lagi kau, teman baikku? Ayo, orang-orang butuh bantuan. Tak ada waktu untuk kalah."]],
				['2'] = [[Setelah meningkatkan fokus, aku memikirkan keajaiban Natal sekuat tenaga. Tidak terjadi apa-apa. Mungkin karena aku sendiri adalah keajaiban Natal, karena aku juga bisa dibilang Sinterklas. Aku perlu memikirkan sesuatu yang spesifik.]],
				['5'] = [[Sebuah portal muncul di sebelahku, dan Claws melangkah keluar.<new_line>"Orang-orang membutuhkan bantuan. Ayo, kawan! Demi solidaritas proletariat!"]],
				['4'] = [[Sebuah portal muncul di sebelahku, dan Cave melangkah keluar.<new_line>"Eh aaah!" dia berteriak dan bergegas menuju ke medan pertempuran. Sepertinya aku harus mengikutinya.]],
				['7'] = [[Sebuah portal muncul di sebelahku, dan Wizard melangkah keluar.<new_line>"Kau lagi? Dasar bodoh, tak berguna. Hal kecil seperti ini saja tak bisa kau tangani sendiri? Baiklah, biarkan aku membantumu dalam sekejap."]],
				['6'] = [[Sebuah portal muncul di sebelahku, dan Cybersanta melangkah keluar.<new_line>"'Hai, choom! Ada apa ini? Kasus psikosis cyber lain? Ayo buat mereka babak belur!"]]
