		['santa_cyber'] = {
			['choice'] = {
				['1'] = [["Hai, kami perlu..."]],
				['3'] = [["Lalu apa?"]],
				['2'] = [["Kami butuh informasi tentang..."]]
			},
			['episode'] = {
				['1'] = [["Yo, choombas, ada apa? Siapa yang mengundang penjilat ini ke pesta?"]],
				['3'] = [["Persetan! Aku akan pergi dari sini. Siapa saja yang ingin bersenang-senang bersamaku di atas panggung, sampai jumpa di konser."]],
				['2'] = [["Jaga ucapanmu, Cybersanta! Dan kalian, kawan-kawan, jaga kantong kalian agar tidak dicopet olehnya."]],
				['5'] = [["Mau bagaimana lagi? Kita akan datang ke konser itu dan mencoba membujuknya. Business, sebaiknya kau tetap berada di sini."]],
