		['gigahive_bar'] = {
			['episode'] = {
				['1'] = [[Ini adalah tempat yang sangat berbahaya: rumah ratu lebah. Banyak yang mencoba membunuhnya, tapi tidak ada yang pernah lolos dari sarangnya hidup-hidup. Semoga mereka beristirahat dengan tenang.']],
				['3'] = [[Kami kehabisan stok saat ini, tapi jika kau membawa kembali lilin untuk ahli kami, dia akan bisa memberimu satu dosis. Kau bisa menemukan sarang lebah induk di sekitar Ufa, ambil lilinnya di sana.']],
				['2'] = [[Begitulah cara mereka melindungi diri dari orang luar. Kami bahkan telah mengembangkan penangkal untuk hal ini.<new_line>Apa kamu serius masuk ke sana? Nah, jika kau entah bagaimana membunuh ratu, bawakan aku kepalanya dan aku pasti akan memberimu hadiah yang mahal.'<new_line>]],
				['5'] = [["Nah, kamerad, baumu benar-benar seperti lebah! Kau benar-benar membunuh sang ratu?! Aku tak percaya sama sekali, bawa kepalanya kemari."]],
				['4'] = [[Aku tidak percaya bahwa kau benar-benar membunuh ratu lebah! Kau benar-benar pahlawan lokal sekarang! Aku akan menggantung piala ini di sini di bar! Dan aku menjanjikanmu hadiah yang bagus. Ini, ambil ini.']]
			},
			['choice'] = {
				['1'] = [[Sebutkan lecet-lecetnya]],
				['3'] = [[Mengangguk kepadanya dan pergi]],
				['2'] = [[Tapi, di mana aku bisa mendapatkan penawarnya?']]
			}
		},
		['emba_energy_easy3'] = {
			['episode'] = {
				['1'] = [["Oh, hai. Kalau kau butuh sesuatu, kau harus menunggu, maaf. Kami sedang sibuk sekarang dan jujur saja, semua orang sudah lelah."]],
				['3'] = [["Aku dengar, mutan hijau baru itu berkumpul di tempat-tempat yang banyak jamurnya. Mereka memakannya.<new_line>Jadi, pekarangan kami seperti kantin gratis bagi mereka sekarang. Berbahaya membiarkannya seperti itu, tapi semua orang sekarang kelelahan."]],
				['2'] = [["Kami memerlukan bantuan. Sebenarnya, kami tidak sengaja melihat peternakan ini kemarin dan tempat ini sepenuhnya diselimuti akar dan jamur aneh ini.<new_line>Kami belum bisa membersihkan semua, padahal kami sudah bekerja sekitar sepuluh jam. Semua orang hampir tidak bisa berdiri, dan semua sampah ini harus dikumpulkan, lalu ditumpuk dan dibakar."]],
				['5'] = [[Para petani menyiramkan bensin ke atas tumpukan itu, kemudian kami menyaksikannya terbakar, rasanya agak lega.<new_line>Lalu, aku berpamitan setelah mereka mengucapkan terima kasih dan mendoakan kebaikan untukku.]],
				['4'] = [[Ternyata tugas ini tidak mudah. Potongan batang tebal dan cacahan akar berserakan di mana-mana.Potongan jamur itu ternyata lebih berat dari yang aku kira.<new_line><new_line>Orang-orang ini benar-benar bekerja keras, tapi aku juga melakukan hal yang sama. Sekarang semuanya sudah ditumpuk jadi satu.]]
			},
			['choice'] = {
				['1'] = [["Kau perlu bantuan?"]],
				['3'] = [[Panggil petani itu]],
				['2'] = [["Kenapa terburu-buru?"]]
			}
		},
		['emba_energy_easy'] = {
			['episode'] = {
				['1'] = [["Hey, tunggu! Aku perlu bantuan. Akar sialan ini menutupi seluruh pertanian. Mutan hijau itu mulai mengunjungi tempat ini seolah-olah kita ini kantin pribadi mereka."]],
				['3'] = [["Kawan, kau penyelamatku!<new_line>Lihat, kau harus memotong semua dahan ini sekaligus, tumpuk di perapian, dan bakar. Kau tak akan punya waktu untuk beristirahat, atau makhluk ini akan berakar lagi dan menyebarkan sporanya ke mana-mana. Mari mulai dengan ladang ini."]],
				['2'] = [["Ini kesalahanku sendiri! Tetanggaku bercerita tentang miselium yang bisa membuat ladang subur dan mendapatkan panen yang lebih bagus. Aku menanamnya di rumah kaca dan tumbuh sangat subur, aku bahkan tak bisa memotongnya sendiri sekarang. Tolonglah aku!"]],
				['5'] = [[Seolah-olah miselium itu berusaha membungkus lumbung dalam balutan dahan dan akar, tapi kita sudah membereskannya.<new_line>Sekarang tinggal mengumpulkan tumbuhan mutan ini dan membakarnya. Petani itu pergi mengambil bensin dan menyuruhku mengumpulkan akar-akar itu.]],
				['4'] = [[Kita sudah menyelesaikan bagian pertama dan membersihkan ladang, tapi itu belum semua, kita harus melanjutkan ke lumbung.]],
				['7'] = [["Oh, sialan! Semuanya sia-sia, tumbuhan itu akan tumbuh lagi dan semua berulang lagi. Terserah. Terima kasih sudah berusaha, tapi kau harus tahu batas kemampuanmu..."<new_line><new_line>Petani itu berjalan menuju pondoknya dengan sedih, menendang akar yang tumbuh ke dalam tanah di hadapanmu.]],
				['6'] = [["Yah, sudah selesai sekarang! Akhirnya sampah ini terbakar dan aku bisa mengelola pertanianku lagi.<new_line>Terima kasih, kau orang baik. Aku akan membayar jasamu sebisa mungkin."]]
			},
			['choice'] = {
				['1'] = [["Bagaimana bisa terjadi?"]],
				['3'] = [[Tumpuk akar]],
				['2'] = [[Potong akar]]
			}
		},
		['halloween_boss_2022_5_2'] = {
			['choice'] = {
				['1'] = [[Singkirkan penghalang dengan tanganmu]],
				['3'] = [[Merangkak lebih dekat]],
				['2'] = [[Singkirkan penghalang dengan sekop]],
				['5'] = [[Serangan dari semak-semak]],
				['4'] = [[Bahkan lebih dekat...]],
				['7'] = [["Jangan bergerak!"]],
				['6'] = [["Apa?"]]
			},
			['episode'] = {
				['9'] = [[Setelah aku mengatasi si monster, aku pergi mencari pria yang kabur itu. Aku menemukannya di sekitar: rupanya, saat dia melarikan diri tanpa melihat ke belakang, dia terjatuh ke jurang. Lehernya patah.]],
				['8'] = [[Si bungkuk bergegas menuju elektroda besar, dan beberapa makhluk lain—tentunya juga eksperimen dari lab Victor Frankenstein—bergabung dengan monster itu.]],
				['1'] = [[Seseorang akan meninggal di bangunan ini. Tapi tidak ada seorang pun di sini... kecuali pintu masuk ke satu ruangan yang tertutup penghalang. Mungkin mereka ada di dalam?]],
				['10'] = [["Yah, kau tak bisa lari dari takdir. Apalagi jika itu sudah tertulis di Buku Kematian. Segera temui aku begitu kamu sudah siap, kontrak terakhir menantimu."]],
				['3'] = [[Setelah mendengar suara cadel, aku memutuskan untuk berjalan melewati semak-semak agar tidak membuat takut siapa pun.<new_line><new_line>"Kau sepelti tak peduli sedikit pun! Ini HATI, bodoh!"]],
				['2'] = [[Aku menemukan satu mayat di dalam. Dia terlihat masih utuh, tapi dosimeterku mulai berdengung hebat. Dia mungkin terkurung di sini dan mati kelaparan. Atau mungkin karena radiasi. Sebaiknya aku pergi jika tidak ingin bernasib sama dengannya.]],
				['5'] = [[Aku hampir berada tepat di samping mereka. Terdengar suara cadel itu lagi:<new_line>"Tempat umum apa? Pelgi ke lual ke mana, tepatnya? Kau itu monstel! Dan itu hati!"]],
				['4'] = [[Suara cadel itu dijawab dengan suara menggeram yang menggelegar:<new_line>"Igor, itu hati milik orang mabuk yang aneh. Aku tak mau. Aku malu pergi ke luar, ke tempat umum dengan hati seperti itu!"]],
				['7'] = [[Si bungkuk membalas dengan suara cadelnya:<new_line>"Dia sungguh spesimen yang belkualitas. Akan kutunjukkan sesuatu padamu, Kawan—lihatlah elektloda cantik di sini..."]],
				['6'] = [[Aku melihat orang bungkuk pendek dan monster humanoid besar. Mereka menoleh ke arahku, dan dengan terkejut, monster itu melepaskan pria memar yang dia pegang. Pria itu segera bangkit, dan wajah monster itu tersenyum mengancam:<new_line>"Iiiigor! Ini beda cerita. Aku ingin hati MILIKNYA!"]]
			}
		},
		['place_stash_quest'] = {
			['episode'] = {
				['11'] = [[Pemuda itu mengangguk, menyelipkan rokok di sakunya, dan pergi.]],
				['10'] = [[Di semak-semak, aku menemukan pemuda kebingungan yang aku lihat di bar.<new_line>'Ini adalah masa yang penuh gejolak, kau tahu... Mereka memintaku untuk mengawasimu.'<new_line>Dia mengulurkan tangannya. Yang satu memegang sebungkus rokok, yang lain sebotol vodka.<new_line>'Ini. Selamat menikmati.']],
				['12'] = [[Pemuda itu mengangguk, menyimpan sebotol vodka, dan pergi.]],
				['1'] = [[Aku menemukan tempatnya di peta. Ini pasti akan menjadi tempat persembunyian yang bagus.]],
				['9'] = [[Di semak-semak, aku menemukan sekelompok orang yang tampak tidak ramah. Mereka jelas mencari wadah rahasiaku.]],
				['3'] = [[Aku mendengar suara mencurigakan dari semak-semak. Aku sudah mengerjakan tugas yang diberikan padaku, tetapi aku yakin mereka akan membayar lebih banyak dalam permukiman jika aku bisa mencari tahu siapa yang mengambil wadah rahasia itu.]],
				['2'] = [[Aku mengubur peti dan menutupi tempat persembunyian dengan daun-daun mati.]],
				['5'] = [[Aku melihat seseorang keluar dari semak-semak dengan seekor anjing yang diikat.]],
				['4'] = [[Ada suara keras dari ranting yang patah dan aku melihat para pemangsa itu.]],
				['7'] = [[Granat itu mendarat tepat di bawah kaki si penjarah. Itu meledak dengan kekuatan sedemikian rupa sehingga dia tidak pernah memiliki kesempatan. Anjing itu berlari menjauh, ketakutan oleh suara itu.]],
				['6'] = [[Aku sudah berurusan dengan musuh. Sekarang, aku harus segera pergi dari sini, sebelum bertemu orang lain.]]
			},
			['choice'] = {
				['11'] = [[Ambil vodkanya]],
				['8'] = [[Lempar granat dari tempat persembunyian]],
				['12'] = [[Ambil rokoknya]],
				['1'] = [[Gali dengan sekop]],
				['3'] = [[Kabur]],
				['2'] = [[Gali dengan tanganmu]],
				['5'] = [[Periksa semak-semak]],
				['4'] = [[Sembunyi]],
				['7'] = [[Melompat keluar dari persembunyian dan menyerang]]
			}
		},
		['frankenstein'] = {
			['episode'] = {
				['1'] = [[Benang itu mencapai ujungnya, dan itu jelas tempat monster itu berada. Awan badai hitam menggantung di atas bangunan terdekat dan melepaskan petir ke dalam penangkal petir bengkok yang berada di atap.]],
				['2'] = [[Tidak ada pintu—atau lebih tepatnya, itu tergeletak di samping jalan masuk. Di dalamnya berbau ozon menyengat, dan ada kabel-kabel bersilangan di seluruh tempat. Aku mengikutinya sambil masuk lebih dalam ke bangunan itu. Aku sampai ke ruangan di tengah-tengah tempat Monster itu terbaring diam di atas meja logam saat pria bungkuk yang aneh sibuk di dekatnya. Ketika dia melihatku, dia menjerit ketakutan dan menekan saklar di dinding. Ada kejutan listrik seketika, dan Monster itu siuman.]]
			}
		},
		['emba_energy_easy2'] = {
			['episode'] = {
				['1'] = [[Pintu bunker itu rusak. Seseorang mungkin sudah pernah kemari.]],
				['3'] = [[Ternyata benda itu adalah peti plastik semitransparan. Sesuatu yang tampak seperti tumbuhan aneh itu bisa terlihat di dalamnya.<new_line>Apakah ada orang yang mempelajarinya di sini sebelumnya?]],
				['2'] = [[Di dalamnya kosong dan semuanya terendam air. Makin jauh aku berjalan, airnya makin dalam. Di satu titik, aku harus tenggelam sampai ke leher untuk terus berjalan.<new_line>Tapi itu ruangan yang memiliki sesuatu di rak di atas permukaan air!]],
				['5'] = [[Akhirnya aku bisa keluar! Aku yakin para ilmuwan yang aku kenal ingin melihat ini, aku akan menunjukkannya kepada mereka saat bertemu nanti.]],
				['4'] = [[Peti itu berat dan sulit berjalan sambil membawanya ketika ketinggian air mencapai leherku. Dan aku baru mencapai tengah ruangan.]],
				['6'] = [[Setelah bersusah payah, akhirnya aku keluar dari air dan mulai bisa bernapas lega. Aku sudah muak dengan urusan bunker misterius dan riset ini.<new_line>Terutama karena aku menjatuhkan peti itu dan benda itu berada di suatu tempat di lantai di bawah air.]]
			},
			['choice'] = {
				['1'] = [[Masuk ke air dan berjalan maju]],
				['4'] = [[Terus berjalan dengan peti]],
				['3'] = [[Letakkan peti dan pergi]],
				['2'] = [[Berjalan kembali dengan peti]]
			}
		},
		['emba_fight_easy2'] = {
			['episode'] = {
				['1'] = [[Meskipun pertanian itu tidak tampak tua dan lapuk, tapi diliputi sepenuhnya oleh tumbuhan mutasi baru ini. Kawanan biomutan ada di sana, berkeliaran dan memakani jamur yang terus tumbuh.]],
				['4'] = [[Sebuah pintu dari bawah tanah terbuka di dekat rumah itu. Seorang petani muncul dari bawah dengan wajah ketakutan.<new_line>Dia melihat ke sekeliling dengan wajah terkejut dan mendekatiku. Dia memberiku kantong yang mungkin berisi makanan dan akhirnya bisa menggumamkan ucapan terima kasih.]],
				['3'] = [[Bahaya sudah disingkirkan. Aku menemukan makanan di sini yang belum sempat dirusak oleh jamur itu.<new_line>Jika pemilik tempat ini benar-benar kembali kemari, aku pikir mereka tidak keberatan aku membawa ini sebagai imbalan kecil atas keselamatan mereka.]],
				['2'] = [[Aku tak melihat manusia. Jika ada yang selamat dan berusaha untuk kembali, mereka akan langsung jadi santapan kawanan biomutan itu.<new_line>Aku mungkin bisa menyelamatkan beberapa jiwa jika aku membinasakan makhluk-makhluk itu sekarang.]]
