		},
		['winter_queen'] = {
			['choice'] = {
				['1'] = [[Berangkat ke istana es]],
				['3'] = [[Masuk ke ruangan]],
				['2'] = [[Masuk]],
				['5'] = [[Kembali]],
				['4'] = [[Lalu apa?]]
			},
			['episode'] = {
				['9'] = [[Beruang itu membawa kami kembali dengan cepat. Gadis Salju duduk di atas punggungnya. «Selesaikan menghias pohon Tahun Baru, aku akan menyiapkan semuanya,» katanya padaku, lalu terbang ke suatu tempat di kejauhan.]],
				['8'] = [[Gadis Salju menoleh padaku dan ada jejak air mata beku di pipinya. «Aku tahu apa yang harus kita lakukan,» katanya. «Cepat, kita harus kembali secepat mungkin.» Lalu dia lari ke pintu keluar.]],
				['1'] = [[Seperti sebelumnya, aku mengangkat Bintang Kutub dan Umka muncul di depan kami. Aku membantu Gadis Salju duduk di atas punggungnya kemudian ikut memanjat naik. Waktunya berangkat.]],
				['3'] = [[Umka menunggu di gerbang, sementara aku dan Gadis Salju masuk untuk mencari Kakek Salju. Di dalam, istana itu terlihat bak gua es yang sangat luas. Kemudian, kami pun sampai di ruang singgasana.]],
				['2'] = [[Mengangguk, beruang itu berlari ke atas, lalu melompat ke udara. Aku dan Gadis Salju berhasil berpegangan di punggungnya. Cahaya Utara dan gemerlap ribuan bintang mengelilingi kami. Beberapa detik kemudian kami sampai di gerbang istana es, yang ternyata terletak jauh di wilayah utara.]],
				['5'] = [[Habis tenaganya karena pertempuran, Ratu Es menatap kami dengan kebencian di matanya, «Baiklah, tapi aku akan membawa kalian bersamaku!» Setelah berkata itu, dia mengetuk tongkatnya untuk terakhir kali. Terdengar suara retakan keras, dan bongkahan es mulai berjatuhan dari langit-langit. Retakan mulai menyebar ke seluruh dinding. Istana ini akan runtuh.]],
				['4'] = [[Ratu Es duduk di singgasananya dengan Kakek yang ada di sebelahnya, tepat seperti yang kulihat di cermin. «Ternyata kau benar-benar datang,» kata Ratu Es, bibirnya nyaris tidak bergerak. «Tak apa, sekarang kau akan menjadi bagian dari istana ini.» Sambil berkata, dia melambaikan tongkatnya dan melayangkan aliran angin dingin ke arah kami.]],
				['7'] = [[Aku dan Gadis Salju berdiri memandangi bongkahan es besar yang menahan langit-langit yang tadinya adalah ruang singgasana. Jauh di dalamnya, kami bisa melihat sosok Kakek Salju dan Ratu Es yang membeku.]],
				['6'] = [[Tiba-tiba Kakek Salju muncul di samping Ratu Es. Dia pasti membebaskan dirinya saat esnya retak. Lelaki tua itu merebut tongkat dari tangan Ratu Es yang lemas, dan mematahkannya menjadi dua bagian! Lalu terjadi ledakan.]]
			}
		},
		['newyearEvent_craft_easy'] = {
			['episode'] = {
				['9'] = [["Ada sedikit kesalahan dengan hadiahnya, tapi aku masih belajar tentang duniamu, dan aku sangat rindu elf setiaku di sini. Lain kali, aku akan merenungkan kesalahan ini, dan akan membuat hadiah yang lebih baik. Tapi kau sudah melakukan perbuatan yang mulia!"]],
				['8'] = [[Wanita itu dengan cepat merobek kertas kado dan membuka kado itu. "Oh, apa benar ini untukku? Bagus sekali. Terima kasih. Mungkin aku salah paham tentangmu. Aku akan mempertimbangkan pekerjaan itu."]],
				['1'] = [["Ho ho ho! Halo kawan! Aku butuh bantuanmu. Masalahnya, bukan hanya anak-anak yang berhak mendapatkan hadiah—beberapa orang dewasa berhak mendapatkannya. Tapi pabrikku tak bisa membuat hadiah yang cocok untuk orang dewasa di sini. Tak ada cetak biru atau materialnya. Bisakah kau membuatkan sesuatu untukku?"]],
				['3'] = [[Aku mengambil hadiah itu, dan kami berjalan ke salah satu rumah di desa. Business mengetuk pintu.<new_line>"Ho ho ho!"]],
				['2'] = [["Oh, sangat menarik! Oh, bagus sekali... Bagaimana jika kita pergi dan mengantarkan hadiah ini bersama? Sepertinya penerimanya akan sangat senang melihat perajin yang membuat barang ini."]],
				['5'] = [["Aku ingin memperkenalkan Sinterklas baru di wilayahmu. Dia membawakanmu hadiah kecil. Dia akan bertanggung jawab atas sihir dan keajaiban di sini."]],
				['4'] = [[Seorang wanita membuka pintu.<new_line>"Siapa? Oh, ini... Sudah ribuan kali aku bilang bahwa aku tak akan datang ke pabrikmu!"]],
				['7'] = [[Wanita itu dengan cepat merobek kertas kado dan membuka kado itu.<new_line>"Kau serius? Tentunya terima kasih, tapi sebaiknya kau pergi sebelum anjingku bangun."]],
				['6'] = [["Ah, ini untukku? Apa itu?"]]
			},
			['choice'] = {
				['1'] = [["Ambil ini."]],
				['3'] = [["Baiklah, ayo pergi."]],
				['2'] = [["Biar kulihat yang bisa kulakukan." (Pergi)]],
				['5'] = [[Serahkan hadiah]],
				['4'] = [["Tidak, terima kasih, aku harus pergi." (Pergi)]],
				['6'] = [[Tinggalkan rumah]]
			}
