			['episode'] = {
				['1'] = [["Salam, Pak. Apa yang kau butuhkan?"]],
				['4'] = [["Cukup, cukup, aku hanya bercanda, mengapa kau begitu tegang dan galau? Ini, hadiah untukmu."]],
				['3'] = [["Aku belum pernah melihatmu sebelumnya! Jika kau tidak akan menjual sesuatu, silakan pergi, Pak."]],
				['2'] = [["Aku tak mengerti maksudmu. Apakah kau ingin menjual sesuatu?"]]
			},
			['choice'] = {
				['1'] = [["Aku datang untuk mengambil hadiah."]],
				['3'] = [["Kita sudah sepakat..."]],
				['2'] = [["Kita sudah sepakat, perlu aku ingatkan lagi?" (meletakkan tanganmu di senjata)]]
