		['camp_defence_quest'] = {
			['episode'] = {
				['9'] = [[Tapi, secara keseluruhan, ini tidak terlalu buruk. Mereka akan menjadi lebih baik, itu hanya masalah waktu.]],
				['8'] = [[Kita berhasil mempertahankan posisi, tetapi beberapa pejuang kita terluka parah.]],
				['13'] = [[Jika kau mempercayai peta, aku makin dekat dengan kamp.]],
				['12'] = [[Aku tidak dapat membantu orang-orang ini, tetapi akan kubalas dendam mereka.]],
				['11'] = [[Para bandit akhirnya pergi. Aku mencari di kamp, tetapi hanya menemukan mayat sekutu kami.]],
				['1'] = [[Seorang pria bersenjata berdiri di pos terdepan:<new_line>'Apakah kau datang untuk membantu? Oke, ambil posisi. Mereka sudah dalam perjalanan.']],
				['10'] = [[Mendengar suara, aku bersembunyi. Beberapa orang asing berkeliaran di sekitar kamp.<new_line>Aku terlambat. Para bandit sudah mengambil alih kamp.]],
				['3'] = [[Di titik pertemuan, aku hanya melihat satu mayat tergeletak di tanah.]],
				['2'] = [[Saat aku mendekati pos terdepan, aku mendengar suara tembakan. Tidak diragukan lagi: para bandit sudah menyerang kamp.]],
				['5'] = [[Aku melihat tempat yang sempurna untuk lubang serigala di dekat kamp dan berhasil menggalinya dengan cepat.]],
				['4'] = [[Aku berhasil memasang batang kayu dengan tiang.]],
				['7'] = [[Kita mengatur segalanya di bawah kendali. Kita mempertahankan tanah kami. Tidak ada yang akan mengganggu kita dalam waktu dekat.]],
				['6'] = [[Ada bandit. Banyak sekali...<new_line>Tapi ada banyak dari kita juga. Dan kami siap!]]
			},
			['choice'] = {
				['9'] = [[Ambil posisi dan tunggu]],
				['8'] = [[Lihat apa lagi yang harus dilakukan]],
				['14'] = [[Ikuti pertarungan]],
				['10'] = [[Pertahankan kamp kita]],
				['17'] = [[Balas dendam rekan-rekan yang gugur]],
