		['embacity_biolab_6'] = {
			['episode'] = {
				['1'] = [["Gangguan baru lagi... Apa, si Gerin bodoh itu tidak selamat? Di sisi lain, aku senang saat kau bunuh diri."]],
				['3'] = [[Di aula, aku melihat seorang pria mengenakan baju pelindung berat. Dia berjalan di sekitar autoklaf di aula.<new_line>Setelah menyadari kehadiranku, pria itu bersiap untuk menyerang.]],
				['2'] = [["Bisa dibilang begitu. Panggil aku Utusan. Jadi, kau sudah mengetahui sesuatu, dan kau sudah melakukan banyak hal. Tapi kau seharusnya tidak ikut campur dengan desainku, aku menghabiskan begitu banyak waktu untuk mengerjakan Pengembang biak. Jadi sekarang giliranmu untuk mati."]]
			},
			['choice'] = {
				['1'] = [["Apakah suaramu yang Gerin bicarakan?"]]
			}
		},
		['embacity_biolab_4'] = {
			['episode'] = {
				['1'] = [[Aku hanya melihat satu sosok di aula ini. Dari jarak ini, dia terlihat seperti manusia, tapi ada sesuatu yang sangat tidak wajar dari gerakannya.<new_line>Sosok itu berjalan mengelilingi aula, memeriksa autoklaf di sana. Melihatku, dia bersiap untuk menyerang.]]
			}
		},
		['provision_bar'] = {
			['episode'] = {
				['1'] = [["Bagus, kami berharap banyak pada operasi ini. Kontribusimu akan membantu kami mendapatkan informasi penting tentang proyek EMBA. Terima kasih."]]
			}
		},
		['gigahive_ufa'] = {
			['episode'] = {
				['1'] = [[Ironisnya, lebah mutan sudah membangun sarang terbesar yang pernah kulihat tepat di bekas institut pembiakan lebah.<new_line>Tanah di sekitar sarang hampir berguncang karena suara dengungan yang luar biasa.]],
				['3'] = [[Menjelajahi sarang bukanlah hal yang mudah. Aku pernah mendengar bahwa ada orang-orang yang memburu lebah mutan di Izhevsk. Mudah-mudahan mereka akan bisa memberiku beberapa nasihat yang baik.]],
				['2'] = [[Aku mendapati diriku berada di koridor yang gelap dan tampaknya tak berujung. Setelah hanya beberapa menit, aku mulai melihat lepuh bergelegak di kulitku.<new_line>]],
				['5'] = [[Setelah keluar sebagai pemenang, aku memutuskan untuk mempercepat langkah. Terbang menyusuri koridor, aku menemukan ruangan lain yang tertutup sarang lebah dan larva. Dan bahkan sebelum aku bisa meluruskan pikiran, aku melihat seekor induk lebah yang marah menyerang langsung ke arahku.]],
				['4'] = [[Melangkah ke dalam, aku mendapati diriku berada di koridor yang gelap dan tampaknya tak berujung. Saat berjalan menyusuri lorong, aku menemukan sebuah ruangan yang penuh dengan lebah.]],
				['6'] = [[Saat ratu lebah mati, aku diliputi oleh gelombang ketakutan dan penderitaan yang mengerikan. Dengungan mengerikan itu makin keras: lebah-lebah mulai melarikan diri dari sarangnya dengan ketakutan.<new_line>Aku harus bergegas dan memeriksa area itu, jadi aku bisa keluar dari sini.]]
			},
			['choice'] = {
				['1'] = [[Melarikan diri]],
				['2'] = [[Gunakan penawarnya dan masuk]]
			}
		},
		['emba_commander_medium2'] = {
			['episode'] = {
				['9'] = [["Tahukah kau bahwa kadal dan biawak adalah pemakan bangkai dan mereka dapat dengan mudah mengatasi rintangan apa pun? Setidaknya, mereka tidak terbang.<new_line>Aku selalu tertarik dengan reptil. Dan ini mempunyai semua kemampuan itu ... Semoga aku bisa menangkapnya kali ini.<new_line>Hmm, ya, sudah cukup bicaranya. Saatnya bergerak. Ini radionya. Kau sudah siap?"]],
				['8'] = [[Bagus. Sambil menunggu gelap, aku bisa menceritakan sesuatu tentang biomutan.<new_line>Melihat senjatamu, apa kau sudah membersihkannya baru-baru ini? Aku tak akan kaget kalau senjata itu macet di setiap tembakan kedua."]],
				['11'] = [["Kau memang banyak akal dan aku bisa mengandalkanmu. Tentu akan lebih baik jika kita menangkap sesuatu hidup-hidup ... yah, penasaran saja.<new_line>Ah, tidak masalah. Lain kali. Terima kasih atas bantuanmu."]],
				['1'] = [["Tunggu. Aku sedang mengerjakan sebuah proyek kecil dan aku perlu bantuan. Aku perlu seseorang yang bisa membedakan antara senapan serbu dengan sekop.<new_line>Tapi ada urusan lain sebelumnya. Aku kehabisan kamuflase. Dapatkah kau membawakan pengencer untuk porsi baru selagi aku mengencerkan catnya?"]],
				['10'] = [[Kami menyelesaikan urusan masing-masing sebentar. Setelah gelap mulai datang, wanita Pemburu itu bangkit, memberikan radio kepadaku, dan menyarankan untuk memulai.]],
				['3'] = [["Baik, mari bahas urusan kita. Operasi ini rumit dan Progress tidak bisa menemukan orang yang berpengalaman untuk jadi partnerku.<new_line>Jadi, mau tidak mau, kaulah orangnya — kau pernah berburu biomutan di malam hari?"]],
				['2'] = [["Tunggu. Aku sedang mengerjakan sebuah proyek kecil dan aku perlu bantuan. Aku perlu seseorang yang bisa membedakan antara senapan serbu dengan sekop.<new_line>Tapi ada urusan lain yang harus dikerjakan lebih dahulu. Aku perlu tali, jaga-jaga aku membawa seseorang yang masih hidup. Kau punya? Cukup jauh kalau harus berjalan kaki ke ATV."]],
				['5'] = [["Ya. Jadi, bagaimana kalau kita sedikit bertualang? Kau tidak takut gelap, 'kan?"]],
				['4'] = [["Tidak banyak orang yang pernah melakukannya. Itulah sebabnya aku ingin tahu bagaimana makhluk ini berperilaku dalam gelap.<new_line>Mungkin aku bahkan bisa menangkap seseorang! Itu akan menjadi terobosan untuk proyekku.<new_line>Tapi itu pekerjaan untuk dua pemburu."]],
				['7'] = [["Aku harus bergerak dengan gesit dan ringan, berada dalam kegelapan tanpa perlengkapan tambahan.<new_line>Itulah sebabnya aku memintamu untuk membawa teropong malam, mengambil posisi di sekitar sambil memegang radio, dan melaporkan situasi medan tempur kepadaku. Aku akan bertindak sesuai laporanmu."]],
				['6'] = [["Kenapa, kau terburu-buru pergi ke suatu tempat? Oh, ya, intinya adalah aku ingin menyerang biomutan dalam keadaan gelap dan melihat bagaimana mereka mencari arah tanpa ada cahaya. Eksperimen lainnya."]]
			},
			['choice'] = {
				['9'] = [[Mulai Operasi]],
				['8'] = [[Menyiapkan Senjataku]],
				['1'] = [["Apa yang sebenarnya kau inginkan dariku?"]],
				['3'] = [["Aku tidak takut, apa yang harus dilakukan?"]],
				['2'] = [["Untuk kita berdua?"]],
				['5'] = [["Aku bisa berusaha."]],
				['4'] = [["Apa yang kau butuhkan dariku?"]],
				['7'] = [[Cari tahu tentang Biomutan]],
				['6'] = [["Aku belum siap."]]
			}
		},
