	['chatRules'] = {
		['1'] = [[Pesan yang berisi materi berikut tidak diizinkan dalam obrolan:<new_line>1. Penghinaan dan bahasa yang tidak senonoh.<new_line>2. Spam, flooding.<new_line>3. Tautan situs web.<new_line>4. Ancaman dan pesan yang mendorong kekerasan atau kegiatan ilegal lainnya.<new_line>5. Kebencian ras, etnis, atau agama.<new_line>6. Bahasa atau simbol Nazi atau ekstremis.<new_line>7. Pesan atau slogan yang menghasut.<new_line>8. Materi atau bahasa pornografi.<new_line>9. Pesan yang mendorong pelanggaran aturan.<new_line>10. Diskusi tentang hukuman yang diterapkan pada pemain.]],
		['4'] = [[Untuk informasi lebih lanjut, lihat Perjanjian Pengguna:]],
		['3'] = [[Saat terjadi pelanggaran aturan, moderator server berhak untuk menerapkan:<new_line>Mute—mencegah pemain mengirim pesan ke dalam obrolan selama jangka waktu tertentu.<new_line>Ban—sepenuhnya mencegah pemain berinteraksi dengan pemain lain.]],
		['2'] = [[Tindakan berikut dilarang (termasuk diskusi tentang):<new_line>1. Penjualan atau transfer akun ke pemain lain.<new_line>2. Transaksi antar pemain yang melibatkan uang sungguhan.<new_line>3. Penggunaan program hack atau klik otomatis.<new_line>4. Penggunaan kerentanan atau bug; penipuan item dalam game.<new_line>5. Instalasi versi game lama (bajakan).<new_line>6. Penggunaan nama panggilan yang melanggar salah satu aturan permainan.]]
	},
