	['responseChangeName'] = [[Kamu telah mengubah nama menjadi <name>]],
	['broken_moto'] = {
		['description'] = [[Ini adalah sepeda motor Ural bersespan yang rusak. Mungkin, aku bisa memperbaikinya.]],
		['name'] = [[Motor yang rusak]]
	},
	['base'] = [[Alas]],
	['scroll_summon'] = {
		['name'] = [[Gulungan Memanggil]]
	},
	['battle_ap_description'] = [[Poin Tindakan diperoleh setiap putaran dan dijumlahkan. Setiap tindakan memerlukan sejumlah tertentu poin.]],
	['buy_help_box2'] = {
		['description'] = [[Berisi barang untuk membantu berjuang hidup.]],
		['name'] = [[Kit berjuang hidup]]
	},
	['hand_mill'] = {
		['description'] = [[Ini membantumu menggiling gandum menjadi tepung]],
		['name'] = [[Gilingan tangan]]
	},
	['npc'] = {
		['svetlana'] = {
			['episode'] = {
				['16'] = [[(Svetlana mengangguk pelan. Jawabanku tidak mengejutkannya. Tapi kenapa dia sangat cemas?)<new_line><new_line>Aku merasa kasihan padamu, pengembara. Orang-orang sepertimu adalah yang pertama mati. Orang-orang yang punya hati. Siapa yang mengambil risiko untuk menyelamatkan orang lain. Kupikir semua orang sepertimu sudah mati di tahun '84. Dan yang tersisa hanyalah bandit dan orang-orang seperti aku, orang-orang yang telah berhenti peduli sama sekali.]],
				['10'] = [[Kamu tidak ingat apa-apa? Apa yang terjadi?]],
				['14'] = [[(Pintu ke kamar Svetlana ditutup. Tapi sepertinya dia ada di dalam. Bisa kuketuk, tapi sepertinya dia hanya ingin sendiri untuk sementara waktu)]],
				['12'] = [[Tapi di sini, kita semua bekerja untuk kebaikan bersama. Dan kita menjalankan peraturan yang ketat: setiap orang memiliki pekerjaan yang harus dilakukan, tetapi masih banyak pekerjaan yang harus dilakukan. Kita bisa menggunakan bantuan tambahan untuk membantu.]],
				['22'] = {
					['1'] = [[Aku mengerti. Tapi kamu harus memahamiku. Aku tidak punya kerabat dekat, dan tidak ada yang membutuhkan obat untuk penyakit itu lagi. Apa yang harus kulakukan? Bertahan hidup. Tujuan hidupku adalah untuk hidup sedikit lebih lama. Tapi aku sudah muak dengan kehidupan ini!]],
					['2'] = [[Tapi kita bisa melakukan satu hal: menemukan kebenaran. Cari tahu siapa pelaku yang telah membunuh jutaan orang dan mengubah negara kita, bahkan mungkin dunia, menjadi padang gurun. Dan cari tahu mengapa mereka melakukannya. Itu penting, kan? Bukan hanya untukku, tapi untuk kita semua?<new_line>Aku tahu kamu tidak perlu melakukannya. Yang bisa aku lakukan adalah meminta. Dan menawarkan hadiah.]]
				},
				['32'] = [[(Svetlana melompat dan menatapku dengan tatapan kemarahan yang aneh.)<new_line><new_line>Aku tahu itu! Tapi lebih gila lagi kalau harus pergi ke padang gurun untuk mencari keluargamu!]],
				['20'] = [[Dia seorang ahli mikrobiologi. Salah satu yang terbaik di negara ini. Dia bekerja di Moskow, di Institut Virologi. Itu terakhir kali aku melihatnya, dia meninggal karena serangan jantung pada Juni 1984. Ibuku memberitahuku melalui telepon. Aku bahkan tidak sempat mengucapkan selamat tinggal. Aku bahkan tidak pergi ke pemakaman, aku beristirahat di tempat tidur karena sakit amandel.]],
				['30'] = {
					['1'] = [[(Svetlana berbicara lebih tenang)<new_line><new_line>Epidemi itu! Epidemi itu mewabah di saat yang sama, di berbagai kota, di semua ujung Uni Soviet! Sepanjang sejarah, tidak ada virus yang pernah menyebar seperti itu. Penyakit selalu bisa dilokalisasi, kamu bisa membuat karantina, dan mengambil tindakan pencegahan. Setidaknya, kamu bisa mencobanya. Tapi tidak dengan penyakit ini.]],
					['2'] = [[Aku tadi memeriksanya. Virus itu menyerang siapa pun dan di mana pun secara bersamaan. Tidak ada cara untuk menghentikannya.<new_line>Ini terdengar gila, tetapi mungkinkah ada seseorang yang dengan sengaja memulai epidemi ini? Menginfeksi orang-orang di berbagai kota pada waktu yang bersamaan?]]
				},
				['1'] = [[(Wanita itu menatap ke mataku dengan mata abu-abunya yang sedih dan tersenyum hangat)<new_line><new_line>Halo. Kamu sudah lama tidak bertemu dengan orang-orang baru. Jadi, siapa kamu? Dari mana asalmu?]],
				['3'] = [[Tentu, ada pemukiman lain, tetapi mengapa aku harus meninggalkan Tver? Kami punya cukup makanan. Para bandit meninggalkan kita sendirian. Jangan bodoh, tetap di sini!]],
				['2'] = [[(Svetlana berbalik)<new_line><new_line>Lihatlah ke luar jendela. Terlihat padang gurun. Pertama epidemi, lalu perang nuklir... Bumi mati. Kamu tidak bisa bertahan hidup di sana. Radiasi, pemangsa. Orang-orang gila yang sudah tidak punya apa-apa. Bandit. Kamu tidak akan menemukan siapa pun di luar sana. Tapi jika kamu pergi, kamu tidak akan kembali. Kami tidak akan mencoba menahanmu di sini, tapi berpikirlah sebentar!]],
				['5'] = [[(Svetlana menatap mataku lama, lalu mengalihkan tatapannya)<new_line><new_line>Aku juga berjuang selama yang aku bisa. Aku percaya bahwa kita masih bisa melakukan sesuatu, kekacauan ini benar adanya...]],
				['4'] = [[(Svetlana menatapku dengan sedih)<new_line><new_line>Kamu tahu, ketika aku tiba di sini, aku juga berusaha menemukan kerabatku. Dan korban hidup lainnya. Aku mencari cara untuk menghubungi rekan-rekanku, untuk mencari tahu lebih jauh tentang epidemi terkutuk ini, tentang pengeboman. Aku melakukan perjalanan dari kota ke kota, sampai aku kehabisan bahan bakar. Tapi semua sia-sia. Semua usahaku hangus!]],
				['7'] = [[Betulkah? (wanita itu tercengang) Ini adalah perjalanan yang panjang dan berbahaya. Masuklah, buat dirimu nyaman. Omong-omong, aku Svetlana.]],
				['6'] = [[(Svetlana menggenggam kepalanya dengan tangannya)<new_line><new_line>Kamu tidak mengerti ...]],
				['9'] = [[Baiklah, tapi... kenapa kamu mencariku?]],
				['8'] = [[(Svetlana mengerutkan kening)<new_line><new_line>Ya, itu benar. Tapi bagaimana kamu tahu semua ini?]],
				['28'] = [[Aku harus mengungkap kebenaran. Kebenaran tentang malapetaka ini.]],
				['38'] = [[(Ketika aku mengetuk pintu Svetlana, pintu itu lama terbukanya.<new_line>Seorang gadis muda sedang duduk di sofa kecil di kantor. Dia malu ketika melihatku, mengucapkan terima kasih kepada Svetlana dan berlari keluar. Svetlana tertawa dan berkedip padaku.)<new_line><new_line>Gadis bodoh! Jadi, apa yang baru?]],
				['18'] = [[Aktifkan Day R pada 17 Agustus seperti yang diperintahkan'... Apa ini? Dari mana kamu mendapatkannya?]],
				['27'] = [[(Svetlana menurunkan suaranya, seolah takut akan ada yang mendengar)<new_line><new_line>Ada satu hal yang bisa kamu lakukan. Sudah kumulai sejak lama, tetapi tidak pernah terselesaikan. Aku tidak bisa, aku sudah menyerah di tengah jalan (wajah Svetlana tampak masam). Tapi kamu bisa melakukannya!]],
				['17'] = [[(Svetlana mulai tenang dan menurunkan suaranya lagi)<new_line><new_line>Dan lagi, kita masih tahu sesuatu. Wabah mematikan pertama muncul pada 2-3 September. Mempertimbangkan masa inkubasi, sepertinya orang-orang itu terinfeksi sekitar 17-19 Agustus. Artinya...]],
				['13'] = [[(Svetlana terdiam, lalu dia berdiri dan berjalan ke jendela)<new_line><new_line>Kenapa tidak?]],
				['15'] = [[(Ketika aku bangun, Svetlana sedang berdiri di sampingku. Dia menatapku dengan saksama, dengan gugup menggigit-gigit bibirnya.)<new_line><new_line>Apakah kamu berubah pikiran? Ada kemungkinan kamu akan tinggal?]],
				['33'] = [[(Ketika dia melihatku, Svetlana tersenyum sejenak sebelum dia menyembunyikan gejolak hatinya dan memasang ekspresi cuek yang dingin di wajahnya.)<new_line><new_line>Nah, bagaimana kemajuanmu dalam mencari istri dan anakmu?]],
				['23'] = [[Aku akan pergi dan bersiap-siap untuk berangkat. Bisa menunggu sedikit lebih lama?]],
				['31'] = [[Aku tidak tahu. Aku telah mencoba mencari tahu semenjak diriku ditahan oleh bandit. Aku mencoba mencari penjelasan yang rasional. Kemudian di sini, di luar Tver, aku bertanya pada para pengungsi lainnya. Aku melihat melalui surat kabar, cerita tentang penyakit itu...]],
				['21'] = [[(Svetlana menahan napas)<new_line><new_line>Jadi kamu setuju untuk membantuku?]],
				['48'] = [[Pergilah, kumohon... Ada hal yang harus kukerjakan.]],
				['47'] = [[Ya, kebenarannya. Tapi yang kamu ceritakan dari awal sampai akhir adalah kebohongan yang menjijikkan. Aku tidak tahu siapa yang ada di belakangnya, tapi aku harap itu bukan dirimu. Tetapi aku tidak berniat mendengarnya lagi.]],
				['46'] = [[(Svetlana berbalik ke arahku. Matanya benar-benar acuh tak acuh, tetapi tangannya gemetar.)<new_line><new_line>Dan mengapa kamu menceritakan semua ini kepadaku?]],
				['45'] = [[(Ketika aku selesai, kuperhatikan Svetlana sedang menatap kosong ke arah dinding belakangku. Aku mengharapkan semacam reaksi, tetapi bukan ini. Aku menggerakkan kepala, mencoba menarik perhatiannya, tetapi dia tidak menanggapi. Keheningan tegang mengikuti.)]],
				['44'] = {
					['1'] = [[Baiklah. Sepertinya kamu ingin menceritakan sesuatu. Mari bercerita. Apakah kamu tahu sesuatu? Tentang apa yang kita diskusikan?]],
					['2'] = [[(Menatap wajah Svetlana yang terfokus tapi senang, aku tersendat, tidak tahu bagaimana memulainya. Aku menggumamkan sesuatu yang tidak dapat dimengerti. Suaraku tiba-tiba serak. Svetlana mengulurkanku segelas air. Aku meminumnya dalam satu tegukan dan memutuskan.)]]
				},
				['43'] = [[Ini bukan hanya sebuah kesenangan baginya. Ini adalah kesempatan yang menyenangkan bagi kita semua. Kita selamat dari epidemi dan perang nuklir. Kemudian kita menghabiskan bertahun-tahun berjuang melawan kelaparan! Tapi ada musuh yang bahkan lebih menakutkan daripada kelaparan, dan itu adalah keputusasaan. Dan kamu tahu? Hari ini musuh belum menang. Tuhanlah yang menentukan akhir segalanya. Itu berarti kita punya masa depan!]],
				['42'] = [[Kamu tahu, pertama baginya. Kamu belum berhasil? Dia hamil!]],
				['41'] = [[(Svetlana mulai tertawa lagi. Dia dalam suasana hati yang sangat baik.)<new_line><new_line>Tidak memperhatikan. Dia hanya kurang sehat. Ini pertama baginya.]],
				['40'] = {
					['1'] = [[(Svetlana tiba-tiba meraih bagian depan bajuku. Aku tersentak mundur.)<new_line><new_line>Cukup! Jangan katakan sepatah kata pun tentang ayahku! Dia orang yang hebat! Dia mencintaiku, mencintai ibuku, mencintai negaranya! Dia meninggal karena serangan jantung, paham?! Itu kata ibuku di suratnya! Serangan jantung, kamu dengar... Serangan jantung...]],
					['4'] = [[Terima kasih telah mengungkap kebenaran. Aku minta maaf karena telah meneriakimu. Kamu tidak bersalah di sini. Apa yang kamu katakan: itu rasa sakitku dan juga masalahku. Milikku sendiri.<new_line>Sekali lagi terima kasih dan... sampai jumpa.]],
					['3'] = [[(Svetlana sedikit tenang setelah beberapa saat. Dia menyeka hidungnya dengan sapu tangan dan duduk di sofa. Aku duduk di sampingnya, tetapi dia mengangkat tangannya, menolak. Aku mengangguk dan menuju ke pintu. Svetlana harus memikirkan kembali banyak hal sekarang. Dan aku tidak bisa membantunya dengan semua itu.)<new_line><new_line>Tunggu...]],
					['2'] = [[(Svetlana melepaskan cengkeramannya dan mulai terisak. Pahit, isak tangis yang membuat dadaku sangat sakit. Dia menangis dan, seperti idiot, aku hanya menatapnya, terdiam. Di sini lain, aku bahkan mulai memaki diriku sendiri dalam hati karena aku tidak membohongi Svetlana saja, bukannya malah membunuhnya dengan kebenaran itu.)]]
				},
				['11'] = [[(Svetlana menatapku dengan simpati yang tulus)<new_line><new_line>Itu mengerikan! Sungguh ajaib kamu bisa sampai sejauh ini! (Dia tersenyum ramah) Tapi itu semua masa lalu. Kami akan menjagamu. Kami akan memberimu kamar, dan memberimu makan. Dan sedikit demi sedikit, ingatanmu akan kembali, aku yakin.]],
				['36'] = [[(Svetlana menyapaku dengan senyuman.)<new_line><new_line>Halo! Baik? Ada berita?]],
				['37'] = [[Baik-baik saja. Jika kamu bisa melawan keputusasaan dan hilangnya harapan.]],
				['35'] = [[Semuanya baik.<new_line><new_line>(Nada Svetlana dingin dan lugas. Dia jelas kesal.)]],
				['34'] = {
					['1'] = [[(Senyum Svetlana sekarang bersinar dengan kegembiraan yang jelas.)]],
					['2'] = [[Aku akan pergi dan bersiap-siap untuk berangkat. Bisa menunggu sedikit lebih lama?]]
				},
				['25'] = [[Kamu yakin?]],
				['24'] = [[(Svetlana mengerutkan kening, lalu berbalik dan berjalan ke jendela)<new_line><new_line>Kalau begitu kembalilah jika punya waktu.]],
				['39'] = [[Tidak perlu terlalu formal. Bagaimana pun juga, kita seperti teman lama sekarang, bukan?]],
				['29'] = [[Kamu membaca buku harianku, kan? Di salah satu halaman yang hilang, aku menulis ide yang aneh. Aku masih tidak tahu kenapa aku tidak memikirkannya sejak dulu. Tapi ide ini... membuatku terjaga di malam hari.]],
				['19'] = [[Tidak. Aku tidak tahu. Tapi sepertinya aku pernah mendengar nama itu sebelumnya. Dari ayahku. Ketika dia mengunjungi institut itu, dia berbicara dengan seseorang di telepon dan menyebut ini 'Day R.' Aku bertanya apa artinya, tapi dia hanya mengalihkannya dengan lelucon. Tapi aku ingat.]],
				['26'] = [[(Svetlana tersenyum aneh)<new_line><new_line>Apa pentingnya? Aku tidak mempertaruhkan apa pun. Oke, sial kamu, jika ingin pergi – pergilah. Tapi kalau kamu tetap mau pergi...<new_line><new_line>(Dia berpikir sejenak, memilih apa yang mau dikatakan)]]
			},
			['name'] = [[Svetlana]],
			['choice'] = {
				['16'] = [[17 Agustus... Tunggu sebentar... Aku sudah melihat tanggal itu di suatu tempat sebelumnya... (tunjukkan Svetlana telegram milik pria yang mati di Novgorod)]],
				['10'] = [[Aku mencari korban yang selamat. Orang yang bisa mengatakan yang sebenarnya tentang apa yang terjadi. Kembali di tahun 1984. Seperti yang kamu lihat, aku tidak ingat apa-apa... Aku bahkan lupa namaku sendiri,]],
				['14'] = [[Tidak, aku harus pergi.]],
				['12'] = [[Svetlana, aku...]],
				['22'] = [[Baiklah, aku mengerti apa yang bisa aku lakukan.]],
				['32'] = [[Aku yakin.]],
				['20'] = [[Aku akan mencoba. Tapi aku tidak akan menjanjikan apa pun. Prioritas utamaku adalah menemukan istri dan anak perempuanku.]],
				['30'] = [[Svetlana, ini gila. Siapa yang mau melakukannya dan apa keuntungan mereka?]],
				['1'] = [[Aku punya istri dan anak perempuan. Aku tidak tahu di mana mereka berada, tapi aku yakin mereka masih hidup dan sedang menungguku. Aku harus menemukan mereka.]],
				['3'] = [[Svetlana, semua yang kamu katakan itu benar, tapi... jika kamu berada di posisiku, bukankah kamu akan tetap pergi dan mencari keluargamu? Tidak peduli betapa berbahayanya itu?]],
				['2'] = [[Kau benar, tapi aku harus mencoba. Aku tidak bisa meninggalkan keluargaku. Dan setelah itu, mungkin ada permukiman lain. Aku akan terus mencari sampai aku tahu apa yang terjadi.]],
				['5'] = [[Tapi kamu tidak melakukan apa-apa? Kamu mengenakan jas dokter. Kamu membantu orang-orang. Kamu belum menyerah.<new_line>Dan ... apakah kamu menyerah ketika bandit membawamu sebagai tahanan? Tidak, kamu meracuni mereka, meskipun kamu mempertaruhkan nyawamu sendiri untuk melakukannya!]],
				['4'] = [[Aku turut prihatin. Dan aku tahu bahwa kesempatanku kecil. Tapi itu kesempatan. Aku akan mencari. Jika harus, aku akan keluar dari Ural, ke negara lain...]],
				['7'] = [[Perkenalkan. Aku seorang pengembara. Aku datang dari utara.]],
				['6'] = [[Mungkin tidak. Tapi, senang bertemu denganmu, Svetlana. Semoga kita berjumpa lagi suatu hari nanti... (pergi)]],
				['9'] = [[Aku mencarimu. Aku menemukan perekammu di Leningrad. Aku juga di Novgorod waktu itu, di markas bandit...]],
				['8'] = [[Svetlana dari Leningrad, kan? Kamu adalah orang yang menemukan obat epidemi, yang tertangkap oleh bandit, dan yang melarikan diri dan berhasil sampai ke Tver dengan berjalan kaki?]],
				['28'] = [[Aku tidak mengerti. Apa lagi yang harus diungkap? Kamu tahu segalanya. Epidemi mewabah, lalu terjadi perang nuklir...]],
				['38'] = [[Ayahmu...]],
				['18'] = [[Menarik. Siapa ayahmu?]],
				['11'] = [[Itu yang aku katakan, aku tidak tahu. Aku terbangun di Nikel, tidak tahu siapa diriku, dari mana aku berasal, atau bagaimana aku bisa sampai di sana. Dan aku dikelilingi oleh mayat dan reruntuhan yang membusuk... Aku mulai mencari jawaban. Untuk orang-orang yang bisa memberi aku semacam penjelasan. Tapi tidak ada yang tahu apa yang terjadi, atau mengapa.]],
				['17'] = [[Di Novgorod, dari sebuah mayat. Tapi ada lagi. Aku menemukan kaleng semprotan berlabel 'Day R' ini juga.<new_line>Apakah kau tahu artinya?]],
				['13'] = [[Svetlana, terima kasih. Terima kasih atas tawarannya, tapi aku tidak bisa tinggal di sini bersama kalian semua.]],
				['15'] = [[(Diam)]],
				['33'] = [[Sejujurnya, tidak juga. Aku baru saja berpikir... Kurasa aku masih akan pergi ke Moskow, ke Institut Virologi. Aku akan mencari beberapa petunjuk di sana. Siapa tahu, mungkin aku akan mendapat beberapa kemajuan dalam pencarianku sendiri.]],
				['23'] = [[Jangan salah paham, tapi aku... tidak yakin aku punya waktu untuk ini...]],
				['31'] = [[Svetlana, walaupun kamu benar, jika wabah itu buatan manusia, semacam konspirasi... bagaimana caramu mengungkap kebenaran itu? Bertahun-tahun telah berlalu. Ini gila.]],
				['21'] = [[Tidak, tunggu sebentar. Aku belum memutuskan apa pun. Tetap saja, prioritasku adalah menemukan istri dan anak perempuanku.]],
				['47'] = [[Selamat tinggal (pergi dan diam-diam menutup pintu di belakangmu)]],
				['46'] = [[Svetlana, dengar... Aku ingat bahwa ibumu mengirimimu surat tentang serangan jantung ayahmu. Tapi mungkin dia hanya tidak mau...]],
				['45'] = [[Apa yang kamu maksud dengan mengapa? Kamulah yang ingin tahu kebenarannya.]],
				['44'] = [[Svetlana?]],
				['43'] = [[(Katakan semuanya padanya)]],
				['42'] = [[Ya, aku setuju.]],
				['41'] = [[Oh, jadi begitu...]],
				['40'] = [[Apa yang pertama baginya?]],
				['24'] = [[Svetlana, ini ide yang buruk. Padang gurun terlalu berbahaya untuk seseorang yang tidak siap. Aku tidak bisa melindungimu. Dan kamu akan meninggalkan orang-orang di sini tanpa seorang dokter. Lebih baik jika kamu tetap di sini.]],
				['37'] = [[Halo lagi, Svetlana.]],
				['36'] = [[Sampai jumpa.]],
				['35'] = [[Halo. Tidak, belum apa-apa. Bagaimana denganmu?]],
				['34'] = [[Perlahan-lahan, tapi itu akan... Aku datang hanya ingin melihat keadaanmu.]],
				['26'] = [[Ya? Kudengarkan.]],
				['25'] = [[Maaf, Svetlana, tapi kamu tidak terlihat seperti seseorang yang sudah berhenti peduli. Bagaimana pun juga, kamu mencoba menyelamatkanku.]],
				['39'] = [[Ya, benar. Masalahnya adalah... uh... mengapa gadis itu sangat takut padaku?]],
				['29'] = [[Aku tidak paham.]],
				['19'] = [[Di Institut Virologi, katamu? Di Moskow? Karena ini adalah satu-satunya petunjukku, sepertinya aku akan pergi ke Moskow.]],
				['27'] = [[Oke, lalu apa masalahnya?]]
			}
		},
		['varya'] = {
			['episode'] = {
				['9'] = [[(Aku akui bahwa aku hampir menjatuhkan Varya karena terkejut.)<new_line><new_line>Ya, lalu apa? Kamu kuat, berani, kamu bisa melindungiku. Kamu, seperti, mempertaruhkan hidupmu untuk menyelamatkanku. Sekarang aku harus membalas kebaikanmu! Apa yang akan kulakukan di rumah bibiku? Aku akan bosan bukan main!]],
				['8'] = [[Tentu. Tapi kamu tahu apa yang aku pikirkan? Aku tidak ingin kembali ke bibiku. Mungkin aku bisa bepergian denganmu?]],
				['11'] = [[Bosan dan menganggur... Baiklah, tidak ada yang perlu dilakukan, kukira. Hei, peluru kaliber seperti apa yang kamu punya? Yang besar seperti pamanku atau lebih kecil?]],
				['1'] = [[(Tidak ada sumber cahaya di gudang selain sorotan terpisah-pisah dari sinar matahari yang masuk melalui lubang-lubang di atap. Papan lantai berderit sebagai protes saat kuinjak mereka.<new_line>Tahanan itu duduk meringkuk di sudut gelap yang sama.)]],
				['10'] = [[Kembali ke bibiku? Ahh... Baiklah. Hei, bisakah kamu ajari aku cara menembak?]],
				['3'] = [[Kamu membunuh mereka? (Mulutnya menganga takjub.) Kamu sendiri? Melawan mereka semua? Tidak mungkin! Bagaimana kamu melakukannya? Ngomong-ngomong, aku Varya.]],
				['2'] = [[(Mula-mula gadis itu menatapku dengan kebencian, tetapi kemudian ekspresinya berubah.)<new_line><new_line>Hei! Aku tidak mengingatmu! Kamu bukan golongan mereka?]],
				['5'] = [[Lihat dirimu sendiri.]],
				['4'] = [[(Varya melangkah tidak pasti dan terhuyung-huyung. Dia meraih tembok.)<new_line><new_line>Maaf, kedua kakiku tak sejalan. Apa yang kita lakukan? Bisakah... bisakah kamu menggendongku? Hanya sebentar, sampai kakiku kembali normal.]],
				['7'] = [[Aku mengatakan bahwa dia hanya cemburu padaku.]],
				['6'] = [[(Di perjalanan kami menuju hutan, Varya menceritakan cobaan dan kesengsaraannya saat ditawan para bandit. Lalu dia pindah ke kisah penculikannya yang keji, dan kemudian menceritakan secara detail tentang keburukan bibinya, yang tinggal di sebuah desa di luar Bryansk. Aku hampir tidak mendengarkan, terlalu tenggelam dalam pikiranku sendiri.)]]
			},
			['name'] = [[Varya]],
			['choice'] = {
				['9'] = [[Uh... mari kita lakukan seperti ini: Aku akan membawamu kembali ke bibimu dan aku akan pergi sebentar. Kamu memiliki kesempatan untuk memikirkan caramu membalas budiku. Oke?]],
				['8'] = [[Denganku?!]],
				['1'] = [[(Membebaskan dia)]],
				['10'] = [[(Dengan lembut namun tegas mengakhiri percakapan)]],
				['3'] = [[Senang bertemu denganmu, Varya. Aku akan ceritakan segalanya di jalan. Jadi, bisakah kamu berjalan sendiri?]],
				['2'] = [[Tidak, aku bukan golongan mereka. Dan mereka pergi sekarang, aku membunuh mereka. Bangun, kita harus keluar dari sini. Bisakah kamu berjalan sendiri?]],
				['5'] = [[(Gendong Varya dan bawa dia keluar dari gudang)]],
				['4'] = [[Uh... kamu benar-benar tidak bisa berjalan sendiri?]],
				['7'] = [[Tidak, sebelumnya. Ada desa para korban selamat di luar Bryansk? Bisakah kamu tunjukkan jalannya?]],
